  INSERT INTO words (word, word_type, description) VALUES  (    'Yoke', 'v. t.', 'To couple; to join with another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoke', 'v. t.', 'To enslave; to bring into bondage; to restrain; to\n   confine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoke', 'v. i.', 'To be joined or associated; to be intimately connected; to\n   consort closely; to mate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yokeage', 'n.', 'See Rokeage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yokefellow', 'n.', 'An associate or companion in, or as in; a mate; a\n   fellow; especially, a partner in marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yokel', 'n.', 'A country bumpkin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yokelet', 'n.', 'A small farm; -- so called as requiring but one yoke of\n   oxen to till it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yokemate', 'n.', 'Same as Yokefellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoke-toed', 'a.', 'Having two toes in front and two behind, as the trogons\n   and woodpeckers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yold', 'p. p.', 'Yielded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yolden', 'p. p.', 'Yielded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yolk', 'n.', 'The yellow part of an egg; the vitellus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yolk', 'n.', 'An oily secretion which naturally covers the wool of sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoll', 'v. i.', 'To yell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yon', 'a.', 'At a distance, but within view; yonder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yon', 'adv.', 'Yonder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoncopin', 'n.', 'A local name in parts of the Mississippi Valley for the\n   American lotus (Nelumbo lutea).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yond', 'a.', 'Furious; mad; angry; fierce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yond', 'a.', 'Yonder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yonder', 'adv.', 'At a distance, but within view.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yonder', 'a.', 'Being at a distance within view, or conceived of as within\n   view; that or those there; yon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yoni', 'n.', 'The symbol under which Sakti, or the personification of the\n   female power in nature, is worshiped. Cf. Lingam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yonker', 'n.', 'A young fellow; a younker.');
