  INSERT INTO words (word, word_type, description) VALUES  (    'York use', '', 'The one of the three printed uses of England which was\n   followed in the north. It was based on the Sarum use. See Use, n., 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yot', 'v. t.', 'To unite closely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yote', 'v. t.', 'To pour water on; to soak in, or mix with, water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'You', 'dat. & obj.', 'The pronoun of the second person, in the nominative,\n   dative, and objective case, indicating the person or persons addressed.\n   See the Note under Ye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youl', 'v. i.', 'To yell; to yowl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Young', 'superl.', 'Not long born; still in the first part of life; not yet\n   arrived at adolescence, maturity, or age; not old; juvenile; -- said of\n   animals; as, a young child; a young man; a young fawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Young', 'superl.', 'Being in the first part, pr period, of growth; as, a\n   young plant; a young tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Young', 'superl.', 'Having little experience; inexperienced; unpracticed;\n   ignorant; weak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Young', 'n.', 'The offspring of animals, either a single animal or\n   offspring collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youngger', 'n.', 'One who is younger; an inferior in age; a junior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youngish', 'a.', 'Somewhat young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youngling', 'n.', 'A young person; a youth; also, any animal in its early\n   life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youngling', 'a.', 'Young; youthful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youngly', 'a.', 'Like a young person or thing; young; youthful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youngly', 'adv.', 'In a young manner; in the period of youth; early in\n   life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youngly', 'adv.', 'Ignorantly; weakly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youngness', 'n.', 'The quality or state of being young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youngster', 'n.', 'A young person; a youngling; a lad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youngth', 'n.', 'Youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youngthly', 'a.', 'Pertaining to, or resembling, youth; youthful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Younker', 'a.', 'A young person; a stripling; a yonker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youpon', 'n.', 'Same as Yaupon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Your', 'pron. & a.', 'The form of the possessive case of the personal\n   pronoun you.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yours', 'pron.', 'See the Note under Your.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yourselves', 'pl. ', 'of Yourself');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yourself', 'pron.', 'An emphasized or reflexive form of the pronoun of the\n   second person; -- used as a subject commonly with you; as, you yourself\n   shall see it; also, alone in the predicate, either in the nominative or\n   objective case; as, you have injured yourself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youths', 'pl. ', 'of Youth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youth', 'pl. ', 'of Youth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youth', 'n.', 'The quality or state of being young; youthfulness;\n   juvenility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youth', 'n.', 'The part of life that succeeds to childhood; the period of\n   existence preceding maturity or age; the whole early part of life, from\n   childhood, or, sometimes, from infancy, to manhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youth', 'n.', 'A young person; especially, a young man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youth', 'n.', 'Young persons, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youthful', 'a.', 'Not yet mature or aged; young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youthful', 'a.', 'Also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youthful', 'a.', 'Of or pertaining to the early part of life; suitable to\n   early life; as, youthful days; youthful sports.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youthful', 'a.', 'Fresh; vigorous, as in youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youthhood', 'n.', 'The quality or state of being a youth; the period of\n   youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youthly', 'a.', 'Young; youthful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youthsome', 'a.', 'Youthful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youthy', 'a.', 'Young.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Youze', 'n.', 'The cheetah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yow', 'pron.', 'You.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yowe', 'n.', 'A ewe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yowl', 'v. i.', 'To utter a loud, long, and mournful cry, as a dog; to\n   howl; to yell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yowl', 'n.', 'A loud, protracted, and mournful cry, as that of a dog; a\n   howl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yowley', 'n.', 'The European yellow-hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yox', 'v. i.', 'See Yex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ypight', 'p. p.', 'See Pight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ypocras', 'n.', 'Hippocras.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ypres lace', '', 'Fine bobbin lace made at Ypres in Belgium, usually\n   exactly like Valenciennes lace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ypsiliform', 'a.', 'Resembling the / in appearance; -- said of the germinal\n   spot in the ripe egg at one of the stages of fecundation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ypsiloid', 'a.', 'In the form of the letter Y; Y-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yraft', 'p. p.', 'Bereft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yren', 'n.', 'Iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yronne', 'p. p.', 'Run.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ysame', 'adv.', 'Together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yt', '', 'Alt. of Yt');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yt', '', 'an old method of printing that (AS. /aet, /aet) the \"y\" taking\n   the place of the old letter \"thorn\" (/). Cf. Ye, the.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ythrowe', '', 'p. p. of Throw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ytterbic', 'a.', 'Pertaining to, or derived from, ytterbium; containing\n   ytterbium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ytterbium', 'n.', 'A rare element of the boron group, sometimes associated\n   with yttrium or other related elements, as in euxenite and gadolinite.\n   Symbol Yb; provisional atomic weight 173.2. Cf. Yttrium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yttria', 'n.', 'The oxide, Y2O3, or earth, of yttrium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yttric', 'a.', 'Pertaining to, derived from, or containing, yttrium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yttriferous', 'a.', 'Bearing or containing yttrium or the allied elements;\n   as, gadolinite is one of the yttriferous minerals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yttrious', 'a.', 'Same as Yttric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yttrium', 'n.', 'A rare metallic element of the boron-aluminium group,\n   found in gadolinite and other rare minerals, and extracted as a dark\n   gray powder. Symbol Y. Atomic weight, 89.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yttro-cerite', 'n.', 'A mineral of a violet-blue color, inclining to gray\n   and white. It is a hydrous fluoride of cerium, yttrium, and calcium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yttro-columbite', 'n.', 'Alt. of Yttro-tantalite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yttro-tantalite', 'n.', 'A tantalate of uranium, yttrium, and calcium, of a\n   brown or black color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yu', 'n.', 'Jade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yucca', 'n.', 'See Flicker, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yucca', 'n.', 'A genus of American liliaceous, sometimes arborescent,\n   plants having long, pointed, and often rigid, leaves at the top of a\n   more or less woody stem, and bearing a large panicle of showy white\n   blossoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yuck', 'v. i.', 'To itch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yuck', 'v. t.', 'To scratch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yuckel', 'n.', 'Same as Yockel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yuen', 'n.', 'The crowned gibbon (Hylobates pileatus), native of Siam,\n   Southern China, and the Island of Hainan. It is entirely arboreal in\n   its habits, and has very long arms. the males are dark brown or\n   blackish, with a caplike mass of long dark hair, and usually with a\n   white band around the face. The females are yellowish white, with a\n   dark spot on the breast and another on the crown. Called also wooyen,\n   and wooyen ape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yufts', 'n.', 'Russia leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yug', 'n.', 'Alt. of Yuga');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yuga', 'n.', 'Any one of the four ages, Krita, or Satya, Treta, Dwapara,\n   and Kali, into which the Hindoos divide the duration or existence of\n   the world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yuke', 'v. i. & t.', 'Same as Yuck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yulan', 'n.', 'A species of Magnolia (M. conspicua) with large white\n   blossoms that open before the leaves. See the Note under Magnolia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yule', 'n.', 'Christmas or Christmastide; the feast of the Nativity of our\n   Savior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yuletide', 'n.', 'Christmas time; Christmastide; the season of Christmas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yumas', 'n. pl.', 'A tribe of Indians native of Arizona and the adjacent\n   parts of Mexico and California. They are agricultural, and cultivate\n   corn, wheat, barley, melons, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yunx', 'n.', 'A genus of birds comprising the wrynecks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yupon', 'n.', 'Same as Yaupon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yux', 'n. & v.', 'See Yex, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Yvel', 'a. & adv.', 'Evil; ill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ywar', 'a.', 'Aware; wary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ywis', 'adv.', 'Certainly; most likely; truly; probably.\n   Z () Z, the twenty-sixth and last letter of the English alphabet, is a\n   vocal consonant. It is taken from the Latin letter Z, which came from\n   the Greek alphabet, this having it from a Semitic source. The ultimate\n   origin is probably Egyptian. Etymologically, it is most closely related\n   to s, y, and j; as in glass, glaze; E. yoke, Gr. /, L. yugum; E.\n   zealous, jealous. See Guide to Pronunciation, // 273, 274.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Za', 'n.', 'An old solfeggio name for B flat; the seventh harmonic, as\n   heard in the or aeolian string; -- so called by Tartini. It was long\n   considered a false, but is the true note of the chord of the flat\n   seventh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zabaism', 'n.', 'Alt. of Zabism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zabism', 'n.', 'See Sabianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zabian', 'a. & n.', 'See Sabian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zacco', 'n.', 'See Zocco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zachun', 'n.', 'An oil pressed by the Arabs from the fruit of a small\n   thorny tree (Balanites Aegyptiaca), and sold to piligrims for a healing\n   ointment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zaerthe', 'n.', 'Same as Z/rthe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zaffer', 'n.', 'A pigment obtained, usually by roasting cobalt glance with\n   sand or quartz, as a dark earthy powder. It consists of crude cobalt\n   oxide, or of an impure cobalt arseniate. It is used in porcelain\n   painting, and in enameling pottery, to produce a blue color, and is\n   often confounded with smalt, from which, however, it is distinct, as it\n   contains no potash. The name is often loosely applied to mixtures of\n   zaffer proper with silica, or oxides of iron, manganese, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zaim', 'n.', 'A Turkish chief who supports a mounted militia bearing the\n   same name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zaimet', 'n.', 'A district from which a Zaim draws his revenue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zain', 'n.', 'A horse of a dark color, neither gray nor white, and having\n   no spots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zalambdodont', 'a.', 'Of or pertaining to a tribe (Zalambdodonta) of\n   Insectivora in which the molar teeth have but one V-shaped ridge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zalambdodont', 'n.', 'One of the Zalambdodonta. The tenrec, solenodon, and\n   golden moles are examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zamang', 'n.', 'An immense leguminous tree (Pithecolobium Saman) of\n   Venezuela. Its branches form a hemispherical mass, often one hundred\n   and eighty feet across. The sweet pulpy pods are used commonly for\n   feeding cattle. Also called rain tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zambos', 'pl. ', 'of Zambo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zambo', 'n.', 'The child of a mulatto and a negro; also, the child of an\n   Indian and a negro; colloquially or humorously, a negro; a sambo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zamia', 'n.', 'A genus of cycadaceous plants, having the appearance of low\n   palms, but with exogenous wood. See Coontie, and Illust. of Strobile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zamindar', 'n.', 'A landowner; also, a collector of land revenue; now,\n   usually, a kind of feudatory recognized as an actual proprietor so long\n   as he pays to the government a certain fixed revenue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zamindary', 'n.', 'Alt. of Zamindari');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zamindari', 'n.', 'The jurisdiction of a zamindar; the land possessed by a\n   zamindar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zamite', 'n.', 'A fossil cycad of the genus Zamia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zamouse', 'n.', 'A West African buffalo (Bubalus brachyceros) having short\n   horns depressed at the base, and large ears fringed internally with\n   three rows of long hairs. It is destitute of a dewlap. Called also\n   short-horned buffalo, and bush cow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zampogna', 'n.', 'A sort of bagpipe formerly in use among Italian peasants.\n   It is now almost obsolete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zander', 'n.', 'A European pike perch (Stizostedion lucioperca) allied to\n   the wall-eye; -- called also sandari, sander, sannat, schill, and zant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zandmole', 'n.', 'The sand mole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zante', 'n.', 'See Zantewood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zante currant', '', 'A kind of seedless grape or raisin; -- so called from\n   Zante, one of the Ionian Islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zantewood', 'n.', 'A yellow dyewood; fustet; -- called also zante, and\n   zante fustic. See Fustet, and the Note under Fustic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zantewood', 'n.', 'Satinwood (Chloroxylon Swietenia).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zantiot', 'n.', 'A native or inhabitant of Zante, one of the Ionian\n   Islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zanies', 'pl. ', 'of Zany');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zany', 'n.', 'A merry-andrew; a buffoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zany', 'v. t.', 'To mimic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zanyism', 'n.', 'State or character of a zany; buffoonery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zaphara', 'n.', 'Zaffer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zaphrentis', 'n.', 'An extinct genus of cyathophylloid corals common in the\n   Paleozoic formations. It is cup-shaped with numerous septa, and with a\n   deep pit in one side of the cup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zapotilla', 'n.', 'See Sapodilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zaptiah', 'n.', 'A Turkish policeman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zarathustrian', 'a.', 'Alt. of Zarathustric');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zarathustric', 'a.', 'Of or pertaining to Zarathustra, or Zoroaster;\n   Zoroastrian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zarathustrism', 'n.', 'See Zoroastrianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zaratite', 'n.', 'A hydrous carbonate of nickel occurring as an\n   emerald-green incrustation on chromite; -- called also emerald nickel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zareba', 'n.', 'An improvised stockade; especially, one made of thorn\n   bushes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zarnich', 'n.', 'Native sulphide of arsenic, including sandarach, or\n   realgar, and orpiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zarthe', 'n.', 'A European bream (Abramis vimba).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zati', 'n.', 'A species of macaque (Macacus pileatus) native of India and\n   Ceylon. It has a crown of long erect hair, and tuft of radiating hairs\n   on the back of the head. Called also capped macaque.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zauschneria', 'n.', 'A genus of flowering plants. Zauschneria Californica\n   is a suffrutescent perennial, with showy red flowers much resembling\n   those of the garden fuchsia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zax', 'n.', 'A tool for trimming and puncturing roofing slates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zayat', 'n.', 'A public shed, or portico, for travelers, worshipers, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zea', 'n.', 'A genus of large grasses of which the Indian corn (Zea Mays)\n   is the only species known. Its origin is not yet ascertained. See\n   Maize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zeal', 'n.', 'Passionate ardor in the pursuit of anything; eagerness in\n   favor of a person or cause; ardent and active interest; engagedness;\n   enthusiasm; fervor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zeal', 'n.', 'A zealot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zeal', 'v. i.', 'To be zealous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zealant', 'n.', 'One who is zealous; a zealot; an enthusiast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zealed', 'a.', 'Full of zeal; characterized by zeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zealful', 'a.', 'Full of zeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zealless', 'a.', 'Wanting zeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zealot', 'n.', 'One who is zealous; one who engages warmly in any cause,\n   and pursues his object with earnestness and ardor; especially, one who\n   is overzealous, or carried away by his zeal; one absorbed in devotion\n   to anything; an enthusiast; a fanatical partisan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zealotical', 'a.', 'Like, or suitable to, a zealot; ardently zealous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zealotism', 'n.', 'The character or conduct of a zealot; zealotry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zealotist', 'n.', 'A zealot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zealotry', 'n.', 'The character and behavior of a zealot; excess of zeal;\n   fanatical devotion to a cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zealous', 'a.', 'Filled with, or characterized by, zeal; warmly engaged, or\n   ardent, in behalf of an object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zealous', 'a.', 'Filled with religious zeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zebec', 'n.', 'See Xebec.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zebra', 'n.', 'Either one of two species of South African wild horses\n   remarkable for having the body white or yellowish white, and\n   conspicuously marked with dark brown or brackish bands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zebrawood', 'n.', 'A kind of cabinet wood having beautiful black, brown,\n   and whitish stripes, the timber of a tropical American tree (Connarus\n   Guianensis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zebrawood', 'n.', 'The wood of a small West Indian myrtaceous tree (Eugenia\n   fragrans).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zebrawood', 'n.', 'The wood of an East Indian tree of the genus Guettarda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zebrine', 'a.', 'Pertaining to, or resembling, the zebra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zebu', 'n.', 'A bovine mammal (Ros Indicus) extensively domesticated in\n   India, China, the East Indies, and East Africa. It usually has short\n   horns, large pendulous ears, slender legs, a large dewlap, and a large,\n   prominent hump over the shoulders; but these characters vary in\n   different domestic breeds, which range in size from that of the common\n   ox to that of a large mastiff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zebub', 'n.', 'A large noxious fly of Abyssinia, which like the tsetse fly,\n   is destructive to cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zechin', 'n.', 'See Sequin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zechstein', 'n.', 'The upper division of the Permian (Dyas) of Europe. The\n   prevailing rock is a magnesian limestone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zed', 'n.', 'The letter Z; -- called also zee, and formerly izzard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zedoary', 'n.', 'A medicinal substance obtained in the East Indies, having\n   a fragrant smell, and a warm, bitter, aromatic taste. It is used in\n   medicine as a stimulant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zeekoe', 'n.', 'A hippopotamus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zehner', 'n.', 'An Austrian silver coin equal to ten kreutzers, or about\n   five cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zein', 'n.', 'A nitrogenous substance of the nature of gluten, obtained\n   from the seeds of Indian corn (Zea) as a soft, yellowish, amorphous\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zemindar', 'n.', 'Same as Zamindar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zemindary', 'n.', 'Alt. of Zemindari');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zemindari', 'n.', 'Same as Zamindary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zemni', 'n.', 'The blind mole rat (Spalax typhlus), native of Eastern\n   Europe and Asia. Its eyes and ears are rudimentary, and its fur is soft\n   and brownish, more or less tinged with gray. It constructs extensive\n   burrows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zenana', 'n.', 'The part of a dwelling appropriated to women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zend', 'n.', 'Properly, the translation and exposition in the Huzv/resh, or\n   literary Pehlevi, language, of the Avesta, the Zoroastrian sacred\n   writings; as commonly used, the language (an ancient Persian dialect)\n   in which the Avesta is written.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zend-Avesta', 'n.', 'The sacred writings of the ancient Persian religion,\n   attributed to Zoroaster, but chiefly of a later date.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zendik', 'n.', 'An atheist or unbeliever; -- name given in the East to\n   those charged with disbelief of any revealed religion, or accused of\n   magical heresies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zenick', 'n.', 'A South African burrowing mammal (Suricata tetradactyla),\n   allied to the civets. It is grayish brown, with yellowish transverse\n   stripes on the back. Called also suricat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zenik', 'n.', 'See Zenick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zenith', 'n.', 'That point in the visible celestial hemisphere which is\n   vertical to the spectator; the point of the heavens directly overhead;\n   -- opposed to nadir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zenith', 'n.', 'hence, figuratively, the point of culmination; the greatest\n   height; the height of success or prosperity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zenithal', 'a.', 'Of or pertaining to the zenith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zeolite', 'n.', 'A term now used to designate any one of a family of\n   minerals, hydrous silicates of alumina, with lime, soda, potash, or\n   rarely baryta. Here are included natrolite, stilbite, analcime,\n   chabazite, thomsonite, heulandite, and others. These species occur of\n   secondary origin in the cavities of amygdaloid, basalt, and lava, also,\n   less frequently, in granite and gneiss. So called because many of these\n   species intumesce before the blowpipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zeolitic', 'a.', 'Of or pertaining to a zeolite; consisting of, or\n   resembling, a zeolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zeolitiform', 'a.', 'Having the form of a zeolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zephyr', 'n.', 'The west wind; poetically, any soft, gentle breeze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zephyrus', 'n.', 'The west wind, or zephyr; -- usually personified, and\n   made the most mild and gentle of all the sylvan deities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zequin', 'n.', 'See Sequin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zerda', 'n.', 'The fennec.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zeriba', 'n.', 'Same as Zareba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zeros', 'pl. ', 'of Zero');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zeroes', 'pl. ', 'of Zero');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zero', 'n.', 'A cipher; nothing; naught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zero', 'n.', 'The point from which the graduation of a scale, as of a\n   thermometer, commences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zero', 'n.', 'Fig.: The lowest point; the point of exhaustion; as, his\n   patience had nearly reached zero.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zest', 'n.', 'A piece of orange or lemon peel, or the aromatic oil which\n   may be squeezed from such peel, used to give flavor to liquor, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zest', 'n.', 'Hence, something that gives or enhances a pleasant taste, or\n   the taste itself; an appetizer; also, keen enjoyment; relish; gusto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zest', 'n.', 'The woody, thick skin inclosing the kernel of a walnut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zested', 'imp. & p. p.', 'of Zest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zesting', 'p. pr. & vb. n.', 'of Zest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zest', 'v. t.', 'To cut into thin slips, as the peel of an orange, lemon,\n   etc.; to squeeze, as peel, over the surface of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zest', 'v. t.', 'To give a relish or flavor to; to heighten the taste or\n   relish of; as, to zest wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zeta', 'n.', 'A Greek letter corresponding to our z.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zetetic', 'a.', 'Seeking; proceeding by inquiry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zetetic', 'n.', 'A seeker; -- a name adopted by some of the Pyrrhonists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zetetics', 'a.', 'A branch of algebra which relates to the direct search\n   for unknown quantities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zeuglodon', 'n.', 'A genus of extinct Eocene whales, remains of which have\n   been found in the Gulf States. The species had very long and slender\n   bodies and broad serrated teeth. See Phocodontia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zeuglodont', '', 'Any species of Zeuglodonta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zeuglodonta', 'n. pl.', 'Same as Phocodontia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zeugma', 'n.', 'A figure by which an adjective or verb, which agrees with a\n   nearer word, is, by way of supplement, referred also to another more\n   remote; as, \"hic illius arma, hic currus fuit;\" where fuit, which\n   agrees directly with currus, is referred also to arma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zeugmatic', 'a.', 'Of or pertaining to zeugma; characterized by zeugma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zeugobranchiata', 'n. pl.', 'Same as Zygobranchia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zeus', 'n.', 'The chief deity of the Greeks, and ruler of the upper world\n   (cf. Hades). He was identified with Jupiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zeuzerian', 'n.', 'Any one of a group of bombycid moths of which the genus\n   Zeuzera is the type. Some of these moths are of large size. The goat\n   moth is an example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zeylanite', 'n.', 'See Ceylanite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zibet', 'n.', 'Alt. of Zibeth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zibeth', 'n.', 'A carnivorous mammal (Viverra zibetha) closely allied to\n   the civet, from which it differs in having the spots on the body less\n   distinct, the throat whiter, and the black rings on the tail more\n   numerous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ziega', 'n.', 'Curd produced from milk by adding acetic acid, after rennet\n   has ceased to cause coagulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zietrisikite', 'n.', 'A mineral wax, vert similar to ozocerite. It is found\n   at Zietrisika, Moldavia, whence its name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zif', 'n.', 'The second month of the Jewish ecclesiastical year,\n   corresponding to our May.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zigger', 'v. i.', 'Alt. of Zighyr');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zighyr', 'v. i.', 'Same as Sicker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zigzag', 'n.', 'Something that has short turns or angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zigzag', 'n.', 'A molding running in a zigzag line; a chevron, or series of\n   chevrons. See Illust. of Chevron, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zigzag', 'n.', 'See Boyau.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zigzag', 'a.', 'Having short, sharp turns; running this way and that in an\n   onward course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zigzagged', 'imp. & p. p.', 'of Zigzag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zigzagging', 'p. pr. & vb. n.', 'of Zigzag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zigzag', 'v. t.', 'To form with short turns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zigzag', 'v. i.', 'To move in a zigzag manner; also, to have a zigzag\n   shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zigzaggery', 'n.', 'The quality or state of being zigzag; crookedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zigzaggy', 'a.', 'Having sharp turns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zilla', 'n.', 'A low, thorny, suffrutescent, crucifeous plant (Zilla\n   myagroides) found in the deserts of Egypt. Its leaves are boiled in\n   water, and eaten, by the Arabs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zillah', 'n.', 'A district or local division, as of a province.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zimb', 'n.', 'A large, venomous, two-winged fly, native of Abyssinia. It is\n   allied to the tsetse fly, and, like the latter, is destructive to\n   cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ziment-water', 'n.', 'A kind of water found in copper mines; water\n   impregnated with copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zinc', 'n.', 'An abundant element of the magnesium-cadmium group, extracted\n   principally from the minerals zinc blende, smithsonite, calamine, and\n   franklinite, as an easily fusible bluish white metal, which is\n   malleable, especially when heated. It is not easily oxidized in moist\n   air, and hence is used for sheeting, coating galvanized iron, etc. It\n   is used in making brass, britannia, and other alloys, and is also\n   largely consumed in electric batteries. Symbol Zn. Atomic weight 64.9.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zincked', 'imp. & p. p.', 'of Zinc');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zinced', '', 'of Zinc');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zincking', 'p. pr. & vb. n.', 'of Zinc');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zincing', '', 'of Zinc');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zinc', 'v. t.', 'To coat with zinc; to galvanize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zincane', 'n.', 'Zinc chloride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zincic', 'a.', 'Pertaining to, containing, or resembling, zinc; zincous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zincide', 'n.', 'A binary compound of zinc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zinciferous', 'a.', 'Containing or affording zinc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zincification', 'n.', 'The act or process of applying zinc; the condition\n   of being zincified, or covered with zinc; galvanization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zincify', 'v. t.', 'To coat or impregnate with zinc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zincite', 'n.', 'Native zinc oxide; a brittle, translucent mineral, of an\n   orange-red color; -- called also red zinc ore, and red oxide of zinc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zincking', 'n.', 'Alt. of Zincing');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zincing', 'n.', 'The act or process of applying zinc; galvanization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zincky', 'a.', 'Pertaining to zinc, or having its appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zinco-', '', 'A combining form from zinc; in chemistry, designating zinc as\n   an element of certain double compounds. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zincode', 'n.', 'The positive electrode of an electrolytic cell; anode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zincographer', 'n.', 'An engraver on zinc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zincongraphic', 'a.', 'Alt. of Zincongraphical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zincongraphical', 'a.', 'Of or pertaining to zincography; as, zincographic\n   processes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zincography', 'n.', 'The art or process of engraving or etching on zinc, in\n   which the design is left in relief in the style of a wood cut, the rest\n   of the ground being eaten away by acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zincoid', 'a.', 'Pertaining to, or resembling, zinc; -- said of the\n   electricity of the zincous plate in connection with a copper plate in a\n   voltaic circle; also, designating the positive pole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zinco-polar', 'a.', 'Electrically polarized like the surface of the zinc\n   presented to the acid in a battery, which has zincous affinity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zincous', 'a.', 'Of, pertaining to, or containing, zinc; zincic; as,\n   zincous salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zincous', 'a.', 'Hence, formerly, basic, basylous, as opposed to chlorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zincous', 'a.', 'Of or pertaining to the positive pole of a galvanic\n   battery; electro-positive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zingari', 'pl. ', 'of Zingaro');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zingaro', 'n.', 'A gypsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zingel', 'n.', 'A small, edible, freshwater European perch (Aspro zingel),\n   having a round, elongated body and prominent snout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zingiberaceous', 'a.', 'Of or pertaining to ginger, or to a tribe\n   (Zingibereae) of endogenous plants of the order Scitamineae. See\n   Scitamineous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zink', 'n.', 'See Zinc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zinkenite', 'n.', 'A steel-gray metallic mineral, a sulphide of antimony\n   and lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zinky', 'a.', 'See Zincky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zinnia', 'n.', 'Any plant of the composite genus Zinnia, Mexican herbs with\n   opposite leaves and large gay-colored blossoms. Zinnia elegans is the\n   commonest species in cultivation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zinnwaldite', 'n.', 'A kind of mica containing lithium, often associated\n   with tin ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zinsang', 'n.', 'The delundung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zinziberaceous', 'a.', 'Same as Zingiberaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zion', 'n.', 'A hill in Jerusalem, which, after the capture of that city by\n   the Israelites, became the royal residence of David and his successors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zion', 'n.', 'Hence, the theocracy, or church of God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zion', 'n.', 'The heavenly Jerusalem; heaven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ziphioid', 'n.', 'See Xiphioid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zirco-', '', 'A combining form (also used adjectively) designating\n   zirconium as an element of certain double compounds; zircono-; as in\n   zircofluoric acid, sodium zircofluoride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zircofluoride', 'n.', 'A double fluoride of zirconium and hydrogen, or some\n   other positive element or radical; as, zircofluoride of sodium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zircon', 'n.', 'A mineral occurring in tetragonal crystals, usually of a\n   brown or gray color. It consists of silica and zirconia. A red variety,\n   used as a gem, is called hyacinth. Colorless, pale-yellow or\n   smoky-brown varieties from Ceylon are called jargon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zircona', 'n.', 'Zirconia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zirconate', 'n.', 'A salt of zirconic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zirconia', 'n.', 'The oxide of zirconium, obtained as a white powder, and\n   possessing both acid and basic properties. On account of its\n   infusibility, and brilliant luminosity when incandescent, it is used as\n   an ingredient of sticks for the Drummomd light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zirconic', 'a.', 'Pertaining to, containing, or resembling, zirconium; as,\n   zirconic oxide; zirconic compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zirconium', 'n.', 'A rare element of the carbon-silicon group, intermediate\n   between the metals and nonmetals, obtained from the mineral zircon as a\n   dark sooty powder, or as a gray metallic crystalline substance. Symbol\n   Zr. Atomic weight, 90.4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zircono', '', 'See Zirco-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zirconoid', 'n.', 'A double eight-sided pyramid, a form common with\n   tetragonal crystals; -- so called because this form often occurs in\n   crystals of zircon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zither', 'n.', 'An instrument of music used in Austria and Germany. It has\n   from thirty to forty wires strung across a shallow sounding-board,\n   which lies horizontally on a table before the performer, who uses both\n   hands in playing on it. [Not to be confounded with the old lute-shaped\n   cittern, or cithern.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zittern', 'n.', 'See Cittern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zizania', 'n.', 'A genus of grasses including Indian rice. See Indian rice,\n   under Rice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zizel', 'n.', 'The suslik.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoanthacea', 'n. pl.', 'A suborder of Actinaria, including Zoanthus and\n   allied genera, which are permanently attached by their bases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoantharia', 'n. pl.', 'Same as Anthozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoantharian', 'a.', 'Of or pertaining to the Zoantharia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoantharian', 'n.', 'One of the Anthozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoanthodeme', 'n.', 'The zooids of a compound anthozoan, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoanthoid', 'a.', 'Of or pertaining to the Zoanthacea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoanthropy', 'n.', 'A kind of monomania in which the patient believes\n   himself transformed into one of the lower animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoanthus', 'n.', 'A genus of Actinaria, including numerous species, found\n   mostly in tropical seas. The zooids or polyps resemble small, elongated\n   actinias united together at their bases by fleshy stolons, and thus\n   forming extensive groups. The tentacles are small and bright colored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zobo', 'n.', 'A kind of domestic cattle reared in Asia for its flesh and\n   milk. It is supposed to be a hybrid between the zebu and the yak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zocle', 'n.', 'Same as Socle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zocco', 'n.', 'Alt. of Zoccolo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoccolo', 'n.', 'Same as Socle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zodiac', 'n.', 'An imaginary belt in the heavens, 16¡ or 18¡ broad, in the\n   middle of which is the ecliptic, or suns path. It comprises the twelve\n   constellations, which one constituted, and from which were named, the\n   twelve signs of the zodiac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zodiac', 'n.', 'A figure representing the signs, symbols, and\n   constellations of the zodiac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zodiac', 'n.', 'A girdle; a belt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zodiacal', 'a.', 'Of or pertaining to the zodiac; situated within the\n   zodiac; as, the zodiacal planets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoea', 'n.', 'A peculiar larval stage of certain decapod Crustacea,\n   especially of crabs and certain Anomura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoetrope', 'n.', 'An optical toy, in which figures made to revolve on the\n   inside of a cylinder, and viewed through slits in its circumference,\n   appear like a single figure passing through a series of natural motions\n   as if animated or mechanically moved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zohar', 'n.', 'A Jewish cabalistic book attributed by tradition to Rabbi\n   Simon ben Yochi, who lived about the end of the 1st century, a. d.\n   Modern critics believe it to be a compilation of the 13th century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoic', 'a.', 'Of or pertaining to animals, or animal life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoide', 'n.', 'See Meride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoilean', 'a.', 'Having the characteristic of Zoilus, a bitter, envious,\n   unjust critic, who lived about 270 years before Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoilism', 'n.', 'Resemblance to Zoilus in style or manner; carping\n   criticism; detraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoisite', 'n.', 'A grayish or whitish mineral occurring in orthorhombic,\n   prismatic crystals, also in columnar masses. It is a silicate of\n   alumina and lime, and is allied to epidote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zokor', 'n.', 'An Asiatic burrowing rodent (Siphneus aspalax) resembling\n   the mole rat. It is native of the Altai Mountains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zollverein', 'n.', 'Literally, a customs union; specifically, applied to\n   the several customs unions successively formed under the leadership of\n   Prussia among certain German states for establishing liberty of\n   commerce among themselves and common tariff on imports, exports, and\n   transit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zomboruk', 'n.', 'See Zumbooruk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zonae', 'pl. ', 'of Zona');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zona', 'n.', 'A zone or band; a layer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zonal', 'a.', 'Of or pertaining to a zone; having the form of a zone or\n   zones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zonar', 'n.', 'A belt or girdle which the Christians and Jews of the Levant\n   were obliged to wear to distinguish them from Mohammedans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zonaria', 'n. pl.', 'A division of Mammalia in which the placenta is\n   zonelike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zonate', 'a.', 'Divided by parallel planes; as, zonate tetraspores, found\n   in certain red algae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zone', 'n.', 'A girdle; a cincture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zone', 'n.', 'One of the five great divisions of the earth, with respect to\n   latitude and temperature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zone', 'n.', 'The portion of the surface of a sphere included between two\n   parallel planes; the portion of a surface of revolution included\n   between two planes perpendicular to the axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zone', 'n.', 'A band or stripe extending around a body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zone', 'n.', 'A band or area of growth encircling anything; as, a zone of\n   evergreens on a mountain; the zone of animal or vegetable life in the\n   ocean around an island or a continent; the Alpine zone, that part of\n   mountains which is above the limit of tree growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zone', 'n.', 'A series of planes having mutually parallel intersections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zone', 'n.', 'Circuit; circumference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zone', 'v. t.', 'To girdle; to encircle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoned', 'a.', 'Wearing a zone, or girdle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoned', 'a.', 'Having zones, or concentric bands; striped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoned', 'a.', 'Zonate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoneless', 'a.', 'Not having a zone; ungirded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zonnar', 'n.', 'See Zonar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zonular', 'a.', 'Of or pertaining to a zone; zone-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zonule', 'n.', 'A little zone, or girdle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zonulet', 'n.', 'A zonule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zonure', 'n.', 'Any one of several of South African lizards of the genus\n   Zonura, common in rocky situations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoo-', '', 'A combining form from Gr. zwo^,n an animal, as in zoogenic,\n   zoology, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoochemical', 'a.', 'Pertaining to zoochemistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoochemistry', 'n.', 'Animal chemistry; particularly, the description of\n   the chemical compounds entering into the composition of the animal\n   body, in distinction from biochemistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoochemy', 'n.', 'Animal chemistry; zoochemistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoochlorella', 'n.', 'One of the small green granulelike bodies found in\n   the interior of certain stentors, hydras, and other invertebrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoocyst', 'n.', 'A cyst formed by certain Protozoa and unicellular plants\n   which the contents divide into a large number of granules, each of\n   which becomes a germ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoocytia', 'pl. ', 'of Zoocytium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoocytium', 'n.', 'The common support, often branched, of certain species\n   of social Infusoria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoodendria', 'pl. ', 'of Zoodendrium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoodendrium', 'n.', 'The branched, and often treelike, support of the\n   colonies of certain Infusoria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zooecia', 'pl. ', 'of Zooecium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zooecium', 'n.', 'One of the cells or tubes which inclose the feeling\n   zooids of Bryozoa. See Illust. of Sea Moss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zooerythrine', 'n.', 'A peculiar organic red coloring matter found in the\n   feathers of various birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoogamous', 'a.', 'Of or pertaining zoogamy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoogamy', 'n.', 'The sexual reproduction of animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoogenic', 'a.', 'Of or pertaining to zoogeny, animal production.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoogeny', 'n.', 'Alt. of Zoogony');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoogony', 'n.', 'The doctrine of the formation of living beings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoogeography', 'n.', 'The study or description of the geographical\n   distribution of animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoogeographical', 'a.', 'Of or pertaining to zoography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoogloea', 'n.', 'A colony or mass of bacteria imbedded in a viscous\n   gelatinous substance. The zoogloea is characteristic of a transitory\n   stage through which rapidly multiplying bacteria pass in the course of\n   their evolution. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoographer', 'n.', 'One who describes animals, their forms and habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoographic', 'a.', 'Alt. of Zoographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoographical', 'a.', 'Of or pertaining to the description of animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoographist', 'n.', 'A zoographer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoography', 'n.', 'A description of animals, their forms and habits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zooid', 'a.', 'Pertaining to, or resembling, an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zooid', 'n.', 'An organic body or cell having locomotion, as a spermatic\n   cell or spermatozooid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zooid', 'n.', 'An animal in one of its inferior stages of development, as\n   one of the intermediate forms in alternate generation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zooid', 'n.', 'One of the individual animals in a composite group, as of\n   Anthozoa, Hydroidea, and Bryozoa; -- sometimes restricted to those\n   individuals in which the mouth and digestive organs are not developed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zooidal', 'a.', 'Of or pertaining to a zooid; as, a zooidal form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoolatry', 'n.', 'The worship of animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoologer', 'n.', 'A zoologist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoological', 'a.', 'Of or pertaining to zoology, or the science of animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoologically', 'adv.', 'In a zoological manner; according to the principles\n   of zoology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoologist', 'n.', 'One who is well versed in zoology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoologies', 'pl. ', 'of Zoology');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoology', 'n.', 'That part of biology which relates to the animal kingdom,\n   including the structure, embryology, evolution, classification, habits,\n   and distribution of all animals, both living and extinct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoology', 'n.', 'A treatise on this science.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoomelanin', 'n.', 'A pigment giving the black color to the feathers of\n   many birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoomorphic', 'a.', 'Of or pertaining to zoomorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoomorphism', 'n.', 'The transformation of men into beasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoomorphism', 'n.', 'The quality of representing or using animal forms; as,\n   zoomorphism in ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoomorphism', 'n.', 'The representation of God, or of gods, in the form, or\n   with the attributes, of the lower animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoa', 'pl. ', 'of Zoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoon', 'n.', 'An animal which is the sole product of a single egg; --\n   opposed to zooid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoon', 'n.', 'Any one of the perfectly developed individuals of a compound\n   animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoonic', 'a.', 'Of or pertaining to animals; obtained from animal\n   substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoonite', 'n.', 'One of the segments of the body of an articulate animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoonite', 'n.', 'One of the theoretic transverse divisions of any segmented\n   animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoonomy', 'n.', 'The laws of animal life, or the science which treats of\n   the phenomena of animal life, their causes and relations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoonule', 'n.', 'Same as Zoonite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoopathology', 'n.', 'Animal pathology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoophaga', 'n. pl.', 'An artificial group comprising various carnivorous\n   and insectivorous animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoophagan', 'n.', 'A animal that feeds on animal food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoophagous', 'a.', 'Feeding on animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoophilist', 'n.', 'A lover of animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoophily', 'n.', 'Love of animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoophite', 'n.', 'A zoophyte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoophoric', 'a.', 'Bearing or supporting the figure of an animal; as, a\n   zoophoric column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoophorous', 'n.', 'The part between the architrave and cornice; the\n   frieze; -- so called from the figures of animals carved upon it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoophyta', 'n. pl.', 'An extensive artificial and heterogeneous group of\n   animals, formerly adopted by many zoologists. It included the\n   c/lenterates, echinoderms, sponges, Bryozoa, Protozoa, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoophyte', 'v. i.', 'Any one of numerous species of invertebrate animals\n   which more or less resemble plants in appearance, or mode of growth, as\n   the corals, gorgonians, sea anemones, hydroids, bryozoans, sponges,\n   etc., especially any of those that form compound colonies having a\n   branched or treelike form, as many corals and hydroids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoophyte', 'v. i.', 'Any one of the Zoophyta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoophytic', 'a.', 'Alt. of Zoophytical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoophytical', 'a.', 'Of or pertaining to zoophytes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoophytoid', 'a.', 'Pertaining to, or resembling, a zoophyte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoophytological', 'a.', 'Of or pertaining to zoophytology; as,\n   zoophytological observations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoophytology', 'n.', 'The natural history zoophytes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoopraxiscope', 'n.', 'An instrument similar to, or the same as, the, the\n   phenakistoscope, by means of which pictures projected upon a screen are\n   made to exhibit the natural movements of animals, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoopsychology', 'n.', 'Animal psychology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoosperm', 'n.', 'One of the spermatic particles; spermatozoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-sporangia', 'pl. ', 'of Zoosporangium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoosporangium', 'n.', 'A spore, or conceptacle containing zoospores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoospore', 'n.', 'A spore provided with one or more slender cilia, by the\n   vibration of which it swims in the water. Zoospores are produced by\n   many green, and by some olive-brown, algae. In certain species they are\n   divided into the larger macrozoospores and the smaller microzoospores.\n   Called also sporozoid, and swarmspore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoospore', 'n.', 'See Swarmspore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoosporic', 'a.', 'Of or pertaining to zoospores; of the nature of\n   zoospores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zootic', 'a.', 'Containing the remains of organized bodies; -- said of rock\n   or soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zootomical', 'a.', 'Of or pertaining to zootomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zootomist', 'n.', 'One who dissects animals, or is skilled in zootomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zootomy', 'n.', 'The dissection or the anatomy of animals; -- distinguished\n   from androtomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zootrophic', 'a.', 'Of or pertaining to the nourishment of animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoozoo', 'n.', 'The wood pigeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zope', 'n.', 'A European fresh-water bream (Abramis ballerus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zopilote', 'n.', 'The urubu, or American black vulture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoril', 'n.', 'Same as Zorilla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zorilla', 'n.', 'Either one of two species of small African carnivores of\n   the genus Ictonyx allied to the weasels and skunks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoroastrian', 'a.', 'Of or pertaining to Zoroaster, or his religious\n   system.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoroastrian', 'n.', 'A follower of Zoroaster; one who accepts\n   Zoroastrianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoroastrianism', 'n.', 'The religious system of Zoroaster, the legislator\n   and prophet of the ancient Persians, which was the national faith of\n   Persia; mazdeism. The system presupposes a good spirit (Ormuzd) and an\n   opposing evil spirit (Ahriman). Cf. Fire worship, under Fire, and\n   Parsee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoroastrism', 'n.', 'Same as Zoroastrianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoster', 'n.', 'Shingles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zostera', 'n.', 'A genus of plants of the Naiadaceae, or Pondweed family.\n   Zostera marina is commonly known as sea wrack, and eelgrass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zosterops', 'n.', 'A genus of birds that comprises the white-eyes. See\n   White-eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zouave', 'n.', 'One of an active and hardy body of soldiers in the French\n   service, originally Arabs, but now composed of Frenchmen who wear the\n   Arab dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zouave', 'n.', 'Hence, one of a body of soldiers who adopt the dress and\n   drill of the Zouaves, as was done by a number of volunteer regiments in\n   the army of the United States in the Civil War, 1861-65.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zounds', 'interj.', 'An exclamation formerly used as an oath, and an\n   expression of anger or wonder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zoutch', 'v. t.', 'To stew, as flounders, eels, etc., with just enough or\n   liquid to cover them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zubr', 'n.', 'The aurochs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zuche', 'n.', 'A stump of a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zuchetto', 'n.', 'A skullcap covering the tonsure, worn under the berretta.\n   The popes is white; a cardinals red; a bishops purple; a priests\n   black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zufolo', 'n.', 'A little flute or flageolet, especially that which is used\n   to teach birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zuisin', 'n.', 'The American widgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zulus', 'n. pl.', 'The most important tribe belonging to the Kaffir race.\n   They inhabit a region on the southeast coast of Africa, but formerly\n   occupied a much more extensive country. They are noted for their\n   warlike disposition, courage, and military skill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zumbooruk', 'n.', 'A small cannon supported by a swiveled rest on the back\n   of a camel, whence it is fired, -- used in the East.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zumic', 'n.', 'Alt. of Zumometer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zumological', 'n.', 'Alt. of Zumometer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zumology', 'n.', 'Alt. of Zumometer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zumometer', 'n.', 'See Zymic, Zymological, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zu/is', 'n. pl.', 'A tribe of Pueblo Indians occupying a village in New\n   Mexico, on the Zu/i River.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zunyite', 'n.', 'A fluosilicate of alumina occurring in tetrahedral\n   crystals at the Zu/i mine in Colorado.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zwanziger', 'n.', 'An Austrian silver coin equivalent to 20 kreutzers, or\n   about 10 cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygantra', 'pl. ', 'of Zygantrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygantrum', 'n.', 'See under Zygosphene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygapophyses', 'pl. ', 'of Zygapophysis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygapophysis', 'n.', 'One of the articular processes of a vertebra, of\n   which there are usually four, two anterior and two posterior. See under\n   Vertebra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygenid', 'n.', 'Any one of numerous species of moths of the family\n   Zygaenidae, most of which are bright colored. The wood nymph and the\n   vine forester are examples. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygobranchia', 'n. pl.', 'A division of marine gastropods in which the\n   gills are developed on both sides of the body and the renal organs are\n   also paired. The abalone (Haliotis) and the keyhole limpet (Fissurella)\n   are examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygobranchiate', 'a.', 'Of or pertaining to the Zygobranchia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygodactyl', 'n.', 'Alt. of Zygodactyle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygodactyle', 'n.', 'Any zygodactylous bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygodactylae', 'n. pl.', 'The zygodactylous birds. In a restricted sense\n   applied to a division of birds which includes the barbets, toucans,\n   honey guides, and other related birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygodactyli', 'n. pl.', 'Same as Scansores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygodactylic', 'a.', 'Alt. of Zygodactylous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygodactylous', 'a.', 'Yoke-footed; having the toes disposed in pairs; --\n   applied to birds which have two toes before and two behind, as the\n   parrot, cuckoo, woodpecker, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygoma', 'n.', 'The jugal, malar, or cheek bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygoma', 'n.', 'The zygomatic process of the temporal bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygoma', 'n.', 'The whole zygomatic arch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygomatic', 'a.', 'Of, pertaining to, or in the region of, the zygoma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygomorphic', 'a.', 'Alt. of Zygomorphous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygomorphous', 'a.', 'Symmetrical bilaterally; -- said of organisms, or\n   parts of organisms, capable of division into two symmetrical halves\n   only in a single plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zyophyte', 'n.', 'Any plant of a proposed class or grand division\n   (Zygophytes, Zygophyta, or Zygosporeae), in which reproduction consists\n   in the union of two similar cells. Cf. Oophyte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygosis', 'n.', 'Same as Conjugation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygosperm', 'n.', 'A spore formed by the union of the contents of two\n   similar cells, either of the same or of distinct individual plants.\n   Zygosperms are found in certain orders of algae and fungi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygosphene', 'n.', 'A median process on the front part of the neural arch\n   of the vertebrae of most snakes and some lizards, which fits into a\n   fossa, called the zygantrum, on the back part of the arch in front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygospore', 'n.', 'Same as Zygosperm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zygospore', 'n.', 'A spore formed by the union of several zoospores; --\n   called also zygozoospore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zylonite', 'n.', 'Celluloid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zymase', 'n.', 'A soluble ferment, or enzyme. See Enzyme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zyme', 'n.', 'A ferment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zyme', 'n.', 'The morbific principle of a zymotic disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zymic', 'a.', 'Pertaining to, or produced by, fermentation; -- formerly, by\n   confusion, used to designate lactic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zymogen', 'n.', 'A mother substance, or antecedent, of an enzyme or\n   chemical ferment; -- applied to such substances as, not being\n   themselves actual ferments, may by internal changes give rise to a\n   ferment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zymogene', 'n.', 'One of a physiological group of globular bacteria which\n   produces fermentations of diverse nature; -- distinguished from\n   pathogene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zymogenic', 'a.', 'Pertaining to, or formed by, a zymogene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zymogenic', 'a.', 'Capable of producing a definite zymogen or ferment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zymologic', 'a.', 'Alt. of Zymological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zymological', 'a.', 'Of or pertaining to zymology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zymologist', 'n.', 'One who is skilled in zymology, or in the fermentation\n   of liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zymology', 'n.', 'A treatise on the fermentation of liquors, or the\n   doctrine of fermentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zymome', 'n.', 'A glutinous substance, insoluble in alcohol, resembling\n   legumin; -- now called vegetable fibrin, vegetable albumin, or gluten\n   casein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zymometer', 'n.', 'Alt. of Zymosimeter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zymosimeter', 'n.', 'An instrument for ascertaining the degree of\n   fermentation occasioned by the mixture of different liquids, and the\n   degree of heat which they acquire in fermentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zymophyte', 'n.', 'A bacteroid ferment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zymose', 'n.', 'Invertin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zymosis', 'n.', 'A fermentation; hence, an analogous process by which an\n   infectious disease is believed to be developed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zymosis', 'n.', 'A zymotic disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zymotic', 'a.', 'Of, pertaining to, or caused by, fermentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zymotic', 'a.', 'Designating, or pertaining to, a certain class of\n   diseases. See Zymotic disease, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Zythem',    'n.',    'See Zythum.');
