INSERT INTO words (word, word_type, description) VALUES  (  'A','','The first letter of the English and of many other alphabets. The\n   capital A of the alphabets of Middle and Western Europe, as also the\n   small letter (a); besides the forms in Italic, black letter, etc., are\n   all descended from the old Latin A, which was borrowed from the Greek');
  INSERT INTO words (word, word_type, description) VALUES  (  'A', '', 'The name of the sixth tone in the model major scale (that in C);\n   or the first tone of the minor scale, which is named after it the scale\n   in A minor. The second string of the violin is tuned to the A in the\n   treble staff. -- A sharp (A/) is the name of a musical tone\n   ');
  INSERT INTO words (word, word_type, description) VALUES  (  'A', '', 'An adjective, commonly called the indefinite article, and\n   signifying one or any, but less emphatically.');
  INSERT INTO words (word, word_type, description) VALUES  (  'A', '', 'In each; to or for each; as, \"twenty leagues a day\", \"a hundred\n   pounds a year\", \"a dollar a yard\", etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'A', 'prep.', 'In; on; at; by.');
  INSERT INTO words (word, word_type, description) VALUES  (  'A', 'prep.', 'In process of; in the act of; into; to; -- used with verbal\n   substantives in -ing which begin with a consonant. This is a shortened\n   form of the preposition an (which was used before the vowel sound); as\n   in a hunting, a building, a begging.');
  INSERT INTO words (word, word_type, description) VALUES  (  'A', '', 'Of.');
  INSERT INTO words (word, word_type, description) VALUES  (  'A', '', 'A barbarous corruption of have, of he, and sometimes of it and of\n   they.');
  INSERT INTO words (word, word_type, description) VALUES  (  'A', '', 'An expletive, void of sense, to fill up the meter');
  INSERT INTO words (word, word_type, description) VALUES  (  'A-', '', 'A, as a prefix to English words, is derived from various sources.\n   (1) It frequently signifies on or in (from an, a forms of AS. on);\n   denoting a state, as in afoot, on foot, abed, amiss, asleep, aground,');
  INSERT INTO words (word, word_type, description) VALUES  (  'A 1', '', 'A registry mark given by underwriters (as at Lloyds) to ships\n   in first-class condition. Inferior grades are indicated by A 2 and A 3.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aam', 'n.', 'A Dutch and German measure of liquids, varying in different\n   cities, being at Amsterdam about 41 wine gallons, at Antwerp 36 1/2, at\n   Hamburg 38 1/4.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aard-vark', 'n.', 'An edentate mammal, of the genus Orycteropus, somewhat\n   resembling a pig, common in some parts of Southern Africa. It burrows\n   in the ground, and feeds entirely on ants, which it catches with its\n   long, slimy tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aard-wolf', 'n.', 'A carnivorous quadruped (Proteles Lalandii); of South\n   Africa, resembling the fox and hyena. See Proteles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aaronic', 'a.', 'Alt. of Aaronical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aaronical', 'a.', 'Pertaining to Aaron, the first high priest of the Jews.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aarons rod', '', 'A rod with one serpent twined around it, thus differing\n   from the caduceus of Mercury, which has two.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aarons rod', '', 'A plant with a tall flowering stem; esp. the great\n   mullein, or hag-taper, and the golden-rod.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ab-', '', 'A prefix in many words of Latin origin. It signifies from, away\n   , separating, or departure, as in abduct, abstract, abscond. See A-(6).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ab', 'n.', 'The fifth month of the Jewish year according to the\n   ecclesiastical reckoning, the eleventh by the civil computation,\n   coinciding nearly with August.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abaca', 'n.', 'The Manila-hemp plant (Musa textilis); also, its fiber. See\n   Manila hemp under Manila.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abacinate', 'v. t.', 'To blind by a red-hot metal plate held before the\n   eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abacination', 'n.', 'The act of abacinating.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abaciscus', 'n.', 'One of the tiles or squares of a tessellated pavement;\n   an abaculus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abacist', 'n.', 'One who uses an abacus in casting accounts; a calculator.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aback', 'adv.', 'Toward the back or rear; backward.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aback', 'adv.', 'Behind; in the rear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aback', 'adv.', 'Backward against the mast; -- said of the sails when\n   pressed by the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aback', 'n.', 'An abacus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abactinal', 'a.', 'Pertaining to the surface or end opposite to the mouth\n   in a radiate animal; -- opposed to actinal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abaction', 'n.', 'Stealing cattle on a large scale.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abactor', 'n.', 'One who steals and drives away cattle or beasts by herds\n   or droves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abaculi', 'pl. ', 'of Abaculus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abaculus', 'n.', 'A small tile of glass, marble, or other substance, of\n   various colors, used in making ornamental patterns in mosaic pavements.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abacuses', 'pl. ', 'of Abacus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abaci', 'pl. ', 'of Abacus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abacus', 'n.', 'A table or tray strewn with sand, anciently used for\n   drawing, calculating, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abacus', 'n.', 'A calculating table or frame; an instrument for performing\n   arithmetical calculations by balls sliding on wires, or counters in\n   grooves, the lowest line representing units, the second line, tens,\n   etc. It is still employed in China.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abacus', 'n.', 'The uppermost member or division of the capital of a\n   column, immediately under the architrave. See Column.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abacus', 'n.', 'A tablet, panel, or compartment in ornamented or mosaic\n   work.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abacus', 'n.', 'A board, tray, or table, divided into perforated\n   compartments, for holding cups, bottles, or the like; a kind of\n   cupboard, buffet, or sideboard.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abada', 'n.', 'The rhinoceros.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abaddon', 'n.', 'The destroyer, or angel of the bottomless pit; -- the same\n   as Apollyon and Asmodeus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abaddon', 'n.', 'Hell; the bottomless pit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abaft', 'prep.', 'Behind; toward the stern from; as, abaft the wheelhouse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abaft', 'adv.', 'Toward the stern; aft; as, to go abaft.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abaisance', 'n.', 'Obeisance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abaiser', 'n.', 'Ivory black or animal charcoal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abaist', 'p. p.', 'Abashed; confounded; discomfited.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abalienate', 'v. t.', 'To transfer the title of from one to another; to\n   alienate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abalienate', 'v. t.', 'To estrange; to withdraw.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abalienate', 'v. t.', 'To cause alienation of (mind).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abalienation', 'n.', 'The act of abalienating; alienation; estrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abalone', 'n.', 'A univalve mollusk of the genus Haliotis. The shell is\n   lined with mother-of-pearl, and used for ornamental purposes; the\n   sea-ear. Several large species are found on the coast of California,\n   clinging closely to the rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aband', 'v. t.', 'To abandon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aband', 'v. t.', 'To banish; to expel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abandoned', 'imp. & p. p.', 'of Abandon');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abandoning', 'p. pr. & vb. n.', 'of Abandon');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abandon', 'v. t.', 'To cast or drive out; to banish; to expel; to reject.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abandon', 'v. t.', 'To give up absolutely; to forsake entirely ; to\n   renounce utterly; to relinquish all connection with or concern on; to\n   desert, as a person to whom one owes allegiance or fidelity; to quit;\n   to surrender.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abandon', 'v. t.', 'Reflexively: To give (ones self) up without attempt at\n   self-control; to yield (ones self) unrestrainedly; -- often in a bad\n   sense.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abandon', 'v. t.', 'To relinquish all claim to; -- used when an insured\n   person gives up to underwriters all claim to the property covered by a\n   policy, which may remain after loss or damage by a peril insured\n   against.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abandon', 'v.', 'Abandonment; relinquishment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abandon', 'n.', 'A complete giving up to natural impulses; freedom from\n   artificial constraint; careless freedom or ease.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abandoned', 'a.', 'Forsaken, deserted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abandoned', 'a.', 'Self-abandoned, or given up to vice; extremely wicked,\n   or sinning without restraint; irreclaimably wicked ; as, an abandoned\n   villain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abandonedly', 'adv.', 'Unrestrainedly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abandonee', 'n.', 'One to whom anything is legally abandoned.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abandoner', 'n.', 'One who abandons.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abandonment', 'n.', 'The act of abandoning, or the state of being\n   abandoned; total desertion; relinquishment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abandonment', 'n.', 'The relinquishment by the insured to the underwriters\n   of what may remain of the property insured after a loss or damage by a\n   peril insured against.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abandonment', 'n.', 'The relinquishment of a right, claim, or privilege, as\n   to mill site, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abandonment', 'n.', 'The voluntary leaving of a person to whom one is bound\n   by a special relation, as a wife, husband, or child; desertion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abandonment', 'n.', 'Careless freedom or ease; abandon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abandum', 'n.', 'Anything forfeited or confiscated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abanet', 'n.', 'See Abnet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abanga', 'n.', 'A West Indian palm; also the fruit of this palm, the seeds\n   of which are used as a remedy for diseases of the chest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abannation', 'n.', 'Alt. of Abannition');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abannition', 'n.', 'Banishment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abarticulation', 'n.', 'Articulation, usually that kind of articulation\n   which admits of free motion in the joint; diarthrosis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abased', 'imp. & p. p.', 'of Abase');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abasing', 'p. pr. & vb. n.', 'of Abase');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abase', 'a.', 'To lower or depress; to throw or cast down; as, to abase the\n   eye.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abase', 'a.', 'To cast down or reduce low or lower, as in rank, office,\n   condition in life, or estimation of worthiness; to depress; to humble;\n   to degrade.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abased', 'a.', 'Lowered; humbled.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abased', 'a.', 'Borne lower than usual, as a fess; also, having the ends of\n   the wings turned downward towards the point of the shield.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abasedly', 'adv.', 'Abjectly; downcastly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abasement', 'n.', 'The act of abasing, humbling, or bringing low; the state\n   of being abased or humbled; humiliation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abaser', 'n.', 'He who, or that which, abases.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abashed', 'imp. & p. p.', 'of Abash');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abashing', 'p. pr. & vb. n.', 'of Abash');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abash', 'v. t.', 'To destroy the self-possession of; to confuse or\n   confound, as by exciting suddenly a consciousness of guilt, mistake, or\n   inferiority; to put to shame; to disconcert; to discomfit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abashedly', 'adv.', 'In an abashed manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abashment', 'n.', 'The state of being abashed; confusion from shame.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abassi', 'n.', 'Alt. of Abassis');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abassis', 'n.', 'A silver coin of Persia, worth about twenty cents.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abatable', 'a.', 'Capable of being abated; as, an abatable writ or\n   nuisance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abated', 'imp. & p. p.', 'of Abate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abating', 'p. pr. & vb. n.', 'of Abate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abate', 'v. t.', 'To beat down; to overthrow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abate', 'v. t.', 'To bring down or reduce from a higher to a lower state,\n   number, or degree; to lessen; to diminish; to contract; to moderate; to\n   cut short; as, to abate a demand; to abate pride, zeal, hope.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abate', 'v. t.', 'To deduct; to omit; as, to abate something from a price.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abate', 'v. t.', 'To blunt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abate', 'v. t.', 'To reduce in estimation; to deprive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abate', 'v. t.', 'To bring entirely down or put an end to; to do away with;\n   as, to abate a nuisance, to abate a writ.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abate', 'v. t.', 'To diminish; to reduce. Legacies are liable to be abated\n   entirely or in proportion, upon a deficiency of assets.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abate', 'v. t.', 'To decrease, or become less in strength or violence; as,\n   pain abates, a storm abates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abate', 'v. t.', 'To be defeated, or come to naught; to fall through; to\n   fail; as, a writ abates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abate', 'n.', 'Abatement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abatement', 'n.', 'The act of abating, or the state of being abated; a\n   lessening, diminution, or reduction; removal or putting an end to; as,\n   the abatement of a nuisance is the suppression thereof.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abatement', 'n.', 'The amount abated; that which is taken away by way of\n   reduction; deduction; decrease; a rebate or discount allowed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abatement', 'n.', 'A mark of dishonor on an escutcheon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abatement', 'n.', 'The entry of a stranger, without right, into a freehold\n   after the death of the last possessor, before the heir or devisee.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abater', 'n.', 'One who, or that which, abates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abatis', 'n.', 'Alt. of Abattis');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abattis', 'n.', 'A means of defense formed by felled trees, the ends of\n   whose branches are sharpened and directed outwards, or against the\n   enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abatised', 'a.', 'Provided with an abatis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abator', 'n.', 'One who abates a nuisance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abator', 'n.', 'A person who, without right, enters into a freehold on the\n   death of the last possessor, before the heir or devisee.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abattoirs', 'pl. ', 'of Abattoir');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abattoir', 'n.', 'A public slaughterhouse for cattle, sheep, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abature', 'n.', 'Grass and sprigs beaten or trampled down by a stag passing\n   through them.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abatvoix', 'n.', 'The sounding-board over a pulpit or rostrum.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abawed', 'p. p.', 'Astonished; abashed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abaxial', 'a.', 'Alt. of Abaxile');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abaxile', 'a.', 'Away from the axis or central line; eccentric.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abay', 'n.', 'Barking; baying of dogs upon their prey. See Bay.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abb', 'n.', 'Among weavers, yarn for the warp. Hence, abb wool is wool for\n   the abb.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abba', 'n.', 'Father; religious superior; -- in the Syriac, Coptic, and\n   Ethiopic churches, a title given to the bishops, and by the bishops to\n   the patriarch.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbacies', 'pl. ', 'of Abbacy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbacy', 'n.', 'The dignity, estate, or jurisdiction of an abbot.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbatial', 'a.', 'Belonging to an abbey; as, abbatial rights.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbatical', 'a.', 'Abbatial.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbe', 'n.', 'The French word answering to the English abbot, the head of\n   an abbey; but commonly a title of respect given in France to every one\n   vested with the ecclesiastical habit or dress.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbess', 'n.', 'A female superior or governess of a nunnery, or convent of\n   nuns, having the same authority over the nuns which the abbots have\n   over the monks. See Abbey.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbeys', 'pl. ', 'of Abbey');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbey', 'n.', 'A monastery or society of persons of either sex, secluded\n   from the world and devoted to religion and celibacy; also, the monastic\n   building or buildings.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbey', 'n.', 'The church of a monastery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbot', 'n.', 'The superior or head of an abbey.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbot', 'n.', 'One of a class of bishops whose sees were formerly abbeys.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbotship', 'n.', 'The state or office of an abbot.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbreviated', 'imp. & p. p.', 'of Abbreviate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbreviating', 'p. pr. & vb. n.', 'of Abbreviate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbreviate', 'v. t.', 'To make briefer; to shorten; to abridge; to reduce\n   by contraction or omission, especially of words written or spoken.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbreviate', 'v. t.', 'To reduce to lower terms, as a fraction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbreviate', 'a.', 'Abbreviated; abridged; shortened.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbreviate', 'a.', 'Having one part relatively shorter than another or than\n   the ordinary type.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbreviate', 'n.', 'An abridgment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbreviated', 'a.', 'Shortened; relatively short; abbreviate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbreviation', 'n.', 'The act of shortening, or reducing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbreviation', 'n.', 'The result of abbreviating; an abridgment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbreviation', 'n.', 'The form to which a word or phrase is reduced by\n   contraction and omission; a letter or letters, standing for a word or\n   phrase of which they are a part; as, Gen. for Genesis; U.S.A. for\n   United States of America.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbreviation', 'n.', 'One dash, or more, through the stem of a note,\n   dividing it respectively into quavers, semiquavers, or\n   demi-semiquavers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbreviator', 'n.', 'One who abbreviates or shortens.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbreviator', 'n.', 'One of a college of seventy-two officers of the papal\n   court whose duty is to make a short minute of a decision on a petition,\n   or reply of the pope to a letter, and afterwards expand the minute into\n   official form.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbreviatory', 'a.', 'Serving or tending to abbreviate; shortening;\n   abridging.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbreviature', 'n.', 'An abbreviation; an abbreviated state or form.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abbreviature', 'n.', 'An abridgment; a compendium or abstract.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abb wool', '', 'See Abb.');
  INSERT INTO words (word, word_type, description) VALUES  (  'A B C', '', 'The first three letters of the alphabet, used for the whole\n   alphabet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'A B C', '', 'A primer for teaching the alphabet and first elements of\n   reading.');
  INSERT INTO words (word, word_type, description) VALUES  (  'A B C', '', 'The simplest rudiments of any subject; as, the A B C of\n   finance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abdal', 'n.', 'A religious devotee or dervish in Persia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abderian', 'a.', 'Given to laughter; inclined to foolish or incessant\n   merriment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abderite', 'n.', 'An inhabitant of Abdera, in Thrace.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abdest', 'n.', 'Purification by washing the hands before prayer; -- a\n   Mohammedan rite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abdicable', 'a.', 'Capable of being abdicated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abdicant', 'a.', 'Abdicating; renouncing; -- followed by of.');
