  INSERT INTO words (word, word_type, description) VALUES  (  'Abrahamic', 'a.', 'Pertaining to Abraham, the patriarch; as, the Abrachamic\n   covenant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrahamitic', 'a.', 'Alt. of ical');
  INSERT INTO words (word, word_type, description) VALUES  (  'ical', 'a.', 'Relating to the patriarch Abraham.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abraham-man', 'n.', 'Alt. of Abram-man');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abram-man', 'n.', 'One of a set of vagabonds who formerly roamed through\n   England, feigning lunacy for the sake of obtaining alms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abraid', 'v. t. & i.', 'To awake; to arouse; to stir or start up; also, to\n   shout out.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abranchial', 'a.', 'Abranchiate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abranchiata', 'n. pl.', 'A group of annelids, so called because the species\n   composing it have no special organs of respiration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abranchiate', 'a.', 'Without gills.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrase', 'a.', 'Rubbed smooth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrasion', 'n.', 'The act of abrading, wearing, or rubbing off; the wearing\n   away by friction; as, the abrasion of coins.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrasion', 'n.', 'The substance rubbed off.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrasion', 'n.', 'A superficial excoriation, with loss of substance under\n   the form of small shreds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrasive', 'a.', 'Producing abrasion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abraum', 'n.', 'Alt. of Abraum salts');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abraum salts', 'n.', 'A red ocher used to darken mahogany and for making\n   chloride of potassium.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abraxas', 'n.', 'A mystical word used as a charm and engraved on gems among\n   the ancients; also, a gem stone thus engraved.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abray', 'v.', 'See Abraid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abreast', 'adv.', 'Side by side, with breasts in a line; as, \"Two men could\n   hardly walk abreast.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abreast', 'adv.', 'Side by side; also, opposite; over against; on a line\n   with the vessels beam; -- with of.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abreast', 'adv.', 'Up to a certain level or line; equally advanced; as, to\n   keep abreast of [or with] the present state of science.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abreast', 'adv.', 'At the same time; simultaneously.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abregge', 'v. t.', 'See Abridge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrenounce', 'v. t.', 'To renounce.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrenunciation', 'n.', 'Absolute renunciation or repudiation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abreption', 'n.', 'A snatching away.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abreuvoir', 'n.', 'The joint or interstice between stones, to be filled\n   with mortar.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abricock', 'n.', 'See Apricot.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abridged', 'imp. & p. p.', 'of Abridge');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abridging', 'p. pr. & vb. n.', 'of Abridge');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abridge', 'v. t.', 'To make shorter; to shorten in duration; to lessen; to\n   diminish; to curtail; as, to abridge labor; to abridge power or rights.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abridge', 'v. t.', 'To shorten or contract by using fewer words, yet\n   retaining the sense; to epitomize; to condense; as, to abridge a\n   history or dictionary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abridge', 'v. t.', 'To deprive; to cut off; -- followed by of, and formerly\n   by from; as, to abridge one of his rights.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abridger', 'n.', 'One who abridges.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abridgment', 'n.', 'The act of abridging, or the state of being abridged;\n   diminution; lessening; reduction or deprivation; as, an abridgment of\n   pleasures or of expenses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abridgment', 'n.', 'An epitome or compend, as of a book; a shortened or\n   abridged form; an abbreviation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abridgment', 'n.', 'That which abridges or cuts short; hence, an\n   entertainment that makes the time pass quickly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abroach', 'v. t.', 'To set abroach; to let out, as liquor; to broach; to\n   tap.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abroach', 'adv.', 'Broached; in a condition for letting out or yielding\n   liquor, as a cask which is tapped.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abroach', 'adv.', 'Hence: In a state to be diffused or propagated; afoot;\n   astir.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abroad', 'adv.', 'At large; widely; broadly; over a wide space; as, a tree\n   spreads its branches abroad.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abroad', 'adv.', 'Without a certain confine; outside the house; away from\n   ones abode; as, to walk abroad.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abroad', 'adv.', 'Beyond the bounds of a country; in foreign countries; as,\n   we have broils at home and enemies abroad.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abroad', 'adv.', 'Before the public at large; throughout society or the\n   world; here and there; widely.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrogable', 'a.', 'Capable of being abrogated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrogate', 'a.', 'Abrogated; abolished.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrogated', 'imp. & p. p.', 'of Abrogate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrogating', 'p. pr. & vb. n.', 'of Abrogate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrogate', 'v. t.', 'To annul by an authoritative act; to abolish by the\n   authority of the maker or his successor; to repeal; -- applied to the\n   repeal of laws, decrees, ordinances, the abolition of customs, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrogate', 'v. t.', 'To put an end to; to do away with.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrogation', 'n.', 'The act of abrogating; repeal by authority.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrogative', 'a.', 'Tending or designed to abrogate; as, an abrogative law.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrogator', 'n.', 'One who repeals by authority.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrood', 'adv.', 'In the act of brooding.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrook', 'v. t.', 'To brook; to endure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrupt', 'a.', 'Broken off; very steep, or craggy, as rocks, precipices,\n   banks; precipitous; steep; as, abrupt places.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrupt', 'a.', 'Without notice to prepare the mind for the event; sudden;\n   hasty; unceremonious.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrupt', 'a.', 'Having sudden transitions from one subject to another;\n   unconnected.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrupt', 'a.', 'Suddenly terminating, as if cut off.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrupt', 'n.', 'An abrupt place.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abrupt', 'v. t.', 'To tear off or asunder.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abruption', 'n.', 'A sudden breaking off; a violent separation of bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abruptly', 'adv.', 'In an abrupt manner; without giving notice, or without\n   the usual forms; suddenly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abruptly', 'adv.', 'Precipitously.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abruptness', 'n.', 'The state of being abrupt or broken; craggedness;\n   ruggedness; steepness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abruptness', 'n.', 'Suddenness; unceremonious haste or vehemence; as,\n   abruptness of style or manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abscesses', 'pl. ', 'of Abscess');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abscess', 'n.', 'A collection of pus or purulent matter in any tissue or\n   organ of the body, the result of a morbid process.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abscession', 'n.', 'A separating; removal; also, an abscess.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abscind', 'v. t.', 'To cut off.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abscision', 'n.', 'See Abscission.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abscisses', 'pl. ', 'of Absciss');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absciss', 'n.', 'See Abscissa.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abscissas', 'pl. ', 'of Abscissa');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abscissae', 'pl. ', 'of Abscissa');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abscissa', 'n.', 'One of the elements of reference by which a point, as of\n   a curve, is referred to a system of fixed rectilineal coordinate axes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abscission', 'n.', 'The act or process of cutting off.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abscission', 'n.', 'The state of being cut off.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abscission', 'n.', 'A figure of speech employed when a speaker having begun\n   to say a thing stops abruptly: thus, \"He is a man of so much honor and\n   candor, and of such generosity -- but I need say no more.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absconded', 'imp. & p. p.', 'of Abscond');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absconding', 'p. pr. & vb. n.', 'of Abscond');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abscond', 'v. i.', 'To hide, withdraw, or be concealed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abscond', 'v. i.', 'To depart clandestinely; to steal off and secrete ones\n   self; -- used especially of persons who withdraw to avoid a legal\n   process; as, an absconding debtor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abscond', 'v. t.', 'To hide; to conceal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abscondence', 'n.', 'Fugitive concealment; secret retirement; hiding.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absconder', 'n.', 'One who absconds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absence', 'n.', 'A state of being absent or withdrawn from a place or from\n   companionship; -- opposed to presence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absence', 'n.', 'Want; destitution; withdrawal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absence', 'n.', 'Inattention to things present; abstraction (of mind); as,\n   absence of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absent', 'a.', 'Being away from a place; withdrawn from a place; not\n   present.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absent', 'a.', 'Not existing; lacking; as, the part was rudimental or\n   absent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absent', 'a.', 'Inattentive to what is passing; absent-minded; preoccupied;\n   as, an absent air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absented', 'imp. & p. p.', 'of Absent');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absenting', 'p. pr. & vb. n.', 'of Absent');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absent', 'v. t.', 'To take or withdraw (ones self) to such a distance as\n   to prevent intercourse; -- used with the reflexive pronoun.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absent', 'v. t.', 'To withhold from being present.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absentaneous', 'a.', 'Pertaining to absence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absentation', 'n.', 'The act of absenting ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absentee', 'n.', 'One who absents himself from his country, office, post,\n   or duty; especially, a landholder who lives in another country or\n   district than that where his estate is situated; as, an Irish absentee.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absenteeism', 'n.', 'The state or practice of an absentee; esp. the\n   practice of absenting ones self from the country or district where\n   ones estate is situated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absenter', 'n.', 'One who absents ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absently', 'adv.', 'In an absent or abstracted manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absentment', 'n.', 'The state of being absent; withdrawal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absent-minded', 'a.', 'Absent in mind; abstracted; preoccupied.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absentness', 'n.', 'The quality of being absent-minded.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absey-book', 'n.', 'An A-B-C book; a primer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absinthate', 'n.', 'A combination of absinthic acid with a base or positive\n   radical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absinth', 'n.', 'Alt. of Absinthe');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absinthe', 'n.', 'The plant absinthium or common wormwood.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absinthe', 'n.', 'A strong spirituous liqueur made from wormwood and brandy\n   or alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absinthial', 'a.', 'Of or pertaining to wormwood; absinthian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absinthian', 'n.', 'Of the nature of wormwood.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absinthiate', 'v. t.', 'To impregnate with wormwood.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absinthiated', 'a.', 'Impregnated with wormwood; as, absinthiated wine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absinthic', 'a.', 'Relating to the common wormwood or to an acid obtained\n   from it.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absinthin', 'n.', 'The bitter principle of wormwood (Artemisia absinthium).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absinthism', 'n.', 'The condition of being poisoned by the excessive use of\n   absinth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absinthium', 'n.', 'The common wormwood (Artemisia absinthium); an\n   intensely bitter plant, used as a tonic and for making the oil of\n   wormwood.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absis', 'n.', 'See Apsis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absist', 'v. i.', 'To stand apart from; top leave off; to desist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absistence', 'n.', 'A standing aloof.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolute', 'a.', 'Loosed from any limitation or condition; uncontrolled;\n   unrestricted; unconditional; as, absolute authority, monarchy,\n   sovereignty, an absolute promise or command; absolute power; an\n   absolute monarch.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolute', 'a.', 'Complete in itself; perfect; consummate; faultless; as,\n   absolute perfection; absolute beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolute', 'a.', 'Viewed apart from modifying influences or without\n   comparison with other objects; actual; real; -- opposed to relative and\n   comparative; as, absolute motion; absolute time or space.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolute', 'a.', 'Loosed from, or unconnected by, dependence on any other\n   being; self-existent; self-sufficing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolute', 'a.', 'Capable of being thought or conceived by itself alone;\n   unconditioned; non-relative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolute', 'a.', 'Positive; clear; certain; not doubtful.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolute', 'a.', 'Authoritative; peremptory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolute', 'a.', 'Pure; unmixed; as, absolute alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolute', 'a.', 'Not immediately dependent on the other parts of the\n   sentence in government; as, the case absolute. See Ablative absolute,\n   under Ablative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolute', 'n.', 'In a plane, the two imaginary circular points at\n   infinity; in space of three dimensions, the imaginary circle at\n   infinity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolutely', 'adv.', 'In an absolute, independent, or unconditional manner;\n   wholly; positively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absoluteness', 'n.', 'The quality of being absolute; independence of\n   everything extraneous; unlimitedness; absolute power; independent\n   reality; positiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolution', 'n.', 'An absolving, or setting free from guilt, sin, or\n   penalty; forgiveness of an offense.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolution', 'n.', 'An acquittal, or sentence of a judge declaring and\n   accused person innocent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolution', 'n.', 'The exercise of priestly jurisdiction in the sacrament\n   of penance, by which Catholics believe the sins of the truly penitent\n   are forgiven.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolution', 'n.', 'An absolving from ecclesiastical penalties, -- for\n   example, excommunication.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolution', 'n.', 'The form of words by which a penitent is absolved.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolution', 'n.', 'Delivery, in speech.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolutism', 'n.', 'The state of being absolute; the system or doctrine of\n   the absolute; the principles or practice of absolute or arbitrary\n   government; despotism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolutism', 'n.', 'Doctrine of absolute decrees.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolutist', 'n.', 'One who is in favor of an absolute or autocratic\n   government.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolutist', 'n.', 'One who believes that it is possible to realize a\n   cognition or concept of the absolute.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolutist', 'a.', 'Of or pertaining to absolutism; arbitrary; despotic;\n   as, absolutist principles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolutistic', 'a.', 'Pertaining to absolutism; absolutist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolutory', 'a.', 'Serving to absolve; absolving.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolvable', 'a.', 'That may be absolved.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolvatory', 'a.', 'Conferring absolution; absolutory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolved', 'imp. & p. p.', 'of Absolve');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolving', 'p. pr. & vb. n.', 'of Absolve');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolve', 'v. t.', 'To set free, or release, as from some obligation, debt,\n   or responsibility, or from the consequences of guilt or such ties as it\n   would be sin or guilt to violate; to pronounce free; as, to absolve a\n   subject from his allegiance; to absolve an offender, which amounts to\n   an acquittal and remission of his punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolve', 'v. t.', 'To free from a penalty; to pardon; to remit (a sin); --\n   said of the sin or guilt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolve', 'v. t.', 'To finish; to accomplish.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolve', 'v. t.', 'To resolve or explain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolvent', 'a.', 'Absolving.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolvent', 'n.', 'An absolver.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absolver', 'n.', 'One who absolves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absonant', 'a.', 'Discordant; contrary; -- opposed to consonant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absonous', 'a.', 'Discordant; inharmonious; incongruous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorbed', 'imp. & p. p.', 'of Absorb');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorbing', 'p. pr. & vb. n.', 'of Absorb');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorb', 'v. t.', 'To swallow up; to engulf; to overwhelm; to cause to\n   disappear as if by swallowing up; to use up; to include.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorb', 'v. t.', 'To suck up; to drink in; to imbibe; as a sponge or as\n   the lacteals of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorb', 'v. t.', 'To engross or engage wholly; to occupy fully; as,\n   absorbed in study or the pursuit of wealth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorb', 'v. t.', 'To take up by cohesive, chemical, or any molecular\n   action, as when charcoal absorbs gases. So heat, light, and electricity\n   are absorbed or taken up in the substances into which they pass.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorbability', 'n.', 'The state or quality of being absorbable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorbable', 'a.', 'Capable of being absorbed or swallowed up.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorbedly', 'adv.', 'In a manner as if wholly engrossed or engaged.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorbency', 'n.', 'Absorptiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorbent', 'a.', 'Absorbing; swallowing; absorptive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorbent', 'n.', 'Anything which absorbs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorbent', 'n.', 'Any substance which absorbs and neutralizes acid fluid\n   in the stomach and bowels, as magnesia, chalk, etc.; also a substance\n   e. g., iodine) which acts on the absorbent vessels so as to reduce\n   enlarged and indurated parts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorbent', 'n.', 'The vessels by which the processes of absorption are\n   carried on, as the lymphatics in animals, the extremities of the roots\n   in plants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorber', 'n.', 'One who, or that which, absorbs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorbing', 'a.', 'Swallowing, engrossing; as, an absorbing pursuit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorbition', 'n.', 'Absorption.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorpt', 'a.', 'Absorbed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorption', 'n.', 'The act or process of absorbing or sucking in anything,\n   or of being absorbed and made to disappear; as, the absorption of\n   bodies in a whirlpool, the absorption of a smaller tribe into a larger.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorption', 'n.', 'An imbibing or reception by molecular or chemical\n   action; as, the absorption of light, heat, electricity, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorption', 'n.', 'In living organisms, the process by which the materials\n   of growth and nutrition are absorbed and conveyed to the tissues and\n   organs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorption', 'n.', 'Entire engrossment or occupation of the mind; as,\n   absorption in some employment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorptive', 'a.', 'Having power, capacity, or tendency to absorb or\n   imbibe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorptiveness', 'n.', 'The quality of being absorptive; absorptive power.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absorptivity', 'n.', 'Absorptiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absquatulate', 'v. i.', 'To take ones self off; to decamp.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absque hoc', '', 'The technical words of denial used in traversing what has\n   been alleged, and is repeated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstained', 'imp. & p. p.', 'of Abstain');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstaining', 'p. pr. & vb. n.', 'of Abstain');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstain', 'v. i.', 'To hold ones self aloof; to forbear or refrain\n   voluntarily, and especially from an indulgence of the passions or\n   appetites; -- with from.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstain', 'v. t.', 'To hinder; to withhold.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstainer', 'n.', 'One who abstains; esp., one who abstains from the use of\n   intoxicating liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstemious', 'a.', 'Abstaining from wine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstemious', 'a.', 'Sparing in diet; refraining from a free use of food and\n   strong drinks; temperate; abstinent; sparing in the indulgence of the\n   appetite or passions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstemious', 'a.', 'Sparingly used; used with temperance or moderation; as,\n   an abstemious diet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstemious', 'a.', 'Marked by, or spent in, abstinence; as, an abstemious\n   life.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstemious', 'a.', 'Promotive of abstemiousness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstemiousness', 'n.', 'The quality of being abstemious, temperate, or\n   sparing in the use of food and strong drinks. It expresses a greater\n   degree of abstinence than temperance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstention', 'a.', 'The act of abstaining; a holding aloof.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstentious', 'a.', 'Characterized by abstinence; self-restraining.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absterge', 'v. t.', 'To make clean by wiping; to wipe away; to cleanse;\n   hence, to purge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstergent', 'a.', 'Serving to cleanse, detergent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstergent', 'n.', 'A substance used in cleansing; a detergent; as, soap is\n   an abstergent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absterse', 'v. t.', 'To absterge; to cleanse; to purge away.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstersion', 'n.', 'Act of wiping clean; a cleansing; a purging.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstersive', 'a.', 'Cleansing; purging.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstersive', 'n.', 'Something cleansing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstersiveness', 'n.', 'The quality of being abstersive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstinence', 'n.', 'The act or practice of abstaining; voluntary\n   forbearance of any action, especially the refraining from an indulgence\n   of appetite, or from customary gratifications of animal or sensual\n   propensities. Specifically, the practice of abstaining from\n   intoxicating beverages, -- called also total abstinence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstinence', 'n.', 'The practice of self-denial by depriving ones self of\n   certain kinds of food or drink, especially of meat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstinency', 'n.', 'Abstinence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstinent', 'a.', 'Refraining from indulgence, especially from the\n   indulgence of appetite; abstemious; continent; temperate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstinent', 'n.', 'One who abstains.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstinent', 'n.', 'One of a sect who appeared in France and Spain in the 3d\n   century.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstinently', 'adv.', 'With abstinence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstorted', 'a.', 'Wrested away.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstract', 'a.', 'Withdraw; separate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstract', 'a.', 'Considered apart from any application to a particular\n   object; separated from matter; existing in the mind only; as, abstract\n   truth, abstract numbers. Hence: ideal; abstruse; difficult.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstract', 'a.', 'Expressing a particular property of an object viewed\n   apart from the other properties which constitute it; -- opposed to\n   concrete; as, honesty is an abstract word.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstract', 'a.', 'Resulting from the mental faculty of abstraction; general\n   as opposed to particular; as, \"reptile\" is an abstract or general name.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstract', 'a.', 'Abstracted; absent in mind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstracted', 'imp. & p. p.', 'of Abstract');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstracting', 'p. pr. & vb. n.', 'of Abstract');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstract', 'a.', 'To withdraw; to separate; to take away.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstract', 'a.', 'To draw off in respect to interest or attention; as, his\n   was wholly abstracted by other objects.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstract', 'a.', 'To separate, as ideas, by the operation of the mind; to\n   consider by itself; to contemplate separately, as a quality or\n   attribute.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstract', 'a.', 'To epitomize; to abridge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstract', 'a.', 'To take secretly or dishonestly; to purloin; as, to\n   abstract goods from a parcel, or money from a till.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstract', 'a.', 'To separate, as the more volatile or soluble parts of a\n   substance, by distillation or other chemical processes. In this sense\n   extract is now more generally used.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstract', 'v. t.', 'To perform the process of abstraction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstract', 'a.', 'That which comprises or concentrates in itself the\n   essential qualities of a larger thing or of several things.\n   Specifically: A summary or an epitome, as of a treatise or book, or of\n   a statement; a brief.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstract', 'a.', 'A state of separation from other things; as, to consider\n   a subject in the abstract, or apart from other associated things.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstract', 'a.', 'An abstract term.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstract', 'a.', 'A powdered solid extract of a vegetable substance mixed\n   with sugar of milk in such proportion that one part of the abstract\n   represents two parts of the original substance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstracted', 'a.', 'Separated or disconnected; withdrawn; removed; apart.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstracted', 'a.', 'Separated from matter; abstract; ideal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstracted', 'a.', 'Abstract; abstruse; difficult.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstracted', 'a.', 'Inattentive to surrounding objects; absent in mind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstractedly', 'adv.', 'In an abstracted manner; separately; with absence\n   of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstractedness', 'n.', 'The state of being abstracted; abstract character.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstracter', 'n.', 'One who abstracts, or makes an abstract.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstraction', 'a.', 'The act of abstracting, separating, or withdrawing, or\n   the state of being withdrawn; withdrawal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstraction', 'a.', 'The act process of leaving out of consideration one or\n   more properties of a complex object so as to attend to others;\n   analysis. Thus, when the mind considers the form of a tree by itself,\n   or the color of the leaves as separate from their size or figure, the\n   act is called abstraction. So, also, when it considers whiteness,\n   softness, virtue, existence, as separate from any particular objects.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstraction', 'a.', 'An idea or notion of an abstract, or theoretical\n   nature; as, to fight for mere abstractions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstraction', 'a.', 'A separation from worldly objects; a recluse life; as,\n   a hermits abstraction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstraction', 'a.', 'Absence or absorption of mind; inattention to present\n   objects.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstraction', 'a.', 'The taking surreptitiously for ones own use part of\n   the property of another; purloining.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstraction', 'a.', 'A separation of volatile parts by the act of\n   distillation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstractional', 'a.', 'Pertaining to abstraction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstractionist', 'n.', 'An idealist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstractitious', 'a.', 'Obtained from plants by distillation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstractive', 'a.', 'Having the power of abstracting; of an abstracting\n   nature.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstractively', 'adv.', 'In a abstract manner; separately; in or by itself.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstractiveness', 'n.', 'The quality of being abstractive; abstractive\n   property.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstractly', 'adv.', 'In an abstract state or manner; separately;\n   absolutely; by itself; as, matter abstractly considered.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstractness', 'n.', 'The quality of being abstract.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstringe', 'v. t.', 'To unbind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstrude', 'v. t.', 'To thrust away.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstruse', 'a.', 'Concealed or hidden out of the way.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstruse', 'a.', 'Remote from apprehension; difficult to be comprehended or\n   understood; recondite; as, abstruse learning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstrusely', 'adv.', 'In an abstruse manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstruseness', 'n.', 'The quality of being abstruse; difficulty of\n   apprehension.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstrusion', 'n.', 'The act of thrusting away.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abstrusity', 'n.', 'Abstruseness; that which is abstruse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absume', 'v. t.', 'To consume gradually; to waste away.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absumption', 'n.', 'Act of wasting away; a consuming; extinction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absurd', 'a.', 'Contrary to reason or propriety; obviously and fiatly\n   opposed to manifest truth; inconsistent with the plain dictates of\n   common sense; logically contradictory; nonsensical; ridiculous; as, an\n   absurd person, an absurd opinion; an absurd dream.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absurd', 'n.', 'An absurdity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absurdities', 'pl. ', 'of Absurdity');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absurdity', 'n.', 'The quality of being absurd or inconsistent with obvious\n   truth, reason, or sound judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absurdity', 'n.', 'That which is absurd; an absurd action; a logical\n   contradiction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absurdly', 'adv.', 'In an absurd manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Absurdness', 'n.', 'Absurdity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abuna', 'n.', 'The Patriarch, or head of the Abyssinian Church.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abundance', 'n.', 'An overflowing fullness; ample sufficiency; great\n   plenty; profusion; copious supply; superfluity; wealth: -- strictly\n   applicable to quantity only, but sometimes used of number.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abundant', 'a.', 'Fully sufficient; plentiful; in copious supply; --\n   followed by in, rarely by with.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abundantly', 'adv.', 'In a sufficient degree; fully; amply; plentifully; in\n   large measure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aburst', 'adv.', 'In a bursting condition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abusable', 'a.', 'That may be abused.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abusage', 'n.', 'Abuse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abused', 'imp. & p. p.', 'of Abuse');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abusing', 'p. pr. & vb. n.', 'of Abuse');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abuse', 'v. t.', 'To put to a wrong use; to misapply; to misuse; to put to\n   a bad use; to use for a wrong purpose or end; to pervert; as, to abuse\n   inherited gold; to make an excessive use of; as, to abuse ones\n   authority.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abuse', 'v. t.', 'To use ill; to maltreat; to act injuriously to; to punish\n   or to tax excessively; to hurt; as, to abuse prisoners, to abuse ones\n   powers, ones patience.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abuse', 'v. t.', 'To revile; to reproach coarsely; to disparage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abuse', 'v. t.', 'To dishonor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abuse', 'v. t.', 'To violate; to ravish.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abuse', 'v. t.', 'To deceive; to impose on.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abuse', 'v. t.', 'Improper treatment or use; application to a wrong or bad\n   purpose; misuse; as, an abuse of our natural powers; an abuse of civil\n   rights, or of privileges or advantages; an abuse of language.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abuse', 'v. t.', 'Physical ill treatment; injury.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abuse', 'v. t.', 'A corrupt practice or custom; offense; crime; fault; as,\n   the abuses in the civil service.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abuse', 'v. t.', 'Vituperative words; coarse, insulting speech; abusive\n   language; virulent condemnation; reviling.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abuse', 'v. t.', 'Violation; rape; as, abuse of a female child.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abuseful', 'a.', 'Full of abuse; abusive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abuser', 'n.', 'One who abuses [in the various senses of the verb].');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abusion', 'v. t.', 'Evil or corrupt usage; abuse; wrong; reproach;\n   deception; cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abusive', 'a.', 'Wrongly used; perverted; misapplied.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abusive', 'a.', 'Given to misusing; also, full of abuses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abusive', 'a.', 'Practicing abuse; prone to ill treat by coarse, insulting\n   words or by other ill usage; as, an abusive author; an abusive fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abusive', 'a.', 'Containing abuse, or serving as the instrument of abuse;\n   vituperative; reproachful; scurrilous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abusive', 'a.', 'Tending to deceive; fraudulent; cheating.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abusively', 'adv.', 'In an abusive manner; rudely; with abusive language.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abusiveness', 'n.', 'The quality of being abusive; rudeness of language, or\n   violence to the person.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abutted', 'imp. & p. p.', 'of Abut');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abutting', 'p. pr. & vb. n.', 'of Abut');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abut', 'v. i.', 'To project; to terminate or border; to be contiguous; to\n   meet; -- with on, upon, or against; as, his land abuts on the road.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abutilon', 'n.', 'A genus of malvaceous plants of many species, found in\n   the torrid and temperate zones of both continents; -- called also\n   Indian mallow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abutment', 'n.', 'State of abutting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abutment', 'n.', 'That on or against which a body abuts or presses');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abutment', 'n.', 'The solid part of a pier or wall, etc., which receives\n   the thrust or lateral pressure of an arch, vault, or strut.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abutment', 'n.', 'A fixed point or surface from which resistance or\n   reaction is obtained, as the cylinder head of a steam engine, the\n   fulcrum of a lever, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abutment', 'n.', 'In breech-loading firearms, the block behind the barrel\n   which receives the pressure due to recoil.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abuttal', 'n.', 'The butting or boundary of land, particularly at the end;\n   a headland.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abutter', 'n.', 'One who, or that which, abuts. Specifically, the owner of\n   a contiguous estate; as, the abutters on a street or a river.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abuzz', 'a.', 'In a buzz; buzzing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abought', 'imp. & p. p.', 'of Abye');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aby', 'v. t. & i.', 'Alt. of Abye');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abye', 'v. t. & i.', 'To pay for; to suffer for; to atone for; to make\n   amends for; to give satisfaction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abye', 'v. t. & i.', 'To endure; to abide.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abysm', 'n.', 'An abyss; a gulf.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abysmal', 'a.', 'Pertaining to, or resembling, an abyss; bottomless;\n   unending; profound.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abysmally', 'adv.', 'To a fathomless depth; profoundly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abyss', 'n.', 'A bottomless or unfathomed depth, gulf, or chasm; hence, any\n   deep, immeasurable, and, specifically, hell, or the bottomless pit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abyss', 'n.', 'Infinite time; a vast intellectual or moral depth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abyss', 'n.', 'The center of an escutcheon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abyssal', 'a.', 'Belonging to, or resembling, an abyss; unfathomable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abyssinian', 'a.', 'Of or pertaining to Abyssinia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abyssinian', 'n.', 'A native of Abyssinia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Abyssinian', 'n.', 'A member of the Abyssinian Church.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acacia', 'n.', 'A roll or bag, filled with dust, borne by Byzantine\n   emperors, as a memento of mortality. It is represented on medals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acacias', 'pl. ', 'of Acacia');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acaciae', 'pl. ', 'of Acacia');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acacia', 'n.', 'A genus of leguminous trees and shrubs. Nearly 300 species\n   are Australian or Polynesian, and have terete or vertically compressed\n   leaf stalks, instead of the bipinnate leaves of the much fewer species\n   of America, Africa, etc. Very few are found in temperate climates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acacia', 'n.', 'The inspissated juice of several species of acacia; --\n   called also gum acacia, and gum arabic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acacin', 'n.', 'Alt. of Acacine');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acacine', 'n.', 'Gum arabic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academe', 'n.', 'An academy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academial', 'a.', 'Academic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academian', 'n.', 'A member of an academy, university, or college.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academic', 'a.', 'Alt. of Academical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academical', 'a.', 'Belonging to the school or philosophy of Plato; as, the\n   Academic sect or philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academical', 'a.', 'Belonging to an academy or other higher institution of\n   learning; scholarly; literary or classical, in distinction from\n   scientific.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academic', 'n.', 'One holding the philosophy of Socrates and Plato; a\n   Platonist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academic', 'n.', 'A member of an academy, college, or university; an\n   academician.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academically', 'adv.', 'In an academical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academicals', 'n. pl.', 'The articles of dress prescribed and worn at some\n   colleges and universities.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academician', 'n.', 'A member of an academy, or society for promoting\n   science, art, or literature, as of the French Academy, or the Royal\n   Academy of arts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academician', 'n.', 'A collegian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academicism', 'n.', 'A tenet of the Academic philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academicism', 'n.', 'A mannerism or mode peculiar to an academy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academism', 'n.', 'The doctrines of the Academic philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academist', 'n.', 'An Academic philosopher.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academist', 'n.', 'An academician.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academies', 'pl. ', 'of Academy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academy', 'n.', 'A garden or grove near Athens (so named from the hero\n   Academus); where Plato and his followers held their philosophical\n   conferences; hence, the school of philosophy of which Plato was head.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academy', 'n.', 'An institution for the study of higher learning; a college\n   or a university. Popularly, a school, or seminary of learning, holding\n   a rank between a college and a common school.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academy', 'n.', 'A place of training; a school.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academy', 'n.', 'A society of learned men united for the advancement of the\n   arts and sciences, and literature, or some particular art or science;\n   as, the French Academy; the American Academy of Arts and Sciences;\n   academies of literature and philology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Academy', 'n.', 'A school or place of training in which some special art is\n   taught; as, the military academy at West Point; a riding academy; the\n   Academy of Music.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acadian', 'a.', 'Of or pertaining to Acadie, or Nova Scotia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acadian', 'n.', 'A native of Acadie.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acajou', 'n.', 'The cashew tree; also, its fruit. See Cashew.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acajou', 'n.', 'The mahogany tree; also, its timber.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acalephs', 'pl. ', 'of Acalephan');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acalephans', 'pl. ', 'of Acalephan');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acaleph', 'n.', 'Alt. of Acalephan');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acalephan', 'n.', 'One of the Acalephae.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acalephae', 'n. pl.', 'A group of Coelenterata, including the Medusae or\n   jellyfishes, and hydroids; -- so called from the stinging power they\n   possess. Sometimes called sea nettles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acalephoid', 'a.', 'Belonging to or resembling the Acalephae or\n   jellyfishes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acalycine', 'a.', 'Alt. of Acalysinous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acalysinous', 'a.', 'Without a calyx, or outer floral envelope.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acanth', 'n.', 'Same as Acanthus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acantha', 'n.', 'A prickle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acantha', 'n.', 'A spine or prickly fin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acantha', 'n.', 'The vertebral column; the spinous process of a vertebra.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acanthaceous', 'a.', 'Armed with prickles, as a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acanthaceous', 'a.', 'Of, pertaining to, or resembling, the family of\n   plants of which the acanthus is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acanthine', 'a.', 'Of, pertaining to, or resembling, the plant acanthus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acanthocarpous', 'a.', 'Having the fruit covered with spines.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acanthocephala', 'n. pl.', 'A group of intestinal worms, having the\n   proboscis armed with recurved spines.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acanthocephalous', 'a.', 'Having a spiny head, as one of the\n   Acanthocephala.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acanthophorous', 'a.', 'Spine-bearing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acanthopodious', 'a.', 'Having spinous petioles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acanthopteri', 'n. pl.', 'A group of teleostean fishes having spiny fins.\n   See Acanthopterygii.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acanthopterous', 'a.', 'Spiny-winged.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acanthopterous', 'a.', 'Acanthopterygious.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acanthopterygian', 'a.', 'Belonging to the order of fishes having spinose\n   fins, as the perch.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acanthopterygian', 'n.', 'A spiny-finned fish.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acanthopterygii', 'n. pl.', 'An order of fishes having some of the rays of\n   the dorsal, ventral, and anal fins unarticulated and spinelike, as the\n   perch.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acanthopterygious', 'a.', 'Having fins in which the rays are hard and\n   spinelike; spiny-finned.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acanthuses', 'pl. ', 'of Acanthus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acanthi', 'pl. ', 'of Acanthus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acanthus', 'n.', 'A genus of herbaceous prickly plants, found in the south\n   of Europe, Asia Minor, and India; bears-breech.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acanthus', 'n.', 'An ornament resembling the foliage or leaves of the\n   acanthus (Acanthus spinosus); -- used in the capitals of the Corinthian\n   and Composite orders.');
  INSERT INTO words (word, word_type, description) VALUES  (  'A cappella', '', 'In church or chapel style; -- said of compositions sung\n   in the old church style, without instrumental accompaniment; as, a mass\n   a capella, i. e., a mass purely vocal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'A cappella', '', 'A time indication, equivalent to alla breve.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acapsular', 'a.', 'Having no capsule.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acardiac', 'a.', 'Without a heart; as, an acardiac fetus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acaridan', 'n.', 'One of a group of arachnids, including the mites and\n   ticks.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acarina', 'n. pl.', 'The group of Arachnida which includes the mites and\n   ticks. Many species are parasitic, and cause diseases like the itch and\n   mange.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acarine', 'a.', 'Of or caused by acari or mites; as, acarine diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acaroid', 'a.', 'Shaped like or resembling a mite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acarpellous', 'a.', 'Having no carpels.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acarpous', 'a.', 'Not producing fruit; unfruitful.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acari', 'pl. ', 'of Acarus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acarus', 'n.', 'A genus including many species of small mites.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acatalectic', 'a.', 'Not defective; complete; as, an acatalectic verse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acatalectic', 'n.', 'A verse which has the complete number of feet and\n   syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acatalepsy', 'n.', 'Incomprehensibility of things; the doctrine held by the\n   ancient Skeptic philosophers, that human knowledge never amounts to\n   certainty, but only to probability.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acataleptic', 'a.', 'Incapable of being comprehended; incomprehensible.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acater', 'n.', 'See Caterer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acates', 'n. pl.', 'See Cates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acaudate', 'a.', 'Tailless.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acaulescent', 'a.', 'Having no stem or caulis, or only a very short one\n   concealed in the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acauline', 'a.', 'Same as Acaulescent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acaulose', 'a.', 'Alt. of Acaulous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acaulous', 'a.', 'Same as Acaulescent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accadian', 'a.', 'Pertaining to a race supposed to have lived in Babylonia\n   before the Assyrian conquest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceded', 'imp. & p. p.', 'of Accede');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceding', 'p. pr. & vb. n.', 'of Accede');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accede', 'v. i.', 'To approach; to come forward; -- opposed to recede.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accede', 'v. i.', 'To enter upon an office or dignity; to attain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accede', 'v. i.', 'To become a party by associating ones self with others;\n   to give ones adhesion. Hence, to agree or assent to a proposal or a\n   view; as, he acceded to my request.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accedence', 'n.', 'The act of acceding.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceder', 'n.', 'One who accedes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accelerando', 'a.', 'Gradually accelerating the movement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accelerated', 'imp. & p. p.', 'of Accelerate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accelerating', 'p. pr. & vb. n.', 'of Accelerate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accelerate', 'v. t.', 'To cause to move faster; to quicken the motion of;\n   to add to the speed of; -- opposed to retard.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accelerate', 'v. t.', 'To quicken the natural or ordinary progression or\n   process of; as, to accelerate the growth of a plant, the increase of\n   wealth, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accelerate', 'v. t.', 'To hasten, as the occurence of an event; as, to\n   accelerate our departure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceleration', 'n.', 'The act of accelerating, or the state of being\n   accelerated; increase of motion or action; as, a falling body moves\n   toward the earth with an acceleration of velocity; -- opposed to\n   retardation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accelerative', 'a.', 'Relating to acceleration; adding to velocity;\n   quickening.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accelerator', 'n.', 'One who, or that which, accelerates. Also as an adj.;\n   as, accelerator nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceleratory', 'a.', 'Accelerative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accelerograph', 'n.', 'An apparatus for studying the combustion of powder\n   in guns, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accelerometer', 'n.', 'An apparatus for measuring the velocity imparted by\n   gunpowder.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accend', 'v. t.', 'To set on fire; to kindle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accendibility', 'n.', 'Capacity of being kindled, or of becoming inflamed;\n   inflammability.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accendible', 'a.', 'Capable of being inflamed or kindled; combustible;\n   inflammable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accension', 'n.', 'The act of kindling or the state of being kindled;\n   ignition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accensor', 'n.', 'One of the functionaries who light and trim the tapers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accent', 'n.', 'A superior force of voice or of articulative effort upon\n   some particular syllable of a word or a phrase, distinguishing it from\n   the others.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accent', 'n.', 'A mark or character used in writing, and serving to\n   regulate the pronunciation; esp.: (a) a mark to indicate the nature and\n   place of the spoken accent; (b) a mark to indicate the quality of sound\n   of the vowel marked; as, the French accents.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accent', 'n.', 'Modulation of the voice in speaking; manner of speaking or\n   pronouncing; peculiar or characteristic modification of the voice;\n   tone; as, a foreign accent; a French or a German accent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accent', 'n.', 'A word; a significant tone');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accent', 'n.', 'expressions in general; speech.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accent', 'n.', 'Stress laid on certain syllables of a verse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accent', 'n.', 'A regularly recurring stress upon the tone to mark the\n   beginning, and, more feebly, the third part of the measure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accent', 'n.', 'A special emphasis of a tone, even in the weaker part of\n   the measure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accent', 'n.', 'The rhythmical accent, which marks phrases and sections of\n   a period.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accent', 'n.', 'The expressive emphasis and shading of a passage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accent', 'n.', 'A mark placed at the right hand of a letter, and a little\n   above it, to distinguish magnitudes of a similar kind expressed by the\n   same letter, but differing in value, as y, y.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accent', 'n.', 'A mark at the right hand of a number, indicating minutes of\n   a degree, seconds, etc.; as, 1227, i. e., twelve minutes twenty\n   seven seconds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accent', 'n.', 'A mark used to denote feet and inches; as, 6 10 is six\n   feet ten inches.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accented', 'imp. & p. p.', 'of Accent');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accenting', 'p. pr. & vb. n.', 'of Accent');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accent', 'v. t.', 'To express the accent of (either by the voice or by a\n   mark); to utter or to mark with accent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accent', 'v. t.', 'To mark emphatically; to emphasize.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accentless', 'a.', 'Without accent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accentor', 'n.', 'One who sings the leading part; the director or leader.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accentor', 'n.', 'A genus of European birds (so named from their sweet\n   notes); including the hedge warbler. In America sometimes applied to\n   the water thrushes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accentuable', 'a.', 'Capable of being accented.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accentual', 'a.', 'Of or pertaining to accent; characterized or formed by\n   accent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accentuality', 'n.', 'The quality of being accentual.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accentually', 'adv.', 'In an accentual manner; in accordance with accent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accentuated', 'imp. & p. p.', 'of Accentuate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accentuating', 'p. pr. & vb. n.', 'of Accentuate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accentuate', 'v. t.', 'To pronounce with an accent or with accents.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accentuate', 'v. t.', 'To bring out distinctly; to make prominent; to\n   emphasize.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accentuate', 'v. t.', 'To mark with the written accent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accentuation', 'n.', 'Act of accentuating; applications of accent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accentuation', 'n.', 'pitch or modulation of the voice in reciting portions\n   of the liturgy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accepted', 'imp. & p. p.', 'of Accept');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accepting', 'p. pr. & vb. n.', 'of Accept');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accept', 'v. t.', 'To receive with a consenting mind (something offered);\n   as, to accept a gift; -- often followed by of.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accept', 'v. t.', 'To receive with favor; to approve.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accept', 'v. t.', 'To receive or admit and agree to; to assent to; as, I\n   accept your proposal, amendment, or excuse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accept', 'v. t.', 'To take by the mind; to understand; as, How are these\n   words to be accepted?');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accept', 'v. t.', 'To receive as obligatory and promise to pay; as, to\n   accept a bill of exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accept', 'v. t.', 'In a deliberate body, to receive in acquittance of a\n   duty imposed; as, to accept the report of a committee. [This makes it\n   the property of the body, and the question is then on its adoption.]');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accept', 'a.', 'Accepted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceptability', 'n.', 'The quality of being acceptable; acceptableness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceptable', 'a.', 'Capable, worthy, or sure of being accepted or received\n   with pleasure; pleasing to a receiver; gratifying; agreeable; welcome;\n   as, an acceptable present, one acceptable to us.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceptableness', 'n.', 'The quality of being acceptable, or suitable to be\n   favorably received; acceptability.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceptably', 'adv.', 'In an acceptable manner; in a manner to please or\n   give satisfaction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceptance', 'n.', 'The act of accepting; a receiving what is offered, with\n   approbation, satisfaction, or acquiescence; esp., favorable reception;\n   approval; as, the acceptance of a gift, office, doctrine, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceptance', 'n.', 'State of being accepted; acceptableness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceptance', 'n.', 'An assent and engagement by the person on whom a bill\n   of exchange is drawn, to pay it when due according to the terms of the\n   acceptance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceptance', 'n.', 'The bill itself when accepted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceptance', 'n.', 'An agreeing to terms or proposals by which a bargain is\n   concluded and the parties are bound; the reception or taking of a thing\n   bought as that for which it was bought, or as that agreed to be\n   delivered, or the taking possession as owner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceptance', 'n.', 'An agreeing to the action of another, by some act which\n   binds the person in law.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceptance', 'n.', 'Meaning; acceptation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceptancy', 'n.', 'Acceptance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceptant', 'a.', 'Accepting; receiving.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceptant', 'n.', 'An accepter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceptation', 'n.', 'Acceptance; reception; favorable reception or regard;\n   state of being acceptable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceptation', 'n.', 'The meaning in which a word or expression is\n   understood, or generally received; as, term is to be used according to\n   its usual acceptation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceptedly', 'adv.', 'In a accepted manner; admittedly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accepter', 'n.', 'A person who accepts; a taker.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accepter', 'n.', 'A respecter; a viewer with partiality.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accepter', 'n.', 'An acceptor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceptilation', 'n.', 'Gratuitous discharge; a release from debt or\n   obligation without payment; free remission.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acception', 'n.', 'Acceptation; the received meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceptive', 'a.', 'Fit for acceptance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceptive', 'a.', 'Ready to accept.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceptor', 'n.', 'One who accepts');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acceptor', 'n.', 'one who accepts an order or a bill of exchange; a drawee\n   after he has accepted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Access', 'n.', 'A coming to, or near approach; admittance; admission;\n   accessibility; as, to gain access to a prince.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Access', 'n.', 'The means, place, or way by which a thing may be\n   approached; passage way; as, the access is by a neck of land.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Access', 'n.', 'Admission to sexual intercourse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Access', 'n.', 'Increase by something added; addition; as, an access of\n   territory. [In this sense accession is more generally used.]');
  INSERT INTO words (word, word_type, description) VALUES  (  'Access', 'n.', 'An onset, attack, or fit of disease.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Access', 'n.', 'A paroxysm; a fit of passion; an outburst; as, an access of\n   fury.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accessarily', 'adv.', 'In the manner of an accessary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accessariness', 'n.', 'The state of being accessary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accessary', 'a.', 'Accompanying, as a subordinate; additional; accessory;\n   esp., uniting in, or contributing to, a crime, but not as chief actor.\n   See Accessory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accessaries', 'pl. ', 'of Accessary');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accessary', 'n.', 'One who, not being present, contributes as an assistant\n   or instigator to the commission of an offense.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accessibility', 'n.', 'The quality of being accessible, or of admitting\n   approach; receptibility.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accessible', 'a.', 'Easy of access or approach; approachable; as, an\n   accessible town or mountain, an accessible person.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accessible', 'a.', 'Open to the influence of; -- with to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accessible', 'a.', 'Obtainable; to be got at.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accessibly', 'adv.', 'In an accessible manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accession', 'n.', 'A coming to; the act of acceding and becoming joined;\n   as, a kings accession to a confederacy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accession', 'n.', 'Increase by something added; that which is added;\n   augmentation from without; as, an accession of wealth or territory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accession', 'n.', 'A mode of acquiring property, by which the owner of a\n   corporeal substance which receives an addition by growth, or by labor,\n   has a right to the part or thing added, or the improvement (provided\n   the thing is not changed into a different species). Thus, the owner of\n   a cow becomes the owner of her calf.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accession', 'n.', 'The act by which one power becomes party to engagements\n   already in force between other powers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accession', 'n.', 'The act of coming to or reaching a throne, an office, or\n   dignity; as, the accession of the house of Stuart; -- applied\n   especially to the epoch of a new dynasty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accession', 'n.', 'The invasion, approach, or commencement of a disease; a\n   fit or paroxysm.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accessional', 'a.', 'Pertaining to accession; additional.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accessive', 'a.', 'Additional.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accessorial', 'a.', 'Of or pertaining to an accessory; as, accessorial\n   agency, accessorial guilt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accessorily', 'adv.', 'In the manner of an accessory; auxiliary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accessoriness', 'n.', 'The state of being accessory, or connected\n   subordinately.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accessory', 'a.', 'Accompanying as a subordinate; aiding in a secondary\n   way; additional; connected as an incident or subordinate to a\n   principal; contributing or contributory; said of persons and things,\n   and, when of persons, usually in a bad sense; as, he was accessory to\n   the riot; accessory sounds in music.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accessories', 'pl. ', 'of Accessory');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accessory', 'n.', 'That which belongs to something else deemed the\n   principal; something additional and subordinate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accessory', 'n.', 'Same as Accessary, n.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accessory', 'n.', 'Anything that enters into a work of art without being\n   indispensably necessary, as mere ornamental parts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acciaccatura', 'n.', 'A short grace note, one semitone below the note to\n   which it is prefixed; -- used especially in organ music. Now used as\n   equivalent to the short appoggiatura.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accidence', 'n.', 'The accidents, of inflections of words; the rudiments of\n   grammar.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accidence', 'n.', 'The rudiments of any subject.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accident', 'n.', 'Literally, a befalling; an event that takes place without\n   ones foresight or expectation; an undesigned, sudden, and unexpected\n   event; chance; contingency; often, an undesigned and unforeseen\n   occurrence of an afflictive or unfortunate character; a casualty; a\n   mishap; as, to die by an accident.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accident', 'n.', 'A property attached to a word, but not essential to it,\n   as gender, number, case.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accident', 'n.', 'A point or mark which may be retained or omitted in a\n   coat of arms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accident', 'n.', 'A property or quality of a thing which is not essential\n   to it, as whiteness in paper; an attribute.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accident', 'n.', 'A quality or attribute in distinction from the substance,\n   as sweetness, softness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accident', 'n.', 'Any accidental property, fact, or relation; an accidental\n   or nonessential; as, beauty is an accident.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accident', 'n.', 'Unusual appearance or effect.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accidental', 'a.', 'Happening by chance, or unexpectedly; taking place not\n   according to the usual course of things; casual; fortuitous; as, an\n   accidental visit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accidental', 'a.', 'Nonessential; not necessary belonging; incidental; as,\n   are accidental to a play.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accidental', 'n.', 'A property which is not essential; a nonessential;\n   anything happening accidentally.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accidental', 'n.', 'Those fortuitous effects produced by luminous rays\n   falling on certain objects so that some parts stand forth in abnormal\n   brightness and other parts are cast into a deep shadow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accidental', 'n.', 'A sharp, flat, or natural, occurring not at the\n   commencement of a piece of music as the signature, but before a\n   particular note.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accidentalism', 'n.', 'Accidental character or effect.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accidentality', 'n.', 'The quality of being accidental; accidentalness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accidentally', 'adv.', 'In an accidental manner; unexpectedly; by chance;\n   unintentionally; casually; fortuitously; not essentially.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accidentalness', 'n.', 'The quality of being accidental; casualness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accidie', 'n.', 'Sloth; torpor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accipenser', 'n.', 'See Acipenser.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accipient', 'n.', 'A receiver.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accipiters', 'pl. ', 'of Accipiter');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accipitres', 'pl. ', 'of Accipiter');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accipiter', 'n.', 'A genus of rapacious birds; one of the Accipitres or\n   Raptores.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accipiter', 'n.', 'A bandage applied over the nose, resembling the claw of\n   a hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accipitral', 'n.', 'Pertaining to, or of the nature of, a falcon or hawk;\n   hawklike.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accipitres', 'n. pl.', 'The order that includes rapacious birds. They have\n   a hooked bill, and sharp, strongly curved talons. There are three\n   families, represented by the vultures, the falcons or hawks, and the\n   owls.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accipitrine', 'a.', 'Like or belonging to the Accipitres; raptorial;\n   hawklike.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accismus', 'n.', 'Affected refusal; coyness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accite', 'v. t.', 'To cite; to summon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclaim', 'v. t.', 'To applaud.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclaim', 'v. t.', 'To declare by acclamations.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclaim', 'v. t.', 'To shout; as, to acclaim my joy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclaim', 'v. i.', 'To shout applause.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclaim', 'n.', 'Acclamation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclaimer', 'n.', 'One who acclaims.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclamation', 'n.', 'A shout of approbation, favor, or assent; eager\n   expression of approval; loud applause.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclamation', 'n.', 'A representation, in sculpture or on medals, of people\n   expressing joy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclamatory', 'a.', 'Pertaining to, or expressing approval by, acclamation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclimatable', 'a.', 'Capable of being acclimated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclimatation', 'n.', 'Acclimatization.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclimated', 'imp. & p. p.', 'of Acclimate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclimating', 'p. pr. & vb. n.', 'of Acclimate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclimate', 'v. t.', 'To habituate to a climate not native; to acclimatize.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclimatement', 'n.', 'Acclimation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclimation', 'n.', 'The process of becoming, or the state of being,\n   acclimated, or habituated to a new climate; acclimatization.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclimatizable', 'a.', 'Capable of being acclimatized.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclimatization', 'n.', 'The act of acclimatizing; the process of inuring\n   to a new climate, or the state of being so inured.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclimatized', 'imp. & p. p.', 'of Acclimatize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclimatizing', 'p. pr. & vb. n.', 'of Acclimatize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclimatize', 'v. t.', 'To inure or habituate to a climate different from\n   that which is natural; to adapt to the peculiarities of a foreign or\n   strange climate; said of man, the inferior animals, or plants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclimature', 'n.', 'The act of acclimating, or the state of being\n   acclimated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclive', 'a.', 'Acclivous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclivitous', 'a.', 'Acclivous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclivities', 'pl. ', 'of Acclivity');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclivity', 'n.', 'A slope or inclination of the earth, as the side of a\n   hill, considered as ascending, in opposition to declivity, or\n   descending; an upward slope; ascent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acclivous', 'a.', 'Sloping upward; rising as a hillside; -- opposed to\n   declivous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accloy', 'v. t.', 'To fill to satiety; to stuff full; to clog; to overload;\n   to burden. See Cloy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accoast', 'v. t. & i.', 'To lie or sail along the coast or side of; to\n   accost.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accoil', 'v. t.', 'To gather together; to collect.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accoil', 'v. t.', 'To coil together.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accolade', 'n.', 'A ceremony formerly used in conferring knighthood,\n   consisting am embrace, and a slight blow on the shoulders with the flat\n   blade of a sword.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accolade', 'n.', 'A brace used to join two or more staves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accombination', 'n.', 'A combining together.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accommodable', 'a.', 'That may be accommodated, fitted, or made to agree.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accommodableness', 'n.', 'The quality or condition of being accommodable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accommodated', 'imp. & p. p.', 'of Accommodate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accommodating', 'p. pr. & vb. n.', 'of Accommodate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accommodate', 'v. t.', 'To render fit, suitable, or correspondent; to\n   adapt; to conform; as, to accommodate ourselves to circumstances.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accommodate', 'v. t.', 'To bring into agreement or harmony; to reconcile;\n   to compose; to adjust; to settle; as, to accommodate differences, a\n   dispute, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accommodate', 'v. t.', 'To furnish with something desired, needed, or\n   convenient; to favor; to oblige; as, to accommodate a friend with a\n   loan or with lodgings.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accommodate', 'v. t.', 'To show the correspondence of; to apply or make\n   suit by analogy; to adapt or fit, as teachings to accidental\n   circumstances, statements to facts, etc.; as, to accommodate prophecy\n   to events.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accommodate', 'v. i.', 'To adapt ones self; to be conformable or adapted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accommodate', 'a.', 'Suitable; fit; adapted; as, means accommodate to end.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accommodately', 'adv.', 'Suitably; fitly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accommodateness', 'n.', 'Fitness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accommodating', 'a.', 'Affording, or disposed to afford, accommodation;\n   obliging; as an accommodating man, spirit, arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accommodation', 'n.', 'The act of fitting or adapting, or the state of\n   being fitted or adapted; adaptation; adjustment; -- followed by to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accommodation', 'n.', 'Willingness to accommodate; obligingness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accommodation', 'n.', 'Whatever supplies a want or affords ease,\n   refreshment, or convenience; anything furnished which is desired or\n   needful; -- often in the plural; as, the accommodations -- that is,\n   lodgings and food -- at an inn.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accommodation', 'n.', 'An adjustment of differences; state of agreement;\n   reconciliation; settlement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accommodation', 'n.', 'The application of a writers language, on the\n   ground of analogy, to something not originally referred to or intended.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accommodation', 'n.', 'A loan of money.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accommodation', 'n.', 'An accommodation bill or note.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accommodator', 'n.', 'He who, or that which, accommodates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accompanable', 'a.', 'Sociable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accompanier', 'n.', 'He who, or that which, accompanies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accompaniment', 'n.', 'That which accompanies; something that attends as a\n   circumstance, or which is added to give greater completeness to the\n   principal thing, or by way of ornament, or for the sake of symmetry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accompaniment', 'n.', 'A part performed by instruments, accompanying\n   another part or parts performed by voices; the subordinate part, or\n   parts, accompanying the voice or a principal instrument; also, the\n   harmony of a figured bass.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accompanist', 'n.', 'The performer in music who takes the accompanying\n   part.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accompanied', 'imp. & p. p.', 'of Accompany');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accompanying', 'p. pr. & vb. n.', 'of Accompany');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accompany', 'v. t.', 'To go with or attend as a companion or associate; to\n   keep company with; to go along with; -- followed by with or by; as, he\n   accompanied his speech with a bow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accompany', 'v. t.', 'To cohabit with.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accompany', 'v. i.', 'To associate in a company; to keep company.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accompany', 'v. i.', 'To cohabit (with).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accompany', 'v. i.', 'To perform an accompanying part or parts in a\n   composition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accompletive', 'a.', 'Tending to accomplish.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accomplice', 'n.', 'A cooperator.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accomplice', 'n.', 'An associate in the commission of a crime; a\n   participator in an offense, whether a principal or an accessory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accompliceship', 'n.', 'The state of being an accomplice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accomplicity', 'n.', 'The act or state of being an accomplice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accomplished', 'imp. & p. p.', 'of Accomplish');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accomplishing', 'p. pr. & vb. n.', 'of Accomplish');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accomplish', 'v. t.', 'To complete, as time or distance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accomplish', 'v. t.', 'To bring to an issue of full success; to effect; to\n   perform; to execute fully; to fulfill; as, to accomplish a design, an\n   object, a promise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accomplish', 'v. t.', 'To equip or furnish thoroughly; hence, to complete\n   in acquirements; to render accomplished; to polish.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accomplish', 'v. t.', 'To gain; to obtain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accomplishable', 'a.', 'Capable of being accomplished; practicable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accomplished', 'a.', 'Completed; effected; established; as, an accomplished\n   fact.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accomplished', 'a.', 'Complete in acquirements as the result usually of\n   training; -- commonly in a good sense; as, an accomplished scholar, an\n   accomplished villain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accomplisher', 'n.', 'One who accomplishes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accomplishment', 'n.', 'The act of accomplishing; entire performance;\n   completion; fulfillment; as, the accomplishment of an enterprise, of a\n   prophecy, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accomplishment', 'n.', 'That which completes, perfects, or equips\n   thoroughly; acquirement; attainment; that which constitutes excellence\n   of mind, or elegance of manners, acquired by education or training.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accompt', 'n.', 'See Account.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accomptable', 'a.', 'See Accountable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accomptant', 'n.', 'See Accountant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accord', 'v. t.', 'Agreement or concurrence of opinion, will, or action;\n   harmony of mind; consent; assent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accord', 'v. t.', 'Harmony of sounds; agreement in pitch and tone; concord;\n   as, the accord of tones.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accord', 'v. t.', 'Agreement, harmony, or just correspondence of things;\n   as, the accord of light and shade in painting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accord', 'v. t.', 'Voluntary or spontaneous motion or impulse to act; --\n   preceded by own; as, of ones own accord.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accord', 'v. t.', 'An agreement between parties in controversy, by which\n   satisfaction for an injury is stipulated, and which, when executed,\n   bars a suit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accorded', 'imp. & p. p.', 'of Accord');
  INSERT INTO words (word, word_type, description) VALUES  (  'According', 'p. pr. & vb. n.', 'of Accord');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accord', 'v. t.', 'To make to agree or correspond; to suit one thing to\n   another; to adjust; -- followed by to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accord', 'v. t.', 'To bring to an agreement, as persons; to reconcile; to\n   settle, adjust, harmonize, or compose, as things; as, to accord suits\n   or controversies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accord', 'v. t.', 'To grant as suitable or proper; to concede; to award;\n   as, to accord to one due praise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accord', 'v. i.', 'To agree; to correspond; to be in harmony; -- followed\n   by with, formerly also by to; as, his disposition accords with his\n   looks.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accord', 'v. i.', 'To agree in pitch and tone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accordable', 'a.', 'Agreeing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accordable', 'a.', 'Reconcilable; in accordance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accordance', 'n.', 'Agreement; harmony; conformity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accordancy', 'n.', 'Accordance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accordant', 'a.', 'Agreeing; consonant; harmonious; corresponding;\n   conformable; -- followed by with or to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accordantly', 'adv.', 'In accordance or agreement; agreeably; conformably;\n   -- followed by with or to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accorder', 'n.', 'One who accords, assents, or concedes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'According', 'p. a.', 'Agreeing; in agreement or harmony; harmonious.');
  INSERT INTO words (word, word_type, description) VALUES  (  'According', 'adv.', 'Accordingly; correspondingly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accordingly', 'adv.', 'Agreeably; correspondingly; suitably; in a manner\n   conformable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accordingly', 'adv.', 'In natural sequence; consequently; so.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accordion', 'n.', 'A small, portable, keyed wind instrument, whose tones\n   are generated by play of the wind upon free metallic reeds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accordionist', 'n.', 'A player on the accordion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accordment', 'v.', 'Agreement; reconcilement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accorporate', 'v. t.', 'To unite; to attach; to incorporate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accosted', 'imp. & p. p.', 'of Accost');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accosting', 'p. pr. & vb. n.', 'of Accost');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accost', 'v. t.', 'To join side to side; to border; hence, to sail along\n   the coast or side of.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accost', 'v. t.', 'To approach; to make up to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accost', 'v. t.', 'To speak to first; to address; to greet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accost', 'v. i.', 'To adjoin; to lie alongside.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accost', 'n.', 'Address; greeting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accostable', 'a.', 'Approachable; affable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accosted', 'a.', 'Supported on both sides by other charges; also, side by\n   side.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accouchement', 'n.', 'Delivery in childbed');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accoucheur', 'n.', 'A man who assists women in childbirth; a man midwife;\n   an obstetrician.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accoucheuse', 'n.', 'A midwife.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Account', 'n.', 'A reckoning; computation; calculation; enumeration; a\n   record of some reckoning; as, the Julian account of time.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Account', 'n.', 'A registry of pecuniary transactions; a written or printed\n   statement of business dealings or debts and credits, and also of other\n   things subjected to a reckoning or review; as, to keep ones account at\n   the bank.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Account', 'n.', 'A statement in general of reasons, causes, grounds, etc.,\n   explanatory of some event; as, no satisfactory account has been given\n   of these phenomena. Hence, the word is often used simply for reason,\n   ground, consideration, motive, etc.; as, on no account, on every\n   account, on all accounts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Account', 'n.', 'A statement of facts or occurrences; recital of\n   transactions; a relation or narrative; a report; a description; as, an\n   account of a battle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Account', 'n.', 'A statement and explanation or vindication of ones\n   conduct with reference to judgment thereon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Account', 'n.', 'An estimate or estimation; valuation; judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Account', 'n.', 'Importance; worth; value; advantage; profit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accounted', 'imp. & p. p.', 'of Account');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accounting', 'p. pr. & vb. n.', 'of Account');
  INSERT INTO words (word, word_type, description) VALUES  (  'Account', 'v. t.', 'To reckon; to compute; to count.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Account', 'v. t.', 'To place to ones account; to put to the credit of; to\n   assign; -- with to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Account', 'v. t.', 'To value, estimate, or hold in opinion; to judge or\n   consider; to deem.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Account', 'v. t.', 'To recount; to relate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Account', 'v. i.', 'To render or receive an account or relation of\n   particulars; as, an officer must account with or to the treasurer for\n   money received.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Account', 'v. i.', 'To render an account; to answer in judgment; -- with\n   for; as, we must account for the use of our opportunities.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Account', 'v. i.', 'To give a satisfactory reason; to tell the cause of; to\n   explain; -- with for; as, idleness accounts for poverty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accountability', 'n.', 'The state of being accountable; liability to be\n   called on to render an account; accountableness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accountable', 'a.', 'Liable to be called on to render an account;\n   answerable; as, every man is accountable to God for his conduct.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accountable', 'a.', 'Capable of being accounted for; explicable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accountable ness', 'n.', 'The quality or state of being accountable;\n   accountability.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accountably', 'adv.', 'In an accountable manner.');
