  INSERT INTO words (word, word_type, description) VALUES  (  'Accretion', 'n.', 'The act of increasing by natural growth; esp. the\n   increase of organic bodies by the internal accession of parts; organic\n   growth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accretion', 'n.', 'The act of increasing, or the matter added, by an\n   accession of parts externally; an extraneous addition; as, an accretion\n   of earth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accretion', 'n.', 'Concretion; coherence of separate particles; as, the\n   accretion of particles so as to form a solid mass.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accretion', 'n.', 'A growing together of parts naturally separate, as of\n   the fingers toes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accretion', 'n.', 'The adhering of property to something else, by which the\n   owner of one thing becomes possessed of a right to another; generally,\n   gain of land by the washing up of sand or sail from the sea or a river,\n   or by a gradual recession of the water from the usual watermark.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accretion', 'n.', 'Gain to an heir or legatee, failure of a coheir to the\n   same succession, or a co-legatee of the same thing, to take his share.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accretive', 'a.', 'Relating to accretion; increasing, or adding to, by\n   growth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accriminate', 'v. t.', 'To accuse of a crime.');
