  INSERT INTO words (word, word_type, description) VALUES  (  'Accumulative', 'a.', 'Characterized by accumulation; serving to collect or\n   amass; cumulative; additional.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accumulator', 'n.', 'One who, or that which, accumulates, collects, or\n   amasses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accumulator', 'n.', 'An apparatus by means of which energy or power can be\n   stored, such as the cylinder or tank for storing water for hydraulic\n   elevators, the secondary or storage battery used for accumulating the\n   energy of electrical charges, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accumulator', 'n.', 'A system of elastic springs for relieving the strain\n   upon a rope, as in deep-sea dredging.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accuracy', 'n.', 'The state of being accurate; freedom from mistakes, this\n   exemption arising from carefulness; exact conformity to truth, or to a\n   rule or model; precision; exactness; nicety; correctness; as, the value\n   of testimony depends on its accuracy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accurate', 'a.', 'In exact or careful conformity to truth, or to some\n   standard of requirement, the result of care or pains; free from\n   failure, error, or defect; exact; as, an accurate calculator; an\n   accurate measure; accurate expression, knowledge, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accurate', 'a.', 'Precisely fixed; executed with care; careful.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accurately', 'adv.', 'In an accurate manner; exactly; precisely; without\n   error or defect.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accurateness', 'n.', 'The state or quality of being accurate; accuracy;\n   exactness; nicety; precision.');
