  INSERT INTO words (word, word_type, description) VALUES  (  'Accursed', 'p. p. & a.', 'Alt. of Accurst');
  INSERT INTO words (word, word_type, description) VALUES  (  'Accurst', 'p. p. & a.', 'Doomed to destruction or misery; cursed; hence,\n   bad enough to be under the curse; execrable; detestable; exceedingly\n   hateful; -- as, an accursed deed.');
