  INSERT INTO words (word, word_type, description) VALUES  (  'Acephala', 'n. pl.', 'That division of the Mollusca which includes the\n   bivalve shells, like the clams and oysters; -- so called because they\n   have no evident head. Formerly the group included the Tunicata,\n   Brachiopoda, and sometimes the Bryozoa. See Mollusca.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acephalan', 'n.', 'Same as Acephal.');
