  INSERT INTO words (word, word_type, description) VALUES  (  'Acephali', 'n. pl.', 'A fabulous people reported by ancient writers to have\n   heads.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acephali', 'n. pl.', 'A Christian sect without a leader.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acephali', 'n. pl.', 'Bishops and certain clergymen not under regular\n   diocesan control.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acephali', 'n. pl.', 'A class of levelers in the time of K. Henry I.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acephalist', 'n.', 'One who acknowledges no head or superior.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acephalocyst', 'n.', 'A larval entozoon in the form of a subglobular or\n   oval vesicle, or hydatid, filled with fluid, sometimes found in the\n   tissues of man and the lower animals; -- so called from the absence of\n   a head or visible organs on the vesicle. These cysts are the immature\n   stages of certain tapeworms. Also applied to similar cysts of different\n   origin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acephalocystic', 'a.', 'Pertaining to, or resembling, the acephalocysts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acephalous', 'a.', 'Headless.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acephalous', 'a.', 'Without a distinct head; -- a term applied to bivalve\n   mollusks.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acephalous', 'a.', 'Having the style spring from the base, instead of from\n   the apex, as is the case in certain ovaries.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acephalous', 'a.', 'Without a leader or chief.');
