  INSERT INTO words (word, word_type, description) VALUES  (  'Acerbity', 'n.', 'Harshness, bitterness, or severity; as, acerbity of\n   temper, of language, of pain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aceric', 'a.', 'Pertaining to, or obtained from, the maple; as, aceric\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acerose', 'a.', 'Having the nature of chaff; chaffy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acerose', 'a.', 'Needle-shaped, having a sharp, rigid point, as the leaf of\n   the pine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acerous', 'a.', 'Same as Acerose.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acerous', 'a.', 'Destitute of tentacles, as certain mollusks.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acerous', 'a.', 'Without antennae, as some insects.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acerval', 'a.', 'Pertaining to a heap.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acervate', 'v. t.', 'To heap up.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acervate', 'a.', 'Heaped, or growing in heaps, or closely compacted\n   clusters.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acervation', 'n.', 'A heaping up; accumulation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acervative', 'a.', 'Heaped up; tending to heap up.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acervose', 'a.', 'Full of heaps.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acervuline', 'a.', 'Resembling little heaps.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acescence', 'n.', 'Alt. of Acescency');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acescency', 'n.', 'The quality of being acescent; the process of acetous\n   fermentation; a moderate degree of sourness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acescent', 'a.', 'Turning sour; readily becoming tart or acid; slightly\n   sour.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acescent', 'n.', 'A substance liable to become sour.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetable', 'n.', 'An acetabulum; or about one eighth of a pint.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetabular', 'a.', 'Cup-shaped; saucer-shaped; acetabuliform.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetabulifera', 'n. pl.', 'The division of Cephalopoda in which the arms\n   are furnished with cup-shaped suckers, as the cuttlefishes, squids, and\n   octopus; the Dibranchiata. See Cephalopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetabuliferous', 'a.', 'Furnished with fleshy cups for adhering to bodies,\n   as cuttlefish, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetabuliform', 'a.', 'Shaped like a shallow cup; saucer-shaped; as, an\n   acetabuliform calyx.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetabulum', 'n.', 'A vinegar cup; socket of the hip bone; a measure of\n   about one eighth of a pint, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetabulum', 'n.', 'The bony cup which receives the head of the thigh bone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetabulum', 'n.', 'The cavity in which the leg of an insect is inserted at\n   its articulation with the body.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetabulum', 'n.', 'A sucker of the sepia or cuttlefish and related\n   animals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetabulum', 'n.', 'The large posterior sucker of the leeches.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetabulum', 'n.', 'One of the lobes of the placenta in ruminating animals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetal', 'n.', 'A limpid, colorless, inflammable liquid from the slow\n   oxidation of alcohol under the influence of platinum black.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetaldehyde', 'n.', 'Acetic aldehyde. See Aldehyde.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetamide', 'n.', 'A white crystalline solid, from ammonia by replacement\n   of an equivalent of hydrogen by acetyl.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetanilide', 'n.', 'A compound of aniline with acetyl, used to allay fever\n   or pain; -- called also antifebrine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetarious', 'a.', 'Used in salads; as, acetarious plants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetary', 'n.', 'An acid pulp in certain fruits, as the pear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetate', 'n.', 'A salt formed by the union of acetic acid with a base or\n   positive radical; as, acetate of lead, acetate of potash.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetated', 'a.', 'Combined with acetic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetic', 'a.', 'Of a pertaining to vinegar; producing vinegar; producing\n   vinegar; as, acetic fermentation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetic', 'a.', 'Pertaining to, containing, or derived from, acetyl, as\n   acetic ether, acetic acid. The latter is the acid to which the sour\n   taste of vinegar is due.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetification', 'n.', 'The act of making acetous or sour; the process of\n   converting, or of becoming converted, into vinegar.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetifier', 'n.', 'An apparatus for hastening acetification.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetified', 'imp. & p. p.', 'of Acetify');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetifying', 'p. pr. & vb. n.', 'of Acetify');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetify', 'v. t.', 'To convert into acid or vinegar.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetify', 'v. i.', 'To turn acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetimeter', 'n.', 'An instrument for estimating the amount of acetic acid\n   in vinegar or in any liquid containing acetic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetimetry', 'n.', 'The act or method of ascertaining the strength of\n   vinegar, or the proportion of acetic acid contained in it.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetin', 'n.', 'A combination of acetic acid with glycerin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetize', 'v. i.', 'To acetify.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetometer', 'n.', 'Same as Acetimeter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetone', 'n.', 'A volatile liquid consisting of three parts of carbon, six\n   of hydrogen, and one of oxygen; pyroacetic spirit, -- obtained by the\n   distillation of certain acetates, or by the destructive distillation of\n   citric acid, starch, sugar, or gum, with quicklime.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetonic', 'a.', 'Of or pertaining to acetone; as, acetonic bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetose', 'a.', 'Sour like vinegar; acetous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetosity', 'n.', 'The quality of being acetous; sourness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetous', 'a.', 'Having a sour taste; sour; acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetous', 'a.', 'Causing, or connected with, acetification; as, acetous\n   fermentation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetyl', 'n.', 'A complex, hypothetical radical, composed of two parts of\n   carbon to three of hydrogen and one of oxygen. Its hydroxide is acetic\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acetylene', 'n.', 'A gaseous compound of carbon and hydrogen, in the\n   proportion of two atoms of the former to two of the latter. It is a\n   colorless gas, with a peculiar, unpleasant odor, and is produced for\n   use as an illuminating gas in a number of ways, but chiefly by the\n   action of water on calcium carbide. Its light is very brilliant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ach', 'n.', 'Alt. of Ache');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ache', 'n.', 'A name given to several species of plants; as, smallage, wild\n   celery, parsley.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achaean', 'a.', 'Alt. of Achaian');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achaian', 'a.', 'Of or pertaining to Achaia in Greece; also, Grecian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achaian', 'n.', 'A native of Achaia; a Greek.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acharnement', 'n.', 'Savage fierceness; ferocity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achate', 'n.', 'An agate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achate', 'n.', 'Purchase; bargaining.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achate', 'n.', 'Provisions. Same as Cates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achatina', 'n.', 'A genus of land snails, often large, common in the warm\n   parts of America and Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achatour', 'n.', 'Purveyor; acater.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ache', 'v. i.', 'Continued pain, as distinguished from sudden twinges, or\n   spasmodic pain. \"Such an ache in my bones.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ached', 'imp. & p. p.', 'of Ache');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aching', 'p. pr. & vb. n.', 'of Ache');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ache', 'v. i.', 'To suffer pain; to have, or be in, pain, or in continued\n   pain; to be distressed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achean', 'a & n.', 'See Achaean, Achaian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achene', 'n.', 'Alt. of Achenium');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achenium', 'n.', 'A small, dry, indehiscent fruit, containing a single\n   seed, as in the buttercup; -- called a naked seed by the earlier\n   botanists.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achenial', 'a.', 'Pertaining to an achene.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acheron', 'n.', 'A river in the Nether World or infernal regions; also, the\n   infernal regions themselves. By some of the English poets it was\n   supposed to be a flaming lake or gulf.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acherontic', 'a.', 'Of or pertaining to Acheron; infernal; hence, dismal,\n   gloomy; moribund.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achievable', 'a.', 'Capable of being achieved.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achievance', 'n.', 'Achievement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achieved', 'imp. & p. p.', 'of Achieve');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achieving', 'p. pr. & vb. n.', 'of Achieve');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achieve', 'v. t.', 'To carry on to a final close; to bring out into a\n   perfected state; to accomplish; to perform; -- as, to achieve a feat,\n   an exploit, an enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achieve', 'v. t.', 'To obtain, or gain, as the result of exertion; to\n   succeed in gaining; to win.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achieve', 'v. t.', 'To finish; to kill.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achievement', 'n.', 'The act of achieving or performing; an obtaining by\n   exertion; successful performance; accomplishment; as, the achievement\n   of his object.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achievement', 'n.', 'A great or heroic deed; something accomplished by\n   valor, boldness, or praiseworthy exertion; a feat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achievement', 'n.', 'An escutcheon or ensign armorial; now generally\n   applied to the funeral shield commonly called hatchment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achiever', 'n.', 'One who achieves; a winner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achillean', 'a.', 'Resembling Achilles, the hero of the Iliad; invincible.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achilles tendon', 'n.', 'The strong tendon formed of the united tendons of\n   the large muscles in the calf of the leg, an inserted into the bone of\n   the heel; -- so called from the mythological account of Achilles being\n   held by the heel when dipped in the River Styx.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achilous', 'a.', 'Without a lip.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aching', 'a.', 'That aches; continuously painful. See Ache.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achiote', 'n.', 'Seeds of the annotto tree; also, the coloring matter,\n   annotto.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achlamydate', 'a.', 'Not possessing a mantle; -- said of certain\n   gastropods.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achlamydeous', 'a.', 'Naked; having no floral envelope, neither calyx nor\n   corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acholia', 'n.', 'Deficiency or want of bile.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acholous', 'a.', 'Lacking bile.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achromatic', 'a.', 'Free from color; transmitting light without decomposing\n   it into its primary colors.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achromatic', 'a.', 'Uncolored; not absorbing color from a fluid; -- said of\n   tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achromatically', 'adv.', 'In an achromatic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achromaticity', 'n.', 'Achromatism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achromatin', 'n.', 'Tissue which is not stained by fluid dyes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achromatism', 'n.', 'The state or quality of being achromatic; as, the\n   achromatism of a lens; achromaticity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achromatization', 'n.', 'The act or process of achromatizing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achromatized', 'imp. & p. p.', 'of Achromatize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achromatizing', 'p. pr. & vb. n.', 'of Achromatize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achromatize', 'v. t.', 'To deprive of color; to make achromatic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achromatopsy', 'n.', 'Color blindness; inability to distinguish colors;\n   Daltonism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achronic', 'a.', 'See Acronyc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achroodextrin', 'n.', 'Dextrin not colorable by iodine. See Dextrin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achroous', 'a.', 'Colorless; achromatic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achylous', 'a.', 'Without chyle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Achymous', 'a.', 'Without chyme.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aciculae', 'pl. ', 'of Acicula');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acicula', 'n.', 'One of the needlelike or bristlelike spines or prickles of\n   some animals and plants; also, a needlelike crystal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acicular', 'a.', 'Needle-shaped; slender like a needle or bristle, as some\n   leaves or crystals; also, having sharp points like needless.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aciculate', 'a.', 'Alt. of Aciculated');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aciculated', 'a.', 'Furnished with aciculae.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aciculated', 'a.', 'Acicular.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aciculated', 'a.', 'Marked with fine irregular streaks as if scratched by a\n   needle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aciculiform', 'a.', 'Needle-shaped; acicular.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aciculite', 'n.', 'Needle ore.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acid', 'a.', 'Sour, sharp, or biting to the taste; tart; having the taste\n   of vinegar: as, acid fruits or liquors. Also fig.: Sour-tempered.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acid', 'a.', 'Of or pertaining to an acid; as, acid reaction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acid', 'n.', 'A sour substance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acid', 'n.', 'One of a class of compounds, generally but not always\n   distinguished by their sour taste, solubility in water, and reddening\n   of vegetable blue or violet colors. They are also characterized by the\n   power of destroying the distinctive properties of alkalies or bases,\n   combining with them to form salts, at the same time losing their own\n   peculiar properties. They all contain hydrogen, united with a more\n   negative element or radical, either alone, or more generally with\n   oxygen, and take their names from this negative element or radical.\n   Those which contain no oxygen are sometimes called hydracids in\n   distinction from the others which are called oxygen acids or oxacids.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acidic', 'a.', 'Containing a high percentage of silica; -- opposed to\n   basic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acidiferous', 'a.', 'Containing or yielding an acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acidifiable', 'a.', 'Capable of being acidified, or converted into an acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acidific', 'a.', 'Producing acidity; converting into an acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acidification', 'n.', 'The act or process of acidifying, or changing into\n   an acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acidifier', 'n.', 'A simple or compound principle, whose presence is\n   necessary to produce acidity, as oxygen, chlorine, bromine, iodine,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acidified', 'imp. & p. p.', 'of Acidify');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acidifying', 'p. pr. & vb. n.', 'of Acidify');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acidify', 'v. t.', 'To make acid; to convert into an acid; as, to acidify\n   sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acidify', 'v. t.', 'To sour; to imbitter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acidimeter', 'n.', 'An instrument for ascertaining the strength of acids.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acidimetry', 'n.', 'The measurement of the strength of acids, especially by\n   a chemical process based on the law of chemical combinations, or the\n   fact that, to produce a complete reaction, a certain definite weight of\n   reagent is required.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acidity', 'n.', 'The quality of being sour; sourness; tartness; sharpness\n   to the taste; as, the acidity of lemon juice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acidly', 'adv.', 'Sourly; tartly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acidness', 'n.', 'Acidity; sourness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acidulated', 'imp. & p. p.', 'of Acidulate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acidulating', 'p. pr. & vb. n.', 'of Acidulate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acidulate', 'v. t.', 'To make sour or acid in a moderate degree; to sour\n   somewhat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acidulent', 'a.', 'Having an acid quality; sour; acidulous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acidulous', 'a.', 'Slightly sour; sub-acid; sourish; as, an acidulous\n   tincture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acierage', 'n.', 'The process of coating the surface of a metal plate (as a\n   stereotype plate) with steellike iron by means of voltaic electricity;\n   steeling.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aciform', 'a.', 'Shaped like a needle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acinaceous', 'a.', 'Containing seeds or stones of grapes, or grains like\n   them.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acinaces', 'n.', 'A short sword or saber.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acinaciform', 'a.', 'Scimeter-shaped; as, an acinaciform leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acinesia', 'n.', 'Same as Akinesia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acinetae', 'n. pl.', 'A group of suctorial Infusoria, which in the adult\n   stage are stationary. See Suctoria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acinetiform', 'a.', 'Resembling the Acinetae.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aciniform', 'a.', 'Having the form of a cluster of grapes; clustered like\n   grapes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aciniform', 'a.', 'Full of small kernels like a grape.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acinose', 'a.', 'Alt. of Acinous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acinous', 'a.', 'Consisting of acini, or minute granular concretions; as,\n   acinose or acinous glands.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acini', 'pl. ', 'of Acinus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acinus', 'n.', 'One of the small grains or drupelets which make up some\n   kinds of fruit, as the blackberry, raspberry, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acinus', 'n.', 'A grapestone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acinus', 'n.', 'One of the granular masses which constitute a racemose or\n   compound gland, as the pancreas; also, one of the saccular recesses in\n   the lobules of a racemose gland.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acipenser', 'n.', 'A genus of ganoid fishes, including the sturgeons,\n   having the body armed with bony scales, and the mouth on the under side\n   of the head. See Sturgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aciurgy', 'n.', 'Operative surgery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acknow', 'v. t.', 'To recognize.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acknow', 'v. t.', 'To acknowledge; to confess.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acknowledged', 'imp. & p. p.', 'of Acknowledge');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acknowledging', 'p. pr. & vb. n.', 'of Acknowledge');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acknowledge', 'v. t.', 'To of or admit the knowledge of; to recognize as a\n   fact or truth; to declare ones belief in; as, to acknowledge the being\n   of a God.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acknowledge', 'v. t.', 'To own or recognize in a particular character or\n   relationship; to admit the claims or authority of; to give recognition\n   to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acknowledge', 'v. t.', 'To own with gratitude or as a benefit or an\n   obligation; as, to acknowledge a favor, the receipt of a letter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acknowledge', 'v. t.', 'To own as genuine; to assent to, as a legal\n   instrument, to give it validity; to avow or admit in legal form; as, to\n   acknowledgea deed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acknowledgedly', 'adv.', 'Confessedly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acknowledger', 'n.', 'One who acknowledges.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acknowledgment', 'n.', 'The act of acknowledging; admission; avowal;\n   owning; confession.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acknowledgment', 'n.', 'The act of owning or recognized in a particular\n   character or relationship; recognition as regards the existence,\n   authority, truth, or genuineness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acknowledgment', 'n.', 'The owning of a benefit received; courteous\n   recognition; expression of thanks.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acknowledgment', 'n.', 'Something given or done in return for a favor,\n   message, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acknowledgment', 'n.', 'A declaration or avowal of ones own act, to give\n   it legal validity; as, the acknowledgment of a deed before a proper\n   officer. Also, the certificate of the officer attesting such\n   declaration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aclinic', 'a.', 'Without inclination or dipping; -- said the magnetic\n   needle balances itself horizontally, having no dip. The aclinic line is\n   also termed the magnetic equator.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acme', 'n.', 'The top or highest point; the culmination.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acme', 'n.', 'The crisis or height of a disease.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acme', 'n.', 'Mature age; full bloom of life.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acne', 'n.', 'A pustular affection of the skin, due to changes in the\n   sebaceous glands.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acnodal', 'a.', 'Pertaining to acnodes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acnode', 'n.', 'An isolated point not upon a curve, but whose coordinates\n   satisfy the equation of the curve so that it is considered as belonging\n   to the curve.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acock', 'adv.', 'In a cocked or turned up fashion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acockbill', 'adv.', 'Hanging at the cathead, ready to let go, as an anchor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acockbill', 'adv.', 'Topped up; having one yardarm higher than the other.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acold', 'a.', 'Cold.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acologic', 'a.', 'Pertaining to acology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acology', 'n.', 'Materia medica; the science of remedies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acolothist', 'n.', 'See Acolythist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acolyctine', 'n.', 'An organic base, in the form of a white powder,\n   obtained from Aconitum lycoctonum.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acolyte', 'n.', 'One who has received the highest of the four minor orders\n   in the Catholic church, being ordained to carry the wine and water and\n   the lights at the Mass.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acolyte', 'n.', 'One who attends; an assistant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acolyth', 'n.', 'Same as Acolyte.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acolythist', 'n.', 'An acolyte.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aconddylose', 'a.', 'Alt. of Acondylous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acondylous', 'a.', 'Being without joints; jointless.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aconital', 'a.', 'Of the nature of aconite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aconite', 'n.', 'The herb wolfsbane, or monkshood; -- applied to any plant\n   of the genus Aconitum (tribe Hellebore); all the species of which are\n   poisonous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aconite', 'n.', 'An extract or tincture obtained from Aconitum napellus,\n   used as a poison and medicinally.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aconitia', 'n.', 'Same as Aconitine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aconitic', 'a.', 'Of or pertaining to aconite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aconitine', 'n.', 'An intensely poisonous alkaloid, extracted from aconite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aconitum', 'n.', 'The poisonous herb aconite; also, an extract from it.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acontia', 'n. pl.', 'Threadlike defensive organs, composed largely of\n   nettling cells (cnidae); thrown out of the mouth or special pores of\n   certain Actiniae when irritated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acontias', 'n.', 'Anciently, a snake, called dart snake; now, one of a\n   genus of reptiles closely allied to the lizards.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acopic', 'a.', 'Relieving weariness; restorative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acorn', 'n.', 'The fruit of the oak, being an oval nut growing in a woody\n   cup or cupule.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acorn', 'n.', 'A cone-shaped piece of wood on the point of the spindle\n   above the vane, on the mast-head.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acorn', 'n.', 'See Acorn-shell.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acorn cup', '', 'The involucre or cup in which the acorn is fixed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acorned', 'a.', 'Furnished or loaded with acorns.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acorned', 'a.', 'Fed or filled with acorns.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acorn-shell', 'n.', 'One of the sessile cirripeds; a barnacle of the genus\n   Balanus. See Barnacle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acosmism', 'n.', 'A denial of the existence of the universe as distinct\n   from God.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acosmist', 'n.', 'One who denies the existence of the universe, or of a\n   universe as distinct from God.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acotyledon', 'n.', 'A plant which has no cotyledons, as the dodder and all\n   flowerless plants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acotyledonous', 'a.', 'Having no seed lobes, as the dodder; also applied to\n   plants which have no true seeds, as ferns, mosses, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acouchy', 'n.', 'A small species of agouti (Dasyprocta acouchy).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acoumeter', 'n.', 'An instrument for measuring the acuteness of the sense\n   of hearing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acoumetry', 'n.', 'The measuring of the power or extent of hearing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acoustic', 'a.', 'Pertaining to the sense of hearing, the organs of\n   hearing, or the science of sounds; auditory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acoustic', 'n.', 'A medicine or agent to assist hearing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acoustical', 'a.', 'Of or pertaining to acoustics.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acoustically', 'adv.', 'In relation to sound or to hearing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acoustician', 'n.', 'One versed in acoustics.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acoustics', 'n.', 'The science of sounds, teaching their nature, phenomena,\n   and laws.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquaint', 'v. t.', 'Acquainted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquainted', 'imp. & p. p.', 'of Acquaint');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquainting', 'p. pr. & vb. n.', 'of Acquaint');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquaint', 'v. t.', 'To furnish or give experimental knowledge of; to make\n   (one) to know; to make familiar; -- followed by with.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquaint', 'v. t.', 'To communicate notice to; to inform; to make\n   cognizant; -- followed by with (formerly, also, by of); or by that,\n   introducing the intelligence; as, to acquaint a friend with the\n   particulars of an act.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquaint', 'v. t.', 'To familiarize; to accustom.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquaintable', 'a.', 'Easy to be acquainted with; affable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquaintance', 'n.', 'A state of being acquainted, or of having intimate,\n   or more than slight or superficial, knowledge; personal knowledge\n   gained by intercourse short of that of friendship or intimacy; as, I\n   know the man; but have no acquaintance with him.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquaintance', 'n.', 'A person or persons with whom one is acquainted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquaintanceship', 'n.', 'A state of being acquainted; acquaintance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquaintant', 'n.', 'An acquaintance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquainted', 'a.', 'Personally known; familiar. See To be acquainted with,\n   under Acquaint, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquaintedness', 'n.', 'State of being acquainted; degree of acquaintance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquest', 'n.', 'Acquisition; the thing gained.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquest', 'n.', 'Property acquired by purchase, gift, or otherwise than by\n   inheritance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquiesced', 'imp. & p. p.', 'of Acquiesce');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquiescing', 'p. pr. & vb. n.', 'of Acquiesce');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquiesce', 'v. i.', 'To rest satisfied, or apparently satisfied, or to\n   rest without opposition and discontent (usually implying previous\n   opposition or discontent); to accept or consent by silence or by\n   omitting to object; -- followed by in, formerly also by with and to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquiesce', 'v. i.', 'To concur upon conviction; as, to acquiesce in an\n   opinion; to assent to; usually, to concur, not heartily but so far as\n   to forbear opposition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquiescence', 'n.', 'A silent or passive assent or submission, or a\n   submission with apparent content; -- distinguished from avowed consent\n   on the one hand, and on the other, from opposition or open discontent;\n   quiet satisfaction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquiescence', 'n.', 'Submission to an injury by the party injured.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquiescence', 'n.', 'Tacit concurrence in the action of another.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquiescency', 'n.', 'The quality of being acquiescent; acquiescence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquiescent', 'a.', 'Resting satisfied or submissive; disposed tacitly to\n   submit; assentive; as, an acquiescent policy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquiescently', 'adv.', 'In an acquiescent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquiet', 'v. t.', 'To quiet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquirability', 'n.', 'The quality of being acquirable; attainableness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquirable', 'a.', 'Capable of being acquired.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquired', 'imp. & p. p.', 'of Acquire');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquiring', 'p. pr. & vb. n.', 'of Acquire');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquire', 'v. t.', 'To gain, usually by ones own exertions; to get as\n   ones own; as, to acquire a title, riches, knowledge, skill, good or\n   bad habits.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquirement', 'n.', 'The act of acquiring, or that which is acquired;\n   attainment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquirer', 'n.', 'A person who acquires.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquiry', 'n.', 'Acquirement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquisite', 'a.', 'Acquired.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquisition', 'n.', 'The act or process of acquiring.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquisition', 'n.', 'The thing acquired or gained; an acquirement; a gain;\n   as, learning is an acquisition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquisitive', 'a.', 'Acquired.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquisitive', 'a.', 'Able or disposed to make acquisitions; acquiring; as,\n   an acquisitive person or disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquisitively', 'adv.', 'In the way of acquisition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquisitiveness', 'n.', 'The quality of being acquisitive; propensity to\n   acquire property; desire of possession.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquisitiveness', 'n.', 'The faculty to which the phrenologists attribute\n   the desire of acquiring and possessing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquisitor', 'n.', 'One who acquires.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquist', 'n.', 'Acquisition; gain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquit', 'p. p.', 'Acquitted; set free; rid of.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquitted', 'imp. & p. p.', 'of Acquit');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquitting', 'p. pr. & vb. n.', 'of Acquit');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquit', 'v. t.', 'To discharge, as a claim or debt; to clear off; to pay\n   off; to requite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquit', 'v. t.', 'To pay for; to atone for.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquit', 'v. t.', 'To set free, release or discharge from an obligation,\n   duty, liability, burden, or from an accusation or charge; -- now\n   followed by of before the charge, formerly by from; as, the jury\n   acquitted the prisoner; we acquit a man of evil intentions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquit', 'v. t.', 'To clear ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquit', 'v. t.', 'To bear or conduct ones self; to perform ones part;\n   as, the soldier acquitted himself well in battle; the orator acquitted\n   himself very poorly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquitment', 'n.', 'Acquittal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquittal', 'n.', 'The act of acquitting; discharge from debt or\n   obligation; acquittance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquittal', 'n.', 'A setting free, or deliverance from the charge of an\n   offense, by verdict of a jury or sentence of a court.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquittance', 'n.', 'The clearing off of debt or obligation; a release or\n   discharge from debt or other liability.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquittance', 'n.', 'A writing which is evidence of a discharge; a receipt\n   in full, which bars a further demand.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquittance', 'v. t.', 'To acquit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acquitter', 'n.', 'One who acquits or releases.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrania', 'n.', 'Partial or total absence of the skull.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrania', 'n.', 'The lowest group of Vertebrata, including the amphioxus,\n   in which no skull exists.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acranial', 'a.', 'Wanting a skull.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrase', 'v. t.', 'Alt. of Acraze');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acraze', 'v. t.', 'To craze.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acraze', 'v. t.', 'To impair; to destroy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrasia', 'n.', 'Alt. of Acrasy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrasy', 'n.', 'Excess; intemperance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acraspeda', 'n. pl.', 'A group of acalephs, including most of the larger\n   jellyfishes; the Discophora.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acre', 'n.', 'Any field of arable or pasture land.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acre', 'n.', 'A piece of land, containing 160 square rods, or 4,840 square\n   yards, or 43,560 square feet. This is the English statute acre. That of\n   the United States is the same. The Scotch acre was about 1.26 of the\n   English, and the Irish 1.62 of the English.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acreable', 'a.', 'Of an acre; per acre; as, the acreable produce.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acreage', 'n.', 'Acres collectively; as, the acreage of a farm or a\n   country.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acred', 'a.', 'Possessing acres or landed property; -- used in composition;\n   as, large-acred men.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrid', 'a.', 'Sharp and harsh, or bitter and not, to the taste; pungent;\n   as, acrid salts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrid', 'a.', 'Causing heat and irritation; corrosive; as, acrid\n   secretions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrid', 'a.', 'Caustic; bitter; bitterly irritating; as, acrid temper,\n   mind, writing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acridity', 'n.', 'Alt. of Acridness');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acridness', 'n.', 'The quality of being acrid or pungent; irritant\n   bitterness; acrimony; as, the acridity of a plant, of a speech.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acridly', 'adv.', 'In an acid manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrimonious', 'a.', 'Acrid; corrosive; as, acrimonious gall.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrimonious', 'a.', 'Caustic; bitter-tempered sarcastic; as, acrimonious\n   dispute, language, temper.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrimoniously', 'adv.', 'In an acrimonious manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrimoniousness', 'n.', 'The quality of being acrimonious; asperity;\n   acrimony.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrimonies', 'pl. ', 'of Acrimony');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrimony', 'n.', 'A quality of bodies which corrodes or destroys others;\n   also, a harsh or biting sharpness; as, the acrimony of the juices of\n   certain plants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrimony', 'n.', 'Sharpness or severity, as of language or temper;\n   irritating bitterness of disposition or manners.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrisia', 'n.', 'Alt. of Acrisy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrisy', 'n.', 'Inability to judge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrisy', 'n.', 'Undecided character of a disease.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrita', 'n. pl.', 'The lowest groups of animals, in which no nervous\n   system has been observed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acritan', 'a.', 'Of or pertaining to the Acrita.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acritan', 'n.', 'An individual of the Acrita.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrite', 'a.', 'Acritan.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acritical', 'a.', 'Having no crisis; giving no indications of a crisis; as,\n   acritical symptoms, an acritical abscess.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acritochromacy', 'n.', 'Color blindness; achromatopsy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acritude', 'n.', 'Acridity; pungency joined with heat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrity', 'n.', 'Sharpness; keenness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acroamatic', 'a.', 'Alt. of Acroamatical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acroamatical', 'a.', 'Communicated orally; oral; -- applied to the esoteric\n   teachings of Aristotle, those intended for his genuine disciples, in\n   distinction from his exoteric doctrines, which were adapted to\n   outsiders or the public generally. Hence: Abstruse; profound.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acroatic', 'a.', 'Same as Acroamatic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrobat', 'n.', 'One who practices rope dancing, high vaulting, or other\n   daring gymnastic feats.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrobatic', 'a.', 'Pertaining to an acrobat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrobatism', 'n.', 'Feats of the acrobat; daring gymnastic feats; high\n   vaulting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrocarpous', 'a.', 'Having a terminal fructification; having the fruit at\n   the end of the stalk.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrocarpous', 'a.', 'Having the fruit stalks at the end of a leafy stem, as\n   in certain mosses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrocephalic', 'a.', 'Characterized by a high skull.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrocephaly', 'n.', 'Loftiness of skull.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acroceraunian', 'a.', 'Of or pertaining to the high mountain range of\n   \"thunder-smitten\" peaks (now Kimara); between Epirus and Macedonia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrodactylum', 'n.', 'The upper surface of the toes, individually.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrodont', 'n.', 'One of a group of lizards having the teeth immovably\n   united to the top of the alveolar ridge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrodont', 'a.', 'Of or pertaining to the acrodonts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrogen', 'n.', 'A plant of the highest class of cryptogams, including the\n   ferns, etc. See Cryptogamia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrogenous', 'a.', 'Increasing by growth from the extremity; as, an\n   acrogenous plant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrolein', 'n.', 'A limpid, colorless, highly volatile liquid, obtained by\n   the dehydration of glycerin, or the destructive distillation of neutral\n   fats containing glycerin. Its vapors are intensely irritating.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrolith', 'n.', 'A statue whose extremities are of stone, the trunk being\n   generally of wood.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrolithan', 'a.', 'Alt. of Acrolithic');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrolithic', 'a.', 'Pertaining to, or like, an acrolith.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acromegaly', 'n.', 'Chronic enlargement of the extremities and face.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acromial', 'a.', 'Of or pertaining to the acromion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acromion', 'n.', 'The outer extremity of the shoulder blade.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acromonogrammatic', 'a.', 'Having each verse begin with the same letter as\n   that with which the preceding verse ends.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acronyc', 'a.', 'Alt. of Acronychal');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acronychal', 'a.', 'Rising at sunset and setting at sunrise, as a star; --\n   opposed to cosmical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acronycally', 'adv.', 'In an acronycal manner as rising at the setting of\n   the sun, and vice versa.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acronyctous', 'a.', 'Acronycal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrook', 'adv.', 'Crookedly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acropetal', 'a.', 'Developing from below towards the apex, or from the\n   circumference towards the center; centripetal; -- said of certain\n   inflorescence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrophony', 'n.', 'The use of a picture symbol of an object to represent\n   phonetically the initial sound of the name of the object.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acropodium', 'n.', 'The entire upper surface of the foot.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acropolis', 'n.', 'The upper part, or the citadel, of a Grecian city;\n   especially, the citadel of Athens.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acropolitan', 'a.', 'Pertaining to an acropolis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrospire', 'n.', 'The sprout at the end of a seed when it begins to\n   germinate; the plumule in germination; -- so called from its spiral\n   form.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrospire', 'v. i.', 'To put forth the first sprout.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrospore', 'n.', 'A spore borne at the extremity of the cells of\n   fructification in fungi.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrosporous', 'a.', 'Having acrospores.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Across', 'n.', 'From side to side; athwart; crosswise, or in a direction\n   opposed to the length; quite over; as, a bridge laid across a river.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Across', 'adv.', 'From side to side; crosswise; as, with arms folded\n   across.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Across', 'adv.', 'Obliquely; athwart; amiss; awry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrostic', 'n.', 'A composition, usually in verse, in which the first or\n   the last letters of the lines, or certain other letters, taken in\n   order, form a name, word, phrase, or motto.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrostic', 'n.', 'A Hebrew poem in which the lines or stanzas begin with\n   the letters of the alphabet in regular order (as Psalm cxix.). See\n   Abecedarian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrostic', 'n.', 'Alt. of Acrostical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrostical', 'n.', 'Pertaining to, or characterized by, acrostics.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrostically', 'adv.', 'After the manner of an acrostic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrotarsium', 'n.', 'The instep or front of the tarsus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acroteleutic', 'n.', 'The end of a verse or psalm, or something added\n   thereto, to be sung by the people, by way of a response.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acroter', 'n.', 'Same as Acroterium.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acroterial', 'a.', 'Pertaining to an acroterium; as, acroterial ornaments.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acroteria', 'pl. ', 'of Acroterium');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acroterium', 'n.', 'One of the small pedestals, for statues or other\n   ornaments, placed on the apex and at the basal angles of a pediment.\n   Acroteria are also sometimes placed upon the gables in Gothic\n   architecture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acroterium', 'n.', 'One of the pedestals, for vases or statues, forming a\n   part roof balustrade.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrotic', 'a.', 'Pertaining to or affecting the surface.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrotism', 'n.', 'Lack or defect of pulsation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrotomous', 'a.', 'Having a cleavage parallel with the base.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acrylic', 'a.', 'Of or containing acryl, the hypothetical radical of which\n   acrolein is the hydride; as, acrylic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Act', 'n.', 'That which is done or doing; the exercise of power, or the\n   effect, of which power exerted is the cause; a performance; a deed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Act', 'n.', 'The result of public deliberation; the decision or\n   determination of a legislative body, council, court of justice, etc.; a\n   decree, edit, law, judgment, resolve, award; as, an act of Parliament,\n   or of Congress.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Act', 'n.', 'A formal solemn writing, expressing that something has been\n   done.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Act', 'n.', 'A performance of part of a play; one of the principal\n   divisions of a play or dramatic work in which a certain definite part\n   of the action is completed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Act', 'n.', 'A thesis maintained in public, in some English universities,\n   by a candidate for a degree, or to show the proficiency of a student.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Act', 'n.', 'A state of reality or real existence as opposed to a\n   possibility or possible existence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Act', 'n.', 'Process of doing; action. In act, in the very doing; on the\n   point of (doing).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acted', 'imp. & p. p.', 'of Act');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acting', 'p. pr. & vb. n.', 'of Act');
  INSERT INTO words (word, word_type, description) VALUES  (  'Act', 'v. t.', 'To move to action; to actuate; to animate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Act', 'v. t.', 'To perform; to execute; to do.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Act', 'v. t.', 'To perform, as an actor; to represent dramatically on the\n   stage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Act', 'v. t.', 'To assume the office or character of; to play; to\n   personate; as, to act the hero.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Act', 'v. t.', 'To feign or counterfeit; to simulate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Act', 'v. i.', 'To exert power; to produce an effect; as, the stomach acts\n   upon food.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Act', 'v. i.', 'To perform actions; to fulfill functions; to put forth\n   energy; to move, as opposed to remaining at rest; to carry into effect\n   a determination of the will.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Act', 'v. i.', 'To behave or conduct, as in morals, private duties, or\n   public offices; to bear or deport ones self; as, we know not why he\n   has acted so.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Act', 'v. i.', 'To perform on the stage; to represent a character.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actable', 'a.', 'Capable of being acted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinal', 'a.', 'Pertaining to the part of a radiate animal which contains\n   the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinaria', 'n. pl.', 'A large division of Anthozoa, including those which\n   have simple tentacles and do not form stony corals. Sometimes, in a\n   wider sense, applied to all the Anthozoa, expert the Alcyonaria,\n   whether forming corals or not.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acting', 'a.', 'Operating in any way.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acting', 'a.', 'Doing duty for another; officiating; as, an acting\n   superintendent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actiniae', 'pl. ', 'of Actinia');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinias', 'pl. ', 'of Actinia');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinia', 'n.', 'An animal of the class Anthozoa, and family Actinidae.\n   From a resemblance to flowers in form and color, they are often called\n   animal flowers and sea anemones. [See Polyp.].');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinia', 'n.', 'A genus in the family Actinidae.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinic', 'a.', 'Of or pertaining to actinism; as, actinic rays.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actiniform', 'a.', 'Having a radiated form, like a sea anemone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinism', 'n.', 'The property of radiant energy (found chiefly in solar or\n   electric light) by which chemical changes are produced, as in\n   photography.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinium', 'n.', 'A supposed metal, said by Phipson to be contained in\n   commercial zinc; -- so called because certain of its compounds are\n   darkened by exposure to light.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actino-chemistry', 'n.', 'Chemistry in its relations to actinism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinograph', 'n.', 'An instrument for measuring and recording the\n   variations in the actinic or chemical force of rays of light.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinoid', 'a.', 'Having the form of rays; radiated, as an actinia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinolite', 'n.', 'A bright green variety of amphibole occurring usually\n   in fibrous or columnar masses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinolitic', 'a.', 'Of the nature of, or containing, actinolite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinology', 'n.', 'The science which treats of rays of light, especially\n   of the actinic or chemical rays.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinomere', 'n.', 'One of the radial segments composing the body of one of\n   the Coelenterata.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinometer', 'n.', 'An instrument for measuring the direct heating power\n   of the suns rays.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinometer', 'n.', 'An instrument for measuring the actinic effect of rays\n   of light.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinometric', 'a.', 'Pertaining to the measurement of the intensity of the\n   solar rays, either (a) heating, or (b) actinic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinometry', 'n.', 'The measurement of the force of solar radiation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinometry', 'n.', 'The measurement of the chemical or actinic energy of\n   light.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinophorous', 'a.', 'Having straight projecting spines.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinosome', 'n.', 'The entire body of a coelenterate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinost', 'n.', 'One of the bones at the base of a paired fin of a fish.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinostome', 'n.', 'The mouth or anterior opening of a coelenterate\n   animal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinotrocha', 'n. pl.', 'A peculiar larval form of Phoronis, a genus of\n   marine worms, having a circle of ciliated tentacles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinozoa', 'n. pl.', 'A group of Coelenterata, comprising the Anthozoa and\n   Ctenophora. The sea anemone, or actinia, is a familiar example.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinozoal', 'a.', 'Of or pertaining to the Actinozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinozoon', 'n.', 'One of the Actinozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actinula', 'n. pl.', 'A kind of embryo of certain hydroids (Tubularia);\n   having a stellate form.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Action', 'n.', 'A process or condition of acting or moving, as opposed to\n   rest; the doing of something; exertion of power or force, as when one\n   body acts on another; the effect of power exerted on one body by\n   another; agency; activity; operation; as, the action of heat; a man of\n   action.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Action', 'n.', 'An act; a thing done; a deed; an enterprise. (pl.):\n   Habitual deeds; hence, conduct; behavior; demeanor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Action', 'n.', 'The event or connected series of events, either real or\n   imaginary, forming the subject of a play, poem, or other composition;\n   the unfolding of the drama of events.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Action', 'n.', 'Movement; as, the horse has a spirited action.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Action', 'n.', 'Effective motion; also, mechanism; as, the breech action of\n   a gun.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Action', 'n.', 'Any one of the active processes going on in an organism;\n   the performance of a function; as, the action of the heart, the\n   muscles, or the gastric juice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Action', 'n.', 'Gesticulation; the external deportment of the speaker, or\n   the suiting of his attitude, voice, gestures, and countenance, to the\n   subject, or to the feelings.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Action', 'n.', 'The attitude or position of the several parts of the body\n   as expressive of the sentiment or passion depicted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Action', 'n.', 'A suit or process, by which a demand is made of a right in\n   a court of justice; in a broad sense, a judicial proceeding for the\n   enforcement or protection of a right, the redress or prevention of a\n   wrong, or the punishment of a public offense.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Action', 'n.', 'A right of action; as, the law gives an action for every\n   claim.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Action', 'n.', 'A share in the capital stock of a joint-stock company, or\n   in the public funds; hence, in the plural, equivalent to stocks.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Action', 'n.', 'An engagement between troops in war, whether on land or\n   water; a battle; a fight; as, a general action, a partial action.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Action', 'n.', 'The mechanical contrivance by means of which the impulse of\n   the players finger is transmitted to the strings of a pianoforte or to\n   the valve of an organ pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actionable', 'a.', 'That may be the subject of an action or suit at law;\n   as, to call a man a thief is actionable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actionably', 'adv.', 'In an actionable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actionary', 'n.', 'Alt. of Actionist');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actionist', 'n.', 'A shareholder in joint-stock company.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actionless', 'a.', 'Void of action.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Activate', 'v. t.', 'To make active.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Active', 'a.', 'Having the power or quality of acting; causing change;\n   communicating action or motion; acting; -- opposed to passive, that\n   receives; as, certain active principles; the powers of the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Active', 'a.', 'Quick in physical movement; of an agile and vigorous body;\n   nimble; as, an active child or animal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Active', 'a.', 'In action; actually proceeding; working; in force; --\n   opposed to quiescent, dormant, or extinct; as, active laws; active\n   hostilities; an active volcano.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Active', 'a.', 'Given to action; constantly engaged in action; energetic;\n   diligent; busy; -- opposed to dull, sluggish, indolent, or inert; as,\n   an active man of business; active mind; active zeal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Active', 'a.', 'Requiring or implying action or exertion; -- opposed to\n   sedentary or to tranquil; as, active employment or service; active\n   scenes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Active', 'a.', 'Given to action rather than contemplation; practical;\n   operative; -- opposed to speculative or theoretical; as, an active\n   rather than a speculative statesman.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Active', 'a.', 'Brisk; lively; as, an active demand for corn.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Active', 'a.', 'Implying or producing rapid action; as, an active disease;\n   an active remedy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Active', 'a.', 'Applied to a form of the verb; -- opposed to passive. See\n   Active voice, under Voice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Active', 'a.', 'Applied to verbs which assert that the subject acts upon or\n   affects something else; transitive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Active', 'a.', 'Applied to all verbs that express action as distinct from\n   mere existence or state.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actively', 'adv.', 'In an active manner; nimbly; briskly; energetically;\n   also, by ones own action; voluntarily, not passively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actively', 'adv.', 'In an active signification; as, a word used actively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Activeness', 'n.', 'The quality of being active; nimbleness; quickness of\n   motion; activity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Activities', 'pl. ', 'of Activity');
  INSERT INTO words (word, word_type, description) VALUES  (  'Activity', 'n.', 'The state or quality of being active; nimbleness;\n   agility; vigorous action or operation; energy; active force; as, an\n   increasing variety of human activities.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actless', 'a.', 'Without action or spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acton', 'n.', 'A stuffed jacket worn under the mail, or (later) a jacket\n   plated with mail.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actor', 'n.', 'One who acts, or takes part in any affair; a doer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actor', 'n.', 'A theatrical performer; a stageplayer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actor', 'n.', 'An advocate or proctor in civil courts or causes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actor', 'n.', 'One who institutes a suit; plaintiff or complainant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actress', 'n.', 'A female actor or doer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actress', 'n.', 'A female stageplayer; a woman who acts a part.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actual', 'a.', 'Involving or comprising action; active.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actual', 'a.', 'Existing in act or reality; really acted or acting; in\n   fact; real; -- opposed to potential, possible, virtual, speculative,\n   conceivable, theoretical, or nominal; as, the actual cost of goods; the\n   actual case under discussion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actual', 'a.', 'In action at the time being; now exiting; present; as the\n   actual situation of the country.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actual', 'n.', 'Something actually received; real, as distinct from\n   estimated, receipts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actualist', 'n.', 'One who deals with or considers actually existing facts\n   and conditions, rather than fancies or theories; -- opposed to\n   idealist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actualities', 'pl. ', 'of Actuality');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actuality', 'n.', 'The state of being actual; reality; as, the actuality of\n   Gods nature.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actualization', 'n.', 'A making actual or really existent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actualize', 'v. t.', 'To make actual; to realize in action.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actually', 'adv.', 'Actively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actually', 'adv.', 'In act or in fact; really; in truth; positively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actualness', 'n.', 'Quality of being actual; actuality.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actuarial', 'a.', 'Of or pertaining to actuaries; as, the actuarial value\n   of an annuity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actuaries', 'pl. ', 'of Actuary');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actuary', 'n.', 'A registrar or clerk; -- used originally in courts of\n   civil law jurisdiction, but in Europe used for a clerk or registrar\n   generally.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actuary', 'n.', 'The computing official of an insurance company; one whose\n   profession it is to calculate for insurance companies the risks and\n   premiums for life, fire, and other insurances.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actuated', 'imp. & p. p.', 'of Actuate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actuating', 'p. pr. & vb. n.', 'of Actuate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actuate', 'v. t.', 'To put into action or motion; to move or incite to\n   action; to influence actively; to move as motives do; -- more commonly\n   used of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actuate', 'v. t.', 'To carry out in practice; to perform.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actuate', 'a.', 'Put in action; actuated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actuation', 'n.', 'A bringing into action; movement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actuator', 'n.', 'One who actuates, or puts into action.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actuose', 'a.', 'Very active.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Actuosity', 'n.', 'Abundant activity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acture', 'n.', 'Action.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acturience', 'n.', 'Tendency or impulse to act.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acuate', 'v. t.', 'To sharpen; to make pungent; to quicken.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acuate', 'a.', 'Sharpened; sharp-pointed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acuation', 'n.', 'Act of sharpening.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acuition', 'n.', 'The act of sharpening.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acuity', 'n.', 'Sharpness or acuteness, as of a needle, wit, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aculeate', 'a.', 'Having a sting; covered with prickles; sharp like a\n   prickle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aculeate', 'a.', 'Having prickles, or sharp points; beset with prickles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aculeate', 'a.', 'Severe or stinging; incisive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aculeated', 'a.', 'Having a sharp point; armed with prickles; prickly;\n   aculeate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aculeiform', 'a.', 'Like a prickle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aculeolate', 'a.', 'Having small prickles or sharp points.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aculeous', 'a.', 'Aculeate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aculei', 'pl. ', 'of Aculeus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aculeus', 'n.', 'A prickle growing on the bark, as in some brambles and\n   roses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aculeus', 'n.', 'A sting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acumen', 'n.', 'Quickness of perception or discernment; penetration of\n   mind; the faculty of nice discrimination.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acuminate', 'a.', 'Tapering to a point; pointed; as, acuminate leaves,\n   teeth, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acuminate', 'v. t.', 'To render sharp or keen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acuminate', 'v. i.', 'To end in, or come to, a sharp point.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acumination', 'n.', 'A sharpening; termination in a sharp point; a tapering\n   point.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acuminose', 'a.', 'Terminating in a flat, narrow end.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acuminous', 'a.', 'Characterized by acumen; keen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acupressure', 'n.', 'A mode of arresting hemorrhage resulting from wounds\n   or surgical operations, by passing under the divided vessel a needle,\n   the ends of which are left exposed externally on the cutaneous surface.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acupuncturation', 'n.', 'See Acupuncture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acupuncture', 'n.', 'Pricking with a needle; a needle prick.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acupuncture', 'n.', 'The insertion of needles into the living tissues for\n   remedial purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acupuncture', 'v. t.', 'To treat with acupuncture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acustumaunce', 'n.', 'See Accustomance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acutangular', 'a.', 'Acute-angled.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acute', 'a.', 'Sharp at the end; ending in a sharp point; pointed; --\n   opposed to blunt or obtuse; as, an acute angle; an acute leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acute', 'a.', 'Having nice discernment; perceiving or using minute\n   distinctions; penetrating; clever; shrewd; -- opposed to dull or\n   stupid; as, an acute observer; acute remarks, or reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acute', 'a.', 'Having nice or quick sensibility; susceptible to slight\n   impressions; acting keenly on the senses; sharp; keen; intense; as, a\n   man of acute eyesight, hearing, or feeling; acute pain or pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acute', 'a.', 'High, or shrill, in respect to some other sound; -- opposed\n   to grave or low; as, an acute tone or accent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acute', 'a.', 'Attended with symptoms of some degree of severity, and\n   coming speedily to a crisis; -- opposed to chronic; as, an acute\n   disease.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acute', 'v. t.', 'To give an acute sound to; as, he acutes his rising\n   inflection too much.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acute-angled', 'a.', 'Having acute angles; as, an acute-angled triangle, a\n   triangle with every one of its angles less than a right angle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acutely', 'adv.', 'In an acute manner; sharply; keenly; with nice\n   discrimination.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acuteness', 'n.', 'The quality of being acute or pointed; sharpness; as,\n   the acuteness of an angle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acuteness', 'n.', 'The faculty of nice discernment or perception; acumen;\n   keenness; sharpness; sensitiveness; -- applied to the senses, or the\n   understanding. By acuteness of feeling, we perceive small objects or\n   slight impressions: by acuteness of intellect, we discern nice\n   distinctions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acuteness', 'n.', 'Shrillness; high pitch; -- said of sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acuteness', 'n.', 'Violence of a disease, which brings it speedily to a\n   crisis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acutifoliate', 'a.', 'Having sharp-pointed leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Acutilobate', 'a.', 'Having acute lobes, as some leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ad-', '', 'As a prefix ad- assumes the forms ac-, af-, ag-, al-, an-, ap-,\n   ar-, as-, at-, assimilating the d with the first letter of the word to\n   which ad- is prefixed. It remains unchanged before vowels, and before\n   d, h, j, m, v. Examples: adduce, adhere, adjacent, admit, advent,\n   accord, affect, aggregate, allude, annex, appear, etc. It becomes ac-\n   before qu, as in acquiesce.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adact', 'v. t.', 'To compel; to drive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adactyl', 'a.', 'Alt. of Adactylous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adactylous', 'a.', 'Without fingers or without toes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adactylous', 'a.', 'Without claws on the feet (of crustaceous animals).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adage', 'n.', 'An old saying, which has obtained credit by long use; a\n   proverb.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adagial', 'a.', 'Pertaining to an adage; proverbial.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adagio', 'a. & adv.', 'Slow; slowly, leisurely, and gracefully. When\n   repeated, adagio, adagio, it directs the movement to be very slow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adagio', 'n.', 'A piece of music in adagio time; a slow movement; as, an\n   adagio of Haydn.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adam', 'n.', 'The name given in the Bible to the first man, the progenitor\n   of the human race.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adam', 'n.', '\"Original sin;\" human frailty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adamant', 'n.', 'A stone imagined by some to be of impenetrable hardness; a\n   name given to the diamond and other substances of extreme hardness; but\n   in modern mineralogy it has no technical signification. It is now a\n   rhetorical or poetical name for the embodiment of impenetrable\n   hardness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adamant', 'n.', 'Lodestone; magnet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adamantean', 'a.', 'Of adamant; hard as adamant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adamantine', 'a.', 'Made of adamant, or having the qualities of adamant;\n   incapable of being broken, dissolved, or penetrated; as, adamantine\n   bonds or chains.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adamantine', 'a.', 'Like the diamond in hardness or luster.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adambulacral', 'a.', 'Next to the ambulacra; as, the adambulacral ossicles\n   of the starfish.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adamic', 'a.', 'Alt. of Adamical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adamical', 'a.', 'Of or pertaining to Adam, or resembling him.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adamite', 'n.', 'A descendant of Adam; a human being.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adamite', 'n.', 'One of a sect of visionaries, who, professing to imitate\n   the state of Adam, discarded the use of dress in their assemblies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adams apple', '', 'See under Adam.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adance', 'adv.', 'Dancing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adangle', 'adv.', 'Dangling.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adansonia', 'n.', 'A genus of great trees related to the Bombax. There are\n   two species, A. digitata, the baobab or monkey-bread of Africa and\n   India, and A. Gregorii, the sour gourd or cream-of-tartar tree of\n   Australia. Both have a trunk of moderate height, but of enormous\n   diameter, and a wide-spreading head. The fruit is oblong, and filled\n   with pleasantly acid pulp. The wood is very soft, and the bark is used\n   by the natives for making ropes and cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adapt', 'a.', 'Fitted; suited.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adapted', 'imp. & p. p.', 'of Adapt');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adapting', 'p. pr. & vb. n.', 'of Adapt');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adapt', 'v. t.', 'To make suitable; to fit, or suit; to adjust; to alter so\n   as to fit for a new use; -- sometimes followed by to or for.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adaptability', 'n.', 'Alt. of Adaptableness');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adaptableness', 'n.', 'The quality of being adaptable; suitableness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adaptable', 'a.', 'Capable of being adapted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adaptation', 'n.', 'The act or process of adapting, or fitting; or the\n   state of being adapted or fitted; fitness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adaptation', 'n.', 'The result of adapting; an adapted form.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adaptative', 'a.', 'Adaptive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adaptedness', 'n.', 'The state or quality of being adapted; suitableness;\n   special fitness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adapter', 'n.', 'One who adapts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adapter', 'n.', 'A connecting tube; an adopter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adaption', 'n.', 'Adaptation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adaptive', 'a.', 'Suited, given, or tending, to adaptation; characterized\n   by adaptation; capable of adapting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adaptiveness', 'n.', 'The quality of being adaptive; capacity to adapt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adaptly', 'adv.', 'In a suitable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adaptness', 'n.', 'Adaptedness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adaptorial', 'a.', 'Adaptive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adar', 'n.', 'The twelfth month of the Hebrew ecclesiastical year, and the\n   sixth of the civil. It corresponded nearly with March.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adarce', 'n.', 'A saltish concretion on reeds and grass in marshy grounds\n   in Galatia. It is soft and porous, and was formerly used for cleansing\n   the skin from freckles and tetters, and also in leprosy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adatis', 'n.', 'A fine cotton cloth of India.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adaunt', 'v. t.', 'To daunt; to subdue; to mitigate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adaw', 'v. t.', 'To subdue; to daunt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adaw', 'v. t. & i.', 'To awaken; to arouse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adays', 'adv.', 'By day, or every day; in the daytime.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ad captandum', '', 'A phrase used adjectively sometimes of meretricious\n   attempts to catch or win popular favor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Added', 'imp. & p. p.', 'of Add');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adding', 'p. pr. & vb. n.', 'of Add');
  INSERT INTO words (word, word_type, description) VALUES  (  'Add', 'v. t.', 'To give by way of increased possession (to any one); to\n   bestow (on).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Add', 'v. t.', 'To join or unite, as one thing to another, or as several\n   particulars, so as to increase the number, augment the quantity,\n   enlarge the magnitude, or so as to form into one aggregate. Hence: To\n   sum up; to put together mentally; as, to add numbers; to add up a\n   column.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Add', 'v. t.', 'To append, as a statement; to say further.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Add', 'v. i.', 'To make an addition. To add to, to augment; to increase;\n   as, it adds to our anxiety.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Add', 'v. i.', 'To perform the arithmetical operation of addition; as, he\n   adds rapidly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addable', 'a.', 'Addible.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addax', 'n.', 'One of the largest African antelopes (Hippotragus, / Oryx,\n   nasomaculatus).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addeem', 'v. t.', 'To award; to adjudge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addenda', 'pl. ', 'of Addendum');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addendum', 'n.', 'A thing to be added; an appendix or addition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adder', 'n.', 'One who, or that which, adds; esp., a machine for adding\n   numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adder', 'n.', 'A serpent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adder', 'n.', 'A small venomous serpent of the genus Vipera. The common\n   European adder is the Vipera (/ Pelias) berus. The puff adders of\n   Africa are species of Clotho.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adder', 'n.', 'In America, the term is commonly applied to several harmless\n   snakes, as the milk adder, puffing adder, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adder', 'n.', 'Same as Sea Adder.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adder fly/', '', 'A dragon fly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adders-tongue', 'n.', 'A genus of ferns (Ophioglossum); whose seeds are\n   produced on a spike resembling a serpents tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adders-tongue', 'n.', 'The yellow dogtooth violet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adderwort', 'n.', 'The common bistort or snakeweed (Polygonum bistorta).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addibility', 'n.', 'The quantity of being addible; capability of addition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addible', 'a.', 'Capable of being added.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addice', 'n.', 'See Adze.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addict', 'p. p.', 'Addicted; devoted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addicted', 'imp. & p. p.', 'of Addict');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addicting', 'p. pr. & vb. n.', 'of Addict');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addict', 'v. t.', 'To apply habitually; to devote; to habituate; -- with\n   to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addict', 'v. t.', 'To adapt; to make suitable; to fit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addictedness', 'n.', 'The quality or state of being addicted; attachment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addiction', 'n.', 'The state of being addicted; devotion; inclination.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addisons disease', '', 'A morbid condition causing a peculiar brownish\n   discoloration of the skin, and thought, at one time, to be due to\n   disease of the suprarenal capsules (two flat triangular bodies covering\n   the upper part of the kidneys); but now known not to be dependent upon\n   this causes exclusively. It is usually fatal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Additament', 'n.', 'An addition, or a thing added.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addition', 'n.', 'The act of adding two or more things together; -- opposed\n   to subtraction or diminution.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addition', 'n.', 'Anything added; increase; augmentation; as, a piazza is\n   an addition to a building.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addition', 'n.', 'That part of arithmetic which treats of adding numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addition', 'n.', 'A dot at the right side of a note as an indication that\n   its sound is to be lengthened one half.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addition', 'n.', 'A title annexed to a mans name, to identify him more\n   precisely; as, John Doe, Esq.; Richard Roe, Gent.; Robert Dale, Mason;\n   Thomas Way, of New York; a mark of distinction; a title.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addition', 'n.', 'Something added to a coat of arms, as a mark of honor; --\n   opposed to abatement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Additional', 'a.', 'Added; supplemental; in the way of an addition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Additional', 'n.', 'Something added.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Additionally', 'adv.', 'By way of addition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Additionary', 'a.', 'Additional.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addititious', 'a.', 'Additive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Additive', 'a.', 'Proper to be added; positive; -- opposed to subtractive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Additory', 'a.', 'Tending to add; making some addition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addle', 'n.', 'Liquid filth; mire.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addle', 'n.', 'Lees; dregs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addle', 'a.', 'Having lost the power of development, and become rotten, as\n   eggs; putrid. Hence: Unfruitful or confused, as brains; muddled.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addled', 'imp. & p. p.', 'of Addle');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addling', 'p. pr. & vb. n.', 'of Addle');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addle', 'v. t. & i.', 'To make addle; to grow addle; to muddle; as, he\n   addled his brain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addle', 'v. t. & i.', 'To earn by labor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addle', 'v. t. & i.', 'To thrive or grow; to ripen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addle-brain', 'n.', 'Alt. of Addle-pate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addle-head', 'n.', 'Alt. of Addle-pate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addle-pate', 'n.', 'A foolish or dull-witted fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addle-brained', 'a.', 'Alt. of Addle-pated');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addle-headed', 'a.', 'Alt. of Addle-pated');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addle-pated', 'a.', 'Dull-witted; stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addle-patedness', 'n.', 'Stupidity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addlings', 'n. pl.', 'Earnings.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addoom', 'v. t.', 'To adjudge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addorsed', 'a.', 'Set or turned back to back.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addressed', 'imp. & p. p.', 'of Address');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addressing', 'p. pr. & vb. n.', 'of Address');
  INSERT INTO words (word, word_type, description) VALUES  (  'Address', 'v.', 'To aim; to direct.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Address', 'v.', 'To prepare or make ready.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Address', 'v.', 'Reflexively: To prepare ones self; to apply ones skill\n   or energies (to some object); to betake.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Address', 'v.', 'To clothe or array; to dress.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Address', 'v.', 'To direct, as words (to any one or any thing); to make, as\n   a speech, petition, etc. (to any one, an audience).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Address', 'v.', 'To direct speech to; to make a communication to, whether\n   spoken or written; to apply to by words, as by a speech, petition,\n   etc., to speak to; to accost.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Address', 'v.', 'To direct in writing, as a letter; to superscribe, or to\n   direct and transmit; as, he addressed a letter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Address', 'v.', 'To make suit to as a lover; to court; to woo.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Address', 'v.', 'To consign or intrust to the care of another, as agent or\n   factor; as, the ship was addressed to a merchant in Baltimore.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Address', 'v. i.', 'To prepare ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Address', 'v. i.', 'To direct speech.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Address', 'v. t.', 'Act of preparing ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Address', 'v. t.', 'Act of addressing ones self to a person; verbal\n   application.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Address', 'v. t.', 'A formal communication, either written or spoken; a\n   discourse; a speech; a formal application to any one; a petition; a\n   formal statement on some subject or special occasion; as, an address of\n   thanks, an address to the voters.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Address', 'v. t.', 'Direction or superscription of a letter, or the name,\n   title, and place of residence of the person addressed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Address', 'v. t.', 'Manner of speaking to another; delivery; as, a man of\n   pleasing or insinuating address.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Address', 'v. t.', 'Attention in the way ones addresses to a lady.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Address', 'v. t.', 'Skill; skillful management; dexterity; adroitness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addressee', 'n.', 'One to whom anything is addressed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Addression', 'n.', 'The act of addressing or directing ones course.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adduced', 'imp. & p. p.', 'of Adduce');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adducing', 'p. pr. & vb. n.', 'of Adduce');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adduce', 'v. t.', 'To bring forward or offer, as an argument, passage, or\n   consideration which bears on a statement or case; to cite; to allege.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adducent', 'a.', 'Bringing together or towards a given point; -- a word\n   applied to those muscles of the body which pull one part towards\n   another. Opposed to abducent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adducer', 'n.', 'One who adduces.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adducible', 'a.', 'Capable of being adduced.');
