  INSERT INTO words (word, word_type, description) VALUES  (  'Adelaster', 'n.', 'A provisional name for a plant which has not had its\n   flowers botanically examined, and therefore has not been referred to\n   its proper genus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adeling', 'n.', 'Same as Atheling.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adelocodonic', 'a.', 'Applied to sexual zooids of hydroids, that have a\n   saclike form and do not become free; -- opposed to phanerocodonic.');
