  INSERT INTO words (word, word_type, description) VALUES  (  'Adelphia', 'n.', 'A \"brotherhood,\" or collection of stamens in a bundle; --\n   used in composition, as in the class names, Monadelphia, Diadelphia,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adelphous', 'a.', 'Having coalescent or clustered filaments; -- said of\n   stamens; as, adelphous stamens. Usually in composition; as,\n   monadelphous.');
