  INSERT INTO words (word, word_type, description) VALUES  (  'Adeps', 'n.', 'Animal fat; lard.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adept', 'n.', 'One fully skilled or well versed in anything; a proficient;\n   as, adepts in philosophy.');
