  INSERT INTO words (word, word_type, description) VALUES  (  'Adequacy', 'n.', 'The state or quality of being adequate, proportionate, or\n   sufficient; a sufficiency for a particular purpose; as, the adequacy of\n   supply to the expenditure.');
