  INSERT INTO words (word, word_type, description) VALUES  (  'Adhere', 'v. i.', 'To stick fast or cleave, as a glutinous substance does;\n   to become joined or united; as, wax to the finger; the lungs sometimes\n   adhere to the pleura.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adhere', 'v. i.', 'To hold, be attached, or devoted; to remain fixed,\n   either by personal union or conformity of faith, principle, or opinion;\n   as, men adhere to a party, a cause, a leader, a church.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adhere', 'v. i.', 'To be consistent or coherent; to be in accordance; to\n   agree.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adherence', 'n.', 'The quality or state of adhering.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adherence', 'n.', 'The state of being fixed in attachment; fidelity; steady\n   attachment; adhesion; as, adherence to a party or to opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adherency', 'n.', 'The state or quality of being adherent; adherence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adherency', 'n.', 'That which adheres.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adherent', 'a.', 'Sticking; clinging; adhering.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adherent', 'a.', 'Attached as an attribute or circumstance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adherent', 'a.', 'Congenitally united with an organ of another kind, as\n   calyx with ovary, or stamens with petals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adherent', 'n.', 'One who adheres; one who adheres; one who follows a\n   leader, party, or profession; a follower, or partisan; a believer in a\n   particular faith or church.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adherent', 'n.', 'That which adheres; an appendage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adherently', 'adv.', 'In an adherent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adherer', 'n.', 'One who adheres; an adherent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adhesion', 'n.', 'The action of sticking; the state of being attached;\n   intimate union; as, the adhesion of glue, or of parts united by growth,\n   cement, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adhesion', 'n.', 'Adherence; steady or firm attachment; fidelity; as,\n   adhesion to error, to a policy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adhesion', 'n.', 'Agreement to adhere; concurrence; assent.');
