  INSERT INTO words (word, word_type, description) VALUES  (  'Adipocere', 'n.', 'A soft, unctuous, or waxy substance, of a light brown\n   color, into which the fat and muscle tissue of dead bodies sometimes\n   are converted, by long immersion in water or by burial in moist places.\n   It is a result of fatty degeneration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adipoceriform', 'a.', 'Having the form or appearance of adipocere; as, an\n   adipoceriform tumor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adipocerous', 'a.', 'Like adipocere.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adipose', 'a.', 'Of or pertaining to animal fat; fatty.');
