  INSERT INTO words (word, word_type, description) VALUES  (  'Adit', 'n.', 'An entrance or passage. Specifically: The nearly horizontal\n   opening by which a mine is entered, or by which water and ores are\n   carried away; -- called also drift and tunnel.');
