  INSERT INTO words (word, word_type, description) VALUES  (  'Adjective', 'n.', 'Relating to procedure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjective', 'n.', 'A word used with a noun, or substantive, to express a\n   quality of the thing named, or something attributed to it, or to limit\n   or define it, or to specify or describe a thing, as distinct from\n   something else. Thus, in phrase, \"a wise ruler,\" wise is the adjective,\n   expressing a property of ruler.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjective', 'n.', 'A dependent; an accessory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjectived', 'imp. & p. p.', 'of Adjective');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjectiving', 'p. pr. & vb. n.', 'of Adjective');
