  INSERT INTO words (word, word_type, description) VALUES  (  'Adjourning', 'p. pr. & vb. n.', 'of Adjourn');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjourn', 'v. t.', 'To put off or defer to another day, or indefinitely; to\n   postpone; to close or suspend for the day; -- commonly said of the\n   meeting, or the action, of convened body; as, to adjourn the meeting;\n   to adjourn a debate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjourn', 'v. i.', 'To suspend business for a time, as from one day to\n   another, or for a longer period, or indefinitely; usually, to suspend\n   public business, as of legislatures and courts, or other convened\n   bodies; as, congress adjourned at four oclock; the court adjourned\n   without day.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjournal', 'n.', 'Adjournment; postponement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjournment', 'n.', 'The act of adjourning; the putting off till another\n   day or time specified, or without day.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjournment', 'n.', 'The time or interval during which a public body\n   adjourns its sittings or postpones business.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjudged', 'imp. & p. p.', 'of Adjudge');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjudging', 'p. pr. & vb. n.', 'of Adjudge');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjudge', 'v. t.', 'To award judicially in the case of a controverted\n   question; as, the prize was adjudged to the victor.');
