  INSERT INTO words (word, word_type, description) VALUES  (  'Adjuvant', 'n.', 'A substance added to an immunogenic agent to enhance the\n   production of antibodies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjuvant', 'n.', 'A substance added to a formulation of a drug which\n   enhances the effect of the active ingredient.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjunct', 'a.', 'Conjoined; attending; consequent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjunct', 'n.', 'Something joined or added to another thing, but not\n   essentially a part of it.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjunct', 'n.', 'A person joined to another in some duty or service; a\n   colleague; an associate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjunct', 'n.', 'A word or words added to quality or amplify the force of\n   other words; as, the History of the American Revolution, where the\n   words in italics are the adjunct or adjuncts of \"History.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjunct', 'n.', 'A quality or property of the body or the mind, whether\n   natural or acquired; as, color, in the body, judgment in the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjunct', 'n.', 'A key or scale closely related to another as principal; a\n   relative or attendant key. [R.] See Attendant keys, under Attendant, a.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjunction', 'n.', 'The act of joining; the thing joined or added.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjunctive', 'a.', 'Joining; having the quality of joining; forming an\n   adjunct.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjunctive', 'n.', 'One who, or that which, is joined.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjunctively', 'adv.', 'In an adjunctive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjunctly', 'adv.', 'By way of addition or adjunct; in connection with.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjuration', 'n.', 'The act of adjuring; a solemn charging on oath, or\n   under the penalty of a curse; an earnest appeal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjuration', 'n.', 'The form of oath or appeal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjuratory', 'a.', 'Containing an adjuration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjured', 'imp. & p. p.', 'of Adjure');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjuring', 'p. pr. & vb. n.', 'of Adjure');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjure', 'v. t.', 'To charge, bind, or command, solemnly, as if under oath,\n   or under the penalty of a curse; to appeal to in the most solemn or\n   impressive manner; to entreat earnestly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjurer', 'n.', 'One who adjures.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjusted', 'imp. & p. p.', 'of Adjust');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjusting', 'p. pr. & vb. n.', 'of Adjust');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjust', 'v. t.', 'To make exact; to fit; to make correspondent or\n   conformable; to bring into proper relations; as, to adjust a garment to\n   the body, or things to a standard.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjust', 'v. t.', 'To put in order; to regulate, or reduce to system.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjust', 'v. t.', 'To settle or bring to a satisfactory state, so that\n   parties are agreed in the result; as, to adjust accounts; the\n   differences are adjusted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjust', 'v. t.', 'To bring to a true relative position, as the parts of an\n   instrument; to regulate for use; as, to adjust a telescope or\n   microscope.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjustable', 'a.', 'Capable of being adjusted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjustage', 'n.', 'Adjustment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjuster', 'n.', 'One who, or that which, adjusts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjustive', 'a.', 'Tending to adjust.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjustment', 'n.', 'The act of adjusting, or condition of being adjusted;\n   act of bringing into proper relations; regulation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjustment', 'n.', 'Settlement of claims; an equitable arrangement of\n   conflicting claims, as in set-off, contribution, exoneration,\n   subrogation, and marshaling.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjustment', 'n.', 'The operation of bringing all the parts of an\n   instrument, as a microscope or telescope, into their proper relative\n   position for use; the condition of being thus adjusted; as, to get a\n   good adjustment; to be in or out of adjustment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjutage', 'n.', 'Same as Ajutage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjutancy', 'n.', 'The office of an adjutant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjutancy', 'n.', 'Skillful arrangement in aid; assistance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjutant', 'n.', 'A helper; an assistant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjutant', 'n.', 'A regimental staff officer, who assists the colonel, or\n   commanding officer of a garrison or regiment, in the details of\n   regimental and garrison duty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjutant', 'n.', 'A species of very large stork (Ciconia argala); a native\n   of India; -- called also the gigantic crane, and by the native name\n   argala. It is noted for its serpent-destroying habits.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjutator', 'n.', 'A corruption of Agitator.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjute', 'v. t.', 'To add.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjutor', 'n.', 'A helper or assistant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjutory', 'a.', 'Serving to help or assist; helping.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjutrix', 'n.', 'A female helper or assistant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjuvant', 'a.', 'Helping; helpful; assisting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjuvant', 'n.', 'An assistant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adjuvant', 'n.', 'An ingredient, in a prescription, which aids or modifies\n   the action of the principal ingredient.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adlegation', 'n.', 'A right formerly claimed by the states of the German\n   Empire of joining their own ministers with those of the emperor in\n   public treaties and negotiations to the common interest of the empire.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ad libitum', '', 'At ones pleasure; as one wishes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adlocution', 'n.', 'See Allocution.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admarginate', 'v. t.', 'To write in the margin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admaxillary', 'a.', 'Near to the maxilla or jawbone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admeasure', 'v. t.', 'To measure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admeasure', 'v. t.', 'To determine the proper share of, or the proper\n   apportionment; as, to admeasure dower; to admeasure common of pasture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admeasure', 'v. t.', 'The measure of a thing; dimensions; size.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admeasure', 'v. t.', 'Formerly, the adjustment of proportion, or\n   ascertainment of shares, as of dower or pasture held in common. This\n   was by writ of admeasurement, directed to the sheriff.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admeasurer', 'n.', 'One who admeasures.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admensuration', 'n.', 'Same as Admeasurement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adminicle', 'n.', 'Help or support; an auxiliary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adminicle', 'n.', 'Corroborative or explanatory proof.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adminicular', 'a.', 'Supplying help; auxiliary; corroborative; explanatory;\n   as, adminicular evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adminiculary', 'a.', 'Adminicular.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administered', 'imp. & p. p.', 'of Administer');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administering', 'p. pr. & vb. n.', 'of Administer');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administer', 'v. t.', 'To manage or conduct, as public affairs; to direct\n   or superintend the execution, application, or conduct of; as, to\n   administer the government or the state.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administer', 'v. t.', 'To dispense; to serve out; to supply; execute; as,\n   to administer relief, to administer the sacrament.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administer', 'v. t.', 'To apply, as medicine or a remedy; to give, as a\n   dose or something beneficial or suitable. Extended to a blow, a\n   reproof, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administer', 'v. t.', 'To tender, as an oath.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administer', 'v. t.', 'To settle, as the estate of one who dies without a\n   will, or whose will fails of an executor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administer', 'v. i.', 'To contribute; to bring aid or supplies; to conduce;\n   to minister.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administer', 'v. i.', 'To perform the office of administrator; to act\n   officially; as, A administers upon the estate of B.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administer', 'n.', 'Administrator.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administerial', 'a.', 'Pertaining to administration, or to the executive\n   part of government.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administrable', 'a.', 'Capable of being administered; as, an administrable\n   law.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administrant', 'a.', 'Executive; acting; managing affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administrant', 'n.', 'One who administers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administrate', 'v. t.', 'To administer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administration', 'n.', 'The act of administering; government of public\n   affairs; the service rendered, or duties assumed, in conducting\n   affairs; the conducting of any office or employment; direction;\n   management.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administration', 'n.', 'The executive part of government; the persons\n   collectively who are intrusted with the execution of laws and the\n   superintendence of public affairs; the chief magistrate and his cabinet\n   or council; or the council, or ministry, alone, as in Great Britain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administration', 'n.', 'The act of administering, or tendering something to\n   another; dispensation; as, the administration of a medicine, of an\n   oath, of justice, or of the sacrament.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administration', 'n.', 'The management and disposal, under legal authority,\n   of the estate of an intestate, or of a testator having no competent\n   executor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administration', 'n.', 'The management of an estate of a deceased person by\n   an executor, the strictly corresponding term execution not being in\n   use.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administrative', 'a.', 'Pertaining to administration; administering;\n   executive; as, an administrative body, ability, or energy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administrator', 'n.', 'One who administers affairs; one who directs,\n   manages, executes, or dispenses, whether in civil, judicial, political,\n   or ecclesiastical affairs; a manager.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administrator', 'n.', 'A man who manages or settles the estate of an\n   intestate, or of a testator when there is no competent executor; one to\n   whom the right of administration has been committed by competent\n   authority.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administratorship', 'n.', 'The position or office of an administrator.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Administratrix', 'n.', 'A woman who administers; esp., one who administers\n   the estate of an intestate, or to whom letters of administration have\n   been granted; a female administrator.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admirability', 'n.', 'Admirableness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admirable', 'a.', 'Fitted to excite wonder; wonderful; marvelous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admirable', 'a.', 'Having qualities to excite wonder united with\n   approbation; deserving the highest praise; most excellent; -- used of\n   persons or things.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admirableness', 'n.', 'The quality of being admirable; wonderful\n   excellence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admirably', 'adv.', 'In an admirable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admiral', 'n.', 'A naval officer of the highest rank; a naval officer of\n   high rank, of which there are different grades. The chief gradations in\n   rank are admiral, vice admiral, and rear admiral. The admiral is the\n   commander in chief of a fleet or of fleets.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admiral', 'n.', 'The ship which carries the admiral; also, the most\n   considerable ship of a fleet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admiral', 'n.', 'A handsome butterfly (Pyrameis Atalanta) of Europe and\n   America. The larva feeds on nettles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admiralship', 'n.', 'The office or position oaf an admiral; also, the naval\n   skill of an admiral.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admiralties', 'pl. ', 'of Admiralty');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admiralty', 'n.', 'The office or jurisdiction of an admiral.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admiralty', 'n.', 'The department or officers having authority over naval\n   affairs generally.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admiralty', 'n.', 'The court which has jurisdiction of maritime questions\n   and offenses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admiralty', 'n.', 'The system of jurisprudence of admiralty courts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admiralty', 'n.', 'The building in which the lords of the admiralty, in\n   England, transact business.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admirance', 'n.', 'Admiration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admiration', 'n.', 'Wonder; astonishment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admiration', 'n.', 'Wonder mingled with approbation or delight; an emotion\n   excited by a person or thing possessed of wonderful or high excellence;\n   as, admiration of a beautiful woman, of a landscape, of virtue.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admiration', 'n.', 'Cause of admiration; something to excite wonder, or\n   pleased surprise; a prodigy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admirative', 'a.', 'Relating to or expressing admiration or wonder.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admired', 'imp. & p. p.', 'of Admire');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admiring', 'p. pr. & vb. n.', 'of Admire');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admire', 'v. t.', 'To regard with wonder or astonishment; to view with\n   surprise; to marvel at.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admire', 'v. t.', 'To regard with wonder and delight; to look upon with an\n   elevated feeling of pleasure, as something which calls out approbation,\n   esteem, love, or reverence; to estimate or prize highly; as, to admire\n   a person of high moral worth, to admire a landscape.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admire', 'v. i.', 'To wonder; to marvel; to be affected with surprise; --\n   sometimes with at.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admired', 'a.', 'Regarded with wonder and delight; highly prized; as, an\n   admired poem.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admired', 'a.', 'Wonderful; also, admirable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admirer', 'n.', 'One who admires; one who esteems or loves greatly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admiring', 'a.', 'Expressing admiration; as, an admiring glance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admissibility', 'n.', 'The quality of being admissible; admissibleness; as,\n   the admissibility of evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admissible', 'a.', 'Entitled to be admitted, or worthy of being admitted;\n   that may be allowed or conceded; allowable; as, the supposition is\n   hardly admissible.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admission', 'n.', 'The act or practice of admitting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admission', 'n.', 'Power or permission to enter; admittance; entrance;\n   access; power to approach.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admission', 'n.', 'The granting of an argument or position not fully\n   proved; the act of acknowledging something /serted; acknowledgment;\n   concession.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admission', 'n.', 'Acquiescence or concurrence in a statement made by\n   another, and distinguishable from a confession in that an admission\n   presupposes prior inquiry by another, but a confession may be made\n   without such inquiry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admission', 'n.', 'A fact, point, or statement admitted; as, admission made\n   out of court are received in evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admission', 'n.', 'Declaration of the bishop that he approves of the\n   presentee as a fit person to serve the cure of the church to which he\n   is presented.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admissive', 'a.', 'Implying an admission; tending to admit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admissory', 'a.', 'Pertaining to admission.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admitted', 'imp. & p. p.', 'of Admit');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admitting', 'p. pr. & vb. n.', 'of Admit');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admit', 'v. t.', 'To suffer to enter; to grant entrance, whether into a\n   place, or into the mind, or consideration; to receive; to take; as,\n   they were into his house; to admit a serious thought into the mind; to\n   admit evidence in the trial of a cause.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admit', 'v. t.', 'To give a right of entrance; as, a ticket admits one into\n   a playhouse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admit', 'v. t.', 'To allow (one) to enter on an office or to enjoy a\n   privilege; to recognize as qualified for a franchise; as, to admit an\n   attorney to practice law; the prisoner was admitted to bail.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admit', 'v. t.', 'To concede as true; to acknowledge or assent to, as an\n   allegation which it is impossible to deny; to own or confess; as, the\n   argument or fact is admitted; he admitted his guilt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admit', 'v. t.', 'To be capable of; to permit; as, the words do not admit\n   such a construction. In this sense, of may be used after the verb, or\n   may be omitted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admittable', 'a.', 'Admissible.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admittance', 'n.', 'The act of admitting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admittance', 'n.', 'Permission to enter; the power or right of entrance;\n   also, actual entrance; reception.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admittance', 'n.', 'Concession; admission; allowance; as, the admittance of\n   an argument.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admittance', 'n.', 'Admissibility.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admittance', 'n.', 'The act of giving possession of a copyhold estate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admittatur', 'n.', 'The certificate of admission given in some American\n   colleges.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admitted', 'a.', 'Received as true or valid; acknowledged.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admittedly', 'adv.', 'Confessedly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admitter', 'n.', 'One who admits.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admix', 'v. t.', 'To mingle with something else; to mix.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admixtion', 'n.', 'A mingling of different things; admixture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admixture', 'n.', 'The act of mixing; mixture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admixture', 'n.', 'The compound formed by mixing different substances\n   together.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admixture', 'n.', 'That which is mixed with anything.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admonished', 'imp. & p. p.', 'of Admonish');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admonishing', 'p. pr. & vb. n.', 'of Admonish');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admonish', 'v. t.', 'To warn or notify of a fault; to reprove gently or\n   kindly, but seriously; to exhort.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admonish', 'v. t.', 'To counsel against wrong practices; to cation or\n   advise; to warn against danger or an offense; -- followed by of,\n   against, or a subordinate clause.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admonish', 'v. t.', 'To instruct or direct; to inform; to notify.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admonisher', 'n.', 'One who admonishes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admonishment', 'n.', 'Admonition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admonition', 'n.', 'Gentle or friendly reproof; counseling against a fault\n   or error; expression of authoritative advice; friendly caution or\n   warning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admonitioner', 'n.', 'Admonisher.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admonitive', 'a.', 'Admonitory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admonitor', 'n.', 'Admonisher; monitor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admonitorial', 'a.', 'Admonitory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admonitory', 'a.', 'That conveys admonition; warning or reproving; as, an\n   admonitory glance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admonitrix', 'n.', 'A female admonitor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admortization', 'n.', 'The reducing or lands or tenements to mortmain. See\n   Mortmain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Admove', 'v. t.', 'To move or conduct to or toward.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adnascent', 'a.', 'Growing to or on something else.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adnate', 'a.', 'Grown to congenitally.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adnate', 'a.', 'Growing together; -- said only of organic cohesion of\n   unlike parts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adnate', 'a.', 'Growing with one side adherent to a stem; -- a term applied\n   to the lateral zooids of corals and other compound animals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adnation', 'n.', 'The adhesion or cohesion of different floral verticils or\n   sets of organs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adnominal', 'a.', 'Pertaining to an adnoun; adjectival; attached to a noun.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adnoun', 'n.', 'An adjective, or attribute.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adnubilated', 'a.', 'Clouded; obscured.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ado', 'n.', 'To do; in doing; as, there is nothing ado.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ado', 'n.', 'Doing; trouble; difficulty; troublesome business; fuss;\n   bustle; as, to make a great ado about trifles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adobe', 'n.', 'An unburnt brick dried in the sun; also used as an\n   adjective, as, an adobe house, in Texas or New Mexico.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adolescence', 'n.', 'The state of growing up from childhood to manhood or\n   womanhood; youth, or the period of life between puberty and maturity,\n   generally considered to be, in the male sex, from fourteen to\n   twenty-one. Sometimes used with reference to the lower animals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adolescency', 'n.', 'The quality of being adolescent; youthfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adolescent', 'a.', 'Growing; advancing from childhood to maturity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adolescent', 'n.', 'A youth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adonean', 'a.', 'Pertaining to Adonis; Adonic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adonic', 'a.', 'Relating to Adonis, famed for his beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adonic', 'n.', 'An Adonic verse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adonis', 'n.', 'A youth beloved by Venus for his beauty. He was killed in\n   the chase by a wild boar.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adonis', 'n.', 'A preeminently beautiful young man; a dandy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adonis', 'n.', 'A genus of plants of the family Ranunculaceae, containing\n   the pheasants eye (Adonis autumnalis); -- named from Adonis, whose\n   blood was fabled to have stained the flower.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adonist', 'n.', 'One who maintains that points of the Hebrew word\n   translated \"Jehovah\" are really the vowel points of the word \"Adonai.\"\n   See Jehovist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adonize', 'v. t.', 'To beautify; to dandify.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adoor', '', 'Alt. of Adoors');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adoors', '', 'At the door; of the door; as, out adoors.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adopted', 'imp. & p. p.', 'of Adopt');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adopting', 'p. pr. & vb. n.', 'of Adopt');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adopt', 'v. t.', 'To take by choice into relationship, as, child, heir,\n   friend, citizen, etc.; esp. to take voluntarily (a child of other\n   parents) to be in the place of, or as, ones own child.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adopt', 'v. t.', 'To take or receive as ones own what is not so naturally;\n   to select and take or approve; as, to adopt the view or policy of\n   another; these resolutions were adopted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adoptable', 'a.', 'Capable of being adopted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adopted', 'a.', 'Taken by adoption; taken up as ones own; as, an adopted\n   son, citizen, country, word.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adopter', 'n.', 'One who adopts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adopter', 'n.', 'A receiver, with two necks, opposite to each other, one of\n   which admits the neck of a retort, and the other is joined to another\n   receiver. It is used in distillations, to give more space to elastic\n   vapors, to increase the length of the neck of a retort, or to unite two\n   vessels whose openings have different diameters.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adoption', 'n.', 'The act of adopting, or state of being adopted; voluntary\n   acceptance of a child of other parents to be the same as ones own\n   child.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adoption', 'n.', 'Admission to a more intimate relation; reception; as, the\n   adoption of persons into hospitals or monasteries, or of one society\n   into another.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adoption', 'n.', 'The choosing and making that to be ones own which\n   originally was not so; acceptance; as, the adoption of opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adoptionist', 'n.', 'One of a sect which maintained that Christ was the Son\n   of God not by nature but by adoption.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adoptious', 'a.', 'Adopted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adoptive', 'a.', 'Pertaining to adoption; made or acquired by adoption;\n   fitted to adopt; as, an adoptive father, an child; an adoptive\n   language.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adorability', 'n.', 'Adorableness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adorable', 'a.', 'Deserving to be adored; worthy of divine honors.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adorable', 'a.', 'Worthy of the utmost love or respect.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adorableness', 'n.', 'The quality of being adorable, or worthy of\n   adoration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adorably', 'adv.', 'In an adorable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adoration', 'n.', 'The act of playing honor to a divine being; the worship\n   paid to God; the act of addressing as a god.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adoration', 'n.', 'Homage paid to one in high esteem; profound veneration;\n   intense regard and love; fervent devotion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adoration', 'n.', 'A method of electing a pope by the expression of homage\n   from two thirds of the conclave.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adoring', 'imp. & p. p. Adored ', '/); p. pr. & vb. n.) of Adore');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adore', 'v. t.', 'To worship with profound reverence; to pay divine honors\n   to; to honor as deity or as divine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adore', 'v. t.', 'To love in the highest degree; to regard with the utmost\n   esteem and affection; to idolize.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adore', 'v. t.', 'To adorn.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adorement', 'n.', 'The act of adoring; adoration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adorer', 'n.', 'One who adores; a worshiper; one who admires or loves\n   greatly; an ardent admirer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adoringly', 'adv.', 'With adoration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adorned', 'imp. & p. p.', 'of Adorn');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adorning', 'p. pr. & vb. n.', 'of Adorn');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adorn', 'v. t.', 'To deck or dress with ornaments; to embellish; to set off\n   to advantage; to render pleasing or attractive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adorn', 'n.', 'Adornment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adorn', 'a.', 'Adorned; decorated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adornation', 'n.', 'Adornment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adorner', 'n.', 'He who, or that which, adorns; a beautifier.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adorningly', 'adv.', 'By adorning; decoratively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adornment', 'n.', 'An adorning; an ornament; a decoration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adosculation', 'n.', 'Impregnation by external contact, without\n   intromission.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adown', 'adv.', 'From a higher to a lower situation; downward; down, to or\n   on the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adown', 'prep.', 'Down.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adpress', 'v. t.', 'See Appressed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adrad', 'p. a.', 'Put in dread; afraid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adragant', 'n.', 'Gum tragacanth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adread', 'v. t. & i.', 'To dread.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adreamed', 'p. p.', 'Visited by a dream; -- used in the phrase, To be\n   adreamed, to dream.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adrenal', 'a.', 'Suprarenal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adrian', 'a.', 'Pertaining to the Adriatic Sea; as, Adrian billows.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adriatic', 'a.', 'Of or pertaining to a sea so named, the northwestern part\n   of which is known as the Gulf of Venice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adrift', 'adv. & a.', 'Floating at random; in a drifting condition; at the\n   mercy of wind and waves. Also fig.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adrip', 'adv. & a.', 'In a dripping state; as, leaves all adrip.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adrogate', 'v. t.', 'To adopt (a person who is his own master).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adrogation', 'n.', 'A kind of adoption in ancient Rome. See Arrogation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adroit', 'a.', 'Dexterous in the use of the hands or in the exercise of the\n   mental faculties; exhibiting skill and readiness in avoiding danger or\n   escaping difficulty; ready in invention or execution; -- applied to\n   persons and to acts; as, an adroit mechanic, an adroit reply.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adroitly', 'adv.', 'In an adroit manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adroitness', 'n.', 'The quality of being adroit; skill and readiness;\n   dexterity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adry', 'a.', 'In a dry or thirsty condition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adscititious', 'a.', 'Supplemental; additional; adventitious; ascititious.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adscript', 'a.', 'Held to service as attached to the soil; -- said of\n   feudal serfs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adscript', 'n.', 'One held to service as attached to the glebe or estate; a\n   feudal serf.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adscriptive', 'a.', 'Attached or annexed to the glebe or estate and\n   transferable with it.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adsignification', 'n.', 'Additional signification.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adsignify', 'v. t.', 'To denote additionally.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adstrict', 'n.', 'See Astrict, and Astriction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adstrictory', 'a.', 'See Astrictory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adstringent', 'a.', 'See Astringent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adularia', 'n.', 'A transparent or translucent variety of common feldspar,\n   or orthoclase, which often shows pearly opalescent reflections; --\n   called by lapidaries moonstone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulate', 'v. t.', 'To flatter in a servile way.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulation', 'n.', 'Servile flattery; praise in excess, or beyond what is\n   merited.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulator', 'n.', 'A servile or hypocritical flatterer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulatory', 'a.', 'Containing excessive praise or compliment; servilely\n   praising; flattering; as, an adulatory address.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulatress', 'n.', 'A woman who flatters with servility.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adult', 'a.', 'Having arrived at maturity, or to full size and strength;\n   matured; as, an adult person or plant; an adult ape; an adult age.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adult', 'n.', 'A person, animal, or plant grown to full size and strength;\n   one who has reached maturity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulter', 'v. i.', 'To commit adultery; to pollute.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulterant', 'n.', 'That which is used to adulterate anything.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulterant', 'a.', 'Adulterating; as, adulterant agents and processes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulterated', 'imp. & p. p.', 'of Adulterate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulterating', 'p. pr. & vb. n.', 'of Adulterate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulterate', 'v. t.', 'To defile by adultery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulterate', 'v. t.', 'To corrupt, debase, or make impure by an admixture\n   of a foreign or a baser substance; as, to adulterate food, drink,\n   drugs, coin, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulterate', 'v. i.', 'To commit adultery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulterate', 'a.', 'Tainted with adultery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulterate', 'a.', 'Debased by the admixture of a foreign substance;\n   adulterated; spurious.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulteration', 'n.', 'The act of adulterating; corruption, or debasement\n   (esp. of food or drink) by foreign mixture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulteration', 'n.', 'An adulterated state or product.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulterator', 'n.', 'One who adulterates or corrupts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulterer', 'n.', 'A man who commits adultery; a married man who has sexual\n   intercourse with a woman not his wife.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulterer', 'n.', 'A man who violates his religious covenant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulteress', 'n.', 'A woman who commits adultery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulteress', 'n.', 'A woman who violates her religious engagements.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulterine', 'a.', 'Proceeding from adulterous intercourse. Hence:\n   Spurious; without the support of law; illegal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulterine', 'n.', 'An illegitimate child.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulterize', 'v. i.', 'To commit adultery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulterous', 'a.', 'Guilty of, or given to, adultery; pertaining to\n   adultery; illicit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulterous', 'a.', 'Characterized by adulteration; spurious.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulterously', 'adv.', 'In an adulterous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adulteries', 'pl. ', 'of Adultery');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adultery', 'n.', 'The unfaithfulness of a married person to the marriage\n   bed; sexual intercourse by a married man with another than his wife, or\n   voluntary sexual intercourse by a married woman with another than her\n   husband.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adultery', 'n.', 'Adulteration; corruption.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adultery', 'n.', 'Lewdness or unchastity of thought as well as act, as\n   forbidden by the seventh commandment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adultery', 'n.', 'Faithlessness in religion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adultery', 'n.', 'The fine and penalty imposed for the offense of adultery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adultery', 'n.', 'The intrusion of a person into a bishopric during the\n   life of the bishop.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adultery', 'n.', 'Injury; degradation; ruin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adultness', 'n.', 'The state of being adult.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adumbrant', 'a.', 'Giving a faint shadow, or slight resemblance; shadowing\n   forth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adumbrate', 'v. t.', 'To give a faint shadow or slight representation of;\n   to outline; to shadow forth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adumbrate', 'v. t.', 'To overshadow; to shade.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adumbration', 'n.', 'The act of adumbrating, or shadowing forth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adumbration', 'n.', 'A faint sketch; an outline; an imperfect portrayal or\n   representation of a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adumbration', 'n.', 'The shadow or outlines of a figure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adumbrative', 'a.', 'Faintly representing; typical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adunation', 'n.', 'A uniting; union.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adunc', 'a.', 'Alt. of Adunque');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adunque', 'a.', 'Hooked; as, a parrot has an adunc bill.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aduncity', 'n.', 'Curvature inwards; hookedness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aduncous', 'a.', 'Curved inwards; hooked.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adure', 'v. t.', 'To burn up.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adust', 'a.', 'Inflamed or scorched; fiery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adust', 'a.', 'Looking as if or scorched; sunburnt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adust', 'a.', 'Having much heat in the constitution and little serum in the\n   blood. [Obs.] Hence: Atrabilious; sallow; gloomy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adusted', 'a.', 'Burnt; adust.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adustible', 'a.', 'That may be burnt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adustion', 'n.', 'The act of burning, or heating to dryness; the state of\n   being thus heated or dried.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Adustion', 'n.', 'Cauterization.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ad valorem', '', 'A term used to denote a duty or charge laid upon goods,\n   at a certain rate per cent upon their value, as stated in their\n   invoice, -- in opposition to a specific sum upon a given quantity or\n   number; as, an ad valorem duty of twenty per cent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advanced', 'imp. & p. p.', 'of Advance');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advancing', 'p. pr. & vb. n.', 'of Advance');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advance', 'v. t.', 'To bring forward; to move towards the van or front; to\n   make to go on.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advance', 'v. t.', 'To raise; to elevate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advance', 'v. t.', 'To raise to a higher rank; to promote.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advance', 'v. t.', 'To accelerate the growth or progress; to further; to\n   forward; to help on; to aid; to heighten; as, to advance the ripening\n   of fruit; to advance ones interests.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advance', 'v. t.', 'To bring to view or notice; to offer or propose; to\n   show; as, to advance an argument.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advance', 'v. t.', 'To make earlier, as an event or date; to hasten.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advance', 'v. t.', 'To furnish, as money or other value, before it becomes\n   due, or in aid of an enterprise; to supply beforehand; as, a merchant\n   advances money on a contract or on goods consigned to him.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advance', 'v. t.', 'To raise to a higher point; to enhance; to raise in\n   rate; as, to advance the price of goods.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advance', 'v. t.', 'To extol; to laud.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advance', 'v. i.', 'To move or go forward; to proceed; as, he advanced to\n   greet me.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advance', 'v. i.', 'To increase or make progress in any respect; as, to\n   advance in knowledge, in stature, in years, in price.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advance', 'v. i.', 'To rise in rank, office, or consequence; to be\n   preferred or promoted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advance', 'v.', 'The act of advancing or moving forward or upward;\n   progress.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advance', 'v.', 'Improvement or progression, physically, mentally, morally,\n   or socially; as, an advance in health, knowledge, or religion; an\n   advance in rank or office.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advance', 'v.', 'An addition to the price; rise in price or value; as, an\n   advance on the prime cost of goods.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advance', 'v.', 'The first step towards the attainment of a result;\n   approach made to gain favor, to form an acquaintance, to adjust a\n   difference, etc.; an overture; a tender; an offer; -- usually in the\n   plural.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advance', 'v.', 'A furnishing of something before an equivalent is received\n   (as money or goods); towards a capital or stock, or on loan; payment\n   beforehand; the money or goods thus furnished; money or value supplied\n   beforehand.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advance', 'a.', 'Before in place, or beforehand in time; -- used for\n   advanced; as, an advance guard, or that before the main guard or body\n   of an army; advance payment, or that made before it is due; advance\n   proofs, advance sheets, pages of a forthcoming volume, received in\n   advance of the time of publication.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advanced', 'a.', 'In the van or front.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advanced', 'a.', 'In the front or before others, as regards progress or\n   ideas; as, advanced opinions, advanced thinkers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advanced', 'a.', 'Far on in life or time.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advancement', 'v. t.', 'The act of advancing, or the state of being\n   advanced; progression; improvement; furtherance; promotion to a higher\n   place or dignity; as, the advancement of learning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advancement', 'v. t.', 'An advance of money or value; payment in advance.\n   See Advance, 5.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advancement', 'v. t.', 'Property given, usually by a parent to a child, in\n   advance of a future distribution.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advancement', 'v. t.', 'Settlement on a wife, or jointure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Advancer', 'n.', 'One who advances; a promoter.');
