  INSERT INTO words (word, word_type, description) VALUES  (  'Aeration', 'n.', 'A change produced in the blood by exposure to the air in\n   respiration; oxygenation of the blood in respiration; arterialization.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aeration', 'n.', 'The act or preparation of charging with carbonic acid gas\n   or with oxygen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerator', 'n.', 'That which supplies with air; esp. an apparatus used for\n   charging mineral waters with gas and in making soda water.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerial', 'a.', 'Of or pertaining to the air, or atmosphere; inhabiting or\n   frequenting the air; produced by or found in the air; performed in the\n   air; as, aerial regions or currents.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerial', 'a.', 'Consisting of air; resembling, or partaking of the nature\n   of air. Hence: Unsubstantial; unreal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerial', 'a.', 'Rising aloft in air; high; lofty; as, aerial spires.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerial', 'a.', 'Growing, forming, or existing in the air, as opposed to\n   growing or existing in earth or water, or underground; as, aerial\n   rootlets, aerial plants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerial', 'a.', 'Light as air; ethereal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aeriality', 'n.', 'The state of being aerial; unsubstantiality.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerially', 'adv.', 'Like, or from, the air; in an aerial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerie', 'n.', 'The nest of a bird of prey, as of an eagle or hawk; also a\n   brood of such birds; eyrie. Shak. Also fig.: A human residence or\n   resting place perched like an eagles nest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aeriferous', 'a.', 'Conveying or containing air; air-bearing; as, the\n   windpipe is an aeriferous tube.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerification', 'n.', 'The act of combining air with another substance, or\n   the state of being filled with air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerification', 'n.', 'The act of becoming aerified, or of changing from a\n   solid or liquid form into an aeriform state; the state of being\n   aeriform.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aeriform', 'a.', 'Having the form or nature of air, or of an elastic fluid;\n   gaseous. Hence fig.: Unreal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerify', 'v. t.', 'To infuse air into; to combine air with.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerify', 'v. t.', 'To change into an aeriform state.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aero-', '', 'The combining form of the Greek word meaning air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerobies', 'n. pl.', 'Microorganisms which live in contact with the air and\n   need oxygen for their growth; as the microbacteria which form on the\n   surface of putrefactive fluids.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerobiotic', 'a.', 'Related to, or of the nature of, aerobies; as,\n   aerobiotic plants, which live only when supplied with free oxygen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerocyst', 'n.', 'One of the air cells of algals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerodynamic', 'a.', 'Pertaining to the force of air in motion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerodynamics', 'n.', 'The science which treats of the air and other gaseous\n   bodies under the action of force, and of their mechanical effects.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerognosy', 'n.', 'The science which treats of the properties of the air,\n   and of the part it plays in nature.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerographer', 'n.', 'One versed in aeography: an aerologist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerographic', 'a.', 'Alt. of Aerographical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerographical', 'a.', 'Pertaining to aerography; aerological.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerography', 'n.', 'A description of the air or atmosphere; aerology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerohydrodynamic', 'a.', 'Acting by the force of air and water; as, an\n   aerohydrodynamic wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerolite', 'n.', 'A stone, or metallic mass, which has fallen to the earth\n   from distant space; a meteorite; a meteoric stone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerolith', 'n.', 'Same as A/rolite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerolithology', 'n.', 'The science of aerolites.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerolitic', 'a.', 'Of or pertaining to aerolites; meteoric; as, aerolitic\n   iron.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerologic', 'a.', 'Alt. of Aerological');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerological', 'a.', 'Of or pertaining to aerology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerologist', 'n.', 'One versed in aerology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerology', 'n.', 'That department of physics which treats of the\n   atmosphere.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aeromancy', 'n.', 'Divination from the state of the air or from atmospheric\n   substances; also, forecasting changes in the weather.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerometer', 'n.', 'An instrument for ascertaining the weight or density of\n   air and gases.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerometric', 'a.', 'Of or pertaining to aerometry; as, aerometric\n   investigations.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerometry', 'n.', 'The science of measuring the air, including the doctrine\n   of its pressure, elasticity, rarefaction, and condensation; pneumatics.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aeronaut', 'n.', 'An aerial navigator; a balloonist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aeronautic', 'a.', 'Alt. of Aeronautical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aeronautical', 'a.', 'Pertaining to aeronautics, or aerial sailing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aeronautics', 'n.', 'The science or art of ascending and sailing in the\n   air, as by means of a balloon; aerial navigation; ballooning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerophobia', 'n.', 'Alt. of Aerophoby');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerophoby', 'n.', 'Dread of a current of air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerophyte', 'n.', 'A plant growing entirely in the air, and receiving its\n   nourishment from it; an air plant or epiphyte.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aeroplane', 'n.', 'A flying machine, or a small plane for experiments on\n   flying, which floats in the air only when propelled through it.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aeroscope', 'n.', 'An apparatus designed for collecting spores, germs,\n   bacteria, etc., suspended in the air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aeroscopy', 'n.', 'The observation of the state and variations of the\n   atmosphere.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerose', 'a.', 'Of the nature of, or like, copper; brassy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerosiderite', 'n.', 'A mass of meteoric iron.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerosphere', 'n.', 'The atmosphere.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerostat', 'n.', 'A balloon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerostat', 'n.', 'A balloonist; an aeronaut.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerostatic', 'a.', 'Alt. of Aerostatical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerostatical', 'a.', 'Of or pertaining to aerostatics; pneumatic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerostatical', 'a.', 'Aeronautic; as, an aerostatic voyage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerostatics', 'n.', 'The science that treats of the equilibrium of elastic\n   fluids, or that of bodies sustained in them. Hence it includes\n   aeronautics.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerostation', 'n.', 'Aerial navigation; the art of raising and guiding\n   balloons in the air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerostation', 'n.', 'The science of weighing air; aerostatics.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aeruginous', 'a.', 'Of the nature or color of verdigris, or the rust of\n   copper.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aerugo', 'n.', 'The rust of any metal, esp. of brass or copper; verdigris.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aery', 'n.', 'An aerie.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aery', 'a.', 'Aerial; ethereal; incorporeal; visionary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aesculapian', 'a.', 'Pertaining to Aesculapius or to the healing art;\n   medical; medicinal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aesculapius', 'n.', 'The god of medicine. Hence, a physician.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aesculin', 'n.', 'Same as Esculin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Esopian', 'a.', 'Of or pertaining to Aesop, or in his manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aesopic', 'a.', 'Alt. of Esopic');
  INSERT INTO words (word, word_type, description) VALUES  (  'Esopic', 'a.', 'Same as Aesopian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aesthesia', 'n.', 'Perception by the senses; feeling; -- the opposite of\n   anaesthesia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aesthesiometer', 'n.', 'Alt. of Esthesiometer');
  INSERT INTO words (word, word_type, description) VALUES  (  'Esthesiometer', 'n.', 'An instrument to measure the degree of sensation, by\n   determining at how short a distance two impressions upon the skin can\n   be distinguished, and thus to determine whether the condition of\n   tactile sensibility is normal or altered.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aesthesis', 'n.', 'Sensuous perception.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aesthesodic', 'a.', 'Conveying sensory or afferent impulses; -- said of\n   nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aesthete', 'n.', 'One who makes much or overmuch of aesthetics.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aesthetic', 'a.', 'Alt. of Aesthetical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aesthetical', 'a.', 'Of or Pertaining to aesthetics; versed in aesthetics;\n   as, aesthetic studies, emotions, ideas, persons, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aesthetican', 'n.', 'One versed in aesthetics.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aestheticism', 'n.', 'The doctrine of aesthetics; aesthetic principles;\n   devotion to the beautiful in nature and art.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aesthetics', 'n.', 'Alt. of Esthetics');
  INSERT INTO words (word, word_type, description) VALUES  (  'Esthetics', 'n.', 'The theory or philosophy of taste; the science of the\n   beautiful in nature and art; esp. that which treats of the expression\n   and embodiment of beauty by art.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aestho-physiology', 'n.', 'The science of sensation in relation to nervous\n   action.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aestival', 'a.', 'Of or belonging to the summer; as, aestival diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aestivate', 'v. i.', 'To spend the summer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aestivate', 'v. i.', 'To pass the summer in a state of torpor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aestivation', 'n.', 'The state of torpidity induced by the heat and dryness\n   of summer, as in certain snails; -- opposed to hibernation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aestivation', 'n.', 'The arrangement of the petals in a flower bud, as to\n   folding, overlapping, etc.; prefloration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aestuary', 'n. & a.', 'See Estuary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aestuous', 'a.', 'Glowing; agitated, as with heat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aetheogamous', 'a.', 'Propagated in an unusual way; cryptogamous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aether', 'n.', 'See Ether.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aethiops mineral', '', 'Same as Ethiops mineral.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aethogen', 'n.', 'A compound of nitrogen and boro/, which, when heated\n   before the blowpipe, gives a brilliant phosphorescent; boric nitride.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aethrioscope', 'n.', 'An instrument consisting in part of a differential\n   thermometer. It is used for measuring changes of temperature produced\n   by different conditions of the sky, as when clear or clouded.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aetiological', 'a.', 'Pertaining to aetiology; assigning a cause.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aetiology', 'n.', 'The science, doctrine, or demonstration of causes; esp.,\n   the investigation of the causes of any disease; the science of the\n   origin and development of things.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aetiology', 'n.', 'The assignment of a cause.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aetites', 'n.', 'See Eaglestone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afar', 'adv.', 'At, to, or from a great distance; far away; -- often used\n   with from preceding, or off following; as, he was seen from afar; I saw\n   him afar off.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afeard', 'p. a.', 'Afraid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afer', 'n.', 'The southwest wind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affability', 'n.', 'The quality of being affable; readiness to converse;\n   courteousness in receiving others and in conversation; complaisant\n   behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affable', 'a.', 'Easy to be spoken to or addressed; receiving others kindly\n   and conversing with them in a free and friendly manner; courteous;\n   sociable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affable', 'a.', 'Gracious; mild; benign.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affableness', 'n.', 'Affability.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affably', 'adv.', 'In an affable manner; courteously.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affabrous', 'a.', 'Executed in a workmanlike manner; ingeniously made.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affair', 'n.', 'That which is done or is to be done; matter; concern; as, a\n   difficult affair to manage; business of any kind, commercial,\n   professional, or public; -- often in the plural. \"At the head of\n   affairs.\" Junius.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affair', 'n.', 'Any proceeding or action which it is wished to refer to or\n   characterize vaguely; as, an affair of honor, i. e., a duel; an affair\n   of love, i. e., an intrigue.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affair', 'n.', 'An action or engagement not of sufficient magnitude to be\n   called a battle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affair', 'n.', 'Action; endeavor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affair', 'n.', 'A material object (vaguely designated).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affamish', 'v. t. & i.', 'To afflict with, or perish from, hunger.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affamishment', 'n.', 'Starvation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affatuate', 'v. t.', 'To infatuate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affear', 'v. t.', 'To frighten.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affected', 'imp. & p. p.', 'of Affect');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affecting', 'p. pr. & vb. n.', 'of Affect');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affect', 'v. t.', 'To act upon; to produce an effect or change upon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affect', 'v. t.', 'To influence or move, as the feelings or passions; to\n   touch.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affect', 'v. t.', 'To love; to regard with affection.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affect', 'v. t.', 'To show a fondness for; to like to use or practice; to\n   choose; hence, to frequent habitually.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affect', 'v. t.', 'To dispose or incline.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affect', 'v. t.', 'To aim at; to aspire; to covet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affect', 'v. t.', 'To tend to by affinity or disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affect', 'v. t.', 'To make a show of; to put on a pretense of; to feign; to\n   assume; as, to affect ignorance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affect', 'v. t.', 'To assign; to appoint.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affect', 'n.', 'Affection; inclination; passion; feeling; disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affectation', 'n.', 'An attempt to assume or exhibit what is not natural or\n   real; false display; artificial show.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affectation', 'n.', 'A striving after.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affectation', 'n.', 'Fondness; affection.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affectationist', 'n.', 'One who exhibits affectation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affected', 'p. p. & a.', 'Regarded with affection; beloved.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affected', 'p. p. & a.', 'Inclined; disposed; attached.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affected', 'p. p. & a.', 'Given to false show; assuming or pretending to\n   possess what is not natural or real.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affected', 'p. p. & a.', 'Assumed artificially; not natural.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affected', 'p. p. & a.', 'Made up of terms involving different powers of\n   the unknown quantity; adfected; as, an affected equation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affectedly', 'adv.', 'In an affected manner; hypocritically; with more show\n   than reality.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affectedly', 'adv.', 'Lovingly; with tender care.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affectedness', 'n.', 'Affectation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affecter', 'n.', 'One who affects, assumes, pretends, or strives after.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affectibility', 'n.', 'The quality or state of being affectible.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affectible', 'a.', 'That may be affected.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affecting', 'a.', 'Moving the emotions; fitted to excite the emotions;\n   pathetic; touching; as, an affecting address; an affecting sight.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affecting', 'a.', 'Affected; given to false show.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affectingly', 'adv.', 'In an affecting manner; is a manner to excite\n   emotions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affection', 'n.', 'The act of affecting or acting upon; the state of being\n   affected.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affection', 'n.', 'An attribute; a quality or property; a condition; a\n   bodily state; as, figure, weight, etc. , are affections of bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affection', 'n.', 'Bent of mind; a feeling or natural impulse or natural\n   impulse acting upon and swaying the mind; any emotion; as, the\n   benevolent affections, esteem, gratitude, etc.; the malevolent\n   affections, hatred, envy, etc.; inclination; disposition; propensity;\n   tendency.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affection', 'n.', 'A settled good will; kind feeling; love; zealous or\n   tender attachment; -- often in the pl. Formerly followed by to, but now\n   more generally by for or towards; as, filial, social, or conjugal\n   affections; to have an affection for or towards children.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affection', 'n.', 'Prejudice; bias.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affection', 'n.', 'Disease; morbid symptom; malady; as, a pulmonary\n   affection.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affection', 'n.', 'The lively representation of any emotion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affection', 'n.', 'Affectation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affection', 'n.', 'Passion; violent emotion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affectional', 'a.', 'Of or pertaining to the affections; as, affectional\n   impulses; an affectional nature.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affectionate', 'a.', 'Having affection or warm regard; loving; fond; as, an\n   affectionate brother.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affectionate', 'a.', 'Kindly inclined; zealous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affectionate', 'a.', 'Proceeding from affection; indicating love; tender;\n   as, the affectionate care of a parent; affectionate countenance,\n   message, language.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affectionate', 'a.', 'Strongly inclined; -- with to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affectionated', 'a.', 'Disposed; inclined.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affectionately', 'adv.', 'With affection; lovingly; fondly; tenderly;\n   kindly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affectionateness', 'n.', 'The quality of being affectionate; fondness;\n   affection.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affectioned', 'a.', 'Disposed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affectioned', 'a.', 'Affected; conceited.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affective', 'a.', 'Tending to affect; affecting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affective', 'a.', 'Pertaining to or exciting emotion; affectional;\n   emotional.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affectively', 'adv.', 'In an affective manner; impressively; emotionally.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affectuous', 'a.', 'Full of passion or emotion; earnest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affeer', 'v. t.', 'To confirm; to assure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affeer', 'v. t.', 'To assess or reduce, as an arbitrary penalty or\n   amercement, to a certain and reasonable sum.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affeerer', 'n.', 'Alt. of Affeeror');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affeeror', 'n.', 'One who affeers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affeerment', 'n.', 'The act of affeering.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afferent', 'a.', 'Bearing or conducting inwards to a part or organ; --\n   opposed to efferent; as, afferent vessels; afferent nerves, which\n   convey sensations from the external organs to the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affettuoso', 'adv.', 'With feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affiance', 'n.', 'Plighted faith; marriage contract or promise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affiance', 'n.', 'Trust; reliance; faith; confidence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affianced', 'imp. / p. p.', 'of Affiance');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affiancing', 'p. pr. / vb. n.', 'of Affiance');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affiance', 'v. t.', 'To betroth; to pledge ones faith to for marriage, or\n   solemnly promise (ones self or another) in marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affiance', 'v. t.', 'To assure by promise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affiancer', 'n.', 'One who makes a contract of marriage between two\n   persons.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affiant', 'n.', 'One who makes an affidavit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affidavit', 'n.', 'A sworn statement in writing; a declaration in writing,\n   signed and made upon oath before an authorized magistrate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affile', 'v. t.', 'To polish.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affiliable', 'a.', 'Capable of being affiliated to or on, or connected with\n   in origin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affiliated', 'imp. & p. p.', 'of Affiliate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affiliating', 'p. pr. & vb. n.', 'of Affiliate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affiliate', 'v. t.', 'To adopt; to receive into a family as a son; hence,\n   to bring or receive into close connection; to ally.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affiliate', 'v. t.', 'To fix the paternity of; -- said of an illegitimate\n   child; as, to affiliate the child to (or on or upon) one man rather\n   than another.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affiliate', 'v. t.', 'To connect in the way of descent; to trace origin to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affiliate', 'v. t.', 'To attach (to) or unite (with); to receive into a\n   society as a member, and initiate into its mysteries, plans, etc.; --\n   followed by to or with.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affiliate', 'v. i.', 'To connect or associate ones self; -- followed by\n   with; as, they affiliate with no party.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affiliation', 'n.', 'Adoption; association or reception as a member in or\n   of the same family or society.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affiliation', 'n.', 'The establishment or ascertaining of parentage; the\n   assignment of a child, as a bastard, to its father; filiation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affiliation', 'n.', 'Connection in the way of descent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affinal', 'a.', 'Related by marriage; from the same source.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affine', 'v. t.', 'To refine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affined', 'a.', 'Joined in affinity or by any tie.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affinitative', 'a.', 'Of the nature of affinity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affinitive', 'a.', 'Closely connected, as by affinity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affinities', 'pl. ', 'of Affinity');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affinity', 'n.', 'Relationship by marriage (as between a husband and his\n   wifes blood relations, or between a wife and her husbands blood\n   relations); -- in contradistinction to consanguinity, or relationship\n   by blood; -- followed by with, to, or between.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affinity', 'n.', 'Kinship generally; close agreement; relation; conformity;\n   resemblance; connection; as, the affinity of sounds, of colors, or of\n   languages.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affinity', 'n.', 'Companionship; acquaintance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affinity', 'n.', 'That attraction which takes place, at an insensible\n   distance, between the heterogeneous particles of bodies, and unites\n   them to form chemical compounds; chemism; chemical or elective affinity\n   or attraction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affinity', 'n.', 'A relation between species or highe/ groups dependent on\n   resemblance in the whole plan of structure, and indicating community of\n   origin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affinity', 'n.', 'A superior spiritual relationship or attraction held to\n   exist sometimes between persons, esp. persons of the opposite sex;\n   also, the man or woman who exerts such psychical or spiritual\n   attraction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirmed', 'imp. & p. p.', 'of Affirm');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirming', 'p. pr. & vb. n.', 'of Affirm');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirm', 'v. t.', 'to assert or confirm, as a judgment, decree, or order,\n   brought before an appellate court for review.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirm', 'v. t.', 'To assert positively; to tell with confidence; to aver;\n   to maintain as true; -- opposed to deny.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirm', 'v. t.', 'To declare, as a fact, solemnly, under judicial\n   sanction. See Affirmation, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirm', 'v. i.', 'To declare or assert positively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirm', 'v. i.', 'To make a solemn declaration, before an authorized\n   magistrate or tribunal, under the penalties of perjury; to testify by\n   affirmation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirmable', 'a.', 'Capable of being affirmed, asserted, or declared; --\n   followed by of; as, an attribute affirmable of every just man.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirmance', 'n.', 'Confirmation; ratification; confirmation of a voidable\n   act.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirmance', 'n.', 'A strong declaration; affirmation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirmant', 'n.', 'One who affirms or asserts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirmant', 'n.', 'One who affirms, instead of taking an oath.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirmation', 'n.', 'Confirmation of anything established; ratification;\n   as, the affirmation of a law.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirmation', 'n.', 'The act of affirming or asserting as true; assertion;\n   -- opposed to negation or denial.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirmation', 'n.', 'That which is asserted; an assertion; a positive\n   statement; an averment; as, an affirmation, by the vender, of title to\n   property sold, or of its quality.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirmation', 'n.', 'A solemn declaration made under the penalties of\n   perjury, by persons who conscientiously decline taking an oath, which\n   declaration is in law equivalent to an oath.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirmative', 'a.', 'Confirmative; ratifying; as, an act affirmative of\n   common law.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirmative', 'a.', 'That affirms; asserting that the fact is so;\n   declaratory of what exists; answering \"yes\" to a question; -- opposed\n   to negative; as, an affirmative answer; an affirmative vote.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirmative', 'a.', 'Positive; dogmatic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirmative', 'a.', 'Expressing the agreement of the two terms of a\n   proposition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirmative', 'a.', 'Positive; -- a term applied to quantities which are to\n   be added, and opposed to negative, or such as are to be subtracted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirmative', 'n.', 'That which affirms as opposed to that which denies; an\n   affirmative proposition; that side of question which affirms or\n   maintains the proposition stated; -- opposed to negative; as, there\n   were forty votes in the affirmative, and ten in the negative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirmative', 'n.', 'A word or phrase expressing affirmation or assent; as,\n   yes, that is so, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirmatively', 'adv.', 'In an affirmative manner; on the affirmative side\n   of a question; in the affirmative; -- opposed to negatively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirmatory', 'a.', 'Giving affirmation; assertive; affirmative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affirmer', 'n.', 'One who affirms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affixed', 'imp. & p. p.', 'of Affix');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affixing', 'p. pr. & vb. n.', 'of Affix');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affix', 'v. t.', 'To subjoin, annex, or add at the close or end; to append\n   to; to fix to any part of; as, to affix a syllable to a word; to affix\n   a seal to an instrument; to affix ones name to a writing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affix', 'v. t.', 'To fix or fasten in any way; to attach physically.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affix', 'v. t.', 'To attach, unite, or connect with; as, names affixed to\n   ideas, or ideas affixed to things; to affix a stigma to a person; to\n   affix ridicule or blame to any one.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affix', 'v. t.', 'To fix or fasten figuratively; -- with on or upon; as,\n   eyes affixed upon the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affixes', 'pl. ', 'of Affix');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affix', 'n.', 'That which is affixed; an appendage; esp. one or more\n   letters or syllables added at the end of a word; a suffix; a postfix.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affixion', 'n.', 'Affixture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affixture', 'n.', 'The act of affixing, or the state of being affixed;\n   attachment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afflation', 'n.', 'A blowing or breathing on; inspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afflatus', 'n.', 'A breath or blast of wind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afflatus', 'n.', 'A divine impartation of knowledge; supernatural impulse;\n   inspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afflicted', 'imp. & p. p.', 'of Afflict');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afflicting', 'p. pr. & vb. n.', 'of Afflict');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afflict', 'v. t.', 'To strike or cast down; to overthrow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afflict', 'v. t.', 'To inflict some great injury or hurt upon, causing\n   continued pain or mental distress; to trouble grievously; to torment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afflict', 'v. t.', 'To make low or humble.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afflict', 'p. p. & a.', 'Afflicted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afflictedness', 'n.', 'The state of being afflicted; affliction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afflicter', 'n.', 'One who afflicts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afflicting', 'a.', 'Grievously painful; distressing; afflictive; as, an\n   afflicting event. -- Af*flict\"ing*ly, adv.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affliction', 'n.', 'The cause of continued pain of body or mind, as\n   sickness, losses, etc.; an instance of grievous distress; a pain or\n   grief.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affliction', 'n.', 'The state of being afflicted; a state of pain,\n   distress, or grief.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afflictionless', 'a.', 'Free from affliction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afflictive', 'a.', 'Giving pain; causing continued or repeated pain or\n   grief; distressing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afflictively', 'adv.', 'In an afflictive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affluence', 'n.', 'A flowing to or towards; a concourse; an influx.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affluence', 'n.', 'An abundant supply, as of thought, words, feelings,\n   etc.; profusion; also, abundance of property; wealth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affluency', 'n.', 'Affluence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affluent', 'a.', 'Flowing to; flowing abundantly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affluent', 'a.', 'Abundant; copious; plenteous; hence, wealthy; abounding\n   in goods or riches.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affluent', 'n.', 'A stream or river flowing into a larger river or into a\n   lake; a tributary stream.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affluently', 'adv.', 'Abundantly; copiously.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affluentness', 'n.', 'Great plenty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afflux', 'n.', 'A flowing towards; that which flows to; as, an afflux of\n   blood to the head.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affluxion', 'n.', 'The act of flowing towards; afflux.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affodill', 'n.', 'Asphodel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afforce', 'v. t.', 'To reenforce; to strengthen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afforcement', 'n.', 'A fortress; a fortification for defense.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afforcement', 'n.', 'A reenforcement; a strengthening.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afforciament', 'n.', 'See Afforcement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afforded', 'imp. & p. p.', 'of Afford');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affording', 'p. pr. & vb. n.', 'of Afford');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afford', 'v. t.', 'To give forth; to supply, yield, or produce as the\n   natural result, fruit, or issue; as, grapes afford wine; olives afford\n   oil; the earth affords fruit; the sea affords an abundant supply of\n   fish.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afford', 'v. t.', 'To give, grant, or confer, with a remoter reference to\n   its being the natural result; to provide; to furnish; as, a good life\n   affords consolation in old age.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afford', 'v. t.', 'To offer, provide, or supply, as in selling, granting,\n   expending, with profit, or without loss or too great injury; as, A\n   affords his goods cheaper than B; a man can afford a sum yearly in\n   charity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afford', 'v. t.', 'To incur, stand, or bear without serious detriment, as\n   an act which might under other circumstances be injurious; -- with an\n   auxiliary, as can, could, might, etc.; to be able or rich enough.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affordable', 'a.', 'That may be afforded.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affordment', 'n.', 'Anything given as a help; bestowal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afforest', 'v. t.', 'To convert into a forest; as, to afforest a tract of\n   country.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afforestation', 'n.', 'The act of converting into forest or woodland.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afformative', 'n.', 'An affix.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affranchise', 'v. t.', 'To make free; to enfranchise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affranchisement', 'n.', 'The act of making free; enfranchisement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affrap', 'v. t. & i.', 'To strike, or strike down.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affrayed', 'p. p.', 'of Affray');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affray', 'v. t.', 'To startle from quiet; to alarm.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affray', 'v. t.', 'To frighten; to scare; to frighten away.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affray', 'v. t.', 'The act of suddenly disturbing any one; an assault or\n   attack.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affray', 'v. t.', 'Alarm; terror; fright.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affray', 'v. t.', 'A tumultuous assault or quarrel; a brawl; a fray.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affray', 'v. t.', 'The fighting of two or more persons, in a public place,\n   to the terror of others.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affrayer', 'n.', 'One engaged in an affray.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affrayment', 'n.', 'Affray.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affreight', 'v. t.', 'To hire, as a ship, for the transportation of goods\n   or freight.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affreighter', 'n.', 'One who hires or charters a ship to convey goods.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affreightment', 'n.', 'The act of hiring, or the contract for the use of, a\n   vessel, or some part of it, to convey cargo.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affret', 'n.', 'A furious onset or attack.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affriction', 'n.', 'The act of rubbing against.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affriended', 'p. p.', 'Made friends; reconciled.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affrighted', 'imp. & p. p.', 'of Affright');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affrighting', 'p. pr. & vb. n.', 'of Affright');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affright', 'v. t.', 'To impress with sudden fear; to frighten; to alarm.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affright', 'p. a.', 'Affrighted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affright', 'n.', 'Sudden and great fear; terror. It expresses a stronger\n   impression than fear, or apprehension, perhaps less than terror.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affright', 'n.', 'The act of frightening; also, a cause of terror; an\n   object of dread.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affrightedly', 'adv.', 'With fright.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affrighten', 'v. t.', 'To frighten.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affrighter', 'n.', 'One who frightens.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affrightful', 'a.', 'Terrifying; frightful.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affrightment', 'n.', 'Affright; the state of being frightened; sudden fear\n   or alarm.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affronted', 'imp. & p. p.', 'of Affront');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affronting', 'p. pr. & vb. n.', 'of Affront');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affront', 'v. t.', 'To front; to face in position; to meet or encounter\n   face to face.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affront', 'v. t.', 'To face in defiance; to confront; as, to affront death;\n   hence, to meet in hostile encounter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affront', 'v. t.', 'To offend by some manifestation of disrespect; to\n   insult to the face by demeanor or language; to treat with marked\n   incivility.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affront', 'n.', 'An encounter either friendly or hostile.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affront', 'n.', 'Contemptuous or rude treatment which excites or justifies\n   resentment; marked disrespect; a purposed indignity; insult.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affront', 'n.', 'An offense to ones self-respect; shame.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affronte', 'a.', 'Face to face, or front to front; facing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affrontedly', 'adv.', 'Shamelessly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affrontee', 'n.', 'One who receives an affront.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affronter', 'n.', 'One who affronts, or insults to the face.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affrontingly', 'adv.', 'In an affronting manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affrontive', 'a.', 'Tending to affront or offend; offensive; abusive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affrontiveness', 'n.', 'The quality that gives an affront or offense.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affused', 'imp. & p. p.', 'of Affuse');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affusing', 'p. pr. & vb. n.', 'of Affuse');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affuse', 'v. t.', 'To pour out or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affusion', 'n.', 'The act of pouring upon, or sprinkling with a liquid, as\n   water upon a child in baptism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affusion', 'n.', 'The act of pouring water or other fluid on the whole or a\n   part of the body, as a remedy in disease.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affied', 'imp. & p. p.', 'of Affy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affying', 'p. pr.', 'of Affy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affy', 'v. t.', 'To confide (ones self to, or in); to trust.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affy', 'v. t.', 'To betroth or espouse; to affiance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affy', 'v. t.', 'To bind in faith.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Affy', 'v. i.', 'To trust or confide.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afghan', 'a.', 'Of or pertaining to Afghanistan.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afghan', 'n.', 'A native of Afghanistan.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afghan', 'n.', 'A kind of worsted blanket or wrap.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afield', 'adv.', 'To, in, or on the field.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afield', 'adv.', 'Out of the way; astray.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afire', 'adv. & a.', 'On fire.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aflame', 'adv. & a.', 'Inflames; glowing with light or passion; ablaze.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aflat', 'adv.', 'Level with the ground; flat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aflaunt', 'adv. & a.', 'In a flaunting state or position.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aflicker', 'adv. & a.', 'In a flickering state.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afloat', 'adv. & a.', 'Borne on the water; floating; on board ship.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afloat', 'adv. & a.', 'Moving; passing from place to place; in general\n   circulation; as, a rumor is afloat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afloat', 'adv. & a.', 'Unfixed; moving without guide or control; adrift;\n   as, our affairs are all afloat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aflow', 'adv. & a.', 'Flowing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aflush', 'adv. & a.', 'In a flushed or blushing state.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aflush', 'adv. & a.', 'On a level.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aflutter', 'adv. & a.', 'In a flutter; agitated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afoam', 'adv. & a.', 'In a foaming state; as, the sea is all afoam.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afoot', 'adv.', 'On foot.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afoot', 'adv.', 'Fig.: In motion; in action; astir; in progress.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afore', 'adv.', 'Before.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afore', 'adv.', 'In the fore part of a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afore', 'prep.', 'Before (in all its senses).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afore', 'prep.', 'Before; in front of; farther forward than; as, afore the\n   windlass.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aforecited', 'a.', 'Named or quoted before.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aforegoing', 'a.', 'Going before; foregoing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aforehand', 'adv.', 'Beforehand; in anticipation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aforehand', 'a.', 'Prepared; previously provided; -- opposed to behindhand.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aforementioned', 'a.', 'Previously mentioned; before-mentioned.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aforenamed', 'a.', 'Named before.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aforesaid', 'a.', 'Said before, or in a preceding part; already described\n   or identified.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aforethought', 'a.', 'Premeditated; prepense; previously in mind; designed;\n   as, malice aforethought, which is required to constitute murder.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aforethought', 'n.', 'Premeditation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aforetime', 'adv.', 'In time past; formerly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'A fortiori', '', 'With stronger reason.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afoul', 'adv. & a.', 'In collision; entangled.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afraid', 'p. a.', 'Impressed with fear or apprehension; in fear;\n   apprehensive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afreet', 'n.', 'Same as Afrit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afresh', 'adv.', 'Anew; again; once more; newly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afric', 'a.', 'African.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afric', 'n.', 'Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (  'African', 'a.', 'Of or pertaining to Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (  'African', 'n.', 'A native of Africa; also one ethnologically belonging to\n   an African race.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Africander', 'n.', 'One born in Africa, the offspring of a white father and\n   a \"colored\" mother. Also, and now commonly in Southern Africa, a native\n   born of European settlers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Africanism', 'n.', 'A word, phrase, idiom, or custom peculiar to Africa or\n   Africans.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Africanize', 'v. t.', 'To place under the domination of Africans or\n   negroes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afrit', 'n.', 'Alt. of Afreet');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afrite', 'n.', 'Alt. of Afreet');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afreet', 'n.', 'A powerful evil jinnee, demon, or monstrous giant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afront', 'adv.', 'In front; face to face.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afront', 'prep.', 'In front of.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aft', 'adv. & a.', 'Near or towards the stern of a vessel; astern; abaft.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After', 'a.', 'Next; later in time; subsequent; succeeding; as, an after\n   period of life.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After', 'a.', 'Hinder; nearer the rear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After', 'a.', 'To ward the stern of the ship; -- applied to any object in\n   the rear part of a vessel; as the after cabin, after hatchway.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After', 'prep.', 'Behind in place; as, men in line one after another.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After', 'prep.', 'Below in rank; next to in order.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After', 'prep.', 'Later in time; subsequent; as, after supper, after three\n   days. It often precedes a clause. Formerly that was interposed between\n   it and the clause.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After', 'prep.', 'Subsequent to and in consequence of; as, after what you\n   have said, I shall be careful.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After', 'prep.', 'Subsequent to and notwithstanding; as, after all our\n   advice, you took that course.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After', 'prep.', 'Moving toward from behind; following, in search of; in\n   pursuit of.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After', 'prep.', 'Denoting the aim or object; concerning; in relation to;\n   as, to look after workmen; to inquire after a friend; to thirst after\n   righteousness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After', 'prep.', 'In imitation of; in conformity with; after the manner of;\n   as, to make a thing after a model; a picture after Rubens; the boy\n   takes after his father.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After', 'prep.', 'According to; in accordance with; in conformity with the\n   nature of; as, he acted after his kind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After', 'prep.', 'According to the direction and influence of; in\n   proportion to; befitting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After', 'adv.', 'Subsequently in time or place; behind; afterward; as, he\n   follows after.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afterbirth', 'n.', 'The placenta and membranes with which the fetus is\n   connected, and which come away after delivery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aftercast', 'n.', 'A throw of dice after the game in ended; hence, anything\n   done too late.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afterclap', 'n.', 'An unexpected subsequent event; something disagreeable\n   happening after an affair is supposed to be at an end.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aftercrop', 'n.', 'A second crop or harvest in the same year.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After damp', '', 'An irrespirable gas, remaining after an explosion of fire\n   damp in mines; choke damp. See Carbonic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After-dinner', 'n.', 'The time just after dinner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After-dinner', 'a.', 'Following dinner; post-prandial; as, an after-dinner\n   nap.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After-eatage', 'n.', 'Aftergrass.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aftereye', 'v. t.', 'To look after.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aftergame', 'n.', 'A second game; hence, a subsequent scheme or expedient.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After-glow', 'n.', 'A glow of refulgence in the western sky after sunset.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aftergrass', 'n.', 'The grass that grows after the first crop has been\n   mown; aftermath.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aftergrowth', 'n.', 'A second growth or crop, or (metaphorically)\n   development.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afterguard', 'n.', 'The seaman or seamen stationed on the poop or after\n   part of the ship, to attend the after-sails.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After-image', 'n.', 'The impression of a vivid sensation retained by the\n   retina of the eye after the cause has been removed; also extended to\n   impressions left of tones, smells, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afterings', 'n. pl.', 'The last milk drawn in milking; strokings.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aftermath', 'n.', 'A second moving; the grass which grows after the first\n   crop of hay in the same season; rowen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After-mentioned', 'a.', 'Mentioned afterwards; as, persons after-mentioned\n   (in a writing).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aftermost', 'a. superl.', 'Hindmost; -- opposed to foremost.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aftermost', 'a. superl.', 'Nearest the stern; most aft.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afternoon', 'n.', 'The part of the day which follows noon, between noon and\n   evening.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After-note', 'n.', 'One of the small notes occur on the unaccented parts of\n   the measure, taking their time from the preceding note.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afterpains', 'n. pl.', 'The pains which succeed childbirth, as in expelling\n   the afterbirth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afterpiece', 'n.', 'A piece performed after a play, usually a farce or\n   other small entertainment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afterpiece', 'n.', 'The heel of a rudder.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After-sails', 'n.', 'The sails on the mizzenmast, or on the stays between\n   the mainmast and mizzenmast.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aftershaft', 'n.', 'The hypoptilum.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aftertaste', 'n.', 'A taste which remains in the mouth after eating or\n   drinking.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afterthought', 'n.', 'Reflection after an act; later or subsequent thought\n   or expedient.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afterwards', 'adv.', 'Alt. of Afterward');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afterward', 'adv.', 'At a later or succeeding time.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Afterwise', 'a.', 'Wise after the event; wise or knowing, when it is too\n   late.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After-wit', 'n.', 'Wisdom or perception that comes after it can be of use.');
  INSERT INTO words (word, word_type, description) VALUES  (  'After-witted', 'a.', 'Characterized by after-wit; slow-witted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aftmost', 'a.', 'Nearest the stern.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aftward', 'adv.', 'Toward the stern.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aga', 'n.', 'Alt. of Agha');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agha', 'n.', 'In Turkey, a commander or chief officer. It is used also as a\n   title of respect.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Again', 'adv.', 'In return, back; as, bring us word again.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Again', 'adv.', 'Another time; once more; anew.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Again', 'adv.', 'Once repeated; -- of quantity; as, as large again, half as\n   much again.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Again', 'adv.', 'In any other place.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Again', 'adv.', 'On the other hand.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Again', 'adv.', 'Moreover; besides; further.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Again', 'prep.', 'Alt. of Agains');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agains', 'prep.', 'Against; also, towards (in order to meet).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Againbuy', 'v. t.', 'To redeem.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Againsay', 'v. t.', 'To gainsay.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Against', 'prep.', 'Abreast; opposite to; facing; towards; as, against the\n   mouth of a river; -- in this sense often preceded by over.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Against', 'prep.', 'From an opposite direction so as to strike or come in\n   contact with; in contact with; upon; as, hail beats against the roof.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Against', 'prep.', 'In opposition to, whether the opposition is of\n   sentiment or of action; on the other side; counter to; in contrariety\n   to; hence, adverse to; as, against reason; against law; to run a race\n   against time.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Against', 'prep.', 'By of before the time that; in preparation for; so as\n   to be ready for the time when.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Againstand', 'v. t.', 'To withstand.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Againward', 'adv.', 'Back again.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agalactia', 'n.', 'Alt. of Agalaxy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agalaxy', 'n.', 'Failure of the due secretion of milk after childbirth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agalactous', 'a.', 'Lacking milk to suckle with.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agal-agal', 'n.', 'Same as Agar-agar.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agalloch', 'n.', 'Alt. of Agallochum');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agallochum', 'n.', 'A soft, resinous wood (Aquilaria Agallocha) of highly\n   aromatic smell, burnt by the orientals as a perfume. It is called also\n   agalwood and aloes wood. The name is also given to some other species.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agalmatolite', 'n.', 'A soft, compact stone, of a grayish, greenish, or\n   yellowish color, carved into images by the Chinese, and hence called\n   figure stone, and pagodite. It is probably a variety of pinite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agama', 'n.', 'A genus of lizards, one of the few which feed upon vegetable\n   substances; also, one of these lizards.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agamis', 'pl. ', 'of Agami');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agami', 'n.', 'A South American bird (Psophia crepitans); allied to the\n   cranes, and easily domesticated; -- called also the gold-breasted\n   trumpeter. Its body is about the size of the pheasant. See Trumpeter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agamic', 'a.', 'Produced without sexual union; as, agamic or unfertilized\n   eggs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agamic', 'a.', 'Not having visible organs of reproduction, as flowerless\n   plants; agamous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agamically', 'adv.', 'In an agamic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agamist', 'n.', 'An unmarried person; also, one opposed to marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agamogenesis', 'n.', 'Reproduction without the union of parents of distinct\n   sexes: asexual reproduction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agamogenetic', 'n.', 'Reproducing or produced without sexual union.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agamous', 'a.', 'Having no visible sexual organs; asexual.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agamous', 'a.', 'cryptogamous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aganglionic', 'a.', 'Without ganglia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agape', 'adv. & a.', 'Gaping, as with wonder, expectation, or eager\n   attention.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agapae', 'pl. ', 'of Agape');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agape', 'n.', 'The love feast of the primitive Christians, being a meal\n   partaken of in connection with the communion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agar-agar', 'n.', 'A fucus or seaweed much used in the East for soups and\n   jellies; Ceylon moss (Gracilaria lichenoides).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agaric', 'n.', 'A fungus of the genus Agaricus, of many species, of which\n   the common mushroom is an example.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agaric', 'n.', 'An old name for several species of Polyporus, corky fungi\n   growing on decaying wood.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agasp', 'adv. & a.', 'In a state of gasping.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agast', 'v. t.', 'Alt. of Aghast');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aghast', 'v. t.', 'To affright; to terrify.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agast', 'p. p. & a.', 'See Aghast.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agastric', 'a.', 'Having to stomach, or distinct digestive canal, as the\n   tapeworm.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agate', 'adv.', 'On the way; agoing; as, to be agate; to set the bells\n   agate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agate', 'n.', 'A semipellucid, uncrystallized variety of quartz, presenting\n   various tints in the same specimen. Its colors are delicately arranged\n   in stripes or bands, or blended in clouds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agate', 'n.', 'A kind of type, larger than pearl and smaller than\n   nonpareil; in England called ruby.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agate', 'n.', 'A diminutive person; so called in allusion to the small\n   figures cut in agate for rings and seals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agate', 'n.', 'A tool used by gold-wire drawers, bookbinders, etc.; -- so\n   called from the agate fixed in it for burnishing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agatiferous', 'a.', 'Containing or producing agates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agatine', 'a.', 'Pertaining to, or like, agate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agatize', 'v. t.', 'To convert into agate; to make resemble agate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agaty', 'a.', 'Of the nature of agate, or containing agate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agave', 'n.', 'A genus of plants (order Amaryllidaceae) of which the chief\n   species is the maguey or century plant (A. Americana); wrongly called\n   Aloe. It is from ten to seventy years, according to climate, in\n   attaining maturity, when it produces a gigantic flower stem, sometimes\n   forty feet in height, and perishes. The fermented juice is the pulque\n   of the Mexicans; distilled, it yields mescal. A strong thread and a\n   tough paper are made from the leaves, and the wood has many uses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agazed', 'p. p.', 'Gazing with astonishment; amazed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Age', 'n.', 'The whole duration of a being, whether animal, vegetable, or\n   other kind; lifetime.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Age', 'n.', 'That part of the duration of a being or a thing which is\n   between its beginning and any given time; as, what is the present age\n   of a man, or of the earth?');
  INSERT INTO words (word, word_type, description) VALUES  (  'Age', 'n.', 'The latter part of life; an advanced period of life;\n   seniority; state of being old.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Age', 'n.', 'One of the stages of life; as, the age of infancy, of youth,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Age', 'n.', 'Mature age; especially, the time of life at which one attains\n   full personal rights and capacities; as, to come of age; he (or she) is\n   of age.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Age', 'n.', 'The time of life at which some particular power or capacity is\n   understood to become vested; as, the age of consent; the age of\n   discretion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Age', 'n.', 'A particular period of time in history, as distinguished from\n   others; as, the golden age, the age of Pericles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Age', 'n.', 'A great period in the history of the Earth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Age', 'n.', 'A century; the period of one hundred years.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Age', 'n.', 'The people who live at a particular period; hence, a\n   generation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Age', 'n.', 'A long time.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aged', 'imp. & p. p.', 'of Age');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aging', 'p. pr. & vb. n.', 'of Age');
  INSERT INTO words (word, word_type, description) VALUES  (  'Age', 'v. i.', 'To grow aged; to become old; to show marks of age; as, he\n   grew fat as he aged.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Age', 'v. t.', 'To cause to grow old; to impart the characteristics of age\n   to; as, grief ages us.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aged', 'a.', 'Old; having lived long; having lived almost to or beyond the\n   usual time allotted to that species of being; as, an aged man; an aged\n   oak.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aged', 'a.', 'Belonging to old age.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aged', 'a.', 'Having a certain age; at the age of; having lived; as, a man\n   aged forty years.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agedly', 'adv.', 'In the manner of an aged person.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agedness', 'n.', 'The quality of being aged; oldness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ageless', 'a.', 'Without old age limits of duration; as, fountains of\n   ageless youth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agen', 'adv. & prep.', 'See Again.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agencies', 'pl. ', 'of Agency');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agency', 'n.', 'The faculty of acting or of exerting power; the state of\n   being in action; action; instrumentality.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agency', 'n.', 'The office of an agent, or factor; the relation between a\n   principal and his agent; business of one intrusted with the concerns of\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agency', 'n.', 'The place of business of am agent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agend', 'n.', 'See Agendum.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agenda', 'pl. ', 'of Agendum');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agendum', 'n.', 'Something to be done; in the pl., a memorandum book.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agendum', 'n.', 'A church service; a ritual or liturgy. [In this sense,\n   usually Agenda.]');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agenesic', 'a.', 'Characterized by sterility; infecund.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agenesis', 'n.', 'Any imperfect development of the body, or any anomaly of\n   organization.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agennesis', 'n.', 'Impotence; sterility.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agent', 'a.', 'Acting; -- opposed to patient, or sustaining, action.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agent', 'n.', 'One who exerts power, or has the power to act; an actor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agent', 'n.', 'One who acts for, or in the place of, another, by authority\n   from him; one intrusted with the business of another; a substitute; a\n   deputy; a factor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agent', 'n.', 'An active power or cause; that which has the power to\n   produce an effect; as, a physical, chemical, or medicinal agent; as,\n   heat is a powerful agent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agential', 'a.', 'Of or pertaining to an agent or an agency.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agentship', 'n.', 'Agency.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ageratum', 'n.', 'A genus of plants, one species of which (A. Mexicanum)\n   has lavender-blue flowers in dense clusters.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggeneration', 'n.', 'The act of producing in addition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agger', 'n.', 'An earthwork; a mound; a raised work.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggerate', 'v. t.', 'To heap up.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggeration', 'n.', 'A heaping up; accumulation; as, aggerations of sand.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggerose', 'a.', 'In heaps; full of heaps.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggest', 'v. t.', 'To heap up.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglomerated', 'imp. & p. p.', 'of Agglomerate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglomerating', 'p. pr. & vb. n.', 'of Agglomerate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglomerate', 'v. t.', 'To wind or collect into a ball; hence, to gather\n   into a mass or anything like a mass.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglomerate', 'v. i.', 'To collect in a mass.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglomerate', 'a.', 'Alt. of Agglomerated');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglomerated', 'a.', 'Collected into a ball, heap, or mass.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglomerated', 'a.', 'Collected into a rounded head of flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglomerate', 'n.', 'A collection or mass.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglomerate', 'n.', 'A mass of angular volcanic fragments united by heat;\n   -- distinguished from conglomerate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglomeration', 'n.', 'The act or process of collecting in a mass; a\n   heaping together.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglomeration', 'n.', 'State of being collected in a mass; a mass; cluster.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglomerative', 'a.', 'Having a tendency to gather together, or to make\n   collections.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglutinant', 'a.', 'Uniting, as glue; causing, or tending to cause,\n   adhesion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglutinant', 'n.', 'Any viscous substance which causes bodies or parts to\n   adhere.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglutinated', 'imp. & p. p.', 'of Agglutinate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglutinating', 'p. pr. & vb. n.', 'of Agglutinate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglutinate', 'v. t.', 'To unite, or cause to adhere, as with glue or other\n   viscous substance; to unite by causing an adhesion of substances.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglutinate', 'a.', 'United with glue or as with glue; cemented together.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglutinate', 'a.', 'Consisting of root words combined but not materially\n   altered as to form or meaning; as, agglutinate forms, languages, etc.\n   See Agglutination, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglutination', 'n.', 'The act of uniting by glue or other tenacious\n   substance; the state of being thus united; adhesion of parts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglutination', 'n.', 'Combination in which root words are united with\n   little or no change of form or loss of meaning. See Agglutinative, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglutinative', 'a.', 'Pertaining to agglutination; tending to unite, or\n   having power to cause adhesion; adhesive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agglutinative', 'a.', 'Formed or characterized by agglutination, as a\n   language or a compound.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggrace', 'v. t.', 'To favor; to grace.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggrace', 'n.', 'Grace; favor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggrandizable', 'a.', 'Capable of being aggrandized.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggrandization', 'n.', 'Aggrandizement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggrandized', 'imp. & p. p.', 'of Aggrandize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggrandizing', 'p. pr. & vb. n.', 'of Aggrandize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggrandize', 'v. t.', 'To make great; to enlarge; to increase; as, to\n   aggrandize our conceptions, authority, distress.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggrandize', 'v. t.', 'To make great or greater in power, rank, honor, or\n   wealth; -- applied to persons, countries, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggrandize', 'v. t.', 'To make appear great or greater; to exalt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggrandize', 'v. i.', 'To increase or become great.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggrandizement', 'n.', 'The act of aggrandizing, or the state of being\n   aggrandized or exalted in power, rank, honor, or wealth; exaltation;\n   enlargement; as, the emperor seeks only the aggrandizement of his own\n   family.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggrandizer', 'n.', 'One who aggrandizes, or makes great.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggrate', 'a.', 'To please.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggravated', 'imp. & p. p.', 'of Aggravate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggravating', 'p. pr. & vb. n.', 'of Aggravate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggravate', 'v. t.', 'To make heavy or heavier; to add to; to increase.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggravate', 'v. t.', 'To make worse, or more severe; to render less\n   tolerable or less excusable; to make more offensive; to enhance; to\n   intensify.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggravate', 'v. t.', 'To give coloring to in description; to exaggerate;\n   as, to aggravate circumstances.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggravate', 'v. t.', 'To exasperate; to provoke; to irritate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggravating', 'a.', 'Making worse or more heinous; as, aggravating\n   circumstances.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggravating', 'a.', 'Exasperating; provoking; irritating.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggravatingly', 'adv.', 'In an aggravating manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggravation', 'n.', 'The act of aggravating, or making worse; -- used of\n   evils, natural or moral; the act of increasing in severity or\n   heinousness; something additional to a crime or wrong and enhancing its\n   guilt or injurious consequences.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggravation', 'n.', 'Exaggerated representation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggravation', 'n.', 'An extrinsic circumstance or accident which increases\n   the guilt of a crime or the misery of a calamity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggravation', 'n.', 'Provocation; irritation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggravative', 'a.', 'Tending to aggravate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggravative', 'n.', 'That which aggravates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggregated', 'imp. & p. p.', 'of Aggregate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggregating', 'p. pr. & vb. n.', 'of Aggregate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggregate', 'v. t.', 'To bring together; to collect into a mass or sum.\n   \"The aggregated soil.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggregate', 'v. t.', 'To add or unite, as, a person, to an association.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggregate', 'v. t.', 'To amount in the aggregate to; as, ten loads,\n   aggregating five hundred bushels.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggregate', 'a.', 'Formed by a collection of particulars into a whole mass\n   or sum; collective.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggregate', 'a.', 'Formed into clusters or groups of lobules; as, aggregate\n   glands.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggregate', 'a.', 'Composed of several florets within a common involucre,\n   as in the daisy; or of several carpels formed from one flower, as in\n   the raspberry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggregate', 'a.', 'Having the several component parts adherent to each\n   other only to such a degree as to be separable by mechanical means.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggregate', 'a.', 'United into a common organized mass; -- said of certain\n   compound animals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggregate', 'n.', 'A mass, assemblage, or sum of particulars; as, a house\n   is an aggregate of stone, brick, timber, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggregate', 'n.', 'A mass formed by the union of homogeneous particles; --\n   in distinction from a compound, formed by the union of heterogeneous\n   particles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggregately', 'adv.', 'Collectively; in mass.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggregation', 'n.', 'The act of aggregating, or the state of being\n   aggregated; collection into a mass or sum; a collection of particulars;\n   an aggregate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggregative', 'a.', 'Taken together; collective.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggregative', 'a.', 'Gregarious; social.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggregator', 'n.', 'One who aggregates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggrege', 'v. t.', 'To make heavy; to aggravate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggressed', 'imp. & p. p.', 'of Aggress');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggressing', 'p. pr. & vb. n.', 'of Aggress');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggress', 'v. i.', 'To commit the first act of hostility or offense; to\n   begin a quarrel or controversy; to make an attack; -- with on.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggress', 'v. t.', 'To set upon; to attack.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggress', 'n.', 'Aggression.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggression', 'n.', 'The first attack, or act of hostility; the first act of\n   injury, or first act leading to a war or a controversy; unprovoked\n   attack; assault; as, a war of aggression. \"Aggressions of power.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggressive', 'a.', 'Tending or disposed to aggress; characterized by\n   aggression; making assaults; unjustly attacking; as, an aggressive\n   policy, war, person, nation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggressor', 'n.', 'The person who first attacks or makes an aggression; he\n   who begins hostility or a quarrel; an assailant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggrievance', 'n.', 'Oppression; hardship; injury; grievance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggrieved', 'imp. & p. p.', 'of Aggrieve');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggrieving', 'p. pr. & vb. n.', 'of Aggrieve');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggrieve', 'v. t.', 'To give pain or sorrow to; to afflict; hence, to\n   oppress or injure in ones rights; to bear heavily upon; -- now\n   commonly used in the passive TO be aggrieved.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggrieve', 'v. i.', 'To grieve; to lament.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggrouped', 'imp. & p. p.', 'of Aggroup');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggrouping', 'p. pr. & vb. n.', 'of Aggroup');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggroup', 'v. t.', 'To bring together in a group; to group.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggroupment', 'n.', 'Arrangement in a group or in groups; grouping.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggry', 'a.', 'Alt. of Aggri');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aggri', 'a.', 'Applied to a kind of variegated glass beads of ancient\n   manufacture; as, aggry beads are found in Ashantee and Fantee in\n   Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aghast', 'v. t.', 'See Agast, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aghast', 'a & p. p.', 'Terrified; struck with amazement; showing signs of\n   terror or horror.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agible', 'a.', 'Possible to be done; practicable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agile', 'a.', 'Having the faculty of quick motion in the limbs; apt or\n   ready to move; nimble; active; as, an agile boy; an agile tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agilely', 'adv.', 'In an agile manner; nimbly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agileness', 'n.', 'Agility; nimbleness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agility', 'n.', 'The quality of being agile; the power of moving the limbs\n   quickly and easily; nimbleness; activity; quickness of motion; as,\n   strength and agility of body.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agility', 'n.', 'Activity; powerful agency.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agios', 'pl. ', 'of Agio');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agio', 'n.', 'The premium or percentage on a better sort of money when it\n   is given in exchange for an inferior sort. The premium or discount on\n   foreign bills of exchange is sometimes called agio.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agiotage', 'n.', 'Exchange business; also, stockjobbing; the maneuvers of\n   speculators to raise or lower the price of stocks or public funds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agist', 'v. t.', 'To take to graze or pasture, at a certain sum; -- used\n   originally of the feeding of cattle in the kings forests, and\n   collecting the money for the same.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agistator', 'n.', 'See Agister.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agister', 'n.', 'Alt. of Agistor');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agistor', 'n.', 'Formerly, an officer of the kings forest, who had the\n   care of cattle agisted, and collected the money for the same; -- hence\n   called gisttaker, which in England is corrupted into guest-taker.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agistor', 'n.', 'Now, one who agists or takes in cattle to pasture at a\n   certain rate; a pasturer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agistment', 'n.', 'Formerly, the taking and feeding of other mens cattle\n   in the kings forests.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agistment', 'n.', 'The taking in by any one of other mens cattle to graze\n   at a certain rate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agistment', 'n.', 'The price paid for such feeding.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agistment', 'n.', 'A charge or rate against lands; as, an agistment of sea\n   banks, i. e., charge for banks or dikes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agitable', 'a.', 'Capable of being agitated, or easily moved.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agitated', 'imp. & p. p.', 'of Agitate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agitating', 'p. pr. & vb. n.', 'of Agitate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agitate', 'v. t.', 'To move with a violent, irregular action; as, the wind\n   agitates the sea; to agitate water in a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agitate', 'v. t.', 'To move or actuate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agitate', 'v. t.', 'To stir up; to disturb or excite; to perturb; as, he\n   was greatly agitated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agitate', 'v. t.', 'To discuss with great earnestness; to debate; as, a\n   controversy hotly agitated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agitate', 'v. t.', 'To revolve in the mind, or view in all its aspects; to\n   contrive busily; to devise; to plot; as, politicians agitate desperate\n   designs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agitatedly', 'adv.', 'In an agitated manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agitation', 'n.', 'The act of agitating, or the state of being agitated;\n   the state of being moved with violence, or with irregular action;\n   commotion; as, the sea after a storm is in agitation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agitation', 'n.', 'A stirring up or arousing; disturbance of tranquillity;\n   disturbance of mind which shows itself by physical excitement;\n   perturbation; as, to cause any one agitation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agitation', 'n.', 'Excitement of public feeling by discussion, appeals,\n   etc.; as, the antislavery agitation; labor agitation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agitation', 'n.', 'Examination or consideration of a subject in\n   controversy, or of a plan proposed for adoption; earnest discussion;\n   debate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agitative', 'a.', 'Tending to agitate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agitato', 'a.', 'Sung or played in a restless, hurried, and spasmodic\n   manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agitator', 'n.', 'One who agitates; one who stirs up or excites others; as,\n   political reformers and agitators.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agitator', 'n.', 'One of a body of men appointed by the army, in Cromwells\n   time, to look after their interests; -- called also adjutators.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agitator', 'n.', 'An implement for shaking or mixing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agleam', 'adv. & a.', 'Gleaming; as, faces agleam.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aglet', 'n.', 'Alt. of Aiglet');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aiglet', 'n.', 'A tag of a lace or of the points, braids, or cords formerly\n   used in dress. They were sometimes formed into small images. Hence,\n   \"aglet baby\" (Shak.); an aglet image.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aiglet', 'n.', 'A round white staylace.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agley', 'adv.', 'Aside; askew.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aglimmer', 'adv. & a.', 'In a glimmering state.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aglitter', 'adv. & a.', 'Glittering; in a glitter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aglossal', 'a.', 'Without tongue; tongueless.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aglow', 'adv. & a.', 'In a glow; glowing; as, cheeks aglow; the landscape\n   all aglow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aglutition', 'n.', 'Inability to swallow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agminal', 'a.', 'Pertaining to an army marching, or to a train.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agminate', 'a.', 'Alt. of Agminated');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agminated', 'a.', 'Grouped together; as, the agminated glands of Peyer in\n   the small intestine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnail', 'n.', 'A corn on the toe or foot.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnail', 'n.', 'An inflammation or sore under or around the nail; also, a\n   hangnail.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnate', 'a.', 'Related or akin by the fathers side; also, sprung from the\n   same male ancestor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnate', 'a.', 'Allied; akin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnate', 'n.', 'A relative whose relationship can be traced exclusively\n   through males.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnatic', 'a.', 'Pertaining to descent by the male line of ancestors.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnation', 'n.', 'Consanguinity by a line of males only, as distinguished\n   from cognation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnition', 'n.', 'Acknowledgment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnize', 'v. t.', 'To recognize; to acknowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnoiology', 'n.', 'The doctrine concerning those things of which we are\n   necessarily ignorant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnomen', 'n.', 'An additional or fourth name given by the Romans, on\n   account of some remarkable exploit or event; as, Publius Caius Scipio\n   Africanus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnomen', 'n.', 'An additional name, or an epithet appended to a name; as,\n   Aristides the Just.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnominate', 'v. t.', 'To name.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnomination', 'n.', 'A surname.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnomination', 'n.', 'Paronomasia; also, alliteration; annomination.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnostic', 'a.', 'Professing ignorance; involving no dogmatic; pertaining\n   to or involving agnosticism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnostic', 'n.', 'One who professes ignorance, or denies that we have any\n   knowledge, save of phenomena; one who supports agnosticism, neither\n   affirming nor denying the existence of a personal Deity, a future life,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnosticism', 'n.', 'That doctrine which, professing ignorance, neither\n   asserts nor denies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnosticism', 'n.', 'The doctrine that the existence of a personal Deity,\n   an unseen world, etc., can be neither proved nor disproved, because of\n   the necessary limits of the human mind (as sometimes charged upon\n   Hamilton and Mansel); or because of the insufficiency of the evidence\n   furnished by physical and physical data, to warrant a positive\n   conclusion (as taught by the school of Herbert Spencer); -- opposed\n   alike dogmatic skepticism and to dogmatic theism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnuses', 'pl. ', 'of Agnus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agni', 'pl. ', 'of Agnus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnus', 'n.', 'Agnus Dei.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnus castus', '', 'A species of Vitex (V. agnus castus); the chaste tree.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnus Dei', '', 'A figure of a lamb bearing a cross or flag.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnus Dei', '', 'A cake of wax stamped with such a figure. It is made from\n   the remains of the paschal candles and blessed by the Pope.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agnus Dei', '', 'A triple prayer in the sacrifice of the Mass, beginning\n   with the words \"Agnus Dei.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ago', 'a. & adv.', 'Past; gone by; since; as, ten years ago; gone long ago.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agog', 'a. & adv.', 'In eager desire; eager; astir.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agoing', 'adv.', 'In motion; in the act of going; as, to set a mill agoing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agones', 'pl. ', 'of Agon');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agon', 'n.', 'A contest for a prize at the public games.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agone', 'a. & adv.', 'Ago.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agone', 'n.', 'Agonic line.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agonic', 'a.', 'Not forming an angle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agonism', 'n.', 'Contention for a prize; a contest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agonist', 'n.', 'One who contends for the prize in public games.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agonistic', 'a.', 'Alt. of Agonistical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agonistical', 'a.', 'Pertaining to violent contests, bodily or mental;\n   pertaining to athletic or polemic feats; athletic; combative; hence,\n   strained; unnatural.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agonistically', 'adv.', 'In an agonistic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agonistics', 'n.', 'The science of athletic combats, or contests in public\n   games.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agonized', 'imp. & p. p.', 'of Agonize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agonizing', 'p. pr. & vb. n.', 'of Agonize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agonize', 'v. i.', 'To writhe with agony; to suffer violent anguish.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agonize', 'v. i.', 'To struggle; to wrestle; to strive desperately.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agonize', 'v. t.', 'To cause to suffer agony; to subject to extreme pain;\n   to torture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agonizingly', 'adv.', 'With extreme anguish or desperate struggles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agonothete', 'n.', 'An officer who presided over the great public games in\n   Greece.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agonothetic', 'a.', 'Pertaining to the office of an agonothete.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agonies', 'pl. ', 'of Agony');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agony', 'n.', 'Violent contest or striving.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agony', 'n.', 'Pain so extreme as to cause writhing or contortions of the\n   body, similar to those made in the athletic contests in Greece; and\n   hence, extreme pain of mind or body; anguish; paroxysm of grief;\n   specifically, the sufferings of Christ in the garden of Gethsemane.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agony', 'n.', 'Paroxysm of joy; keen emotion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agony', 'n.', 'The last struggle of life; death struggle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agood', 'adv.', 'In earnest; heartily.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agora', 'n.', 'An assembly; hence, the place of assembly, especially the\n   market place, in an ancient Greek city.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agouara', 'n.', 'The crab-eating raccoon (Procyon cancrivorus); found in\n   the tropical parts of America.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agouta', 'n.', 'A small insectivorous mammal (Solenodon paradoxus); allied\n   to the moles, found only in Hayti.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agouti', 'n.', 'Alt. of Agouty');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agouty', 'n.', 'A rodent of the genus Dasyprocta, about the size of a\n   rabbit, peculiar to South America and the West Indies. The most common\n   species is the Dasyprocta agouti.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrace', 'n. & v.', 'See Aggrace.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agraffe', 'n.', 'A hook or clasp.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agraffe', 'n.', 'A hook, eyelet, or other device by which a piano wire is\n   so held as to limit the vibration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrammatist', 'n.', 'A illiterate person.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agraphia', 'n.', 'The absence or loss of the power of expressing ideas by\n   written signs. It is one form of aphasia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agraphic', 'a.', 'Characterized by agraphia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrappes', 'n. pl.', 'Hooks and eyes for armor, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrarian', 'a.', 'Pertaining to fields, or lands, or their tenure; esp.,\n   relating to an equal or equitable division of lands; as, the agrarian\n   laws of Rome, which distributed the conquered and other public lands\n   among citizens.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrarian', 'a.', 'Wild; -- said of plants growing in the fields.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrarian', 'n.', 'One in favor of an equal division of landed property.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrarian', 'n.', 'An agrarian law.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrarianism', 'n.', 'An equal or equitable division of landed property; the\n   principles or acts of those who favor a redistribution of land.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrarianize', 'v. t.', 'To distribute according to, or to imbue with, the\n   principles of agrarianism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agre', 'adv.', 'Alt. of Agree');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agree', 'adv.', 'In good part; kindly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agreed', 'imp. & p. p.', 'of Agree');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agreeing', 'p. pr. & vb. n.', 'of Agree');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agree', 'v. i.', 'To harmonize in opinion, statement, or action; to be in\n   unison or concord; to be or become united or consistent; to concur; as,\n   all parties agree in the expediency of the law.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agree', 'v. i.', 'To yield assent; to accede; -- followed by to; as, to\n   agree to an offer, or to opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agree', 'v. i.', 'To make a stipulation by way of settling differences or\n   determining a price; to exchange promises; to come to terms or to a\n   common resolve; to promise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agree', 'v. i.', 'To be conformable; to resemble; to coincide; to\n   correspond; as, the picture does not agree with the original; the two\n   scales agree exactly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agree', 'v. i.', 'To suit or be adapted in its effects; to do well; as, the\n   same food does not agree with every constitution.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agree', 'v. i.', 'To correspond in gender, number, case, or person.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agree', 'v. t.', 'To make harmonious; to reconcile or make friends.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agree', 'v. t.', 'To admit, or come to one mind concerning; to settle; to\n   arrange; as, to agree the fact; to agree differences.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agreeability', 'n.', 'Easiness of disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agreeability', 'n.', 'The quality of being, or making ones self,\n   agreeable; agreeableness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agreeable', 'a.', 'Pleasing, either to the mind or senses; pleasant;\n   grateful; as, agreeable manners or remarks; an agreeable person; fruit\n   agreeable to the taste.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agreeable', 'a.', 'Willing; ready to agree or consent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agreeable', 'a.', 'Agreeing or suitable; conformable; correspondent;\n   concordant; adapted; -- followed by to, rarely by with.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agreeable', 'a.', 'In pursuance, conformity, or accordance; -- in this\n   sense used adverbially for agreeably; as, agreeable to the order of the\n   day, the House took up the report.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agreeableness', 'n.', 'The quality of being agreeable or pleasing; that\n   quality which gives satisfaction or moderate pleasure to the mind or\n   senses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agreeableness', 'n.', 'The quality of being agreeable or suitable;\n   suitableness or conformity; consistency.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agreeableness', 'n.', 'Resemblance; concordance; harmony; -- with to or\n   between.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agreeably', 'adv.', 'In an agreeably manner; in a manner to give pleasure;\n   pleasingly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agreeably', 'adv.', 'In accordance; suitably; consistently; conformably; --\n   followed by to and rarely by with. See Agreeable, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agreeably', 'adv.', 'Alike; similarly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agreeingly', 'adv.', 'In an agreeing manner (to); correspondingly;\n   agreeably.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agreement', 'n.', 'State of agreeing; harmony of opinion, statement,\n   action, or character; concurrence; concord; conformity; as, a good\n   agreement subsists among the members of the council.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agreement', 'n.', 'Concord or correspondence of one word with another in\n   gender, number, case, or person.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agreement', 'n.', 'A concurrence in an engagement that something shall be\n   done or omitted; an exchange of promises; mutual understanding,\n   arrangement, or stipulation; a contract.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agreement', 'n.', 'The language, oral or written, embodying reciprocal\n   promises.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agreer', 'n.', 'One who agrees.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrestic', 'a.', 'Pertaining to fields or the country, in opposition to the\n   city; rural; rustic; unpolished; uncouth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrestical', 'a.', 'Agrestic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agricolation', 'n.', 'Agriculture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agricolist', 'n.', 'A cultivator of the soil; an agriculturist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agricultor', 'n.', 'An agriculturist; a farmer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agricultural', 'a.', 'Of or pertaining to agriculture; connected with, or\n   engaged in, tillage; as, the agricultural class; agricultural\n   implements, wages, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agriculturalist', 'n.', 'An agriculturist (which is the preferred form.)');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agriculture', 'n.', 'The art or science of cultivating the ground,\n   including the harvesting of crops, and the rearing and management of\n   live stock; tillage; husbandry; farming.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agriculturism', 'n.', 'Agriculture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agriculturist', 'n.', 'One engaged or skilled in agriculture; a husbandman.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrief', 'adv.', 'In grief; amiss.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrimony', 'n.', 'A genus of plants of the Rose family.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrimony', 'n.', 'The name is also given to various other plants; as, hemp\n   agrimony (Eupatorium cannabinum); water agrimony (Bidens).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrin', 'adv. & a.', 'In the act of grinning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agriologist', 'n.', 'One versed or engaged in agriology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agriology', 'n.', 'Description or comparative study of the customs of\n   savage or uncivilized tribes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrise', 'v. i.', 'To shudder with terror; to tremble with fear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrise', 'v. t.', 'To shudder at; to abhor; to dread; to loathe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrise', 'v. t.', 'To terrify; to affright.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrom', 'n.', 'A disease occurring in Bengal and other parts of the East\n   Indies, in which the tongue chaps and cleaves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agronomic', '', 'Alt. of Agronomical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agronomical', '', 'Pertaining to agronomy, of the management of farms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agronomics', 'n.', 'The science of the distribution and management of land.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agronomist', 'n.', 'One versed in agronomy; a student of agronomy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agronomy', 'n.', 'The management of land; rural economy; agriculture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrope', 'adv. & a.', 'In the act of groping.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrostis', 'n.', 'A genus of grasses, including species called in common\n   language bent grass. Some of them, as redtop (Agrostis vulgaris); are\n   valuable pasture grasses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrostographic', 'a.', 'Alt. of Agrostographical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrostographical', 'a.', 'Pertaining to agrostography.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrostography', 'n.', 'A description of the grasses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrostologic', 'a.', 'Alt. of Agrostological');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrostological', 'a.', 'Pertaining to agrostology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrostologist', 'n.', 'One skilled in agrostology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrostology', 'n.', 'That part of botany which treats of the grasses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aground', 'adv. & a.', 'On the ground; stranded; -- a nautical term applied\n   to a ship when its bottom lodges on the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agroupment', 'n.', 'See Aggroupment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agrypnotic', 'n.', 'Anything which prevents sleep, or produces wakefulness,\n   as strong tea or coffee.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aguardiente', 'n.', 'A inferior brandy of Spain and Portugal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aguardiente', 'n.', 'A strong alcoholic drink, especially pulque.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ague', 'n.', 'An acute fever.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ague', 'n.', 'An intermittent fever, attended by alternate cold and hot\n   fits.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ague', 'n.', 'The cold fit or rigor of the intermittent fever; as, fever\n   and ague.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ague', 'n.', 'A chill, or state of shaking, as with cold.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agued', 'imp. & p. p.', 'of Ague');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ague', 'v. t.', 'To strike with an ague, or with a cold fit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aguilt', 'v. t.', 'To be guilty of; to offend; to sin against; to wrong.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aguise', 'n.', 'Dress.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aguise', 'v. t.', 'To dress; to attire; to adorn.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aguish', 'a.', 'Having the qualities of an ague; somewhat cold or\n   shivering; chilly; shaky.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aguish', 'a.', 'Productive of, or affected by, ague; as, the aguish\n   districts of England.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agush', 'adv. & a.', 'In a gushing state.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Agynous', 'a.', 'Without female organs; male.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ah', 'interj.', 'An exclamation, expressive of surprise, pity, complaint,\n   entreaty, contempt, threatening, delight, triumph, etc., according to\n   the manner of utterance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aha', 'interj.', 'An exclamation expressing, by different intonations,\n   triumph, mixed with derision or irony, or simple surprise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aha', 'n.', 'A sunk fence. See Ha-ha.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ahead', 'adv.', 'In or to the front; in advance; onward.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ahead', 'adv.', 'Headlong; without restraint.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aheap', 'adv.', 'In a heap; huddled together.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aheight', 'adv.', 'Aloft; on high.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ahem', 'interj.', 'An exclamation to call ones attention; hem.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ahey', 'interj.', 'Hey; ho.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ahigh', 'adv.', 'On high.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ahold', 'adv.', 'Near the wind; as, to lay a ship ahold.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ahorseback', 'adv.', 'On horseback.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ahoy', 'interj.', 'A term used in hailing; as, \"Ship ahoy.\"');
