  INSERT INTO words (word, word_type, description) VALUES  (  'Ahu', 'n.', 'The Asiatic gazelle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ahull', 'adv.', 'With the sails furled, and the helm lashed alee; --\n   applied to ships in a storm. See Hull, n.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ahungered', 'a.', 'Pinched with hunger; very hungry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ais', 'pl. ', 'of Ai');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ai', 'n.', 'The three-toed sloth (Bradypus tridactylus) of South America.\n   See Sloth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aiblins', 'adv.', 'Alt. of Ablins');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ablins', 'adv.', 'Perhaps; possibly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aichs metal', '', 'A kind of gun metal, containing copper, zinc, and iron,\n   but no tin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aided', 'imp. & p. p.', 'of Aid');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aiding', 'p. pr. & vb. n.', 'of Aid');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aid', 'v. t.', 'To support, either by furnishing strength or means in\n   cooperation to effect a purpose, or to prevent or to remove evil; to\n   help; to assist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aid', 'v. t.', 'Help; succor; assistance; relief.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aid', 'v. t.', 'The person or thing that promotes or helps in something\n   done; a helper; an assistant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aid', 'v. t.', 'A subsidy granted to the king by Parliament; also, an\n   exchequer loan.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aid', 'v. t.', 'A pecuniary tribute paid by a vassal to his lord on special\n   occasions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aid', 'v. t.', 'An aid-de-camp, so called by abbreviation; as, a generals\n   aid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aidance', 'n.', 'Aid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aidant', 'a.', 'Helping; helpful; supplying aid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aids-de-camp', 'pl. ', 'of Aid-de-camp');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aid-de-camp', 'n.', 'An officer selected by a general to carry orders, also\n   to assist or represent him in correspondence and in directing\n   movements.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aider', 'n.', 'One who, or that which, aids.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aidful', 'a.', 'Helpful.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aidless', 'a.', 'Helpless; without aid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aid-major', 'n.', 'The adjutant of a regiment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aiel', 'n.', 'See Ayle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aiglet', 'n.', 'Same as Aglet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aigre', 'a.', 'Sour.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aigremore', 'n.', 'Charcoal prepared for making powder.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aigret', 'n.', 'Alt. of Aigrette');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aigrette', 'n.', 'The small white European heron. See Egret.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aigrette', 'n.', 'A plume or tuft for the head composed of feathers, or of\n   gems, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aigrette', 'n.', 'A tuft like that of the egret.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aigrette', 'n.', 'A feathery crown of seed; egret; as, the aigrette or down\n   of the dandelion or the thistle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aiguille', 'n.', 'A needle-shaped peak.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aiguille', 'n.', 'An instrument for boring holes, used in blasting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aiguillette', 'n.', 'A point or tag at the end of a fringe or lace; an\n   aglet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aiguillette', 'n.', 'One of the ornamental tags, cords, or loops on some\n   military and naval uniforms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aigulet', 'n.', 'See Aglet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ailed', 'imp. & p. p.', 'of Ail');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ailing', 'p. pr. & vb. n.', 'of Ail');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ail', 'v. t.', 'To affect with pain or uneasiness, either physical or\n   mental; to trouble; to be the matter with; -- used to express some\n   uneasiness or affection, whose cause is unknown; as, what ails the man?\n   I know not what ails him.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ail', 'v. i.', 'To be affected with pain or uneasiness of any sort; to be\n   ill or indisposed or in trouble.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ail', 'n.', 'Indisposition or morbid affection.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ailanthus', 'n.', 'Same as Ailantus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ailantus', 'n.', 'A genus of beautiful trees, natives of the East Indies.\n   The tree imperfectly di/cious, and the staminate or male plant is very\n   offensive when blossom.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ailette', 'n.', 'A small square shield, formerly worn on the shoulders of\n   knights, -- being the prototype of the modern epaulet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ailment', 'n.', 'Indisposition; morbid affection of the body; -- not\n   applied ordinarily to acute diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ailuroidea', 'n. pl.', 'A group of the Carnivora, which includes the cats,\n   civets, and hyenas.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aimed', 'imp. & p. p.', 'of Aim');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aiming', 'p. pr. & vb. n.', 'of Aim');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aim', 'v. i.', 'To point or direct a missile weapon, or a weapon which\n   propels as missile, towards an object or spot with the intent of\n   hitting it; as, to aim at a fox, or at a target.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aim', 'v. i.', 'To direct the indention or purpose; to attempt the\n   accomplishment of a purpose; to try to gain; to endeavor; -- followed\n   by at, or by an infinitive; as, to aim at distinction; to aim to do\n   well.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aim', 'v. i.', 'To guess or conjecture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aim', 'v. t.', 'To direct or point, as a weapon, at a particular object; to\n   direct, as a missile, an act, or a proceeding, at, to, or against an\n   object; as, to aim a musket or an arrow, the fist or a blow (at\n   something); to aim a satire or a reflection (at some person or vice).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aim', 'v. i.', 'The pointing of a weapon, as a gun, a dart, or an arrow, in\n   the line of direction with the object intended to be struck; the line\n   of fire; the direction of anything, as a spear, a blow, a discourse, a\n   remark, towards a particular point or object, with a view to strike or\n   affect it.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aim', 'v. i.', 'The point intended to be hit, or object intended to be\n   attained or affected.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aim', 'v. i.', 'Intention; purpose; design; scheme.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aim', 'v. i.', 'Conjecture; guess.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aimer', 'n.', 'One who aims, directs, or points.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aimless', 'a.', 'Without aim or purpose; as, an aimless life.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aino', 'n.', 'One of a peculiar race inhabiting Yesso, the Kooril Islands\n   etc., in the northern part of the empire of Japan, by some supposed to\n   have been the progenitors of the Japanese. The Ainos are stout and\n   short, with hairy bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aint', '', 'A contraction for are not and am not; also used for is not.\n   [Colloq. or illiterate speech]. See Ant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air', 'n.', 'The fluid which we breathe, and which surrounds the earth; the\n   atmosphere. It is invisible, inodorous, insipid, transparent,\n   compressible, elastic, and ponderable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air', 'n.', 'Symbolically: Something unsubstantial, light, or volatile.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air', 'n.', 'A particular state of the atmosphere, as respects heat, cold,\n   moisture, etc., or as affecting the sensations; as, a smoky air, a damp\n   air, the morning air, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air', 'n.', 'Any aeriform body; a gas; as, oxygen was formerly called vital\n   air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air', 'n.', 'Air in motion; a light breeze; a gentle wind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air', 'n.', 'Odoriferous or contaminated air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air', 'n.', 'That which surrounds and influences.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air', 'n.', 'Utterance abroad; publicity; vent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air', 'n.', 'Intelligence; information.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air', 'n.', 'A musical idea, or motive, rhythmically developed in\n   consecutive single tones, so as to form a symmetrical and balanced\n   whole, which may be sung by a single voice to the stanzas of a hymn or\n   song, or even to plain prose, or played upon an instrument; a melody; a\n   tune; an aria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air', 'n.', 'In harmonized chorals, psalmody, part songs, etc., the part\n   which bears the tune or melody -- in modern harmony usually the upper\n   part -- is sometimes called the air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air', 'n.', 'The peculiar look, appearance, and bearing of a person; mien;\n   demeanor; as, the air of a youth; a heavy air; a lofty air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air', 'n.', 'Peculiar appearance; apparent character; semblance; manner;\n   style.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air', 'n.', 'An artificial or affected manner; show of pride or vanity;\n   haughtiness; as, it is said of a person, he puts on airs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air', 'n.', 'The representation or reproduction of the effect of the\n   atmospheric medium through which every object in nature is viewed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air', 'n.', 'Carriage; attitude; action; movement; as, the head of that\n   portrait has a good air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air', 'n.', 'The artificial motion or carriage of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aired', 'imp. & p. p.', 'of Air');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airing', 'p. pr. & vb. n.', 'of Air');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air', 'n.', 'To expose to the air for the purpose of cooling, refreshing,\n   or purifying; to ventilate; as, to air a room.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air', 'n.', 'To expose for the sake of public notice; to display\n   ostentatiously; as, to air ones opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air', 'n.', 'To expose to heat, for the purpose of expelling dampness, or\n   of warming; as, to air linen; to air liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air bed', '', 'A sack or matters inflated with air, and used as a bed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air bladder', '', 'An air sac, sometimes double or variously lobed, in the\n   visceral cavity of many fishes. It originates in the same way as the\n   lungs of air-breathing vertebrates, and in the adult may retain a\n   tubular connection with the pharynx or esophagus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air bladder', '', 'A sac or bladder full of air in an animal or plant; also\n   an air hole in a casting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air brake', '', 'A railway brake operated by condensed air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air-built', 'a.', 'Erected in the air; having no solid foundation;\n   chimerical; as, an air-built castle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air cell', '', 'A cavity in the cellular tissue of plants, containing air\n   only.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air cell', '', 'A receptacle of air in various parts of the system; as, a\n   cell or minute cavity in the walls of the air tubes of the lungs; the\n   air sac of birds; a dilatation of the air vessels in insects.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air chamber', '', 'A chamber or cavity filled with air, in an animal or\n   plant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air chamber', '', 'A cavity containing air to act as a spring for\n   equalizing the flow of a liquid in a pump or other hydraulic machine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air cock', '', 'A faucet to allow escape of air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air-drawn', 'a.', 'Drawn in air; imaginary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air drill', '', 'A drill driven by the elastic pressure of condensed air; a\n   pneumatic drill.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air engine', '', 'An engine driven by heated or by compressed air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airer', 'n.', 'One who exposes to the air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airer', 'n.', 'A frame on which clothes are aired or dried.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air gas', '', 'See under Gas.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air gun', '', 'A kind of gun in which the elastic force of condensed air is\n   used to discharge the ball. The air is powerfully compressed into a\n   reservoir attached to the gun, by a condensing pump, and is controlled\n   by a valve actuated by the trigger.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air hole', '', 'A hole to admit or discharge air; specifically, a spot in\n   the ice not frozen over.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air hole', '', 'A fault in a casting, produced by a bubble of air; a\n   blowhole.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airily', 'adv.', 'In an airy manner; lightly; gaily; jauntily; flippantly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airiness', 'n.', 'The state or quality of being airy; openness or exposure\n   to the air; as, the airiness of a country seat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airiness', 'n.', 'Lightness of spirits; gayety; levity; as, the airiness of\n   young persons.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airing', 'n.', 'A walk or a ride in the open air; a short excursion for\n   healths sake.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airing', 'n.', 'An exposure to air, or to a fire, for warming, drying,\n   etc.; as, the airing of linen, or of a room.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air jacket', '', 'A jacket having air-tight cells, or cavities which can be\n   filled with air, to render persons buoyant in swimming.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airless', 'a.', 'Not open to a free current of air; wanting fresh air, or\n   communication with the open air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air level', '', 'Spirit level. See Level.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airlike', 'a.', 'Resembling air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airling', 'n.', 'A thoughtless, gay person.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airometer', 'n.', 'A hollow cylinder to contain air. It is closed above and\n   open below, and has its open end plunged into water.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air pipe', '', 'A pipe for the passage of air; esp. a ventilating pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air plant', '', 'A plant deriving its sustenance from the air alone; an\n   aerophyte.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air poise', '', 'An instrument to measure the weight of air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air pump', '', 'A kind of pump for exhausting air from a vessel or closed\n   space; also, a pump to condense air or force it into a closed space.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air pump', '', 'A pump used to exhaust from a condenser the condensed\n   steam, the water used for condensing, and any commingled air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air sac', '', 'One of the spaces in different parts of the bodies of birds,\n   which are filled with air and connected with the air passages of the\n   lungs; an air cell.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air shaft', '', 'A passage, usually vertical, for admitting fresh air into\n   a mine or a tunnel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air-slacked', 'a.', 'Slacked, or pulverized, by exposure to the air; as,\n   air-slacked lime.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air stove', '', 'A stove for heating a current of air which is directed\n   against its surface by means of pipes, and then distributed through a\n   building.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air-tight', 'a.', 'So tight as to be impermeable to air; as, an air-tight\n   cylinder.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air-tight', 'n.', 'A stove the draft of which can be almost entirely shut\n   off.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Air vessel', '', 'A vessel, cell, duct, or tube containing or conducting\n   air; as the air vessels of insects, birds, plants, etc.; the air vessel\n   of a pump, engine, etc. For the latter, see Air chamber. The air\n   vessels of insects are called tracheae, of plants spiral vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airward', 'adv.', 'Alt. of Airwards');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airwards', 'adv.', 'Toward the air; upward.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airy', 'a.', 'Consisting of air; as, an airy substance; the airy parts of\n   bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airy', 'a.', 'Relating or belonging to air; high in air; aerial; as, an\n   airy flight.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airy', 'a.', 'Open to a free current of air; exposed to the air; breezy;\n   as, an airy situation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airy', 'a.', 'Resembling air; thin; unsubstantial; not material; airlike.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airy', 'a.', 'Relating to the spirit or soul; delicate; graceful; as, airy\n   music.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airy', 'a.', 'Without reality; having no solid foundation; empty; trifling;\n   visionary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airy', 'a.', 'Light of heart; vivacious; sprightly; flippant; superficial.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airy', 'a.', 'Having an affected manner; being in the habit of putting on\n   airs; affectedly grand.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Airy', 'a.', 'Having the light and aerial tints true to nature.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aisle', 'n.', 'A lateral division of a building, separated from the middle\n   part, called the nave, by a row of columns or piers, which support the\n   roof or an upper wall containing windows, called the clearstory wall.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aisle', 'n.', 'Improperly used also for the have; -- as in the phrases, a\n   church with three aisles, the middle aisle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aisle', 'n.', 'Also (perhaps from confusion with alley); a passage into\n   which the pews of a church open.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aisled', 'a.', 'Furnished with an aisle or aisles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aisless', 'a.', 'Without an aisle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ait', 'n.', 'An islet, or little isle, in a river or lake; an eyot.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ait', 'n.', 'Oat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aitch', 'n.', 'The letter h or H.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aitchbone', 'n.', 'The bone of the rump; also, the cut of beef surrounding\n   this bone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aitiology', 'n.', 'See Aetiology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ajar', 'adv.', 'Slightly turned or opened; as, the door was standing ajar.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ajar', 'adv.', 'In a state of discord; out of harmony; as, he is ajar with\n   the world.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ajog', 'adv.', 'On the jog.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ajutage', 'n.', 'A tube through which water is discharged; an efflux tube;\n   as, the ajutage of a fountain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ake', 'n. & v.', 'See Ache.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Akene', 'n.', 'Same as Achene.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aketon', 'n.', 'See Acton.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Akimbo', 'a.', 'With a crook or bend; with the hand on the hip and elbow\n   turned outward.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Akin', 'a.', 'Of the same kin; related by blood; -- used of persons; as,\n   the two families are near akin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Akin', 'a.', 'Allied by nature; partaking of the same properties; of the\n   same kind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Akinesia', 'n.', 'Paralysis of the motor nerves; loss of movement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Akinesic', 'a.', 'Pertaining to akinesia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aknee', 'adv.', 'On the knee.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aknow', '', 'Earlier form of Acknow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Al', 'a.', 'All.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Al-', 'A prefix.', 'All; wholly; completely; as, almighty, almost.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Al-', 'A prefix.', 'To; at; on; -- in OF. shortened to a-. See Ad-.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Al-', 'A prefix.', 'The Arabic definite article answering to the English\n   the; as, Alkoran, the Koran or the Book; alchemy, the chemistry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Al', 'conj.', 'Although; if.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alae', 'pl. ', 'of Ala');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ala', 'n.', 'A winglike organ, or part.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alabama period', '', 'A period in the American eocene, the lowest in the\n   tertiary age except the lignitic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alabaster', 'n.', 'A compact variety or sulphate of lime, or gypsum, of\n   fine texture, and usually white and translucent, but sometimes yellow,\n   red, or gray. It is carved into vases, mantel ornaments, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alabaster', 'n.', 'A hard, compact variety of carbonate of lime, somewhat\n   translucent, or of banded shades of color; stalagmite. The name is used\n   in this sense by Pliny. It is sometimes distinguished as oriental\n   alabaster.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alabaster', 'n.', 'A box or vessel for holding odoriferous ointments, etc.;\n   -- so called from the stone of which it was originally made.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alabastrian', 'a.', 'Alabastrine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alabastrine', 'a.', 'Of, pertaining to, or like, alabaster; as alabastrine\n   limbs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alabastra', 'pl. ', 'of Alabastrum');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alabastrum', 'n.', 'A flower bud.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alack', 'interj.', 'An exclamation expressive of sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alackaday', 'interj.', 'An exclamation expressing sorrow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alacrify', 'v. t.', 'To rouse to action; to inspirit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alacrious', 'a.', 'Brisk; joyously active; lively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alacriously', 'adv.', 'With alacrity; briskly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alacriousness', 'n.', 'Alacrity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alacrity', 'n.', 'A cheerful readiness, willingness, or promptitude; joyous\n   activity; briskness; sprightliness; as, the soldiers advanced with\n   alacrity to meet the enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aladinist', 'n.', 'One of a sect of freethinkers among the Mohammedans.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alalonga', 'n.', 'Alt. of Alilonghi');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alilonghi', 'n.', 'The tunny. See Albicore.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alamire', 'n.', 'The lowest note but one in Guido Aretinos scale of music.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alamodality', 'n.', 'The quality of being a la mode; conformity to the mode\n   or fashion; fashionableness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alamode', 'adv. & a.', 'According to the fashion or prevailing mode.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alamode', 'n.', 'A thin, black silk for hoods, scarfs, etc.; -- often\n   called simply mode.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alamort', 'a.', 'To the death; mortally.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alan', 'n.', 'A wolfhound.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aland', 'adv.', 'On land; to the land; ashore.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alanine', 'n.', 'A white crystalline base, C3H7NO2, derived from aldehyde\n   ammonia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alantin', 'n.', 'See Inulin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alar', 'a.', 'Pertaining to, or having, wings.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alar', 'a.', 'Axillary; in the fork or axil.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alarm', 'n.', 'A summons to arms, as on the approach of an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alarm', 'n.', 'Any sound or information intended to give notice of\n   approaching danger; a warning sound to arouse attention; a warning of\n   danger.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alarm', 'n.', 'A sudden attack; disturbance; broil.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alarm', 'n.', 'Sudden surprise with fear or terror excited by apprehension\n   of danger; in the military use, commonly, sudden apprehension of being\n   attacked by surprise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alarm', 'n.', 'A mechanical contrivance for awaking persons from sleep, or\n   rousing their attention; an alarum.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alarmed', 'imp. & p. p.', 'of Alarm');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alarming', 'p. pr. & vb. n.', 'of Alarm');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alarm', 'v. t.', 'To call to arms for defense; to give notice to (any one)\n   of approaching danger; to rouse to vigilance and action; to put on the\n   alert.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alarm', 'v. t.', 'To keep in excitement; to disturb.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alarm', 'v. t.', 'To surprise with apprehension of danger; to fill with\n   anxiety in regard to threatening evil; to excite with sudden fear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alarmable', 'a.', 'Easily alarmed or disturbed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alarmed', 'a.', 'Aroused to vigilance; excited by fear of approaching\n   danger; agitated; disturbed; as, an alarmed neighborhood; an alarmed\n   modesty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alarmedly', 'adv.', 'In an alarmed manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alarming', 'a.', 'Exciting, or calculated to excite, alarm; causing\n   apprehension of danger; as, an alarming crisis or report. --\n   A*larm\"ing*ly, adv.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alarmist', 'n.', 'One prone to sound or excite alarms, especially, needless\n   alarms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alarum', 'n.', 'See Alarm.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alary', 'a.', 'Of or pertaining to wings; also, wing-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alas', 'interj.', 'An exclamation expressive of sorrow, pity, or\n   apprehension of evil; -- in old writers, sometimes followed by day or\n   white; alas the day, like alack a day, or alas the white.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alate', 'adv.', 'Lately; of late.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alate', 'a.', 'Alt. of Alated');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alated', 'a.', 'Winged; having wings, or side appendages like wings.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alatern', 'n.', 'Alt. of Alaternus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alaternus', 'n.', 'An ornamental evergreen shrub (Rhamnus alaternus)\n   belonging to the buckthorns.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alation', 'n.', 'The state of being winged.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alaunt', 'n.', 'See Alan.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alb', 'n.', 'A vestment of white linen, reaching to the feet, an enveloping\n   the person; -- in the Roman Catholic church, worn by those in holy\n   orders when officiating at mass. It was formerly worn, at least by\n   clerics, in daily life.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albacore', 'n.', 'See Albicore.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alban', 'n.', 'A white crystalline resinous substance extracted from\n   gutta-percha by the action of alcohol or ether.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albanian', 'a.', 'Of or pertaining to Albania, a province of Turkey.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albanian', 'n.', 'A native of Albania.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albata', 'n.', 'A white metallic alloy; which is made into spoons, forks,\n   teapots, etc. British plate or German silver. See German silver, under\n   German.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albatross', 'n.', 'A web-footed bird, of the genus Diomedea, of which there\n   are several species. They are the largest of sea birds, capable of\n   long-continued flight, and are often seen at great distances from the\n   land. They are found chiefly in the southern hemisphere.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albe', 'conj.', 'Alt. of Albee');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albee', 'conj.', 'Although; albeit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albedo', 'n.', 'Whiteness. Specifically: (Astron.) The ratio which the\n   light reflected from an unpolished surface bears to the total light\n   falling upon that surface.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albeit', 'conj.', 'Even though; although; notwithstanding.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albertite', 'n.', 'A bituminous mineral resembling asphaltum, found in the\n   county of A. /bert, New Brunswick.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albertype', 'n.', 'A picture printed from a kind of gelatine plate produced\n   by means of a photographic negative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albescence', 'n.', 'The act of becoming white; whitishness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albescent', 'a.', 'Becoming white or whitish; moderately white.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albicant', 'a.', 'Growing or becoming white.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albication', 'n.', 'The process of becoming white, or developing white\n   patches, or streaks.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albicore', 'n.', 'A name applied to several large fishes of the Mackerel\n   family, esp. Orcynus alalonga. One species (Orcynus thynnus); common in\n   the Mediterranean and Atlantic, is called in New England the horse\n   mackerel; the tunny.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albification', 'n.', 'The act or process of making white.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albigenses', 'n. pl.', 'Alt. of Albigeois');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albigeois', 'n. pl.', 'A sect of reformers opposed to the church of Rome in\n   the 12th centuries.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albigensian', 'a.', 'Of or pertaining to the Albigenses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albiness', 'n.', 'A female albino.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albinism', 'n.', 'The state or condition of being an albino: abinoism;\n   leucopathy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albinistic', 'a.', 'Affected with albinism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albinos', 'pl. ', 'of Albino');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albino', 'n.', 'A person, whether negro, Indian, or white, in whom by some\n   defect of organization the substance which gives color to the skin,\n   hair, and eyes is deficient or in a morbid state. An albino has a skin\n   of a milky hue, with hair of the same color, and eyes with deep red\n   pupil and pink or blue iris. The term is also used of the lower\n   animals, as white mice, elephants, etc.; and of plants in a whitish\n   condition from the absence of chlorophyll.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albinoism', 'n.', 'The state or condition of being an albino; albinism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albinotic', 'a.', 'Affected with albinism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albion', 'n.', 'An ancient name of England, still retained in poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albite', 'n.', 'A mineral of the feldspar family, triclinic in\n   crystallization, and in composition a silicate of alumina and soda. It\n   is a common constituent of granite and of various igneous rocks. See\n   Feldspar.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albolith', 'n.', 'A kind of plastic cement, or artificial stone, consisting\n   chiefly of magnesia and silica; -- called also albolite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alborak', 'n.', 'The imaginary milk-white animal on which Mohammed was said\n   to have been carried up to heaven; a white mule.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albugineous', 'a.', 'Of the nature of, or resembling, the white of the eye,\n   or of an egg; albuminous; -- a term applied to textures, humors, etc.,\n   which are perfectly white.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albugines', 'pl. ', 'of Albugo');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albugo', 'n.', 'Same as Leucoma.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Album', 'n.', 'A white tablet on which anything was inscribed, as a list of\n   names, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Album', 'n.', 'A register for visitors names; a visitors book.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Album', 'n.', 'A blank book, in which to insert autographs sketches,\n   memorial writing of friends, photographs, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albumen', 'n.', 'The white of an egg.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albumen', 'n.', 'Nourishing matter stored up within the integuments of the\n   seed in many plants, but not incorporated in the embryo. It is the\n   floury part in corn, wheat, and like grains, the oily part in poppy\n   seeds, the fleshy part in the cocoanut, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albumen', 'n.', 'Same as Albumin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albumenized', 'imp. & p. p.', 'of Albumenize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albumenizing', 'p. pr. & vb. n.', 'of Albumenize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albumenize', 'v. t.', 'To cover or saturate with albumen; to coat or treat\n   with an albuminous solution; as, to albumenize paper.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Album Graecum', '', 'Dung of dogs or hyenas, which becomes white by\n   exposure to air. It is used in dressing leather, and was formerly used\n   in medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albumin', 'n.', 'A thick, viscous nitrogenous substance, which is the chief\n   and characteristic constituent of white of eggs and of the serum of\n   blood, and is found in other animal substances, both fluid and solid,\n   also in many plants. It is soluble in water and is coagulated by heat\n   and by certain chemical reagents.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albuminate', 'n.', 'A substance produced by the action of an alkali upon\n   albumin, and resembling casein in its properties; also, a compound\n   formed by the union of albumin with another substance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albuminiferous', 'a.', 'Supplying albumen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albuminimeter', 'n.', 'An instrument for ascertaining the quantity of\n   albumen in a liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albuminin', 'n.', 'The substance of the cells which inclose the white of\n   birds eggs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albuminiparous', 'a.', 'Producing albumin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albuminoid', 'a.', 'Resembling albumin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albuminoid', 'n.', 'One of a class of organic principles (called also\n   proteids) which form the main part of organized tissues.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albuminoidal', 'a.', 'Of the nature of an albuminoid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albuminose', 'n.', 'A diffusible substance formed from albumin by the\n   action of natural or artificial gastric juice. See Peptone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albuminous', 'a.', 'Alt. of Albuminose');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albuminose', 'a.', 'Pertaining to, or containing, albumen; having the\n   properties of, or resembling, albumen or albumin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albuminuria', 'n.', 'A morbid condition in which albumin is present in the\n   urine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albumose', 'n.', 'A compound or class of compounds formed from albumin by\n   dilute acids or by an acid solution of pepsin. Used also in\n   combination, as antialbumose, hemialbumose.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alburn', 'n.', 'The bleak, a small European fish having scales of a\n   peculiarly silvery color which are used in making artificial pearls.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alburnous', 'a.', 'Of or pertaining to alburnum; of the alburnum; as,\n   alburnous substances.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alburnum', 'n.', 'The white and softer part of wood, between the inner bark\n   and the hard wood or duramen; sapwood.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Albyn', 'n.', 'Scotland; esp. the Highlands of Scotland.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcade', 'n.', 'Same as Alcaid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcahest', 'n.', 'Same as Alkahest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcaic', 'a.', 'Pertaining to Alcaeus, a lyric poet of Mitylene, about 6000\n   b. c.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcaic', 'n.', 'A kind of verse, so called from Alcaeus. One variety\n   consists of five feet, a spondee or iambic, an iambic, a long syllable,\n   and two dactyls.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcaid', 'n.', 'Alt. of Alcayde');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcayde', 'n.', 'A commander of a castle or fortress among the Spaniards,\n   Portuguese, and Moors.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcayde', 'n.', 'The warden, or keeper of a jail.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcalde', 'n.', 'A magistrate or judge in Spain and in Spanish America,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcalimeter', 'n.', 'See Alkalimeter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcanna', 'n.', 'An oriental shrub (Lawsonia inermis) from which henna is\n   obtained.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcarrazas', 'pl. ', 'of Alcarraza');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcarraza', 'n.', 'A vessel of porous earthenware, used for cooling liquids\n   by evaporation from the exterior surface.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcayde', 'n.', 'Same as Alcaid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcazar', 'n.', 'A fortress; also, a royal palace.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcedo', 'n.', 'A genus of perching birds, including the European\n   kingfisher (Alcedo ispida). See Halcyon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alchemic', 'a.', 'Alt. of Alchemical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alchemical', 'a.', 'Of or relating to alchemy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alchemically', 'adv.', 'In the manner of alchemy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alchemist', 'n.', 'One who practices alchemy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alchemistic', 'a.', 'Alt. of Alchemistical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alchemistical', 'a.', 'Relating to or practicing alchemy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alchemistry', 'n.', 'Alchemy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alchemize', 'v. t.', 'To change by alchemy; to transmute.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alchemy', 'n.', 'An imaginary art which aimed to transmute the baser metals\n   into gold, to find the panacea, or universal remedy for diseases, etc.\n   It led the way to modern chemistry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alchemy', 'n.', 'A mixed metal composed mainly of brass, formerly used for\n   various utensils; hence, a trumpet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alchemy', 'n.', 'Miraculous power of transmuting something common into\n   something precious.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alchymic', 'n.', 'Alt. of Alchymy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alchymist', 'n.', 'Alt. of Alchymy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alchymistic', 'n.', 'Alt. of Alchymy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alchymy', 'n.', 'See Alchemic, Alchemist, Alchemistic, Alchemy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alco', 'n.', 'A small South American dog, domesticated by the aborigines.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoate', 'n.', 'Alt. of Alcohate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcohate', 'n.', 'Shortened forms of Alcoholate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcohol', 'n.', 'An impalpable powder.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcohol', 'n.', 'The fluid essence or pure spirit obtained by distillation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcohol', 'n.', 'Pure spirit of wine; pure or highly rectified spirit\n   (called also ethyl alcohol); the spirituous or intoxicating element of\n   fermented or distilled liquors, or more loosely a liquid containing it\n   in considerable quantity. It is extracted by simple distillation from\n   various vegetable juices and infusions of a saccharine nature, which\n   have undergone vinous fermentation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcohol', 'n.', 'A class of compounds analogous to vinic alcohol in\n   constitution. Chemically speaking, they are hydroxides of certain\n   organic radicals; as, the radical ethyl forms common or ethyl alcohol\n   (C2H5.OH); methyl forms methyl alcohol (CH3.OH) or wood spirit; amyl\n   forms amyl alcohol (C5H11.OH) or fusel oil, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoholate', 'n.', 'A crystallizable compound of a salt with alcohol, in\n   which the latter plays a part analogous to that of water of\n   crystallization.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoholature', 'n.', 'An alcoholic tincture prepared with fresh plants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoholic', 'a.', 'Of or pertaining to alcohol, or partaking of its\n   qualities; derived from, or caused by, alcohol; containing alcohol; as,\n   alcoholic mixtures; alcoholic gastritis; alcoholic odor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoholic', 'n.', 'A person given to the use of alcoholic liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoholic', 'n.', 'Alcoholic liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoholism', 'n.', 'A diseased condition of the system, brought about by\n   the continued use of alcoholic liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoholization', 'n.', 'The act of reducing a substance to a fine or\n   impalpable powder.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoholization', 'n.', 'The act rectifying spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoholization', 'n.', 'Saturation with alcohol; putting the animal system\n   under the influence of alcoholic liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoholized', 'imp. & p. p.', 'of Alcoholize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoholizing', 'p. pr. & vb. n.', 'of Alcoholize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoholize', 'v. t.', 'To reduce to a fine powder.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoholize', 'v. t.', 'To convert into alcohol; to rectify; also, to\n   saturate with alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoholometer', 'n.', 'Alt. of Alcoholmeter');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoholmeter', 'n.', 'An instrument for determining the strength of\n   spirits, with a scale graduated so as to indicate the percentage of\n   pure alcohol, either by weight or volume. It is usually a form of\n   hydrometer with a special scale.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoholometric', 'a.', 'Alt. of Alcoholmetrical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoholometrical', 'a.', 'Alt. of Alcoholmetrical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoholmetrical', 'a.', 'Relating to the alcoholometer or alcoholometry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoholometry', 'n.', 'The process or method of ascertaining the proportion\n   of pure alcohol which spirituous liquors contain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcohometer', 'a.', 'Alt. of Alcohometric');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcohometric', 'a.', 'Same as Alcoholometer, Alcoholometric.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoometry', 'n.', 'See Alcoholometry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoran', 'n.', 'The Mohammedan Scriptures; the Koran (now the usual form).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoranic', 'a.', 'Of or pertaining to the Koran.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcoranist', 'n.', 'One who adheres to the letter of the Koran, rejecting\n   all traditions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcove', 'n.', 'A recessed portion of a room, or a small room opening into\n   a larger one; especially, a recess to contain a bed; a lateral recess\n   in a library.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcove', 'n.', 'A small ornamental building with seats, or an arched seat,\n   in a pleasure ground; a garden bower.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcove', 'n.', 'Any natural recess analogous to an alcove or recess in an\n   apartment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcyon', 'n.', 'See Halcyon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcyonacea', 'n. pl.', 'A group of soft-bodied Alcyonaria, of which\n   Alcyonium is the type. See Illust. under Alcyonaria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcyonaria', 'n. pl.', 'One of the orders of Anthozoa. It includes the\n   Alcyonacea, Pennatulacea, and Gorgonacea.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcyones', 'n. pl.', 'The kingfishers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcyonic', 'a.', 'Of or pertaining to the Alcyonaria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcyonium', 'n.', 'A genus of fleshy Alcyonaria, its polyps somewhat\n   resembling flowers with eight fringed rays. The term was also formerly\n   used for certain species of sponges.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcyonoid', 'a.', 'Like or pertaining to the Alcyonaria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alcyonoid', 'n.', 'A zoophyte of the order Alcyonaria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alday', 'adv.', 'Continually.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aldebaran', 'n.', 'A red star of the first magnitude, situated in the eye\n   of Taurus; the Bulls Eye. It is the bright star in the group called\n   the Hyades.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aldehyde', 'n.', 'A colorless, mobile, and very volatile liquid obtained\n   from alcohol by certain processes of oxidation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aldehydic', 'a.', 'Of or pertaining to aldehyde; as, aldehydic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alder', 'n.', 'A tree, usually growing in moist land, and belonging to the\n   genus Alnus. The wood is used by turners, etc.; the bark by dyers and\n   tanners. In the U. S. the species of alder are usually shrubs or small\n   trees.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alder', 'a.', 'Alt. of Aller');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aller', 'a.', 'Of all; -- used in composition; as, alderbest, best of all,\n   alderwisest, wisest of all.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alder-liefest', 'a.', 'Most beloved.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aldermen', 'pl. ', 'of Alderman');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alderman', 'n.', 'A senior or superior; a person of rank or dignity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alderman', 'n.', 'One of a board or body of municipal officers next in\n   order to the mayor and having a legislative function. They may, in some\n   cases, individually exercise some magisterial and administrative\n   functions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aldermancy', 'n.', 'The office of an alderman.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aldermanic', 'a.', 'Relating to, becoming to, or like, an alderman;\n   characteristic of an alderman.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aldermanity', 'n.', 'Aldermen collectively; the body of aldermen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aldermanity', 'n.', 'The state of being an alderman.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aldermanlike', 'a.', 'Like or suited to an alderman.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aldermanly', 'a.', 'Pertaining to, or like, an alderman.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aldermanly', 'a.', 'Pertaining to, or like, an alderman.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aldermanry', 'n.', 'The district or ward of an alderman.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aldermanry', 'n.', 'The office or rank of an alderman.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aldermanship', 'n.', 'The condition, position, or office of an alderman.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aldern', 'a.', 'Made of alder.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alderney', 'n.', 'One of a breed of cattle raised in Alderney, one of the\n   Channel Islands. Alderneys are of a dun or tawny color and are often\n   called Jersey cattle. See Jersey, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aldine', 'a.', 'An epithet applied to editions (chiefly of the classics)\n   which proceeded from the press of Aldus Manitius, and his family, of\n   Venice, for the most part in the 16th century and known by the sign of\n   the anchor and the dolphin. The term has also been applied to certain\n   elegant editions of English works.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ale', 'n.', 'An intoxicating liquor made from an infusion of malt by\n   fermentation and the addition of a bitter, usually hops.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ale', 'n.', 'A festival in English country places, so called from the\n   liquor drunk.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aleak', 'adv. & a.', 'In a leaking condition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aleatory', 'a.', 'Depending on some uncertain contingency; as, an aleatory\n   contract.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alebench', 'n.', 'A bench in or before an alehouse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aleberry', 'n.', 'A beverage, formerly made by boiling ale with spice,\n   sugar, and sops of bread.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alecithal', 'a.', 'Applied to those ova which segment uniformly, and which\n   have little or no food yelk embedded in their protoplasm.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aleconner', 'n.', 'Orig., an officer appointed to look to the goodness of\n   ale and beer; also, one of the officers chosen by the liverymen of\n   London to inspect the measures used in public houses. But the office is\n   a sinecure. [Also called aletaster.]');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alecost', 'n.', 'The plant costmary, which was formerly much used for\n   flavoring ale.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alectorides', 'n. pl.', 'A group of birds including the common fowl and the\n   pheasants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alectoromachy', 'n.', 'Cockfighting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alectoromancy', 'n.', 'See Alectryomancy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alectryomachy', 'n.', 'Cockfighting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alectryomancy', 'n.', 'Divination by means of a cock and grains of corn\n   placed on the letters of the alphabet, the letters being put together\n   in the order in which the grains were eaten.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alee', 'adv.', 'On or toward the lee, or the side away from the wind; the\n   opposite of aweather. The helm of a ship is alee when pressed close to\n   the lee side.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alegar', 'n.', 'Sour ale; vinegar made of ale.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aleger', 'a.', 'Gay; cheerful; sprightly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alegge', 'v. t.', 'To allay or alleviate; to lighten.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alehoof', 'n.', 'Ground ivy (Nepeta Glechoma).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alehouse', 'n.', 'A house where ale is retailed; hence, a tippling house.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ale-knight', 'n.', 'A pot companion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alemannic', 'a.', 'Belonging to the Alemanni, a confederacy of warlike\n   German tribes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alemannic', 'n.', 'The language of the Alemanni.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alembic', 'n.', 'An apparatus formerly used in distillation, usually made\n   of glass or metal. It has mostly given place to the retort and worm\n   still.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alembroth', 'n.', 'The salt of wisdom of the alchemists, a double salt\n   composed of the chlorides of ammonium and mercury. It was formerly used\n   as a stimulant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alen/on lace', '', 'See under Lace.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alength', 'adv.', 'At full length; lengthwise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alepidote', 'a.', 'Not having scales.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alepidote', 'n.', 'A fish without scales.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alepole', 'n.', 'A pole set up as the sign of an alehouse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alert', 'a.', 'Watchful; vigilant; active in vigilance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alert', 'a.', 'Brisk; nimble; moving with celerity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alert', 'n.', 'An alarm from a real or threatened attack; a sudden attack;\n   also, a bugle sound to give warning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alertly', 'adv.', 'In an alert manner; nimbly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alertness', 'n.', 'The quality of being alert or on the alert; briskness;\n   nimbleness; activity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ale silver', '', 'A duty payable to the lord mayor of London by the sellers\n   of ale within the city.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alestake', 'n.', 'A stake or pole projecting from, or set up before, an\n   alehouse, as a sign; an alepole. At the end was commonly suspended a\n   garland, a bunch of leaves, or a \"bush.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aletaster', 'n.', 'See Aleconner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alethiology', 'n.', 'The science which treats of the nature of truth and\n   evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alethoscope', 'n.', 'An instrument for viewing pictures by means of a lens,\n   so as to present them in their natural proportions and relations.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aleuromancy', 'n.', 'Divination by means of flour.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aleurometer', 'n.', 'An instrument for determining the expansive\n   properties, or quality, of gluten in flour.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aleurone', 'n.', 'An albuminoid substance which occurs in minute grains\n   (\"protein granules\") in maturing seeds and tubers; -- supposed to be a\n   modification of protoplasm.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aleuronic', 'a.', 'Having the nature of aleurone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aleutian', 'a.', 'Alt. of Aleutic');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aleutic', 'a.', 'Of or pertaining to a chain of islands between Alaska and\n   Kamtchatka; also, designating these islands.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alevin', 'n.', 'Young fish; fry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alew', 'n.', 'Halloo.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alewives', 'pl. ', 'of Alewife');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alewife', 'n.', 'A woman who keeps an alehouse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alewives', 'pl. ', 'of Alewife');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alewife', 'n.', 'A North American fish (Clupea vernalis) of the Herring\n   family. It is called also ellwife, ellwhop, branch herring. The name is\n   locally applied to other related species.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alexanders', 'n.', 'Alt. of Alisanders');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alisanders', 'n.', 'A name given to two species of the genus Smyrnium,\n   formerly cultivated and used as celery now is; -- called also horse\n   parsely.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alexandrian', 'a.', 'Of or pertaining to Alexandria in Egypt; as, the\n   Alexandrian library.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alexandrian', 'a.', 'Applied to a kind of heroic verse. See Alexandrine, n.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alexandrine', 'a.', 'Belonging to Alexandria; Alexandrian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alexandrine', 'n.', 'A kind of verse consisting in English of twelve\n   syllables.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alexipharmac', 'a. & n.', 'Alt. of Alexipharmacal');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alexipharmacal', 'a. & n.', 'Alexipharmic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alexipharmic', 'a.', 'Alt. of Alexipharmical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alexipharmical', 'a.', 'Expelling or counteracting poison; antidotal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alexipharmic', 'n.', 'An antidote against poison or infection; a\n   counterpoison.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alexipyretic', 'a.', 'Serving to drive off fever; antifebrile.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alexipyretic', 'n.', 'A febrifuge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alexiteric', 'a.', 'Alt. of Alexiterical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alexiterical', 'a.', 'Resisting poison; obviating the effects of venom;\n   alexipharmic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alexiteric', 'n.', 'A preservative against contagious and infectious\n   diseases, and the effects of poison in general.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alfa', 'n.', 'Alt. of Alfa grass');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alfa grass', 'n.', 'A plant (Macrochloa tenacissima) of North Africa; also,\n   its fiber, used in paper making.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alfalfa', 'n.', 'The lucern (Medicago sativa); -- so called in California,\n   Texas, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alfenide', 'n.', 'An alloy of nickel and silver electroplated with silver.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alferes', 'n.', 'An ensign; a standard bearer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alfet', 'n.', 'A caldron of boiling water into which an accused person\n   plunged his forearm as a test of innocence or guilt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alfilaria', 'n.', 'The pin grass (Erodium cicutarium); a weed in\n   California.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alfione', 'n.', 'An edible marine fish of California (Rhacochilus toxotes).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alfresco', 'adv. & a.', 'In the open-air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algae', 'pl. ', 'of Alga');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alga', 'n.', 'A kind of seaweed; pl. the class of cellular cryptogamic\n   plants which includes the black, red, and green seaweeds, as kelp,\n   dulse, sea lettuce, also marine and fresh water confervae, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algal', 'a.', 'Pertaining to, or like, algae.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algaroba', 'n.', 'The Carob, a leguminous tree of the Mediterranean region;\n   also, its edible beans or pods, called St. Johns bread.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algaroba', 'n.', 'The Honey mesquite (Prosopis juliflora); a small tree\n   found from California to Buenos Ayres; also, its sweet, pulpy pods. A\n   valuable gum, resembling gum arabic, is collected from the tree in\n   Texas and Mexico.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algarot', 'n.', 'Alt. of Algaroth');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algaroth', 'n.', 'A term used for the Powder of Algaroth, a white powder\n   which is a compound of trichloride and trioxide of antimony. It was\n   formerly used in medicine as an emetic, purgative, and diaphoretic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algarovilla', 'n.', 'The agglutinated seeds and husks of the legumes of a\n   South American tree (Inga Marthae). It is valuable for tanning leather,\n   and as a dye.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algate', 'adv.', 'Alt. of Algates');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algates', 'adv.', 'Always; wholly; everywhere.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algates', 'adv.', 'By any or means; at all events.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algates', 'adv.', 'Notwithstanding; yet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algazel', 'n.', 'The true gazelle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algebra', 'n.', 'That branch of mathematics which treats of the relations\n   and properties of quantity by means of letters and other symbols. It is\n   applicable to those relations that are true of every kind of magnitude.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algebra', 'n.', 'A treatise on this science.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algebraic', 'a.', 'Alt. of Algebraical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algebraical', 'a.', 'Of or pertaining to algebra; containing an operation\n   of algebra, or deduced from such operation; as, algebraic characters;\n   algebraical writings.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algebraically', 'adv.', 'By algebraic process.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algebraist', 'n.', 'One versed in algebra.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algebraize', 'v. t.', 'To perform by algebra; to reduce to algebraic form.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algerian', 'a.', 'Of or pertaining to Algeria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algerian', 'n.', 'A native of Algeria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algerine', 'a.', 'Of or pertaining to Algiers or Algeria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algerine', 'n.', 'A native or one of the people of Algiers or Algeria.\n   Also, a pirate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algid', 'a.', 'Cold; chilly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algidity', 'n.', 'Chilliness; coldness');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algidity', 'n.', 'coldness and collapse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algidness', 'n.', 'Algidity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algific', 'a.', 'Producing cold.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algoid', 'a.', 'Of the nature of, or resembling, an alga.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algol', 'n.', 'A fixed star, in Medusas head, in the constellation\n   Perseus, remarkable for its periodic variation in brightness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algological', 'a.', 'Of or pertaining to algology; as, algological\n   specimens.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algologist', 'n.', 'One learned about algae; a student of algology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algology', 'n.', 'The study or science of algae or seaweeds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algonquin', 'n.', 'Alt. of Algonkin');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algonkin', 'n.', 'One of a widely spread family of Indians, including many\n   distinct tribes, which formerly occupied most of the northern and\n   eastern part of North America. The name was originally applied to a\n   group of Indian tribes north of the River St. Lawrence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algor', 'n.', 'Cold; chilliness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algorism', 'n.', 'Alt. of Algorithm');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algorithm', 'n.', 'The art of calculating by nine figures and zero.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algorithm', 'n.', 'The art of calculating with any species of notation; as,\n   the algorithms of fractions, proportions, surds, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algous', 'a.', 'Of or pertaining to the algae, or seaweeds; abounding with,\n   or like, seaweed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alguazil', 'n.', 'An inferior officer of justice in Spain; a warrant\n   officer; a constable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algum', 'n.', 'Same as Almug (and etymologically preferable).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alhambra', 'n.', 'The palace of the Moorish kings at Granada.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alhambraic', 'a.', 'Alt. of Alhambresque');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alhambresque', 'a.', 'Made or decorated after the fanciful style of the\n   ornamentation in the Alhambra, which affords an unusually fine\n   exhibition of Saracenic or Arabesque architecture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alhenna', 'n.', 'See Henna.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alias', 'adv.', 'Otherwise; otherwise called; -- a term used in legal\n   proceedings to connect the different names of any one who has gone by\n   two or more, and whose true name is for any cause doubtful; as, Smith,\n   alias Simpson.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alias', 'adv.', 'At another time.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aliases', 'pl. ', 'of Alias');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alias', 'n.', 'A second or further writ which is issued after a first writ\n   has expired without effect.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alias', 'n.', 'Another name; an assumed name.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alibi', 'n.', 'The plea or mode of defense under which a person on trial\n   for a crime proves or attempts to prove that he was in another place\n   when the alleged act was committed; as, to set up an alibi; to prove an\n   alibi.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alibility', 'n.', 'Quality of being alible.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alible', 'a.', 'Nutritive; nourishing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alicant', 'n.', 'A kind of wine, formerly much esteemed; -- said to have\n   been made near Alicant, in Spain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alidade', 'n.', 'The portion of a graduated instrument, as a quadrant or\n   astrolabe, carrying the sights or telescope, and showing the degrees\n   cut off on the arc of the instrument');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alien', 'a.', 'Not belonging to the same country, land, or government, or\n   to the citizens or subjects thereof; foreign; as, alien subjects,\n   enemies, property, shores.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alien', 'a.', 'Wholly different in nature; foreign; adverse; inconsistent\n   (with); incongruous; -- followed by from or sometimes by to; as,\n   principles alien from our religion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alien', 'n.', 'A foreigner; one owing allegiance, or belonging, to another\n   country; a foreign-born resident of a country in which he does not\n   possess the privileges of a citizen. Hence, a stranger. See Alienage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alien', 'n.', 'One excluded from certain privileges; one alienated or\n   estranged; as, aliens from Gods mercies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alien', 'v. t.', 'To alienate; to estrange; to transfer, as property or\n   ownership.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alienability', 'n.', 'Capability of being alienated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alienable', 'a.', 'Capable of being alienated, sold, or transferred to\n   another; as, land is alienable according to the laws of the state.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alienage', 'n.', 'The state or legal condition of being an alien.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alienage', 'n.', 'The state of being alienated or transferred to another.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alienate', 'a.', 'Estranged; withdrawn in affection; foreign; -- with from.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alienated', 'imp. & p. p.', 'of Alienate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alienating', 'p. pr. & vb. n.', 'of Alienate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alienate', 'v. t.', 'To convey or transfer to another, as title, property,\n   or right; to part voluntarily with ownership of.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alienate', 'v. t.', 'To withdraw, as the affections; to make indifferent of\n   averse, where love or friendship before subsisted; to estrange; to\n   wean; -- with from.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alienate', 'n.', 'A stranger; an alien.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alienation', 'n.', 'The act of alienating, or the state of being alienated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alienation', 'n.', 'A transfer of title, or a legal conveyance of property\n   to another.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alienation', 'n.', 'A withdrawing or estrangement, as of the affections.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alienation', 'n.', 'Mental alienation; derangement of the mental faculties;\n   insanity; as, alienation of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alienator', 'n.', 'One who alienates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aliene', 'v. t.', 'To alien or alienate; to transfer, as title or property;\n   as, to aliene an estate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alienee', 'n.', 'One to whom the title of property is transferred; --\n   opposed to alienor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alienism', 'n.', 'The status or legal condition of an alien; alienage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alienism', 'n.', 'The study or treatment of diseases of the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alienist', 'n.', 'One who treats diseases of the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alienor', 'n.', 'One who alienates or transfers property to another.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aliethmoid', 'a.', 'Alt. of Aliethmoidal');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aliethmoidal', 'a.', 'Pertaining to expansions of the ethmoid bone or\n   cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alife', 'adv.', 'On my life; dearly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aliferous', 'a.', 'Having wings, winged; aligerous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aliform', 'a.', 'Wing-shaped; winglike.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aligerous', 'a.', 'Having wings; winged.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alighted', 'imp. & p. p.', 'of Alight');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alit', '', 'of Alight');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alighting', 'p. pr. & vb. n.', 'of Alight');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alight', 'v. i.', 'To spring down, get down, or descend, as from on\n   horseback or from a carriage; to dismount.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alight', 'v. i.', 'To descend and settle, lodge, rest, or stop; as, a\n   flying bird alights on a tree; snow alights on a roof.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alight', 'v. i.', 'To come or chance (upon).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alight', 'a.', 'Lighted; lighted up; in a flame.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Align', 'v. t.', 'To adjust or form to a line; to range or form in line; to\n   bring into line; to aline.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Align', 'v. t.', 'To form in line; to fall into line.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alignment', 'n.', 'The act of adjusting to a line; arrangement in a line or\n   lines; the state of being so adjusted; a formation in a straight line;\n   also, the line of adjustment; esp., an imaginary line to regulate the\n   formation of troops or of a squadron.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alignment', 'n.', 'The ground-plan of a railway or other road, in\n   distinction from the grades or profile.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alike', 'a.', 'Having resemblance or similitude; similar; without\n   difference.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alike', 'adv.', 'In the same manner, form, or degree; in common; equally;\n   as, we are all alike concerned in religion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alike-minded', 'a.', 'Like-minded.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aliment', 'n.', 'That which nourishes; food; nutriment; anything which\n   feeds or adds to a substance in natural growth. Hence: The necessaries\n   of life generally: sustenance; means of support.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aliment', 'n.', 'An allowance for maintenance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aliment', 'v. t.', 'To nourish; to support.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aliment', 'v. t.', 'To provide for the maintenance of.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alimental', 'a.', 'Supplying food; having the quality of nourishing;\n   furnishing the materials for natural growth; as, alimental sap.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alimentally', 'adv.', 'So as to serve for nourishment or food; nourishing\n   quality.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alimentariness', 'n.', 'The quality of being alimentary; nourishing\n   quality.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alimentary', 'a.', 'Pertaining to aliment or food, or to the function of\n   nutrition; nutritious; alimental; as, alimentary substances.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alimentation', 'n.', 'The act or process of affording nutriment; the\n   function of the alimentary canal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alimentation', 'n.', 'State or mode of being nourished.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alimentiveness', 'n.', 'The instinct or faculty of appetite for food.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alimonious', 'a.', 'Affording food; nourishing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alimony', 'n.', 'Maintenance; means of living.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alimony', 'n.', 'An allowance made to a wife out of her husbands estate or\n   income for her support, upon her divorce or legal separation from him,\n   or during a suit for the same.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alinasal', 'a.', 'Pertaining to expansions of the nasal bone or cartilage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aline', 'v. t.', 'To range or place in a line; to bring into line; to\n   align.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alineation', 'n.', 'See Allineation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alinement', 'n.', 'Same as Alignment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aliner', 'n.', 'One who adjusts things to a line or lines or brings them\n   into line.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alioth', 'n.', 'A star in the tail of the Great Bear, the one next the bowl\n   in the Dipper.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aliped', 'a.', 'Wing-footed, as the bat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aliped', 'n.', 'An animal whose toes are connected by a membrane, serving\n   for a wing, as the bat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aliquant', 'a.', 'An aliquant part of a number or quantity is one which\n   does not divide it without leaving a remainder; thus, 5 is an aliquant\n   part of 16. Opposed to aliquot.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aliquot', 'a.', 'An aliquot part of a number or quantity is one which will\n   divide it without a remainder; thus, 5 is an aliquot part of 15.\n   Opposed to aliquant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aliseptal', 'a.', 'Relating to expansions of the nasal septum.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alish', 'a.', 'Like ale; as, an alish taste.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alisphenoid', 'a.', 'Alt. of Alisphenoidal');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alisphenoidal', 'a.', 'Pertaining to or forming the wing of the sphenoid;\n   relating to a bone in the base of the skull, which in the adult is\n   often consolidated with the sphenoid; as, alisphenoid bone; alisphenoid\n   canal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alisphenoid', 'n.', 'The alisphenoid bone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alitrunk', 'n.', 'The segment of the body of an insect to which the wings\n   are attached; the thorax.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aliturgical', 'a.', 'Applied to those days when the holy sacrifice is not\n   offered.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aliunde', 'adv. & a.', 'From another source; from elsewhere; as, a case\n   proved aliunde; evidence aliunde.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alive', 'a.', 'Having life, in opposition to dead; living; being in a state\n   in which the organs perform their functions; as, an animal or a plant\n   which is alive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alive', 'a.', 'In a state of action; in force or operation; unextinguished;\n   unexpired; existent; as, to keep the fire alive; to keep the affections\n   alive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alive', 'a.', 'Exhibiting the activity and motion of many living beings;\n   swarming; thronged.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alive', 'a.', 'Sprightly; lively; brisk.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alive', 'a.', 'Having susceptibility; easily impressed; having lively\n   feelings, as opposed to apathy; sensitive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alive', 'a.', 'Of all living (by way of emphasis).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alizari', 'n.', 'The madder of the Levant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alizarin', 'n.', 'A coloring principle, C14H6O2(OH)2, found in madder, and\n   now produced artificially from anthracene. It produces the Turkish\n   reds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkahest', 'n.', 'The fabled \"universal solvent\" of the alchemists; a\n   menstruum capable of dissolving all bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalamide', 'n.', 'One of a series of compounds that may be regarded as\n   ammonia in which a part of the hydrogen has been replaced by basic, and\n   another part by acid, atoms or radicals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalescence', 'n.', 'Alt. of Alkalescency');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalescency', 'n.', 'A tendency to become alkaline; or the state of a\n   substance in which alkaline properties begin to be developed, or to\n   predominant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalescent', 'a.', 'Tending to the properties of an alkali; slightly\n   alkaline.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalis', 'pl. ', 'of Alkali');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalies', 'pl. ', 'of Alkali');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkali', 'n.', 'Soda ash; caustic soda, caustic potash, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkali', 'n.', 'One of a class of caustic bases, such as soda, potash,\n   ammonia, and lithia, whose distinguishing peculiarities are solubility\n   in alcohol and water, uniting with oils and fats to form soap,\n   neutralizing and forming salts with acids, turning to brown several\n   vegetable yellows, and changing reddened litmus to blue.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalifiable', 'a.', 'Capable of being alkalified, or converted into an\n   alkali.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalified', 'imp. & p. p.', 'of Alkalify');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalifying', 'p. pr. & vb. n.', 'of Alkalify');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalify', 'v. t.', 'To convert into an alkali; to give alkaline properties\n   to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalify', 'v. i.', 'To become changed into an alkali.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalimeter', 'n.', 'An instrument to ascertain the strength of alkalies,\n   or the quantity of alkali in a mixture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalimetric', 'a.', 'Alt. of Alkalimetrical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalimetrical', 'a.', 'Of or pertaining to alkalimetry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalimetry', 'n.', 'The art or process of ascertaining the strength of\n   alkalies, or the quantity present in alkaline mixtures.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkaline', 'a.', 'Of or pertaining to an alkali or to alkalies; having the\n   properties of an alkali.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalinity', 'n.', 'The quality which constitutes an alkali; alkaline\n   property.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalious', 'a.', 'Alkaline.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalizate', 'a.', 'Alkaline.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalizate', 'v. t.', 'To alkalizate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalization', 'n.', 'The act rendering alkaline by impregnating with an\n   alkali; a conferring of alkaline qualities.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalized', 'imp. & p. p.', 'of Alkalize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalizing', 'p. pr. & vb. n.', 'of Alkalize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkalize', 'v. t.', 'To render alkaline; to communicate the properties of\n   an alkali to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkaloid', 'a.', 'Alt. of Alkaloidal');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkaloidal', 'a.', 'Pertaining to, resembling, or containing, alkali.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkaloid', 'n.', 'An organic base, especially one of a class of substances\n   occurring ready formed in the tissues of plants and the bodies of\n   animals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkanet', 'n.', 'A dyeing matter extracted from the roots of Alkanna\n   tinctoria, which gives a fine deep red color.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkanet', 'n.', 'A boraginaceous herb (Alkanna tinctoria) yielding the dye;\n   orchanet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkanet', 'n.', 'The similar plant Anchusa officinalis; bugloss; also, the\n   American puccoon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkargen', 'n.', 'Same as Cacodylic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkarsin', 'n.', 'A spontaneously inflammable liquid, having a repulsive\n   odor, and consisting of cacodyl and its oxidation products; -- called\n   also Cadels fuming liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkazar', '', 'See Alcazar.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkekengi', 'n.', 'An herbaceous plant of the nightshade family (Physalis\n   alkekengi) and its fruit, which is a well flavored berry, the size of a\n   cherry, loosely inclosed in a enlarged leafy calyx; -- also called\n   winter cherry, ground cherry, and strawberry tomato.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkermes', 'n.', 'A compound cordial, in the form of a confection, deriving\n   its name from the kermes insect, its principal ingredient.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkoran', 'n.', 'The Mohammedan Scriptures. Same as Alcoran and Koran.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkoranic', 'a.', 'Same as Alcoranic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alkoranist', 'n.', 'Same as Alcoranist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'All', 'a.', 'The whole quantity, extent, duration, amount, quality, or\n   degree of; the whole; the whole number of; any whatever; every; as, all\n   the wheat; all the land; all the year; all the strength; all happiness;\n   all abundance; loss of all power; beyond all doubt; you will see us all\n   (or all of us).');
  INSERT INTO words (word, word_type, description) VALUES  (  'All', 'a.', 'Any.');
  INSERT INTO words (word, word_type, description) VALUES  (  'All', 'a.', 'Only; alone; nothing but.');
  INSERT INTO words (word, word_type, description) VALUES  (  'All', 'adv.', 'Wholly; completely; altogether; entirely; quite; very; as,\n   all bedewed; my friend is all for amusement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'All', 'adv.', 'Even; just. (Often a mere intensive adjunct.)');
  INSERT INTO words (word, word_type, description) VALUES  (  'All', 'n.', 'The whole number, quantity, or amount; the entire thing;\n   everything included or concerned; the aggregate; the whole; totality;\n   everything or every person; as, our all is at stake.');
  INSERT INTO words (word, word_type, description) VALUES  (  'All', 'conj.', 'Although; albeit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alla breve', '', 'With one breve, or four minims, to measure, and sung\n   faster like four crotchets; in quick common time; -- indicated in the\n   time signature by /.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allah', 'n.', 'The name of the Supreme Being, in use among the Arabs and\n   the Mohammedans generally.');
  INSERT INTO words (word, word_type, description) VALUES  (  'All-a-mort', 'a.', 'See Alamort.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allanite', 'n.', 'A silicate containing a large amount of cerium. It is\n   usually black in color, opaque, and is related to epidote in form and\n   composition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allantoic', 'a.', 'Pertaining to, or contained in, the allantois.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allantoid', 'a.', 'Alt. of Allantoidal');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allantoidal', 'a.', 'Of or pertaining to the allantois.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allantoidea', 'n. pl.', 'The division of Vertebrata in which the embryo\n   develops an allantois. It includes reptiles, birds, and mammals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allantoin', 'n.', 'A crystalline, transparent, colorless substance found in\n   the allantoic liquid of the fetal calf; -- formerly called allantoic\n   acid and amniotic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allantois', 'n.', 'Alt. of Allantoid');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allantoid', 'n.', 'A membranous appendage of the embryos of mammals, birds,\n   and reptiles, -- in mammals serving to connect the fetus with the\n   parent; the urinary vesicle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allatrate', 'v. i.', 'To bark as a dog.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allayed', 'imp. & p. p.', 'of Allay');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allaying', 'p. pr. & vb. n.', 'of Allay');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allay', 'v. t.', 'To make quiet or put at rest; to pacify or appease; to\n   quell; to calm; as, to allay popular excitement; to allay the tumult of\n   the passions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allay', 'v. t.', 'To alleviate; to abate; to mitigate; as, to allay the\n   severity of affliction or the bitterness of adversity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allay', 'v. t.', 'To diminish in strength; to abate; to subside.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allay', 'n.', 'Alleviation; abatement; check.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allay', 'n.', 'Alloy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allay', 'v. t.', 'To mix (metals); to mix with a baser metal; to alloy; to\n   deteriorate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allayer', 'n.', 'One who, or that which, allays.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allayment', 'n.', 'An allaying; that which allays; mitigation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allecret', 'n.', 'A kind of light armor used in the sixteenth century, esp.\n   by the Swiss.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allect', 'v. t.', 'To allure; to entice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allectation', 'n.', 'Enticement; allurement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allective', 'a.', 'Alluring.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allective', 'n.', 'Allurement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alledge', 'v. t.', 'See Allege.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegation', 'n.', 'The act of alleging or positively asserting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegation', 'n.', 'That which is alleged, asserted, or declared; positive\n   assertion; formal averment');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegation', 'n.', 'A statement by a party of what he undertakes to prove,\n   -- usually applied to each separate averment; the charge or matter\n   undertaken to be proved.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alleged', 'imp. & p. p.', 'of Allege');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alleging', 'p. pr. & vb. n.', 'of Allege');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allege', 'v. t.', 'To bring forward with positiveness; to declare; to\n   affirm; to assert; as, to allege a fact.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allege', 'v. t.', 'To cite or quote; as, to allege the authority of a\n   judge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allege', 'v. t.', 'To produce or urge as a reason, plea, or excuse; as, he\n   refused to lend, alleging a resolution against lending.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allege', 'v. t.', 'To alleviate; to lighten, as a burden or a trouble.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegeable', 'a.', 'Capable of being alleged or affirmed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegeance', 'n.', 'Allegation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegement', 'n.', 'Allegation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alleger', 'n.', 'One who affirms or declares.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegge', 'v. t.', 'See Alegge and Allay.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegiance', 'n.', 'The tie or obligation, implied or expressed, which a\n   subject owes to his sovereign or government; the duty of fidelity to\n   ones king, government, or state.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegiance', 'n.', 'Devotion; loyalty; as, allegiance to science.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegiant', 'a.', 'Loyal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegoric', 'a.', 'Alt. of Allegorical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegorical', 'a.', 'Belonging to, or consisting of, allegory; of the\n   nature of an allegory; describing by resemblances; figurative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegorist', 'n.', 'One who allegorizes; a writer of allegory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegorization', 'n.', 'The act of turning into allegory, or of\n   understanding in an allegorical sense.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegorized', 'imp. & p. p.', 'of Allegorize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegorizing', 'p. pr. & vb. n.', 'of Allegorize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegorize', 'v. t.', 'To form or turn into allegory; as, to allegorize the\n   history of a people.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegorize', 'v. t.', 'To treat as allegorical; to understand in an\n   allegorical sense; as, when a passage in a writer may understood\n   literally or figuratively, he who gives it a figurative sense is said\n   to allegorize it.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegorize', 'v. t.', 'To use allegory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegorizer', 'n.', 'One who allegorizes, or turns things into allegory; an\n   allegorist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegories', 'pl. ', 'of Allegory');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegory', 'n.', 'A figurative sentence or discourse, in which the\n   principal subject is described by another subject resembling it in its\n   properties and circumstances. The real subject is thus kept out of\n   view, and we are left to collect the intentions of the writer or\n   speaker by the resemblance of the secondary to the primary subject.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegory', 'n.', 'Anything which represents by suggestive resemblance; an\n   emblem.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegory', 'n.', 'A figure representation which has a meaning beyond notion\n   directly conveyed by the object painted or sculptured.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegresse', 'n.', 'Joy; gladsomeness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegretto', 'a.', 'Quicker than andante, but not so quick as allegro.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegretto', 'n.', 'A movement in this time.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegro', 'a.', 'Brisk, lively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allegro', 'n.', 'An allegro movement; a quick, sprightly strain or piece.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alleluia', 'n.', 'Alt. of Alleluiah');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alleluiah', 'n.', 'An exclamation signifying Praise ye Jehovah. Hence: A\n   song of praise to God. See Hallelujah, the commoner form.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allemande', 'n.', 'A dance in moderate twofold time, invented by the French\n   in the reign of Louis XIV.; -- now mostly found in suites of pieces,\n   like those of Bach and Handel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allemande', 'n.', 'A figure in dancing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allemannic', 'a.', 'See Alemannic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allenarly', 'adv.', 'Solely; only.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aller', 'a.', 'Same as Alder, of all.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allerion', 'n.', 'Am eagle without beak or feet, with expanded wings.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alleviated', 'imp. & p. p.', 'of Alleviate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alleviating', 'p. pr. & vb. n.', 'of Alleviate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alleviate', 'v. t.', 'To lighten or lessen the force or weight of.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alleviate', 'v. t.', 'To lighten or lessen (physical or mental troubles);\n   to mitigate, or make easier to be endured; as, to alleviate sorrow,\n   pain, care, etc. ; -- opposed to aggravate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alleviate', 'v. t.', 'To extenuate; to palliate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alleviation', 'n.', 'The act of alleviating; a lightening of weight or\n   severity; mitigation; relief.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alleviation', 'n.', 'That which mitigates, or makes more tolerable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alleviative', 'a.', 'Tending to alleviate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alleviative', 'n.', 'That which alleviates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alleviator', 'n.', 'One who, or that which, alleviates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alleviatory', 'a.', 'Alleviative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alleys', 'pl. ', 'of Alley');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alley', 'n.', 'A narrow passage; especially a walk or passage in a garden\n   or park, bordered by rows of trees or bushes; a bordered way.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alley', 'n.', 'A narrow passage or way in a city, as distinct from a public\n   street.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alley', 'n.', 'A passageway between rows of pews in a church.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alley', 'n.', 'Any passage having the entrance represented as wider than\n   the exit, so as to give the appearance of length.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alley', 'n.', 'The space between two rows of compositors stands in a\n   printing office.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alleys', 'pl. ', 'of Alley');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alley', 'n.', 'A choice taw or marble.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alleyed', 'a.', 'Furnished with alleys; forming an alley.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alleyway', 'n.', 'An alley.');
  INSERT INTO words (word, word_type, description) VALUES  (  'All Fools Day', '', 'The first day of April, a day on which sportive\n   impositions are practiced.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allfours', '', 'A game at cards, called \"High, Low, Jack, and the Game.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'All fours', '', 'All four legs of a quadruped; or the two legs and two arms\n   of a person.');
  INSERT INTO words (word, word_type, description) VALUES  (  'All hail', 'interj.', 'All health; -- a phrase of salutation or welcome.');
  INSERT INTO words (word, word_type, description) VALUES  (  'All-hail', 'v. t.', 'To salute; to greet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allhallond', 'n.', 'Allhallows.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allhallow', 'n.', 'Alt. of Allhallows');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allhallows', 'n.', 'All the saints (in heaven).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allhallows', 'n.', 'All Saints Day, November 1st.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allhallow eve', '', 'The evening before Allhallows. See Halloween.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allhallowmas', 'n.', 'The feast of All Saints.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allhallown', 'a.', 'Of or pertaining to the time of Allhallows. [Obs.]\n   \"Allhallown summer.\" Shak. (i. e., late summer; \"Indian Summer\").');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allhallowtide', 'n.', 'The time at or near All Saints, or November 1st.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allheal', 'n.', 'A name popularly given to the officinal valerian, and to\n   some other plants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alliable', 'a.', 'Able to enter into alliance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alliaceous', 'a.', 'Of or pertaining to the genus Allium, or garlic,\n   onions, leeks, etc.; having the smell or taste of garlic or onions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alliance', 'n.', 'The state of being allied; the act of allying or uniting;\n   a union or connection of interests between families, states, parties,\n   etc., especially between families by marriage and states by compact,\n   treaty, or league; as, matrimonial alliances; an alliance between\n   church and state; an alliance between France and England.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alliance', 'n.', 'Any union resembling that of families or states; union by\n   relationship in qualities; affinity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alliance', 'n.', 'The persons or parties allied.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alliance', 'v. t.', 'To connect by alliance; to ally.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alliant', 'n.', 'An ally; a confederate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allice', 'n.', 'Alt. of Allis');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allis', 'n.', 'The European shad (Clupea vulgaris); allice shad. See Alose.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alliciency', 'n.', 'Attractive power; attractiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allicient', 'a.', 'That attracts; attracting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allicient', 'n.', 'That attracts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allied', 'a.', 'United; joined; leagued; akin; related. See Ally.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alligate', 'v. t.', 'To tie; to unite by some tie.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alligation', 'n.', 'The act of tying together or attaching by some bond, or\n   the state of being attached.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alligation', 'n.', 'A rule relating to the solution of questions concerning\n   the compounding or mixing of different ingredients, or ingredients of\n   different qualities or values.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alligator', 'n.', 'A large carnivorous reptile of the Crocodile family,\n   peculiar to America. It has a shorter and broader snout than the\n   crocodile, and the large teeth of the lower jaw shut into pits in the\n   upper jaw, which has no marginal notches. Besides the common species of\n   the southern United States, there are allied species in South America.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alligator', 'n.', 'Any machine with strong jaws, one of which opens like\n   the movable jaw of an alligator');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alligator', 'n.', 'a form of squeezer for the puddle ball');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alligator', 'n.', 'a rock breaker');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alligator', 'n.', 'a kind of job press, called also alligator press.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allignment', 'n.', 'See Alignment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allineate', 'v. t.', 'To align.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allineation', 'n.', 'Alt. of Alineation');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alineation', 'n.', 'Alignment; position in a straight line, as of two\n   planets with the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allision', 'n.', 'The act of dashing against, or striking upon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alliteral', 'a.', 'Pertaining to, or characterized by alliteration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alliterate', 'v. t.', 'To employ or place so as to make alliteration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alliterate', 'v. i.', 'To compose alliteratively; also, to constitute\n   alliteration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alliteration', 'n.', 'The repetition of the same letter at the beginning of\n   two or more words immediately succeeding each other, or at short\n   intervals; as in the following lines: -');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alliterative', 'a.', 'Pertaining to, or characterized by, alliteration; as,\n   alliterative poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alliterator', 'n.', 'One who alliterates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allium', 'n.', 'A genus of plants, including the onion, garlic, leek,\n   chive, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allmouth', 'n.', 'The angler.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allness', 'n.', 'Totality; completeness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allnight', 'n.', 'Light, fuel, or food for the whole night.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allocate', 'v. t.', 'To distribute or assign; to allot.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allocate', 'v. t.', 'To localize.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allocation', 'n.', 'The act of putting one thing to another; a placing;\n   disposition; arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allocation', 'n.', 'An allotment or apportionment; as, an allocation of\n   shares in a company.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allocation', 'n.', 'The admission of an item in an account, or an allowance\n   made upon an account; -- a term used in the English exchequer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allocatur', 'n.', '\"Allowed.\" The word allocatur expresses the allowance of\n   a proceeding, writ, order, etc., by a court, judge, or judicial\n   officer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allochroic', 'a.', 'Changeable in color.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allochroite', 'n.', 'See Garnet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allochroous', 'a.', 'Changing color.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allocution', 'n.', 'The act or manner of speaking to, or of addressing in\n   words.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allocution', 'n.', 'An address; a hortatory or authoritative address as of\n   a pope to his clergy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allod', 'n.', 'See Allodium.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allodial', 'a.', 'Pertaining to allodium; freehold; free of rent or\n   service; held independent of a lord paramount; -- opposed to feudal;\n   as, allodial lands; allodial system.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allodial', 'a.', 'Anything held allodially.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allodialism', 'n.', 'The allodial system.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allodialist', 'n.', 'One who holds allodial land.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allodially', 'adv.', 'By allodial tenure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allodiary', 'n.', 'One who holds an allodium.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allodium', 'n.', 'Freehold estate; land which is the absolute property of\n   the owner; real estate held in absolute independence, without being\n   subject to any rent, service, or acknowledgment to a superior. It is\n   thus opposed to feud.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allogamous', 'a.', 'Characterized by allogamy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allogamy', 'n.', 'Fertilization of the pistil of a plant by pollen from\n   another of the same species; cross-fertilization.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allogeneous', 'a.', 'Different in nature or kind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allograph', 'n.', 'A writing or signature made by some person other than\n   any of the parties thereto; -- opposed to autograph.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allomerism', 'n.', 'Variability in chemical constitution without variation\n   in crystalline form.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allomerous', 'a.', 'Characterized by allomerism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allomorph', 'n.', 'Any one of two or more distinct crystalline forms of the\n   same substance; or the substance having such forms; -- as, carbonate of\n   lime occurs in the allomorphs calcite and aragonite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allomorph', 'n.', 'A variety of pseudomorph which has undergone partial or\n   complete change or substitution of material; -- thus limonite is\n   frequently an allomorph after pyrite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allomorphic', 'a.', 'Of or pertaining to allomorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allomorphism', 'n.', 'The property which constitutes an allomorph; the\n   change involved in becoming an allomorph.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allonge', 'v.', 'A thrust or pass; a lunge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allonge', 'v.', 'A slip of paper attached to a bill of exchange for\n   receiving indorsements, when the back of the bill itself is already\n   full; a rider.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allonge', 'v. i.', 'To thrust with a sword; to lunge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allonym', 'n.', 'The name of another person assumed by the author of a\n   work.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allonym', 'n.', 'A work published under the name of some one other than the\n   author.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allonymous', 'a.', 'Published under the name of some one other than the\n   author.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alloo', 'v. t. / i.', 'To incite dogs by a call; to halloo.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allopath', 'n.', 'An allopathist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allopathic', 'a.', 'Of or pertaining to allopathy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allopathically', 'adv.', 'In a manner conformable to allopathy; by\n   allopathic methods.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allopathist', 'n.', 'One who practices allopathy; one who professes\n   allopathy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allopathy', 'n.', 'That system of medical practice which aims to combat\n   disease by the use of remedies which produce effects different from\n   those produced by the special disease treated; -- a term invented by\n   Hahnemann to designate the ordinary practice, as opposed to homeopathy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allophylic', 'a.', 'Alt. of Allophylian');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allophylian', 'a.', 'Pertaining to a race or a language neither Aryan nor\n   Semitic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alloquy', 'n.', 'A speaking to another; an address.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allotted', 'imp. & p. p.', 'of Allot');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allotting', 'p. pr. & vb. n.', 'of Allot');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allot', 'v. t.', 'To distribute by lot.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allot', 'v. t.', 'To distribute, or parcel out in parts or portions; or to\n   distribute to each individual concerned; to assign as a share or lot;\n   to set apart as ones share; to bestow on; to grant; to appoint; as,\n   let every man be contented with that which Providence allots him.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allotheism', 'n.', 'The worship of strange gods.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allotment', 'n.', 'The act of allotting; assignment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allotment', 'n.', 'That which is allotted; a share, part, or portion\n   granted or distributed; that which is assigned by lot, or by the act of\n   God; anything set apart for a special use or to a distinct party.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allotment', 'n.', 'The allowance of a specific amount of scrip or of a\n   particular thing to a particular person.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allotriophagy', 'n.', 'A depraved appetite; a desire for improper food.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allotropic', 'a.', 'Alt. of Allotropical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allotropical', 'a.', 'Of or pertaining to allotropism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allotropicity', 'n.', 'Allotropic property or nature.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allotropism', 'n.', 'Alt. of Allotropy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allotropy', 'n.', 'The property of existing in two or more conditions which\n   are distinct in their physical or chemical relations.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allotropize', 'v. t.', 'To change in physical properties but not in\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allottable', 'a.', 'Capable of being allotted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allottee', 'n.', 'One to whom anything is allotted; one to whom an\n   allotment is made.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allotter', 'n.', 'One who allots.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allottery', 'n.', 'Allotment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allowed', 'imp. & p. p.', 'of Allow');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allowing', 'p. pr. & vb. n.', 'of Allow');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allow', 'v. t.', 'To praise; to approve of; hence, to sanction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allow', 'v. t.', 'To like; to be suited or pleased with.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allow', 'v. t.', 'To sanction; to invest; to intrust.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allow', 'v. t.', 'To grant, give, admit, accord, afford, or yield; to let\n   one have; as, to allow a servant his liberty; to allow a free passage;\n   to allow one day for rest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allow', 'v. t.', 'To own or acknowledge; to accept as true; to concede; to\n   accede to an opinion; as, to allow a right; to allow a claim; to allow\n   the truth of a proposition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allow', 'v. t.', 'To grant (something) as a deduction or an addition; esp.\n   to abate or deduct; as, to allow a sum for leakage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allow', 'v. t.', 'To grant license to; to permit; to consent to; as, to\n   allow a son to be absent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allow', 'v. i.', 'To admit; to concede; to make allowance or abatement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allowable', 'a.', 'Praiseworthy; laudable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allowable', 'a.', 'Proper to be, or capable of being, allowed; permissible;\n   admissible; not forbidden; not unlawful or improper; as, a certain\n   degree of freedom is allowable among friends.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allowableness', 'n.', 'The quality of being allowable; permissibleness;\n   lawfulness; exemption from prohibition or impropriety.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allowably', 'adv.', 'In an allowable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allowance', 'n.', 'Approval; approbation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allowance', 'n.', 'The act of allowing, granting, conceding, or admitting;\n   authorization; permission; sanction; tolerance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allowance', 'n.', 'Acknowledgment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allowance', 'n.', 'License; indulgence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allowance', 'n.', 'That which is allowed; a share or portion allotted or\n   granted; a sum granted as a reimbursement, a bounty, or as appropriate\n   for any purpose; a stated quantity, as of food or drink; hence, a\n   limited quantity of meat and drink, when provisions fall short.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allowance', 'n.', 'Abatement; deduction; the taking into account of\n   mitigating circumstances; as, to make allowance for the inexperience of\n   youth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allowance', 'n.', 'A customary deduction from the gross weight of goods,\n   different in different countries, such as tare and tret.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allowancing', 'imp. & p. p.', 'of Allowance');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allowance', 'n.', 'To put upon a fixed allowance (esp. of provisions and\n   drink); to supply in a fixed and limited quantity; as, the captain was\n   obliged to allowance his crew; our provisions were allowanced.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allowedly', 'adv.', 'By allowance; admittedly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allower', 'n.', 'An approver or abettor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allower', 'n.', 'One who allows or permits.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alloxan', 'n.', 'An oxidation product of uric acid. It is of a pale reddish\n   color, readily soluble in water or alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alloxanate', 'n.', 'A combination of alloxanic acid and a base or base or\n   positive radical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alloxanic', 'a.', 'Of or pertaining to alloxan; -- applied to an acid\n   obtained by the action of soluble alkalies on alloxan.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alloxantin', 'n.', 'A substance produced by acting upon uric with warm and\n   very dilute nitric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alloy', 'v. t.', 'Any combination or compound of metals fused together; a\n   mixture of metals; for example, brass, which is an alloy of copper and\n   zinc. But when mercury is one of the metals, the compound is called an\n   amalgam.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alloy', 'v. t.', 'The quality, or comparative purity, of gold or silver;\n   fineness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alloy', 'v. t.', 'A baser metal mixed with a finer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alloy', 'v. t.', 'Admixture of anything which lessens the value or detracts\n   from; as, no happiness is without alloy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alloyed', 'imp. & p. p.', 'of Alloy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alloying', 'p. pr. & vb. n.', 'of Alloy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alloy', 'v. t.', 'To reduce the purity of by mixing with a less valuable\n   substance; as, to alloy gold with silver or copper, or silver with\n   copper.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alloy', 'v. t.', 'To mix, as metals, so as to form a compound.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alloy', 'v. t.', 'To abate, impair, or debase by mixture; to allay; as, to\n   alloy pleasure with misfortunes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alloy', 'v. t.', 'To form a metallic compound.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alloyage', 'n.', 'The act or art of alloying metals; also, the combination\n   or alloy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'All-possessed', 'a.', 'Controlled by an evil spirit or by evil passions;\n   wild.');
  INSERT INTO words (word, word_type, description) VALUES  (  'All Saints', '', 'Alt. of All Saints');
  INSERT INTO words (word, word_type, description) VALUES  (  'All Saints', '', 'The first day of November, called, also, Allhallows or\n   Hallowmas; a feast day kept in honor of all the saints; also, the\n   season of this festival.');
  INSERT INTO words (word, word_type, description) VALUES  (  'All Souls Day', '', 'The second day of November; a feast day of the Roman\n   Catholic church, on which supplications are made for the souls of the\n   faithful dead.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allspice', 'n.', 'The berry of the pimento (Eugenia pimenta); a tree of the\n   West Indies; a spice of a mildly pungent taste, and agreeably aromatic;\n   Jamaica pepper; pimento. It has been supposed to combine the flavor of\n   cinnamon, nutmegs, and cloves; and hence the name. The name is also\n   given to other aromatic shrubs; as, the Carolina allspice (Calycanthus\n   floridus); wild allspice (Lindera benzoin); called also spicebush,\n   spicewood, and feverbush.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allthing', 'adv.', 'Altogether.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alluded', 'imp. & p. p.', 'of Allude');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alluding', 'p. pr. & vb. n.', 'of Allude');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allude', 'v. i.', 'To refer to something indirectly or by suggestion; to\n   have reference to a subject not specifically and plainly mentioned; --\n   followed by to; as, the story alludes to a recent transaction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allude', 'v. t.', 'To compare allusively; to refer (something) as\n   applicable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allumette', 'n.', 'A match for lighting candles, lamps, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alluminor', 'n.', 'An illuminator of manuscripts and books; a limner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allurance', 'n.', 'Allurement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alluded', 'imp. & p. p.', 'of Allure');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alluring', 'p. pr. & vb. n.', 'of Allure');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allure', 'v. t.', 'To attempt to draw; to tempt by a lure or bait, that is,\n   by the offer of some good, real or apparent; to invite by something\n   flattering or acceptable; to entice; to attract.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allure', 'n.', 'Allurement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allure', 'n.', 'Gait; bearing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allurement', 'n.', 'The act alluring; temptation; enticement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allurement', 'n.', 'That which allures; any real or apparent good held\n   forth, or operating, as a motive to action; as, the allurements of\n   pleasure, or of honor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allurer', 'n.', 'One who, or that which, allures.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alluring', 'a.', 'That allures; attracting; charming; tempting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allusion', 'n.', 'A figurative or symbolical reference.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allusion', 'n.', 'A reference to something supposed to be known, but not\n   explicitly mentioned; a covert indication; indirect reference; a hint.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allusive', 'a.', 'Figurative; symbolical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allusive', 'a.', 'Having reference to something not fully expressed;\n   containing an allusion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allusively', 'adv.', 'Figuratively [Obs.]; by way of allusion; by\n   implication, suggestion, or insinuation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allusiveness', 'n.', 'The quality of being allusive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allusory', 'a.', 'Allusive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alluvial', 'a.', 'Pertaining to, contained in, or composed of, alluvium;\n   relating to the deposits made by flowing water; washed away from one\n   place and deposited in another; as, alluvial soil, mud, accumulations,\n   deposits.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alluvion', 'n.', 'Wash or flow of water against the shore or bank.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alluvion', 'n.', 'An overflowing; an inundation; a flood.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alluvion', 'n.', 'Matter deposited by an inundation or the action of\n   flowing water; alluvium.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alluvion', 'n.', 'An accession of land gradually washed to the shore or\n   bank by the flowing of water. See Accretion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alluvious', 'n.', 'Alluvial.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alluviums', 'pl. ', 'of Alluvium');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alluvia', 'pl. ', 'of Alluvium');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alluvium', 'n.', 'Deposits of earth, sand, gravel, and other transported\n   matter, made by rivers, floods, or other causes, upon land not\n   permanently submerged beneath the waters of lakes or seas.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allwhere', 'adv.', 'Everywhere.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allwork', 'n.', 'Domestic or other work of all kinds; as, a maid of\n   allwork, that is, a general servant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allied', 'imp. & p. p.', 'of Ally');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allying', 'p. pr. & vb. n.', 'of Ally');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ally', 'v. t.', 'To unite, or form a connection between, as between\n   families by marriage, or between princes and states by treaty, league,\n   or confederacy; -- often followed by to or with.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ally', 'v. t.', 'To connect or form a relation between by similitude,\n   resemblance, friendship, or love.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allies', 'pl. ', 'of Ally');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ally', 'v.', 'A relative; a kinsman.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ally', 'v.', 'One united to another by treaty or league; -- usually applied\n   to sovereigns or states; a confederate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ally', 'v.', 'Anything associated with another as a helper; an auxiliary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ally', 'v.', 'Anything akin to another by structure, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ally', 'n.', 'See Alley, a marble or taw.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allyl', 'n.', 'An organic radical, C3H5, existing especially in oils of\n   garlic and mustard.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Allylene', 'n.', 'A gaseous hydrocarbon, C3H4, homologous with acetylene;\n   propine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alma', 'n.', 'Alt. of Almah');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almah', 'n.', 'Same as Alme.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almacantar', 'n.', 'Same as Almucantar.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almacantar', 'n.', 'A recently invented instrument for observing the\n   heavenly bodies as they cross a given almacantar circle. See\n   Almucantar.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almadia', 'n.', 'Alt. of Almadie');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almadie', 'n.', 'A bark canoe used by the Africans.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almadie', 'n.', 'A boat used at Calicut, in India, about eighty feet long,\n   and six or seven broad.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almagest', 'n.', 'The celebrated work of Ptolemy of Alexandria, which\n   contains nearly all that is known of the astronomical observations and\n   theories of the ancients. The name was extended to other similar works.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almagra', 'n.', 'A fine, deep red ocher, somewhat purplish, found in Spain.\n   It is the sil atticum of the ancients. Under the name of Indian red it\n   is used for polishing glass and silver.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almain', 'n.', 'Alt. of Alman');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almayne', 'n.', 'Alt. of Alman');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alman', 'n.', 'A German.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alman', 'adj.', 'German.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alman', 'adj.', 'The German language.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alman', 'adj.', 'A kind of dance. See Allemande.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alma Mater', '', 'A college or seminary where one is educated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almanac', 'n.', 'A book or table, containing a calendar of days, and\n   months, to which astronomical data and various statistics are often\n   added, such as the times of the rising and setting of the sun and moon,\n   eclipses, hours of full tide, stated festivals of churches, terms of\n   courts, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almandine', 'n.', 'The common red variety of garnet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alme', 'n.', 'Alt. of Almeh');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almeh', 'n.', 'An Egyptian dancing girl; an Alma.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almendron', 'n.', 'The lofty Brazil-nut tree.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almery', 'n.', 'See Ambry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almesse', 'n.', 'See Alms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almightful', 'a.', 'Alt. of Almightiful');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almightiful', 'a.', 'All-powerful; almighty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almightily', 'adv.', 'With almighty power.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almightiness', 'n.', 'Omnipotence; infinite or boundless power; unlimited\n   might.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almighty', 'a.', 'Unlimited in might; omnipotent; all-powerful;\n   irresistible.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almighty', 'a.', 'Great; extreme; terrible.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almner', 'n.', 'An almoner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almond', 'n.', 'The fruit of the almond tree.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almond', 'n.', 'The tree that bears the fruit; almond tree.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almond', 'n.', 'Anything shaped like an almond.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almond', 'n.', 'One of the tonsils.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almond furnace', '', 'A kind of furnace used in refining, to separate the\n   metal from cinders and other foreign matter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almondine', 'n.', 'See Almandine');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almoner', 'n.', 'One who distributes alms, esp. the doles and alms of\n   religious houses, almshouses, etc.; also, one who dispenses alms for\n   another, as the almoner of a prince, bishop, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almonership', 'n.', 'The office of an almoner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almonries', 'pl. ', 'of Almonry');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almonry', 'n.', 'The place where an almoner resides, or where alms are\n   distributed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almose', 'n.', 'Alms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almost', 'adv.', 'Nearly; well nigh; all but; for the greatest part.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almry', 'n.', 'See Almonry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alms', 'n. sing. & pl.', 'Anything given gratuitously to relieve the poor,\n   as money, food, or clothing; a gift of charity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almsdeed', 'n.', 'An act of charity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almsfolk', 'n.', 'Persons supported by alms; almsmen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almsgiver', 'n.', 'A giver of alms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almsgiving', 'n.', 'The giving of alms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almshouse', 'n.', 'A house appropriated for the use of the poor; a\n   poorhouse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almsman', 'n.', 'A recipient of alms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almsman', 'n.', 'A giver of alms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almucantar', 'n.', 'A small circle of the sphere parallel to the horizon; a\n   circle or parallel of altitude. Two stars which have the same\n   almucantar have the same altitude. See Almacantar.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almuce', 'n.', 'Same as Amice, a hood or cape.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almude', 'n.', 'A measure for liquids in several countries. In Portugal the\n   Lisbon almude is about 4.4, and the Oporto almude about 6.6, gallons U.\n   S. measure. In Turkey the \"almud\" is about 1.4 gallons.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Almug', 'n.', 'Alt. of Algum');
  INSERT INTO words (word, word_type, description) VALUES  (  'Algum', 'n.', 'A tree or wood of the Bible (2 Chron. ii. 8; 1 K. x. 11).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alnage', 'n.', 'Measurement (of cloth) by the ell; also, a duty for such\n   measurement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alnager', 'n.', 'A measure by the ell; formerly a sworn officer in England,\n   whose duty was to inspect and measure woolen cloth, and fix upon it a\n   seal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aloes', 'pl. ', 'of Aloe');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aloe', 'n.', 'The wood of the agalloch.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aloe', 'n.', 'A genus of succulent plants, some classed as trees, others as\n   shrubs, but the greater number having the habit and appearance of\n   evergreen herbaceous plants; from some of which are prepared articles\n   for medicine and the arts. They are natives of warm countries.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aloe', 'n.', 'The inspissated juice of several species of aloe, used as a\n   purgative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aloes wood', '', 'See Agalloch.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aloetic', 'a.', 'Consisting chiefly of aloes; of the nature of aloes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aloetic', 'n.', 'A medicine containing chiefly aloes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aloft', 'adv.', 'On high; in the air; high above the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aloft', 'adv.', 'In the top; at the mast head, or on the higher yards or\n   rigging; overhead; hence (Fig. and Colloq.); in or to heaven.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aloft', 'prep.', 'Above; on top of.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alogian', 'n.', 'One of an ancient sect who rejected St. Johns Gospel and\n   the Apocalypse, which speak of Christ as the Logos.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alogy', 'n.', 'Unreasonableness; absurdity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aloin', 'n.', 'A bitter purgative principle in aloes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alomancy', 'n.', 'Divination by means of salt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alone', 'a.', 'Quite by ones self; apart from, or exclusive of, others;\n   single; solitary; -- applied to a person or thing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alone', 'a.', 'Of or by itself; by themselves; without any thing more or\n   any one else; without a sharer; only.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alone', 'a.', 'Sole; only; exclusive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alone', 'a.', 'Hence; Unique; rare; matchless.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alone', 'adv.', 'Solely; simply; exclusively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alonely', 'adv.', 'Only; merely; singly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alonely', 'a.', 'Exclusive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aloneness', 'n.', 'A state of being alone, or without company;\n   solitariness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Along', 'adv.', 'By the length; in a line with the length; lengthwise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Along', 'adv.', 'In a line, or with a progressive motion; onward; forward.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Along', 'adv.', 'In company; together.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Along', 'prep.', 'By the length of, as distinguished from across.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Along', '', '(Now heard only in the prep. phrase along of.)');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alongshore', 'adv.', 'Along the shore or coast.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alongshoreman', 'n.', 'See Longshoreman.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alongside', 'adv.', 'Along or by the side; side by side with; -- often with\n   of; as, bring the boat alongside; alongside of him; alongside of the\n   tree.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alongst', 'prep. & adv.', 'Along.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aloof', 'n.', 'Same as Alewife.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aloof', 'adv.', 'At or from a distance, but within view, or at a small\n   distance; apart; away.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aloof', 'adv.', 'Without sympathy; unfavorably.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aloof', 'prep.', 'Away from; clear from.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aloofness', 'n.', 'State of being aloof.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alopecia', 'n.', 'Alt. of Alopecy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alopecy', 'n.', 'Loss of the hair; baldness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alopecist', 'n.', 'A practitioner who tries to prevent or cure baldness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alose', 'v. t.', 'To praise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alose', 'n.', 'The European shad (Clupea alosa); -- called also allice shad\n   or allis shad. The name is sometimes applied to the American shad\n   (Clupea sapidissima). See Shad.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alouatte', 'n.', 'One of the several species of howling monkeys of South\n   America. See Howler, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aloud', 'adv.', 'With a loud voice, or great noise; loudly; audibly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alow', 'adv.', 'Below; in a lower part.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alp', 'n.', 'A very high mountain. Specifically, in the plural, the highest\n   chain of mountains in Europe, containing the lofty mountains of\n   Switzerland, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alp', 'n.', 'Fig.: Something lofty, or massive, or very hard to be\n   surmounted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alp', 'n.', 'A bullfinch.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alpaca', 'n.', 'An animal of Peru (Lama paco); having long, fine, wooly\n   hair, supposed by some to be a domesticated variety of the llama.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alpaca', 'n.', 'Wool of the alpaca.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alpaca', 'n.', 'A thin kind of cloth made of the wooly hair of the alpaca,\n   often mixed with silk or with cotton.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alpen', 'a.', 'Of or pertaining to the Alps.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alpenstock', 'n.', 'A long staff, pointed with iron, used in climbing the\n   Alps.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alpestrine', 'a.', 'Pertaining to the Alps, or other high mountains; as,\n   Alpestrine diseases, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alpha', 'n.', 'The first letter in the Greek alphabet, answering to A, and\n   hence used to denote the beginning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alphabet', 'n.', 'The letters of a language arranged in the customary\n   order; the series of letters or signs which form the elements of\n   written language.');
