  INSERT INTO words (word, word_type, description) VALUES  (  'Alphabetize', 'v. t.', 'To furnish with an alphabet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Al-phitomancy', 'n.', 'Divination by means of barley meal.');
