  INSERT INTO words (word, word_type, description) VALUES  (  'Alpinist', 'n.', 'A climber of the Alps.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alpist', 'n.', 'Alt. of Alpia');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alpia', 'n.', 'The seed of canary grass (Phalaris Canariensis); used for\n   feeding cage birds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alquifou', 'n.', 'A lead ore found in Cornwall, England, and used by\n   potters to give a green glaze to their wares; potters ore.');
