  INSERT INTO words (word, word_type, description) VALUES  (  'Als', 'adv.', 'Also.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Als', 'adv.', 'As.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alsatian', 'a.', 'Pertaining to Alsatia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alsatian', 'n.', 'An inhabitant of Alsatia or Alsace in Germany, or of\n   Alsatia or White Friars (a resort of debtors and criminals) in London.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Al segno', '', 'A direction for the performer to return and recommence from\n   the sign /.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alsike', 'n.', 'A species of clover with pinkish or white flowers;\n   Trifolium hybridum.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Also', 'adv. & conj.', 'In like manner; likewise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Also', 'adv. & conj.', 'In addition; besides; as well; further; too.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Also', 'adv. & conj.', 'Even as; as; so.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alt', 'a. & n.', 'The higher part of the scale. See Alto.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altaian', 'a.', 'Alt. of Altaic');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altaic', 'a.', 'Of or pertaining to the Altai, a mountain chain in Central\n   Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altar', 'n.', 'A raised structure (as a square or oblong erection of stone\n   or wood) on which sacrifices are offered or incense burned to a deity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altar', 'n.', 'In the Christian church, a construction of stone, wood, or\n   other material for the celebration of the Holy Eucharist; the communion\n   table.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altarage', 'n.', 'The offerings made upon the altar, or to a church.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altarage', 'n.', 'The profit which accrues to the priest, by reason of the\n   altar, from the small tithes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altarist', 'n.', 'A chaplain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altarist', 'n.', 'A vicar of a church.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altarpiece', 'n.', 'The painting or piece of sculpture above and behind the\n   altar; reredos.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altarwise', 'adv.', 'In the proper position of an altar, that is, at the\n   east of a church with its ends towards the north and south.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altazimuth', 'n.', 'An instrument for taking azimuths and altitudes\n   simultaneously.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altered', 'imp. & p. p.', 'of Alter');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altering', 'p. pr. & vb. n.', 'of Alter');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alter', 'v. t.', 'To make otherwise; to change in some respect, either\n   partially or wholly; to vary; to modify.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alter', 'v. t.', 'To agitate; to affect mentally.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alter', 'v. t.', 'To geld.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alter', 'v. i.', 'To become, in some respects, different; to vary; to\n   change; as, the weather alters almost daily; rocks or minerals alter by\n   exposure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alterability', 'n.', 'The quality of being alterable; alterableness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alterable', 'a.', 'Capable of being altered.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alterableness', 'n.', 'The quality of being alterable; variableness;\n   alterability.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alterably', 'adv.', 'In an alterable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alterant', 'a.', 'Altering; gradually changing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alterant', 'n.', 'An alterative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alteration', 'n.', 'The act of altering or making different.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alteration', 'n.', 'The state of being altered; a change made in the form\n   or nature of a thing; changed condition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alterative', 'a.', 'Causing ateration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alterative', 'a.', 'Gradually changing, or tending to change, a morbid\n   state of the functions into one of health.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alterative', 'n.', 'A medicine or treatment which gradually induces a\n   change, and restores healthy functions without sensible evacuations.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altercated', 'imp. & p. p.', 'of Altercate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altercating', 'p. pr. & vb. n.', 'of Altercate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altercate', 'v. i.', 'To contend in words; to dispute with zeal, heat, or\n   anger; to wrangle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altercation', 'n.', 'Warm contention in words; dispute carried on with heat\n   or anger; controversy; wrangle; wordy contest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altercative', 'a.', 'Characterized by wrangling; scolding.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alterity', 'n.', 'The state or quality of being other; a being otherwise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altern', 'a.', 'Acting by turns; alternate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternacy', 'n.', 'Alternateness; alternation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternant', 'v. t.', 'Composed of alternate layers, as some rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternate', 'a.', 'Being or succeeding by turns; one following the other in\n   succession of time or place; by turns first one and then the other;\n   hence, reciprocal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternate', 'a.', 'Designating the members in a series, which regularly\n   intervene between the members of another series, as the odd or even\n   numbers of the numerals; every other; every second; as, the alternate\n   members 1, 3, 5, 7, etc. ; read every alternate line.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternate', 'a.', 'Distributed, as leaves, singly at different heights of\n   the stem, and at equal intervals as respects angular divergence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternate', 'n.', 'That which alternates with something else; vicissitude.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternate', 'n.', 'A substitute; one designated to take the place of\n   another, if necessary, in performing some duty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternate', 'n.', 'A proportion derived from another proportion by\n   interchanging the means.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternated', 'imp. & p. p.', 'of Alternate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternating', 'p. pr. & vb. n.', 'of Alternate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternate', 'v. t.', 'To perform by turns, or in succession; to cause to\n   succeed by turns; to interchange regularly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternate', 'v. i.', 'To happen, succeed, or act by turns; to follow\n   reciprocally in place or time; -- followed by with; as, the flood and\n   ebb tides alternate with each other.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternate', 'v. i.', 'To vary by turns; as, the land alternates between\n   rocky hills and sandy plains.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternately', 'adv.', 'In reciprocal succession; succeeding by turns; in\n   alternate order.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternately', 'adv.', 'By alternation; when, in a proportion, the\n   antecedent term is compared with antecedent, and consequent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternateness', 'n.', 'The quality of being alternate, or of following by\n   turns.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternation', 'n.', 'The reciprocal succession of things in time or place;\n   the act of following and being followed by turns; alternate succession,\n   performance, or occurrence; as, the alternation of day and night, cold\n   and heat, summer and winter, hope and fear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternation', 'n.', 'Permutation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternation', 'n.', 'The response of the congregation speaking alternately\n   with the minister.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternative', 'a.', 'Offering a choice of two things.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternative', 'a.', 'Disjunctive; as, an alternative conjunction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternative', 'a.', 'Alternate; reciprocal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternative', 'n.', 'An offer of two things, one of which may be chosen,\n   but not both; a choice between two things, so that if one is taken, the\n   other must be left.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternative', 'n.', 'Either of two things or propositions offered to ones\n   choice. Thus when two things offer a choice of one only, the two things\n   are called alternatives.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternative', 'n.', 'The course of action or the thing offered in place of\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternative', 'n.', 'A choice between more than two things; one of several\n   things offered to choose among.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternatively', 'adv.', 'In the manner of alternatives, or that admits the\n   choice of one out of two things.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternativeness', 'n.', 'The quality of being alternative, or of offering a\n   choice between two.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alternity', 'n.', 'Succession by turns; alternation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Althaea', 'n.', 'Alt. of Althea');
  INSERT INTO words (word, word_type, description) VALUES  (  'Althea', 'n.', 'A genus of plants of the Mallow family. It includes the\n   officinal marsh mallow, and the garden hollyhocks.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Althea', 'n.', 'An ornamental shrub (Hibiscus Syriacus) of the Mallow\n   family.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altheine', 'n.', 'Asparagine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altho', 'conj.', 'Although.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Althorn', 'n.', 'An instrument of the saxhorn family, used exclusively in\n   military music, often replacing the French horn.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Although', 'conj.', 'Grant all this; be it that; supposing that;\n   notwithstanding; though.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altiloquence', 'n.', 'Lofty speech; pompous language.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altiloquent', 'a.', 'High-sounding; pompous in speech.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altimeter', 'n.', 'An instrument for taking altitudes, as a quadrant,\n   sextant, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altimetry', 'n.', 'The art of measuring altitudes, or heights.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altincar', 'n.', 'See Tincal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altiscope', 'n.', 'An arrangement of lenses and mirrors which enables a\n   person to see an object in spite of intervening objects.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altisonant', 'a.', 'High-sounding; lofty or pompous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altisonous', 'a.', 'Altisonant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altissimo', 'n.', 'The part or notes situated above F in alt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altitude', 'n.', 'Space extended upward; height; the perpendicular\n   elevation of an object above its foundation, above the ground, or above\n   a given level, or of one object above another; as, the altitude of a\n   mountain, or of a bird above the top of a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altitude', 'n.', 'The elevation of a point, or star, or other celestial\n   object, above the horizon, measured by the arc of a vertical circle\n   intercepted between such point and the horizon. It is either true or\n   apparent; true when measured from the rational or real horizon,\n   apparent when from the sensible or apparent horizon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altitude', 'n.', 'The perpendicular distance from the base of a figure to\n   the summit, or to the side parallel to the base; as, the altitude of a\n   triangle, pyramid, parallelogram, frustum, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altitude', 'n.', 'Height of degree; highest point or degree.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altitude', 'n.', 'Height of rank or excellence; superiority.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altitude', 'n.', 'Elevation of spirits; heroics; haughty airs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altitudinal', 'a.', 'Of or pertaining to height; as, altitudinal\n   measurements.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altitudinarian', 'a.', 'Lofty in doctrine, aims, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altivolant', 'a.', 'Flying high.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altos', 'pl. ', 'of Alto');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alto', 'n.', 'Formerly the part sung by the highest male, or counter-tenor,\n   voices; now the part sung by the lowest female, or contralto, voices,\n   between in tenor and soprano. In instrumental music it now signifies\n   the tenor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alto', 'n.', 'An alto singer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altogether', 'adv.', 'All together; conjointly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altogether', 'adv.', 'Without exception; wholly; completely.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altometer', 'n.', 'A theodolite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alto-relievo', 'n.', 'Alto-rilievo.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alto-rilievos', 'pl. ', 'of Alto-rilievo');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alto-rilievo', 'n.', 'High relief; sculptured work in which the figures\n   project more than half their thickness; as, this figure is an\n   alto-rilievo or in alto-rilievo.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altrical', 'a.', 'Like the articles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altrices', 'n. pl.', 'Nursers, -- a term applied to those birds whose young\n   are hatched in a very immature and helpless condition, so as to require\n   the care of their parents for some time; -- opposed to praecoces.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altruism', 'n.', 'Regard for others, both natural and moral; devotion to\n   the interests of others; brotherly kindness; -- opposed to egoism or\n   selfishness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altruist', 'n.', 'One imbued with altruism; -- opposed to egoist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Altruistic', 'a.', 'Regardful of others; beneficent; unselfish; -- opposed\n   to egoistic or selfish.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aludel', 'n.', 'One of the pear-shaped pots open at both ends, and so\n   formed as to be fitted together, the neck of one into the bottom of\n   another in succession; -- used in the process of sublimation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alula', 'n.', 'A false or bastard wing. See under Bastard.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alular', 'a.', 'Pertaining to the alula.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alum', 'n.', 'A double sulphate formed of aluminium and some other element\n   (esp. an alkali metal) or of aluminium. It has twenty-four molecules of\n   water of crystallization.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alum', 'v. t.', 'To steep in, or otherwise impregnate with, a solution of\n   alum; to treat with alum.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alumen', 'n.', 'Alum.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alumina', 'n.', 'One of the earths, consisting of two parts of aluminium\n   and three of oxygen, Al2O3.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aluminate', 'n.', 'A compound formed from the hydrate of aluminium by the\n   substitution of a metal for the hydrogen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aluminated', 'a.', 'Combined with alumina.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alumine', 'n.', 'Alumina.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aluminic', 'a.', 'Of or containing aluminium; as, aluminic phosphate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aluminiferous', 'a.', 'Containing alum.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aluminiform', 'a.', 'Having the form of alumina.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aluminium', 'n.', 'The metallic base of alumina. This metal is white, but\n   with a bluish tinge, and is remarkable for its resistance to oxidation,\n   and for its lightness, having a specific gravity of about 2.6. Atomic\n   weight 27.08. Symbol Al.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aluminize', 'v. t.', 'To treat or impregnate with alum; to alum.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aluminous', 'a.', 'Pertaining to or containing alum, or alumina; as,\n   aluminous minerals, aluminous solution.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aluminum', 'n.', 'See Aluminium.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alumish', 'a.', 'Somewhat like alum.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alumnae', 'pl. ', 'of Alumna');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alumna', 'n. fem.', 'A female pupil; especially, a graduate of a school or\n   college.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alumni', 'pl. ', 'of Alumnus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alumnus', 'n.', 'A pupil; especially, a graduate of a college or other\n   seminary of learning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alum root', '', 'A North American herb (Heuchera Americana) of the\n   Saxifrage family, whose root has astringent properties.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alum schist', '', 'Alt. of Alum shale');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alum shale', '', 'A variety of shale or clay slate, containing iron\n   pyrites, the decomposition of which leads to the formation of alum,\n   which often effloresces on the rock.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alum stone', '', 'A subsulphate of alumina and potash; alunite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alunite', 'n.', 'Alum stone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alunogen', 'n.', 'A white fibrous mineral frequently found on the walls of\n   mines and quarries, chiefly hydrous sulphate of alumina; -- also called\n   feather alum, and hair salt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alure', 'n.', 'A walk or passage; -- applied to passages of various kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alutaceous', 'a.', 'Leathery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alutaceous', 'a.', 'Of a pale brown color; leather-yellow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alutation', 'n.', 'The tanning or dressing of leather.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alvearies', 'pl. ', 'of Alveary');
