  INSERT INTO words (word, word_type, description) VALUES  (  'Alveary', 'n.', 'The hollow of the external ear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alveated', 'a.', 'Formed or vaulted like a beehive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Alveolar', 'a.', 'Of, pertaining to, or resembling, alveoli or little\n   cells, sacs, or sockets.');
