  INSERT INTO words (word, word_type, description) VALUES  (  'Alyssum', 'n.', 'A genus of cruciferous plants; madwort. The sweet alyssum\n   (A. maritimum); cultivated for bouquets, bears small, white,\n   sweet-scented flowers.');
