  INSERT INTO words (word, word_type, description) VALUES  (  'Amadavat', 'n.', 'The strawberry finch, a small Indian song bird (Estrelda\n   amandava); commonly caged and kept for fighting. The female is olive\n   brown; the male, in summer, mostly crimson; -- called also red waxbill.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amadou', 'n.', 'A spongy, combustible substance, prepared from fungus\n   (Boletus and Polyporus) which grows on old trees; German tinder; punk.\n   It has been employed as a styptic by surgeons, but its common use is as\n   tinder, for which purpose it is prepared by soaking it in a strong\n   solution of niter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amain', 'n.', 'With might; with full force; vigorously; violently;\n   exceedingly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amain', 'n.', 'At full speed; in great haste; also, at once.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amain', 'v. t.', 'To lower, as a sail, a yard, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amain', 'v. i.', 'To lower the topsail, in token of surrender; to yield.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amalgam', 'n.', 'An alloy of mercury with another metal or metals; as, an\n   amalgam of tin, bismuth, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amalgam', 'n.', 'A mixture or compound of different things.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amalgam', 'n.', 'A native compound of mercury and silver.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amalgam', 'v. t. / i.', 'To amalgamate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amalgama', 'n.', 'Same as Amalgam.');
