  INSERT INTO words (word, word_type, description) VALUES  (  'Amalgamating', 'p. pr. & vb. n.', 'of Amalgamate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amalgamate', 'v. t.', 'To compound or mix, as quicksilver, with another\n   metal; to unite, combine, or alloy with mercury.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amalgamate', 'v. t.', 'To mix, so as to make a uniform compound; to unite\n   or combine; as, to amalgamate two races; to amalgamate one race with\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amalgamate', 'v. i.', 'To unite in an amalgam; to blend with another metal,\n   as quicksilver.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amalgamate', 'v. i.', 'To coalesce, as a result of growth; to combine into\n   a uniform whole; to blend; as, two organs or parts amalgamate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amalgamate', 'a.', 'Alt. of Amalgamated');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amalgamated', 'a.', 'Coalesced; united; combined.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amalgamation', 'n.', 'The act or operation of compounding mercury with\n   another metal; -- applied particularly to the process of separating\n   gold and silver from their ores by mixing them with mercury.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amalgamation', 'n.', 'The mixing or blending of different elements, races,\n   societies, etc.; also, the result of such combination or blending; a\n   homogeneous union.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amalgamative', 'a.', 'Characterized by amalgamation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amalgamator', 'n.', 'One who, or that which, amalgamates. Specifically: A\n   machine for separating precious metals from earthy particles by\n   bringing them in contact with a body of mercury with which they form an\n   amalgam.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amalgamize', 'v. t.', 'To amalgamate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amandine', 'n.', 'The vegetable casein of almonds.');
