  INSERT INTO words (word, word_type, description) VALUES  (  'Amanitine', 'n.', 'The poisonous principle of some fungi.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amanuenses', 'pl. ', 'of Amanuensis');
