  INSERT INTO words (word, word_type, description) VALUES  (  'Amaranthine', 'a.', 'Of a purplish color.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amaranthus', 'n.', 'Alt. of Amarantus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amarantus', 'n.', 'Same as Amaranth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amarine', 'n.', 'A characteristic crystalline substance, obtained from oil\n   of bitter almonds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amaritude', 'n.', 'Bitterness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amaryllidaceous', 'a.', 'Alt. of Amaryllideous');
