  INSERT INTO words (word, word_type, description) VALUES  (  'Amaryllis', 'n.', 'A genus of the same family, including the Belladonna\n   lily.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amassed', 'imp. & p. p.', 'of Amass');
