  INSERT INTO words (word, word_type, description) VALUES  (  'Amassment', 'n.', 'An amassing; a heap collected; a large quantity or\n   number brought together; an accumulation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amasthenic', 'a.', 'Uniting the chemical rays of light into one focus, as a\n   certain kind of lens; amacratic.');
