  INSERT INTO words (word, word_type, description) VALUES  (  'Amateur', 'n.', 'A person attached to a particular pursuit, study, or\n   science as to music or painting; esp. one who cultivates any study or\n   art, from taste or attachment, without pursuing it professionally.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amateurish', 'a.', 'In the style of an amateur; superficial or defective\n   like the work of an amateur.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amateurism', 'n.', 'The practice, habit, or work of an amateur.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amateurship', 'n.', 'The quality or character of an amateur.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amative', 'a.', 'Full of love; amatory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amativeness', 'n.', 'The faculty supposed to influence sexual desire;\n   propensity to love.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amatorial', 'a.', 'Of or pertaining to a lover or to love making; amatory;\n   as, amatorial verses.');
