  INSERT INTO words (word, word_type, description) VALUES  (  'Amaze', 'v. t.', 'To confound, as by fear, wonder, extreme surprise; to\n   overwhelm with wonder; to astound; to astonish greatly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amaze', 'v. i.', 'To be astounded.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amaze', 'v. t.', 'Bewilderment, arising from fear, surprise, or wonder;\n   amazement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amazedly', 'adv.', 'In amazement; with confusion or astonishment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amazedness', 'n.', 'The state of being amazed, or confounded with fear,\n   surprise, or wonder.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amazeful', 'a.', 'Full of amazement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amazement', 'n.', 'The condition of being amazed; bewilderment [Obs.];\n   overwhelming wonder, as from surprise, sudden fear, horror, or\n   admiration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amazement', 'n.', 'Frenzy; madness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amazing', 'a.', 'Causing amazement; very wonderful; as, amazing grace.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amazon', 'n.', 'One of a fabulous race of female warriors in Scythia;\n   hence, a female warrior.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amazon', 'n.', 'A tall, strong, masculine woman; a virago.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amazon', 'n.', 'A name numerous species of South American parrots of the\n   genus Chrysotis');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amazonian', 'a.', 'Pertaining to or resembling an Amazon; of masculine\n   manners; warlike.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amazonian', 'a.', 'Of or pertaining to the river Amazon in South America,\n   or to its valley.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amazonite', 'n.', 'Alt. of Amazon stone');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amazon stone', 'n.', 'A variety of feldspar, having a verdigris-green\n   color.');
