  INSERT INTO words (word, word_type, description) VALUES  (  'Ambaginous', 'a.', 'Ambagious.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambagious', 'a.', 'Circumlocutory; circuitous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambagitory', 'a.', 'Ambagious.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambassade', 'ambassade.', 'Alt. of Embassade');
  INSERT INTO words (word, word_type, description) VALUES  (  'Embassade', 'ambassade.', 'The mission of an ambassador.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Embassade', 'ambassade.', 'An embassy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambassador', 'n.', 'Alt. of Embassador');
  INSERT INTO words (word, word_type, description) VALUES  (  'Embassador', 'n.', 'A minister of the highest rank sent to a foreign court\n   to represent there his sovereign or country.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Embassador', 'n.', 'An official messenger and representative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambassadorial', 'a.', 'Of or pertaining to an ambassador.');
