  INSERT INTO words (word, word_type, description) VALUES  (  'Ambassy', 'n.', 'See Embassy, the usual spelling.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amber', 'n.', 'A yellowish translucent resin resembling copal, found as a\n   fossil in alluvial soils, with beds of lignite, or on the seashore in\n   many places. It takes a fine polish, and is used for pipe mouthpieces,\n   beads, etc., and as a basis for a fine varnish. By friction, it becomes\n   strongly electric.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amber', 'n.', 'Amber color, or anything amber-colored; a clear light\n   yellow; as, the amber of the sky.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amber', 'n.', 'Ambergris.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amber', 'n.', 'The balsam, liquidambar.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amber', 'a.', 'Consisting of amber; made of amber.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amber', 'a.', 'Resembling amber, especially in color; amber-colored.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambered', 'p. p. & p. a.', 'of Amber');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amber', 'v. t.', 'To scent or flavor with ambergris; as, ambered wine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amber', 'v. t.', 'To preserve in amber; as, an ambered fly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amber fish', '', 'A fish of the southern Atlantic coast (Seriola\n   Carolinensis.)');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambergrease', 'n.', 'See Ambergris.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambergris', 'n.', 'A substance of the consistence of wax, found floating in\n   the Indian Ocean and other parts of the tropics, and also as a morbid\n   secretion in the intestines of the sperm whale (Physeter\n   macrocephalus); which is believed to be in all cases its true origin.\n   In color it is white, ash-gray, yellow, or black, and often variegated\n   like marble. The floating masses are sometimes from sixty to two\n   hundred and twenty-five pounds in weight. It is wholly volatilized as a\n   white vapor at 212¡ Fahrenheit, and is highly valued in perfumery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amber room', '', 'A room formerly in the Czars Summer Palace in Russia,\n   which was richly decorated with walls and fixtures made from amber. The\n   amber was removed by occupying German troops during the Second World\n   War and has, as of 1997, never been recovered. The room is being\n   recreated from old photographs by Russian artisans.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amber seed', '', 'Seed of the Hibiscus abelmoschus, somewhat resembling\n   millet, brought from Egypt and the West Indies, and having a flavor\n   like that of musk; musk seed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amber tree', '', 'A species of Anthospermum, a shrub with evergreen leaves,\n   which, when bruised, emit a fragrant odor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambes-as', 'n.', 'Ambs-ace.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambidexter', 'a.', 'Using both hands with equal ease.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambidexter', 'n.', 'A person who uses both hands with equal facility.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambidexter', 'n.', 'A double-dealer; one equally ready to act on either\n   side in party disputes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambidexter', 'n.', 'A juror who takes money from both parties for giving\n   his verdict.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambidexterity', 'n.', 'The quality of being ambidextrous; the faculty of\n   using both hands with equal facility.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambidexterity', 'n.', 'Versatility; general readiness; as, ambidexterity of\n   argumentation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambidexterity', 'n.', 'Double-dealing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambidexterity', 'n.', 'A jurors taking of money from the both parties for\n   a verdict.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambidextral', 'a.', 'Pertaining equally to the right-hand side and the\n   left-hand side.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambidextrous', 'a.', 'Having the faculty of using both hands with equal\n   ease.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambidextrous', 'a.', 'Practicing or siding with both parties.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambidextrously', 'adv.', 'In an ambidextrous manner; cunningly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambidextrousness', 'n.', 'The quality of being ambidextrous; ambidexterity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambient', 'a.', 'Encompassing on all sides; circumfused; investing.');
