  INSERT INTO words (word, word_type, description) VALUES  (  'Ambiguities', 'pl. ', 'of Ambiguity');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambiguity', 'n.', 'The quality or state of being ambiguous; doubtfulness or\n   uncertainty, particularly as to the signification of language, arising\n   from its admitting of more than one meaning; an equivocal word or\n   expression.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambiguous', 'a.', 'Doubtful or uncertain, particularly in respect to\n   signification; capable of being understood in either of two or more\n   possible senses; equivocal; as, an ambiguous course; an ambiguous\n   expression.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambiguously', 'adv.', 'In an ambiguous manner; with doubtful meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambiguousness', 'n.', 'Ambiguity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambilevous', 'a.', 'Left-handed on both sides; clumsy; -- opposed to\n   ambidexter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambiloquy', 'n.', 'Doubtful or ambiguous language.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambiparous', 'a.', 'Characterized by containing the rudiments of both\n   flowers and leaves; -- applied to a bud.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambit', 'n.', 'Circuit or compass.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambition', 'n.', 'The act of going about to solicit or obtain an office, or\n   any other object of desire; canvassing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambition', 'n.', 'An eager, and sometimes an inordinate, desire for\n   preferment, honor, superiority, power, or the attainment of something.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambition', 'v. t.', 'To seek after ambitiously or eagerly; to covet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambitionist', 'n.', 'One excessively ambitious.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambitionless', 'a.', 'Devoid of ambition.');
