  INSERT INTO words (word, word_type, description) VALUES  (  'Ambitiousness', 'n.', 'The quality of being ambitious; ambition;\n   pretentiousness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambitus', 'n.', 'The exterior edge or border of a thing, as the border of a\n   leaf, or the outline of a bivalve shell.');
