  INSERT INTO words (word, word_type, description) VALUES  (  'Amble', 'v. i.', 'To move somewhat like an ambling horse; to go easily or\n   without hard shocks.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amble', 'n.', 'A peculiar gait of a horse, in which both legs on the same\n   side are moved at the same time, alternating with the legs on the other\n   side.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amble', 'n.', 'A movement like the amble of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambler', 'n.', 'A horse or a person that ambles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amblingly', 'adv.', 'With an ambling gait.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amblotic', 'a.', 'Tending to cause abortion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amblygon', 'n.', 'An obtuse-angled figure, esp. and obtuse-angled triangle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amblygonal', 'a.', 'Obtuse-angled.');
