  INSERT INTO words (word, word_type, description) VALUES  (  'Amblypoda', 'n. pl.', 'A group of large, extinct, herbivorous mammals,\n   common in the Tertiary formation of the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambos', 'pl. ', 'of Ambo');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambo', 'n.', 'A large pulpit or reading desk, in the early Christian\n   churches.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambon', 'n.', 'Same as Ambo.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amboyna wood', '', 'A beautiful mottled and curled wood, used in\n   cabinetwork. It is obtained from the Pterocarpus Indicus of Amboyna,\n   Borneo, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambreate', 'n.', 'A salt formed by the combination of ambreic acid with a\n   base or positive radical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambreic', 'a.', 'Of or pertaining to ambrein; -- said of a certain acid\n   produced by digesting ambrein in nitric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambrein', 'n.', 'A fragrant substance which is the chief constituent of\n   ambergris.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambrite', 'n.', 'A fossil resin occurring in large masses in New Zealand.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambrose', 'n.', 'A sweet-scented herb; ambrosia. See Ambrosia, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambrosia', 'n.', 'The fabled food of the gods (as nectar was their drink);\n   which conferred immortality upon those who partook of it.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambrosia', 'n.', 'An unguent of the gods.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambrosia', 'n.', 'A perfumed unguent, salve, or draught; something very\n   pleasing to the taste or smell.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambrosia', 'n.', 'Formerly, a kind of fragrant plant; now (Bot.); a genus\n   of plants, including some coarse and worthless weeds, called ragweed,\n   hogweed, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambrosiac', 'a.', 'Having the qualities of ambrosia; delicious.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambrosial', 'a.', 'Consisting of, or partaking of the nature of, ambrosia;\n   delighting the taste or smell; delicious.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambrosial', 'a.', 'Divinely excellent or beautiful.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambrosially', 'adv.', 'After the manner of ambrosia; delightfully.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambrosian', 'a.', 'Ambrosial.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambrosian', 'a.', 'Of or pertaining to St. Ambrose; as, the Ambrosian\n   office, or ritual, a formula of worship in the church of Milan,\n   instituted by St. Ambrose.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambrosin', 'n.', 'An early coin struck by the dukes of Milan, and bearing\n   the figure of St. Ambrose on horseback.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambrotype', 'n.', 'A picture taken on a plate of prepared glass, in which\n   the lights are represented in silver, and the shades are produced by a\n   dark background visible through the unsilvered portions of the glass.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambries', 'pl. ', 'of Ambry');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambry', 'n.', 'In churches, a kind of closet, niche, cupboard, or locker\n   for utensils, vestments, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambry', 'n.', 'A store closet, as a pantry, cupboard, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambry', 'n.', 'Almonry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambs-ace', 'n.', 'Double aces, the lowest throw of all at dice. Hence: Bad\n   luck; anything of no account or value.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambulacral', 'a.', 'Of or pertaining to ambulacra; avenuelike; as, the\n   ambulacral ossicles, plates, spines, and suckers of echinoderms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambulacriform', 'a.', 'Having the form of ambulacra.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambulacra', 'pl. ', 'of Ambulacrum');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambulacrum', 'n.', 'One of the radical zones of echinoderms, along which\n   run the principal nerves, blood vessels, and water tubes. These zones\n   usually bear rows of locomotive suckers or tentacles, which protrude\n   from regular pores. In star fishes they occupy the grooves along the\n   under side of the rays.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambulacrum', 'n.', 'One of the suckers on the feet of mites.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambulance', 'n.', 'A field hospital, so organized as to follow an army in\n   its movements, and intended to succor the wounded as soon as possible.\n   Often used adjectively; as, an ambulance wagon; ambulance stretcher;\n   ambulance corps.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambulance', 'n.', 'An ambulance wagon or cart for conveying the wounded\n   from the field, or to a hospital.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambulant', 'a.', 'Walking; moving from place to place.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambulate', 'v. i.', 'To walk; to move about.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambulation', 'n.', 'The act of walking.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambulative', 'a.', 'Walking.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambulator', 'n.', 'One who walks about; a walker.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambulator', 'n.', 'A beetle of the genus Lamia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambulator', 'n.', 'A genus of birds, or one of this genus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambulator', 'n.', 'An instrument for measuring distances; -- called also\n   perambulator.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambulatorial', 'a.', 'Ambulatory; fitted for walking.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambulatory', 'a.', 'Of or pertaining to walking; having the faculty of\n   walking; formed or fitted for walking; as, an ambulatory animal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambulatory', 'a.', 'Accustomed to move from place to place; not stationary;\n   movable; as, an ambulatory court, which exercises its jurisdiction in\n   different places.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambulatory', 'a.', 'Pertaining to a walk.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambulatory', 'a.', 'Not yet fixed legally, or settled past alteration;\n   alterable; as, the dispositions of a will are ambulatory until the\n   death of the testator.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambulatories', 'pl. ', 'of Ambulatory');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambulatory', 'n.', 'A place to walk in, whether in the open air, as the\n   gallery of a cloister, or within a building.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amburry', 'n.', 'Same as Anbury.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambuscade', 'v. t.', 'A lying in a wood, concealed, for the purpose of\n   attacking an enemy by surprise. Hence: A lying in wait, and concealed\n   in any situation, for a like purpose; a snare laid for an enemy; an\n   ambush.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambuscade', 'v. t.', 'A place in which troops lie hid, to attack an enemy\n   unexpectedly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambuscade', 'v. t.', 'The body of troops lying in ambush.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambuscaded', 'imp. & p. p.', 'of Ambuscade');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambuscading', 'p. pr. & vb. n.', 'of Ambuscade');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambuscade', 'v. t.', 'To post or conceal in ambush; to ambush.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambuscade', 'v. t.', 'To lie in wait for, or to attack from a covert or\n   lurking place; to waylay.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambuscade', 'v. i.', 'To lie in ambush.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambuscado', 'n.', 'Ambuscade.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambuscadoed', 'p. p.', 'Posted in ambush; ambuscaded.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambush', 'v. t.', 'A disposition or arrangement of troops for attacking an\n   enemy unexpectedly from a concealed station. Hence: Unseen peril; a\n   device to entrap; a snare.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambush', 'v. t.', 'A concealed station, where troops or enemies lie in wait\n   to attack by surprise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambush', 'v. t.', 'The troops posted in a concealed place, for attacking by\n   surprise; liers in wait.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambushed', 'imp. & p. p.', 'of Ambush');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambushing', 'p. pr. & vb. n.', 'of Ambush');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambush', 'v. t.', 'To station in ambush with a view to surprise an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambush', 'v. t.', 'To attack by ambush; to waylay.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambush', 'v. i.', 'To lie in wait, for the purpose of attacking by\n   surprise; to lurk.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambusher', 'n.', 'One lying in ambush.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambushment', 'v. t.', 'An ambush.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambustion', 'n.', 'A burn or scald.');
