  INSERT INTO words (word, word_type, description) VALUES  (  'Ameliorating', 'p. pr. & vb. n.', 'of Ameliorate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ameliorate', 'v. t.', 'To make better; to improve; to meliorate.');
