  INSERT INTO words (word, word_type, description) VALUES  (  'Ameliorator', 'n.', 'One who ameliorates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amen', 'interj., adv., & n.', 'An expression used at the end of prayers,\n   and meaning, So be it. At the end of a creed, it is a solemn\n   asseveration of belief. When it introduces a declaration, it is\n   equivalent to truly, verily.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amen', 'v. t.', 'To say Amen to; to sanction fully.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amenability', 'n.', 'The quality of being amenable; amenableness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amenable', 'a.', 'Easy to be led; governable, as a woman by her husband.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amenable', 'a.', 'Liable to be brought to account or punishment;\n   answerable; responsible; accountable; as, amenable to law.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amenable', 'a.', 'Liable to punishment, a charge, a claim, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amenable', 'a.', 'Willing to yield or submit; responsive; tractable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amenableness', 'n.', 'The quality or state of being amenable; liability to\n   answer charges; answerableness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amenably', 'adv.', 'In an amenable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amenage', 'v. t.', 'To manage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amenance', 'n.', 'Behavior; bearing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amended', 'imp. & p. p.', 'of Amend');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amending', 'p. pr. & vb. n.', 'of Amend');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amend', 'v. t.', 'To change or modify in any way for the better');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amend', 'v. t.', 'by simply removing what is erroneous, corrupt,\n   superfluous, faulty, and the like;');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amend', 'v. t.', 'by supplying deficiencies;');
