  INSERT INTO words (word, word_type, description) VALUES  (  'Amender', 'n.', 'One who amends.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amendful', 'a.', 'Much improving.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amendment', 'n.', 'An alteration or change for the better; correction of a\n   fault or of faults; reformation of life by quitting vices.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amendment', 'n.', 'In public bodies; Any alternation made or proposed to be\n   made in a bill or motion by adding, changing, substituting, or\n   omitting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amendment', 'n.', 'Correction of an error in a writ or process.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amends', 'n. sing. & pl.', 'Compensation for a loss or injury; recompense;\n   reparation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amenities', 'pl. ', 'of Amenity');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amenity', 'n.', 'The quality of being pleasant or agreeable, whether in\n   respect to situation, climate, manners, or disposition; pleasantness;\n   civility; suavity; gentleness.');
