  INSERT INTO words (word, word_type, description) VALUES  (  'Amentia', 'n.', 'Imbecility; total want of understanding.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amentiferous', 'a.', 'Bearing catkins.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amentiform', 'a.', 'Shaped like a catkin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amenta', 'pl. ', 'of Amentum');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amentum', 'n.', 'Same as Ament.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amenuse', 'v. t.', 'To lessen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amerced', 'imp. & p. p.', 'of Amerce');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amercing', 'p. pr. & vb. n.', 'of Amerce');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amerce', 'v. t.', 'To punish by a pecuniary penalty, the amount of which is\n   not fixed by law, but left to the discretion of the court; as, the\n   amerced the criminal in the sum on the hundred dollars.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amerce', 'v. t.', 'To punish, in general; to mulct.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amerceable', 'a.', 'Liable to be amerced.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amercement', 'n.', 'The infliction of a penalty at the discretion of the\n   court; also, a mulct or penalty thus imposed. It differs from a fine,in\n   that the latter is, or was originally, a fixed and certain sum\n   prescribed by statue for an offense; but an amercement is arbitrary.\n   Hence, the act or practice of affeering. [See Affeer.]');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amercer', 'n.', 'One who amerces.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amerciament', 'n.', 'Same as Amercement.');
