  INSERT INTO words (word, word_type, description) VALUES  (  'Americanism', 'n.', 'A word or phrase peculiar to the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Americanization', 'n.', 'The process of Americanizing.');
