  INSERT INTO words (word, word_type, description) VALUES  (  'Ames-ace', 'n.', 'Same as Ambs-ace.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amess', 'n.', 'Amice, a hood or cape. See 2d Amice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ametabola', 'n. pl.', 'A group of insects which do not undergo any\n   metamorphosis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ametabolian', 'a.', 'Of or pertaining to insects that do undergo any\n   metamorphosis.');
