  INSERT INTO words (word, word_type, description) VALUES  (  'Amethystine', 'a.', 'Resembling amethyst, especially in color; bluish\n   violet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amethystine', 'a.', 'Composed of, or containing, amethyst.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ametropia', 'n.', 'Any abnormal condition of the refracting powers of the\n   eye.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amharic', 'a.', 'Of or pertaining to Amhara, a division of Abyssinia; as,\n   the Amharic language is closely allied to the Ethiopic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amharic', 'n.', 'The Amharic language (now the chief language of\n   Abyssinia).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amia', 'n.', 'A genus of fresh-water ganoid fishes, exclusively confined to\n   North America; called bowfin in Lake Champlain, dogfish in Lake Erie,\n   and mudfish in South Carolina, etc. See Bowfin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amiability', 'n.', 'The quality of being amiable; amiableness; sweetness of\n   disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amiable', 'a.', 'Lovable; lovely; pleasing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amiable', 'a.', 'Friendly; kindly; sweet; gracious; as, an amiable temper\n   or mood; amiable ideas.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amiable', 'a.', 'Possessing sweetness of disposition; having sweetness of\n   temper, kind-heartedness, etc., which causes one to be liked; as, an\n   amiable woman.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amiable', 'a.', 'Done out of love.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amiableness', 'n.', 'The quality of being amiable; amiability.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amiably', 'adv.', 'In an amiable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amianth', 'n.', 'See Amianthus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amianthiform', 'a.', 'Resembling amianthus in form.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amianthoid', 'a.', 'Resembling amianthus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amianthus', 'n.', 'Earth flax, or mountain flax; a soft silky variety of\n   asbestus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amic', 'a.', 'Related to, or derived, ammonia; -- used chiefly as a suffix;\n   as, amic acid; phosphamic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amicability', 'n.', 'The quality of being amicable; friendliness;\n   amicableness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amicable', 'a.', 'Friendly; proceeding from, or exhibiting, friendliness;\n   after the manner of friends; peaceable; as, an amicable disposition, or\n   arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amicableness', 'n.', 'The quality of being amicable; amicability.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amicably', 'adv.', 'In an amicable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amice', 'n.', 'A square of white linen worn at first on the head, but now\n   about the neck and shoulders, by priests of the Roman Catholic Church\n   while saying Mass.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amice', 'n.', 'A hood, or cape with a hood, made of lined with gray fur,\n   formerly worn by the clergy; -- written also amess, amyss, and almuce.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amid', 'prep.', 'See Amidst.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amide', 'n.', 'A compound formed by the union of amidogen with an acid\n   element or radical. It may also be regarded as ammonia in which one or\n   more hydrogen atoms have been replaced by an acid atom or radical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amidin', 'n.', 'Start modified by heat so as to become a transparent mass,\n   like horn. It is soluble in cold water.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amido', 'a.', 'Containing, or derived from, amidogen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amidogen', 'n.', 'A compound radical, NH2, not yet obtained in a separate\n   state, which may be regarded as ammonia from the molecule of which one\n   of its hydrogen atoms has been removed; -- called also the amido group,\n   and in composition represented by the form amido.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amidships', 'adv.', 'In the middle of a ship, with regard to her length,\n   and sometimes also her breadth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amidst', 'prep.', 'Alt. of Amid');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amid', 'prep.', 'In the midst or middle of; surrounded or encompassed by;\n   among.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amine', 'n.', 'One of a class of strongly basic substances derived from\n   ammonia by replacement of one or more hydrogen atoms by a basic atom or\n   radical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amioid', 'a.', 'Like or pertaining to the Amioidei.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amioid', 'n.', 'One of the Amioidei.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amioidei', 'n. pl.', 'An order of ganoid fishes of which Amia is the type.\n   See Bowfin and Ganoidei.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amir', 'n.', 'Same as Ameer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amiss', 'adv.', 'Astray; faultily; improperly; wrongly; ill.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amiss', 'a.', 'Wrong; faulty; out of order; improper; as, it may not be\n   amiss to ask advice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amiss', 'n.', 'A fault, wrong, or mistake.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amissibility', '', 'The quality of being amissible; possibility of being\n   lost.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amissible', 'a.', 'Liable to be lost.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amission', 'n.', 'Deprivation; loss.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amit', 'v. t.', 'To lose.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amities', 'pl. ', 'of Amity');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amity', 'n.', 'Friendship, in a general sense, between individuals,\n   societies, or nations; friendly relations; good understanding; as, a\n   treaty of amity and commerce; the amity of the Whigs and Tories.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amma', 'n.', 'An abbes or spiritual mother.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ammeter', 'n.', 'A contraction of amperometer or amperemeter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ammiral', 'n.', 'An obsolete form of admiral.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ammite', 'n.', 'Oolite or roestone; -- written also hammite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ammodyte', 'n.', 'One of a genus of fishes; the sand eel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ammodyte', 'n.', 'A kind of viper in southern Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ammonia', 'n.', 'A gaseous compound of hydrogen and nitrogen, NH3, with a\n   pungent smell and taste: -- often called volatile alkali, and spirits\n   of hartshorn.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ammoniac', 'a.', 'Alt. of Ammoniacal');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ammoniacal', 'a.', 'Of or pertaining to ammonia, or possessing its\n   properties; as, an ammoniac salt; ammoniacal gas.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ammoniac', 'n.', 'Alt. of Gum ammoniac');
  INSERT INTO words (word, word_type, description) VALUES  (  'Gum ammoniac', 'n.', 'The concrete juice (gum resin) of an umbelliferous\n   plant, the Dorema ammoniacum. It is brought chiefly from Persia in the\n   form of yellowish tears, which occur singly, or are aggregated into\n   masses. It has a peculiar smell, and a nauseous, sweet taste, followed\n   by a bitter one. It is inflammable, partially soluble in water and in\n   spirit of wine, and is used in medicine as an expectorant and\n   resolvent, and for the formation of certain plasters.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ammoniated', 'a.', 'Combined or impregnated with ammonia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ammonic', 'a.', 'Of or pertaining to ammonia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ammonite', 'n.', 'A fossil cephalopod shell related to the nautilus. There\n   are many genera and species, and all are extinct, the typical forms\n   having existed only in the Mesozoic age, when they were exceedingly\n   numerous. They differ from the nautili in having the margins of the\n   septa very much lobed or plaited, and the siphuncle dorsal. Also called\n   serpent stone, snake stone, and cornu Ammonis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ammonitiferous', 'a.', 'Containing fossil ammonites.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ammonitoidea', 'n. pl.', 'An extensive group of fossil cephalopods often\n   very abundant in Mesozoic rocks. See Ammonite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ammonium', 'n.', 'A compound radical, NH4, having the chemical relations of\n   a strongly basic element like the alkali metals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ammunition', 'n.', 'Military stores, or provisions of all kinds for attack\n   or defense.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ammunition', 'n.', 'Articles used in charging firearms and ordnance of all\n   kinds; as powder, balls, shot, shells, percussion caps, rockets, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ammunition', 'n.', 'Any stock of missiles, literal or figurative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ammunitioned', 'imp. & p. p.', 'of Ammunition');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ammunitioning', 'p pr. & vb. n.', 'of Ammunition');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ammunition', 'v. t.', 'To provide with ammunition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amnesia', 'n.', 'Forgetfulness; also, a defect of speech, from cerebral\n   disease, in which the patient substitutes wrong words or names in the\n   place of those he wishes to employ.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amnesic', 'a.', 'Of or pertaining to amnesia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amnestic', 'a.', 'Causing loss of memory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amnesty', 'v.', 'Forgetfulness; cessation of remembrance of wrong;\n   oblivion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amnesty', 'v.', 'An act of the sovereign power granting oblivion, or a\n   general pardon, for a past offense, as to subjects concerned in an\n   insurrection.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amnestied', 'imp. p. p.', 'of Amnesty');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amnestying', 'p. pr. & vb. n.', 'of Amnesty');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amnesty', 'v. t.', 'To grant amnesty to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amnicolist', 'n.', 'One who lives near a river.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amnigenous', 'a.', 'Born or bred in, of, or near a river.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amnion', 'n.', 'A thin membrane surrounding the embryos of mammals, birds,\n   and reptiles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amnios', 'n.', 'Same as Amnion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amniota', 'n. pl.', 'That group of vertebrates which develops in its\n   embryonic life the envelope called the amnion. It comprises the\n   reptiles, the birds, and the mammals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amniotic', 'a.', 'Of or pertaining to the amnion; characterized by an\n   amnion; as, the amniotic fluid; the amniotic sac.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amoebae', 'pl. ', 'of Amoeba');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amoebas', 'pl. ', 'of Amoeba');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amoeba', 'n.', 'A rhizopod. common in fresh water, capable of undergoing\n   many changes of form at will. See Rhizopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amoebaeum', 'n.', 'A poem in which persons are represented at speaking\n   alternately; as the third and seventh eclogues of Virgil.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amoebea', 'n. pl.', 'That division of the Rhizopoda which includes the\n   amoeba and similar forms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amoebean', 'a.', 'Alternately answering.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amoebian', 'n.', 'One of the Amoebea.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amoebiform', 'a.', 'Alt. of Amoeboid');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amoeboid', 'a.', 'Resembling an amoeba; amoeba-shaped; changing in shape\n   like an amoeba.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amoebous', 'a.', 'Like an amoeba in structure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amolition', 'n.', 'Removal; a putting away.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amomum', 'n.', 'A genus of aromatic plants. It includes species which bear\n   cardamoms, and grains of paradise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amoneste', 'v. t.', 'To admonish.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Among', 'prep.', 'Alt. of Amongst');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amongst', 'prep.', 'Mixed or mingled; surrounded by.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amongst', 'prep.', 'Conjoined, or associated with, or making part of the\n   number of; in the number or class of.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amongst', 'prep.', 'Expressing a relation of dispersion, distribution,\n   etc.; also, a relation of reciprocal action.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amontillado', 'n.', 'A dry kind of cherry, of a light color.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amoret', 'n.', 'An amorous girl or woman; a wanton.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amoret', 'n.', 'A love knot, love token, or love song. (pl.) Love glances\n   or love tricks.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amoret', 'n.', 'A petty love affair or amour.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amorette', 'n.', 'An amoret.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amorist', 'n.', 'A lover; a gallant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'A-mornings', 'adv.', 'In the morning; every morning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amorosa', 'n.', 'A wanton woman; a courtesan.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amorosity', 'n.', 'The quality of being amorous; lovingness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amoroso', 'n.', 'A lover; a man enamored.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amoroso', 'adv.', 'In a soft, tender, amatory style.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amorous', 'a.', 'Inclined to love; having a propensity to love, or to\n   sexual enjoyment; loving; fond; affectionate; as, an amorous\n   disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amorous', 'a.', 'Affected with love; in love; enamored; -- usually with of;\n   formerly with on.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amorous', 'a.', 'Of or relating to, or produced by, love.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amorously', 'adv.', 'In an amorous manner; fondly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amorousness', 'n.', 'The quality of being amorous, or inclined to sexual\n   love; lovingness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amorphas', 'pl. ', 'of Amorpha');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amorpha', 'n.', 'A genus of leguminous shrubs, having long clusters of\n   purple flowers; false or bastard indigo.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amorphism', 'n.', 'A state of being amorphous; esp. a state of being\n   without crystallization even in the minutest particles, as in glass,\n   opal, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amorphous', 'a.', 'Having no determinate form; of irregular; shapeless.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amorphous', 'a.', 'Without crystallization in the ultimate texture of a\n   solid substance; uncrystallized.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amorphous', 'a.', 'Of no particular kind or character; anomalous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amorphozoa', 'n. pl.', 'Animals without a mouth or regular internal organs,\n   as the sponges.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amorphozoic', 'a.', 'Of or pertaining to the Amorphozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amorphy', 'n.', 'Shapelessness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amort', 'a.', 'As if dead; lifeless; spiritless; dejected; depressed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amortise', 'n.', 'Alt. of Amortisement');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amortisation', 'n.', 'Alt. of Amortisement');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amortisable', 'n.', 'Alt. of Amortisement');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amortisement', 'n.', 'Same as Amortize, Amortization, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amortizable', 'a.', 'Capable of being cleared off, as a debt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amortization', 'n.', 'The act or right of alienating lands to a\n   corporation, which was considered formerly as transferring them to dead\n   hands, or in mortmain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amortization', 'n.', 'The extinction of a debt, usually by means of a\n   sinking fund; also, the money thus paid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amortize', 'v. t.', 'To make as if dead; to destroy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amortize', 'v. t.', 'To alienate in mortmain, that is, to convey to a\n   corporation. See Mortmain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amortize', 'v. t.', 'To clear off or extinguish, as a debt, usually by\n   means of a sinking fund.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amortizement', 'n.', 'Same as Amortization.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amorwe', 'adv.', 'In the morning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amorwe', 'adv.', 'On the following morning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amotion', 'n.', 'Removal; ousting; especially, the removal of a corporate\n   officer from his office.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amotion', 'n.', 'Deprivation of possession.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amotus', 'a.', 'Elevated, -- as a toe, when raised so high that the tip\n   does not touch the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amounted', 'imp. & p. p.', 'of Amount');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amounting', 'p. pr. & vb. n.', 'of Amount');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amount', 'n.', 'To go up; to ascend.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amount', 'n.', 'To rise or reach by an accumulation of particular sums or\n   quantities; to come (to) in the aggregate or whole; -- with to or unto.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amount', 'n.', 'To rise, reach, or extend in effect, substance, or\n   influence; to be equivalent; to come practically (to); as, the\n   testimony amounts to very little.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amount', 'v. t.', 'To signify; to amount to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amount', 'n.', 'The sum total of two or more sums or quantities; the\n   aggregate; the whole quantity; a totality; as, the amount of 7 and 9 is\n   16; the amount of a bill; the amount of this years revenue.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amount', 'n.', 'The effect, substance, value, significance, or result; the\n   sum; as, the amount of the testimony is this.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amour', 'n.', 'Love; affection.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amour', 'n.', 'Love making; a love affair; usually, an unlawful connection\n   in love; a love intrigue; an illicit love affair.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amour propre', '', 'Self-love; self-esteem.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amovability', 'n.', 'Liability to be removed or dismissed from office.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amovable', 'a.', 'Removable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amove', 'v. t.', 'To remove, as a person or thing, from a position.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amove', 'v. t.', 'To dismiss from an office or station.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amove', 'v. t. & i.', 'To move or be moved; to excite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampelite', 'n.', 'An earth abounding in pyrites, used by the ancients to\n   kill insects, etc., on vines; -- applied by Brongniart to a\n   carbonaceous alum schist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampere', 'n.', 'Alt. of Ampere');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampere', 'n.', 'The unit of electric current; -- defined by the\n   International Electrical Congress in 1893 and by U. S. Statute as, one\n   tenth of the unit of current of the C. G. S. system of electro-magnetic\n   units, or the practical equivalent of the unvarying current which, when\n   passed through a standard solution of nitrate of silver in water,\n   deposits silver at the rate of 0.001118 grams per second. Called also\n   the international ampere.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amperemeter', 'n.', 'Alt. of Amperometer');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amperometer', 'n.', 'An instrument for measuring the strength of an\n   electrical current in amperes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampersand', 'n.', 'A word used to describe the character /, /, or &.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphi-', '', 'A prefix in words of Greek origin, signifying both, of both\n   kinds, on both sides, about, around.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphiarthrodial', 'a.', 'Characterized by amphiarthrosis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphiarthrosis', 'n.', 'A form of articulation in which the bones are\n   connected by intervening substance admitting slight motion; symphysis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphiaster', 'n.', 'The achromatic figure, formed in mitotic cell-division,\n   consisting of two asters connected by a spindle-shaped bundle of\n   rodlike fibers diverging from each aster, and called the spindle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibia', 'n. pl.', 'One of the classes of vertebrates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibial', 'a. & n.', 'Amphibian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibian', 'a.', 'Of or pertaining to the Amphibia; as, amphibian\n   reptiles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibian', 'n.', 'One of the Amphibia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibiological', 'a.', 'Pertaining to amphibiology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibiology', 'n.', 'A treatise on amphibious animals; the department of\n   natural history which treats of the Amphibia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibiotica', 'n. pl.', 'A division of insects having aquatic larvae.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibious', 'a.', 'Having the ability to live both on land and in water,\n   as frogs, crocodiles, beavers, and some plants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibious', 'a.', 'Pertaining to, adapted for, or connected with, both\n   land and water.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibious', 'a.', 'Of a mixed nature; partaking of two natures.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibiously', 'adv.', 'Like an amphibious being.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibia', 'pl. ', 'of Amphibium');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibiums', 'pl. ', 'of Amphibium');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibium', 'n.', 'An amphibian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphiblastic', 'a.', 'Segmenting unequally; -- said of telolecithal ova\n   with complete segmentation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibole', 'n.', 'A common mineral embracing many varieties varying in\n   color and in composition. It occurs in monoclinic crystals; also\n   massive, generally with fibrous or columnar structure. The color varies\n   from white to gray, green, brown, and black. It is a silicate of\n   magnesium and calcium, with usually aluminium and iron. Some common\n   varieties are tremolite, actinolite, asbestus, edenite, hornblende (the\n   last name being also used as a general term for the whole species).\n   Amphibole is a constituent of many crystalline rocks, as syenite,\n   diorite, most varieties of trachyte, etc. See Hornblende.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibolic', 'a.', 'Of or pertaining to amphiboly; ambiguous; equivocal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibolic', 'a.', 'Of or resembling the mineral amphibole.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibological', 'a.', 'Of doubtful meaning; ambiguous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibologies', 'pl. ', 'of Amphibology');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibology', 'n.', 'A phrase, discourse, or proposition, susceptible of\n   two interpretations; and hence, of uncertain meaning. It differs from\n   equivocation, which arises from the twofold sense of a single term.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibolous', 'a.', 'Ambiguous; doubtful.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibolous', 'a.', 'Capable of two meanings.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibolies', 'pl. ', 'of Amphiboly');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphiboly', 'n.', 'Ambiguous discourse; amphibology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphibrach', 'n.', 'A foot of three syllables, the middle one long, the\n   first and last short (~ -- ~); as, h/b/r/. In modern prosody the\n   accented syllable takes the place of the long and the unaccented of the\n   short; as, pro-phet#ic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphicarpic', 'a.', 'Alt. of Amphicarpous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphicarpous', 'a.', 'Producing fruit of two kinds, either as to form or\n   time of ripening.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphichroic', 'a.', 'Exhibiting or producing two colors, as substances\n   which in the color test may change red litmus to blue and blue litmus\n   to red.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphicoelian', 'a.', 'Alt. of Amphicoelous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphicoelous', 'a.', 'Having both ends concave; biconcave; -- said of\n   vertebrae.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphicome', 'n.', 'A kind of figured stone, rugged and beset with\n   eminences, anciently used in divination.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphictyonic', 'a.', 'Of or pertaining to the Amphictyons or their League\n   or Council; as, an Amphictyonic town or state; the Amphictyonic body.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphictyons', 'n. pl.', 'Deputies from the confederated states of ancient\n   Greece to a congress or council. They considered both political and\n   religious matters.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphictyonies', 'pl. ', 'of Amphictyony');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphictyony', 'n.', 'A league of states of ancient Greece; esp. the\n   celebrated confederation known as the Amphictyonic Council. Its object\n   was to maintain the common interests of Greece.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphid', 'n.', 'A salt of the class formed by the combination of an acid\n   and a base, or by the union of two oxides, two sulphides, selenides, or\n   tellurides, as distinguished from a haloid compound.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphidisc', 'n.', 'A peculiar small siliceous spicule having a denticulated\n   wheel at each end; -- found in freshwater sponges.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphidromical', 'a.', 'Pertaining to an Attic festival at the naming of a\n   child; -- so called because the friends of the parents carried the\n   child around the hearth and then named it.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphigamous', 'a.', 'Having a structure entirely cellular, and no distinct\n   sexual organs; -- a term applied by De Candolle to the lowest order of\n   plants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphigean', 'a.', 'Extending over all the zones, from the tropics to the\n   polar zones inclusive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphigen', 'n.', 'An element that in combination produces amphid salt; --\n   applied by Berzelius to oxygen, sulphur, selenium, and tellurium.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphigene', 'n.', 'Leucite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphigenesis', 'n.', 'Sexual generation; amphigony.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphigenous', 'a.', 'Increasing in size by growth on all sides, as the\n   lichens.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphigonic', 'a.', 'Pertaining to amphigony; sexual; as, amphigonic\n   propagation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphigonous', 'a.', 'Relating to both parents.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphigony', 'n.', 'Sexual propagation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphigoric', 'a.', 'Nonsensical; absurd; pertaining to an amphigory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphigory', 'n.', 'A nonsense verse; a rigmarole, with apparent meaning,\n   which on further attention proves to be meaningless.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphilogism', 'n.', 'Alt. of Amphilogy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphilogy', 'n.', 'Ambiguity of speech; equivocation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphimacer', 'n.', 'A foot of three syllables, the middle one short and the\n   others long, as in cast/tas.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphineura', 'n. pl.', 'A division of Mollusca remarkable for the bilateral\n   symmetry of the organs and the arrangement of the nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphioxus', 'n.', 'A fishlike creature (Amphioxus lanceolatus); two or\n   three inches long, found in temperature seas; -- also called the\n   lancelet. Its body is pointed at both ends. It is the lowest and most\n   generalized of the vertebrates, having neither brain, skull, vertebrae,\n   nor red blood. It forms the type of the group Acrania, Leptocardia,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphipneust', 'n.', 'One of a tribe of Amphibia, which have both lungs and\n   gills at the same time, as the proteus and siren.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphipod', 'n.', 'One of the Amphipoda.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphipod', 'a.', 'Alt. of Amphipodan');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphipodan', 'a.', 'Of or pertaining to the Amphipoda.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphipoda', 'n. pl.', 'A numerous group of fourteen -- footed Crustacea,\n   inhabiting both fresh and salt water. The body is usually compressed\n   laterally, and the anterior pairs or legs are directed downward and\n   forward, but the posterior legs are usually turned upward and backward.\n   The beach flea is an example. See Tetradecapoda and Arthrostraca.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphipodous', 'a.', 'Of or pertaining to the Amphipoda.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphiprostyle', 'a.', 'Doubly prostyle; having columns at each end, but not\n   at the sides.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphiprostyle', 'n.', 'An amphiprostyle temple or edifice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphirhina', 'n. pl.', 'A name applied to the elasmobranch fishes, because\n   the nasal sac is double.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphisbaena', 'n.', 'A fabled serpent with a head at each end, moving\n   either way.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphisbaena', 'n.', 'A genus of harmless lizards, serpentlike in form,\n   without legs, and with both ends so much alike that they appear to have\n   a head at each, and ability to move either way. See Illustration in\n   Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphisbaenoid', 'a.', 'Like or pertaining to the lizards of the genus\n   Amphisbaena.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphiscii', 'n. pl.', 'Alt. of Amphiscians');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphiscians', 'n. pl.', 'The inhabitants of the tropic, whose shadows in\n   one part of the year are cast to the north, and in the other to the\n   south, according as the sun is south or north of their zenith.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphistomous', 'a.', 'Having a sucker at each extremity, as certain\n   entozoa, by means of which they adhere.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphistylic', 'a.', 'Having the mandibular arch articulated with the hyoid\n   arch and the cranium, as in the cestraciont sharks; -- said of a skull.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphitheater', 'n.', 'Alt. of Amphitheatre');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphitheatre', 'n.', 'An oval or circular building with rising tiers of\n   seats about an open space called the arena.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphitheatre', 'n.', 'Anything resembling an amphitheater in form; as, a\n   level surrounded by rising slopes or hills, or a rising gallery in a\n   theater.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphitheatral', 'a.', 'Amphitheatrical; resembling an amphitheater.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphitheatric', 'a.', 'Alt. of Amphitheatrical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphitheatrical', 'a.', 'Of, pertaining to, exhibited in, or resembling, an\n   amphitheater.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphitheatrically', 'adv.', 'In the form or manner of an amphitheater.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphitrocha', 'n.', 'A kind of annelid larva having both a dorsal and a\n   ventral circle of special cilia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphitropal', 'a.', 'Alt. of Amphitropous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphitropous', 'a.', 'Having the ovule inverted, but with the attachment\n   near the middle of one side; half anatropous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphiuma', 'n.', 'A genus of amphibians, inhabiting the Southern United\n   States, having a serpentlike form, but with four minute limbs and two\n   persistent gill openings; the Congo snake.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphopeptone', 'n.', 'A product of gastric digestion, a mixture of\n   hemipeptone and antipeptone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amophorae', 'pl. ', 'of Amphora');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphora', 'n.', 'Among the ancients, a two-handled vessel, tapering at the\n   bottom, used for holding wine, oil, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphoral', 'a.', 'Pertaining to, or resembling, an amphora.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphoric', 'a.', 'Produced by, or indicating, a cavity in the lungs, not\n   filled, and giving a sound like that produced by blowing into an empty\n   decanter; as, amphoric respiration or resonance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amphoteric', 'a.', 'Partly one and partly the other; neither acid nor\n   alkaline; neutral.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ample', 'a.', 'Large; great in size, extent, capacity, or bulk; spacious;\n   roomy; widely extended.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ample', 'a.', 'Fully sufficient; abundant; liberal; copious; as, an ample\n   fortune; ample justice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ample', 'a.', 'Not contracted of brief; not concise; extended; diffusive;\n   as, an ample narrative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplectant', 'a.', 'Clasping a support; as, amplectant tendrils.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampleness', 'n.', 'The state or quality of being ample; largeness;\n   fullness; completeness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplexation', 'n.', 'An embrace.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplexicaul', 'a.', 'Clasping or embracing a stem, as the base of some\n   leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampliate', 'v. t.', 'To enlarge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampliate', 'a.', 'Having the outer edge prominent; said of the wings of\n   insects.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampliation', 'n.', 'Enlargement; amplification.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampliation', 'n.', 'A postponement of the decision of a cause, for further\n   consideration or re-argument.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampliative', 'a.', 'Enlarging a conception by adding to that which is\n   already known or received.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplificate', 'v. t.', 'To amplify.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplification', 'n.', 'The act of amplifying or enlarging in dimensions;\n   enlargement; extension.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplification', 'n.', 'The enlarging of a simple statement by particularity\n   of description, the use of epithets, etc., for rhetorical effect;\n   diffuse narrative or description, or a dilating upon all the\n   particulars of a subject.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplification', 'n.', 'The matter by which a statement is amplified; as,\n   the subject was presented without amplifications.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplificative', 'a.', 'Amplificatory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplificatory', 'a.', 'Serving to amplify or enlarge; amplificative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplifier', 'n.', 'One who or that which amplifies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplified', 'imp. & p. p.', 'of Amplify');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplifying', 'p. pr. & vb. n.', 'of Amplify');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplify', 'v. t.', 'To render larger, more extended, or more intense, and\n   the like; -- used especially of telescopes, microscopes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplify', 'v. t.', 'To enlarge by addition or discussion; to treat\n   copiously by adding particulars, illustrations, etc.; to expand; to\n   make much of.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplify', 'v. i.', 'To become larger.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplify', 'v. i.', 'To speak largely or copiously; to be diffuse in\n   argument or description; to dilate; to expatiate; -- often with on or\n   upon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplitude', 'n.', 'State of being ample; extent of surface or space;\n   largeness of dimensions; size.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplitude', 'n.', 'Largeness, in a figurative sense; breadth; abundance;\n   fullness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplitude', 'n.', 'Of extent of capacity or intellectual powers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplitude', 'n.', 'Of extent of means or resources.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplitude', 'n.', 'The arc of the horizon between the true east or west\n   point and the center of the sun, or a star, at its rising or setting.\n   At the rising, the amplitude is eastern or ortive: at the setting, it\n   is western, occiduous, or occasive. It is also northern or southern,\n   when north or south of the equator.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplitude', 'n.', 'The arc of the horizon between the true east or west\n   point and the foot of the vertical circle passing through any star or\n   object.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplitude', 'n.', 'The horizontal line which measures the distance to which\n   a projectile is thrown; the range.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplitude', 'n.', 'The extent of a movement measured from the starting\n   point or position of equilibrium; -- applied especially to vibratory\n   movements.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amplitude', 'n.', 'An angle upon which the value of some function depends;\n   -- a term used more especially in connection with elliptic functions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amply', 'adv.', 'In an ample manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampul', 'n.', 'Same as Ampulla, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampullae', 'pl. ', 'of Ampulla');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampulla', 'n.', 'A narrow-necked vessel having two handles and bellying out\n   like a jug.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampulla', 'n.', 'A cruet for the wine and water at Mass.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampulla', 'n.', 'The vase in which the holy oil for chrism, unction, or\n   coronation is kept.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampulla', 'n.', 'Any membranous bag shaped like a leathern bottle, as the\n   dilated end of a vessel or duct; especially the dilations of the\n   semicircular canals of the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampullaceous', 'a.', 'Like a bottle or inflated bladder; bottle-shaped;\n   swelling.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampullar', 'a.', 'Alt. of Ampullary');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampullary', 'a.', 'Resembling an ampulla.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampullate', 'a.', 'Alt. of Ampullated');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampullated', 'a.', 'Having an ampulla; flask-shaped; bellied.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampulliform', 'a.', 'Flask-shaped; dilated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amputated', 'imp. & p. p.', 'of Amputate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amputating', 'p. pr. & vb. n.', 'of Amputate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amputate', 'v. t.', 'To prune or lop off, as branches or tendrils.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amputate', 'v. t.', 'To cut off (a limb or projecting part of the body)');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amputation', 'n.', 'The act of amputating; esp. the operation of cutting\n   off a limb or projecting part of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amputator', 'n.', 'One who amputates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ampyx', 'n.', 'A womans headband (sometimes of metal); for binding the\n   front hair.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amrita', 'n.', 'Immortality; also, the nectar conferring immortality.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amrita', 'a.', 'Ambrosial; immortal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amsel', 'n.', 'Alt. of Amzel');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amzel', 'n.', 'The European ring ousel (Turdus torquatus).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amuck', 'a. & adv.', 'In a frenzied and reckless manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amulet', 'n.', 'An ornament, gem, or scroll, or a package containing a\n   relic, etc., worn as a charm or preservative against evils or mischief,\n   such as diseases and witchcraft, and generally inscribed with mystic\n   forms or characters. [Also used figuratively.]');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amuletic', 'a.', 'Of or pertaining to an amulet; operating as a charm.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amurcous', 'a.', 'Full off dregs; foul.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amusable', 'a.', 'Capable of being amused.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amused', 'imp. & p. p.', 'of Amuse');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amusing', 'p. pr. & vb. n.', 'of Amuse');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amuse', 'v.', 'To occupy or engage the attention of; to lose in deep\n   thought; to absorb; also, to distract; to bewilder.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amuse', 'v.', 'To entertain or occupy in a pleasant manner; to stir with\n   pleasing or mirthful emotions; to divert.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amuse', 'v.', 'To keep in expectation; to beguile; to delude.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amuse', 'v. i.', 'To muse; to mediate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amused', 'a.', 'Diverted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amused', 'a.', 'Expressing amusement; as, an amused look.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amusement', 'n.', 'Deep thought; muse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amusement', 'n.', 'The state of being amused; pleasurable excitement; that\n   which amuses; diversion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amuser', 'n.', 'One who amuses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amusette', 'n.', 'A light field cannon, or stocked gun mounted on a swivel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amusing', 'a.', 'Giving amusement; diverting; as, an amusing story.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amusive', 'a.', 'Having power to amuse or entertain the mind; fitted to\n   excite mirth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amy', 'n.', 'A friend.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amyelous', 'a.', 'Wanting the spinal cord.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amygdalaceous', 'a.', 'Akin to, or derived from, the almond.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amygdalate', 'a.', 'Pertaining to, resembling, or made of, almonds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amygdalate', 'n.', 'An emulsion made of almonds; milk of almonds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amygdalate', 'n.', 'A salt amygdalic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amygdalic', 'a.', 'Of or pertaining to almonds; derived from amygdalin; as,\n   amygdalic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amygdaliferous', 'a.', 'Almond-bearing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amygdalin', 'n.', 'A glucoside extracted from bitter almonds as a white,\n   crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amygdaline', 'a.', 'Of, pertaining to, or resembling, almonds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amygdaloid', 'n.', 'A variety of trap or basaltic rock, containing small\n   cavities, occupied, wholly or in part, by nodules or geodes of\n   different minerals, esp. agates, quartz, calcite, and the zeolites.\n   When the imbedded minerals are detached or removed by decomposition, it\n   is porous, like lava.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amygdaloid', 'a.', 'Alt. of Amygdaloidal');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amygdaloidal', 'a.', 'Almond-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amygdaloidal', 'a.', 'Pertaining to, or having the nature of, the rock\n   amygdaloid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amyl', 'n.', 'A hydrocarbon radical, C5H11, of the paraffine series found\n   in amyl alcohol or fusel oil, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amylaceous', 'a.', 'Pertaining to starch; of the nature of starch; starchy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amylate', 'n.', 'A compound of the radical amyl with oxygen and a positive\n   atom or radical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amylene', 'n.', 'One of a group of metameric hydrocarbons, C5H10, of the\n   ethylene series. The colorless, volatile, mobile liquid commonly called\n   amylene is a mixture of different members of the group.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amylic', 'a.', 'Pertaining to, or derived from, amyl; as, amylic ether.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amylobacter', 'n.', 'A microorganism (Bacillus amylobacter) which develops\n   in vegetable tissue during putrefaction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amyloid', 'a.', 'Alt. of Amyloidal');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amyloidal', 'a.', 'Resembling or containing amyl; starchlike.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amyloid', 'n.', 'A non-nitrogenous starchy food; a starchlike substance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amyloid', 'n.', 'The substance deposited in the organs in amyloid\n   degeneration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amylolytic', 'a.', 'Effecting the conversion of starch into soluble dextrin\n   and sugar; as, an amylolytic ferment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amylose', 'n.', 'One of the starch group (C6H10O5)n of the carbohydrates;\n   as, starch, arabin, dextrin, cellulose, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amyous', 'a.', 'Wanting in muscle; without flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Amyss', 'n.', 'Same as Amice, a hood or cape.');
  INSERT INTO words (word, word_type, description) VALUES  (  'An', '', 'This word is properly an adjective, but is commonly called the\n   indefinite article. It is used before nouns of the singular number\n   only, and signifies one, or any, but somewhat less emphatically. In\n   such expressions as \"twice an hour,\" \"once an age,\" a shilling an ounce\n   (see 2d A, 2); it has a distributive force, and is equivalent to each,\n   every.');
  INSERT INTO words (word, word_type, description) VALUES  (  'An', 'conj.', 'If; -- a word used by old English authors.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ana-', '', 'A prefix in words from the Greek, denoting up, upward,\n   throughout, backward, back, again, anew.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ana', 'adv.', 'Of each; an equal quantity; as, wine and honey, ana (or,\n   contracted, aa); / ij., that is, of wine and honey, each, two ounces.');
  INSERT INTO words (word, word_type, description) VALUES  (  '-ana', '', 'A suffix to names of persons or places, used to denote a\n   collection of notable sayings, literary gossip, anecdotes, etc. Thus,\n   Scaligerana is a book containing the sayings of Scaliger, Johnsoniana\n   of Johnson, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anabaptism', 'n.', 'The doctrine of the Anabaptists.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anabaptist', 'n.', 'A name sometimes applied to a member of any sect\n   holding that rebaptism is necessary for those baptized in infancy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anabaptistic', 'a.', 'Alt. of Anabaptistical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anabaptistical', 'a.', 'Relating or attributed to the Anabaptists, or their\n   doctrines.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anabaptistry', 'n.', 'The doctrine, system, or practice, of Anabaptists.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anabaptize', 'v. t.', 'To rebaptize; to rechristen; also, to rename.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anabas', 'n.', 'A genus of fishes, remarkable for their power of living\n   long out of water, and of making their way on land for considerable\n   distances, and for climbing trees; the climbing fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anabasis', 'n.', 'A journey or expedition up from the coast, like that of\n   the younger Cyrus into Central Asia, described by Xenophon in his work\n   called \"The Anabasis.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anabasis', 'n.', 'The first period, or increase, of a disease;\n   augmentation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anabatic', 'a.', 'Pertaining to anabasis; as, an anabatic fever.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anabolic', 'a.', 'Pertaining to anabolism; an anabolic changes, or\n   processes, more or less constructive in their nature.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anabolism', 'n.', 'The constructive metabolism of the body, as\n   distinguished from katabolism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anacamptic', 'a.', 'Reflecting of reflected; as, an anacamptic sound (and\n   echo).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anacamptically', 'adv.', 'By reflection; as, echoes are sound produced\n   anacamptically.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anacamptics', 'n.', 'The science of reflected light, now called catoptrics.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anacamptics', 'n.', 'The science of reflected sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anacanthini', 'n. pl.', 'Alt. of Anacanths');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anacanths', 'n. pl.', 'A group of teleostean fishes destitute of spiny\n   fin-rays, as the cod.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anacanthous', 'a.', 'Spineless, as certain fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anacardiaceous', 'a.', 'Belonging to, or resembling, a family, or order, of\n   plants of which the cashew tree is the type, and the species of sumac\n   are well known examples.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anacardic', 'a.', 'Pertaining to, or derived from, the cashew nut; as,\n   anacardic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anacardium', 'n.', 'A genus of plants including the cashew tree. See\n   Cashew.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anacathartic', 'a.', 'Producing vomiting or expectoration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anacathartic', 'n.', 'An anacathartic medicine; an expectorant or an\n   emetic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anacharis', 'n.', 'A fresh-water weed of the frogs-bit family\n   (Hydrocharidaceae); native to America. Transferred to England it became\n   an obstruction to navigation. Called also waterweed and water thyme.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anachoret', 'a.', 'Alt. of Anachoretical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anachoretical', 'a.', 'See Anchoret, Anchoretic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anachorism', 'n.', 'An error in regard to the place of an event or a thing;\n   a referring something to a wrong place.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anachronic', 'a.', 'Alt. of Anachronical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anachronical', 'a.', 'Characterized by, or involving, anachronism;\n   anachronistic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anachronism', 'n.', 'A misplacing or error in the order of time; an error\n   in chronology by which events are misplaced in regard to each other,\n   esp. one by which an event is placed too early; falsification of\n   chronological relation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anachronistic', 'a.', 'Erroneous in date; containing an anachronism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anachronize', 'v. t.', 'To refer to, or put into, a wrong time.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anachronous', 'a.', 'Containing an anachronism; anachronistic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaclastic', 'a.', 'Produced by the refraction of light, as seen through\n   water; as, anaclastic curves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaclastic', 'a.', 'Springing back, as the bottom of an anaclastic glass.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaclastics', 'n.', 'That part of optics which treats of the refraction of\n   light; -- commonly called dioptrics.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anacoenosis', 'n.', 'A figure by which a speaker appeals to his hearers or\n   opponents for their opinion on the point in debate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anacoluthic', 'a.', 'Lacking grammatical sequence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anacoluthon', 'n.', 'A want of grammatical sequence or coherence in a\n   sentence; an instance of a change of construction in a sentence so that\n   the latter part does not syntactically correspond with the first part.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaconda', 'n.', 'A large South American snake of the Boa family (Eunectes\n   murinus); which lives near rivers, and preys on birds and small\n   mammals. The name is also applied to a similar large serpent (Python\n   tigris) of Ceylon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anacreontic', 'a.', 'Pertaining to, after the manner of, or in the meter\n   of, the Greek poet Anacreon; amatory and convivial.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anacreontic', 'n.', 'A poem after the manner of Anacreon; a sprightly\n   little poem in praise of love and wine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anacrotic', 'a.', 'Pertaining to anachronism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anacrotism', 'n.', 'A secondary notch in the pulse curve, obtained in a\n   sphygmographic tracing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anacrusis', 'n.', 'A prefix of one or two unaccented syllables to a verse\n   properly beginning with an accented syllable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anadem', 'n.', 'A garland or fillet; a chaplet or wreath.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anadiplosis', 'n.', 'A repetition of the last word or any prominent word in\n   a sentence or clause, at the beginning of the next, with an adjunct\n   idea; as, \"He retained his virtues amidst all his misfortunes --\n   misfortunes which no prudence could foresee or prevent.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anadrom', 'n.', 'A fish that leaves the sea and ascends rivers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anadromous', 'a.', 'Ascending rivers from the sea, at certain seasons, for\n   breeding, as the salmon, shad, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anadromous', 'a.', 'Tending upwards; -- said of terns in which the lowest\n   secondary segments are on the upper side of the branch of the central\n   stem.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaemia', 'a.', 'A morbid condition in which the blood is deficient in\n   quality or in quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaemic', 'a.', 'Of or pertaining to anaemia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaerobic', 'a.', 'Relating to, or like, anaerobies; anaerobiotic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaerobies', 'n. pl.', 'Microorganisms which do not require oxygen, but are\n   killed by it.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaerobiotic', 'a.', 'Related to, or of the nature of, anaerobies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaesthesia', 'n.', 'Entire or partial loss or absence of feeling or\n   sensation; a state of general or local insensibility produced by\n   disease or by the inhalation or application of an anaesthetic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaesthesis', 'n.', 'See Anaesthesia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaesthetic', 'a.', 'Capable of rendering insensible; as, anaesthetic\n   agents.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaesthetic', 'a.', 'Characterized by, or connected with, insensibility;\n   as, an anaesthetic effect or operation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaesthetic', 'n.', 'That which produces insensibility to pain, as\n   chloroform, ether, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaesthetization', 'n.', 'The process of anaesthetizing; also, the\n   condition of the nervous system induced by anaesthetics.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaesthetize', 'v. t.', 'To render insensible by an anaesthetic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaglyph', 'n.', 'Any sculptured, chased, or embossed ornament worked in\n   low relief, as a cameo.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaglyphic', 'a.', 'Alt. of Anaglyphical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaglyphical', 'a.', 'Pertaining to the art of chasing or embossing in\n   relief; anaglyptic; -- opposed to diaglyptic or sunk work.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaglyphic', 'n.', 'Work chased or embossed relief.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaglyptic', 'a.', 'Relating to the art of carving, enchasing, or embossing\n   in low relief.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaglyptics', 'n.', 'The art of carving in low relief, embossing, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaglyptograph', 'n.', 'An instrument by which a correct engraving of any\n   embossed object, such as a medal or cameo, can be executed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaglyptographic', 'a.', 'Of or pertaining to anaglyptography; as,\n   anaglyptographic engraving.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaglyptography', 'n.', 'The art of copying works in relief, or of\n   engraving as to give the subject an embossed or raised appearance; --\n   used in representing coins, bas-reliefs, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anagnorisis', 'n.', 'The unfolding or denouement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anagoge', 'n.', 'An elevation of mind to things celestial.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anagoge', 'n.', 'The spiritual meaning or application; esp. the application\n   of the types and allegories of the Old Testament to subjects of the\n   New.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anagogic', 'a.', 'Alt. of Anagogical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anagogical', 'a.', 'Mystical; having a secondary spiritual meaning; as, the\n   rest of the Sabbath, in an anagogical sense, signifies the repose of\n   the saints in heaven; an anagogical explication.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anagogics', 'n. pl.', 'Mystical interpretations or studies, esp. of the\n   Scriptures.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anagogy', 'n.', 'Same as Anagoge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anagram', 'n.', 'Literally, the letters of a word read backwards, but in\n   its usual wider sense, the change or one word or phrase into another by\n   the transposition of its letters. Thus Galenus becomes angelus; William\n   Noy (attorney-general to Charles I., and a laborious man) may be turned\n   into I moyl in law.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anagram', 'v. t.', 'To anagrammatize.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anagrammatic', 'a.', 'Alt. of Anagrammatical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anagrammatical', 'a.', 'Pertaining to, containing, or making, an anagram.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anagrammatism', 'n.', 'The act or practice of making anagrams.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anagrammatist', 'n.', 'A maker anagrams.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anagrammatize', 'v. t.', 'To transpose, as the letters of a word, so as to\n   form an anagram.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anagraph', 'n.', 'An inventory; a record.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anakim', 'n. pl.', 'Alt. of Anaks');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaks', 'n. pl.', 'A race of giants living in Palestine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anal', 'a.', 'Pertaining to, or situated near, the anus; as, the anal fin\n   or glands.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analcime', 'n.', 'A white or flesh-red mineral, of the zeolite family,\n   occurring in isometric crystals. By friction, it acquires a weak\n   electricity; hence its name.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analcite', 'n.', 'Analcime.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analectic', 'a.', 'Relating to analects; made up of selections; as, an\n   analectic magazine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analects', 'n. pl.', 'Alt. of Analecta');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analecta', 'n. pl.', 'A collection of literary fragments.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analemma', 'n.', 'An orthographic projection of the sphere on the plane of\n   the meridian, the eye being supposed at an infinite distance, and in\n   the east or west point of the horizon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analemma', 'n.', 'An instrument of wood or brass, on which this projection\n   of the sphere is made, having a movable horizon or cursor; -- formerly\n   much used in solving some common astronomical problems.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analemma', 'n.', 'A scale of the suns declination for each day of the\n   year, drawn across the torrid zone on an artificial terrestrial globe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analepsis', '', 'Alt. of Analepsy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analepsy', '', 'Recovery of strength after sickness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analepsy', '', 'A species of epileptic attack, originating from gastric\n   disorder.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analeptic', 'a.', 'Restorative; giving strength after disease.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analeptic', 'n.', 'A restorative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analgesia', 'n.', 'Absence of sensibility to pain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anallagmatic', 'a.', 'Not changed in form by inversion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anallantoic', 'a.', 'Without, or not developing, an allantois.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anallantoidea', 'n. pl.', 'The division of Vertebrata in which no allantois\n   is developed. It includes amphibians, fishes, and lower forms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analogal', 'a.', 'Analogous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analogic', 'a.', 'Of or belonging to analogy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analogical', 'a.', 'Founded on, or of the nature of, analogy; expressing or\n   implying analogy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analogical', 'a.', 'Having analogy; analogous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analogically', 'adv.', 'In an analogical sense; in accordance with analogy;\n   by way of similitude.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analogicalness', 'n.', 'Quality of being analogical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analogism', 'n.', 'an argument from the cause to the effect; an a priori\n   argument.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analogism', 'n.', 'Investigation of things by the analogy they bear to each\n   other.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analogist', 'n.', 'One who reasons from analogy, or represent, by analogy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analogize', 'v. i.', 'To employ, or reason by, analogy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analogon', 'n.', 'Analogue.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analogous', 'a.', 'Having analogy; corresponding to something else; bearing\n   some resemblance or proportion; -- often followed by to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analogue', 'n.', 'That which is analogous to, or corresponds with, some\n   other thing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analogue', 'n.', 'A word in one language corresponding with one in another;\n   an analogous term; as, the Latin \"pater\" is the analogue of the English\n   \"father.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analogue', 'n.', 'An organ which is equivalent in its functions to a\n   different organ in another species or group, or even in the same group;\n   as, the gill of a fish is the analogue of a lung in a quadruped,\n   although the two are not of like structural relations.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analogue', 'n.', 'A species in one genus or group having its characters\n   parallel, one by one, with those of another group.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analogue', 'n.', 'A species or genus in one country closely related to a\n   species of the same genus, or a genus of the same group, in another:\n   such species are often called representative species, and such genera,\n   representative genera.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analogies', 'pl. ', 'of Analogy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analogy', 'n.', 'A resemblance of relations; an agreement or likeness\n   between things in some circumstances or effects, when the things are\n   otherwise entirely different. Thus, learning enlightens the mind,\n   because it is to the mind what light is to the eye, enabling it to\n   discover things before hidden.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analogy', 'n.', 'A relation or correspondence in function, between organs\n   or parts which are decidedly different.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analogy', 'n.', 'Proportion; equality of ratios.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analogy', 'n.', 'Conformity of words to the genius, structure, or general\n   rules of a language; similarity of origin, inflection, or principle of\n   pronunciation, and the like, as opposed to anomaly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analyse', 'n.', 'Alt. of Analyser');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analyser', 'n.', 'Same as Analyze, Analyzer, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analyses', 'pl. ', 'of Analysis');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analysis', 'n.', 'A resolution of anything, whether an object of the senses\n   or of the intellect, into its constituent or original elements; an\n   examination of the component parts of a subject, each separately, as\n   the words which compose a sentence, the tones of a tune, or the simple\n   propositions which enter into an argument. It is opposed to synthesis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analysis', 'n.', 'The separation of a compound substance, by chemical\n   processes, into its constituents, with a view to ascertain either (a)\n   what elements it contains, or (b) how much of each element is present.\n   The former is called qualitative, and the latter quantitative analysis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analysis', 'n.', 'The tracing of things to their source, and the resolving\n   of knowledge into its original principles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analysis', 'n.', 'The resolving of problems by reducing the conditions that\n   are in them to equations.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analysis', 'n.', 'A syllabus, or table of the principal heads of a\n   discourse, disposed in their natural order.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analysis', 'n.', 'A brief, methodical illustration of the principles of a\n   science. In this sense it is nearly synonymous with synopsis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analysis', 'n.', 'The process of ascertaining the name of a species, or its\n   place in a system of classification, by means of an analytical table or\n   key.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analyst', 'n.', 'One who analyzes; formerly, one skilled in algebraical\n   geometry; now commonly, one skilled in chemical analysis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analytic', 'a.', 'Alt. of Analytical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analytical', 'a.', 'Of or pertaining to analysis; resolving into elements\n   or constituent parts; as, an analytical experiment; analytic reasoning;\n   -- opposed to synthetic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analytically', 'adv.', 'In an analytical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analytics', 'n.', 'The science of analysis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analyzable', 'a.', 'That may be analyzed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analyzation', 'n.', 'The act of analyzing, or separating into constituent\n   parts; analysis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analyzed', 'imp. & p. p.', 'of Analyze');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analyzing', 'p. pr. & vb. n.', 'of Analyze');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analyze', 'v. t.', 'To subject to analysis; to resolve (anything complex)\n   into its elements; to separate into the constituent parts, for the\n   purpose of an examination of each separately; to examine in such a\n   manner as to ascertain the elements or nature of the thing examined;\n   as, to analyze a fossil substance; to analyze a sentence or a word; to\n   analyze an action to ascertain its morality.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analyzer', 'n.', 'One who, or that which, analyzes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Analyzer', 'n.', 'The part of a polariscope which receives the light after\n   polarization, and exhibits its properties.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anamese', 'a.', 'Of or pertaining to Anam, to southeastern Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anamese', 'n.', 'A native of Anam.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anamnesis', 'n.', 'A recalling to mind; recollection.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anamnestic', 'a.', 'Aiding the memory; as, anamnestic remedies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anamniotic', 'a.', 'Without, or not developing, an amnion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anamorphism', 'n.', 'A distorted image.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anamorphism', 'n.', 'A gradual progression from one type to another,\n   generally ascending.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anamorphosis', 'n.', 'A distorted or monstrous projection or representation\n   of an image on a plane or curved surface, which, when viewed from a\n   certain point, or as reflected from a curved mirror or through a\n   polyhedron, appears regular and in proportion; a deformation of an\n   image.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anamorphosis', 'n.', 'Same as Anamorphism, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anamorphosis', 'n.', 'A morbid or monstrous development, or change of form,\n   or degeneration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anamorphosy', 'n.', 'Same as Anamorphosis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anan', 'interj.', 'An expression equivalent to What did you say? Sir? Eh?');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ananas', 'n.', 'The pineapple (Ananassa sativa).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anandrous', 'a.', 'Destitute of stamens, as certain female flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anangular', 'a.', 'Containing no angle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anantherous', 'a.', 'Destitute of anthers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ananthous', 'a.', 'Destitute of flowers; flowerless.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anapaest', '', 'Alt. of Anapaestic');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anapaestic', '', 'Same as Anapest, Anapestic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anapest', 'n.', 'A metrical foot consisting of three syllables, the first\n   two short, or unaccented, the last long, or accented (/ / -); the\n   reverse of the dactyl. In Latin d/-/-tas, and in English in-ter-vene#,\n   are examples of anapests.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anapest', 'n.', 'A verse composed of such feet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anapestic', 'a.', 'Pertaining to an anapest; consisting of an anapests; as,\n   an anapestic meter, foot, verse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anapestic', 'n.', 'Anapestic measure or verse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anapestical', 'a.', 'Anapestic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaphora', 'n.', 'A repetition of a word or of words at the beginning of\n   two or more successive clauses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaphrodisia', 'n.', 'Absence of sexual appetite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaphrodisiac', 'a. & n.', 'Same as Antaphrodisiac.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaphroditic', 'a.', 'Produced without concourse of sexes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaplastic', 'a.', 'Of or pertaining to anaplasty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaplasty', 'n.', 'The art of operation of restoring lost parts or the\n   normal shape by the use of healthy tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaplerotic', 'a.', 'Filling up; promoting granulation of wounds or ulcers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaplerotic', 'n.', 'A remedy which promotes such granulation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anapnograph', 'n.', 'A form of spirometer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anapnoic', 'a.', 'Relating to respiration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anapodeictic', 'a.', 'Not apodeictic; undemonstrable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anapophysis', 'n.', 'An accessory process in many lumbar vertebrae.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaptotic', 'a.', 'Having lost, or tending to lose, inflections by phonetic\n   decay; as, anaptotic languages.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaptichi', 'pl. ', 'of Anaptychus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anaptychus', 'n.', 'One of a pair of shelly plates found in some\n   cephalopods, as the ammonites.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anarch', 'n.', 'The author of anarchy; one who excites revolt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anarchal', 'a.', 'Lawless; anarchical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anarchic', 'a.', 'Alt. of Anarchical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anarchical', 'a.', 'Pertaining to anarchy; without rule or government; in\n   political confusion; tending to produce anarchy; as, anarchic\n   despotism; anarchical opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anarchism', 'n.', 'The doctrine or practice of anarchists.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anarchist', 'n.', 'An anarch; one who advocates anarchy of aims at the\n   overthrow of civil government.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anarchize', 'v. t.', 'To reduce to anarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anarchy', 'n.', 'Absence of government; the state of society where there is\n   no law or supreme power; a state of lawlessness; political confusion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anarchy', 'n.', 'Hence, confusion or disorder, in general.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anarthropoda', 'n. pl.', 'One of the divisions of Articulata in which there\n   are no jointed legs, as the annelids; -- opposed to Arthropoda.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anarthropodous', 'a.', 'Having no jointed legs; pertaining to Anarthropoda.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anarthrous', 'a.', 'Used without the article; as, an anarthrous\n   substantive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anarthrous', 'a.', 'Without joints, or having the joints indistinct, as\n   some insects.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anas', 'n.', 'A genus of water fowls, of the order Anseres, including\n   certain species of fresh-water ducks.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anasarca', 'n.', 'Dropsy of the subcutaneous cellular tissue; an effusion\n   of serum into the cellular substance, occasioning a soft, pale,\n   inelastic swelling of the skin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anasarcous', 'a.', 'Belonging, or affected by, anasarca, or dropsy;\n   dropsical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anastaltic', 'a. & n.', 'Styptic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anastate', 'n.', 'One of a series of substances formed, in secreting cells,\n   by constructive or anabolic processes, in the production of protoplasm;\n   -- opposed to katastate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anastatic', 'a.', 'Pertaining to a process or a style of printing from\n   characters in relief on zinc plates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anastomozed', 'imp. p. p.', 'of Anastomose');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anastomosing', 'p. pr. & vb. n.', 'of Anastomose');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anastomose', 'v. i.', 'To inosculate; to intercommunicate by anastomosis,\n   as the arteries and veins.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anastomoses', 'pl. ', 'of Anastomosis');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anastomosis', 'n.', 'The inosculation of vessels, or intercommunication\n   between two or more vessels or nerves, as the cross communication\n   between arteries or veins.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anastomotic', 'a.', 'Of or pertaining to anastomosis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anastrophe', 'n.', 'An inversion of the natural order of words; as, echoed\n   the hills, for, the hills echoed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anathemas', 'pl. ', 'of Anathema');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anathema', 'n.', 'A ban or curse pronounced with religious solemnity by\n   ecclesiastical authority, and accompanied by excommunication. Hence:\n   Denunciation of anything as accursed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anathema', 'n.', 'An imprecation; a curse; a malediction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anathema', 'n.', 'Any person or thing anathematized, or cursed by\n   ecclesiastical authority.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anathematic', 'a.', 'Alt. of Anathematical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anathematical', 'a.', 'Pertaining to, or having the nature of, an anathema.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anathematism', 'n.', 'Anathematization.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anathematization', 'n.', 'The act of anathematizing, or denouncing as\n   accursed; imprecation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anathematized', 'imp. & p. p.', 'of Anathematize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anathematizing', 'p. pr. & vb. n.', 'of Anathematize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anathematize', 'v. t.', 'To pronounce an anathema against; to curse. Hence:\n   To condemn publicly as something accursed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anathematizer', 'n.', 'One who pronounces an anathema.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatifae', 'pl. ', 'of Anatifa');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatifa', 'n.', 'An animal of the barnacle tribe, of the genus Lepas,\n   having a fleshy stem or peduncle; a goose barnacle. See Cirripedia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatifer', 'n.', 'Same as Anatifa.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatiferous', 'a.', 'Producing ducks; -- applied to Anatifae, under the\n   absurd notion of their turning into ducks or geese. See Barnacle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatine', 'a.', 'Of or pertaining to the ducks; ducklike.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatocism', 'n.', 'Compound interest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatomic', 'a.', 'Alt. of Anatomical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatomical', 'a.', 'Of or relating to anatomy or dissection; as, the\n   anatomic art; anatomical observations.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatomically', 'adv.', 'In an anatomical manner; by means of dissection.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatomism', 'n.', 'The application of the principles of anatomy, as in art.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatomism', 'n.', 'The doctrine that the anatomical structure explains all\n   the phenomena of the organism or of animal life.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatomist', 'n.', 'One who is skilled in the art of anatomy, or dissection.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatomization', 'n.', 'The act of anatomizing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatomized', 'imp. & p. p.', 'of Anatomize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatomizing', 'p. pr. & vb. n.', 'of Anatomize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatomize', 'v. t.', 'To dissect; to cut in pieces, as an animal vegetable\n   body, for the purpose of displaying or examining the structure and use\n   of the several parts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatomize', 'v. t.', 'To discriminate minutely or carefully; to analyze.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatomizer', 'n.', 'A dissector.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatomies', 'pl. ', 'of Anatomy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatomy', 'n.', 'The art of dissecting, or artificially separating the\n   different parts of any organized body, to discover their situation,\n   structure, and economy; dissection.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatomy', 'n.', 'The science which treats of the structure of organic\n   bodies; anatomical structure or organization.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatomy', 'n.', 'A treatise or book on anatomy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatomy', 'n.', 'The act of dividing anything, corporeal or intellectual,\n   for the purpose of examining its parts; analysis; as, the anatomy of a\n   discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatomy', 'n.', 'A skeleton; anything anatomized or dissected, or which has\n   the appearance of being so.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatreptic', 'a.', 'Overthrowing; defeating; -- applied to Platos\n   refutative dialogues.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatron', 'n.', 'Native carbonate of soda; natron.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatron', 'n.', 'Glass gall or sandiver.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatron', 'n.', 'Saltpeter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatropal', 'a.', 'Alt. of Anatropous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatropous', 'a.', 'Having the ovule inverted at an early period in its\n   development, so that the chalaza is as the apparent apex; -- opposed to\n   orthotropous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anatto', 'n.', 'Same as Annotto.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anbury', 'n.', 'Alt. of Ambury');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambury', 'n.', 'A soft tumor or bloody wart on horses or oxen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ambury', 'n.', 'A disease of the roots of turnips, etc.; -- called also\n   fingers and toes.');
  INSERT INTO words (word, word_type, description) VALUES  (  '-ance', '', 'A suffix signifying action; also, quality or state; as,\n   assistance, resistance, appearance, elegance. See -ancy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancestor', 'n.', 'One from whom a person is descended, whether on the\n   fathers or mothers side, at any distance of time; a progenitor; a\n   fore father.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancestor', 'n.', 'An earlier type; a progenitor; as, this fossil animal is\n   regarded as the ancestor of the horse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancestor', 'n.', 'One from whom an estate has descended; -- the correlative\n   of heir.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancestorial', 'a.', 'Ancestral.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancestorially', 'adv.', 'With regard to ancestors.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancestral', 'a.', 'Of, pertaining to, derived from, or possessed by, an\n   ancestor or ancestors; as, an ancestral estate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancestress', 'n.', 'A female ancestor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancestry', 'n.', 'Condition as to ancestors; ancestral lineage; hence,\n   birth or honorable descent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancestry', 'n.', 'A series of ancestors or progenitors; lineage, or those\n   who compose the line of natural descent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchor', 'n.', 'A iron instrument which is attached to a ship by a cable\n   (rope or chain); and which, being cast overboard, lays hold of the\n   earth by a fluke or hook and thus retains the ship in a particular\n   station.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchor', 'n.', 'Any instrument or contrivance serving a purpose like that\n   of a ships anchor, as an arrangement of timber to hold a dam fast; a\n   contrivance to hold the end of a bridge cable, or other similar part; a\n   contrivance used by founders to hold the core of a mold in place.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchor', 'n.', 'Fig.: That which gives stability or security; that on which\n   we place dependence for safety.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchor', 'n.', 'An emblem of hope.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchor', 'n.', 'A metal tie holding adjoining parts of a building together.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchor', 'n.', 'Carved work, somewhat resembling an anchor or arrowhead; --\n   a part of the ornaments of certain moldings. It is seen in the echinus,\n   or egg-and-anchor (called also egg-and-dart, egg-and-tongue) ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchor', 'n.', 'One of the anchor-shaped spicules of certain sponges; also,\n   one of the calcareous spinules of certain Holothurians, as in species\n   of Synapta.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchored', 'imp. & p. p.', 'of Anchor');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchoring', 'p. pr. & vb. n.', 'of Anchor');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchor', 'v. t.', 'To place at anchor; to secure by an anchor; as, to\n   anchor a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchor', 'v. t.', 'To fix or fasten; to fix in a stable condition; as, to\n   anchor the cables of a suspension bridge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchor', 'v. i.', 'To cast anchor; to come to anchor; as, our ship (or the\n   captain) anchored in the stream.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchor', 'v. i.', 'To stop; to fix or rest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchor', 'n.', 'An anchoret.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchorable', 'a.', 'Fit for anchorage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchorage', 'n.', 'The act of anchoring, or the condition of lying at\n   anchor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchorage', 'n.', 'A place suitable for anchoring or where ships anchor; a\n   hold for an anchor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchorage', 'n.', 'The set of anchors belonging to a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchorage', 'n.', 'Something which holds like an anchor; a hold; as, the\n   anchorages of the Brooklyn Bridge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchorage', 'n.', 'Something on which one may depend for security; ground\n   of trust.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchorage', 'n.', 'A toll for anchoring; anchorage duties.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchorage', 'n.', 'Abode of an anchoret.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchorate', 'a.', 'Anchor-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchored', 'a.', 'Held by an anchor; at anchor; held safely; as, an\n   anchored bark; also, shaped like an anchor; forked; as, an anchored\n   tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchored', 'a.', 'Having the extremities turned back, like the flukes of an\n   anchor; as, an anchored cross.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchoress', 'n.', 'A female anchoret.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchoret', 'n.', 'Alt. of Anchorite');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchorite', 'n.', 'One who renounces the world and secludes himself,\n   usually for religious reasons; a hermit; a recluse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchoretic', 'a.', 'Alt. of Anchoretical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchoretical', 'a.', 'Pertaining to an anchoret or hermit; after the manner\n   of an anchoret.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchoretish', 'a.', 'Hermitlike.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchoretism', 'n.', 'The practice or mode of life of an anchoret.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchor-hold', 'n.', 'The hold or grip of an anchor, or that to which it\n   holds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchor-hold', 'n.', 'Hence: Firm hold: security.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchorite', 'n.', 'Same as Anchoret.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchoritess', 'n.', 'An anchoress.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchorless', 'a.', 'Without an anchor or stay. Hence: Drifting; unsettled.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchovy', 'n.', 'A small fish, about three inches in length, of the Herring\n   family (Engraulis encrasicholus); caught in vast numbers in the\n   Mediterranean, and pickled for exportation. The name is also applied to\n   several allied species.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchovy pear', '', 'A West Indian fruit like the mango in taste, sometimes\n   pickled; also, the tree (Grias cauliflora) bearing this fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchusin', 'n.', 'A resinoid coloring matter obtained from alkanet root.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchylosed', 'imp. & p. p.', 'of Anchylose');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchylosing', 'p. pr. & vb. n.', 'of Anchylose');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchylose', 'v. t. & i.', 'To affect or be affected with anchylosis; to\n   unite or consolidate so as to make a stiff joint; to grow together into\n   one.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchylosis', 'n.', 'Alt. of Ankylosis');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ankylosis', 'n.', 'Stiffness or fixation of a joint; formation of a stiff\n   joint.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ankylosis', 'n.', 'The union of two or more separate bones to from a single\n   bone; the close union of bones or other structures in various animals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anchylotic', 'a.', 'Of or pertaining to anchylosis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancient', 'a.', 'Old; that happened or existed in former times, usually at\n   a great distance of time; belonging to times long past; specifically\n   applied to the times before the fall of the Roman empire; -- opposed to\n   modern; as, ancient authors, literature, history; ancient days.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancient', 'a.', 'Old; that has been of long duration; of long standing; of\n   great age; as, an ancient forest; an ancient castle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancient', 'a.', 'Known for a long time, or from early times; -- opposed to\n   recent or new; as, the ancient continent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancient', 'a.', 'Dignified, like an aged man; magisterial; venerable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancient', 'a.', 'Experienced; versed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancient', 'a.', 'Former; sometime.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancient', 'n.', 'Those who lived in former ages, as opposed to the moderns.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancient', 'n.', 'An aged man; a patriarch. Hence: A governor; a ruler; a\n   person of influence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancient', 'n.', 'A senior; an elder; a predecessor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancient', 'n.', 'One of the senior members of the Inns of Court or of\n   Chancery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancient', 'n.', 'An ensign or flag.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancient', 'n.', 'The bearer of a flag; an ensign.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anciently', 'adv.', 'In ancient times.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anciently', 'adv.', 'In an ancient manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancientness', 'n.', 'The quality of being ancient; antiquity; existence\n   from old times.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancientry', 'n.', 'Antiquity; what is ancient.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancientry', 'n.', 'Old age; also, old people.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancientry', 'n.', 'Ancient lineage; ancestry; dignity of birth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancienty', 'n.', 'Age; antiquity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancienty', 'n.', 'Seniority.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancile', 'n.', 'The sacred shield of the Romans, said to have-fallen from\n   heaven in the reign of Numa. It was the palladium of Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancillary', 'a.', 'Subservient or subordinate, like a handmaid; auxiliary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancille', 'n.', 'A maidservant; a handmaid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancipital', 'a.', 'Alt. of Ancipitous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancipitous', 'a.', 'Two-edged instead of round; -- said of certain\n   flattened stems, as those of blue grass, and rarely also of leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancistroid', 'a.', 'Hook-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancle', 'n.', 'See Ankle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancome', 'n.', 'A small ulcerous swelling, coming suddenly; also, a\n   whitlow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancones', 'pl. ', 'of Ancon');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancon', 'n.', 'The olecranon, or the elbow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancon', 'n.', 'Alt. of Ancone');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancone', 'n.', 'The corner or quoin of a wall, cross-beam, or rafter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancone', 'n.', 'A bracket supporting a cornice; a console.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anconal', 'a.', 'Alt. of Anconeal');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anconeal', 'a.', 'Of or pertaining to the ancon or elbow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anconeus', 'n.', 'A muscle of the elbow and forearm.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anconoid', 'a.', 'Elbowlike; anconal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ancony', 'n.', 'A piece of malleable iron, wrought into the shape of a bar\n   in the middle, but unwrought at the ends.');
  INSERT INTO words (word, word_type, description) VALUES  (  '-ancy', '', 'A suffix expressing more strongly than -ance the idea of\n   quality or state; as, constancy, buoyancy, infancy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'And', 'conj.', 'A particle which expresses the relation of connection or\n   addition. It is used to conjoin a word with a word, a clause with a\n   clause, or a sentence with a sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'And', 'conj.', 'In order to; -- used instead of the infinitival to,\n   especially after try, come, go.');
  INSERT INTO words (word, word_type, description) VALUES  (  'And', 'conj.', 'It is sometimes, in old songs, a mere expletive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'And', 'conj.', 'If; though. See An, conj.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Andabatism', 'n.', 'Doubt; uncertainty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Andalusite', 'n.', 'A silicate of aluminium, occurring usually in thick\n   rhombic prisms, nearly square, of a grayish or pale reddish tint. It\n   was first discovered in Andalusia, Spain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Andante', 'a.', 'Moving moderately slow, but distinct and flowing; quicker\n   than larghetto, and slower than allegretto.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Andante', 'n.', 'A movement or piece in andante time.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Andantino', 'a.', 'Rather quicker than andante; between that allegretto.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Andarac', 'n.', 'Red orpiment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Andean', 'a.', 'Pertaining to the Andes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Andesine', 'n.', 'A kind of triclinic feldspar found in the Andes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Andesite', 'n.', 'An eruptive rock allied to trachyte, consisting\n   essentially of a triclinic feldspar, with pyroxene, hornblende, or\n   hypersthene.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Andine', 'a.', 'Andean; as, Andine flora.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Andiron', 'n.', 'A utensil for supporting wood when burning in a fireplace,\n   one being placed on each side; a firedog; as, a pair of andirons.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Andranatomy', 'n.', 'The dissection of a human body, especially of a male;\n   androtomy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Androecium', 'n.', 'The stamens of a flower taken collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Androgyne', 'n.', 'An hermaphrodite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Androgyne', 'n.', 'An androgynous plant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Androgynous', 'a.', 'Alt. of Androgynal');
  INSERT INTO words (word, word_type, description) VALUES  (  'Androgynal', 'a.', 'Uniting both sexes in one, or having the\n   characteristics of both; being in nature both male and female;\n   hermaphroditic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Androgynal', 'a.', 'Bearing both staminiferous and pistilliferous flowers\n   in the same cluster.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Androgyny', 'n.', 'Alt. of Androgynism');
  INSERT INTO words (word, word_type, description) VALUES  (  'Androgynism', 'n.', 'Union of both sexes in one individual;\n   hermaphroditism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Android', 'n.', 'Alt. of Androides');
  INSERT INTO words (word, word_type, description) VALUES  (  'Androides', 'n.', 'A machine or automaton in the form of a human being.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Android', 'a.', 'Resembling a man.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Andromeda', 'n.', 'A northern constellation, supposed to represent the\n   mythical Andromeda.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Andromeda', 'n.', 'A genus of ericaceous flowering plants of northern\n   climates, of which the original species was found growing on a rock\n   surrounded by water.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Andron', 'n.', 'The apartment appropriated for the males. This was in the\n   lower part of the house.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Andropetalous', 'a.', 'Produced by the conversion of the stamens into\n   petals, as double flowers, like the garden ranunculus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Androphagi', 'n. pl.', 'Cannibals; man-eaters; anthropophagi.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Androphagous', 'a.', 'Anthropophagous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Androphore', 'n.', 'A support or column on which stamens are raised.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Androphore', 'n.', 'The part which in some Siphonophora bears the male\n   gonophores.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Androsphinx', 'n.', 'A man sphinx; a sphinx having the head of a man and\n   the body of a lion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Androspore', 'n.', 'A spore of some algae, which has male functions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Androtomous', 'a.', 'Having the filaments of the stamens divided into two\n   parts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Androtomy', 'n.', 'Dissection of the human body, as distinguished from\n   zootomy; anthropotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'androus', '', 'A terminal combining form: Having a stamen or stamens;\n   staminate; as, monandrous, with one stamen; polyandrous, with many\n   stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anear', 'prep. & adv.', 'Near.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anear', 'v. t. & i.', 'To near; to approach.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aneath', 'prep. & adv.', 'Beneath.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anecdotage', 'n.', 'Anecdotes collectively; a collection of anecdotes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anecdotal', 'a.', 'Pertaining to, or abounding with, anecdotes; as,\n   anecdotal conversation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anecdote', 'n.', 'Unpublished narratives.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anecdote', 'n.', 'A particular or detached incident or fact of an\n   interesting nature; a biographical incident or fragment; a single\n   passage of private life.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anecdotic', 'a.', 'Alt. of Anecdotical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anecdotical', 'a.', 'Pertaining to, consisting of, or addicted to,\n   anecdotes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anecdotist', 'n.', 'One who relates or collects anecdotes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anelace', 'n.', 'Same as Anlace.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anele', 'v. t.', 'To anoint.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anele', 'v. t.', 'To give extreme unction to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anelectric', 'a.', 'Not becoming electrified by friction; -- opposed to\n   idioelectric.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anelectric', 'n.', 'A substance incapable of being electrified by friction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anelectrode', 'n.', 'The positive pole of a voltaic battery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anelectrotonus', 'n.', 'The condition of decreased irritability of a nerve\n   in the region of the positive electrode or anode on the passage of a\n   current of electricity through it.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anemogram', 'n.', 'A record made by an anemograph.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anemograph', 'n.', 'An instrument for measuring and recording the direction\n   and force of the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anemographic', 'a.', 'Produced by an anemograph; of or pertaining to\n   anemography.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anemography', 'n.', 'A description of the winds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anemography', 'n.', 'The art of recording the direction and force of the\n   wind, as by means of an anemograph.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anemology', 'n.', 'The science of the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anemometer', 'n.', 'An instrument for measuring the force or velocity of\n   the wind; a wind gauge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anemometric', 'a.', 'Alt. of Anemometrical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anemometrical', 'a.', 'Of or pertaining to anemometry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anemometrograph', 'n.', 'An anemograph.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anemometry', 'n.', 'The act or process of ascertaining the force or\n   velocity of the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anemone', 'n.', 'A genus of plants of the Ranunculus or Crowfoot family;\n   windflower. Some of the species are cultivated in gardens.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anemone', 'n.', 'The sea anemone. See Actinia, and Sea anemone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anemonic', 'a.', 'An acrid, poisonous, crystallizable substance, obtained\n   from, the anemone, or from anemonin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anemonin', 'n.', 'An acrid, poisonous, crystallizable substance, obtained\n   from some species of anemone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anemony', 'n.', 'See Anemone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anemorphilous', 'a.', 'Fertilized by the agency of the wind; -- said of\n   plants in which the pollen is carried to the stigma by the wind;\n   wind-Fertilized.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anemoscope', 'n.', 'An instrument which shows the direction of the wind; a\n   wind vane; a weathercock; -- usually applied to a contrivance\n   consisting of a vane above, connected in the building with a dial or\n   index with pointers to show the changes of the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anencephalic', 'a.', 'Alt. of Anencephalous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anencephalous', 'a.', 'Without a brain; brainless.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anenst', 'a.', 'Alt. of Anent');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anent', 'a.', 'Over against; as, he lives anent the church.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anent', 'a.', 'About; concerning; in respect; as, he said nothing anent\n   this particular.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anenterous', 'a.', 'Destitute of a stomach or an intestine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aneroid', 'a.', 'Containing no liquid; -- said of a kind of barometer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aneroid', 'n.', 'An aneroid barometer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anes', 'adv.', 'Once.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anesthesia', 'a.', 'Alt. of Anesthetic');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anesthetic', 'a.', 'Same as Anaesthesia, Anaesthetic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anet', 'n.', 'The herb dill, or dillseed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anethol', 'n.', 'A substance obtained from the volatile oils of anise,\n   fennel, etc., in the form of soft shining scales; -- called also anise\n   camphor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anetic', 'a.', 'Soothing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aneurism', 'n.', 'A soft, pulsating, hollow tumor, containing blood,\n   arising from the preternatural dilation or rupture of the coats of an\n   artery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aneurismal', 'a.', 'Of or pertaining to an aneurism; as, an aneurismal\n   tumor; aneurismal diathesis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anew', 'adv.', 'Over again; another time; in a new form; afresh; as, to arm\n   anew; to create anew.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anfractuose', 'a.', 'Anfractuous; as, anfractuose anthers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anfractuosities', 'pl. ', 'of Anfractuosity');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anfractuosity', 'n.', 'A state of being anfractuous, or full of windings\n   and turnings; sinuosity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anfractuosity', 'n.', 'A sinuous depression or sulcus like those separating\n   the convolutions of the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anfractuous', 'a.', 'Winding; full of windings and turnings; sinuous;\n   tortuous; as, the anfractuous spires of a born.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anfracture', 'n.', 'A mazy winding.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angariation', 'n.', 'Exaction of forced service; compulsion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angeiology', '', 'Alt. of Angeiotomy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angeiotomy', '', 'Same as Angiology, Angiotomy, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angel', 'n.', 'A messenger.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angel', 'n.', 'A spiritual, celestial being, superior to man in power and\n   intelligence. In the Scriptures the angels appear as Gods messengers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angel', 'n.', 'One of a class of \"fallen angels;\" an evil spirit; as, the\n   devil and his angels.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angel', 'n.', 'A minister or pastor of a church, as in the Seven Asiatic\n   churches.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angel', 'n.', 'Attendant spirit; genius; demon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angel', 'n.', 'An appellation given to a person supposed to be of angelic\n   goodness or loveliness; a darling.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angel', 'n.', 'An ancient gold coin of England, bearing the figure of the\n   archangel Michael. It varied in value from 6s. 8d. to 10s.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angelage', 'n.', 'Existence or state of angels.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angelet', 'n.', 'A small gold coin formerly current in England; a half\n   angel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angel fish', '', 'See under Angel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angelhood', 'n.', 'The state of being an angel; angelic nature.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angelic', 'a.', 'Alt. of Angelical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angelical', 'a.', 'Belonging to, or proceeding from, angels; resembling,\n   characteristic of, or partaking of the nature of, an angel; heavenly;\n   divine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angelic', 'a.', 'Of or derived from angelica; as, angelic acid; angelic\n   ether.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angelica', 'n.', 'An aromatic umbelliferous plant (Archangelica officinalis\n   or Angelica archangelica) the leaf stalks of which are sometimes\n   candied and used in confectionery, and the roots and seeds as an\n   aromatic tonic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angelica', 'n.', 'The candied leaf stalks of angelica.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angelically', 'adv.', 'Like an angel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angelicalness', 'n.', 'The quality of being angelic; excellence more than\n   human.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angelify', 'v. t.', 'To make like an angel; to angelize.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angelize', 'v. t.', 'To raise to the state of an angel; to render angelic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angellike', 'a. & adv.', 'Resembling an angel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angelolatry', 'n.', 'Worship paid to angels.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angelology', 'n.', 'A discourse on angels, or a body of doctrines in regard\n   to angels.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angelophany', 'n.', 'The actual appearance of an angel to man.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angelot', 'n.', 'A French gold coin of the reign of Louis XI., bearing the\n   image of St. Michael; also, a piece coined at Paris by the English\n   under Henry VI.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angelot', 'n.', 'An instrument of music, of the lute kind, now disused.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angelot', 'n.', 'A sort of small, rich cheese, made in Normandy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angelus', 'n.', 'A form of devotion in which three Ave Marias are repeated.\n   It is said at morning, noon, and evening, at the sound of a bell.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angelus', 'n.', 'The Angelus bell.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anger', 'n.', 'Trouble; vexation; also, physical pain or smart of a sore,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anger', 'n.', 'A strong passion or emotion of displeasure or antagonism,\n   excited by a real or supposed injury or insult to ones self or others,\n   or by the intent to do such injury.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angered', 'imp. & p. p.', 'of Anger');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angering', 'p. pr. & vb. n.', 'of Anger');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anger', 'v. t.', 'To make painful; to cause to smart; to inflame.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anger', 'v. t.', 'To excite to anger; to enrage; to provoke.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angerly', 'adv.', 'Angrily.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angevine', 'a.', 'Of or pertaining to Anjou in France.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angevine', 'n.', 'A native of Anjou.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angienchyma', 'n.', 'Vascular tissue of plants, consisting of spiral\n   vessels, dotted, barred, and pitted ducts, and laticiferous vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angina', 'n.', 'Any inflammatory affection of the throat or faces, as the\n   quinsy, malignant sore throat, croup, etc., especially such as tends to\n   produce suffocation, choking, or shortness of breath.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anginous', 'a.', 'Alt. of Anginose');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anginose', 'a.', 'Pertaining to angina or angina pectoris.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angio-', '', 'A prefix, or combining form, in numerous compounds, usually\n   relating to seed or blood vessels, or to something contained in, or\n   covered by, a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angiocarpous', 'a.', 'Having fruit inclosed within a covering that does not\n   form a part of itself; as, the filbert covered by its husk, or the\n   acorn seated in its cupule.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angiocarpous', 'a.', 'Having the seeds or spores covered, as in certain\n   lichens.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angiography', 'n.', 'A description of blood vessels and lymphatics.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angiology', 'n.', 'That part of anatomy which treats of blood vessels and\n   lymphatics.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angioma', 'n.', 'A tumor composed chiefly of dilated blood vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angiomonospermous', 'a.', 'Producing one seed only in a seed pod.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angioscope', 'n.', 'An instrument for examining the capillary vessels of\n   animals and plants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angiosperm', 'n.', 'A plant which has its seeds inclosed in a pericarp.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angiospermatous', 'a.', 'Same as Angiospermous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angiospermous', 'a.', 'Having seeds inclosed in a pod or other pericarp.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angiosporous', 'a.', 'Having spores contained in cells or thecae, as in the\n   case of some fungi.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angiostomous', 'a.', 'With a narrow mouth, as the shell of certain\n   gastropods.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angiotomy', 'n.', 'Dissection of the blood vessels and lymphatics of the\n   body.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angle', 'n.', 'The inclosed space near the point where two lines meet; a\n   corner; a nook.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angle', 'n.', 'The figure made by. two lines which meet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angle', 'n.', 'The difference of direction of two lines. In the lines meet,\n   the point of meeting is the vertex of the angle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angle', 'n.', 'A projecting or sharp corner; an angular fragment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angle', 'n.', 'A name given to four of the twelve astrological \"houses.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angle', 'n.', 'A fishhook; tackle for catching fish, consisting of a line,\n   hook, and bait, with or without a rod.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angled', 'imp. & p. p.', 'of Angle');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angling', 'p. pr. & vb. n.', 'of Angle');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angle', 'v. i.', 'To fish with an angle (fishhook); or with hook and line.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angle', 'v. i.', 'To use some bait or artifice; to intrigue; to scheme; as,\n   to angle for praise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angle', 'v. t.', 'To try to gain by some insinuating artifice; to allure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angled', 'a.', 'Having an angle or angles; -- used in compounds; as,\n   right-angled, many-angled, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglemeter', 'n.', 'An instrument to measure angles, esp. one used by\n   geologists to measure the dip of strata.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angler', 'n.', 'One who angles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angler', 'n.', 'A fish (Lophius piscatorius); of Europe and America, having\n   a large, broad, and depressed head, with the mouth very large. Peculiar\n   appendages on the head are said to be used to entice fishes within\n   reach. Called also fishing frog, frogfish, toadfish, goosefish,\n   allmouth, monkfish, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angles', 'n. pl.', 'An ancient Low German tribe, that settled in Britain,\n   which came to be called Engla-land (Angleland or England). The Angles\n   probably came from the district of Angeln (now within the limits of\n   Schleswig); and the country now Lower Hanover, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglesite', 'n.', 'A native sulphate of lead. It occurs in white or\n   yellowish transparent, prismatic crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglewise', 'adv.', 'In an angular manner; angularly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angleworm', 'n.', 'A earthworm of the genus Lumbricus, frequently used by\n   anglers for bait. See Earthworm.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglian', 'a.', 'Of or pertaining to the Angles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglian', 'n.', 'One of the Angles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglic', 'a.', 'Anglian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglican', 'a.', 'English; of or pertaining to England or the English\n   nation; especially, pertaining to, or connected with, the established\n   church of England; as, the Anglican church, doctrine, orders, ritual,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglican', 'a.', 'Pertaining to, characteristic of, or held by, the high\n   church party of the Church of England.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglican', 'n.', 'A member of the Church of England.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglican', 'n.', 'In a restricted sense, a member of the High Church party,\n   or of the more advanced ritualistic section, in the Church of England.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglicanism', 'n.', 'Strong partiality to the principles and rites of the\n   Church of England.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglicanism', 'n.', 'The principles of the established church of England;\n   also, in a restricted sense, the doctrines held by the high-church\n   party.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglicanism', 'n.', 'Attachment to England or English institutions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglice', 'adv.', 'In English; in the English manner; as, Livorno, Anglice\n   Leghorn.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglicify', 'v. t.', 'To anglicize.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglicism', 'n.', 'An English idiom; a phrase or form language peculiar to\n   the English.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglicism', 'n.', 'The quality of being English; an English characteristic,\n   custom, or method.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglicity', 'n.', 'The state or quality of being English.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglicization', 'n.', 'The act of anglicizing, or making English in\n   character.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglicized', 'imp. & p. p.', 'of Anglicize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglicizing', 'p. pr. & vb. n.', 'of Anglicize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglicize', 'v. t.', 'To make English; to English; to anglify; render\n   conformable to the English idiom, or to English analogies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglified', 'imp. & p. p.', 'of Anglify');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglifying', 'p. pr. & vb. n.', 'of Anglify');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglify', 'v. t.', 'To convert into English; to anglicize.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angling', 'n.', 'The act of one who angles; the art of fishing with rod and\n   line.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglo-', '', 'A combining form meaning the same as English; or English and,\n   or English conjoined with; as, Anglo-Turkish treaty, Anglo-German,\n   Anglo-Irish.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglo-Catholic', 'a.', 'Of or pertaining to a church modeled on the English\n   Reformation; Anglican; -- sometimes restricted to the ritualistic or\n   High Church section of the Church of England.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglo-Catholic', 'n.', 'A member of the Church of England who contends for\n   its catholic character; more specifically, a High Churchman.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglomania', 'n.', 'A mania for, or an inordinate attachment to, English\n   customs, institutions, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglomaniac', 'n.', 'One affected with Anglomania.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglophobia', 'n.', 'Intense dread of, or aversion to, England or the\n   English.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglo-Saxon', 'n.', 'A Saxon of Britain, that is, an English Saxon, or one\n   the Saxons who settled in England, as distinguished from a continental\n   (or \"Old\") Saxon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglo-Saxon', 'n.', 'The Teutonic people (Angles, Saxons, Jutes) of\n   England, or the English people, collectively, before the Norman\n   Conquest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglo-Saxon', 'n.', 'The language of the English people before the Conquest\n   (sometimes called Old English). See Saxon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglo-Saxon', 'n.', 'One of the race or people who claim descent from the\n   Saxons, Angles, or other Teutonic tribes who settled in England; a\n   person of English descent in its broadest sense.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglo-Saxon', 'a.', 'Of or pertaining to the Anglo-Saxons or their\n   language.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglo-Saxondom', 'n.', 'The Anglo-Saxon domain (i. e., Great Britain and\n   the United States, etc.); the Anglo-Saxon race.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglo-Saxonism', 'n.', 'A characteristic of the Anglo-Saxon race;\n   especially, a word or an idiom of the Anglo-Saxon tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anglo-Saxonism', 'n.', 'The quality or sentiment of being Anglo-Saxon, or\n   English in its ethnological sense.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angola', 'n.', 'A fabric made from the wool of the Angora goat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angola pea', '', 'A tropical plant (Cajanus indicus) and its edible seed, a\n   kind of pulse; -- so called from Angola in Western Africa. Called also\n   pigeon pea and Congo pea.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angor', 'n.', 'Great anxiety accompanied by painful constriction at the\n   upper part of the belly, often with palpitation and oppression.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angora', 'n.', 'A city of Asia Minor (or Anatolia) which has given its name\n   to a goat, a cat, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angostura bark', '', 'An aromatic bark used as a tonic, obtained from a\n   South American of the rue family (Galipea cusparia, / officinalis).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angoumois moth', '', 'A small moth (Gelechia cerealella) which is very\n   destructive to wheat and other grain. The larva eats out the interior\n   of the grain, leaving only the shell.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angrily', 'adv.', 'In an angry manner; under the influence of anger.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angriness', 'n.', 'The quality of being angry, or of being inclined to\n   anger.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angry', 'superl.', 'Troublesome; vexatious; rigorous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angry', 'superl.', 'Inflamed and painful, as a sore.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angry', 'superl.', 'Touched with anger; under the emotion of anger; feeling\n   resentment; enraged; -- followed generally by with before a person, and\n   at before a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angry', 'superl.', 'Showing anger; proceeding from anger; acting as if\n   moved by anger; wearing the marks of anger; as, angry words or tones;\n   an angry sky; angry waves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angry', 'superl.', 'Red.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angry', 'superl.', 'Sharp; keen; stimulated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anguiform', 'a.', 'Snake-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anguilliform', 'a.', 'Eel-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anguine', 'a.', 'Of, pertaining to, or resembling, a snake or serpent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anguineal', 'a.', 'Anguineous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anguineous', 'a.', 'Snakelike.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anguish', 'n.', 'Extreme pain, either of body or mind; excruciating\n   distress.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anguish', 'v. t.', 'To distress with extreme pain or grief.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angular', 'a.', 'Relating to an angle or to angles; having an angle or\n   angles; forming an angle or corner; sharp-cornered; pointed; as, an\n   angular figure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angular', 'a.', 'Measured by an angle; as, angular distance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angular', 'a.', 'Fig.: Lean; lank; raw-boned; ungraceful; sharp and stiff\n   in character; as, remarkably angular in his habits and appearance; an\n   angular female.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angular', 'n.', 'A bone in the base of the lower jaw of many birds,\n   reptiles, and fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angularity', 'n.', 'The quality or state of being angular; angularness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angularly', 'adv.', 'In an angular manner; with of at angles or corners.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angularness', 'n.', 'The quality of being angular.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angulate', 'a.', 'Alt. of Angulated');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angulated', 'a.', 'Having angles or corners; angled; as, angulate leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angulate', 'v. t.', 'To make angular.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angulation', 'n.', 'A making angular; angular formation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angulo-dentate', 'a.', 'Angularly toothed, as certain leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angulometer', 'n.', 'An instrument for measuring external angles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angulose', 'a.', 'Angulous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angulosity', 'n.', 'A state of being angulous or angular.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angulous', 'a.', 'Angular; having corners; hooked.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angust', 'a.', 'Narrow; strait.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angustate', 'a.', 'Narrowed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angustation', 'n.', 'The act of making narrow; a straitening or contacting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angustifoliate', 'a.', 'Alt. of Angustifolious');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angustifolious', 'a.', 'Having narrow leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angustura bark', '', 'See Angostura bark.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angwantibo', 'n.', 'A small lemuroid mammal (Arctocebus Calabarensis) of\n   Africa. It has only a rudimentary tail.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anhang', 'v. t.', 'To hang.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anharmonic', 'a.', 'Not harmonic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anhelation', 'n.', 'Short and rapid breathing; a panting; asthma.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anhele', 'v. i.', 'To pant; to be breathlessly anxious or eager (for).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anhelose', 'a.', 'Anhelous; panting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anhelous', 'a.', 'Short of breath; panting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anhima', 'n.', 'A South American aquatic bird; the horned screamer or\n   kamichi (Palamedea cornuta). See Kamichi.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anhinga', 'n.', 'An aquatic bird of the southern United States (Platus\n   anhinga); the darter, or snakebird.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anhistous', 'a.', 'Without definite structure; as, an anhistous membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anhungered', 'a.', 'Ahungered; longing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anhydride', 'n.', 'An oxide of a nonmetallic body or an organic radical,\n   capable of forming an acid by uniting with the elements of water; -- so\n   called because it may be formed from an acid by the abstraction of\n   water.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anhydrite', 'n.', 'A mineral of a white or a slightly bluish color, usually\n   massive. It is anhydrous sulphate of lime, and differs from gypsum in\n   not containing water (whence the name).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anhydrous', 'a.', 'Destitute of water; as, anhydrous salts or acids.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ani', 'n.', 'Alt. of Ano');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ano', 'n.', 'A black bird of tropical America, the West Indies and Florida\n   (Crotophaga ani); allied to the cuckoos, and remarkable for communistic\n   nesting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anicut', 'n.', 'Alt. of Annicut');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annicut', 'n.', 'A dam or mole made in the course of a stream for the\n   purpose of regulating the flow of a system of irrigation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anidiomatical', 'a.', 'Not idiomatic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anient', 'v. t.', 'Alt. of Anientise');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anientise', 'v. t.', 'To frustrate; to bring to naught; to annihilate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anigh', 'prep. & adv.', 'Nigh.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anight', 'adv.', 'Alt. of Anights');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anights', 'adv.', 'In the night time; at night.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anil', 'n.', 'A West Indian plant (Indigofera anil); one of the original\n   sources of indigo; also, the indigo dye.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anile', 'a.', 'Old-womanish; imbecile.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anileness', 'n.', 'Anility.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anilic', 'a.', 'Pertaining to, or obtained from, anil; indigotic; --\n   applied to an acid formed by the action of nitric acid on indigo.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anilide', 'n.', 'One of a class of compounds which may be regarded as\n   amides in which more or less of the hydrogen has been replaced by\n   phenyl.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aniline', 'n.', 'An organic base belonging to the phenylamines. It may be\n   regarded as ammonia in which one hydrogen atom has been replaced by the\n   radical phenyl. It is a colorless, oily liquid, originally obtained\n   from indigo by distillation, but now largely manufactured from coal tar\n   or nitrobenzene as a base from which many brilliant dyes are made.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aniline', 'a.', 'Made from, or of the nature of, aniline.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anility', 'n.', 'The state of being and old woman; old-womanishness;\n   dotage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animadversal', 'n.', 'The faculty of perceiving; a percipient.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animadversion', 'n.', 'The act or power of perceiving or taking notice;\n   direct or simple perception.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animadversion', 'n.', 'Monition; warning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animadversion', 'n.', 'Remarks by way of criticism and usually of censure;\n   adverse criticism; reproof; blame.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animadversion', 'n.', 'Judicial cognizance of an offense; chastisement;\n   punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animadversive', 'a.', 'Having the power of perceiving; percipient.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animadverted', 'imp. & p. p.', 'of Animadvert');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animadverting', 'p. pr. & vb. n.', 'of Animadvert');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animadvert', 'v. i.', 'To take notice; to observe; -- commonly followed by\n   that.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animadvert', 'v. i.', 'To consider or remark by way of criticism or\n   censure; to express censure; -- with on or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animadvert', 'v. i.', 'To take cognizance judicially; to inflict\n   punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animadverter', 'n.', 'One who animadverts; a censurer; also [Obs.], a\n   chastiser.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animal', 'n.', 'An organized living being endowed with sensation and the\n   power of voluntary motion, and also characterized by taking its food\n   into an internal cavity or stomach for digestion; by giving carbonic\n   acid to the air and taking oxygen in the process of respiration; and by\n   increasing in motive power or active aggressive force with progress to\n   maturity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animal', 'n.', 'One of the lower animals; a brute or beast, as\n   distinguished from man; as, men and animals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animal', 'a.', 'Of or relating to animals; as, animal functions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animal', 'a.', 'Pertaining to the merely sentient part of a creature, as\n   distinguished from the intellectual, rational, or spiritual part; as,\n   the animal passions or appetites.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animal', 'a.', 'Consisting of the flesh of animals; as, animal food.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animalcular', 'a.', 'Alt. of Animalculine');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animalculine', 'a.', 'Of, pertaining to, or resembling, animalcules.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animalcule', 'n.', 'A small animal, as a fly, spider, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animalcule', 'n.', 'An animal, invisible, or nearly so, to the naked eye.\n   See Infusoria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animalculism', 'n.', 'The theory which seeks to explain certain\n   physiological and pathological phenomena by means of animalcules.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animalculist', 'n.', 'One versed in the knowledge of animalcules.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animalculist', 'n.', 'A believer in the theory of animalculism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animalcula', 'pl. ', 'of Animalculum');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animalculum', 'n.', 'An animalcule.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animalish', 'a.', 'Like an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animalism', 'n.', 'The state, activity, or enjoyment of animals; mere\n   animal life without intellectual or moral qualities; sensuality.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animality', 'n.', 'Animal existence or nature.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animalization', 'n.', 'The act of animalizing; the giving of animal life,\n   or endowing with animal properties.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animalization', 'n.', 'Conversion into animal matter by the process of\n   assimilation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animalized', 'imp. & p. p.', 'of Animalize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animalizing', 'p. pr. & vb. n.', 'of Animalize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animalize', 'v. t.', 'To endow with the properties of an animal; to\n   represent in animal form.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animalize', 'v. t.', 'To convert into animal matter by the processes of\n   assimilation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animalize', 'v. t.', 'To render animal or sentient; to reduce to the state\n   of a lower animal; to sensualize.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animally', 'adv.', 'Physically.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animalness', 'n.', 'Animality.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animastic', 'a.', 'Pertaining to mind or spirit; spiritual.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animastic', 'n.', 'Psychology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animated', 'imp. & p. p.', 'of Animate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animating', 'p. pr. & vb. n.', 'of Animate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animate', 'v. t.', 'To give natural life to; to make alive; to quicken; as,\n   the soul animates the body.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animate', 'v. t.', 'To give powers to, or to heighten the powers or effect\n   of; as, to animate a lyre.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animate', 'v. t.', 'To give spirit or vigor to; to stimulate or incite; to\n   inspirit; to rouse; to enliven.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animate', 'a.', 'Endowed with life; alive; living; animated; lively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animated', 'a.', 'Endowed with life; full of life or spirit; indicating\n   animation; lively; vigorous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animatedly', 'adv.', 'With animation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animater', 'n.', 'One who animates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animating', 'a.', 'Causing animation; life-giving; inspiriting; rousing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animation', 'n.', 'The act of animating, or giving life or spirit; the\n   state of being animate or alive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animation', 'n.', 'The state of being lively, brisk, or full of spirit and\n   vigor; vivacity; spiritedness; as, he recited the story with great\n   animation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animative', 'a.', 'Having the power of giving life or spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animator', 'n.', 'One who, or that which, animates; an animater.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anime', 'a.', 'Of a different tincture from the animal itself; -- said of\n   the eyes of a rapacious animal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anime', 'n.', 'A resin exuding from a tropical American tree (Hymenaea\n   courbaril); and much used by varnish makers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animism', 'n.', 'The doctrine, taught by Stahl, that the soul is the proper\n   principle of life and development in the body.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animism', 'n.', 'The belief that inanimate objects and the phenomena of\n   nature are endowed with personal life or a living soul; also, in an\n   extended sense, the belief in the existence of soul or spirit apart\n   from matter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animist', 'n.', 'One who maintains the doctrine of animism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animistic', 'a.', 'Of or pertaining to animism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animose', 'a.', 'Alt. of Animous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animous', 'a.', 'Full of spirit; hot; vehement; resolute.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animoseness', 'n.', 'Vehemence of temper.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animosities', 'pl. ', 'of Animosity');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animosity', 'v. t.', 'Mere spiritedness or courage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animosity', 'v. t.', 'Violent hatred leading to active opposition; active\n   enmity; energetic dislike.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animi', 'pl. ', 'of Animus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Animus', 'n.', 'Animating spirit; intention; temper.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anion', 'n.', 'An electro-negative element, or the element which, in\n   electro-chemical decompositions, is evolved at the anode; -- opposed to\n   cation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anise', 'n.', 'An umbelliferous plant (Pimpinella anisum) growing naturally\n   in Egypt, and cultivated in Spain, Malta, etc., for its carminative and\n   aromatic seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anise', 'n.', 'The fruit or seeds of this plant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aniseed', 'n.', 'The seed of the anise; also, a cordial prepared from it.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anisette', 'n.', 'A French cordial or liqueur flavored with anise seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anisic', 'a.', 'Of or derived from anise; as, anisic acid; anisic alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anisodactyla', 'n. pl.', 'Alt. of Anisodactyls');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anisodactyls', 'n. pl.', 'A group of herbivorous mammals characterized by\n   having the hoofs in a single series around the foot, as the elephant,\n   rhinoceros, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anisodactyls', 'n. pl.', 'A group of perching birds which are\n   anisodactylous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anisodactylous', 'a.', 'Characterized by unequal toes, three turned forward\n   and one backward, as in most passerine birds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anisomeric', 'a.', 'Not isomeric; not made of the same components in the\n   same proportions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anisomerous', 'a.', 'Having the number of floral organs unequal, as four\n   petals and six stamens.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anisometric', 'a.', 'Not isometric; having unsymmetrical parts; -- said of\n   crystals with three unequal axes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anisopetalous', 'a.', 'Having unequal petals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anisophyllous', 'a.', 'Having unequal leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anisopleura', 'n. pl.', 'A primary division of gastropods, including those\n   having spiral shells. The two sides of the body are unequally\n   developed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anisopoda', 'n. pl.', 'A division of Crustacea, which, in some its\n   characteristics, is intermediate between Amphipoda and Isopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anisostemonous', 'a.', 'Having unequal stamens; having stamens different in\n   number from the petals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anisosthenic', 'a.', 'Of unequal strength.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anisotrope', 'a.', 'Alt. of Anisotropic');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anisotropic', 'a.', 'Not isotropic; having different properties in\n   different directions; thus, crystals of the isometric system are\n   optically isotropic, but all other crystals are anisotropic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anisotropous', 'a.', 'Anisotropic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anker', 'n.', 'A liquid measure in various countries of Europe. The Dutch\n   anker, formerly also used in England, contained about 10 of the old\n   wine gallons, or 8/ imperial gallons.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ankerite', 'n.', 'A mineral closely related to dolomite, but containing\n   iron.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ankle', 'n.', 'The joint which connects the foot with the leg; the tarsus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ankled', 'a.', 'Having ankles; -- used in composition; as, well-ankled.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anklet', 'n.', 'An ornament or a fetter for the ankle; an ankle ring.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ankylose', 'v. t. & i.', 'Same as Anchylose.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ankylosis', 'n.', 'Same as Anchylosis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anlace', 'n.', 'A broad dagger formerly worn at the girdle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ann', 'n.', 'Alt. of Annat');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annat', 'n.', 'A half yearss stipend, over and above what is owing for the\n   incumbency, due to a ministers heirs after his decease.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anna', 'n.', 'An East Indian money of account, the sixteenth of a rupee, or\n   about 2/ cents.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annal', 'n.', 'See Annals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annalist', 'n.', 'A writer of annals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annalistic', 'a.', 'Pertaining to, or after the manner of, an annalist; as,\n   the dry annalistic style.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annalize', 'v. t.', 'To record in annals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annals', 'n. pl.', 'A relation of events in chronological order, each event\n   being recorded under the year in which it happened.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annals', 'n. pl.', 'Historical records; chronicles; history.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annals', 'n. pl.', 'The record of a single event or item.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annals', 'n. pl.', 'A periodic publication, containing records of\n   discoveries, transactions of societies, etc.; as \"Annals of Science.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annats', 'n. pl.', 'Alt. of Annates');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annates', 'n. pl.', 'The first years profits of a spiritual preferment,\n   anciently paid by the clergy to the pope; first fruits. In England,\n   they now form a fund for the augmentation of poor livings.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annealed', 'imp. & p. p.', 'of Anneal');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annealing', 'p. pr. & vb. n.', 'of Anneal');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anneal', 'v. t.', 'To subject to great heat, and then cool slowly, as\n   glass, cast iron, steel, or other metal, for the purpose of rendering\n   it less brittle; to temper; to toughen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anneal', 'v. t.', 'To heat, as glass, tiles, or earthenware, in order to\n   fix the colors laid on them.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annealer', 'n.', 'One who, or that which, anneals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annealing', 'n.', 'The process used to render glass, iron, etc., less\n   brittle, performed by allowing them to cool very gradually from a high\n   heat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annealing', 'n.', 'The burning of metallic colors into glass, earthenware,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annectent', 'a.', 'Connecting; annexing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annelid', 'a.', 'Alt. of Annelidan');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annelidan', 'a.', 'Of or pertaining to the Annelida.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annelidan', 'n.', 'One of the Annelida.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annelida', 'n. pl.', 'A division of the Articulata, having the body formed\n   of numerous rings or annular segments, and without jointed legs. The\n   principal subdivisions are the Chaetopoda, including the Oligochaeta or\n   earthworms and Polychaeta or marine worms; and the Hirudinea or\n   leeches. See Chaetopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annelidous', 'a.', 'Of the nature of an annelid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annellata', 'n. pl.', 'See Annelida.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anneloid', 'n.', 'An animal resembling an annelid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annexed', 'imp. & p. p.', 'of Annex');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annexing', 'p. pr. & vb. n.', 'of Annex');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annex', 'v. t.', 'To join or attach; usually to subjoin; to affix; to\n   append; -- followed by to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annex', 'v. t.', 'To join or add, as a smaller thing to a greater.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annex', 'v. t.', 'To attach or connect, as a consequence, condition, etc.;\n   as, to annex a penalty to a prohibition, or punishment to guilt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annex', 'v. i.', 'To join; to be united.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annex', 'n.', 'Something annexed or appended; as, an additional stipulation\n   to a writing, a subsidiary building to a main building; a wing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annexation', 'v. t.', 'The act of annexing; process of attaching, adding,\n   or appending; the act of connecting; union; as, the annexation of Texas\n   to the United States, or of chattels to the freehold.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annexation', 'v. t.', 'The union of property with a freehold so as to\n   become a fixture. Bouvier. (b) (Scots Law) The appropriation of lands\n   or rents to the crown.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annexationist', 'n.', 'One who favors annexation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annexer', 'n.', 'One who annexes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annexion', 'n.', 'Annexation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annexionist', 'n.', 'An annexationist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annexment', 'n.', 'The act of annexing, or the thing annexed; appendage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annihilable', 'a.', 'Capable of being annihilated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annihilated', 'imp. & p. p.', 'of Annihilate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annihilating', 'p. pr. & vb. n.', 'of Annihilate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annihilate', 'v. t.', 'To reduce to nothing or nonexistence; to destroy the\n   existence of; to cause to cease to be.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annihilate', 'v. t.', 'To destroy the form or peculiar distinctive\n   properties of, so that the specific thing no longer exists; as, to\n   annihilate a forest by cutting down the trees.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annihilate', 'v. t.', 'To destroy or eradicate, as a property or attribute\n   of a thing; to make of no effect; to destroy the force, etc., of; as,\n   to annihilate an argument, law, rights, goodness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annihilate', 'a.', 'Annihilated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annihilation', 'n.', 'The act of reducing to nothing, or nonexistence; or\n   the act of destroying the form or combination of parts under which a\n   thing exists, so that the name can no longer be applied to it; as, the\n   annihilation of a corporation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annihilation', 'n.', 'The state of being annihilated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annihilationist', 'n.', 'One who believes that eternal punishment consists\n   in annihilation or extinction of being; a destructionist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annihilative', 'a.', 'Serving to annihilate; destructive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annihilator', 'n.', 'One who, or that which, annihilates; as, a fire\n   annihilator.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annihilatory', 'a.', 'Annihilative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anniversarily', 'adv.', 'Annually.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anniversary', 'a.', 'Returning with the year, at a stated time; annual;\n   yearly; as, an anniversary feast.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anniversaries', 'pl. ', 'of Anniversary');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anniversary', 'n.', 'The annual return of the day on which any notable\n   event took place, or is wont to be celebrated; as, the anniversary of\n   the Declaration of Independence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anniversary', 'n.', 'The day on which Mass is said yearly for the soul of a\n   deceased person; the commemoration of some sacred event, as the\n   dedication of a church or the consecration of a pope.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anniversary', 'n.', 'The celebration which takes place on an anniversary\n   day.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anniverse', 'n.', 'Anniversary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annodated', 'a.', 'Curved somewhat in the form of the letter S.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anno Domini', '', 'In the year of the Christian era; as, a. d. 1887.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annominate', 'v. t.', 'To name.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annomination', 'n.', 'Paronomasia; punning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annomination', 'n.', 'Alliteration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annotated', 'imp. & p. p.', 'of Annotate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annotating', 'p. pr. & vb. n.', 'of Annotate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annotate', 'n.', 'To explain or criticize by notes; as, to annotate the\n   works of Bacon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annotate', 'v. i.', 'To make notes or comments; -- with on or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annotation', 'n.', 'A note, added by way of comment, or explanation; --\n   usually in the plural; as, annotations on ancient authors, or on a word\n   or a passage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annotationist', 'n.', 'An annotator.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annotative', 'a.', 'Characterized by annotations; of the nature of\n   annotation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annotator', 'n.', 'A writer of annotations; a commentator.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annotatory', 'a.', 'Pertaining to an annotator; containing annotations.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annotine', 'n.', 'A bird one year old, or that has once molted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annotinous', 'a.', 'A year old; in Yearly growths.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annotto', 'n.', 'Alt. of Arnotto');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arnotto', 'n.', 'A red or yellowish-red dyeing material, prepared from the\n   pulp surrounding the seeds of a tree (Bixa orellana) belonging to the\n   tropical regions of America. It is used for coloring cheese, butter,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Announced', 'imp. & p. p.', 'of Announce');
  INSERT INTO words (word, word_type, description) VALUES  (  'Announcing', 'p. pr. & vb. n.', 'of Announce');
  INSERT INTO words (word, word_type, description) VALUES  (  'Announce', 'v. t.', 'To give public notice, or first notice of; to make\n   known; to publish; to proclaim.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Announce', 'v. t.', 'To pronounce; to declare by judicial sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Announcement', 'n.', 'The act of announcing, or giving notice; that which\n   announces; proclamation; publication.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Announcer', 'n.', 'One who announces.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annoyed', 'imp. & p. p.', 'of Annoy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annoying', 'p. pr. & vb. n.', 'of Annoy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annoy', 'n.', 'To disturb or irritate, especially by continued or repeated\n   acts; to tease; to ruffle in mind; to vex; as, I was annoyed by his\n   remarks.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annoy', 'n.', 'To molest, incommode, or harm; as, to annoy an army by\n   impeding its march, or by a cannonade.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annoy', 'n.', 'A feeling of discomfort or vexation caused by what one\n   dislikes; also, whatever causes such a feeling; as, to work annoy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annoyance', 'n.', 'The act of annoying, or the state of being annoyed;\n   molestation; vexation; annoy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annoyance', 'n.', 'That which annoys.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annoyer', 'n.', 'One who, or that which, annoys.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annoyful', 'a.', 'Annoying.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annoying', 'a.', 'That annoys; molesting; vexatious.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annoyous', 'a.', 'Troublesome; annoying.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annual', 'a.', 'Of or pertaining to a year; returning every year; coming or\n   happening once in the year; yearly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annual', 'a.', 'Performed or accomplished in a year; reckoned by the year;\n   as, the annual motion of the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annual', 'a.', 'Lasting or continuing only one year or one growing season;\n   requiring to be renewed every year; as, an annual plant; annual\n   tickets.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annual', 'n.', 'A thing happening or returning yearly; esp. a literary work\n   published once a year.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annual', 'n.', 'Anything, especially a plant, that lasts but one year or\n   season; an annual plant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annual', 'n.', 'A Mass for a deceased person or for some special object,\n   said daily for a year or on the anniversary day.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annualist', 'n.', 'One who writes for, or who edits, an annual.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annually', 'adv.', 'Yearly; year by year.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annuary', 'a.', 'Annual.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annuary', 'n.', 'A yearbook.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annueler', 'n.', 'A priest employed in saying annuals, or anniversary\n   Masses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annuent', 'a.', 'Nodding; as, annuent muscles (used in nodding).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annuitant', 'n.', 'One who receives, or its entitled to receive, an\n   annuity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annuities', 'pl. ', 'of Annuity');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annuity', 'n.', 'A sum of money, payable yearly, to continue for a given\n   number of years, for life, or forever; an annual allowance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annulled', 'imp. & p. p.', 'of Annul');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annulling', 'p. pr. & vb. n.', 'of Annul');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annul', 'a.', 'To reduce to nothing; to obliterate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annul', 'a.', 'To make void or of no effect; to nullify; to abolish; to do\n   away with; -- used appropriately of laws, decrees, edicts, decisions of\n   courts, or other established rules, permanent usages, and the like,\n   which are made void by component authority.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annular', 'a.', 'Pertaining to, or having the form of, a ring; forming a\n   ring; ringed; ring-shaped; as, annular fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annular', 'a.', 'Banded or marked with circles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annularity', 'n.', 'Annular condition or form; as, the annularity of a\n   nebula.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annularry', 'adv.', 'In an annular manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annulary', 'a.', 'Having the form of a ring; annular.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annulata', 'n. pl.', 'A class of articulate animals, nearly equivalent to\n   Annelida, including the marine annelids, earthworms, Gephyrea,\n   Gymnotoma, leeches, etc. See Annelida.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annulate', 'n.', 'One of the Annulata.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annulate', 'a.', 'Alt. of Annulated');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annulated', 'a.', 'Furnished with, or composed of, rings; ringed;\n   surrounded by rings of color.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annulated', 'a.', 'Of or pertaining to the Annulata.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annulation', 'n.', 'A circular or ringlike formation; a ring or belt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annulet', 'n.', 'A little ring.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annulet', 'n.', 'A small, flat fillet, encircling a column, etc., used by\n   itself, or with other moldings. It is used, several times repeated,\n   under the Doric capital.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annulet', 'n.', 'A little circle borne as a charge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annulet', 'n.', 'A narrow circle of some distinct color on a surface or\n   round an organ.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annullable', 'a.', 'That may be Annulled.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annuller', 'n.', 'One who annuls.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annulment', 'n.', 'The act of annulling; abolition; invalidation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annuloid', 'a.', 'Of or pertaining to the Annuloida.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annuloida', 'n. pl.', 'A division of the Articulata, including the annelids\n   and allied groups; sometimes made to include also the helminths and\n   echinoderms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annulosa', 'n. pl.', 'A division of the Invertebrata, nearly equivalent to\n   the Articulata. It includes the Arthoropoda and Anarthropoda. By some\n   zoologists it is applied to the former only.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annulosan', 'n.', 'One of the Annulosa.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annulose', 'a.', 'Furnished with, or composed of, rings or ringlike\n   segments; ringed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annulose', 'a.', 'Of or pertaining to the Annulosa.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annuli', 'pl. ', 'of Annulus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annulus', 'n.', 'A ring; a ringlike part or space.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annulus', 'n.', 'A space contained between the circumferences of two\n   circles, one within the other.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annulus', 'n.', 'The solid formed by a circle revolving around a line which\n   is the plane of the circle but does not cut it.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annulus', 'n.', 'Ring-shaped structures or markings, found in, or upon,\n   various animals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annumerate', 'v. t.', 'To add on; to count in.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annumeration', 'n.', 'Addition to a former number.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annunciable', 'a.', 'That may be announced or declared; declarable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annunciated', 'imp. & p. p.', 'of Annunciate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annunciating', 'p. pr. & vb. n.', 'of Annunciate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annunciate', 'v. t.', 'To announce.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annunciate', 'p. p. & a.', 'Foretold; preannounced.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annunciation', 'n.', 'The act of announcing; announcement; proclamation;\n   as, the annunciation of peace.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annunciation', 'n.', 'The announcement of the incarnation, made by the\n   angel Gabriel to the Virgin Mary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annunciation', 'n.', 'The festival celebrated (March 25th) by the Church of\n   England, of Rome, etc., in memory of the angels announcement, on that\n   day; Lady Day.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annunciative', 'a.', 'Pertaining to annunciation; announcing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annunciator', 'n.', 'One who announces. Specifically: An officer in the\n   church of Constantinople, whose business it was to inform the people of\n   the festivals to be celebrated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annunciator', 'n.', 'An indicator (as in a hotel) which designates the room\n   where attendance is wanted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Annunciatory', 'a.', 'Pertaining to, or containing, announcement; making\n   known.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anoa', 'n.', 'A small wild ox of Celebes (Anoa depressicornis); allied to\n   the buffalo, but having long nearly straight horns.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anode', 'n.', 'The positive pole of an electric battery, or more strictly\n   the electrode by which the current enters the electrolyte on its way to\n   the other pole; -- opposed to cathode.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anodon', 'n.', 'A genus of fresh-water bivalves, having no teeth at the\n   hinge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anodyne', 'a.', 'Serving to assuage pain; soothing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anodyne', 'a.', 'Any medicine which allays pain, as an opiate or narcotic;\n   anything that soothes disturbed feelings.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anodynous', 'a.', 'Anodyne.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anoil', 'v. t.', 'To anoint with oil.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anointed', 'imp. & p. p.', 'of Anoint');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anointing', 'p. pr. & vb. n.', 'of Anoint');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anoint', 'v. t.', 'To smear or rub over with oil or an unctuous substance;\n   also, to spread over, as oil.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anoint', 'v. t.', 'To apply oil to or to pour oil upon, etc., as a sacred\n   rite, especially for consecration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anoint', 'p. p.', 'Anointed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anointer', 'n.', 'One who anoints.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anointment', 'n.', 'The act of anointing, or state of being anointed; also,\n   an ointment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anolis', 'n.', 'A genus of lizards which belong to the family Iguanidae.\n   They take the place in the New World of the chameleons in the Old, and\n   in America are often called chameleons.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomal', 'n.', 'Anything anomalous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomaliped', 'a.', 'Alt. of Anomalipede');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomalipede', 'a.', 'Having anomalous feet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomaliped', 'n.', 'One of a group of perching birds, having the middle toe\n   more or less united to the outer and inner ones.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomalism', 'n.', 'An anomaly; a deviation from rule.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomalistic', 'a.', 'Alt. of Anomalistical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomalistical', 'a.', 'Irregular; departing from common or established\n   rules.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomalistical', 'a.', 'Pertaining to the anomaly, or angular distance of a\n   planet from its perihelion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomalistically', 'adv.', 'With irregularity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomaloflorous', 'a.', 'Having anomalous flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomalous', 'a.', 'Deviating from a general rule, method, or analogy;\n   abnormal; irregular; as, an anomalous proceeding.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomalously', 'adv.', 'In an anomalous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomalousness', 'n.', 'Quality of being anomalous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomalies', 'pl. ', 'of Anomaly');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomaly', 'n.', 'Deviation from the common rule; an irregularity; anything\n   anomalous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomaly', 'n.', 'The angular distance of a planet from its perihelion, as\n   seen from the sun. This is the true anomaly. The eccentric anomaly is a\n   corresponding angle at the center of the elliptic orbit of the planet.\n   The mean anomaly is what the anomaly would be if the planets angular\n   motion were uniform.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomaly', 'n.', 'The angle measuring apparent irregularities in the motion\n   of a planet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomaly', 'n.', 'Any deviation from the essential characteristics of a\n   specific type.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomia', 'n.', 'A genus of bivalve shells, allied to the oyster, so called\n   from their unequal valves, of which the lower is perforated for\n   attachment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomophyllous', 'a.', 'Having leaves irregularly placed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomura', 'n. pl.', 'Alt. of Anomoura');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomoura', 'n. pl.', 'A group of decapod Crustacea, of which the hermit\n   crab in an example.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomural', 'a.', 'Alt. of Anomuran');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomuran', 'a.', 'Irregular in the character of the tail or abdomen; as,\n   the anomural crustaceans.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomuran', 'n.', 'One of the Anomura.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anomy', 'n.', 'Disregard or violation of law.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anon', 'adv.', 'Straightway; at once.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anon', 'adv.', 'Soon; in a little while.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anon', 'adv.', 'At another time; then; again.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anona', 'n.', 'A genus of tropical or subtropical plants of the natural\n   order Anonaceae, including the soursop.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anonaceous', 'a.', 'Pertaining to the order of plants including the\n   soursop, custard apple, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anonym', 'n.', 'One who is anonymous; also sometimes used for \"pseudonym.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anonym', 'n.', 'A notion which has no name, or which can not be expressed\n   by a single English word.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anonymity', 'n.', 'The quality or state of being anonymous; anonymousness;\n   also, that which anonymous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anonymous', 'a.', 'Nameless; of unknown name; also, of unknown or unavowed\n   authorship; as, an anonymous benefactor; an anonymous pamphlet or\n   letter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anonymously', 'adv.', 'In an anonymous manner; without a name.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anonymousness', 'n.', 'The state or quality of being anonymous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anophyte', 'n.', 'A moss or mosslike plant which cellular stems, having\n   usually an upward growth and distinct leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anopla', 'n. pl.', 'One of the two orders of Nemerteans. See Nemertina.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anoplothere', 'n.', 'Alt. of Anoplotherium');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anoplotherium', 'n.', 'A genus of extinct quadrupeds of the order Ungulata,\n   whose were first found in the gypsum quarries near Paris; characterized\n   by the shortness and feebleness of their canine teeth (whence the\n   name).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anoplura', 'n. pl.', 'A group of insects which includes the lice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anopsia', 'a.', 'Alt. of Anopsy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anopsy', 'a.', 'Want or defect of sight; blindness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anorexia', 'n.', 'Alt. of Anorexy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anorexy', 'n.', 'Want of appetite, without a loathing of food.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anormal', 'a.', 'Not according to rule; abnormal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anorn', 'v. t.', 'To adorn.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anorthic', 'a.', 'Having unequal oblique axes; as, anorthic crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anorthite', 'n.', 'A mineral of the feldspar family, commonly occurring in\n   small glassy crystals, also a constituent of some igneous rocks. It is\n   a lime feldspar. See Feldspar.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anorthoscope', 'n.', 'An optical toy for producing amusing figures or\n   pictures by means of two revolving disks, on one of which distorted\n   figures are painted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anosmia', 'n.', 'Loss of the sense of smell.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Another', 'pron. & a.', 'One more, in addition to a former number; a second\n   or additional one, similar in likeness or in effect.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Another', 'pron. & a.', 'Not the same; different.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Another', 'pron. & a.', 'Any or some; any different person, indefinitely;\n   any one else; some one else.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Another-gaines', 'a.', 'Of another kind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Another-gates', 'a.', 'Of another sort.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Another-guess', 'a.', 'Of another sort.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anotta', 'n.', 'See Annotto.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anoura', 'n.', 'See Anura.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anourous', 'a.', 'See Anurous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ansae', 'pl. ', 'of Ansa');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ansa', 'n.', 'A name given to either of the projecting ends of Saturns\n   ring.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ansated', 'a.', 'Having a handle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anserated', 'a.', 'Having the extremities terminate in the heads of eagles,\n   lions, etc.; as, an anserated cross.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anseres', 'n. pl.', 'A Linnaean order of aquatic birds swimming by means of\n   webbed feet, as the duck, or of lobed feet, as the grebe. In this order\n   were included the geese, ducks, auks, divers, gulls, petrels, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anseriformes', 'n. pl.', 'A division of birds including the geese, ducks,\n   and closely allied forms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anserine', 'a.', 'Pertaining to, or resembling, a goose, or the skin of a\n   goose.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anserine', 'a.', 'Pertaining to the Anseres.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anserous', 'a.', 'Resembling a goose; silly; simple.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answered', 'imp. & p. p.', 'of Answer');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answering', 'p. pr. & vb. n.', 'of Answer');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'n.', 'To speak in defense against; to reply to in defense; as, to\n   answer a charge; to answer an accusation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'n.', 'To speak or write in return to, as in return to a call or\n   question, or to a speech, declaration, argument, or the like; to reply\n   to (a question, remark, etc.); to respond to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'n.', 'To respond to satisfactorily; to meet successfully by way\n   of explanation, argument, or justification, and the like; to refute.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'n.', 'To be or act in return or response to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'n.', 'To be or act in compliance with, in fulfillment or\n   satisfaction of, as an order, obligation, demand; as, he answered my\n   claim upon him; the servant answered the bell.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'n.', 'To render account to or for.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'n.', 'To atone; to be punished for.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'n.', 'To be opposite to; to face.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'n.', 'To be or act an equivalent to, or as adequate or sufficient\n   for; to serve for; to repay.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'n.', 'To be or act in accommodation, conformity, relation, or\n   proportion to; to correspond to; to suit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'v. i.', 'To speak or write by way of return (originally, to a\n   charge); or in reply; to make response.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'v. i.', 'To make a satisfactory response or return.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'v. i.', 'To render account, or to be responsible; to be\n   accountable; to make amends; as, the man must answer to his employer\n   for the money intrusted to his care.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'v. i.', 'To be or act in return.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'v. i.', 'To be or act by way of compliance, fulfillment,\n   reciprocation, or satisfaction; to serve the purpose; as, gypsum\n   answers as a manure on some soils.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'v. i.', 'To be opposite, or to act in opposition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'v. i.', 'To be or act as an equivalent, or as adequate or\n   sufficient; as, a very few will answer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'v. i.', 'To be or act in conformity, or by way of accommodation,\n   correspondence, relation, or proportion; to conform; to correspond; to\n   suit; -- usually with to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'n.', 'A reply to a change; a defense.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'n.', 'Something said or written in reply to a question, a call,\n   an argument, an address, or the like; a reply.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'n.', 'Something done in return for, or in consequence of,\n   something else; a responsive action.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'n.', 'A solution, the result of a mathematical operation; as, the\n   answer to a problem.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answer', 'n.', 'A counter-statement of facts in a course of pleadings; a\n   confutation of what the other party has alleged; a responsive\n   declaration by a witness in reply to a question. In Equity, it is the\n   usual form of defense to the complainants charges in his bill.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answerable', 'a.', 'Obliged to answer; liable to be called to account;\n   liable to pay, indemnify, or make good; accountable; amenable;\n   responsible; as, an agent is answerable to his principal; to be\n   answerable for a debt, or for damages.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answerable', 'a.', 'Capable of being answered or refuted; admitting a\n   satisfactory answer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answerable', 'a.', 'Correspondent; conformable; hence, comparable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answerable', 'a.', 'Proportionate; commensurate; suitable; as, an\n   achievement answerable to the preparation for it.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answerable', 'a.', 'Equal; equivalent; adequate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answerableness', 'n.', 'The quality of being answerable, liable,\n   responsible, or correspondent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answerably', 'adv.', 'In an answerable manner; in due proportion or\n   correspondence; suitably.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answerer', 'n.', 'One who answers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Answerless', 'a.', 'Having no answer, or impossible to be answered.');
  INSERT INTO words (word, word_type, description) VALUES  (  'An t', '', 'An it, that is, and it or if it. See An, conj.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ant', '', 'A contraction for are and am not; also used for is not; -- now\n   usually written aint.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ant-', '', 'See Anti-, prefix.');
  INSERT INTO words (word, word_type, description) VALUES  (  '-ant', '', 'A suffix sometimes marking the agent for action; as, merchant,\n   covenant, servant, pleasant, etc. Cf. -ent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ant', 'n.', 'A hymenopterous insect of the Linnaean genus Formica, which is\n   now made a family of several genera; an emmet; a pismire.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antae', 'pl. ', 'of Anta');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anta', 'n.', 'A species of pier produced by thickening a wall at its\n   termination, treated architecturally as a pilaster, with capital and\n   base.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antacid', 'n.', 'A remedy for acidity of the stomach, as an alkali or\n   absorbent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antacid', 'a.', 'Counteractive of acidity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antacrid', 'a.', 'Corrective of acrimony of the humors.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antaean', 'a.', 'Pertaining to Antaeus, a giant athlete slain by Hercules.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antagonism', 'n.', 'Opposition of action; counteraction or contrariety of\n   things or principles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antagonist', 'n.', 'One who contends with another, especially in combat; an\n   adversary; an opponent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antagonist', 'n.', 'A muscle which acts in opposition to another; as a\n   flexor, which bends a part, is the antagonist of an extensor, which\n   extends it.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antagonist', 'n.', 'A medicine which opposes the action of another medicine\n   or of a poison when absorbed into the blood or tissues.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antagonist', 'a.', 'Antagonistic; opposing; counteracting; as, antagonist\n   schools of philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antagonistic', 'a.', 'Alt. of Antagonistical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antagonistical', 'a.', 'Opposing in combat, combating; contending or acting\n   against; as, antagonistic forces.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antagonized', 'imp. & p. p.', 'of Antagonize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antagonozing', 'p. pr. & vb. n.', 'of Antagonize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antagonize', 'v. t.', 'To contend with; to oppose actively; to counteract.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antagonize', 'v. i.', 'To act in opposition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antagony', 'n.', 'Contest; opposition; antagonism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antalgic', 'a.', 'Alleviating pain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antalgic', 'n.', 'A medicine to alleviate pain; an anodyne.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antalkali', 'n.', 'Alt. of Antalkaline');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antalkaline', 'n.', 'Anything that neutralizes, or that counteracts an\n   alkaline tendency in the system.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antalkaline', 'a.', 'Of power to counteract alkalies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antambulacral', 'a.', 'Away from the ambulacral region.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antanaclasis', 'n.', 'A figure which consists in repeating the same word in\n   a different sense; as, Learn some craft when young, that when old you\n   may live without craft.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antanaclasis', 'n.', 'A repetition of words beginning a sentence, after a\n   long parenthesis; as, Shall that heart (which not only feels them, but\n   which has all motions of life placed in them); shall that heart, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antanagoge', 'n.', 'A figure which consists in answering the charge of an\n   adversary, by a counter charge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antaphrodisiac', 'a.', 'Capable of blunting the venereal appetite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antaphrodisiac', 'n.', 'Anything that quells the venereal appetite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antaphroditic', 'a.', 'Antaphrodisiac.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antaphroditic', 'a.', 'Antisyphilitic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antaphroditic', 'n.', 'An antaphroditic medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antapoplectic', 'a.', 'Good against apoplexy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antapoplectic', 'n.', 'A medicine used against apoplexy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antarchism', 'n.', 'Opposition to government in general.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antarchist', 'n.', 'One who opposes all government.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antarchistic', 'a.', 'Alt. of Antarchistical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antarchistical', 'a.', 'Opposed to all human government.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antarctic', 'a.', 'Opposite to the northern or arctic pole; relating to the\n   southern pole or to the region near it, and applied especially to a\n   circle, distant from the pole 23¡ 28/. Thus we say the antarctic pole,\n   circle, ocean, region, current, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antares', 'n.', 'The principal star in Scorpio: -- called also the\n   Scorpions Heart.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antarthritic', 'a.', 'Counteracting or alleviating gout.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antarthritic', 'n.', 'A remedy against gout.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antasthmatic', 'a.', 'Opposing, or fitted to relieve, asthma.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antasthmatic', 'n.', 'A remedy for asthma.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ant-bear', 'n.', 'An edentate animal of tropical America (the Tamanoir);\n   living on ants. It belongs to the genus Myrmecophaga.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ant bird', '', 'See Ant bird, under Ant, n.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ant-cattle', 'n.', 'Various kinds of plant lice or aphids tended by ants\n   for the sake of the honeydew which they secrete. See Aphips.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ante-', '', 'A Latin preposition and prefix; akin to Gr. anti`, Skr. anti,\n   Goth. and-, anda- (only in comp.); AS. and-, ond-, (only in comp.: cf.\n   Answer, Along); G. ant-, ent- (in comp.). The Latin ante is generally\n   used in the sense of before, in regard to position, order, or time, and\n   the Gr. anti` in that of opposite, or in the place of.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ante', 'n.', 'Each players stake, which is put into the pool before (ante)\n   the game begins.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ante', 'v. t. & i.', 'To put up (an ante).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anteact', 'n.', 'A preceding act.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anteal', 'a.', 'Being before, or in front.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ant-eater', 'n.', 'One of several species of edentates and monotremes that\n   feed upon ants. See Ant-bear, Pangolin, Aard-vark, and Echidna.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antecedaneous', 'a.', 'Antecedent; preceding in time.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antecede', 'v. t. & i.', 'To go before in time or place; to precede; to\n   surpass.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antecedence', 'n.', 'The act or state of going before in time; precedence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antecedence', 'n.', 'An apparent motion of a planet toward the west;\n   retrogradation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antecedency', 'n.', 'The state or condition of being antecedent; priority.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antecedent', 'a.', 'Going before in time; prior; anterior; preceding; as,\n   an event antecedent to the Deluge; an antecedent cause.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antecedent', 'a.', 'Presumptive; as, an antecedent improbability.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antecedent', 'n.', 'That which goes before in time; that which precedes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antecedent', 'n.', 'One who precedes or goes in front.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antecedent', 'n.', 'The earlier events of ones life; previous principles,\n   conduct, course, history.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antecedent', 'n.', 'The noun to which a relative refers; as, in the\n   sentence \"Solomon was the prince who built the temple,\" prince is the\n   antecedent of who.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antecedent', 'n.', 'The first or conditional part of a hypothetical\n   proposition; as, If the earth is fixed, the sun must move.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antecedent', 'n.', 'The first of the two propositions which constitute an\n   enthymeme or contracted syllogism; as, Every man is mortal; therefore\n   the king must die.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antecedent', 'n.', 'The first of the two terms of a ratio; the first or\n   third of the four terms of a proportion. In the ratio a:b, a is the\n   antecedent, and b the consequent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antecedently', 'adv.', 'Previously; before in time; at a time preceding;\n   as, antecedently to conversion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antecessor', 'n.', 'One who goes before; a predecessor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antecessor', 'n.', 'An ancestor; a progenitor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antechamber', 'n.', 'A chamber or apartment before the chief apartment and\n   leading into it, in which persons wait for audience; an outer chamber.\n   See Lobby.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antechamber', 'n.', 'A space viewed as the outer chamber or the entrance to\n   an interior part.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antechapel', 'n.', 'The outer part of the west end of a collegiate or other\n   chapel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antecians', 'n. pl.', 'See Ant/cians.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antecommunion', 'n.', 'A name given to that part of the Anglican liturgy\n   for the communion, which precedes the consecration of the elements.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antecursor', 'n.', 'A forerunner; a precursor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antedate', 'n.', 'Prior date; a date antecedent to another which is the\n   actual date.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antedate', 'n.', 'Anticipation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antedated', 'imp. & p. p.', 'of Antedate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antedating', 'p. pr. & vb. n.', 'of Antedate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antedate', 'v. t.', 'To date before the true time; to assign to an earlier\n   date; thus, to antedate a deed or a bond is to give it a date anterior\n   to the true time of its execution.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antedate', 'v. t.', 'To precede in time.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antedate', 'v. t.', 'To anticipate; to make before the true time.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antediluvial', 'a.', 'Before the flood, or Deluge, in Noahs time.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antediluvian', 'a.', 'Of or relating to the period before the Deluge in\n   Noahs time; hence, antiquated; as, an antediluvian vehicle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antediluvian', 'n.', 'One who lived before the Deluge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antefact', 'n.', 'Something done before another act.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antefixes', 'pl. ', 'of Antefix');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antefixa', 'pl. ', 'of Antefix');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antefix', 'n.', 'An ornament fixed upon a frieze.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antefix', 'n.', 'An ornament at the eaves, concealing the ends of the joint\n   tiles of the roof.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antefix', 'n.', 'An ornament of the cymatium of a classic cornice,\n   sometimes pierced for the escape of water.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anteflexion', 'n.', 'A displacement forward of an organ, esp. the uterus,\n   in such manner that its axis is bent upon itself.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ant egg', '', 'One of the small white egg-shaped pupae or cocoons of the\n   ant, often seen in or about ant-hills, and popularly supposed to be\n   eggs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antelope', 'n.', 'One of a group of ruminant quadrupeds, intermediate\n   between the deer and the goat. The horns are usually annulated, or\n   ringed. There are many species in Africa and Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antelucan', 'a.', 'Held or being before light; -- a word applied to\n   assemblies of Christians, in ancient times of persecution, held before\n   light in the morning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antemeridian', 'a.', 'Being before noon; in or pertaining to the forenoon.\n   (Abbrev. a. m.)');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antemetic', 'a.', 'Tending to check vomiting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antemetic', 'n.', 'A remedy to check or allay vomiting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antemosaic', 'a.', 'Being before the time of Moses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antemundane', 'a.', 'Being or occurring before the creation of the world.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antemural', 'n.', 'An outwork of a strong, high wall, with turrets, in\n   front of the gateway (as of an old castle); for defending the entrance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antenatal', 'a.', 'Before birth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antenicene', 'a.', 'Of or in the Christian church or era, anterior to the\n   first council of Nice, held a. d. 325; as, antenicene faith.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antennae', 'pl. ', 'of Antenna');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antenna', 'n.', 'A movable, articulated organ of sensation, attached to the\n   heads of insects and Crustacea. There are two in the former, and\n   usually four in the latter. They are used as organs of touch, and in\n   some species of Crustacea the cavity of the ear is situated near the\n   basal joint. In insects, they are popularly called horns, and also\n   feelers. The term in also applied to similar organs on the heads of\n   other arthropods and of annelids.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antennal', 'a.', 'Belonging to the antennae.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antenniferous', 'a.', 'Bearing or having antennae.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antenniform', 'a.', 'Shaped like antennae.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antennule', 'n.', 'A small antenna; -- applied to the smaller pair of\n   antennae or feelers of Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antenumber', 'n.', 'A number that precedes another.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antenuptial', 'a.', 'Preceding marriage; as, an antenuptial agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anteorbital', 'a. & n.', 'Same as Antorbital.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antepaschal', 'a.', 'Pertaining to the time before the Passover, or before\n   Easter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antepast', 'n.', 'A foretaste.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antependium', 'n.', 'The hangings or screen in front of the altar; an altar\n   cloth; the frontal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antepenult', 'n.', 'Alt. of Antepenultima');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antepenultima', 'n.', 'The last syllable of a word except two, as -syl- in\n   monosyllable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antepenultimate', 'a.', 'Of or pertaining to the last syllable but two.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antepenultimate', 'n.', 'The antepenult.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antephialtic', 'a.', 'Good against nightmare.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antephialtic', 'n.', 'A remedy nightmare.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antepileptic', 'a.', 'Good against epilepsy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antepileptic', 'n.', 'A medicine for epilepsy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antepone', 'v. t.', 'To put before; to prefer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anteport', 'n.', 'An outer port, gate, or door.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anteportico', 'n.', 'An outer porch or vestibule.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anteposition', 'n.', 'The placing of a before another, which, by ordinary\n   rules, ought to follow it.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anteprandial', 'a.', 'Preceding dinner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antepredicament', 'n.', 'A prerequisite to a clear understanding of the\n   predicaments and categories, such as definitions of common terms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anterior', 'a.', 'Before in time; antecedent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anterior', 'a.', 'Before, or toward the front, in place; as, the anterior\n   part of the mouth; -- opposed to posterior.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anteriority', 'n.', 'The state of being anterior or preceding in time or in\n   situation; priority.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anteriorly', 'adv.', 'In an anterior manner; before.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anteroom', 'n.', 'A room before, or forming an entrance to, another; a\n   waiting room.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antero-', '', 'A combining form meaning anterior, front; as,\n   antero-posterior, front and back; antero-lateral, front side, anterior\n   and at the side.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antes', 'n. pl.', 'Antae. See Anta.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antestature', 'n.', 'A small intrenchment or work of palisades, or of sacks\n   of earth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antestomach', 'n.', 'A cavity which leads into the stomach, as in birds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antetemple', 'n.', 'The portico, or narthex in an ancient temple or church.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anteversion', 'n.', 'A displacement of an organ, esp. of the uterus, in\n   such manner that its whole axis is directed further forward than usual.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antevert', 'v. t.', 'To prevent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antevert', 'v. t.', 'To displace by anteversion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthelia', 'pl. ', 'of Anthelion');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthelion', 'n.', 'A halo opposite the sun, consisting of a colored ring or\n   rings around the shadow of the spectators own head, as projected on a\n   cloud or on an opposite fog bank.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthelix', 'n.', 'Same as Antihelix.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthelmintic', 'a.', 'Good against intestinal worms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthelmintic', 'n.', 'An anthelmintic remedy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthem', 'n.', 'Formerly, a hymn sung in alternate parts, in present usage,\n   a selection from the Psalms, or other parts of the Scriptures or the\n   liturgy, set to sacred music.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthem', 'n.', 'A song or hymn of praise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthem', 'v. t.', 'To celebrate with anthems.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthemion', '', 'A floral ornament. See Palmette');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthemis', 'n.', 'Chamomile; a genus of composite, herbaceous plants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthemwise', 'adv.', 'Alternately.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anther', 'n.', 'That part of the stamen containing the pollen, or\n   fertilizing dust, which, when mature, is emitted for the impregnation\n   of the ovary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antheridia', 'pl. ', 'of Antheridium');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antheridium', 'n.', 'The male reproductive apparatus in the lower,\n   consisting of a cell or other cavity in which spermatozoids are\n   produced; -- called also spermary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antheriferous', 'a.', 'Producing anthers, as plants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antheriferous', 'a.', 'Supporting anthers, as a part of a flower.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antheriform', 'a.', 'Shaped like an anther; anther-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antherogenous', 'a.', 'Transformed from anthers, as the petals of a double\n   flower.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antheroid', 'a.', 'Resembling an anther.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antherozoid', 'n.', 'Alt. of Antherozooid');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antherozooid', 'n.', 'One of the mobile male reproductive bodies in the\n   antheridia of cryptogams.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthesis', 'n.', 'The period or state of full expansion in a flower.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ant-hill', 'n.', 'A mound thrown up by ants or by termites in forming their\n   nests.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthobian', 'n.', 'A beetle which feeds on flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthobranchia', 'n. pl.', 'A division of nudibranchiate Mollusca, in which\n   the gills form a wreath or cluster upon the posterior part of the back.\n   See Nudibranchiata, and Doris.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthocarpous', 'a.', 'Having some portion of the floral envelopes attached\n   to the pericarp to form the fruit, as in the checkerberry, the\n   mulberry, and the pineapple.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthocyanin', 'n.', 'Same as Anthokyan.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthodium', 'n.', 'The inflorescence of a compound flower in which many\n   florets are gathered into a involucrate head.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthography', 'n.', 'A description of flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthoid', 'a.', 'Resembling a flower; flowerlike.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthokyan', 'n.', 'The blue coloring matter of certain flowers. Same as\n   Cyanin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antholite', 'n.', 'A fossil plant, like a petrified flower.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthological', 'a.', 'Pertaining to anthology; consisting of beautiful\n   extracts from different authors, especially the poets.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthologist', 'n.', 'One who compiles an anthology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthology', 'n.', 'A discourse on flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthology', 'n.', 'A collection of flowers; a garland.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthology', 'n.', 'A collection of flowers of literature, that is,\n   beautiful passages from authors; a collection of poems or epigrams; --\n   particularly applied to a collection of ancient Greek epigrams.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthology', 'n.', 'A service book containing a selection of pieces for the\n   festival services.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthomania', 'n.', 'A extravagant fondness for flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthonys Fire', '', 'See Saint Anthonys Fire, under Saint.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthophagous', 'a.', 'Eating flowers; -- said of certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthophore', 'n.', 'The stipe when developed into an internode between\n   calyx and corolla, as in the Pink family.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthophorous', 'a.', 'Flower bearing; supporting the flower.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthophyllite', 'n.', 'A mineral of the hornblende group, of a yellowish\n   gray or clove brown color.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthorism', 'n.', 'A description or definition contrary to that which is\n   given by the adverse party.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthotaxy', 'n.', 'The arrangement of flowers in a cluster; the science of\n   the relative position of flowers; inflorescence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthozoa', 'n. pl.', 'The class of the Coelenterata which includes the\n   corals and sea anemones. The three principal groups or orders are\n   Acyonaria, Actinaria, and Madreporaria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthozoan', 'a.', 'Pertaining to the Anthozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthozoan', 'n.', 'One of the Anthozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthozoic', 'a.', 'Of or pertaining to the Anthozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthracene', 'n.', 'A solid hydrocarbon, C6H4.C2H2.C6H4, which accompanies\n   naphthalene in the last stages of the distillation of coal tar. Its\n   chief use is in the artificial production of alizarin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthracic', 'a.', 'Of or relating to anthrax; as, anthracic blood.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthraciferous', 'a.', 'Yielding anthracite; as, anthraciferous strata.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthracite', 'n.', 'A hard, compact variety of mineral coal, of high\n   luster, differing from bituminous coal in containing little or no\n   bitumen, in consequence of which it burns with a nearly non luminous\n   flame. The purer specimens consist almost wholly of carbon. Also called\n   glance coal and blind coal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthracitic', 'a.', 'Of, pertaining to, or like, anthracite; as,\n   anthracitic formations.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthracoid', 'a.', 'Resembling anthrax in action; of the nature of anthrax;\n   as, an anthracoid microbe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthracomancy', 'n.', 'Divination by inspecting a burning coal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthracometer', 'n.', 'An instrument for measuring the amount of carbonic\n   acid in a mixture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthracometric', 'a.', 'Of or pertaining to an anthracometer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthraconite', 'n.', 'A coal-black marble, usually emitting a fetid smell\n   when rubbed; -- called also stinkstone and swinestone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthraquinone', 'n.', 'A hydrocarbon, C6H4.C2O2.C6H4, subliming in shining\n   yellow needles. It is obtained by oxidation of anthracene.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthrax', 'n.', 'A carbuncle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthrax', 'n.', 'A malignant pustule.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthrax', 'n.', 'A microscopic, bacterial organism (Bacillus anthracis);\n   resembling transparent rods. [See Illust. under Bacillus.]');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthrax', 'n.', 'An infectious disease of cattle and sheep. It is ascribed\n   to the presence of a rod-shaped bacterium (Bacillus anthracis); the\n   spores of which constitute the contagious matter. It may be transmitted\n   to man by inoculation. The spleen becomes greatly enlarged and filled\n   with bacteria. Called also splenic fever.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthrenus', 'n.', 'A genus of small beetles, several of which, in the\n   larval state, are very destructive to woolen goods, fur, etc. The\n   common \"museum pest\" is A. varius; the carpet beetle is A.\n   scrophulariae. The larvae are commonly confounded with moths.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropic', 'a.', 'Alt. of Anthropical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropical', 'a.', 'Like or related to man; human.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropidae', 'n. pl.', 'The group that includes man only.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropocentric', 'a.', 'Assuming man as the center or ultimate end; --\n   applied to theories of the universe or of any part of it, as the solar\n   system.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropogenic', 'a.', 'Of or pertaining to anthropogeny.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropogeny', 'n.', 'The science or study of human generation, or the\n   origin and development of man.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropoglot', 'n.', 'An animal which has a tongue resembling that of man,\n   as the parrot.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropography', 'n.', 'That branch of anthropology which treats of the\n   actual distribution of the human race in its different divisions, as\n   distinguished by physical character, language, institutions, and\n   customs, in contradistinction to ethnography, which treats historically\n   of the origin and filiation of races and nations.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropoid', 'a.', 'Resembling man; -- applied especially to certain apes,\n   as the ourang or gorilla.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropoid', 'n.', 'An anthropoid ape.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropoidal', 'a.', 'Anthropoid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropoidea', 'n. pl.', 'The suborder of primates which includes the\n   monkeys, apes, and man.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropolatry', 'n.', 'Man worship.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropolite', 'n.', 'A petrifaction of the human body, or of any portion\n   of it.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropologic', 'a.', 'Alt. of Anthropological');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropological', 'a.', 'Pertaining to anthropology; belonging to the\n   nature of man.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropologist', 'n.', 'One who is versed in anthropology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropology', 'n.', 'The science of the structure and functions of the\n   human body.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropology', 'n.', 'The science of man; -- sometimes used in a limited\n   sense to mean the study of man as an object of natural history, or as\n   an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropology', 'n.', 'That manner of expression by which the inspired\n   writers attribute human parts and passions to God.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropomancy', 'n.', 'Divination by the entrails of human being.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropometric', 'a.', 'Alt. of Anthropometrical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropometrical', 'a.', 'Pertaining to anthropometry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropometry', 'n.', 'Measurement of the height and other dimensions of\n   human beings, especially at different ages, or in different races,\n   occupations, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropomorpha', 'n. pl.', 'The manlike, or anthropoid, apes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropomorphic', 'a.', 'Of or pertaining to anthropomorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropomorphism', 'n.', 'The representation of the Deity, or of a\n   polytheistic deity, under a human form, or with human attributes and\n   affections.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropomorphism', 'n.', 'The ascription of human characteristics to things\n   not human.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropomorphist', 'n.', 'One who attributes the human form or other human\n   attributes to the Deity or to anything not human.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropomorphite', 'n.', 'One who ascribes a human form or human attributes\n   to the Deity or to a polytheistic deity. Taylor. Specifically, one of a\n   sect of ancient heretics who believed that God has a human form, etc.\n   Tillotson.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropomorphitic', 'a.', 'to anthropomorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropomorphitism', 'n.', 'Anthropomorphism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropomorphize', 'v. t. & i.', 'To attribute a human form or personality\n   to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropomorphology', 'n.', 'The application to God of terms descriptive of\n   human beings.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropomorphosis', 'n.', 'Transformation into the form of a human being.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropomorphous', 'a.', 'Having the figure of, or resemblance to, a man;\n   as, an anthropomorphous plant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropopathic', 'a.', 'Alt. of Anthropopathical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropopathical', 'a.', 'Of or pertaining to anthropopathy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropopathism', 'n.', 'Alt. of Anthropopathy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropopathy', 'n.', 'The ascription of human feelings or passions to God,\n   or to a polytheistic deity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropophagi', 'n. pl.', 'Man eaters; cannibals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropophagic', 'a.', 'Alt. of Anthropophagical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropophagical', 'a.', 'Relating to cannibalism or anthropophagy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropophaginian', 'n.', 'One who east human flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropophagite', 'n.', 'A cannibal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropophagous', 'a.', 'Feeding on human flesh; cannibal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropophagy', 'n.', 'The eating of human flesh; cannibalism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropophuism', 'n.', 'Human nature.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthroposcopy', 'n.', 'The art of discovering or judging of a mans\n   character, passions. and inclinations from a study of his visible\n   features.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthroposophy', 'n.', 'Knowledge of the nature of man; hence, human wisdom.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropotomical', 'a.', 'Pertaining to anthropotomy, or the dissection of\n   human bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropotomist', 'n.', 'One who is versed in anthropotomy, or human\n   anatomy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthropotomy', 'n.', 'The anatomy or dissection of the human body;\n   androtomy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthypnotic', '', 'See Antihypnotic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthypochondriac', 'a. & n.', 'See Antihypochondriac.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anthysteric', 'a. & n.', 'See Antihysteric.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anti', '', 'A prefix meaning against, opposite or opposed to, contrary, or\n   in place of; -- used in composition in many English words. It is often\n   shortened to ant-; as, antacid, antarctic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiae', 'n. pl.', 'The two projecting feathered angles of the forehead of\n   some birds; the frontal points.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antialbumid', 'n.', 'A body formed from albumin by pancreatic and gastric\n   digestion. It is convertible into antipeptone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antialbumose', 'n.', 'See Albumose.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anti-American', 'a.', 'Opposed to the Americans, their aims, or interests,\n   or to the genius of American institutions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiaphrodisiac', 'a. & n.', 'Same as Antaphrodisiac.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiapoplectic', 'a. & n.', 'Same as Antapoplectic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiar', 'n.', 'A Virulent poison prepared in Java from the gum resin of\n   one species of the upas tree (Antiaris toxicaria).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiarin', 'n.', 'A poisonous principle obtained from antiar.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiasthmatic', 'a. & n.', 'Same as Antasthmatic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiattrition', 'n.', 'Anything to prevent the effects of friction, esp. a\n   compound lubricant for machinery, etc., often consisting of plumbago,\n   with some greasy material; antifriction grease.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antibacchius', 'n.', 'A foot of three syllables, the first two long, and\n   the last short (#).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antibillous', 'a.', 'Counteractive of bilious complaints; tending to\n   relieve biliousness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antibrachial', 'a.', 'Of or pertaining to the antibrachium, or forearm.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antibrachium', 'n.', 'That part of the fore limb between the brachium and\n   the carpus; the forearm.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antibromic', 'n.', 'An agent that destroys offensive smells; a deodorizer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiburgher', 'n.', 'One who seceded from the Burghers (1747); deeming it\n   improper to take the Burgess oath.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antic', 'a.', 'Old; antique.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antic', 'a.', '');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antic', 'a.', 'Odd; fantastic; fanciful; grotesque; ludicrous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antic', 'n.', 'A buffoon or merry-andrew; one that practices odd\n   gesticulations; the Fool of the old play.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antic', 'n.', 'An odd imagery, device, or tracery; a fantastic figure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antic', 'n.', 'A grotesque trick; a piece of buffoonery; a caper.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antic', 'n.', 'A grotesque representation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antic', 'n.', 'An antimask.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticked', 'imp. & p. p.', 'of Antic');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antickt', '', 'of Antic');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antic', 'v. t.', 'To make appear like a buffoon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antic', 'v. i.', 'To perform antics.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticatarrhal', 'a.', 'Efficacious against catarrh.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticatarrhal', 'n.', 'An anticatarrhal remedy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticathode', 'n.', 'The part of a vacuum tube opposite the cathode. Upon\n   it the cathode rays impinge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticausodic', 'a. & n.', 'Same as Anticausotic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticausotic', 'a.', 'Good against an inflammatory fever.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticausotic', 'n.', 'A remedy for such a fever.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antichamber', 'n.', 'See Antechamber.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antichlor', 'n.', 'Any substance (but especially sodium hyposulphite) used\n   in removing the excess of chlorine left in paper pulp or stuffs after\n   bleaching.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antichrist', 'n.', 'A denier or opponent of Christ. Specif.: A great\n   antagonist, person or power, expected to precede Christs second\n   coming.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antichristian', 'a.', 'Opposed to the Christian religion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antichristianism', 'n.', 'Alt. of Antichristianity');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antichristianity', 'n.', 'Opposition or contrariety to the Christian\n   religion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antichristianly', 'adv.', 'In an antichristian manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antichronical', 'a.', 'Deviating from the proper order of time.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antichronism', 'n.', 'Deviation from the true order of time; anachronism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antichthones', 'pl. ', 'of Antichthon');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antichthon', 'n.', 'A hypothetical earth counter to ours, or on the\n   opposite side of the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antichthon', 'n.', 'Inhabitants of opposite hemispheres.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticipant', 'a.', 'Anticipating; expectant; -- with of.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticipated', 'imp. & p. p.', 'of Anticipate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticipating', 'p. pr. & vb. n.', 'of Anticipate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticipate', 'v. t.', 'To be before in doing; to do or take before another;\n   to preclude or prevent by prior action.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticipate', 'v. t.', 'To take up or introduce beforehand, or before the\n   proper or normal time; to cause to occur earlier or prematurely; as,\n   the advocate has anticipated a part of his argument.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticipate', 'v. t.', 'To foresee (a wish, command, etc.) and do beforehand\n   that which will be desired.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticipate', 'v. t.', 'To foretaste or foresee; to have a previous view or\n   impression of; as, to anticipate the pleasures of a visit; to\n   anticipate the evils of life.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticipation', 'n.', 'The act of anticipating, taking up, placing, or\n   considering something beforehand, or before the proper time in natural\n   order.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticipation', 'n.', 'Previous view or impression of what is to happen;\n   instinctive prevision; foretaste; antepast; as, the anticipation of the\n   joys of heaven.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticipation', 'n.', 'Hasty notion; intuitive preconception.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticipation', 'n.', 'The commencing of one or more tones of a chord with\n   or during the chord preceding, forming a momentary discord.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticipative', 'a.', 'Anticipating, or containing anticipation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticipator', 'n.', 'One who anticipates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticipatory', 'a.', 'Forecasting; of the nature of anticipation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticivic', 'n.', 'Opposed to citizenship.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticivism', 'n.', 'Opposition to the body politic of citizens.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticlastic', 'a.', 'Having to opposite curvatures, that is, curved\n   longitudinally in one direction and transversely in the opposite\n   direction, as the surface of a saddle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticlimax', 'n.', 'A sentence in which the ideas fall, or become less\n   important and striking, at the close; -- the opposite of climax. It\n   produces a ridiculous effect.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticlinal', 'a.', 'Inclining or dipping in opposite directions. See\n   Synclinal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticlinal', 'n.', 'The crest or line in which strata slope or dip in\n   opposite directions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticlinoria', 'pl. ', 'of Anticlinorium');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticlinorium', 'n.', 'The upward elevation of the crust of the earth,\n   resulting from a geanticlinal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticly', 'adv.', 'Oddly; grotesquely.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antic-mask', 'n.', 'An antimask.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticness', 'n.', 'The quality of being antic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticonstitutional', 'a.', 'Opposed to the constitution; unconstitutional.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticontagious', 'a.', 'Opposing or destroying contagion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticonvulsive', 'a.', 'Good against convulsions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticor', 'n.', 'A dangerous inflammatory swelling of a horses breast,\n   just opposite the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticous', 'a.', 'Facing toward the axis of the flower, as in the introrse\n   anthers of the water lily.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anticyclone', 'n.', 'A movement of the atmosphere opposite in character, as\n   regards direction of the wind and distribution of barometric pressure,\n   to that of a cyclone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antidotal', 'a.', 'Having the quality an antidote; fitted to counteract the\n   effects of poison.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antidotary', 'a.', 'Antidotal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antidote', 'n.', 'A remedy to counteract the effects of poison, or of\n   anything noxious taken into the stomach; -- used with against, for, or\n   to; as, an antidote against, for, or to, poison.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antidote', 'n.', 'Whatever tends to prevent mischievous effects, or to\n   counteract evil which something else might produce.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antidote', 'v. t.', 'To counteract or prevent the effects of, by giving or\n   taking an antidote.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antidote', 'v. t.', 'To fortify or preserve by an antidote.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antidotical', 'a.', 'Serving as an antidote.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antidromous', 'a.', 'Changing the direction in the spiral sequence of\n   leaves on a stem.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antidysenteric', 'a.', 'Good against dysentery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antidysenteric', 'n.', 'A medicine for dysentery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiemetic', 'a. / n.', 'Same as Antemetic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiephialtic', 'a. & n.', 'Same as Antephialtic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiepileptic', 'a. & n.', 'Same as Antepileptic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antifebrile', 'a. & n.', 'Febrifuge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antifebrine', 'n.', 'Acetanilide.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anti-federalist', 'n.', 'One of party opposed to a federative government;\n   -- applied particularly to the party which opposed the adoption of the\n   constitution of the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antifriction', 'n.', 'Something to lessen friction; antiattrition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antifriction', 'a.', 'Tending to lessen friction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antigalastic', 'a.', 'Causing a diminution or a suppression of the\n   secretion of milk.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anti-Gallican', 'a.', 'Opposed to what is Gallic or French.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antigraph', 'n.', 'A copy or transcript.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiguggler', 'n.', 'A crooked tube of metal, to be introduced into the\n   neck of a bottle for drawing out the liquid without disturbing the\n   sediment or causing a gurgling noise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antihelix', 'n.', 'The curved elevation of the cartilage of the ear, within\n   or in front of the helix. See Ear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antihemorrhagic', 'a.', 'Tending to stop hemorrhage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antihemorrhagic', 'n.', 'A remedy for hemorrhage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antihydrophobic', 'a.', 'Counteracting or preventing hydrophobia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antihydrophobic', 'n.', 'A remedy for hydrophobia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antihydropic', 'a.', 'Good against dropsy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antihydropic', 'n.', 'A remedy for dropsy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antihypnotic', 'a.', 'Tending to prevent sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antihypnotic', 'n.', 'An antihypnotic agent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antihypochondriac', 'a.', 'Counteractive of hypochondria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antihypochondriac', 'n.', 'A remedy for hypochondria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antihysteric', 'a.', 'Counteracting hysteria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antihysteric', 'n.', 'A remedy for hysteria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiicteric', 'a.', 'Good against jaundice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiicteric', 'n.', 'A remedy for jaundice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antilegomena', 'n. pl.', 'Certain books of the New Testament which were for\n   a time not universally received, but which are now considered\n   canonical. These are the Epistle to the Hebrews, the Epistles of James\n   and Jude, the second Epistle of Peter, the second and third Epistles of\n   John, and the Revelation. The undisputed books are called the\n   Homologoumena.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antilibration', 'n.', 'A balancing; equipoise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antilithic', 'a.', 'Tending to prevent the formation of urinary calculi, or\n   to destroy them when formed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antilithic', 'n.', 'An antilithic medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antilogarithm', 'n.', 'The number corresponding to a logarithm. The word\n   has been sometimes, though rarely, used to denote the complement of a\n   given logarithm; also the logarithmic cosine corresponding to a given\n   logarithmic sine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antilogous', 'a.', 'Of the contrary name or character; -- opposed to\n   analogous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antilogies', 'pl. ', 'of Antilogy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antilogy', 'n.', 'A contradiction between any words or passages in an\n   author.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiloimic', 'n.', 'A remedy against the plague.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antilopine', 'a.', 'Of or relating to the antelope.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiloquist', 'n.', 'A contradicter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiloquy', 'n.', 'Contradiction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antilyssic', 'a. & n.', 'Antihydrophobic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimacassar', 'n.', 'A cover for the back or arms of a chair or sofa,\n   etc., to prevent them from being soiled by macassar or other oil from\n   the hair.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimagistrical', 'a.', 'Opposed to the office or authority of magistrates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimalarial', 'a.', 'Good against malaria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimask', 'n.', 'A secondary mask, or grotesque interlude, between the\n   parts of a serious mask.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimason', 'n.', 'One opposed to Freemasonry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimasonry', 'n.', 'Opposition to Freemasonry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimephitic', 'a.', 'Good against mephitic or deleterious gases.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimephitic', 'n.', 'A remedy against mephitic gases.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimere', 'n.', 'One of the two halves of bilaterally symmetrical animals;\n   one of any opposite symmetrical or homotypic parts in animals and\n   plants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimetabole', 'n.', 'A figure in which the same words or ideas are\n   repeated in transposed order.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimetathesis', 'n.', 'An antithesis in which the members are repeated in\n   inverse order.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimeter', 'n.', 'A modification of the quadrant, for measuring small\n   angles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimonarchic', '', 'Alt. of Antimonarchical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimonarchical', '', 'Opposed to monarchial government.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimonarchist', 'n.', 'An enemy to monarchial government.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimonate', 'n.', 'A compound of antimonic acid with a base or basic\n   radical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimonial', 'a.', 'Of or pertaining to antimony.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimonial', 'n.', 'A preparation or medicine containing antimony.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimoniated', 'a.', 'Combined or prepared with antimony; as, antimoniated\n   tartar.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimonic', 'a.', 'Pertaining to, or derived from, antimony; -- said of\n   those compounds of antimony in which this element has its highest\n   equivalence; as, antimonic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimonious', 'a.', 'Pertaining to, or derived from, antimony; -- said of\n   those compounds of antimony in which this element has an equivalence\n   next lower than the highest; as, antimonious acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimonite', 'n.', 'A compound of antimonious acid and a base or basic\n   radical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimonite', 'n.', 'Stibnite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimoniureted', 'a.', 'Combined with or containing antimony; as,\n   antimoniureted hydrogen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antimony', 'n.', 'An elementary substance, resembling a metal in its\n   appearance and physical properties, but in its chemical relations\n   belonging to the class of nonmetallic substances. Atomic weight, 120.\n   Symbol, Sb.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antinational', 'a.', 'Antagonistic to ones country or nation, or to a\n   national government.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antinephritic', 'a.', 'Counteracting, or deemed of use in, diseases of the\n   kidneys.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antinephritic', 'n.', 'An antinephritic remedy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antinomian', 'a.', 'Of or pertaining to the Antinomians; opposed to the\n   doctrine that the moral law is obligatory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antinomian', 'n.', 'One who maintains that, under the gospel dispensation,\n   the moral law is of no use or obligation, but that faith alone is\n   necessary to salvation. The sect of Antinomians originated with John\n   Agricola, in Germany, about the year 1535.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antinomianism', 'n.', 'The tenets or practice of Antinomians.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antinomist', 'n.', 'An Antinomian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antinomies', 'pl. ', 'of Antinomy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antinomy', 'n.', 'Opposition of one law or rule to another law or rule.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antinomy', 'n.', 'An opposing law or rule of any kind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antinomy', 'n.', 'A contradiction or incompatibility of thought or\n   language; -- in the Kantian philosophy, such a contradiction as arises\n   from the attempt to apply to the ideas of the reason, relations or\n   attributes which are appropriate only to the facts or the concepts of\n   experience.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiochian', 'a.', 'Pertaining to Antiochus, a contemporary with Cicero,\n   and the founder of a sect of philosophers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiochian', 'a.', 'Of or pertaining to the city of Antioch, in Syria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiodontalgic', 'a.', 'Efficacious in curing toothache.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiodontalgic', 'n.', 'A remedy for toothache.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiorgastic', 'a.', 'Tending to allay venereal excitement or desire;\n   sedative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipapal', 'a.', 'Opposed to the pope or to popery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiparallel', 'a.', 'Running in a contrary direction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiparallels', 'n. pl.', 'Straight lines or planes which make angles in\n   some respect opposite in character to those made by parallel lines or\n   planes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiparalytic', 'a.', 'Good against paralysis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiparalytic', 'n.', 'A medicine for paralysis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiparalytical', 'a.', 'Antiparalytic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipathetic', 'a.', 'Alt. of Antipathetical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipathetical', 'a.', 'Having a natural contrariety, or constitutional\n   aversion, to a thing; characterized by antipathy; -- often followed by\n   to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipathic', 'a.', 'Belonging to antipathy; opposite; contrary; allopathic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipathist', 'n.', 'One who has an antipathy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipathize', 'v. i.', 'To feel or show antipathy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipathous', 'a.', 'Having a natural contrariety; adverse; antipathetic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipathies', 'pl. ', 'of Antipathy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipathy', 'n.', 'Contrariety or opposition in feeling; settled aversion\n   or dislike; repugnance; distaste.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipathy', 'n.', 'Natural contrariety; incompatibility; repugnancy of\n   qualities; as, oil and water have antipathy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipeptone', 'n.', 'A product of gastric and pancreatic digestion,\n   differing from hemipeptone in not being decomposed by the continued\n   action of pancreatic juice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiperiodic', 'n.', 'A remedy possessing the property of preventing the\n   return of periodic paroxysms, or exacerbations, of disease, as in\n   intermittent fevers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiperistaltic', 'a.', 'Opposed to, or checking motion; acting upward; --\n   applied to an inverted action of the intestinal tube.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiperistasis', 'n.', 'Opposition by which the quality opposed asquires\n   strength; resistance or reaction roused by opposition or by the action\n   of an opposite principle or quality.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiperistatic', 'a.', 'Pertaining to antiperistasis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipetalous', 'a.', 'Standing before a petal, as a stamen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipharmic', 'a.', 'Antidotal; alexipharmic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiphlogistian', 'n.', 'An opposer of the theory of phlogiston.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiphlogistic', 'a.', 'Opposed to the doctrine of phlogiston.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiphlogistic', 'a.', 'Counteracting inflammation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiphlogistic', 'n.', 'Any medicine or diet which tends to check\n   inflammation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiphon', 'n.', 'A musical response; alternate singing or chanting. See\n   Antiphony, and Antiphone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiphon', 'n.', 'A verse said before and after the psalms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiphonal', 'a.', 'Of or pertaining to antiphony, or alternate singing;\n   sung alternately by a divided choir or opposite choirs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiphonal', 'n.', 'A book of antiphons or anthems.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiphonary', 'n.', 'A book containing a collection of antiphons; the book\n   in which the antiphons of the breviary, with their musical notes, are\n   contained.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiphone', 'n.', 'The response which one side of the choir makes to the\n   other in a chant; alternate chanting or signing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiphoner', 'n.', 'A book of antiphons.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiphonic', 'a.', 'Antiphonal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiphonies', 'pl. ', 'of Antiphony');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiphony', 'n.', 'A musical response; also, antiphonal chanting or\n   signing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiphony', 'n.', 'An anthem or psalm sung alternately by a choir or\n   congregation divided into two parts. Also figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiphrasis', 'n.', 'The use of words in a sense opposite to their proper\n   meaning; as when a court of justice is called a court of vengeance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiphrastic', 'a.', 'Alt. of Antiphrastical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiphrastical', 'a.', 'Pertaining to antiphrasis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiphthisic', 'a.', 'Relieving or curing phthisis, or consumption.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiphthisic', 'n.', 'A medicine for phthisis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiphysical', 'a.', 'Contrary to nature; unnatural.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiphysical', 'a.', 'Relieving flatulence; carminative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiplastic', 'a.', 'Diminishing plasticity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiplastic', 'a.', 'Preventing or checking the process of healing, or\n   granulation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipodagric', 'a.', 'Good against gout.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipodagric', 'n.', 'A medicine for gout.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipodal', 'a.', 'Pertaining to the antipodes; situated on the opposite\n   side of the globe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipodal', 'a.', 'Diametrically opposite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipode', 'n.', 'One of the antipodes; anything exactly opposite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipodean', 'a.', 'Pertaining to the antipodes, or the opposite side of\n   the world; antipodal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipodes', 'n.', 'Those who live on the side of the globe diametrically\n   opposite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipodes', 'n.', 'The country of those who live on the opposite side of\n   the globe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipodes', 'n.', 'Anything exactly opposite or contrary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipole', 'n.', 'The opposite pole; anything diametrically opposed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipope', 'n.', 'One who is elected, or claims to be, pope in opposition\n   to the pope canonically chosen; esp. applied to those popes who resided\n   at Avignon during the Great Schism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipsoric', 'a.', 'Of use in curing the itch.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipsoric', 'n.', 'An antipsoric remedy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiptosis', 'n.', 'The putting of one case for another.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiputrefactive', 'a.', 'Alt. of Antiputrescent');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiputrescent', 'a.', 'Counteracting, or preserving from, putrefaction;\n   antiseptic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipyic', 'a.', 'Checking or preventing suppuration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipyic', 'n.', 'An antipyic medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipyresis', 'n.', 'The condition or state of being free from fever.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipyretic', 'a.', 'Efficacious in preventing or allaying fever.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipyretic', 'n.', 'A febrifuge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipyrine', 'n.', 'An artificial alkaloid, believed to be efficient in\n   abating fever.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipyrotic', 'a.', 'Good against burns or pyrosis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antipyrotic', 'n.', 'Anything of use in preventing or healing burns or\n   pyrosis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquarian', 'a.', 'Pertaining to antiquaries, or to antiquity; as,\n   antiquarian literature.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquarian', 'n.', 'An antiquary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquarian', 'n.', 'A drawing paper of large size. See under Paper, n.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquarianism', 'n.', 'Character of an antiquary; study or love of\n   antiquities.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquarianize', 'v. i.', 'To act the part of an antiquary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquary', 'a.', 'Pertaining to antiquity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquaries', 'pl. ', 'of Antiquary');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquary', 'n.', 'One devoted to the study of ancient times through their\n   relics, as inscriptions, monuments, remains of ancient habitations,\n   statues, coins, manuscripts, etc.; one who searches for and studies the\n   relics of antiquity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquate', 'v. t.', 'To make old, or obsolete; to make antique; to make\n   old in such a degree as to put out of use; hence, to make void, or\n   abrogate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquated', 'a.', 'Grown old. Hence: Bygone; obsolete; out of use;\n   old-fashioned; as, an antiquated law.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquatedness', 'n.', 'Quality of being antiquated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquateness', 'n.', 'Antiquatedness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquation', 'n.', 'The act of making antiquated, or the state of being\n   antiquated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antique', 'a.', 'Old; ancient; of genuine antiquity; as, an antique statue.\n   In this sense it usually refers to the flourishing ages of Greece and\n   Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antique', 'a.', 'Old, as respects the present age, or a modern period of\n   time; of old fashion; antiquated; as, an antique robe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antique', 'a.', 'Made in imitation of antiquity; as, the antique style of\n   Thomsons \"Castle of Indolence.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antique', 'a.', 'Odd; fantastic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antique', 'a.', 'In general, anything very old; but in a more limited\n   sense, a relic or object of ancient art; collectively, the antique, the\n   remains of ancient art, as busts, statues, paintings, and vases.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquely', 'adv.', 'In an antique manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiqueness', 'n.', 'The quality of being antique; an appearance of ancient\n   origin and workmanship.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquist', 'n.', 'An antiquary; a collector of antiques.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquitarian', 'n.', 'An admirer of antiquity. [Used by Milton in a\n   disparaging sense.]');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquities', 'pl. ', 'of Antiquity');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquity', 'n.', 'The quality of being ancient; ancientness; great age;\n   as, a statue of remarkable antiquity; a family of great antiquity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquity', 'n.', 'Old age.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquity', 'n.', 'Ancient times; former ages; times long since past; as,\n   Cicero was an eloquent orator of antiquity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquity', 'n.', 'The ancients; the people of ancient times.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquity', 'n.', 'An old gentleman.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiquity', 'n.', 'A relic or monument of ancient times; as, a coin, a\n   statue, etc.; an ancient institution. [In this sense, usually in the\n   plural.]');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antirachitic', 'a.', 'Good against the rickets.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antirenter', 'n.', 'One opposed to the payment of rent; esp. one of those\n   who in 1840-47 resisted the collection of rents claimed by the patroons\n   from the settlers on certain manorial lands in the State of New York.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antisabbatarian', 'n.', 'One of a sect which opposes the observance of the\n   Christian Sabbath.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antisacerdotal', 'a.', 'Hostile to priests or the priesthood.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiscians', 'n. pl.', 'Alt. of Antiscii');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiscii', 'n. pl.', 'The inhabitants of the earth, living on different\n   sides of the equator, whose shadows at noon are cast in opposite\n   directions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiscoletic', 'a.', 'Alt. of Antiscolic');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiscolic', 'a.', 'Anthelmintic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiscorbutic', 'a.', 'Counteracting scurvy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiscorbutic', 'n.', 'A remedy for scurvy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiscorbutical', 'a.', 'Antiscorbutic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiscriptural', 'a.', 'Opposed to, or not in accordance with, the Holy\n   Scriptures.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antisepalous', 'a.', 'Standing before a sepal, or calyx leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiseptic', 'a.', 'Alt. of Antiseptical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiseptical', 'a.', 'Counteracting or preventing putrefaction, or a\n   putrescent tendency in the system; antiputrefactive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiseptic', 'n.', 'A substance which prevents or retards putrefaction, or\n   destroys, or protects from, putrefactive organisms; as, salt, carbolic\n   acid, alcohol, cinchona.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antiseptically', 'adv.', 'By means of antiseptics.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antislavery', 'a.', 'Opposed to slavery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antislavery', 'n.', 'Opposition to slavery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antisocial', 'a.', 'Tending to interrupt or destroy social intercourse;\n   averse to society, or hostile to its existence; as, antisocial\n   principles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antisocialist', 'n.', 'One opposed to the doctrines and practices of\n   socialists or socialism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antisolar', 'a.', 'Opposite to the sun; -- said of the point in the heavens\n   180¡ distant from the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antispasmodic', 'a.', 'Good against spasms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antispasmodic', 'n.', 'A medicine which prevents or allays spasms or\n   convulsions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antispast', 'n.', 'A foot of four syllables, the first and fourth short,\n   and the second and third long (#).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antispastic', 'a.', 'Believed to cause a revulsion of fluids or of humors\n   from one part to another.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antispastic', 'a.', 'Counteracting spasms; antispasmodic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antispastic', 'n.', 'An antispastic agent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antisplenetic', 'a.', 'Good as a remedy against disease of the spleen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antisplenetic', 'n.', 'An antisplenetic medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antistrophe', 'n.', 'In Greek choruses and dances, the returning of the\n   chorus, exactly answering to a previous strophe or movement from right\n   to left. Hence: The lines of this part of the choral song.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antistrophe', 'n.', 'The repetition of words in an inverse order; as, the\n   master of the servant and the servant of the master.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antistrophe', 'n.', 'The retort or turning of an adversarys plea against\n   him.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antistrophic', 'a.', 'Of or pertaining to an antistrophe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antistrophon', 'n.', 'An argument retorted on an opponent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antistrumatic', 'a.', 'Antistrumous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antistrumatic', 'n.', 'A medicine for scrofula.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antistrumous', 'a.', 'Good against scrofulous disorders.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antisyphilitic', 'a.', 'Efficacious against syphilis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antisyphilitic', 'n.', 'A medicine for syphilis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antitheism', 'n.', 'The doctrine of antitheists.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antitheist', 'n.', 'A disbeliever in the existence of God.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antitheses', 'pl. ', 'of Antithesis');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antithesis', 'n.', 'An opposition or contrast of words or sentiments\n   occurring in the same sentence; as, \"The prodigal robs his heir; the\n   miser robs himself.\" \"He had covertly shot at Cromwell; he how openly\n   aimed at the Queen.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antithesis', 'n.', 'The second of two clauses forming an antithesis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antithesis', 'n.', 'Opposition; contrast.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antithet', 'n.', 'An antithetic or contrasted statement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antithetic', 'a.', 'Alt. of Antithetical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antithetical', 'a.', 'Pertaining to antithesis, or opposition of words and\n   sentiments; containing, or of the nature of, antithesis; contrasted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antithetically', 'adv.', 'By way antithesis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antitoxin', 'n.', 'Alt. of Antitoxine');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antitoxine', 'n.', 'A substance (sometimes the product of a specific\n   micro-organism and sometimes naturally present in the blood or tissues\n   of an animal); capable of producing immunity from certain diseases, or\n   of counteracting the poisonous effects of pathogenic bacteria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anti-trade', 'n.', 'A tropical wind blowing steadily in a direction\n   opposite to the trade wind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antitragus', 'n.', 'A prominence on the lower posterior portion of the\n   concha of the external ear, opposite the tragus. See Ear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antitrochanter', 'n.', 'An articular surface on the ilium of birds against\n   which the great trochanter of the femur plays.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antitropal', 'a.', 'Alt. of Antitropous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antitropous', 'a.', 'At the extremity most remote from the hilum, as the\n   embryo, or inverted with respect to the seed, as the radicle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antitypal', 'a.', 'Antitypical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antitype', 'n.', 'That of which the type is the pattern or representation;\n   that which is represented by the type or symbol.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antitypical', 'a.', 'Of or pertaining to an antitype; explaining the type.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antitypous', 'a.', 'Resisting blows; hard.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antitypy', 'n.', 'Opposition or resistance of matter to force.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antivaccination', 'n.', 'Opposition to vaccination.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antivaccinationist', 'n.', 'An antivaccinist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antivaccinist', 'n.', 'One opposed to vaccination.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antivariolous', 'a.', 'Preventing the contagion of smallpox.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antivenereal', 'a.', 'Good against venereal poison; antisyphilitic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antivivisection', 'n.', 'Opposition to vivisection.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antivivisectionist', 'n.', 'One opposed to vivisection');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antizymic', 'a.', 'Preventing fermentation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antizymotic', 'a.', 'Preventing fermentation or decomposition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antizymotic', 'n.', 'An agent so used.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antler', 'n.', 'The entire horn, or any branch of the horn, of a cervine\n   animal, as of a stag.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antlered', 'a.', 'Furnished with antlers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antilae', 'pl. ', 'of Antlia');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antlia', 'n.', 'The spiral tubular proboscis of lepidopterous insects. See\n   Lepidoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ant-lion', 'n.', 'A neuropterous insect, the larva of which makes in the\n   sand a pitfall to capture ants, etc. The common American species is\n   Myrmeleon obsoletus, the European is M. formicarius.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antoeci', 'n. pl', 'Alt. of Antoecians');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antoecians', 'n. pl', 'Those who live under the same meridian, but on\n   opposite parallels of latitude, north and south of the equator.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antonomasia', 'n.', 'The use of some epithet or the name of some office,\n   dignity, or the like, instead of the proper name of the person; as when\n   his majesty is used for a king, or when, instead of Aristotle, we say,\n   the philosopher; or, conversely, the use of a proper name instead of an\n   appellative, as when a wise man is called a Solomon, or an eminent\n   orator a Cicero.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antonomastic', 'a.', 'Pertaining to, or characterized by, antonomasia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antonomasy', 'n.', 'Antonomasia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antonym', 'n.', 'A word of opposite meaning; a counterterm; -- used as a\n   correlative of synonym.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antorbital', 'a.', 'Pertaining to, or situated in, the region of the front\n   of the orbit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antorbital', 'n.', 'The antorbital bone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antorgastic', 'a.', 'See Antiorgastic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antozone', 'n.', 'A compound formerly supposed to be modification of\n   oxygen, but now known to be hydrogen dioxide; -- so called because\n   apparently antagonistic to ozone, converting it into ordinary oxygen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antral', 'a.', 'Relating to an antrum.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antre', 'n.', 'A cavern.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antrorse', 'a.', 'Forward or upward in direction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antrovert', 'v. t.', 'To bend forward.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antra', 'pl. ', 'of Antrum');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antrum', 'n.', 'A cavern or cavity, esp. an anatomical cavity or sinus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Antrustion', 'n.', 'A vassal or voluntary follower of Frankish princes in\n   their enterprises');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ant thrush', '', 'One of several species of tropical birds, of the Old\n   World, of the genus Pitta, somewhat resembling the thrushes, and\n   feeding chiefly on ants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ant thrush', '', 'See Ant bird, under Ant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anubis', 'n.', 'An Egyptian deity, the conductor of departed spirits,\n   represented by a human figure with the head of a dog or fox.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anura', 'n. pl.', 'One of the orders of amphibians characterized by the\n   absence of a tail, as the frogs and toads.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anurous', 'a.', 'Destitute of a tail, as the frogs and toads.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anury', 'n.', 'Nonsecretion or defective secretion of urine; ischury.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anus', 'n.', 'The posterior opening of the alimentary canal, through which\n   the excrements are expelled.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anvil', 'n.', 'An iron block, usually with a steel face, upon which metals\n   are hammered and shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anvil', 'n.', 'Anything resembling an anvil in shape or use.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anvil', 'n.', 'the incus. See Incus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anvil', 'v. t.', 'To form or shape on an anvil; to hammer out; as, anviled\n   armor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anxietude', 'n.', 'The state of being anxious; anxiety.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anxieties', 'pl. ', 'of Anxiety');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anxiety', 'n.', 'Concern or solicitude respecting some thing or event,\n   future or uncertain, which disturbs the mind, and keeps it in a state\n   of painful uneasiness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anxiety', 'n.', 'Eager desire.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anxiety', 'n.', 'A state of restlessness and agitation, often with general\n   indisposition and a distressing sense of oppression at the epigastrium.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anxious', 'a.', 'Full of anxiety or disquietude; greatly concerned or\n   solicitous, esp. respecting something future or unknown; being in\n   painful suspense; -- applied to persons; as, anxious for the issue of a\n   battle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anxious', 'a.', 'Accompanied with, or causing, anxiety; worrying; --\n   applied to things; as, anxious labor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anxious', 'a.', 'Earnestly desirous; as, anxious to please.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anxiously', 'adv.', 'In an anxious manner; with painful uncertainty;\n   solicitously.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anxiousness', 'n.', 'The quality of being anxious; great solicitude;\n   anxiety.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Any', 'a. & pron.', 'One indifferently, out of an indefinite number; one\n   indefinitely, whosoever or whatsoever it may be.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Any', 'a. & pron.', 'Some, of whatever kind, quantity, or number; as, are\n   there any witnesses present? are there any other houses like it?');
  INSERT INTO words (word, word_type, description) VALUES  (  'Any', 'adv.', 'To any extent; in any degree; at all.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anybody', 'n.', 'Any one out of an indefinite number of persons; anyone;\n   any person.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anybody', 'n.', 'A person of consideration or standing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anyhow', 'adv.', 'In any way or manner whatever; at any rate; in any event.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anyone', 'n.', 'One taken at random rather than by selection; anybody.\n   [Commonly written as two words.]');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anything', 'n.', 'Any object, act, state, event, or fact whatever; thing of\n   any kind; something or other; aught; as, I would not do it for\n   anything.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anything', 'n.', 'Expressing an indefinite comparison; -- with as or like.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anything', 'adv.', 'In any measure; anywise; at all.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anythingarian', 'n.', 'One who holds to no particular creed or dogma.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anyway', 'adv.', 'Alt. of Anyways');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anyways', 'adv.', 'Anywise; at all.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anywhere', 'adv.', 'In any place.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anywhither', 'adv.', 'To or towards any place.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Anywise', 'adv.', 'In any wise or way; at all.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aonian', 'a.', 'Pertaining to Aonia, in B/otia, or to the Muses, who were\n   supposed to dwell there.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aorist', 'n.', 'A tense in the Greek language, which expresses an action as\n   completed in past time, but leaves it, in other respects, wholly\n   indeterminate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aoristic', 'a.', 'Indefinite; pertaining to the aorist tense.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aorta', 'n.', 'The great artery which carries the blood from the heart to\n   all parts of the body except the lungs; the main trunk of the arterial\n   system.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aortic', 'a.', 'Of or pertaining to the aorta.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aortitis', 'n.', 'Inflammation of the aorta.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aoudad', 'n.', 'An African sheeplike quadruped (the Ammotragus tragelaphus)\n   having a long mane on the breast and fore legs. It is, perhaps, the\n   chamois of the Old Testament.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apace', 'adv.', 'With a quick pace; quick; fast; speedily.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apaches', 'n. pl.', 'A group of nomadic North American Indians including\n   several tribes native of Arizona, New Mexico, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apagoge', 'n.', 'An indirect argument which proves a thing by showing the\n   impossibility or absurdity of the contrary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apagogic', 'a.', 'Alt. of Apagogical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apagogical', 'a.', 'Proving indirectly, by showing the absurdity, or\n   impossibility of the contrary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apaid', 'a.', 'Paid; pleased.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apair', 'v. t. & i.', 'To impair or become impaired; to injure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apalachian', 'a.', 'See Appalachian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apanage', 'n.', 'Same as Appanage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apanthropy', 'n.', 'An aversion to the company of men; a love of solitude.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apar', 'n.', 'Alt. of Apara');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apara', 'n.', 'See Mataco.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aparejo', 'n.', 'A kind of pack saddle used in the American military\n   service and among the Spanish Americans. It is made of leather stuffed\n   with hay, moss, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aparithmesis', 'n.', 'Enumeration of parts or particulars.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apart', 'adv.', 'Separately, in regard to space or company; in a state of\n   separation as to place; aside.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apart', 'adv.', 'In a state of separation, of exclusion, or of distinction,\n   as to purpose, use, or character, or as a matter of thought;\n   separately; independently; as, consider the two propositions apart.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apart', 'adv.', 'Aside; away.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apart', 'adv.', 'In two or more parts; asunder; to piece; as, to take a\n   piece of machinery apart.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apartment', 'n.', 'A room in a building; a division in a house, separated\n   from others by partitions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apartment', 'n.', 'A set or suite of rooms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apartment', 'n.', 'A compartment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apartness', 'n.', 'The quality of standing apart.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apastron', 'n.', 'That point in the orbit of a double star where the\n   smaller star is farthest from its primary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apathetic', 'a.', 'Alt. of Apathetical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apathetical', 'a.', 'Void of feeling; not susceptible of deep emotion;\n   passionless; indifferent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apathetically', 'adv.', 'In an apathetic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apathist', 'n.', 'One who is destitute of feeling.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apathistical', 'a.', 'Apathetic; une motional.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apathies', 'pl. ', 'of Apathy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apathy', 'n.', 'Want of feeling; privation of passion, emotion, or\n   excitement; dispassion; -- applied either to the body or the mind. As\n   applied to the mind, it is a calmness, indolence, or state of\n   indifference, incapable of being ruffled or roused to active interest\n   or exertion by pleasure, pain, or passion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apatite', 'n.', 'Native phosphate of lime, occurring usually in six-sided\n   prisms, color often pale green, transparent or translucent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apaume', 'n.', 'See Appaume.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ape', 'n.', 'A quadrumanous mammal, esp. of the family Simiadae, having\n   teeth of the same number and form as in man, and possessing neither a\n   tail nor cheek pouches. The name is applied esp. to species of the\n   genus Hylobates, and is sometimes used as a general term for all\n   Quadrumana. The higher forms, the gorilla, chimpanzee, and ourang, are\n   often called anthropoid apes or man apes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ape', 'n.', 'One who imitates servilely (in allusion to the manners of the\n   ape); a mimic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ape', 'n.', 'A dupe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aped', 'imp. & p. p.', 'of Ape');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aping', 'p. pr. & vb. n.', 'of Ape');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ape', 'v. t.', 'To mimic, as an ape imitates human actions; to imitate or\n   follow servilely or irrationally.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apeak', 'adv. & a.', 'In a vertical line. The anchor in apeak, when the\n   cable has been sufficiently hove in to bring the ship over it, and the\n   ship is them said to be hove apeak.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apehood', 'n.', 'The state of being an ape.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apellous', 'a.', 'Destitute of skin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apennine', 'a.', 'Of, pertaining to, or designating, the Apennines, a chain\n   of mountains extending through Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apepsy', 'n.', 'Defective digestion, indigestion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aper', 'n.', 'One who apes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aperea', 'n.', 'The wild Guinea pig of Brazil (Cavia aperea).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aperient', 'a.', 'Gently opening the bowels; laxative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aperient', 'n.', 'An aperient medicine or food.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aperitive', 'a.', 'Serving to open; aperient.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apert', 'a.', 'Open; evident; undisguised.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apert', 'adv.', 'Openly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apertion', 'n.', 'The act of opening; an opening; an aperture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apertly', 'adv.', 'Openly; clearly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apertness', 'n.', 'Openness; frankness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aperture', 'n.', 'The act of opening.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aperture', 'n.', 'An opening; an open space; a gap, cleft, or chasm; a\n   passage perforated; a hole; as, an aperture in a wall.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aperture', 'n.', 'The diameter of the exposed part of the object glass of a\n   telescope or other optical instrument; as, a telescope of four-inch\n   aperture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aperies', 'pl. ', 'of Apery');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apery', 'n.', 'A place where apes are kept.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apery', 'n.', 'The practice of aping; an apish action.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apetalous', 'a.', 'Having no petals, or flower leaves. [See Illust. under\n   Anther].');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apetalousness', 'n.', 'The state of being apetalous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apexes', 'pl. ', 'of Apex');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apices', 'pl. ', 'of Apex');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apex', 'n.', 'The tip, top, point, or angular summit of anything; as, the\n   apex of a mountain, spire, or cone; the apex, or tip, of a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apex', 'n.', 'The end or edge of a vein nearest the surface.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphaeresis', 'n.', 'Same as Apheresis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphakia', 'n.', 'An anomalous state of refraction caused by the absence of\n   the crystalline lens, as after operations for cataract. The remedy is\n   the use of powerful convex lenses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphakial', 'a.', 'Pertaining to aphakia; as, aphakial eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphaniptera', 'n. pl.', 'A group of wingless insects, of which the flea in\n   the type. See Flea.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphanipterous', 'a.', 'Of or pertaining to the Aphaniptera.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphanite', 'n.', 'A very compact, dark-colored /ock, consisting of\n   hornblende, or pyroxene, and feldspar, but neither of them in\n   perceptible grains.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphanitic', 'a.', 'Resembling aphanite; having a very fine-grained\n   structure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphasia', 'n.', 'Alt. of Aphasy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphasy', 'n.', 'Loss of the power of speech, or of the appropriate use of\n   words, the vocal organs remaining intact, and the intelligence being\n   preserved. It is dependent on injury or disease of the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphasic', 'a.', 'Pertaining to, or affected by, aphasia; speechless.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphelia', 'pl. ', 'of Aphelion');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphelion', 'n.', 'That point of a planets or comets orbit which is most\n   distant from the sun, the opposite point being the perihelion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apheliotropic', 'a.', 'Turning away from the sun; -- said of leaves, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apheliotropism', 'n.', 'The habit of bending from the sunlight; -- said of\n   certain plants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphemia', 'n.', 'Loss of the power of speaking, while retaining the power\n   of writing; -- a disorder of cerebral origin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apheresis', 'n.', 'The dropping of a letter or syllable from the beginning\n   of a word; e. g., cute for acute.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apheresis', 'n.', 'An operation by which any part is separated from the\n   rest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphesis', 'n.', 'The loss of a short unaccented vowel at the beginning of a\n   word; -- the result of a phonetic process; as, squire for esquire.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphetic', 'a.', 'Shortened by dropping a letter or a syllable from the\n   beginning of a word; as, an aphetic word or form.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphetism', 'n.', 'An aphetized form of a word.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphetize', 'v. t.', 'To shorten by aphesis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphid', 'n.', 'One of the genus Aphis; an aphidian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphides', 'n. pl.', 'See Aphis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphidian', 'a.', 'Of or pertaining to the family Aphidae.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphidian', 'n.', 'One of the aphides; an aphid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphidivorous', '', 'Devouring aphides; aphidophagous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphidophagous', 'a.', 'Feeding upon aphides, or plant lice, as do beetles\n   of the family Coccinellidae.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphilanthropy', 'n.', 'Want of love to mankind; -- the opposite of\n   philanthropy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphides', 'pl. ', 'of Aphis');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphis', 'n.', 'A genus of insects belonging to the order Hemiptera and\n   family Aphidae, including numerous species known as plant lice and\n   green flies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphis lion', '', 'The larva of the lacewinged flies (Chrysopa); which feeds\n   voraciously upon aphids. The name is also applied to the larvae of the\n   ladybugs (Coccinella).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphlogistic', 'a.', 'Flameless; as, an aphlogistic lamp, in which a coil of\n   wire is kept in a state of continued ignition by alcohol, without\n   flame.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphonia', 'n.', 'Alt. of Aphony');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphony', 'n.', 'Loss of voice or vocal utterance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphonic', 'a.', 'Alt. of Aphonous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphonous', 'a.', 'Without voice; voiceless; nonvocal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphorism', 'n.', 'A comprehensive maxim or principle expressed in a few\n   words; a sharply defined sentence relating to abstract truth rather\n   than to practical matters.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphorismatic', 'a.', 'Alt. of Aphorismic');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphorismic', 'a.', 'Pertaining to aphorisms, or having the form of an\n   aphorism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphorismer', 'n.', 'A dealer in aphorisms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphorist', 'n.', 'A writer or utterer of aphorisms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphoristic', 'a.', 'Alt. of Aphoristical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphoristical', 'a.', 'In the form of, or of the nature of, an aphorism; in\n   the form of short, unconnected sentences; as, an aphoristic style.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphoristically', 'adv.', 'In the form or manner of aphorisms; pithily.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphorize', 'v. i.', 'To make aphorisms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphrite', 'n.', 'See under Calcite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphrodisiac', 'a.', 'Alt. of Aphrodisiacal');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphrodisiacal', 'a.', 'Exciting venereal desire; provocative to venery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphrodisiac', 'n.', 'That which (as a drug, or some kinds of food) excites\n   to venery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphrodisian', 'a.', 'Pertaining to Aphrodite or Venus. \"Aphrodisian dames\"\n   [that is, courtesans].');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphrodite', 'n.', 'The Greek goddess of love, corresponding to the Venus of\n   the Romans.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphrodite', 'n.', 'A large marine annelid, covered with long, lustrous,\n   golden, hairlike setae; the sea mouse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphrodite', 'n.', 'A beautiful butterfly (Argunnis Aphrodite) of the United\n   States.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphroditic', 'a.', 'Venereal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphtha', 'n.', 'One of the whitish specks called aphthae.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphtha', 'n.', 'The disease, also called thrush.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphthae', 'n. pl.', 'Roundish pearl-colored specks or flakes in the mouth,\n   on the lips, etc., terminating in white sloughs. They are commonly\n   characteristic of thrush.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphthoid', 'a.', 'Of the nature of aphthae; resembling thrush.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphthong', 'n.', 'A letter, or a combination of letters, employed in\n   spelling a word, but in the pronunciation having no sound.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphthous', 'a.', 'Pertaining to, or caused by, aphthae; characterized by\n   aphtae; as, aphthous ulcers; aphthous fever.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aphyllous', 'a.', 'Destitute of leaves, as the broom rape, certain\n   euphorbiaceous plants, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apiaceous', 'a.', 'Umbelliferous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apian', 'a.', 'Belonging to bees.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apiarian', 'a.', 'Of or relating to bees.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apiarist', 'n.', 'One who keeps an apiary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apiary', 'n.', 'A place where bees are kept; a stand or shed for bees; a\n   beehouse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apical', 'a.', 'At or belonging to an apex, tip, or summit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apices', 'n. pl.', 'See Apex.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apician', 'a.', 'Belonging to Apicius, a notorious Roman epicure; hence\n   applied to whatever is peculiarly refined or dainty and expensive in\n   cookery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apicular', 'a.', 'Situated at, or near, the apex; apical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apiculate', 'a.', 'Alt. of Apiculated');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apiculated', 'a.', 'Terminated abruptly by a small, distinct point, as a\n   leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apiculture', 'n.', 'Rearing of bees for their honey and wax.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apiece', 'adv.', 'Each by itself; by the single one; to each; as the share\n   of each; as, these melons cost a shilling apiece.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apieces', 'adv.', 'In pieces or to pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apiked', 'a.', 'Trimmed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apiol', 'n.', 'An oily liquid derived from parsley.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apiologist', 'n.', 'A student of bees.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apis', 'n.', 'A genus of insects of the order Hymenoptera, including the\n   common honeybee (Apis mellifica) and other related species. See\n   Honeybee.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apish', 'a.', 'Having the qualities of an ape; prone to imitate in a\n   servile manner. Hence: Apelike; fantastically silly; foppish; affected;\n   trifling.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apishly', 'adv.', 'In an apish manner; with servile imitation; foppishly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apishness', 'n.', 'The quality of being apish; mimicry; foppery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apitpat', 'adv.', 'With quick beating or palpitation; pitapat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aplacental', 'a.', 'Belonging to the Aplacentata; without placenta.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aplacentata', 'n. pl.', 'Mammals which have no placenta.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aplacophora', 'n. pl.', 'A division of Amphineura in which the body is\n   naked or covered with slender spines or setae, but is without shelly\n   plates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aplanatic', 'a.', 'Having two or more parts of different curvatures, so\n   combined as to remove spherical aberration; -- said of a lens.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aplanatism', 'n.', 'Freedom from spherical aberration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aplastic', 'a.', 'Not plastic or easily molded.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aplomb', 'n.', 'Assurance of manner or of action; self-possession.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aplotomy', 'n.', 'Simple incision.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aplustre', 'n.', 'An ornamental appendage of wood at the ships stern,\n   usually spreading like a fan and curved like a birds feather.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aplysia', 'n.', 'A genus of marine mollusks of the order Tectibranchiata;\n   the sea hare. Some of the species when disturbed throw out a deep\n   purple liquor, which colors the water to some distance. See Illust. in\n   Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apneumona', 'n. pl.', 'An order of holothurians in which the internal\n   respiratory organs are wanting; -- called also Apoda or Apodes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apnoea', 'n.', 'Partial privation or suspension of breath; suffocation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apo', '', 'A prefix from a Greek preposition. It usually signifies from,\n   away from, off, or asunder, separate; as, in apocope (a cutting off);\n   apostate, apostle (one sent away); apocarpous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocalypse', 'n.', 'The revelation delivered to St. John, in the isle of\n   Patmos, near the close of the first century, forming the last book of\n   the New Testament.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocalypse', 'n.', 'Anything viewed as a revelation; a disclosure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocalyptic', 'a.', 'Alt. of Apocalyptical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocalyptical', 'a.', 'Of or pertaining to a revelation, or, specifically,\n   to the Revelation of St. John; containing, or of the nature of, a\n   prophetic revelation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocalyptic', 'n.', 'Alt. of Apocalyptist');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocalyptist', 'n.', 'The writer of the Apocalypse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocalyptically', 'adv.', 'By revelation; in an apocalyptic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocarpous', 'a.', 'Either entirely or partially separate, as the carpels\n   of a compound pistil; -- opposed to syncarpous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocopate', 'v. t.', 'To cut off or drop; as, to apocopate a word, or the\n   last letter, syllable, or part of a word.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocopate', 'a.', 'Alt. of Apocopated');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocopated', 'a.', 'Shortened by apocope; as, an apocopate form.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocopation', 'n.', 'Shortening by apocope; the state of being apocopated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocope', 'n.', 'The cutting off, or omission, of the last letter,\n   syllable, or part of a word.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocope', 'n.', 'A cutting off; abscission.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocrisiary', 'n.', 'Alt. of Apocrisiarius');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocrisiarius', 'n.', 'A delegate or deputy; especially, the popes nuncio\n   or legate at Constantinople.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocrustic', 'a.', 'Astringent and repellent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocrustic', 'n.', 'An apocrustic medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocryphas', 'pl. ', 'of Apocrypha');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocrypha', 'n. pl.', 'Something, as a writing, that is of doubtful\n   authorship or authority; -- formerly used also adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocrypha', 'n. pl.', 'Specif.: Certain writings which are received by some\n   Christians as an authentic part of the Holy Scriptures, but are\n   rejected by others.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocryphal', 'a.', 'Pertaining to the Apocrypha.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocryphal', 'a.', 'Not canonical. Hence: Of doubtful authority; equivocal;\n   mythic; fictitious; spurious; false.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocryphalist', 'n.', 'One who believes in, or defends, the Apocrypha.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocryphally', 'adv.', 'In an apocryphal manner; mythically; not\n   indisputably.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocryphalness', 'n.', 'The quality or state of being apocryphal;\n   doubtfulness of credit or genuineness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocynaceous', 'a.', 'Alt. of Apocyneous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocyneous', 'a.', 'Belonging to, or resembling, a family of plants, of\n   which the dogbane (Apocynum) is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apocynin', 'n.', 'A bitter principle obtained from the dogbane (Apocynum\n   cannabinum).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apod', 'n.', 'Alt. of Apodal');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apodal', 'n.', 'Without feet; footless.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apodal', 'n.', 'Destitute of the ventral fin, as the eels.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apods', 'pl. ', 'of Apode');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apodes', 'pl. ', 'of Apode');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apod', 'n.', 'Alt. of Apode');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apode', 'n.', 'One of certain animals that have no feet or footlike organs;\n   esp. one of certain fabulous birds which were said to have no feet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apoda', 'n.', 'A group of cirripeds, destitute of footlike organs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apoda', 'n.', 'An order of Amphibia without feet. See Ophiomorpha.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apoda', 'n.', 'A group of worms without appendages, as the leech.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apodan', 'a.', 'Apodal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apodeictic', 'a.', 'Alt. of Apodictical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apodictic', 'a.', 'Alt. of Apodictical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apodeictical', 'a.', 'Alt. of Apodictical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apodictical', 'a.', 'Self-evident; intuitively true; evident beyond\n   contradiction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apodeictically', 'adv.', 'Alt. of Apodictically');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apodictically', 'adv.', 'So as to be evident beyond contradiction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apodeme', 'n.', 'One of the processes of the shell which project inwards\n   and unite with one another, in the thorax of many Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apodes', 'n. pl.', 'An order of fishes without ventral fins, including the\n   eels.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apodes', 'n. pl.', 'A group of holothurians destitute of suckers. See\n   Apneumona.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apodictic', 'a.', 'Same as Apodeictic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apodixis', 'n.', 'Full demonstration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apodosis', 'n.', 'The consequent clause or conclusion in a conditional\n   sentence, expressing the result, and thus distinguished from the\n   protasis or clause which expresses a condition. Thus, in the sentence,\n   \"Though he slay me, yet will I trust in him,\" the former clause is the\n   protasis, and the latter the apodosis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apodous', 'a.', 'Apodal; apod.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apodyterium', 'n.', 'The apartment at the entrance of the baths, or in the\n   palestra, where one stripped; a dressing room.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apogaic', 'a.', 'Apogean.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apogamic', 'a.', 'Relating to apogamy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apogamy', 'n.', 'The formation of a bud in place of a fertilized ovule or\n   oospore.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apogeal', 'a.', 'Apogean.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apogean', 'a.', 'Connected with the apogee; as, apogean (neap) tides, which\n   occur when the moon has passed her apogee.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apogee', 'n.', 'That point in the orbit of the moon which is at the\n   greatest distance from the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apogee', 'n.', 'Fig.: The farthest or highest point; culmination.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apogeotropic', 'a.', 'Bending away from the ground; -- said of leaves, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apogeotropism', 'n.', 'The apogeotropic tendency of some leaves, and other\n   parts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apograph', 'n.', 'A copy or transcript.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apohyal', 'a.', 'Of or pertaining to a portion of the horn of the hyoid\n   bone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apoise', 'adv.', 'Balanced.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apolar', 'a.', 'Having no radiating processes; -- applied particularly to\n   certain nerve cells.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apolaustic', 'a.', 'Devoted to enjoyment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apollinarian', 'a.', 'In honor of Apollo; as, the Apollinarian games.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apollinarian', 'n.', 'A follower of Apollinaris, Bishop of Laodicea in the\n   fourth century, who denied the proper humanity of Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apollinaris water', '', 'An effervescing alkaline mineral water used as a\n   table beverage. It is obtained from a spring in Apollinarisburg, near\n   Bonn.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apollo', 'n.', 'A deity among the Greeks and Romans. He was the god of\n   light and day (the \"sun god\"); of archery, prophecy, medicine, poetry,\n   and music, etc., and was represented as the model of manly grace and\n   beauty; -- called also Phebus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apollonian', 'a.', 'Alt. of Apollonic');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apollonic', 'a.', 'Of, pertaining to, or resembling, Apollo.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apollyon', 'n.', 'The Destroyer; -- a name used (Rev. ix. 11) for the angel\n   of the bottomless pit, answering to the Hebrew Abaddon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apologer', 'n.', 'A teller of apologues.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apologetic', 'a.', 'Alt. of Apologetical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apologetical', 'a.', 'Defending by words or arguments; said or written in\n   defense, or by way of apology; regretfully excusing; as, an apologetic\n   essay.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apologetically', 'adv.', 'By way of apology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apologetics', 'n.', 'That branch of theology which defends the Holy\n   Scriptures, and sets forth the evidence of their divine authority.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apologist', 'n.', 'One who makes an apology; one who speaks or writes in\n   defense of a faith, a cause, or an institution; especially, one who\n   argues in defense of Christianity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apologized', 'imp. & p. p.', 'of Apologize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apologizing', 'p. pr. & vb. n.', 'of Apologize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apologize', 'v. i.', 'To make an apology or defense.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apologize', 'v. i.', 'To make an apology or excuse; to make acknowledgment\n   of some fault or offense, with expression of regret for it, by way of\n   amends; -- with for; as, my correspondent apologized for not answering\n   my letter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apologize', 'v. t.', 'To defend.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apologizer', 'n.', 'One who makes an apology; an apologist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apologue', 'n.', 'A story or relation of fictitious events, intended to\n   convey some moral truth; a moral fable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apologies', 'pl. ', 'of Apology');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apology', 'n.', 'Something said or written in defense or justification of\n   what appears to others wrong, or of what may be liable to\n   disapprobation; justification; as, Tertullians Apology for\n   Christianity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apology', 'n.', 'An acknowledgment intended as an atonement for some\n   improper or injurious remark or act; an admission to another of a wrong\n   or discourtesy done him, accompanied by an expression of regret.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apology', 'n.', 'Anything provided as a substitute; a makeshift.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apology', 'v. i.', 'To offer an apology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apomecometer', 'n.', 'An instrument for measuring the height of objects.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apomecometry', 'n.', 'The art of measuring the distance of objects afar\n   off.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apomorphia', 'n.', 'Alt. of Apomorphine');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apomorphine', 'n.', 'A crystalline alkaloid obtained from morphia. It is a\n   powerful emetic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aponeuroses', 'pl. ', 'of Aponeurosis');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aponeurosis', 'n.', 'Any one of the thicker and denser of the deep fasciae\n   which cover, invest, and the terminations and attachments of, many\n   muscles. They often differ from tendons only in being flat and thin.\n   See Fascia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aponeurotic', 'a.', 'Of or pertaining to an aponeurosis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aponeurotomy', 'n.', 'Dissection of aponeuroses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apopemptic', 'a.', 'Sung or addressed to one departing; valedictory; as,\n   apoplectic songs or hymns.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apophasis', 'n.', 'A figure by which a speaker formally declines to take\n   notice of a favorable point, but in such a manner as to produce the\n   effect desired. [For example, see Mark Antonys oration. Shak., Julius\n   Caesar, iii. 2.]');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apophlegmatic', 'a.', 'Designed to facilitate discharges of phlegm or mucus\n   from mouth or nostrils.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apophlegmatic', 'n.', 'An apophlegmatic medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apophlegmatism', 'n.', 'The action of apophlegmatics.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apophlegmatism', 'n.', 'An apophlegmatic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apophlegmatizant', 'n.', 'An apophlegmatic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apophthegm', 'n.', 'See Apothegm.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apophthegmatic', 'a.', 'Alt. of Apophthegmatical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apophthegmatical', 'a.', 'Same as Apothegmatic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apophyge', 'n.', 'The small hollow curvature given to the top or bottom of\n   the shaft of a column where it expands to meet the edge of the fillet;\n   -- called also the scape.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apophyllite', 'n.', 'A mineral relating to the zeolites, usually occurring\n   in square prisms or octahedrons with pearly luster on the cleavage\n   surface. It is a hydrous silicate of calcium and potassium.');
  INSERT INTO words (word, word_type, description) VALUES  (  '-ses', 'pl. ', 'of Apophysis');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apophysis', 'n.', 'A marked prominence or process on any part of a bone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apophysis', 'n.', 'An enlargement at the top of a pedicel or stem, as seen\n   in certain mosses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apoplectic', 'a.', 'Alt. of Apoplectical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apoplectical', 'a.', 'Relating to apoplexy; affected with, inclined to, or\n   symptomatic of, apoplexy; as, an apoplectic person, medicine, habit or\n   temperament, symptom, fit, or stroke.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apoplectic', 'n.', 'One liable to, or affected with, apoplexy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apoplectiform', 'a.', 'Alt. of Apoplectoid');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apoplectoid', 'a.', 'Resembling apoplexy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apoplex', 'n.', 'Apoplexy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apoplexed', 'a.', 'Affected with apoplexy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apoplexy', 'n.', 'Sudden diminution or loss of consciousness, sensation,\n   and voluntary motion, usually caused by pressure on the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aporetical', 'a.', 'Doubting; skeptical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aporias', 'pl. ', 'of Aporia');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aporia', 'n.', 'A figure in which the speaker professes to be at a loss\n   what course to pursue, where to begin to end, what to say, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aporosa', 'n. pl.', 'A group of corals in which the coral is not porous; --\n   opposed to Perforata.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aporose', 'a.', 'Without pores.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aport', 'adv.', 'On or towards the port or left side; -- said of the helm.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aposiopesis', 'n.', 'A figure of speech in which the speaker breaks off\n   suddenly, as if unwilling or unable to state what was in his mind; as,\n   \"I declare to you that his conduct -- but I can not speak of that,\n   here.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apositic', 'a.', 'Destroying the appetite, or suspending hunger.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostasies', 'pl. ', 'of Apostasy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostasy', 'n.', 'An abandonment of what one has voluntarily professed; a\n   total desertion of departure from ones faith, principles, or party;\n   esp., the renunciation of a religious faith; as, Julians apostasy from\n   Christianity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostate', 'n.', 'One who has forsaken the faith, principles, or party, to\n   which he before adhered; esp., one who has forsaken his religion for\n   another; a pervert; a renegade.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostate', 'n.', 'One who, after having received sacred orders, renounces\n   his clerical profession.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostate', 'a.', 'Pertaining to, or characterized by, apostasy; faithless\n   to moral allegiance; renegade.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostate', 'v. i.', 'To apostatize.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostatic', 'a.', 'Apostatical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostatical', 'a.', 'Apostate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostatized', 'imp. & p. p.', 'of Apostatize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostatizing', 'p. pr. & vb. n.', 'of Apostatize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostatize', 'v. i.', 'To renounce totally a religious belief once\n   professed; to forsake ones church, the faith or principles once held,\n   or the party to which one has previously adhered.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostemate', 'v. i.', 'To form an abscess; to swell and fill with pus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostemation', 'n.', 'The formation of an aposteme; the process of\n   suppuration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostematous', 'a.', 'Pertaining to, or partaking of the nature of, an\n   aposteme.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aposteme', 'n.', 'An abscess; a swelling filled with purulent matter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'A posteriori', '', 'Characterizing that kind of reasoning which derives\n   propositions from the observation of facts, or by generalizations from\n   facts arrives at principles and definitions, or infers causes from\n   effects. This is the reverse of a priori reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'A posteriori', '', 'Applied to knowledge which is based upon or derived\n   from facts through induction or experiment; inductive or empirical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostil', 'n.', 'Alt. of Apostille');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostille', 'n.', 'A marginal note on a letter or other paper; an\n   annotation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostle', 'n.', 'Literally: One sent forth; a messenger. Specifically: One\n   of the twelve disciples of Christ, specially chosen as his companions\n   and witnesses, and sent forth to preach the gospel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostle', 'n.', 'The missionary who first plants the Christian faith in any\n   part of the world; also, one who initiates any great moral reform, or\n   first advocates any important belief; one who has extraordinary success\n   as a missionary or reformer; as, Dionysius of Corinth is called the\n   apostle of France, John Eliot the apostle to the Indians, Theobald\n   Mathew the apostle of temperance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostle', 'n.', 'A brief letter dimissory sent by a court appealed from to\n   the superior court, stating the case, etc.; a paper sent up on appeals\n   in the admiralty courts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostleship', 'n.', 'The office or dignity of an apostle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostolate', 'n.', 'The dignity, office, or mission, of an apostle;\n   apostleship.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostolate', 'n.', 'The dignity or office of the pope, as the holder of the\n   apostolic see.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostolic', 'a.', 'Alt. of Apostolical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostolical', 'a.', 'Pertaining to an apostle, or to the apostles, their\n   times, or their peculiar spirit; as, an apostolical mission; the\n   apostolic age.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostolical', 'a.', 'According to the doctrines of the apostles; delivered\n   or taught by the apostles; as, apostolic faith or practice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostolical', 'a.', 'Of or pertaining to the pope or the papacy; papal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostolic', 'n.', 'A member of one of certain ascetic sects which at\n   various times professed to imitate the practice of the apostles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostolically', 'adv.', 'In an apostolic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostolicalness', 'n.', 'Apostolicity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostolicism', 'n.', 'Alt. of Apostolicity');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostolicity', 'n.', 'The state or quality of being apostolical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostrophe', 'n.', 'A figure of speech by which the orator or writer\n   suddenly breaks off from the previous method of his discourse, and\n   addresses, in the second person, some person or thing, absent or\n   present; as, Miltons apostrophe to Light at the beginning of the third\n   book of \"Paradise Lost.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostrophe', 'n.', 'The contraction of a word by the omission of a letter\n   or letters, which omission is marked by the character [] placed where\n   the letter or letters would have been; as, calld for called.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostrophe', 'n.', 'The mark [] used to denote that a word is contracted\n   (as in neer for never, cant for can not); and as a sign of the\n   possessive, singular and plural; as, a boys hat, boys hats. In the\n   latter use it originally marked the omission of the letter e.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostrophic', 'a.', 'Pertaining to an apostrophe, grammatical or\n   rhetorical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostrophize', 'p. pr. & vb. n.', 'To address by apostrophe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostrophize', 'p. pr. & vb. n.', 'To contract by omitting a letter or\n   letters; also, to mark with an apostrophe () or apostrophes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostrophize', 'v. i.', 'To use the rhetorical figure called apostrophe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apostume', 'n.', 'See Aposteme.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apotactite', 'n.', 'One of a sect of ancient Christians, who, in supposed\n   imitation of the first believers, renounced all their possessions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apotelesm', 'n.', 'The result or issue.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apotelesm', 'n.', 'The calculation and explanation of a nativity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apotelesmatic', 'a.', 'Relating to the casting of horoscopes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apotelesmatic', 'a.', 'Relating to an issue of fulfillment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apothecaries', 'pl. ', 'of Apothecary');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apothecary', 'n.', 'One who prepares and sells drugs or compounds for\n   medicinal purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apothecia', 'pl. ', 'of Apothecium');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apothecium', 'n.', 'The ascigerous fructification of lichens, forming\n   masses of various shapes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apothegm', 'n.', 'Alt. of Apophthegm');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apophthegm', 'n.', 'A short, pithy, and instructive saying; a terse remark,\n   conveying some important truth; a sententious precept or maxim.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apothegmatic', 'a.', 'Alt. of Apothegmatical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apothegmatical', 'a.', 'Pertaining to, or in the manner of, an apothegm;\n   sententious; pithy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apothegmatist', 'n.', 'A collector or maker of apothegms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apothegmatize', 'v. i.', 'To utter apothegms, or short and sententious\n   sayings.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apothem', 'n.', 'The perpendicular from the center to one of the sides of a\n   regular polygon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apothem', 'n.', 'A deposit formed in a liquid extract of a vegetable\n   substance by exposure to the air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apotheoses', 'pl. ', 'of Apotheosis');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apotheosis', 'n. pl.', 'The act of elevating a mortal to the rank of, and\n   placing him among, \"the gods;\" deification.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apotheosis', 'n. pl.', 'Glorification; exaltation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apotheosize', 'v. t.', 'To exalt to the dignity of a deity; to declare to\n   be a god; to deify; to glorify.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apothesis', 'n.', 'A place on the south side of the chancel in the\n   primitive churches, furnished with shelves, for books, vestments, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apothesis', 'n.', 'A dressing room connected with a public bath.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apotome', 'n.', 'The difference between two quantities commensurable only\n   in power, as between Ã2 and 1, or between the diagonal and side of a\n   square.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apotome', 'n.', 'The remaining part of a whole tone after a smaller\n   semitone has been deducted from it; a major semitone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apozem', 'n.', 'A decoction or infusion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apozemical', 'a.', 'Pertaining to, or resembling, a decoction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appair', 'v. t. & i.', 'To impair; to grow worse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appalachian', 'a.', 'Of or pertaining to a chain of mountains in the United\n   States, commonly called the Allegheny mountains.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appalled', 'imp. & p. p.', 'of Appall');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appalling', 'p. pr. & vb. n.', 'of Appall');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appall', 'a.', 'To make pale; to blanch.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appall', 'a.', 'To weaken; to enfeeble; to reduce; as, an old appalled\n   wight.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appall', 'a.', 'To depress or discourage with fear; to impress with fear in\n   such a manner that the mind shrinks, or loses its firmness; to overcome\n   with sudden terror or horror; to dismay; as, the sight appalled the\n   stoutest heart.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appall', 'v. i.', 'To grow faint; to become weak; to become dismayed or\n   discouraged.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appall', 'v. i.', 'To lose flavor or become stale.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appall', 'n.', 'Terror; dismay.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appalling', 'a.', 'Such as to appall; as, an appalling accident.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appallment', 'n.', 'Depression occasioned by terror; dismay.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appanage', 'n.', 'The portion of land assigned by a sovereign prince for\n   the subsistence of his younger sons.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appanage', 'n.', 'A dependency; a dependent territory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appanage', 'n.', 'That which belongs to one by custom or right; a natural\n   adjunct or accompaniment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appanagist', 'n.', 'A prince to whom an appanage has been granted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparaillyng', 'v.', 'Preparation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparatus', 'pl. ', 'of Apparatus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparatuses', 'pl. ', 'of Apparatus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparatus', 'n.', 'Things provided as means to some end.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparatus', 'n.', 'Hence: A full collection or set of implements, or\n   utensils, for a given duty, experimental or operative; any complex\n   instrument or appliance, mechanical or chemical, for a specific action\n   or operation; machinery; mechanism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparatus', 'n.', 'A collection of organs all of which unite in a common\n   function; as, the respiratory apparatus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparel', 'n.', 'External clothing; vesture; garments; dress; garb;\n   external habiliments or array.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparel', 'n.', 'A small ornamental piece of embroidery worn on albs and\n   some other ecclesiastical vestments.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparel', 'n.', 'The furniture of a ship, as masts, sails, rigging,\n   anchors, guns, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appareled', 'imp. & p. p.', 'of Apparel');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparelled', '', 'of Apparel');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appareling', 'p. pr. & vb. n.', 'of Apparel');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparelling', '', 'of Apparel');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparel', 'v. t.', 'To make or get (something) ready; to prepare.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparel', 'v. t.', 'To furnish with apparatus; to equip; to fit out.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparel', 'v. t.', 'To dress or clothe; to attire.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparel', 'v. t.', 'To dress with external ornaments; to cover with\n   something ornamental; to deck; to embellish; as, trees appareled with\n   flowers, or a garden with verdure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparence', 'n.', 'Appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparency', 'n.', 'Appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparency', 'n.', 'Apparentness; state of being apparent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparency', 'n.', 'The position of being heir apparent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparent', 'a.', 'Capable of being seen, or easily seen; open to view;\n   visible to the eye; within sight or view.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparent', 'a.', 'Clear or manifest to the understanding; plain; evident;\n   obvious; known; palpable; indubitable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparent', 'a.', 'Appearing to the eye or mind (distinguished from, but not\n   necessarily opposed to, true or real); seeming; as the apparent motion\n   or diameter of the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparent', 'n.', 'An heir apparent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparently', 'adv.', 'Visibly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparently', 'adv.', 'Plainly; clearly; manifestly; evidently.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparently', 'adv.', 'Seemingly; in appearance; as, a man may be apparently\n   friendly, yet malicious in heart.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparentness', 'n.', 'Plainness to the eye or the mind; visibleness;\n   obviousness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparition', 'n.', 'The act of becoming visible; appearance; visibility.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparition', 'n.', 'The thing appearing; a visible object; a form.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparition', 'n.', 'An unexpected, wonderful, or preternatural appearance;\n   a ghost; a specter; a phantom.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparition', 'n.', 'The first appearance of a star or other luminary after\n   having been invisible or obscured; -- opposed to occultation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparitional', 'a.', 'Pertaining to an apparition or to apparitions;\n   spectral.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparitor', 'n.', 'Formerly, an officer who attended magistrates and judges\n   to execute their orders.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apparitor', 'n.', 'A messenger or officer who serves the process of an\n   ecclesiastical court.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appaume', 'n.', 'A hand open and extended so as to show the palm.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appay', 'v. t.', 'To pay; to satisfy or appease.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeach', 'v. t.', 'To impeach; to accuse; to asperse; to inform against;\n   to reproach.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeacher', 'n.', 'An accuser.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeachment', 'n.', 'Accusation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appealed', 'imp. & p. p.', 'of Appeal');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appealing', 'p. pr. & vb. n.', 'of Appeal');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeal', 'v. t.', 'To make application for the removal of (a cause) from an\n   inferior to a superior judge or court for a rehearing or review on\n   account of alleged injustice or illegality in the trial below. We say,\n   the cause was appealed from an inferior court.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeal', 'v. t.', 'To charge with a crime; to accuse; to institute a\n   private criminal prosecution against for some heinous crime; as, to\n   appeal a person of felony.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeal', 'v. t.', 'To summon; to challenge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeal', 'v. t.', 'To invoke.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeal', 'v. t.', 'To apply for the removal of a cause from an inferior to\n   a superior judge or court for the purpose of reexamination of for\n   decision.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeal', 'v. t.', 'To call upon another to decide a question controverted,\n   to corroborate a statement, to vindicate ones rights, etc.; as, I\n   appeal to all mankind for the truth of what is alleged. Hence: To call\n   on one for aid; to make earnest request.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeal', 'v. t.', 'An application for the removal of a cause or suit from\n   an inferior to a superior judge or court for reexamination or review.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeal', 'v. t.', 'The mode of proceeding by which such removal is\n   effected.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeal', 'v. t.', 'The right of appeal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeal', 'v. t.', 'An accusation; a process which formerly might be\n   instituted by one private person against another for some heinous crime\n   demanding punishment for the particular injury suffered, rather than\n   for the offense against the public.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeal', 'v. t.', 'An accusation of a felon at common law by one of his\n   accomplices, which accomplice was then called an approver. See\n   Approvement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeal', 'v. t.', 'A summons to answer to a charge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeal', 'v. t.', 'A call upon a person or an authority for proof or\n   decision, in ones favor; reference to another as witness; a call for\n   help or a favor; entreaty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeal', 'v. t.', 'Resort to physical means; recourse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appealable', 'a.', 'Capable of being appealed against; that may be removed\n   to a higher tribunal for decision; as, the cause is appealable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appealable', 'a.', 'That may be accused or called to answer by appeal; as,\n   a criminal is appealable for manslaughter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appealant', 'n.', 'An appellant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appealer', 'n.', 'One who makes an appeal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appealing', 'a.', 'That appeals; imploring.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeared', 'imp. & p. p.', 'of Appear');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appearing', 'p. pr. & vb. n.', 'of Appear');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appear', 'v. i.', 'To come or be in sight; to be in view; to become\n   visible.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appear', 'v. i.', 'To come before the public; as, a great writer appeared\n   at that time.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appear', 'v. i.', 'To stand in presence of some authority, tribunal, or\n   superior person, to answer a charge, plead a cause, or the like; to\n   present ones self as a party or advocate before a court, or as a\n   person to be tried.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appear', 'v. i.', 'To become visible to the apprehension of the mind; to be\n   known as a subject of observation or comprehension, or as a thing\n   proved; to be obvious or manifest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appear', 'v. i.', 'To seem; to have a certain semblance; to look.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appear', 'n.', 'Appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appearance', 'n.', 'The act of appearing or coming into sight; the act of\n   becoming visible to the eye; as, his sudden appearance surprised me.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appearance', 'n.', 'A thing seed; a phenomenon; a phase; an apparition; as,\n   an appearance in the sky.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appearance', 'n.', 'Personal presence; exhibition of the person; look;\n   aspect; mien.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appearance', 'n.', 'Semblance, or apparent likeness; external show. pl.\n   Outward signs, or circumstances, fitted to make a particular impression\n   or to determine the judgment as to the character of a person or a\n   thing, an act or a state; as, appearances are against him.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appearance', 'n.', 'The act of appearing in a particular place, or in\n   society, a company, or any proceedings; a coming before the public in a\n   particular character; as, a person makes his appearance as an\n   historian, an artist, or an orator.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appearance', 'n.', 'Probability; likelihood.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appearance', 'n.', 'The coming into court of either of the parties; the\n   being present in court; the coming into court of a party summoned in an\n   action, either by himself or by his attorney, expressed by a formal\n   entry by the proper officer to that effect; the act or proceeding by\n   which a party proceeded against places himself before the court, and\n   submits to its jurisdiction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appearer', 'n.', 'One who appears.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appearingly', 'adv.', 'Apparently.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeasable', 'a.', 'Capable of being appeased or pacified; placable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appealed', 'imp. & p. p.', 'of Appease');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeasing', 'p. pr. & vb. n.', 'of Appease');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appease', 'v. t.', 'To make quiet; to calm; to reduce to a state of peace;\n   to still; to pacify; to dispel (anger or hatred); as, to appease the\n   tumult of the ocean, or of the passions; to appease hunger or thirst.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeasement', 'n.', 'The act of appeasing, or the state of being appeased;\n   pacification.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeaser', 'n.', 'One who appeases; a pacifier.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appeasive', 'a.', 'Tending to appease.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellable', 'a.', 'Appealable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellancy', 'n.', 'Capability of appeal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellant', 'a.', 'Relating to an appeal; appellate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellant', 'n.', 'One who accuses another of felony or treason.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellant', 'n.', 'One who appeals, or asks for a rehearing or review of a\n   cause by a higher tribunal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellant', 'n.', 'A challenger.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellant', 'n.', 'One who appealed to a general council against the bull\n   Unigenitus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellant', 'n.', 'One who appeals or entreats.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellate', 'a.', 'Pertaining to, or taking cognizance of, appeals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellate', 'n.', 'A person or prosecuted for a crime. [Obs.] See Appellee.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellation', 'n.', 'The act of appealing; appeal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellation', 'n.', 'The act of calling by a name.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellation', 'n.', 'The word by which a particular person or thing is\n   called and known; name; title; designation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellative', 'a.', 'Pertaining to a common name; serving as a distinctive\n   denomination; denominative; naming.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellative', 'a.', 'Common, as opposed to proper; denominative of a class.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellative', 'n.', 'A common name, in distinction from a proper name. A\n   common name, or appellative, stands for a whole class, genus, or\n   species of beings, or for universal ideas. Thus, tree is the name of\n   all plants of a particular class; plant and vegetable are names of\n   things that grow out of the earth. A proper name, on the other hand,\n   stands for a single thing; as, Rome, Washington, Lake Erie.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellative', 'n.', 'An appellation or title; a descriptive name.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellatively', 'adv.', 'After the manner of nouns appellative; in a manner\n   to express whole classes or species; as, Hercules is sometimes used\n   appellatively, that is, as a common name, to signify a strong man.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellativeness', 'n.', 'The quality of being appellative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellatory', 'a.', 'Containing an appeal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellee', 'n.', 'The defendant in an appeal; -- opposed to appellant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellee', 'n.', 'The person who is appealed against, or accused of crime;\n   -- opposed to appellor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellor', 'n.', 'The person who institutes an appeal, or prosecutes\n   another for a crime.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appellor', 'n.', 'One who confesses a felony committed and accuses his\n   accomplices.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appenage', 'n.', 'See Appanage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appended', 'imp. & p. p.', 'of Append');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appending', 'p. pr. & vb. n.', 'of Append');
  INSERT INTO words (word, word_type, description) VALUES  (  'Append', 'v. t.', 'To hang or attach to, as by a string, so that the thing\n   is suspended; as, a seal appended to a record; the inscription was\n   appended to the column.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Append', 'v. t.', 'To add, as an accessory to the principal thing; to\n   annex; as, notes appended to this chapter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendage', 'n.', 'Something appended to, or accompanying, a principal or\n   greater thing, though not necessary to it, as a portico to a house.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendage', 'n.', 'A subordinate or subsidiary part or organ; an external\n   organ or limb, esp. of the articulates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendaged', 'a.', 'Furnished with, or supplemented by, an appendage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendance', 'n.', 'Something appendant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendant', 'v. t.', 'Hanging; annexed; adjunct; concomitant; as, a seal\n   appendant to a paper.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendant', 'v. t.', 'Appended by prescription, that is, a personal usage\n   for a considerable time; -- said of a thing of inheritance belonging to\n   another inheritance which is superior or more worthy; as, an advowson,\n   common, etc. , which may be appendant to a manor, common of fishing to\n   a freehold, a seat in church to a house.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendant', 'n.', 'Anything attached to another as incidental or\n   subordinate to it.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendant', 'n.', 'A inheritance annexed by prescription to a superior\n   inheritance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendence', 'n.', 'Alt. of Appendency');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendency', 'n.', 'State of being appendant; appendance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendical', 'a.', 'Of or like an appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendicate', 'v. t.', 'To append.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendication', 'n.', 'An appendage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendicitis', 'n.', 'Inflammation of the vermiform appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendicle', 'n.', 'A small appendage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendicular', 'a.', 'Relating to an appendicle; appendiculate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendicularia', 'n.', 'A genus of small free-swimming Tunicata, shaped\n   somewhat like a tadpole, and remarkable for resemblances to the larvae\n   of other Tunicata. It is the type of the order Copelata or Larvalia.\n   See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendiculata', 'n. pl.', 'An order of annelids; the Polych/ta.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendiculate', 'a.', 'Having small appendages; forming an appendage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendixes', 'pl. ', 'of Appendix');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendices', 'pl. ', 'of Appendix');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendix', 'n.', 'Something appended or added; an appendage, adjunct, or\n   concomitant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appendix', 'n.', 'Any literary matter added to a book, but not necessarily\n   essential to its completeness, and thus distinguished from supplement,\n   which is intended to supply deficiencies and correct inaccuracies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appension', 'n.', 'The act of appending.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apperceive', 'v. t.', 'To perceive; to comprehend.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apperception', 'n.', 'The minds perception of itself as the subject or\n   actor in its own states; perception that reflects upon itself;\n   sometimes, intensified or energetic perception.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apperil', 'n.', 'Peril.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appertained', 'imp. & p. p.', 'of Appertain');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appertaining', 'p. pr. & vb. n.', 'of Appertain');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appertain', 'v. i.', 'To belong or pertain, whether by right, nature,\n   appointment, or custom; to relate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appertainment', 'n.', 'That which appertains to a person; an appurtenance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appertinance', 'n.', 'Alt. of Appertinence');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appertinence', 'n.', 'See Appurtenance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appertinent', 'a.', 'Belonging; appertaining.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appertinent', 'n.', 'That which belongs to something else; an appurtenant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appete', 'v. t.', 'To seek for; to desire.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appetence', 'n.', 'A longing; a desire; especially an ardent desire;\n   appetite; appetency.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appetencies', 'pl. ', 'of Appetency');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appetency', 'n.', 'Fixed and strong desire; esp. natural desire; a craving;\n   an eager appetite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appetency', 'n.', 'Specifically: An instinctive inclination or propensity\n   in animals to perform certain actions, as in the young to suck, in\n   aquatic fowls to enter into water and to swim; the tendency of an\n   organized body to seek what satisfies the wants of its organism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appetency', 'n.', 'Natural tendency; affinity; attraction; -- used of\n   inanimate objects.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appetent', 'a.', 'Desiring; eagerly desirous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appetibility', 'n.', 'The quality of being desirable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appetible', 'a.', 'Desirable; capable or worthy of being the object of\n   desire.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appetite', 'n.', 'The desire for some personal gratification, either of the\n   body or of the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appetite', 'n.', 'Desire for, or relish of, food or drink; hunger.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appetite', 'n.', 'Any strong desire; an eagerness or longing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appetite', 'n.', 'Tendency; appetency.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appetite', 'n.', 'The thing desired.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appetition', 'n.', 'Desire; a longing for, or seeking after, something.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appetitive', 'a.', 'Having the quality of desiring gratification; as,\n   appetitive power or faculty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appetize', 'v. t.', 'To make hungry; to whet the appetite of.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appetizer', 'n.', 'Something which creates or whets an appetite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appetizing', 'a.', 'Exciting appetite; as, appetizing food.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appetizing', 'adv.', 'So as to excite appetite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appian', 'a.', 'Of or pertaining to Appius.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applauded', 'imp. & p. p.', 'of Applaud');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applauding', 'p. pr. & vb. n.', 'of Applaud');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applaud', 'v. t.', 'To show approval of by clapping the hands, acclamation,\n   or other significant sign.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applaud', 'v. t.', 'To praise by words; to express approbation of; to\n   commend; to approve.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applaud', 'v. i.', 'To express approbation loudly or significantly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applauder', 'n.', 'One who applauds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applausable', 'a.', 'Worthy of applause; praiseworthy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applause', 'n.', 'The act of applauding; approbation and praise publicly\n   expressed by clapping the hands, stamping or tapping with the feet,\n   acclamation, huzzas, or other means; marked commendation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applausive', 'a.', 'Expressing applause; approbative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apple', 'n.', 'The fleshy pome or fruit of a rosaceous tree (Pyrus malus)\n   cultivated in numberless varieties in the temperate zones.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apple', 'n.', 'Any tree genus Pyrus which has the stalk sunken into the\n   base of the fruit; an apple tree.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apple', 'n.', 'Any fruit or other vegetable production resembling, or\n   supposed to resemble, the apple; as, apple of love, or love apple (a\n   tomato); balsam apple, egg apple, oak apple.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apple', 'n.', 'Anything round like an apple; as, an apple of gold.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apple', 'v. i.', 'To grow like an apple; to bear apples.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apple-faced', 'a.', 'Having a round, broad face, like an apple.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apple-jack', 'n.', 'Apple brandy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apple-john', 'n.', 'A kind of apple which by keeping becomes much withered;\n   -- called also Johnapple.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apple pie', '', 'A pie made of apples (usually sliced or stewed) with spice\n   and sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apple-squire', 'n.', 'A pimp; a kept gallant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appliable', 'a.', 'Applicable; also, compliant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appliance', 'n.', 'The act of applying; application; [Obs.] subservience.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appliance', 'n.', 'The thing applied or used as a means to an end; an\n   apparatus or device; as, to use various appliances; a mechanical\n   appliance; a machine with its appliances.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applicability', 'n.', 'The quality of being applicable or fit to be\n   applied.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applicable', 'a.', 'Capable of being applied; fit or suitable to be\n   applied; having relevance; as, this observation is applicable to the\n   case under consideration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applicancy', 'n.', 'The quality or state of being applicable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applicant', 'n.', 'One who apples for something; one who makes request; a\n   petitioner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applicate', 'a.', 'Applied or put to some use.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applicate', 'v. i.', 'To apply.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Application', 'n.', 'The act of applying or laying on, in a literal sense;\n   as, the application of emollients to a diseased limb.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Application', 'n.', 'The thing applied.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Application', 'n.', 'The act of applying as a means; the employment of\n   means to accomplish an end; specific use.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Application', 'n.', 'The act of directing or referring something to a\n   particular case, to discover or illustrate agreement or disagreement,\n   fitness, or correspondence; as, I make the remark, and leave you to\n   make the application; the application of a theory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Application', 'n.', 'Hence, in specific uses: (a) That part of a sermon or\n   discourse in which the principles before laid down and illustrated are\n   applied to practical uses; the \"moral\" of a fable. (b) The use of the\n   principles of one science for the purpose of enlarging or perfecting\n   another; as, the application of algebra to geometry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Application', 'n.', 'The capacity of being practically applied or used;\n   relevancy; as, a rule of general application.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Application', 'n.', 'The act of fixing the mind or closely applying ones\n   self; assiduous effort; close attention; as, to injure the health by\n   application to study.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Application', 'n.', 'The act of making request of soliciting; as, an\n   application for an office; he made application to a court of chancery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Application', 'n.', 'A request; a document containing a request; as, his\n   application was placed on file.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applicative', 'a.', 'Capable of being applied or used; applying;\n   applicatory; practical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applicatorily', 'adv.', 'By way of application.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applicatory', 'a.', 'Having the property of applying; applicative;\n   practical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applicatory', 'n.', 'That which applies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appliedly', 'adv.', 'By application.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applier', 'n.', 'He who, or that which, applies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appliment', 'n.', 'Application.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applique', 'a.', 'Ornamented with a pattern (which has been cut out of\n   another color or stuff) applied or transferred to a foundation; as,\n   applique lace; applique work.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applotted', 'imp. & p. p.', 'of Applot');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applotting', 'p. pr. & vb. n.', 'of Applot');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applot', 'v. t.', 'To divide into plots or parts; to apportion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applotment', 'n.', 'Apportionment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applied', 'imp. & p. p.', 'of Apply');
  INSERT INTO words (word, word_type, description) VALUES  (  'Applying', 'p. pr. & vb. n.', 'of Apply');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apply', 'v. t.', 'To lay or place; to put or adjust (one thing to another);\n   -- with to; as, to apply the hand to the breast; to apply medicaments\n   to a diseased part of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apply', 'v. t.', 'To put to use; to use or employ for a particular purpose,\n   or in a particular case; to appropriate; to devote; as, to apply money\n   to the payment of a debt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apply', 'v. t.', 'To make use of, declare, or pronounce, as suitable,\n   fitting, or relative; as, to apply the testimony to the case; to apply\n   an epithet to a person.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apply', 'v. t.', 'To fix closely; to engage and employ diligently, or with\n   attention; to attach; to incline.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apply', 'v. t.', 'To direct or address.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apply', 'v. t.', 'To betake; to address; to refer; -- used reflexively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apply', 'v. t.', 'To busy; to keep at work; to ply.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apply', 'v. t.', 'To visit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apply', 'v. i.', 'To suit; to agree; to have some connection, agreement, or\n   analogy; as, this argument applies well to the case.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apply', 'v. i.', 'To make request; to have recourse with a view to gain\n   something; to make application. (to); to solicit; as, to apply to a\n   friend for information.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apply', 'v. i.', 'To ply; to move.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apply', 'v. i.', 'To apply or address ones self; to give application; to\n   attend closely (to).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appoggiatura', 'n.', 'A passing tone preceding an essential tone, and\n   borrowing the time it occupies from that; a short auxiliary or grace\n   note one degree above or below the principal note unless it be of the\n   same harmony; -- generally indicated by a note of smaller size, as in\n   the illustration above. It forms no essential part of the harmony.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appointed', 'imp. & p. p.', 'of Appoint');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appointing', 'p. pr. & vb. n.', 'of Appoint');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appoint', 'v. t.', 'To fix with power or firmness; to establish; to mark\n   out.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appoint', 'v. t.', 'To fix by a decree, order, command, resolve, decision,\n   or mutual agreement; to constitute; to ordain; to prescribe; to fix the\n   time and place of.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appoint', 'v. t.', 'To assign, designate, or set apart by authority.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appoint', 'v. t.', 'To furnish in all points; to provide with everything\n   necessary by way of equipment; to equip; to fit out.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appoint', 'v. t.', 'To point at by way, or for the purpose, of censure or\n   commendation; to arraign.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appoint', 'v. t.', 'To direct, designate, or limit; to make or direct a new\n   disposition of, by virtue of a power contained in a conveyance; -- said\n   of an estate already conveyed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appoint', 'v. i.', 'To ordain; to determine; to arrange.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appointable', 'a.', 'Capable of being appointed or constituted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appointee', 'v. t.', 'A person appointed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appointee', 'v. t.', 'A person in whose favor a power of appointment is\n   executed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appointer', 'n.', 'One who appoints, or executes a power of appointment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appointive', 'a.', 'Subject to appointment; as, an appointive office.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appointment', 'n.', 'The act of appointing; designation of a person to hold\n   an office or discharge a trust; as, he erred by the appointment of\n   unsuitable men.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appointment', 'n.', 'The state of being appointed to som/ service or\n   office; an office to which one is appointed; station; position; an, the\n   appointment of treasurer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appointment', 'n.', 'Stipulation; agreement; the act of fixing by mutual\n   agreement. Hence:: Arrangement for a meeting; engagement; as, they made\n   an appointment to meet at six.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appointment', 'n.', 'Decree; direction; established order or constitution;\n   as, to submit to the divine appointments.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appointment', 'n.', 'The exercise of the power of designating (under a\n   \"power of appointment\") a person to enjoy an estate or other specific\n   property; also, the instrument by which the designation is made.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appointment', 'n.', 'Equipment, furniture, as for a ship or an army;\n   whatever is appointed for use and management; outfit; (pl.) the\n   accouterments of military officers or soldiers, as belts, sashes,\n   swords.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appointment', 'n.', 'An allowance to a person, esp. to a public officer; a\n   perquisite; -- properly only in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appointment', 'n.', 'A honorary part or exercise, as an oration, etc., at a\n   public exhibition of a college; as, to have an appointment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appointor', 'n.', 'The person who selects the appointee. See Appointee, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apporter', 'n.', 'A bringer in; an importer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apportioned', 'imp. & p. p.', 'of Apportion');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apportioning', 'p. pr. & vb. n.', 'of Apportion');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apportion', 'v. t.', 'To divide and assign in just proportion; to divide\n   and distribute proportionally; to portion out; to allot; as, to\n   apportion undivided rights; to apportion time among various\n   employments.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apportionateness', 'n.', 'The quality of being apportioned or in\n   proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apportioner', 'n.', 'One who apportions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apportionment', 'n.', 'The act of apportioning; a dividing into just\n   proportions or shares; a division or shares; a division and assignment,\n   to each proprietor, of his just portion of an undivided right or\n   property.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appose', 'v. t.', 'To place opposite or before; to put or apply (one thing\n   to another).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appose', 'v. t.', 'To place in juxtaposition or proximity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appose', 'v. t.', 'To put questions to; to examine; to try. [Obs.] See\n   Pose.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apposed', 'a.', 'Placed in apposition; mutually fitting, as the mandibles\n   of a birds beak.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apposer', 'n.', 'An examiner; one whose business is to put questions.\n   Formerly, in the English Court of Exchequer, an officer who audited the\n   sheriffs accounts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apposite', 'a.', 'Very applicable; well adapted; suitable or fit; relevant;\n   pat; -- followed by to; as, this argument is very apposite to the case.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apposition', 'n.', 'The act of adding; application; accretion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apposition', 'n.', 'The putting of things in juxtaposition, or side by\n   side; also, the condition of being so placed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apposition', 'n.', 'The state of two nouns or pronouns, put in the same\n   case, without a connecting word between them; as, I admire Cicero, the\n   orator. Here, the second noun explains or characterizes the first.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appositional', 'a.', 'Pertaining to apposition; put in apposition\n   syntactically.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appositive', 'a.', 'Of or relating to apposition; in apposition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appositive', 'n.', 'A noun in apposition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appraisable', 'a.', 'Capable of being appraised.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appraisal', 'n.', 'A valuation by an authorized person; an appraisement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appraised', 'imp. & p. p.', 'of Appraise');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appraising', 'p. pr. & vb. n.', 'of Appraise');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appraise', 'v. t.', 'To set a value; to estimate the worth of, particularly\n   by persons appointed for the purpose; as, to appraise goods and\n   chattels.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appraise', 'v. t.', 'To estimate; to conjecture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appraise', 'v. t.', 'To praise; to commend.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appraisement', 'n.', 'The act of setting the value; valuation by an\n   appraiser; estimation of worth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appraiser', 'n.', 'One who appraises; esp., a person appointed and sworn to\n   estimate and fix the value of goods or estates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprecation', 'n.', 'Earnest prayer; devout wish.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprecatory', 'a.', 'Praying or wishing good.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appreciable', 'a.', 'Capable of being appreciated or estimated; large\n   enough to be estimated; perceptible; as, an appreciable quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appreciant', 'a.', 'Appreciative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appreciated', 'imp. & p. p.', 'of Appreciate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appreciating', 'p. pr. & vb. n.', 'of Appreciate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appreciate', 'v. t.', 'To set a price or value on; to estimate justly; to\n   value.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appreciate', 'v. t.', 'To raise the value of; to increase the market price\n   of; -- opposed to depreciate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appreciate', 'v. t.', 'To be sensible of; to distinguish.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appreciate', 'v. i.', 'To rise in value. [See note under Rise, v. i.]');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appreciatingly', 'adv.', 'In an appreciating manner; with appreciation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appreciation', 'n.', 'A just valuation or estimate of merit, worth, weight,\n   etc.; recognition of excellence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appreciation', 'n.', 'Accurate perception; true estimation; as, an\n   appreciation of the difficulties before us; an appreciation of colors.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appreciation', 'n.', 'A rise in value; -- opposed to depreciation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appreciative', 'a.', 'Having or showing a just or ready appreciation or\n   perception; as, an appreciative audience.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appreciativeness', 'n.', 'The quality of being appreciative; quick\n   recognition of excellence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appreciator', 'n.', 'One who appreciates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appreciatory', 'a.', 'Showing appreciation; appreciative; as, appreciatory\n   commendation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehended', 'imp. & p. p.', 'of Apprehend');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehending', 'p. pr. & vb. n.', 'of Apprehend');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehend', 'v. t.', 'To take or seize; to take hold of.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehend', 'v. t.', 'Hence: To take or seize (a person) by legal process;\n   to arrest; as, to apprehend a criminal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehend', 'v. t.', 'To take hold of with the understanding, that is, to\n   conceive in the mind; to become cognizant of; to understand; to\n   recognize; to consider.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehend', 'v. t.', 'To know or learn with certainty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehend', 'v. t.', 'To anticipate; esp., to anticipate with anxiety,\n   dread, or fear; to fear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehend', 'v. i.', 'To think, believe, or be of opinion; to understand;\n   to suppose.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehend', 'v. i.', 'To be apprehensive; to fear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehender', 'n.', 'One who apprehends.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehensibiity', 'n.', 'The quality of being apprehensible.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehensible', 'a.', 'Capable of being apprehended or conceived.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehension', 'n.', 'The act of seizing or taking hold of; seizure; as,\n   the hand is an organ of apprehension.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehension', 'n.', 'The act of seizing or taking by legal process;\n   arrest; as, the felon, after his apprehension, escaped.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehension', 'n.', 'The act of grasping with the intellect; the\n   contemplation of things, without affirming, denying, or passing any\n   judgment; intellection; perception.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehension', 'n.', 'Opinion; conception; sentiment; idea.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehension', 'n.', 'The faculty by which ideas are conceived;\n   understanding; as, a man of dull apprehension.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehension', 'n.', 'Anticipation, mostly of things unfavorable; distrust\n   or fear at the prospect of future evil.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehensive', 'a.', 'Capable of apprehending, or quick to do so; apt;\n   discerning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehensive', 'a.', 'Knowing; conscious; cognizant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehensive', 'a.', 'Relating to the faculty of apprehension.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehensive', 'a.', 'Anticipative of something unfavorable fearful of\n   what may be coming; in dread of possible harm; in expectation of evil.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehensive', 'a.', 'Sensible; feeling; perceptive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehensively', 'adv.', 'In an apprehensive manner; with apprehension of\n   danger.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprehensiveness', 'n.', 'The quality or state of being apprehensive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprentice', 'n.', 'One who is bound by indentures or by legal agreement to\n   serve a mechanic, or other person, for a certain time, with a view to\n   learn the art, or trade, in which his master is bound to instruct him.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprentice', 'n.', 'One not well versed in a subject; a tyro.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprentice', 'n.', 'A barrister, considered a learner of law till of\n   sixteen years standing, when he might be called to the rank of\n   serjeant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprenticed', 'imp. & p. p.', 'of Apprentice');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprenticing', 'p. pr. & vb. n.', 'of Apprentice');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprentice', 'v. t.', 'To bind to, or put under the care of, a master, for\n   the purpose of instruction in a trade or business.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprenticeage', 'n.', 'Apprenticeship.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprenticehood', 'n.', 'Apprenticeship.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprenticeship', 'n.', 'The service or condition of an apprentice; the\n   state in which a person is gaining instruction in a trade or art, under\n   legal agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprenticeship', 'n.', 'The time an apprentice is serving (sometimes seven\n   years, as from the age of fourteen to twenty-one).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appressed', 'a.', 'Alt. of Apprest');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprest', 'a.', 'Pressed close to, or lying against, something for its\n   whole length, as against a stem,');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprised', 'imp. & p. p.', 'of Apprise');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprising', 'p. pr. & vb. n.', 'of Apprise');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprise', 'v. t.', 'To give notice, verbal or written; to inform; --\n   followed by of; as, we will apprise the general of an intended attack;\n   he apprised the commander of what he had done.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprise', 'n.', 'Notice; information.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprizal', 'n.', 'See Appraisal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprize', 'v. t.', 'To appraise; to value; to appreciate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprizement', 'n.', 'Appraisement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprizer', 'n.', 'An appraiser.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apprizer', 'n.', 'A creditor for whom an appraisal is made.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approached', 'imp. & p. p.', 'of Approach');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approaching', 'p. pr. & vb. n.', 'of Approach');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approach', 'v. i.', 'To come or go near, in place or time; to draw nigh; to\n   advance nearer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approach', 'v. i.', 'To draw near, in a figurative sense; to make advances;\n   to approximate; as, he approaches to the character of the ablest\n   statesman.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approach', 'v. t.', 'To bring near; to cause to draw near; to advance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approach', 'v. t.', 'To come near to in place, time, or character; to draw\n   nearer to; as, to approach the city; to approach my cabin; he\n   approached the age of manhood.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approach', 'v. t.', 'To take approaches to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approach', 'v. i.', 'The act of drawing near; a coming or advancing near.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approach', 'v. i.', 'A access, or opportunity of drawing near.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approach', 'v. i.', 'Movements to gain favor; advances.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approach', 'v. i.', 'A way, passage, or avenue by which a place or\n   buildings can be approached; an access.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approach', 'v. i.', 'The advanced works, trenches, or covered roads made by\n   besiegers in their advances toward a fortress or military post.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approach', 'v. i.', 'See Approaching.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approachability', 'n.', 'The quality of being approachable;\n   approachableness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approachable', 'a.', 'Capable of being approached; accessible; as,\n   approachable virtue.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approachableness', 'n.', 'The quality or state of being approachable;\n   accessibility.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approacher', 'n.', 'One who approaches.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approaching', 'n.', 'The act of ingrafting a sprig or shoot of one tree\n   into another, without cutting it from the parent stock; -- called,\n   also, inarching and grafting by approach.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approachless', 'a.', 'Impossible to be approached.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approachment', 'n.', 'Approach.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approbate', 'a.', 'Approved.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approbate', 'v. t.', 'To express approbation of; to approve; to sanction\n   officially.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approbation', 'n.', 'Proof; attestation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approbation', 'n.', 'The act of approving; an assenting to the propriety of\n   a thing with some degree of pleasure or satisfaction; approval;\n   sanction; commendation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approbation', 'n.', 'Probation or novitiate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approbative', 'a.', 'Approving, or implying approbation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approbativeness', 'n.', 'The quality of being approbative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approbativeness', 'n.', 'Love of approbation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approbator', 'n.', 'One who approves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approbatory', 'a.', 'Containing or expressing approbation; commendatory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appromt', 'v. t.', 'To quicken; to prompt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approof', 'n.', 'Trial; proof.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approof', 'n.', 'Approval; commendation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropinquate', 'v. i.', 'To approach.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropinquation', 'n.', 'A drawing nigh; approach.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropinquity', 'n.', 'Nearness; propinquity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropre', 'v. t.', 'To appropriate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropriable', 'a.', 'Capable of being appropriated, set apart,\n   sequestered, or assigned exclusively to a particular use.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropriament', 'n.', 'What is peculiarly ones own; peculiar\n   qualification.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropriate', 'a.', 'Set apart for a particular use or person. Hence:\n   Belonging peculiarly; peculiar; suitable; fit; proper.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropriated', 'imp. & p. p.', 'of Appropriate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropriating', 'p. pr. & vb. n.', 'of Appropriate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropriate', 'v. t.', 'To take to ones self in exclusion of others; to\n   claim or use as by an exclusive right; as, let no man appropriate the\n   use of a common benefit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropriate', 'v. t.', 'To set apart for, or assign to, a particular person\n   or use, in exclusion of all others; -- with to or for; as, a spot of\n   ground is appropriated for a garden; to appropriate money for the\n   increase of the navy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropriate', 'v. t.', 'To make suitable; to suit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropriate', 'v. t.', 'To annex, as a benefice, to a spiritual\n   corporation, as its property.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropriate', 'n.', 'A property; attribute.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropriately', 'adv.', 'In an appropriate or proper manner; fitly;\n   properly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropriateness', 'n.', 'The state or quality of being appropriate;\n   peculiar fitness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropriation', 'n.', 'The act of setting apart or assigning to a\n   particular use or person, or of taking to ones self, in exclusion of\n   all others; application to a special use or purpose, as of a piece of\n   ground for a park, or of money to carry out some object.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropriation', 'n.', 'Anything, especially money, thus set apart.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropriation', 'n.', 'The severing or sequestering of a benefice to the\n   perpetual use of a spiritual corporation. Blackstone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropriation', 'n.', 'The application of payment of money by a debtor to\n   his creditor, to one of several debts which are due from the former to\n   the latter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropriative', 'a.', 'Appropriating; making, or tending to, appropriation;\n   as, an appropriative act.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropriator', 'n.', 'One who appropriates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appropriator', 'n.', 'A spiritual corporation possessed of an appropriated\n   benefice; also, an impropriator.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approvable', 'a.', 'Worthy of being approved; meritorious.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approval', 'n.', 'Approbation; sanction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approvance', 'n.', 'Approval.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approved', 'imp. & p. p.', 'of Approve');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approving', 'p. pr. & vb. n.', 'of Approve');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approve', 'v. t.', 'To show to be real or true; to prove.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approve', 'v. t.', 'To make proof of; to demonstrate; to prove or show\n   practically.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approve', 'v. t.', 'To sanction officially; to ratify; to confirm; as, to\n   approve the decision of a court-martial.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approve', 'v. t.', 'To regard as good; to commend; to be pleased with; to\n   think well of; as, we approve the measured of the administration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approve', 'v. t.', 'To make or show to be worthy of approbation or\n   acceptance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approve', 'v. t.', 'To make profit of; to convert to ones own profit; --\n   said esp. of waste or common land appropriated by the lord of the\n   manor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approvedly', 'adv.', 'So as to secure approbation; in an approved manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approvement', 'n.', 'Approbation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approvement', 'n.', 'a confession of guilt by a prisoner charged with\n   treason or felony, together with an accusation of his accomplish and a\n   giving evidence against them in order to obtain his own pardon. The\n   term is no longer in use; it corresponded to what is now known as\n   turning kings (or queens) evidence in England, and states evidence\n   in the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approvement', 'n.', 'Improvement of common lands, by inclosing and\n   converting them to the uses of husbandry for the advantage of the lord\n   of the manor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approver', 'n.', 'One who approves. Formerly, one who made proof or trial.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approver', 'n.', 'An informer; an accuser.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approver', 'n.', 'One who confesses a crime and accuses another. See 1st\n   Approvement, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approver', 'v. t.', 'A bailiff or steward; an agent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approving', 'a.', 'Expressing approbation; commending; as, an approving\n   smile.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approximate', 'a.', 'Approaching; proximate; nearly resembling.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approximate', 'a.', 'Near correctness; nearly exact; not perfectly\n   accurate; as, approximate results or values.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approximated', 'imp. & p. p.', 'of Approximate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approximating', 'p. pr. & vb. n.', 'of Approximate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approximate', 'v. t.', 'To carry or advance near; to cause to approach.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approximate', 'v. t.', 'To come near to; to approach.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approximate', 'v. i.', 'To draw; to approach.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approximately', 'adv.', 'With approximation; so as to approximate; nearly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approximation', 'n.', 'The act of approximating; a drawing, advancing or\n   being near; approach; also, the result of approximating.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approximation', 'n.', 'An approach to a correct estimate, calculation, or\n   conception, or to a given quantity, quality, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approximation', 'n.', 'A continual approach or coming nearer to a result;\n   as, to solve an equation by approximation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approximation', 'n.', 'A value that is nearly but not exactly correct.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approximative', 'a.', 'Approaching; approximate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Approximator', 'n.', 'One who, or that which, approximates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appui', 'n.', 'A support or supporter; a stay; a prop.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appulse', 'n.', 'A driving or running towards; approach; impulse; also, the\n   act of striking against.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appulse', 'n.', 'The near approach of one heavenly body to another, or to\n   the meridian; a coming into conjunction; as, the appulse of the moon to\n   a star, or of a star to the meridian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appulsion', 'n.', 'A driving or striking against; an appulse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appulsive', 'a.', 'Striking against; impinging; as, the appulsive influence\n   of the planets.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appulsively', 'adv.', 'By appulsion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appurtenance', 'n.', 'That which belongs to something else; an adjunct; an\n   appendage; an accessory; something annexed to another thing more\n   worthy; in common parlance and legal acceptation, something belonging\n   to another thing as principal, and which passes as incident to it, as a\n   right of way, or other easement to land; a right of common to pasture,\n   an outhouse, barn, garden, or orchard, to a house or messuage. In a\n   strict legal sense, land can never pass as an appurtenance to land.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appurtenant', 'a.', 'Annexed or pertaining to some more important thing;\n   accessory; incident; as, a right of way appurtenant to land or\n   buildings.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Appurtenant', 'n.', 'Something which belongs or appertains to another\n   thing; an appurtenance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apricate', 'v. t. & i.', 'To bask in the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aprication', 'n.', 'Basking in the sun.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apricot', 'n.', 'A fruit allied to the plum, of an orange color, oval\n   shape, and delicious taste; also, the tree (Prunus Armeniaca of\n   Linnaeus) which bears this fruit. By cultivation it has been introduced\n   throughout the temperate zone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'April', 'n.', 'The fourth month of the year.');
  INSERT INTO words (word, word_type, description) VALUES  (  'April', 'n.', 'Fig.: With reference to April being the month in which\n   vegetation begins to put forth, the variableness of its weather, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'A priori', '', 'Characterizing that kind of reasoning which deduces\n   consequences from definitions formed, or principles assumed, or which\n   infers effects from causes previously known; deductive or deductively.\n   The reverse of a posteriori.');
  INSERT INTO words (word, word_type, description) VALUES  (  'A priori', '', 'Applied to knowledge and conceptions assumed, or\n   presupposed, as prior to experience, in order to make experience\n   rational or possible.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apriorism', 'n.', 'An a priori principle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apriority', 'n.', 'The quality of being innate in the mind, or prior to\n   experience; a priori reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aprocta', 'n. pl.', 'A group of Turbellaria in which there is no anal\n   aperture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aproctous', 'a.', 'Without an anal office.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apron', 'n.', 'An article of dress, of cloth, leather, or other stuff, worn\n   on the fore part of the body, to keep the clothes clean, to defend them\n   from injury, or as a covering. It is commonly tied at the waist by\n   strings.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apron', 'n.', 'Something which by its shape or use suggests an apron;');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apron', 'n.', 'The fat skin covering the belly of a goose or duck.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apron', 'n.', 'A piece of leather, or other material, to be spread before a\n   person riding on an outside seat of a vehicle, to defend him from the\n   rain, snow, or dust; a boot.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apron', 'n.', 'A leaden plate that covers the vent of a cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apron', 'n.', 'A piece of carved timber, just above the foremost end of the\n   keel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apron', 'n.', 'A platform, or flooring of plank, at the entrance of a dock,\n   against which the dock gates are shut.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apron', 'n.', 'A flooring of plank before a dam to cause the water to make\n   a gradual descent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apron', 'n.', 'The piece that holds the cutting tool of a planer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apron', 'n.', 'A strip of lead which leads the drip of a wall into a\n   gutter; a flashing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apron', 'n.', 'The infolded abdomen of a crab.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aproned', 'a.', 'Wearing an apron.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apronfuls', 'pl. ', 'of Apronful');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apronful', 'n.', 'The quantity an apron can hold.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apronless', 'a.', 'Without an apron.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apron man', '', 'A man who wears an apron; a laboring man; a mechanic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apron string', '', 'The string of an apron.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aprosos', 'a. & adv.', 'Opportunely or opportune; seasonably or seasonable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aprosos', 'a. & adv.', 'By the way; to the purpose; suitably to the place\n   or subject; -- a word used to introduce an incidental observation,\n   suited to the occasion, though not strictly belonging to the narration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apse', 'n.', 'A projecting part of a building, esp. of a church, having in\n   the plan a polygonal or semicircular termination, and, most often,\n   projecting from the east end. In early churches the Eastern apse was\n   occupied by seats for the bishop and clergy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apse', 'n.', 'The bishops seat or throne, in ancient churches.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apse', 'n.', 'A reliquary, or case in which the relics of saints were kept.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apsidal', 'a.', 'Of or pertaining to the apsides of an orbit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apsidal', 'a.', 'Of or pertaining to the apse of a church; as, the apsidal\n   termination of the chancel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apsides', 'n. pl.', 'See Apsis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apsides', 'pl. ', 'of Apsis');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apsis', 'n.', 'One of the two points of an orbit, as of a planet or\n   satellite, which are at the greatest and least distance from the\n   central body, corresponding to the aphelion and perihelion of a planet,\n   or to the apogee and perigee of the moon. The more distant is called\n   the higher apsis; the other, the lower apsis; and the line joining\n   them, the line of apsides.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apsis', 'n.', 'In a curve referred to polar coordinates, any point for\n   which the radius vector is a maximum or minimum.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apsis', 'n.', 'Same as Apse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apt', 'a.', 'Fit or fitted; suited; suitable; appropriate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apt', 'a.', 'Having an habitual tendency; habitually liable or likely; --\n   used of things.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apt', 'a.', 'Inclined; disposed customarily; given; ready; -- used of\n   persons.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apt', 'a.', 'Ready; especially fitted or qualified (to do something); quick\n   to learn; prompt; expert; as, a pupil apt to learn; an apt scholar.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apt', 'v. t.', 'To fit; to suit; to adapt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aptable', 'a.', 'Capable of being adapted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aptate', 'v. t.', 'To make fit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aptera', 'n. pl.', 'Insects without wings, constituting the seventh Linnaen\n   order of insects, an artificial group, which included Crustacea,\n   spiders, centipeds, and even worms. These animals are now placed in\n   several distinct classes and orders.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apteral', 'a.', 'Apterous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apteral', 'a.', 'Without lateral columns; -- applied to buildings which\n   have no series of columns along their sides, but are either prostyle or\n   amphiprostyle, and opposed to peripteral.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apteran', 'n.', 'One of the Aptera.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apteria', 'n. pl.', 'Naked spaces between the feathered areas of birds. See\n   Pteryliae.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apterous', 'a.', 'Destitute of wings; apteral; as, apterous insects.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apterous', 'a.', 'Destitute of winglike membranous expansions, as a stem or\n   petiole; -- opposed to alate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apteryges', 'n. pl.', 'An order of birds, including the genus Apteryx.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apteryx', 'n.', 'A genus of New Zealand birds about the size of a hen, with\n   only short rudiments of wings, armed with a claw and without a tail;\n   the kiwi. It is allied to the gigantic extinct moas of the same\n   country. Five species are known.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aptitude', 'n.', 'A natural or acquired disposition or capacity for a\n   particular purpose, or tendency to a particular action or effect; as,\n   oil has an aptitude to burn.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aptitude', 'n.', 'A general fitness or suitableness; adaptation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aptitude', 'n.', 'Readiness in learning; docility; aptness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aptitudinal', 'a.', 'Suitable; fit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aptly', 'adv.', 'In an apt or suitable manner; fitly; properly;\n   pertinently; appropriately; readily.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aptness', 'n.', 'Fitness; suitableness; appropriateness; as, the aptness of\n   things to their end.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aptness', 'n.', 'Disposition of the mind; propensity; as, the aptness of\n   men to follow example.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aptness', 'n.', 'Quickness of apprehension; readiness in learning;\n   docility; as, an aptness to learn is more observable in some children\n   than in others.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aptness', 'n.', 'Proneness; tendency; as, the aptness of iron to rust.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aptote', 'n.', 'A noun which has no distinction of cases; an indeclinable\n   noun.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aptotic', 'a.', 'Pertaining to, or characterized by, aptotes; uninflected;\n   as, aptotic languages.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aptychus', 'n.', 'A shelly plate found in the terminal chambers of ammonite\n   shells. Some authors consider them to be jaws; others, opercula.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apus', 'n.', 'A genus of fresh-water phyllopod crustaceans. See Phyllopod.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apyretic', 'a.', 'Without fever; -- applied to days when there is an\n   intermission of fever.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apyrexia', 'n.', 'Alt. of Apyrexy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apyrexy', 'n.', 'The absence or intermission of fever.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apyrexial', 'a.', 'Relating to apyrexy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Apyrous', 'a.', 'Incombustible; capable of sustaining a strong heat without\n   alteration of form or properties.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aqua', 'n.', 'Water; -- a word much used in pharmacy and the old chemistry,\n   in various signification, determined by the word or words annexed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aqua fortis', '', 'Nitric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquamarine', 'n.', 'A transparent, pale green variety of beryl, used as a\n   gem. See Beryl.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquapuncture', 'n.', 'The introduction of water subcutaneously for the\n   relief of pain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquarelle', 'n.', 'A design or painting in thin transparent water colors;\n   also, the mode of painting in such colors.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquarellist', 'n.', 'A painter in thin transparent water colors.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquarial', 'a.', 'Alt. of Aquarian');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquarian', 'a.', 'Of or pertaining to an aquarium.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquarian', 'n.', 'One of a sect of Christian in the primitive church who\n   used water instead of wine in the Lords Supper.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquariums', 'pl. ', 'of Aquarium');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquaria', 'pl. ', 'of Aquarium');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquarium', 'n.', 'An artificial pond, or a globe or tank (usually with\n   glass sides); in which living specimens of aquatic animals or plants\n   are kept.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquarius', 'n.', 'The Water-bearer; the eleventh sign in the zodiac, which\n   the sun enters about the 20th of January; -- so called from the rains\n   which prevail at that season in Italy and the East.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquarius', 'n.', 'A constellation south of Pegasus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquatic', 'a.', 'Pertaining to water; growing in water; living in, swimming\n   in, or frequenting the margins of waters; as, aquatic plants and fowls.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquatic', 'n.', 'An aquatic animal or plant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquatic', 'n.', 'Sports or exercises practiced in or on the water.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquatical', 'a.', 'Aquatic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquatile', 'a.', 'Inhabiting the water.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquatint', 'n.', 'Alt. of Aquatinta');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquatinta', 'n.', 'A kind of etching in which spaces are bitten by the use\n   of aqua fortis, by which an effect is produced resembling a drawing in\n   water colors or India ink; also, the engraving produced by this method.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aqueduct', 'n.', 'A conductor, conduit, or artificial channel for conveying\n   water, especially one for supplying large cities with water.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aqueduct', 'n.', 'A canal or passage; as, the aqueduct of Sylvius, a\n   channel connecting the third and fourth ventricles of the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aqueity', 'n.', 'Wateriness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aqueous', 'a.', 'Partaking of the nature of water, or abounding with it;\n   watery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aqueous', 'a.', 'Made from, or by means of, water.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aqueousness', 'n.', 'Wateriness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquiferous', 'a.', 'Consisting or conveying water or a watery fluid; as,\n   aquiferous vessels; the aquiferous system.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquiform', 'a.', 'Having the form of water.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquilae', 'pl. ', 'of Aquila');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquila', 'n.', 'A genus of eagles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquila', 'n.', 'A northern constellation southerly from Lyra and Cygnus and\n   preceding the Dolphin; the Eagle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquilated', 'a.', 'Adorned with eagles heads.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquiline', 'a.', 'Belonging to or like an eagle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquiline', 'a.', 'Curving; hooked; prominent, like the beak of an eagle; --\n   applied particularly to the nose');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquilon', 'n.', 'The north wind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquiparous', 'a.', 'Secreting water; -- applied to certain glands.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquitanian', 'a.', 'Of or pertaining to Aquitania, now called Gascony.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquose', 'a.', 'Watery; aqueous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aquosity', 'n.', 'The condition of being wet or watery; wateriness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ar', 'conj.', 'Ere; before.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ara', 'n.', 'The Altar; a southern constellation, south of the tail of the\n   Scorpion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ara', 'n.', 'A name of the great blue and yellow macaw (Ara ararauna);\n   native of South America.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arab', 'n.', 'One of a swarthy race occupying Arabia, and numerous in\n   Syria, Northern Africa, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arabesque', 'n.', 'A style of ornamentation either painted, inlaid, or\n   carved in low relief. It consists of a pattern in which plants, fruits,\n   foliage, etc., as well as figures of men and animals, real or\n   imaginary, are fantastically interlaced or put together.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arabesque', 'a.', 'Arabian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arabesque', 'a.', 'Relating to, or exhibiting, the style of ornament called\n   arabesque; as, arabesque frescoes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arabesqued', 'a.', 'Ornamented in the style of arabesques.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arabian', 'a.', 'Of or pertaining to Arabia or its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arabian', 'n.', 'A native of Arabia; an Arab.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arabic', 'a.', 'Of or pertaining to Arabia or the Arabians.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arabic', 'n.', 'The language of the Arabians.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arabical', 'a.', 'Relating to Arabia; Arabic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arabin', 'n.', 'A carbohydrate, isomeric with cane sugar, contained in gum\n   arabic, from which it is extracted as a white, amorphous substance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arabin', 'n.', 'Mucilage, especially that made of gum arabic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arabinose', 'n.', 'A sugar of the composition C5H10O5, obtained from cherry\n   gum by boiling it with dilute sulphuric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arabism', 'n.', 'An Arabic idiom peculiarly of language.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arabist', 'n.', 'One well versed in the Arabic language or literature;\n   also, formerly, one who followed the Arabic system of surgery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arable', 'a.', 'Fit for plowing or tillage; -- hence, often applied to land\n   which has been plowed or tilled.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arable', 'n.', 'Arable land; plow land.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Araby', 'n.', 'The country of Arabia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aracanese', 'a.', 'Of or pertaining to Aracan, a province of British\n   Burmah.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aracanese', 'n. sing. & pl.', 'A native or natives of Aracan.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aracari', 'n.', 'A South American bird, of the genus Pleroglossius, allied\n   to the toucans. There are several species.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arace', 'v. t.', 'To tear up by the roots; to draw away.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Araceous', 'a.', 'Of or pertaining to an order of plants, of which the\n   genus Arum is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arachnid', 'n.', 'An arachnidan.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arachnida', 'n. pl.', 'One of the classes of Arthropoda. See Illustration\n   in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arachnidan', 'n.', 'One of the Arachnida.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arachnidial', 'a.', 'Of or pertaining to the Arachnida.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arachnidial', 'a.', 'Pertaining to the arachnidium.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arachnidium', 'n.', 'The glandular organ in which the material for the web\n   of spiders is secreted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arachnitis', 'n.', 'Inflammation of the arachnoid membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arachnoid', 'a.', 'Resembling a spiders web; cobweblike.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arachnoid', 'a.', 'Pertaining to a thin membrane of the brain and spinal\n   cord, between the dura mater and pia mater.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arachnoid', 'a.', 'Covered with, or composed of, soft, loose hairs or\n   fibers, so as to resemble a cobweb; cobwebby.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arachnoid', 'n.', 'The arachnoid membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arachnoid', 'n.', 'One of the Arachnoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arachnoidal', 'a.', 'Pertaining to the arachnoid membrane; arachnoid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arachnoidea', 'n. pl.', 'Same as Arachnida.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arachnological', 'a.', 'Of or pertaining to arachnology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arachnologist', 'n.', 'One who is versed in, or studies, arachnology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arachnology', 'n.', 'The department of zoology which treats of spiders and\n   other Arachnida.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Araeometer', '', 'See Areometer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Araeostyle', 'a. & n.', 'See Intercolumniation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Araeosystyle', 'a. & n.', 'See Intercolumniation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aragonese', 'a.', 'Of or pertaining to Aragon, in Spain, or to its\n   inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aragonese', 'n. sing. & pl.', 'A native or natives of Aragon, in Spain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aragonite', 'n.', 'A mineral identical in composition with calcite or\n   carbonate of lime, but differing from it in its crystalline form and\n   some of its physical characters.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Araguato', 'n.', 'A South American monkey, the ursine howler (Mycetes\n   ursinus). See Howler, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Araise', 'v. t.', 'To raise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arak', 'n.', 'Same as Arrack.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aramaean', 'a.', 'Alt. of Aramean');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aramean', 'a.', 'Of or pertaining to the Syrians and Chaldeans, or to their\n   language; Aramaic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aramean', 'n.', 'A native of Aram.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aramaic', 'a.', 'Pertaining to Aram, or to the territory, inhabitants,\n   language, or literature of Syria and Mesopotamia; Aramaean; --\n   specifically applied to the northern branch of the Semitic family of\n   languages, including Syriac and Chaldee.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aramaic', 'n.', 'The Aramaic language.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aramaism', 'n.', 'An idiom of the Aramaic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Araneida', 'n. pl.', 'Alt. of Araneoidea');
  INSERT INTO words (word, word_type, description) VALUES  (  'Araneoidea', 'n. pl.', 'See Araneina.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Araneidan', 'a.', 'Of or pertaining to the Araneina or spiders.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Araneidan', 'n.', 'One of the Araneina; a spider.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Araneiform', 'a.', 'Having the form of a spider.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Araneina', 'n. pl.', 'The order of Arachnida that includes the spiders.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Araneose', 'a.', 'Of the aspect of a spiders web; arachnoid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Araneous', 'a.', 'Cobweblike; extremely thin and delicate, like a cobweb;\n   as, the araneous membrane of the eye. See Arachnoid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arangoes', 'pl. ', 'of Arango');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arango', 'n.', 'A bead of rough carnelian. Arangoes were formerly imported\n   from Bombay for use in the African slave trade.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arapaima', 'n.', 'A large fresh-water food fish of South America.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arara', 'n.', 'The palm (or great black) cockatoo, of Australia\n   (Microglossus aterrimus).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aration', 'n.', 'Plowing; tillage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aratory', 'a.', 'Contributing to tillage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Araucaria', 'n.', 'A genus of tall conifers of the pine family. The species\n   are confined mostly to South America and Australia. The wood cells\n   differ from those of other in having the dots in their lateral surfaces\n   in two or three rows, and the dots of contiguous rows alternating. The\n   seeds are edible.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Araucarian', 'a.', 'Relating to, or of the nature of, the Araucaria. The\n   earliest conifers in geological history were mostly Araucarian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbalest', 'n.', 'Alt. of Arbalist');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbalist', 'n.', 'A crossbow, consisting of a steel bow set in a shaft of\n   wood, furnished with a string and a trigger, and a mechanical device\n   for bending the bow. It served to throw arrows, darts, bullets, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbalester', 'n.', 'Alt. of Arbalister');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbalister', 'n.', 'A crossbowman.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbiter', 'n.', 'A person appointed, or chosen, by parties to determine a\n   controversy between them.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbiter', 'n.', 'Any person who has the power of judging and determining,\n   or ordaining, without control; one whose power of deciding and\n   governing is not limited.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbiter', 'v. t.', 'To act as arbiter between.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitrable', 'v. t.', 'Capable of being decided by arbitration;\n   determinable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitrage', 'n.', 'Judgment by an arbiter; authoritative determination.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitrage', 'n.', 'A traffic in bills of exchange (see Arbitration of\n   Exchange); also, a traffic in stocks which bear differing values at the\n   same time in different markets.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitral', 'a.', 'Of or relating to an arbiter or an arbitration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitrament', 'n.', 'Determination; decision; arbitration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitrament', 'n.', 'The award of arbitrators.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitrarily', 'adv.', 'In an arbitrary manner; by will only; despotically;\n   absolutely.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitrariness', 'n.', 'The quality of being arbitrary; despoticalness;\n   tyranny.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitrarious', 'a.', 'Arbitrary; despotic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitrary', 'a.', 'Depending on will or discretion; not governed by any\n   fixed rules; as, an arbitrary decision; an arbitrary punishment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitrary', 'a.', 'Exercised according to ones own will or caprice, and\n   therefore conveying a notion of a tendency to abuse the possession of\n   power.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitrary', 'a.', 'Despotic; absolute in power; bound by no law; harsh and\n   unforbearing; tyrannical; as, an arbitrary prince or government.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitrated', 'imp. & p. p.', 'of Arbitrate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitrating', 'p. pr. & vb. n.', 'of Arbitrate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitrate', 'v. t.', 'To hear and decide, as arbitrators; as, to choose to\n   arbitrate a disputed case.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitrate', 'v. t.', 'To decide, or determine generally.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitrate', 'v. i.', 'To decide; to determine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitrate', 'v. i.', 'To act as arbitrator or judge; as, to arbitrate upon\n   several reports; to arbitrate in disputes among neighbors; to arbitrate\n   between parties to a suit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitration', 'n.', 'The hearing and determination of a cause between\n   parties in controversy, by a person or persons chosen by the parties.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitrator', 'n.', 'A person, or one of two or more persons, chosen by\n   parties who have a controversy, to determine their differences. See\n   Arbitration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitrator', 'n.', 'One who has the power of deciding or prescribing\n   without control; a ruler; a governor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitratrix', 'n.', 'A female who arbitrates or judges.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbitress', 'n.', 'A female arbiter; an arbitratrix.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arblast', 'n.', 'A crossbow. See Arbalest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbor', 'n.', 'A kind of latticework formed of, or covered with, vines,\n   branches of trees, or other plants, for shade; a bower.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbor', 'n.', 'A tree, as distinguished from a shrub.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbor', 'n.', 'An axle or spindle of a wheel or opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbor', 'n.', 'A mandrel in lathe turning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arborary', 'a.', 'Of or pertaining to trees; arboreal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arborator', 'n.', 'One who plants or who prunes trees.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbor Dianae', '', 'A precipitation of silver, in a beautiful arborescent\n   form.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arboreal', 'a.', 'Of or pertaining to a tree, or to trees; of nature of\n   trees.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arboreal', 'a.', 'Attached to, found in or upon, or frequenting, woods or\n   trees; as, arboreal animals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbored', 'a.', 'Furnished with an arbor; lined with trees.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arboreous', 'a.', 'Having the form, constitution, or habits, of a proper\n   tree, in distinction from a shrub.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arboreous', 'a.', 'Pertaining to, or growing on, trees; as, arboreous moss.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arborescence', 'n.', 'The state of being arborescent; the resemblance to a\n   tree in minerals, or crystallizations, or groups of crystals in that\n   form; as, the arborescence produced by precipitating silver.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arborescent', 'a.', 'Resembling a tree; becoming woody in stalk; dendritic;\n   having crystallizations disposed like the branches and twigs of a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arboret', 'n.', 'A small tree or shrub.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arboreta', 'pl. ', 'of Arboretum');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arboretum', 'n.', 'A place in which a collection of rare trees and shrubs\n   is cultivated for scientific or educational purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arborical', 'a.', 'Relating to trees.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arboricole', 'a.', 'Tree-inhabiting; -- said of certain birds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arboricultural', 'a.', 'Pertaining to arboriculture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arboriculture', 'n.', 'The cultivation of trees and shrubs, chiefly for\n   timber or for ornamental purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arboriculturist', 'n.', 'One who cultivates trees.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arboriform', 'a.', 'Treelike in shape.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arborist', 'n.', 'One who makes trees his study, or who is versed in the\n   knowledge of trees.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arborization', 'n.', 'The appearance or figure of a tree or plant, as in\n   minerals or fossils; a dendrite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arborized', 'a.', 'Having a treelike appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arborous', 'a.', 'Formed by trees.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbor vine', '', 'A species of bindweed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbor vitae', '', 'An evergreen tree of the cypress tribe, genus Thuja. The\n   American species is the T. occidentalis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbor vitae', '', 'The treelike disposition of the gray and white nerve\n   tissues in the cerebellum, as seen in a vertical section.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbuscle', 'n.', 'A dwarf tree, one in size between a shrub and a tree; a\n   treelike shrub.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbuscular', 'a.', 'Of or pertaining to a dwarf tree; shrublike.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbustive', 'a.', 'Containing copses of trees or shrubs; covered with\n   shrubs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbutus', 'n.', 'Alt. of Arbute');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arbute', 'n.', 'The strawberry tree, a genus of evergreen shrubs, of the\n   Heath family. It has a berry externally resembling the strawberry; the\n   arbute tree.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arc', 'n.', 'A portion of a curved line; as, the arc of a circle or of an\n   ellipse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arc', 'n.', 'A curvature in the shape of a circular arc or an arch; as, the\n   colored arc (the rainbow); the arc of Hadleys quadrant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arc', 'n.', 'An arch.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arc', 'n.', 'The apparent arc described, above or below the horizon, by the\n   sun or other celestial body. The diurnal arc is described during the\n   daytime, the nocturnal arc during the night.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcade', 'n.', 'A series of arches with the columns or piers which support\n   them, the spandrels above, and other necessary appurtenances; sometimes\n   open, serving as an entrance or to give light; sometimes closed at the\n   back (as in the cut) and forming a decorative feature.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcade', 'n.', 'A long, arched building or gallery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcade', 'n.', 'An arched or covered passageway or avenue.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcaded', 'a.', 'Furnished with an arcade.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcadia', 'n.', 'A mountainous and picturesque district of Greece, in the\n   heart of the Peloponnesus, whose people were distinguished for\n   contentment and rural happiness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcadia', 'n.', 'Fig.: Any region or scene of simple pleasure and\n   untroubled quiet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcadian', 'a.', 'Alt. of Arcadic');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcadic', 'a.', 'Of or pertaining to Arcadia; pastoral; ideally rural; as,\n   Arcadian simplicity or scenery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcane', 'a.', 'Hidden; secret.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcana', 'pl. ', 'of Arcanum');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcanum', 'n.', 'A secret; a mystery; -- generally used in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcanum', 'n.', 'A secret remedy; an elixir.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcboutant', 'n.', 'A flying buttress.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arch', 'n.', 'Any part of a curved line.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arch', 'n.', 'Usually a curved member made up of separate wedge-shaped\n   solids, with the joints between them disposed in the direction of the\n   radii of the curve; used to support the wall or other weight above an\n   opening. In this sense arches are segmental, round (i. e.,\n   semicircular); or pointed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arch', 'n.', 'A flat arch is a member constructed of stones cut into wedges\n   or other shapes so as to support each other without rising in a curve.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arch', 'n.', 'Any place covered by an arch; an archway; as, to pass into\n   the arch of a bridge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arch', 'n.', 'Any curvature in the form of an arch; as, the arch of the\n   aorta.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arched', 'imp. & p. p.', 'of Arch');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arching', 'p. pr. & vb. n.', 'of Arch');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arch', 'v. t.', 'To cover with an arch or arches.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arch', 'v. t.', 'To form or bend into the shape of an arch.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arch', 'v. i.', 'To form into an arch; to curve.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arch-', '', 'A prefix signifying chief, as in archbuilder, archfiend.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arch', 'a.', 'Chief; eminent; greatest; principal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arch', 'a.', 'Cunning or sly; sportively mischievous; roguish; as, an arch\n   look, word, lad.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arch', 'n.', 'A chief.');
  INSERT INTO words (word, word_type, description) VALUES  (  '-arch', 'a.', 'A suffix meaning a ruler, as in monarch (a sole ruler).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archaean', 'a.', 'Ancient; pertaining to the earliest period in geological\n   history.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archaean', 'n.', 'The earliest period in geological period, extending up to\n   the Lower Silurian. It includes an Azoic age, previous to the\n   appearance of life, and an Eozoic age, including the earliest forms of\n   life.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archaeography', 'n.', 'A description of, or a treatise on, antiquity or\n   antiquities.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archaeolithic', 'a.', 'Of or pertaining to the earliest Stone age; --\n   applied to a prehistoric period preceding the Paleolithic age.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archaeologian', 'n.', 'An archaeologist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archaeologic', '', 'Alt. of Archaeological');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archaeological', '', 'Relating to archaeology, or antiquities; as,\n   archaeological researches.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archaeologist', 'n.', 'One versed in archaeology; an antiquary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archaeology', 'n.', 'The science or study of antiquities, esp. prehistoric\n   antiquities, such as the remains of buildings or monuments of an early\n   epoch, inscriptions, implements, and other relics, written manuscripts,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archaeopteryx', 'n.', 'A fossil bird, of the Jurassic period, remarkable\n   for having a long tapering tail of many vertebrae with feathers along\n   each side, and jaws armed with teeth, with other reptilian\n   characteristics.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archaeostomatous', 'a.', 'Applied to a gastrula when the blastopore does\n   not entirely close up.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archaeozoic', 'a.', 'Like or belonging to the earliest forms of animal\n   life.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archaic', 'a.', 'Of or characterized by antiquity or archaism; antiquated;\n   obsolescent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archaical', 'a.', 'Archaic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archaism', 'a.', 'An ancient, antiquated, or old-fashioned, word,\n   expression, or idiom; a word or form of speech no longer in common use.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archaism', 'a.', 'Antiquity of style or use; obsoleteness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archaist', 'n.', 'Am antiquary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archaist', 'n.', 'One who uses archaisms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archaistic', 'a.', 'Like, or imitative of, anything archaic; pertaining to\n   an archaism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archaized', 'imp. & p. p.', 'of Archaize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archaizing', 'p. pr. & vb. n.', 'of Archaize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archaize', 'v. t.', 'To make appear archaic or antique.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archangel', 'n.', 'A chief angel; one high in the celestial hierarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archangel', 'n.', 'A term applied to several different species of plants\n   (Angelica archangelica, Lamium album, etc.).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archangelic', 'a.', 'Of or pertaining to archangels; of the nature of, or\n   resembling, an archangel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archbishop', 'n.', 'A chief bishop; a church dignitary of the first class\n   (often called a metropolitan or primate) who superintends the conduct\n   of the suffragan bishops in his province, and also exercises episcopal\n   authority in his own diocese.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archbishopric', 'n.', 'The jurisdiction or office of an archbishop; the see\n   or province over which archbishop exercises archiepiscopal authority.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arch brick', '', 'A wedge-shaped brick used in the building of an arch.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archbutler', 'n.', 'A chief butler; -- an officer of the German empire.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archchamberlain', 'n.', 'A chief chamberlain; -- an officer of the old\n   German empire, whose office was similar to that of the great\n   chamberlain in England.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archchancellor', 'n.', 'A chief chancellor; -- an officer in the old German\n   empire, who presided over the secretaries of the court.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archchemic', 'a.', 'Of supreme chemical powers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archdeacon', 'n.', 'In England, an ecclesiastical dignitary, next in rank\n   below a bishop, whom he assists, and by whom he is appointed, though\n   with independent authority.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archdeaconry', 'n.', 'The district, office, or residence of an archdeacon.\n   See Benefice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archdeaconship', 'n.', 'The office of an archdeacon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archdiocese', 'n.', 'The diocese of an archbishop.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archducal', 'a.', 'Of or pertaining to an archduke or archduchy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archduchess', 'n.', 'The consort of an archduke; also, a princess of the\n   imperial family of Austria. See Archduke.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archduchy', 'n.', 'The territory of an archduke or archduchess.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archduke', 'n.', 'A prince of the imperial family of Austria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archdukedom', 'n.', 'An archduchy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archebiosis', 'n.', 'The origination of living matter from non-living. See\n   Abiogenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arched', 'a.', 'Made with an arch or curve; covered with an arch; as, an\n   arched door.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archegonial', 'a.', 'Relating to the archegonium.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archegonium', 'n.', 'The pistillidium or female organ in the higher\n   cryptogamic plants, corresponding to the pistil in flowering plants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archegony', 'n.', 'Spontaneous generation; abiogenesis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archelogy', 'n.', 'The science of, or a treatise on, first principles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archencephala', 'n. pl.', 'The division that includes man alone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archenemy', 'n.', 'A principal enemy. Specifically, Satan, the grand\n   adversary of mankind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archenteric', 'a.', 'Relating to the archenteron; as, archenteric\n   invagination.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archenteron', 'n.', 'The primitive enteron or undifferentiated digestive\n   sac of a gastrula or other embryo. See Illust. under Invagination.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archeology', 'a.', 'Alt. of Archeological');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archeological', 'a.', 'Same as Archaeology, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archer', 'n.', 'A bowman, one skilled in the use of the bow and arrow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archeress', 'n.', 'A female archer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archer fish', '', 'A small fish (Toxotes jaculator); of the East Indies; --\n   so called from its ejecting drops of water from its mouth at its prey.\n   The name is also applied to Chaetodon rostratus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archership', 'n.', 'The art or skill of an archer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archery', 'n.', 'The use of the bow and arrows in battle, hunting, etc.;\n   the art, practice, or skill of shooting with a bow and arrows.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archery', 'n.', 'Archers, or bowmen, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arches', '', 'pl. of Arch, n.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archetypal', 'a.', 'Of or pertaining to an archetype; consisting a model\n   (real or ideal) or pattern; original.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archetypally', 'adv.', 'With reference to the archetype; originally. \"Parts\n   archetypally distinct.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archetype', 'n.', 'The original pattern or model of a work; or the model\n   from which a thing is made or formed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archetype', 'n.', 'The standard weight or coin by which others are\n   adjusted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archetype', 'n.', 'The plan or fundamental structure on which a natural\n   group of animals or plants or their systems of organs are assumed to\n   have been constructed; as, the vertebrate archetype.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archetypical', 'a.', 'Relating to an archetype; archetypal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archeus', 'n.', 'The vital principle or force which (according to the\n   Paracelsians) presides over the growth and continuation of living\n   beings; the anima mundi or plastic power of the old philosophers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archi-', '', 'A prefix signifying chief, arch; as, architect,\n   archiepiscopal. In Biol. and Anat. it usually means primitive,\n   original, ancestral; as, archipterygium, the primitive fin or wing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archiannelida', 'n. pl.', 'A group of Annelida remarkable for having no\n   external segments or distinct ventral nerve ganglions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archiater', 'n.', 'Chief physician; -- a term applied, on the continent of\n   Europe, to the first or body physician of princes and to the first\n   physician of some cities.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archiblastula', 'n.', 'A hollow blastula, supposed to be the primitive\n   form; a c/loblastula.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archical', 'pref.', 'Chief; primary; primordial.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archidiaconal', 'a.', 'Of or pertaining to an archdeacon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archiepiscopacy', 'n.', 'That form of episcopacy in which the chief power\n   is in the hands of archbishops.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archiepiscopacy', 'n.', 'The state or dignity of an archbishop.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archiepiscopal', 'a.', 'Of or pertaining to an archbishop; as, Canterbury\n   is an archiepiscopal see.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archiepiscopality', 'n.', 'The station or dignity of an archbishop;\n   archiepiscopacy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archiepiscopate', 'n.', 'The office of an archbishop; an archbishopric.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archierey', 'n.', 'The higher order of clergy in Russia, including\n   metropolitans, archbishops, and bishops.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archil', 'n.', 'A violet dye obtained from several species of lichen\n   (Roccella tinctoria, etc.); which grow on maritime rocks in the Canary\n   and Cape Verd Islands, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archil', 'n.', 'The plant from which the dye is obtained.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archilochian', 'a.', 'Of or pertaining to the satiric Greek poet\n   Archilochus; as, Archilochian meter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archimage', 'n.', 'Alt. of Archimagus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archimagus', 'n.', 'The high priest of the Persian Magi, or worshipers of\n   fire.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archimagus', 'n.', 'A great magician, wizard, or enchanter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archimandrite', 'n.', 'A chief of a monastery, corresponding to abbot in\n   the Roman Catholic church.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archimandrite', 'n.', 'A superintendent of several monasteries,\n   corresponding to superior abbot, or father provincial, in the Roman\n   Catholic church.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archimedean', 'a.', 'Of or pertaining to Archimedes, a celebrated Greek\n   philosopher; constructed on the principle of Archimedes screw; as,\n   Archimedean drill, propeller, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archimedes', 'n.', 'An extinct genus of Bryzoa characteristic of the\n   subcarboniferous rocks. Its form is that of a screw.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arching', 'n.', 'The arched part of a structure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arching', 'n.', 'Hogging; -- opposed to sagging.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archipelagic', 'a.', 'Of or pertaining to an archipelago.');
  INSERT INTO words (word, word_type, description) VALUES  (  '-goes', 'pl. ', 'of Archipelago');
  INSERT INTO words (word, word_type, description) VALUES  (  '-gos', 'pl. ', 'of Archipelago');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archipelago', 'n.', 'The Grecian Archipelago, or Aegean Sea, separating\n   Greece from Asia Minor. It is studded with a vast number of small\n   islands.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archipelago', 'n.', 'Hence: Any sea or broad sheet of water interspersed\n   with many islands or with a group of islands.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archipterygium', 'n.', 'The primitive form of fin, like that of Ceratodus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Architect', 'n.', 'A person skilled in the art of building; one who\n   understands architecture, or makes it his occupation to form plans and\n   designs of buildings, and to superintend the artificers employed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Architect', 'n.', 'A contriver, designer, or maker.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Architective', 'a.', 'Used in building; proper for building.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Architectonic', 'a.', 'Alt. of Architectonical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Architectonical', 'a.', 'Pertaining to a master builder, or to\n   architecture; evincing skill in designing or construction;\n   constructive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Architectonical', 'a.', 'Relating to the systemizing of knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Architectonic', 'n.', 'The science of architecture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Architectonic', 'n.', 'The act of arranging knowledge into a system.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Architectonics', 'n.', 'The science of architecture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Architector', 'n.', 'An architect.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Architectress', 'n.', 'A female architect.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Architectural', 'a.', 'Of or pertaining to the art of building; conformed\n   to the rules of architecture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Architecture', 'n.', 'The art or science of building; especially, the art\n   of building houses, churches, bridges, and other structures, for the\n   purposes of civil life; -- often called civil architecture.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Architecture', 'n.', 'Construction, in a more general sense; frame or\n   structure; workmanship.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Architeuthis', 'n.', 'A genus of gigantic cephalopods, allied to the\n   squids, found esp. in the North Atlantic and about New Zealand.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Architrave', 'n.', 'The lower division of an entablature, or that part\n   which rests immediately on the column, esp. in classical architecture.\n   See Column.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Architrave', 'n.', 'The group of moldings, or other architectural member,\n   above and on both sides of a door or other opening, especially if\n   square in form.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Architraved', 'a.', 'Furnished with an architrave.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archival', 'a.', 'Pertaining to, or contained in, archives or records.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archives', 'pl. ', 'of Archive');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archive', 'n.', 'The place in which public records or historic documents\n   are kept.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archive', 'n.', 'Public records or documents preserved as evidence of\n   facts; as, the archives of a country or family.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archivist', 'n.', 'A keeper of archives or records.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archivolt', 'n.', 'The architectural member surrounding the curved opening\n   of an arch, corresponding to the architrave in the case of a square\n   opening.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archivolt', 'n.', 'More commonly, the molding or other ornaments with which\n   the wall face of the voussoirs of an arch is charged.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archlute', 'n.', 'Alt. of Archilute');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archilute', 'n.', 'A large theorbo, or double-necked lute, formerly in use,\n   having the bass strings doubled with an octave, and the higher strings\n   with a unison.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archly', 'adv.', 'In an arch manner; with attractive slyness or\n   roguishness; slyly; waggishly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archmarshal', 'n.', 'The grand marshal of the old German empire, a dignity\n   that to the Elector of Saxony.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archness', 'n.', 'The quality of being arch; cleverness; sly humor free\n   from malice; waggishness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archon', 'n.', 'One of the chief magistrates in ancient Athens, especially,\n   by preeminence, the first of the nine chief magistrates.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archonship', 'n.', 'The office of an archon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archontate', 'n.', 'An archons term of office.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archonts', 'p. pr.', 'The group including man alone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archprelate', 'n.', 'An archbishop or other chief prelate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archpresbyter', 'n.', 'Same as Archpriest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archpresbytery', 'n.', 'The absolute dominion of presbytery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archpriest', 'n.', 'A chief priest; also, a kind of vicar, or a rural dean.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archprimate', 'n.', 'The chief primate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arch stone', '', 'A wedge-shaped stone used in an arch; a voussoir.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archtraitor', 'n.', 'A chief or transcendent traitor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archtreasurer', 'n.', 'A chief treasurer. Specifically, the great treasurer\n   of the German empire.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archway', 'n.', 'A way or passage under an arch.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archwife', 'n.', 'A big, masculine wife.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archwise', 'adv.', 'Arch-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Archy', 'a.', 'Arched; as, archy brows.');
  INSERT INTO words (word, word_type, description) VALUES  (  'archy', '', 'A suffix properly meaning a rule, ruling, as in monarchy, the\n   rule of one only. Cf. -arch.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arciform', 'a.', 'Having the form of an arch; curved.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcograph', 'n.', 'An instrument for drawing a circular arc without the use\n   of a central point; a cyclograph.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arctation', 'n.', 'Constriction or contraction of some natural passage, as\n   in constipation from inflammation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arctic', 'a.', 'Pertaining to, or situated under, the northern\n   constellation called the Bear; northern; frigid; as, the arctic pole,\n   circle, region, ocean; an arctic expedition, night, temperature.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arctic', 'n.', 'The arctic circle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arctic', 'n.', 'A warm waterproof overshoe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arctisca', 'n. pl.', 'A group of Arachnida. See Illust. in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arctogeal', 'a.', 'Of or pertaining to arctic lands; as, the arctogeal\n   fauna.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arctoidea', 'n. pl.', 'A group of the Carnivora, that includes the bears,\n   weasels, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcturus', 'n.', 'A fixed star of the first magnitude in the constellation\n   Bootes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcual', 'a.', 'Of or pertaining to an arc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcuate', 'a.', 'Alt. of Arcuated');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcuated', 'a.', 'Bent or curved in the form of a bow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcuately', 'adv.', 'In the form of a bow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcuation', 'n.', 'The act of bending or curving; incurvation; the state of\n   being bent; crookedness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcuation', 'n.', 'A mode of propagating trees by bending branches to the\n   ground, and covering the small shoots with earth; layering.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcubalist', 'n.', 'A crossbow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcubalister', 'n.', 'A crossbowman; one who used the arcubalist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arcubus', 'n.', 'See Arquebus.');
  INSERT INTO words (word, word_type, description) VALUES  (  '-ard', '', 'Alt. of -art');
  INSERT INTO words (word, word_type, description) VALUES  (  '-art', '', 'The termination of many English words; as, coward, reynard,\n   drunkard, mostly from the French, in which language this ending is of\n   German origin, being orig. the same word as English hard. It usually\n   has the sense of one who has to a high or excessive degree the quality\n   expressed by the root; as, braggart, sluggard.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ardassine', 'n.', 'A very fine sort of Persian silk.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ardency', 'n.', 'Heat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ardency', 'n.', 'Warmth of passion or affection; ardor; vehemence;\n   eagerness; as, the ardency of love or zeal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ardent', 'a.', 'Hot or burning; causing a sensation of burning; fiery; as,\n   ardent spirits, that is, distilled liquors; an ardent fever.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ardent', 'a.', 'Having the appearance or quality of fire; fierce; glowing;\n   shining; as, ardent eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ardent', 'a.', 'Warm, applied to the passions and affections; passionate;\n   fervent; zealous; vehement; as, ardent love, feelings, zeal, hope,\n   temper.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ardently', 'adv.', 'In an ardent manner; eagerly; with warmth;\n   affectionately; passionately.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ardentness', 'n.', 'Ardency.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ardor', 'n.', 'Heat, in a literal sense; as, the ardor of the suns rays.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ardor', 'n.', 'Warmth or heat of passion or affection; eagerness; zeal; as,\n   he pursues study with ardor; the fought with ardor; martial ardor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ardor', 'n.', 'Bright and effulgent spirits; seraphim.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arduous', 'a.', 'Steep and lofty, in a literal sense; hard to climb.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arduous', 'a.', 'Attended with great labor, like the ascending of\n   acclivities; difficult; laborious; as, an arduous employment, task, or\n   enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arduously', 'adv.', 'In an arduous manner; with difficulty or\n   laboriousness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arduousness', 'n.', 'The quality of being arduous; difficulty of execution.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ardurous', 'a.', 'Burning; ardent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Are', '', 'The present indicative plural of the substantive verb to be; but\n   etymologically a different word from be, or was. Am, art, are, and is,\n   all come from the root as.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Are', 'n.', 'The unit of superficial measure, being a square of which each\n   side is ten meters in length; 100 square meters, or about 119.6 square\n   yards.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areas', 'pl. ', 'of Area');
  INSERT INTO words (word, word_type, description) VALUES  (  'Area', 'n.', 'Any plane surface, as of the floor of a room or church, or of\n   the ground within an inclosure; an open space in a building.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Area', 'n.', 'The inclosed space on which a building stands.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Area', 'n.', 'The sunken space or court, giving ingress and affording light\n   to the basement of a building.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Area', 'n.', 'An extent of surface; a tract of the earths surface; a\n   region; as, vast uncultivated areas.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Area', 'n.', 'The superficial contents of any figure; the surface included\n   within any given lines; superficial extent; as, the area of a square or\n   a triangle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Area', 'n.', 'A spot or small marked space; as, the germinative area.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Area', 'n.', 'Extent; scope; range; as, a wide area of thought.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aread', 'v. t.', 'Alt. of Areed');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areed', 'v. t.', 'To tell, declare, explain, or interpret; to divine; to\n   guess; as, to aread a riddle or a dream.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areed', 'v. t.', 'To read.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areed', 'v. t.', 'To counsel, advise, warn, or direct.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areed', 'v. t.', 'To decree; to adjudge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areal', 'a.', 'Of or pertaining to an area; as, areal interstices (the\n   areas or spaces inclosed by the reticulate vessels of leaves).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arear', 'v. t. & i.', 'To raise; to set up; to stir up.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arear', 'adv.', 'Backward; in or to the rear; behindhand.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areca', 'n.', 'A genus of palms, one species of which produces the areca\n   nut, or betel nut, which is chewed in India with the leaf of the Piper\n   Betle and lime.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areek', 'adv. & a.', 'In a reeking condition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arefaction', 'n.', 'The act of drying, or the state of growing dry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arefy', 'v. t.', 'To dry, or make dry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arenas', 'pl. ', 'of Arena');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arenae', 'pl. ', 'of Arena');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arena', 'n.', 'The area in the central part of an amphitheater, in which\n   the gladiators fought and other shows were exhibited; -- so called\n   because it was covered with sand.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arena', 'n.', 'Any place of public contest or exertion; any sphere of\n   action; as, the arenaof debate; the arena of life.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arena', 'n.', '\"Sand\" or \"gravel\" in the kidneys.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arenaceous', 'a.', 'Sandy or consisting largely of sand; of the nature of\n   sand; easily disintegrating into sand; friable; as, arenaceous\n   limestone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arenarious', 'a.', 'Sandy; as, arenarious soil.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arenation', 'n.', 'A sand bath; application of hot sand to the body.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arendator', 'n.', 'In some provinces of Russia, one who farms the rents or\n   revenues.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areng', 'n.', 'Alt. of Arenga');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arenga', 'n.', 'A palm tree (Saguerus saccharifer) which furnishes sago,\n   wine, and fibers for ropes; the gomuti palm.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arenicolite', 'n.', 'An ancient wormhole in sand, preserved in the rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arenilitic', 'a.', 'Of or pertaining to sandstone; as, arenilitic\n   mountains.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arenose', 'a.', 'Sandy; full of sand.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arenulous', 'a.', 'Full of fine sand; like sand.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areolae', 'pl. ', 'of Areola');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areola', 'n.', 'An interstice or small space, as between the cracks of the\n   surface in certain crustaceous lichens; or as between the fibers\n   composing organs or vessels that interlace; or as between the nervures\n   of an insects wing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areola', 'n.', 'The colored ring around the nipple, or around a vesicle or\n   pustule.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areolar', 'a.', 'Pertaining to, or like, an areola; filled with interstices\n   or areolae.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areolate', 'a.', 'Alt. of Areolated');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areolated', 'a.', 'Divided into small spaces or areolations, as the wings\n   of insects, the leaves of plants, or the receptacle of compound\n   flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areolation', 'n.', 'Division into areolae.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areolation', 'n.', 'Any small space, bounded by some part different in\n   color or structure, as the spaces bounded by the nervures of the wings\n   of insects, or those by the veins of leaves; an areola.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areole', 'n.', 'Same as Areola.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areolet', 'n.', 'A small inclosed area; esp. one of the small spaces on the\n   wings of insects, circumscribed by the veins.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areometer', 'n.', 'An instrument for measuring the specific gravity of\n   fluids; a form hydrometer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areometric', 'a.', 'Alt. of Areometrical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areometrical', 'a.', 'Pertaining to, or measured by, an areometer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areometry', 'n.', 'The art or process of measuring the specific gravity of\n   fluids.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areopagist', 'n.', 'See Areopagite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areopagite', 'n.', 'A member of the Areopagus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areopagitic', 'a.', 'Pertaining to the Areopagus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areopagus', 'n.', 'The highest judicial court at Athens. Its sessions were\n   held on Mars Hill. Hence, any high court or tribunal');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areostyle', 'a. & n.', 'See Intercolumniation, and Araeostyle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Areosystyle', 'a. & n.', 'See Intercolumniation, and Araeosystyle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arere', 'v. t. & i.', 'See Arear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arest', 'n.', 'A support for the spear when couched for the attack.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aret', 'v. t.', 'To reckon; to ascribe; to impute.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aretaics', 'n.', 'The ethical theory which excludes all relations between\n   virtue and happiness; the science of virtue; -- contrasted with\n   eudemonics.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aretology', 'n.', 'That part of moral philosophy which treats of virtue,\n   its nature, and the means of attaining to it.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arew', 'adv.', 'In a row.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argal', 'n.', 'Crude tartar. See Argol.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argal', 'adv.', 'A ludicrous corruption of the Latin word ergo, therefore.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argal', 'n.', 'Alt. of Argali');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argali', 'n.', 'A species of wild sheep (Ovis ammon, or O. argali);\n   remarkable for its large horns. It inhabits the mountains of Siberia\n   and central Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argala', 'n.', 'The adjutant bird.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argand lamp', '', 'A lamp with a circular hollow wick and glass chimney\n   which allow a current of air both inside and outside of the flame.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argas', 'n.', 'A genus of venomous ticks which attack men and animals. The\n   famous Persian Argas, also called Miana bug, is A. Persicus; that of\n   Central America, called talaje by the natives, is A. Talaje.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argean', 'a.', 'Pertaining to the ship Argo. See Argo.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argent', 'n.', 'Silver, or money.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argent', 'n.', 'Whiteness; anything that is white.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argent', 'n.', 'The white color in coats of arms, intended to represent\n   silver, or, figuratively, purity, innocence, beauty, or gentleness; --\n   represented in engraving by a plain white surface.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argent', 'a.', 'Made of silver; of a silvery color; white; shining.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argental', 'a.', 'Of or pertaining to silver; resembling, containing, or\n   combined with, silver.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argentan', 'n.', 'An alloy of nickel with copper and zinc; German silver.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argentate', 'a.', 'Silvery white.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argentation', 'n.', 'A coating or overlaying with silver.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argentic', 'a.', 'Pertaining to, derived from, or containing, silver; --\n   said of certain compounds of silver in which this metal has its lowest\n   proportion; as, argentic chloride.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argentiferous', 'a.', 'Producing or containing silver; as, argentiferous\n   lead ore or veins.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argentine', 'a.', 'Pertaining to, or resembling, silver; made of, or\n   sounding like, silver; silvery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argentine', 'a.', 'Of or pertaining to the Argentine Republic in South\n   America.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argentine', 'n.', 'A siliceous variety of calcite, or carbonate of lime,\n   having a silvery-white, pearly luster, and a waving or curved lamellar\n   structure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argentine', 'n.', 'White metal coated with silver.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argentine', 'n.', 'A fish of Europe (Maurolicus Pennantii) with silvery\n   scales. The name is also applied to various fishes of the genus\n   Argentina.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argentine', 'n.', 'A citizen of the Argentine Republic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argentite', 'n.', 'Sulphide of silver; -- also called vitreous silver, or\n   silver glance. It has a metallic luster, a lead-gray color, and is\n   sectile like lead.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argentous', 'a.', 'Of, pertaining to, or containing, silver; -- said of\n   certain silver compounds in which silver has a higher proportion than\n   in argentic compounds; as, argentous chloride.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argentry', 'n.', 'Silver plate or vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argil', 'n.', 'Clay, or potters earth; sometimes pure clay, or alumina.\n   See Clay.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argillaceous', 'a.', 'Of the nature of clay; consisting of, or containing,\n   argil or clay; clayey.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argilliferous', 'a.', 'Producing clay; -- applied to such earths as abound\n   with argil.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argillite', 'n.', 'Argillaceous schist or slate; clay slate. Its colors is\n   bluish or blackish gray, sometimes greenish gray, brownish red, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argillo-areenaceous', 'a.', 'Consisting of, or containing, clay and sand,\n   as a soil.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argillo-calcareous', 'a.', 'Consisting of, or containing, clay and\n   calcareous earth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argillo-ferruginous', 'a.', 'Containing clay and iron.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argillous', 'a.', 'Argillaceous; clayey.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argive', 'a.', 'Of or performance to Argos, the capital of Argolis in\n   Greece.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argive', 'n.', 'A native of Argos. Often used as a generic term, equivalent\n   to Grecian or Greek.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argo', 'n.', 'The name of the ship which carried Jason and his fifty-four\n   companions to Colchis, in quest of the Golden Fleece.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argo', 'n.', 'A large constellation in the southern hemisphere, called also\n   Argo Navis. In modern astronomy it is replaced by its three divisions,\n   Carina, Puppis, and Vela.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argoan', 'a.', 'Pertaining to the ship Argo.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argoile', 'n.', 'Potters clay.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argol', 'n.', 'Crude tartar; an acidulous salt from which cream of tartar\n   is prepared. It exists in the juice of grapes, and is deposited from\n   wines on the sides of the casks.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argolic', 'a.', 'Pertaining to Argolis, a district in the Peloponnesus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argon', 'n.', 'A substance regarded as an element, contained in the\n   atmosphere and remarkable for its chemical inertness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argonaut', 'n.', 'Any one of the legendary Greek heroes who sailed with\n   Jason, in the Argo, in quest of the Golden Fleece.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argonaut', 'n.', 'A cephalopod of the genus Argonauta.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argonauta', 'n.', 'A genus of Cephalopoda. The shell is called paper\n   nautilus or paper sailor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argonautic', 'a.', 'Of or pertaining to the Argonauts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argosies', 'pl. ', 'of Argosy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argosy', 'n.', 'A large ship, esp. a merchant vessel of the largest size.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argot', 'n.', 'A secret language or conventional slang peculiar to thieves,\n   tramps, and vagabonds; flash.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arguable', 'a.', 'Capable of being argued; admitting of debate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argued', 'imp. & p. p.', 'of Argue');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arguing', 'p. pr. & vb. n.', 'of Argue');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argue', 'v. i.', 'To invent and offer reasons to support or overthrow a\n   proposition, opinion, or measure; to use arguments; to reason.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argue', 'v. i.', 'To contend in argument; to dispute; to reason; --\n   followed by with; as, you may argue with your friend without convincing\n   him.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argue', 'v. t.', 'To debate or discuss; to treat by reasoning; as, the\n   counsel argued the cause before a full court; the cause was well\n   argued.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argue', 'v. t.', 'To prove or evince; too manifest or exhibit by inference,\n   deduction, or reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argue', 'v. t.', 'To persuade by reasons; as, to argue a man into a\n   different opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argue', 'v. t.', 'To blame; to accuse; to charge with.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arguer', 'n.', 'One who argues; a reasoner; a disputant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argufy', 'v. t. & i.', 'To argue pertinaciously.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argufy', 'v. t. & i.', 'To signify.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argulus', 'n.', 'A genus of copepod Crustacea, parasitic of fishes; a fish\n   louse. See Branchiura.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argument', 'n.', 'Proof; evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argument', 'n.', 'A reason or reasons offered in proof, to induce belief,\n   or convince the mind; reasoning expressed in words; as, an argument\n   about, concerning, or regarding a proposition, for or in favor of it,\n   or against it.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argument', 'n.', 'A process of reasoning, or a controversy made up of\n   rational proofs; argumentation; discussion; disputation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argument', 'n.', 'The subject matter of a discourse, writing, or artistic\n   representation; theme or topic; also, an abstract or summary, as of the\n   contents of a book, chapter, poem.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argument', 'n.', 'Matter for question; business in hand.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argument', 'n.', 'The quantity on which another quantity in a table\n   depends; as, the altitude is the argument of the refraction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argument', 'n.', 'The independent variable upon whose value that of a\n   function depends.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argument', 'v. i.', 'To make an argument; to argue.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argumentable', 'a.', 'Admitting of argument.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argumental', 'a.', 'Of, pertaining to, or containing, argument;\n   argumentative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argumentation', 'n.', 'The act of forming reasons, making inductions,\n   drawing conclusions, and applying them to the case in discussion; the\n   operation of inferring propositions, not known or admitted as true,\n   from facts or principles known, admitted, or proved to be true.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argumentation', 'n.', 'Debate; discussion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argumentative', 'a.', 'Consisting of, or characterized by, argument;\n   containing a process of reasoning; as, an argumentative discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argumentative', 'a.', 'Adductive as proof; indicative; as, the adaptation\n   of things to their uses is argumentative of infinite wisdom in the\n   Creator.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argumentative', 'a.', 'Given to argument; characterized by argument;\n   disputatious; as, an argumentative writer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argumentize', 'v. i.', 'To argue or discuss.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argus', 'n.', 'A fabulous being of antiquity, said to have had a hundred\n   eyes, who has placed by Juno to guard Io. His eyes were transplanted to\n   the peacocks tail.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argus', 'n.', 'One very vigilant; a guardian always watchful.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argus', 'n.', 'A genus of East Indian pheasants. The common species (A.\n   giganteus) is remarkable for the great length and beauty of the wing\n   and tail feathers of the male. The species A. Grayi inhabits Borneo.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argus-eyed', 'a.', 'Extremely observant; watchful; sharp-sighted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argus shell', '', 'A species of shell (Cypraea argus); beautifully\n   variegated with spots resembling those in a peacocks tail.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argutation', 'n.', 'Caviling; subtle disputation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argute', 'a.', 'Sharp; shrill.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argute', 'a.', 'Sagacious; acute; subtle; shrewd.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Argutely', 'adv.', 'In a subtle; shrewdly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arguteness', 'n.', 'Acuteness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arhizal', 'a.', 'Alt. of Arhythmous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arhizous', 'a.', 'Alt. of Arhythmous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arhythmic', 'a.', 'Alt. of Arhythmous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arhythmous', 'a.', 'See Arrhizal, Arrhizous, Arrhythmic, Arrhythmous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aria', 'n.', 'An air or song; a melody; a tune.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arian', 'a. & n.', 'See Aryan.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arian', 'a.', 'Pertaining to Arius, a presbyter of the church of\n   Alexandria, in the fourth century, or to the doctrines of Arius, who\n   held Christ to be inferior to God the Father in nature and dignity,\n   though the first and noblest of all created beings.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arian', 'n.', 'One who adheres to or believes the doctrines of Arius.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arianism', 'n.', 'The doctrines of the Arians.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arianize', 'v. i.', 'To admit or accept the tenets of the Arians; to become\n   an Arian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arianize', 'v. t.', 'To convert to Arianism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aricine', 'n.', 'An alkaloid, first found in white cinchona bark.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arid', 'a.', 'Exhausted of moisture; parched with heat; dry; barren.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aridities', 'pl. ', 'of Aridity');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aridity', 'n.', 'The state or quality of being arid or without moisture;\n   dryness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aridity', 'n.', 'Fig.: Want of interest of feeling; insensibility; dryness\n   of style or feeling; spiritual drought.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aridness', 'n.', 'Aridity; dryness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ariel', '', 'Alt. of Ariel gazelle');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ariel gazelle', '', 'A variety of the gazelle (Antilope, / Gazella,\n   dorcas); found in Arabia and adjacent countries.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ariel gazelle', '', 'A squirrel-like Australian marsupial, a species of\n   Petaurus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ariel gazelle', '', 'A beautiful Brazilian toucan Ramphastos ariel).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aries', 'n.', 'The Ram; the first of the twelve signs in the zodiac, which\n   the sun enters at the vernal equinox, about the 21st of March.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aries', 'n.', 'A constellation west of Taurus, drawn on the celestial globe\n   in the figure of a ram.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aries', 'n.', 'A battering-ram.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arietate', 'v. i.', 'To butt, as a ram.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arietation', 'n.', 'The act of butting like a ram; act of using a\n   battering-ram.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arietation', 'n.', 'Act of striking or conflicting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arietta', 'n.', 'Alt. of Ariette');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ariette', 'n.', 'A short aria, or air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aright', 'adv.', 'Rightly; correctly; in a right way or form; without\n   mistake or crime; as, to worship God aright.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aril', 'n.', 'Alt. of Arillus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arillus', 'n.', 'A exterior covering, forming a false coat or appendage to\n   a seed, as the loose, transparent bag inclosing the seed or the white\n   water lily. The mace of the nutmeg is also an aril.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arillate', 'a.', 'Alt. of Ariled');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arllated', 'a.', 'Alt. of Ariled');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ariled', 'a.', 'Having an aril.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ariman', 'n.', 'See Ahriman.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ariolation', 'n.', 'A soothsaying; a foretelling.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ariose', 'a.', 'Characterized by melody, as distinguished from harmony.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arioso', 'adv. & a.', 'In the smooth and melodious style of an air; ariose.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arose', 'imp.', 'of Arise');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arising', 'p. pr. & vb. n.', 'of Arise');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arisen', 'p. p.', 'of Arise');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arise', 'v. i.', 'To come up from a lower to a higher position; to come\n   above the horizon; to come up from ones bed or place of repose; to\n   mount; to ascend; to rise; as, to arise from a kneeling posture; a\n   cloud arose; the sun ariseth; he arose early in the morning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arise', 'v. i.', 'To spring up; to come into action, being, or notice; to\n   become operative, sensible, or visible; to begin to act a part; to\n   present itself; as, the waves of the sea arose; a persecution arose;\n   the wrath of the king shall arise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arise', 'v. i.', 'To proceed; to issue; to spring.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arise', 'n.', 'Rising.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arist', '', '3d sing. pres. of Arise, for ariseth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arista', 'n.', 'An awn.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristarch', 'n.', 'A severe critic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristarchian', 'a.', 'Severely critical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristarchy', 'n.', 'Severely criticism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristarchy', 'n.', 'Severe criticism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristate', 'a.', 'Having a pointed, beardlike process, as the glumes of\n   wheat; awned.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristate', 'a.', 'Having a slender, sharp, or spinelike tip.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristocracies', 'pl. ', 'of Aristocracy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristocracy', 'n.', 'Government by the best citizens.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristocracy', 'n.', 'A ruling body composed of the best citizens.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristocracy', 'n.', 'A form a government, in which the supreme power is\n   vested in the principal persons of a state, or in a privileged order;\n   an oligarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristocracy', 'n.', 'The nobles or chief persons in a state; a privileged\n   class or patrician order; (in a popular use) those who are regarded as\n   superior to the rest of the community, as in rank, fortune, or\n   intellect.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristocrat', 'n.', 'One of the aristocracy or people of rank in a\n   community; one of a ruling class; a noble.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristocrat', 'n.', 'One who is overbearing in his temper or habits; a proud\n   or haughty person.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristocrat', 'n.', 'One who favors an aristocracy as a form of government,\n   or believes the aristocracy should govern.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristocratic', 'a.', 'Alt. of Aristocratical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristocratical', 'a.', 'Of or pertaining to an aristocracy; consisting in,\n   or favoring, a government of nobles, or principal men; as, an\n   aristocratic constitution.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristocratical', 'a.', 'Partaking of aristocracy; befitting aristocracy;\n   characteristic of, or originating with, the aristocracy; as, an\n   aristocratic measure; aristocratic pride or manners.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristocratism', 'n.', 'The principles of aristocrats.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristocratism', 'n.', 'Aristocrats, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristology', 'n.', 'The science of dining.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristophanic', 'a.', 'Of or pertaining to Aristophanes, the Athenian comic\n   poet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristotelian', 'a.', 'Of or pertaining to Aristotle, the famous Greek\n   philosopher (384-322 b. c.).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristotelian', 'n.', 'A follower of Aristotle; a Peripatetic. See\n   Peripatetic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristotelianism', '', 'The philosophy of Aristotle, otherwise called the\n   Peripatetic philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristotelic', 'a.', 'Pertaining to Aristotle or to his philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristotles lantern', '', 'The five united jaws and accessory ossicles of\n   certain sea urchins.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aristulate', 'a.', 'Having a short beard or awn.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arithmancy', 'n.', 'Divination by means of numbers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arithmetic', 'n.', 'The science of numbers; the art of computation by\n   figures.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arithmetic', 'n.', 'A book containing the principles of this science.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arithmetical', 'a.', 'Of or pertaining to arithmetic; according to the\n   rules or method of arithmetic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arithmetically', 'adv.', 'Conformably to the principles or methods of\n   arithmetic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arithmetician', 'n.', 'One skilled in arithmetic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arithmomancy', 'n.', 'Arithmancy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arithmometer', 'n.', 'A calculating machine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ark', 'n.', 'A chest, or coffer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ark', 'n.', 'The oblong chest of acacia wood, overlaid with gold, which\n   supported the mercy seat with its golden cherubs, and occupied the most\n   sacred place in the sanctuary. In it Moses placed the two tables of\n   stone containing the ten commandments. Called also the Ark of the\n   Covenant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ark', 'n.', 'The large, chestlike vessel in which Noah and his family were\n   preserved during the Deluge. Gen. vi. Hence: Any place of refuge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ark', 'n.', 'A large flatboat used on Western American rivers to transport\n   produce to market.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arkite', 'a.', 'Belonging to the ark.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ark shell', '', 'A marine bivalve shell belonging to the genus Arca and its\n   allies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arles', 'n. pl.', 'An earnest; earnest money; money paid to bind a bargain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arm', 'n.', 'The limb of the human body which extends from the shoulder to\n   the hand; also, the corresponding limb of a monkey.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arm', 'n.', 'Anything resembling an arm');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arm', 'n.', 'The fore limb of an animal, as of a bear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arm', 'n.', 'A limb, or locomotive or prehensile organ, of an invertebrate\n   animal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arm', 'n.', 'A branch of a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arm', 'n.', 'A slender part of an instrument or machine, projecting from a\n   trunk, axis, or fulcrum; as, the arm of a steelyard.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arm', 'n.', 'The end of a yard; also, the part of an anchor which ends in\n   the fluke.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arm', 'n.', 'An inlet of water from the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arm', 'n.', 'A support for the elbow, at the side of a chair, the end of a\n   sofa, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arm', 'n.', 'Fig.: Power; might; strength; support; as, the secular arm;\n   the arm of the law.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arm', 'n.', 'A branch of the military service; as, the cavalry arm was made\n   efficient.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arm', 'n.', 'A weapon of offense or defense; an instrument of warfare; --\n   commonly in the pl.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armed', 'imp. & p. p.', 'of Arm');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arming', 'p. pr. & vb. n.', 'of Arm');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arm', 'v. t.', 'To take by the arm; to take up in ones arms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arm', 'v. t.', 'To furnish with arms or limbs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arm', 'v. t.', 'To furnish or equip with weapons of offense or defense; as,\n   to arm soldiers; to arm the country.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arm', 'v. t.', 'To cover or furnish with a plate, or with whatever will add\n   strength, force, security, or efficiency; as, to arm the hit of a\n   sword; to arm a hook in angling.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arm', 'v. t.', 'Fig.: To furnish with means of defense; to prepare for\n   resistance; to fortify, in a moral sense.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arm', 'v. i.', 'To provide ones self with arms, weapons, or means of\n   attack or resistance; to take arms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armada', 'v. t.', 'A fleet of armed ships; a squadron. Specifically, the\n   Spanish fleet which was sent to assail England, a. d. 1558.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armadillos', 'pl. ', 'of Armadillo');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armadillo', 'n.', 'Any edentate animal if the family Dasypidae, peculiar to\n   America. The body and head are incased in an armor composed of small\n   bony plates. The armadillos burrow in the earth, seldom going abroad\n   except at night. When attacked, they curl up into a ball, presenting\n   the armor on all sides. Their flesh is good food. There are several\n   species, one of which (the peba) is found as far north as Texas. See\n   Peba, Poyou, Tatouay.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armadillo', 'n.', 'A genus of small isopod Crustacea that can roll\n   themselves into a ball.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armado', 'n.', 'Armada.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armament', 'n.', 'A body of forces equipped for war; -- used of a land or\n   naval force.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armament', 'n.', 'All the cannon and small arms collectively, with their\n   equipments, belonging to a ship or a fortification.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armament', 'n.', 'Any equipment for resistance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armamentary', 'n.', 'An armory; a magazine or arsenal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armature', 'n.', 'Armor; whatever is worn or used for the protection and\n   defense of the body, esp. the protective outfit of some animals and\n   plants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armature', 'n.', 'A piece of soft iron used to connect the two poles of a\n   magnet, or electro-magnet, in order to complete the circuit, or to\n   receive and apply the magnetic force. In the ordinary horseshoe magnet,\n   it serves to prevent the dissipation of the magnetic force.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armature', 'n.', 'Iron bars or framing employed for the consolidation of a\n   building, as in sustaining slender columns, holding up canopies, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armchair', 'n.', 'A chair with arms to support the elbows or forearms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armed', 'a.', 'Furnished with weapons of offense or defense; furnished with\n   the means of security or protection.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armed', 'a.', 'Furnished with whatever serves to add strength, force, or\n   efficiency.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armed', 'a.', 'Having horns, beak, talons, etc; -- said of beasts and birds\n   of prey.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armenian', 'a.', 'Of or pertaining to Armenia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armenian', 'n.', 'A native or one of the people of Armenia; also, the\n   language of the Armenians.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armenian', 'n.', 'An adherent of the Armenian Church, an organization\n   similar in some doctrines and practices to the Greek Church, in others\n   to the Roman Catholic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armet', 'n.', 'A kind of helmet worn in the 14th, 15th, and 16th centuries.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armfulus', 'pl. ', 'of Armful');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armful', 'n.', 'As much as the arm can hold.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armgaunt', 'a.', 'With gaunt or slender legs. (?)');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arm-gret', 'a.', 'Great as a mans arm.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armhole', 'n.', 'The cavity under the shoulder; the armpit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armhole', 'n.', 'A hole for the arm in a garment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armiferous', 'a.', 'Bearing arms or weapons.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armiger', 'n.', 'Formerly, an armor bearer, as of a knight, an esquire who\n   bore his shield and rendered other services. In later use, one next in\n   degree to a knight, and entitled to armorial bearings. The term is now\n   superseded by esquire.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armigerous', 'a.', 'Bearing arms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armil', 'n.', 'A bracelet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armil', 'n.', 'An ancient astronomical instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armillas', 'pl. ', 'of Armilla');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armillae', 'pl. ', 'of Armilla');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armilla', 'n.', 'An armil.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armilla', 'n.', 'A ring of hair or feathers on the legs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armillary', 'n.', 'Pertaining to, or resembling, a bracelet or ring;\n   consisting of rings or circles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arming', 'n.', 'The act of furnishing with, or taking, arms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arming', 'n.', 'A piece of tallow placed in a cavity at the lower end of a\n   sounding lead, to bring up the sand, shells, etc., of the sea bottom.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arming', 'n.', 'Red dress cloths formerly hung fore and aft outside of a\n   ships upper works on holidays.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arminian', 'a.', 'Of or pertaining to Arminius of his followers, or to\n   their doctrines. See note under Arminian, n.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arminian', 'n.', 'One who holds the tenets of Arminius, a Dutch divine (b.\n   1560, d. 1609).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arminianism', 'n.', 'The religious doctrines or tenets of the Arminians.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armipotence', 'n.', 'Power in arms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armipotent', 'a.', 'Powerful in arms; mighty in battle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armisonant', 'a.', 'Alt. of Armisonous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armisonous', 'a.', 'Rustling in arms; resounding with arms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armistice', 'n.', 'A cessation of arms for a short time, by convention; a\n   temporary suspension of hostilities by agreement; a truce.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armless', 'a.', 'Without any arm or branch.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armless', 'a.', 'Destitute of arms or weapons.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armlet', 'n.', 'A small arm; as, an armlet of the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armlet', 'n.', 'An arm ring; a bracelet for the upper arm.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armlet', 'n.', 'Armor for the arm.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armoniac', 'a.', 'Ammoniac.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armor', 'n.', 'Defensive arms for the body; any clothing or covering worn\n   to protect ones person in battle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armor', 'n.', 'Steel or iron covering, whether of ships or forts,\n   protecting them from the fire of artillery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armor-bearer', 'n.', 'One who carries the armor or arms of another; an\n   armiger.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armored', 'a.', 'Clad with armor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armorer', 'n.', 'One who makes or repairs armor or arms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armorer', 'n.', 'Formerly, one who had care of the arms and armor of a\n   knight, and who dressed him in armor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armorer', 'n.', 'One who has the care of arms and armor, cleans or repairs\n   them, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armorial', 'a.', 'Belonging to armor, or to the heraldic arms or escutcheon\n   of a family.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armoric', 'a.', 'Alt. of Armorican');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armorican', 'a.', 'Of or pertaining to the northwestern part of France\n   (formerly called Armorica, now Bretagne or Brittany); or to its people.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armorican', 'n.', 'The language of the Armoricans, a Celtic dialect which\n   has remained to the present times.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armorican', 'n.', 'A native of Armorica.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armorist', 'n.', 'One skilled in coat armor or heraldry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armor-plated', 'a.', 'Covered with defensive plates of metal, as a ship of\n   war; steel-clad.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armories', 'pl. ', 'of Armory');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armory', 'n.', 'A place where arms and instruments of war are deposited for\n   safe keeping.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armory', 'n.', 'Armor; defensive and offensive arms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armory', 'n.', 'A manufactory of arms, as rifles, muskets, pistols,\n   bayonets, swords.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armory', 'n.', 'Ensigns armorial; armorial bearings.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armory', 'n.', 'That branch of heraldry which treats of coat armor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armozeen', 'n.', 'Alt. of Armozine');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armozine', 'n.', 'A thick plain silk, generally black, and used for\n   clerical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armpit', 'n.', 'The hollow beneath the junction of the arm and shoulder;\n   the axilla.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armrack', 'n.', 'A frame, generally vertical, for holding small arms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arms', 'n.', 'Instruments or weapons of offense or defense.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arms', 'n.', 'The deeds or exploits of war; military service or science.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arms', 'n.', 'Anything which a man takes in his hand in anger, to strike or\n   assault another with; an aggressive weapon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arms', 'n.', 'The ensigns armorial of a family, consisting of figures and\n   colors borne in shields, banners, etc., as marks of dignity and\n   distinction, and descending from father to son.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arms', 'n.', 'The legs of a hawk from the thigh to the foot.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armure', 'n.', 'Armor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Armure', 'n.', 'A variety of twilled fabric ribbed on the surface.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Army', 'n.', 'A collection or body of men armed for war, esp. one organized\n   in companies, battalions, regiments, brigades, and divisions, under\n   proper officers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Army', 'n.', 'A body of persons organized for the advancement of a cause;\n   as, the Blue Ribbon Army.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Army', 'n.', 'A great number; a vast multitude; a host.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Army worm', '', 'A lepidopterous insect, which in the larval state often\n   travels in great multitudes from field to field, destroying grass,\n   grain, and other crops. The common army worm of the northern United\n   States is Leucania unipuncta. The name is often applied to other\n   related species, as the cotton worm.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Army worm', '', 'The larva of a small two-winged fly (Sciara); which\n   marches in large companies, in regular order. See Cotton worm, under\n   Cotton.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arna', 'n.', 'Alt. of Arnee');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arnee', 'n.', 'The wild buffalo of India (Bos, or Bubalus, arni); larger\n   than the domestic buffalo and having enormous horns.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arnatto', 'n.', 'See Annotto.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arnica', 'n.', 'A genus of plants; also, the most important species (Arnica\n   montana); native of the mountains of Europe, used in medicine as a\n   narcotic and stimulant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arnicin', 'n.', 'An active principle of Arnica montana. It is a bitter\n   resin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arnicine', 'n.', 'An alkaloid obtained from the arnica plant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arnot', 'n.', 'Alt. of Arnut');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arnut', 'n.', 'The earthnut.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arnotto', 'n.', 'Same as Annotto.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aroid', 'a.', 'Alt. of Aroideous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aroideous', 'a.', 'Belonging to, or resembling, the Arum family of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aroint', 'interj.', 'Stand off, or begone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aroint', 'v. t.', 'To drive or scare off by some exclamation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aroma', 'n.', 'The quality or principle of plants or other substances which\n   constitutes their fragrance; agreeable odor; as, the aroma of coffee.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aroma', 'n.', 'Fig.: The fine diffusive quality of intellectual power;\n   flavor; as, the subtile aroma of genius.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aromatic', 'a.', 'Alt. of Aromatical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aromatical', 'a.', 'Pertaining to, or containing, aroma; fragrant; spicy;\n   strong-scented; odoriferous; as, aromatic balsam.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aromatic', 'n.', 'A plant, drug, or medicine, characterized by a fragrant\n   smell, and usually by a warm, pungent taste, as ginger, cinnamon,\n   spices.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aromatization', 'n.', 'The act of impregnating or secting with aroma.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aromatized', 'imp. & p. p.', 'of Aromatize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aromatizing', 'p. pr. & vb. n.', 'of Aromatize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aromatize', 'v. t.', 'To impregnate with aroma; to render aromatic; to give\n   a spicy scent or taste to; to perfume.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aromatizer', 'n.', 'One who, or that which, aromatizes or renders aromatic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aromatous', 'a.', 'Aromatic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aroph', 'n.', 'A barbarous word used by the old chemists to designate\n   various medical remedies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arose', '', 'The past or preterit tense of Arise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Around', 'adv.', 'In a circle; circularly; on every side; round.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Around', 'adv.', 'In a circuit; here and there within the surrounding\n   space; all about; as, to travel around from town to town.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Around', 'adv.', 'Near; in the neighborhood; as, this man was standing\n   around when the fight took place.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Around', 'prep.', 'On all sides of; encircling; encompassing; so as to make\n   the circuit of; about.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Around', 'prep.', 'From one part to another of; at random through; about;\n   on another side of; as, to travel around the country; a house standing\n   around the corner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arousal', 'n.', 'The act of arousing, or the state of being aroused.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aroused', 'imp. & p. p.', 'of Arouse');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arousing', 'p. pr. & vb. n.', 'of Arouse');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arouse', 'v. t.', 'To excite to action from a state of rest; to stir, or\n   put in motion or exertion; to rouse; to excite; as, to arouse one from\n   sleep; to arouse the dormant faculties.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arow', 'adv.', 'In a row, line, or rank; successively; in order.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aroynt', 'interj.', 'See Aroint.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arpeggio', 'n.', 'The production of the tones of a chord in rapid\n   succession, as in playing the harp, and not simultaneously; a strain\n   thus played.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arpent', 'n.', 'Alt. of Arpen');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arpen', 'n.', 'Formerly, a measure of land in France, varying in different\n   parts of the country. The arpent of Paris was 4,088 sq. yards, or\n   nearly five sixths of an English acre. The woodland arpent was about 1\n   acre, 1 rood, 1 perch, English.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arpentator', 'n.', 'The Anglicized form of the French arpenteur, a land\n   surveyor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arpine', 'n.', 'An arpent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arquated', 'a.', 'Shaped like a bow; arcuate; curved.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arquebus', 'n.', 'Alt. of Arquebuse');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arquebuse', 'n.', 'A sort of hand gun or firearm a contrivance answering to\n   a trigger, by which the burning match was applied. The musket was a\n   later invention.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arquebusade', 'n.', 'The shot of an arquebus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arquebusade', 'n.', 'A distilled water from a variety of aromatic plants,\n   as rosemary, millefoil, etc.; -- originally used as a vulnerary in\n   gunshot wounds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arquebusier', 'n.', 'A soldier armed with an arquebus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arquifoux', 'n.', 'Same as Alquifou.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrach', 'n.', 'See Orach.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrack', 'n.', 'A name in the East Indies and the Indian islands for all\n   ardent spirits. Arrack is often distilled from a fermented mixture of\n   rice, molasses, and palm wine of the cocoanut tree or the date palm,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arragonite', 'n.', 'See Aragonite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arraigned', 'imp. & p. p.', 'of Arraign');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arraigning', 'p. pr. & vb. n.', 'of Arraign');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arraign', 'v. t.', 'To call or set as a prisoner at the bar of a court to\n   answer to the matter charged in an indictment or complaint.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arraign', 'v. t.', 'To call to account, or accuse, before the bar of\n   reason, taste, or any other tribunal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arraign', 'n.', 'Arraignment; as, the clerk of the arraigns.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arraign', 'v. t.', 'To appeal to; to demand; as, to arraign an assize of\n   novel disseizin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arraigner', 'n.', 'One who arraigns.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arraignment', 'n.', 'The act of arraigning, or the state of being\n   arraigned; the act of calling and setting a prisoner before a court to\n   answer to an indictment or complaint.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arraignment', 'n.', 'A calling to an account to faults; accusation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arraiment', 'v. t.', 'Alt. of Arrayment');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrayment', 'v. t.', 'Clothes; raiment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arranged', 'imp. & p. p.', 'of Arrange');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arranging', 'p. pr. & vb. n.', 'of Arrange');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrange', 'v. t.', 'To put in proper order; to dispose (persons, or parts)\n   in the manner intended, or best suited for the purpose; as, troops\n   arranged for battle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrange', 'v. t.', 'To adjust or settle; to prepare; to determine; as, to\n   arrange the preliminaries of an undertaking.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrangement', 'n.', 'The act of arranging or putting in an orderly\n   condition; the state of being arranged or put in order; disposition in\n   suitable form.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrangement', 'n.', 'The manner or result of arranging; system of parts\n   disposed in due order; regular and systematic classification; as,\n   arrangement of ones dress; the Linnaean arrangement of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrangement', 'n.', 'Preparatory proceeding or measure; preparation; as, we\n   have made arrangement for receiving company.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrangement', 'n.', 'Settlement; adjustment by agreement; as, the parties\n   have made an arrangement between themselves concerning their disputes;\n   a satisfactory arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrangement', 'n.', 'The adaptation of a composition to voices or\n   instruments for which it was not originally written.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrangement', 'n.', 'A piece so adapted; a transcription; as, a pianoforte\n   arrangement of Beethovens symphonies; an orchestral arrangement of a\n   song, an opera, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arranger', 'n.', 'One who arranges.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrant', 'a.', 'Notoriously or preeminently bad; thorough or downright, in\n   a bad sense; shameless; unmitigated; as, an arrant rogue or coward.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrant', 'a.', 'Thorough or downright, in a good sense.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrantly', 'adv.', 'Notoriously, in an ill sense; infamously; impudently;\n   shamefully.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arras', 'n.', 'Tapestry; a rich figured fabric; especially, a screen or\n   hangings of heavy cloth with interwoven figures.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arras', 'v. t.', 'To furnish with an arras.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrasene', 'n.', 'A material of wool or silk used for working the figures\n   in embroidery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrastre', 'n.', 'A rude apparatus for pulverizing ores, esp. those\n   containing free gold.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arraswise', 'adv.', 'Alt. of Arrasways');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrasways', 'adv.', 'Placed in such a position as to exhibit the top and\n   two sides, the corner being in front; -- said of a rectangular form.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arraught', '', 'Obtained; seized.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Array', 'n.', 'Order; a regular and imposing arrangement; disposition in\n   regular lines; hence, order of battle; as, drawn up in battle array.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Array', 'n.', 'The whole body of persons thus placed in order; an orderly\n   collection; hence, a body of soldiers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Array', 'n.', 'An imposing series of things.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Array', 'n.', 'Dress; garments disposed in order upon the person; rich or\n   beautiful apparel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Array', 'n.', 'A ranking or setting forth in order, by the proper officer,\n   of a jury as impaneled in a cause.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Array', 'n.', 'The panel itself.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Array', 'n.', 'The whole body of jurors summoned to attend the court.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrayed', 'imp. & p. p.', 'of Array');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arraying', 'p. pr. & vb. n.', 'of Array');
  INSERT INTO words (word, word_type, description) VALUES  (  'Array', 'n.', 'To place or dispose in order, as troops for battle; to\n   marshal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Array', 'n.', 'To deck or dress; to adorn with dress; to cloth to envelop;\n   -- applied esp. to dress of a splendid kind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Array', 'n.', 'To set in order, as a jury, for the trial of a cause; that\n   is, to call them man by man.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrayer', 'n.', 'One who arrays. In some early English statutes, applied to\n   an officer who had care of the soldiers armor, and who saw them duly\n   accoutered.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrear', 'adv.', 'To or in the rear; behind; backwards.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrear', 'n.', 'That which is behind in payment, or which remains unpaid,\n   though due; esp. a remainder, or balance which remains due when some\n   part has been paid; arrearage; -- commonly used in the plural, as,\n   arrears of rent, wages, or taxes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrearage', 'n.', 'That which remains unpaid and overdue, after payment of\n   a part; arrears.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrect', 'a.', 'Alt. of Arrected');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrected', 'a.', 'Lifted up; raised; erect.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrected', 'a.', 'Attentive, as a person listening.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrect', 'v. t.', 'To direct.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrect', 'v. t.', 'To impute.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrectary', 'n.', 'An upright beam.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrenotokous', 'a.', 'Producing males from unfertilized eggs, as certain\n   wasps and bees.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrentation', '', 'A letting or renting, esp. a license to inclose land in\n   a forest with a low hedge and a ditch, under a yearly rent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arreption', 'n.', 'The act of taking away.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arreptitious', 'a.', 'Snatched away; seized or possessed, as a demoniac;\n   raving; mad; crack-brained.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrested', 'imp. & p. p.', 'of Arrest');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arresting', 'p. pr. & vb. n.', 'of Arrest');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrest', 'v. t.', 'To stop; to check or hinder the motion or action of; as,\n   to arrest the current of a river; to arrest the senses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrest', 'v. t.', 'To take, seize, or apprehend by authority of law; as, to\n   arrest one for debt, or for a crime.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrest', 'v. t.', 'To seize on and fix; to hold; to catch; as, to arrest\n   the eyes or attention.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrest', 'v. t.', 'To rest or fasten; to fix; to concentrate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrest', 'v. i.', 'To tarry; to rest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrest', 'v. t.', 'The act of stopping, or restraining from further motion,\n   etc.; stoppage; hindrance; restraint; as, an arrest of development.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrest', 'v. t.', 'The taking or apprehending of a person by authority of\n   law; legal restraint; custody. Also, a decree, mandate, or warrant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrest', 'v. t.', 'Any seizure by power, physical or moral.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrest', 'v. t.', 'A scurfiness of the back part of the hind leg of a\n   horse; -- also named rat-tails.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrestation', 'n.', 'Arrest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrestee', 'v.', 'The person in whose hands is the property attached by\n   arrestment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrester', 'n.', 'One who arrests.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrester', 'n.', 'The person at whose suit an arrestment is made.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arresting', 'a.', 'Striking; attracting attention; impressive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrestive', 'a.', 'Tending to arrest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrestment', 'n.', 'The arrest of a person, or the seizure of his effects;\n   esp., a process by which money or movables in the possession of a third\n   party are attached.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrestment', 'n.', 'A stoppage or check.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arret', 'n.', 'A judgment, decision, or decree of a court or high tribunal;\n   also, a decree of a sovereign.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arret', 'n.', 'An arrest; a legal seizure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arret', 'v. t.', 'Same as Aret.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrhaphostic', 'a.', 'Seamless.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrhizal', 'a.', 'Alt. of Arrhizous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrhizous', 'a.', 'Destitute of a true root, as a parasitical plant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrhythmic', 'a.', 'Alt. of Arrhythmous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrhythmous', 'a.', 'Being without rhythm or regularity, as the pulse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrhytmy', 'n.', 'Want of rhythm.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arride', 'v. t.', 'To please; to gratify.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arriere', 'n.', '\"That which is behind\"; the rear; -- chiefly used as an\n   adjective in the sense of behind, rear, subordinate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arriere-ban', 'n.', 'A proclamation, as of the French kings, calling not\n   only their immediate feudatories, but the vassals of these feudatories,\n   to take the field for war; also, the body of vassals called or liable\n   to be called to arms, as in ancient France.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arris', 'n.', 'The sharp edge or salient angle formed by two surfaces\n   meeting each other, whether plane or curved; -- applied particularly to\n   the edges in moldings, and to the raised edges which separate the\n   flutings in a Doric column.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrish', 'n.', 'The stubble of wheat or grass; a stubble field; eddish.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arriswise', 'adv.', 'Diagonally laid, as tiles; ridgewise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrival', 'n.', 'The act of arriving, or coming; the act of reaching a\n   place from a distance, whether by water (as in its original sense) or\n   by land.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrival', 'n.', 'The attainment or reaching of any object, by effort, or in\n   natural course; as, our arrival at this conclusion was wholly\n   unexpected.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrival', 'n.', 'The person or thing arriving or which has arrived; as,\n   news brought by the last arrival.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrival', 'n.', 'An approach.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrivance', 'n.', 'Arrival.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrived', 'imp. & p. p.', 'of Arrive');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arriving', 'p. pr. & vb. n.', 'of Arrive');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrive', 'v. i.', 'To come to the shore or bank. In present usage: To come\n   in progress by water, or by traveling on land; to reach by water or by\n   land; -- followed by at (formerly sometimes by to); also by in and\n   from.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrive', 'v. i.', 'To reach a point by progressive motion; to gain or\n   compass an object by effort, practice, study, inquiry, reasoning, or\n   experiment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrive', 'v. i.', 'To come; said of time; as, the time arrived.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrive', 'v. i.', 'To happen or occur.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrive', 'v. t.', 'To bring to shore.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrive', 'v. t.', 'To reach; to come to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrive', 'n.', 'Arrival.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arriver', 'n.', 'One who arrives.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arroba', 'n.', 'A Spanish weight used in Mexico and South America = 25.36\n   lbs. avoir.; also, an old Portuguese weight, used in Brazil = 32.38\n   lbs. avoir.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arroba', 'n.', 'A Spanish liquid measure for wine = 3.54 imp. gallons, and\n   for oil = 2.78 imp. gallons.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrogance', 'n.', 'The act or habit of arrogating, or making undue claims\n   in an overbearing manner; that species of pride which consists in\n   exorbitant claims of rank, dignity, estimation, or power, or which\n   exalts the worth or importance of the person to an undue degree; proud\n   contempt of others; lordliness; haughtiness; self-assumption;\n   presumption.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrogancy', 'n.', 'Arrogance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrogant', 'a.', 'Making, or having the disposition to make, exorbitant\n   claims of rank or estimation; giving ones self an undue degree of\n   importance; assuming; haughty; -- applied to persons.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrogant', 'a.', 'Containing arrogance; marked with arrogance; proceeding\n   from undue claims or self-importance; -- applied to things; as,\n   arrogant pretensions or behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrogantly', 'adv.', 'In an arrogant manner; with undue pride or\n   self-importance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrogantness', 'n.', 'Arrogance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrogated', 'imp. & p. p.', 'of Arrogate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrogating', 'p. pr. & vb. n.', 'of Arrogate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrogate', 'v. t.', 'To assume, or claim as ones own, unduly, proudly, or\n   presumptuously; to make undue claims to, from vanity or baseless\n   pretensions to right or merit; as, the pope arrogated dominion over\n   kings.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrogation', 'n.', 'The act of arrogating, or making exorbitant claims; the\n   act of taking more than one is justly entitled to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrogation', 'n.', 'Adoption of a person of full age.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrogative', 'a.', 'Making undue claims and pretension; prone to arrogance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrondissement', 'n.', 'A subdivision of a department.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrose', 'v. t.', 'To drench; to besprinkle; to moisten.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrosion', 'n.', 'A gnawing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrow', 'n.', 'A missile weapon of offense, slender, pointed, and usually\n   feathered and barbed, to be shot from a bow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrow grass', 'n.', 'An herbaceous grasslike plant (Triglochin palustre,\n   and other species) with pods opening so as to suggest barbed\n   arrowheads.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrowhead', 'n.', 'The head of an arrow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrowhead', 'n.', 'An aquatic plant of the genus Sagittaria, esp. S.\n   sagittifolia, -- named from the shape of the leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrowheaded', 'a.', 'Shaped like the head of an arrow; cuneiform.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrowroot', 'n.', 'A west Indian plant of the genus Maranta, esp. M.\n   arundinacea, now cultivated in many hot countries. It said that the\n   Indians used the roots to neutralize the venom in wounds made by\n   poisoned arrows.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrowroot', 'n.', 'A nutritive starch obtained from the rootstocks of\n   Maranta arundinacea, and used as food, esp. for children an invalids;\n   also, a similar starch obtained from other plants, as various species\n   of Maranta and Curcuma.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrowwood', 'n.', 'A shrub (Viburnum dentatum) growing in damp woods and\n   thickets; -- so called from the long, straight, slender shoots.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrowworm', 'n.', 'A peculiar transparent worm of the genus Sagitta, living\n   at the surface of the sea. See Sagitta.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrowy', 'a.', 'Consisting of arrows.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arrowy', 'a.', 'Formed or moving like, or in any respect resembling, an\n   arrow; swift; darting; piercing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arroyos', 'pl. ', 'of Arroyo');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arroyo', 'n.', 'A water course; a rivulet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arroyo', 'n.', 'The dry bed of a small stream.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arschin', 'n.', 'See Arshine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arse', 'n.', 'The buttocks, or hind part of an animal; the posteriors; the\n   fundament; the bottom.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arsenal', 'n.', 'A public establishment for the storage, or for the\n   manufacture and storage, of arms and all military equipments, whether\n   for land or naval service.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arsenate', 'n.', 'A salt of arsenic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arseniate', 'n.', 'See Arsenate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arsenic', 'n.', 'One of the elements, a solid substance resembling a metal\n   in its physical properties, but in its chemical relations ranking with\n   the nonmetals. It is of a steel-gray color and brilliant luster, though\n   usually dull from tarnish. It is very brittle, and sublimes at 356¡\n   Fahrenheit. It is sometimes found native, but usually combined with\n   silver, cobalt, nickel, iron, antimony, or sulphur. Orpiment and\n   realgar are two of its sulphur compounds, the first of which is the\n   true arsenicum of the ancients. The element and its compounds are\n   active poisons. Specific gravity from 5.7 to 5.9. Atomic weight 75.\n   Symbol As.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arsenic', 'n.', 'Arsenious oxide or arsenious anhydride; -- called also\n   arsenious acid, white arsenic, and ratsbane.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arsenic', 'a.', 'Pertaining to, or derived from, arsenic; -- said of those\n   compounds of arsenic in which this element has its highest equivalence;\n   as, arsenic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arsenical', 'a.', 'Of or pertaining to, or containing, arsenic; as,\n   arsenical vapor; arsenical wall papers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arsenicated', 'imp. & p. p.', 'of Arsenicate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arsenicating', 'p. pr. & vb. n.', 'of Arsenicate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arsenicate', 'v. t.', 'To combine with arsenic; to treat or impregnate with\n   arsenic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arsenicism', 'n.', 'A diseased condition produced by slow poisoning with\n   arsenic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arsenide', 'n.', 'A compound of arsenic with a metal, or positive element\n   or radical; -- formerly called arseniuret.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arseniferous', 'a.', 'Containing or producing arsenic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arsenious', 'a.', 'Pertaining to, consisting of, or containing, arsenic;\n   as, arsenious powder or glass.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arsenious', 'a.', 'Pertaining to, or derived from, arsenic, when having an\n   equivalence next lower than the highest; as, arsenious acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arsenite', 'n.', 'A salt formed by the union of arsenious acid with a base.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arseniuret', 'n.', 'See Arsenide.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arseniureted', 'a.', 'Combined with arsenic; -- said some elementary\n   substances or radicals; as, arseniureted hydrogen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arsenopyrite', 'n.', 'A mineral of a tin-white color and metallic luster,\n   containing arsenic, sulphur, and iron; -- also called arsenical pyrites\n   and mispickel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arsesmart', 'n.', 'Smartweed; water pepper.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arshine', 'n.', 'A Russian measure of length = 2 ft. 4.246 inches.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arsine', 'n.', 'A compound of arsenic and hydrogen, AsH3, a colorless and\n   exceedingly poisonous gas, having an odor like garlic; arseniureted\n   hydrogen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arsis', 'n.', 'That part of a foot where the ictus is put, or which is\n   distinguished from the rest (known as the thesis) of the foot by a\n   greater stress of voice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arsis', 'n.', 'That elevation of voice now called metrical accentuation, or\n   the rhythmic accent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arsis', 'n.', 'The elevation of the hand, or that part of the bar at which\n   it is raised, in beating time; the weak or unaccented part of the bar;\n   -- opposed to thesis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arsmetrike', 'n.', 'Arithmetic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arson', 'n.', 'The malicious burning of a dwelling house or outhouse of\n   another man, which by the common law is felony; the malicious and\n   voluntary firing of a building or ship.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Art', '', 'The second person singular, indicative mode, present tense, of\n   the substantive verb Be; but formed after the analogy of the plural\n   are, with the ending -t, as in thou shalt, wilt, orig. an ending of the\n   second person sing. pret. Cf. Be. Now used only in solemn or poetical\n   style.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Art', 'n.', 'The employment of means to accomplish some desired end; the\n   adaptation of things in the natural world to the uses of life; the\n   application of knowledge or power to practical purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Art', 'n.', 'A system of rules serving to facilitate the performance of\n   certain actions; a system of principles and rules for attaining a\n   desired end; method of doing well some special work; -- often\n   contradistinguished from science or speculative principles; as, the art\n   of building or engraving; the art of war; the art of navigation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Art', 'n.', 'The systematic application of knowledge or skill in effecting\n   a desired result. Also, an occupation or business requiring such\n   knowledge or skill.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Art', 'n.', 'The application of skill to the production of the beautiful by\n   imitation or design, or an occupation in which skill is so employed, as\n   in painting and sculpture; one of the fine arts; as, he prefers art to\n   literature.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Art', 'n.', 'Those branches of learning which are taught in the academical\n   course of colleges; as, master of arts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Art', 'n.', 'Learning; study; applied knowledge, science, or letters.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Art', 'n.', 'Skill, dexterity, or the power of performing certain actions,\n   acquired by experience, study, or observation; knack; as, a man has the\n   art of managing his business to advantage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Art', 'n.', 'Skillful plan; device.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Art', 'n.', 'Cunning; artifice; craft.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Art', 'n.', 'The black art; magic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artemia', 'n.', 'A genus of phyllopod Crustacea found in salt lakes and\n   brines; the brine shrimp. See Brine shrimp.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artemisia', 'n.', 'A genus of plants including the plants called mugwort,\n   southernwood, and wormwood. Of these A. absinthium, or common wormwood,\n   is well known, and A. tridentata is the sage brush of the Rocky\n   Mountain region.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arteriac', 'a.', 'Of or pertaining to the windpipe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arterial', 'a.', 'Of or pertaining to an artery, or the arteries; as,\n   arterial action; the arterial system.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arterial', 'a.', 'Of or pertaining to a main channel (resembling an\n   artery); as a river, canal, or railroad.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arterialization', 'n.', 'The process of converting venous blood into\n   arterial blood during its passage through the lungs, oxygen being\n   absorbed and carbonic acid evolved; -- called also aeration and\n   hematosis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arterialized', 'imp. & p. p.', 'of Arterialize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arterializing', 'p. pr. & vb. n.', 'of Arterialize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arterialize', 'v. t.', 'To transform, as the venous blood, into arterial\n   blood by exposure to oxygen in the lungs; to make arterial.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arteriography', 'n.', 'A systematic description of the arteries.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arteriole', 'n.', 'A small artery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arteriology', 'n.', 'That part of anatomy which treats of arteries.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arteriotomy', 'n.', 'The opening of an artery, esp. for bloodletting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arteriotomy', 'n.', 'That part of anatomy which treats of the dissection of\n   the arteries.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arteritis', 'n.', 'Inflammation of an artery or arteries.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arteries', 'pl. ', 'of Artery');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artery', 'n.', 'The trachea or windpipe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artery', 'n.', 'One of the vessels or tubes which carry either venous or\n   arterial blood from the heart. They have tricker and more muscular\n   walls than veins, and are connected with them by capillaries.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artery', 'n.', 'Hence: Any continuous or ramified channel of communication;\n   as, arteries of trade or commerce.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artesian', 'a.', 'Of or pertaining to Artois (anciently called Artesium);\n   in France.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artful', 'a.', 'Performed with, or characterized by, art or skill.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artful', 'a.', 'Artificial; imitative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artful', 'a.', 'Using or exhibiting much art, skill, or contrivance;\n   dexterous; skillful.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artful', 'a.', 'Cunning; disposed to cunning indirectness of dealing;\n   crafty; as, an artful boy. [The usual sense.]');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artfully', 'adv.', 'In an artful manner; with art or cunning; skillfully;\n   dexterously; craftily.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artfulness', 'n.', 'The quality of being artful; art; cunning; craft.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arthen', 'a.', 'Same as');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arthritic', 'a.', 'Alt. of Arthritical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arthritical', 'a.', 'Pertaining to the joints.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arthritical', 'a.', 'Of or pertaining to arthritis; gouty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arthritis', 'n.', 'Any inflammation of the joints, particularly the gout.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arthroderm', 'n.', 'The external covering of an Arthropod.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arthrodia', 'n.', 'A form of diarthrodial articulation in which the\n   articular surfaces are nearly flat, so that they form only an imperfect\n   ball and socket.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arthrodial', 'a.', 'Alt. of Arthrodic');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arthrodic', 'a.', 'Of or pertaining to arthrodia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arthrodynia', 'n.', 'An affection characterized by pain in or about a\n   joint, not dependent upon structural disease.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arthrodynic', 'a.', 'Pertaining to arthrodynia, or pain in the joints;\n   rheumatic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arthrogastra', 'n. pl.', 'A division of the Arachnida, having the abdomen\n   annulated, including the scorpions, harvestmen, etc.; pedipalpi.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arthrography', 'n.', 'The description of joints.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arthrology', 'n.', 'That part of anatomy which treats of joints.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arthromere', 'n.', 'One of the body segments of Arthropods. See\n   Arthrostraca.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arthropleura', 'n.', 'The side or limb-bearing portion of an arthromere.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arthropod', 'n.', 'One of the Arthropoda.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arthropoda', 'n. pl.', 'A large division of Articulata, embracing all those\n   that have jointed legs. It includes Insects, Arachnida, Pychnogonida,\n   and Crustacea.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arthropomata', 'n. pl.', 'One of the orders of Branchiopoda. See\n   Branchiopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arthrosis', 'n.', 'Articulation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arthrostraca', 'n. pl.', 'One of the larger divisions of Crustacea, so\n   called because the thorax and abdomen are both segmented;\n   Tetradecapoda. It includes the Amphipoda and Isopoda.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arthrozoic', 'a.', 'Of or pertaining to the Articulata; articulate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artiad', 'a.', 'Even; not odd; -- said of elementary substances and of\n   radicals the valence of which is divisible by two without a remainder.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artichoke', 'n.', 'The Cynara scolymus, a plant somewhat resembling a\n   thistle, with a dilated, imbricated, and prickly involucre. The head\n   (to which the name is also applied) is composed of numerous oval\n   scales, inclosing the florets, sitting on a broad receptacle, which,\n   with the fleshy base of the scales, is much esteemed as an article of\n   food.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artichoke', 'n.', 'See Jerusalem artichoke.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Article', 'n.', 'A distinct portion of an instrument, discourse, literary\n   work, or any other writing, consisting of two or more particulars, or\n   treating of various topics; as, an article in the Constitution. Hence:\n   A clause in a contract, system of regulations, treaty, or the like; a\n   term, condition, or stipulation in a contract; a concise statement; as,\n   articles of agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Article', 'n.', 'A literary composition, forming an independent portion of\n   a magazine, newspaper, or cyclopedia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Article', 'n.', 'Subject; matter; concern; distinct.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Article', 'n.', 'A distinct part.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Article', 'n.', 'A particular one of various things; as, an article of\n   merchandise; salt is a necessary article.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Article', 'n.', 'Precise point of time; moment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Article', 'n.', 'One of the three words, a, an, the, used before nouns to\n   limit or define their application. A (or an) is called the indefinite\n   article, the the definite article.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Article', 'n.', 'One of the segments of an articulated appendage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articled', 'imp. & p. p.', 'of Article');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articling', 'p. pr. & vb. n.', 'of Article');
  INSERT INTO words (word, word_type, description) VALUES  (  'Article', 'n.', 'To formulate in articles; to set forth in distinct\n   particulars.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Article', 'n.', 'To accuse or charge by an exhibition of articles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Article', 'n.', 'To bind by articles of covenant or stipulation; as, to\n   article an apprentice to a mechanic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Article', 'v. i.', 'To agree by articles; to stipulate; to bargain; to\n   covenant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articled', 'a.', 'Bound by articles; apprenticed; as, an articled clerk.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articular', 'n.', 'Of or pertaining to the joints; as, an articular\n   disease; an articular process.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articular', 'n.', 'Alt. of Articulary');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulary', 'n.', 'A bone in the base of the lower jaw of many birds,\n   reptiles, amphibians, and fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articularly', 'adv.', 'In an articular or an articulate manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulata', 'v.', 'One of the four subkingdoms in the classification of\n   Cuvier. It has been much modified by later writers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulata', 'v.', 'One of the subdivisions of the Brachiopoda, including\n   those that have the shells united by a hinge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulata', 'v.', 'A subdivision of the Crinoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulate', 'a.', 'Expressed in articles or in separate items or\n   particulars.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulate', 'a.', 'Jointed; formed with joints; consisting of segments\n   united by joints; as, articulate animals or plants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulate', 'a.', 'Distinctly uttered; spoken so as to be intelligible;\n   characterized by division into words and syllables; as, articulate\n   speech, sounds, words.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulate', 'n.', 'An animal of the subkingdom Articulata.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulated', 'imp. & p. p.', 'of Articulate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulating', 'p. pr. & vb. n.', 'of Articulate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulate', 'v. i.', 'To utter articulate sounds; to utter the elementary\n   sounds of a language; to enunciate; to speak distinctly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulate', 'v. i.', 'To treat or make terms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulate', 'v. i.', 'To join or be connected by articulation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulate', 'v. t.', 'To joint; to unite by means of a joint; to put\n   together with joints or at the joints.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulate', 'v. t.', 'To draw up or write in separate articles; to\n   particularize; to specify.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulate', 'v. t.', 'To form, as the elementary sounds; to utter in\n   distinct syllables or words; to enunciate; as, to articulate letters or\n   language.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulate', 'v. t.', 'To express distinctly; to give utterance to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulated', 'a.', 'United by, or provided with, articulations; jointed;\n   as, an articulated skeleton.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulated', 'a.', 'Produced, as a letter, syllable, or word, by the\n   organs of speech; pronounced.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulately', 'adv.', 'After the manner, or in the form, of a joint.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulately', 'adv.', 'Article by article; in distinct particulars; in\n   detail; definitely.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulately', 'adv.', 'With distinct utterance of the separate sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulateness', 'n.', 'Quality of being articulate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulation', 'n.', 'A joint or juncture between bones in the skeleton.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulation', 'n.', 'The connection of the parts of a plant by joints, as\n   in pods.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulation', 'n.', 'One of the nodes or joints, as in cane and maize.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulation', 'n.', 'One of the parts intercepted between the joints;\n   also, a subdivision into parts at regular or irregular intervals as a\n   result of serial intermission in growth, as in the cane, grasses, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulation', 'n.', 'The act of putting together with a joint or joints;\n   any meeting of parts in a joint.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulation', 'n.', 'The state of being jointed; connection of parts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulation', 'n.', 'The utterance of the elementary sounds of a language\n   by the appropriate movements of the organs, as in pronunciation; as, a\n   distinct articulation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulation', 'n.', 'A sound made by the vocal organs; an articulate\n   utterance or an elementary sound, esp. a consonant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulative', 'a.', 'Of or pertaining to articulation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulator', 'n.', 'One who, or that which, articulates; as: (a) One who\n   enunciates distinctly. (b) One who prepares and mounts skeletons. (c)\n   An instrument to cure stammering.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articuli', 'pl. ', 'of Articulus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Articulus', 'n.', 'A joint of the cirri of the Crinoidea; a joint or\n   segment of an arthropod appendage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artifice', 'n.', 'A handicraft; a trade; art of making.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artifice', 'n.', 'Workmanship; a skillfully contrived work.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artifice', 'n.', 'Artful or skillful contrivance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artifice', 'n.', 'Crafty device; an artful, ingenious, or elaborate trick.\n   [Now the usual meaning.]');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artificer', 'n.', 'An artistic worker; a mechanic or manufacturer; one\n   whose occupation requires skill or knowledge of a particular kind, as a\n   silversmith.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artificer', 'n.', 'One who makes or contrives; a deviser, inventor, or\n   framer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artificer', 'n.', 'A cunning or artful fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artificer', 'n.', 'A military mechanic, as a blacksmith, carpenter, etc.;\n   also, one who prepares the shells, fuses, grenades, etc., in a military\n   laboratory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artificial', 'a.', 'Made or contrived by art; produced or modified by human\n   skill and labor, in opposition to natural; as, artificial heat or\n   light, gems, salts, minerals, fountains, flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artificial', 'a.', 'Feigned; fictitious; assumed; affected; not genuine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artificial', 'a.', 'Artful; cunning; crafty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artificial', 'a.', 'Cultivated; not indigenous; not of spontaneous growth;\n   as, artificial grasses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artificiality', 'n.', 'The quality or appearance of being artificial; that\n   which is artificial.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artificialize', 'v. t.', 'To render artificial.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artificially', 'adv.', 'In an artificial manner; by art, or skill and\n   contrivance, not by nature.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artificially', 'adv.', 'Ingeniously; skillfully.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artificially', 'adv.', 'Craftily; artfully.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artificialness', 'n.', 'The quality of being artificial.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artificious', 'a.', 'Artificial.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artilize', 'v. t.', 'To make resemble.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artillerist', 'n.', 'A person skilled in artillery or gunnery; a gunner; an\n   artilleryman.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artillery', 'n.', 'Munitions of war; implements for warfare, as slings,\n   bows, and arrows.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artillery', 'n.', 'Cannon; great guns; ordnance, including guns, mortars,\n   howitzers, etc., with their equipment of carriages, balls, bombs, and\n   shot of all kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artillery', 'n.', 'The men and officers of that branch of the army to which\n   the care and management of artillery are confided.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artillery', 'n.', 'The science of artillery or gunnery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artilleryman', 'n.', 'A man who manages, or assists in managing, a large\n   gun in firing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artiodactyla', 'n. pl.', 'One of the divisions of the ungulate animals. The\n   functional toes of the hind foot are even in number, and the third\n   digit of each foot (corresponding to the middle finger in man) is\n   asymmetrical and paired with the fourth digit, as in the hog, the\n   sheep, and the ox; -- opposed to Perissodactyla.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artiodactyle', 'n.', 'One of the Artiodactyla.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artiodactylous', 'a.', 'Even-toed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artisan', 'n.', 'One who professes and practices some liberal art; an\n   artist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artisan', 'n.', 'One trained to manual dexterity in some mechanic art or\n   trade; and handicraftsman; a mechanic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artist', 'n.', 'One who practices some mechanic art or craft; an artisan.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artist', 'n.', 'One who professes and practices an art in which science and\n   taste preside over the manual execution.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artist', 'n.', 'One who shows trained skill or rare taste in any manual art\n   or occupation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artist', 'n.', 'An artful person; a schemer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artiste', 'n.', 'One peculiarly dexterous and tasteful in almost any\n   employment, as an opera dancer, a hairdresser, a cook.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artistic', 'a.', 'Alt. of Artistical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artistical', 'a.', 'Of or pertaining to art or to artists; made in the\n   manner of an artist; conformable to art; characterized by art; showing\n   taste or skill.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artistry', 'n.', 'Works of art collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artistry', 'n.', 'Artistic effect or quality.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artistry', 'n.', 'Artistic pursuits; artistic ability.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artless', 'a.', 'Wanting art, knowledge, or skill; ignorant; unskillful.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artless', 'a.', 'Contrived without skill or art; inartistic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artless', 'a.', 'Free from guile, art, craft, or stratagem; characterized\n   by simplicity and sincerity; sincere; guileless; ingenuous; honest; as,\n   an artless mind; an artless tale.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artlessly', 'adv.', 'In an artless manner; without art, skill, or guile;\n   unaffectedly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artlessness', 'n.', 'The quality of being artless, or void of art or guile;\n   simplicity; sincerity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artly', 'adv.', 'With art or skill.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artocarpeous', 'a.', 'Alt. of Artocarpous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artocarpous', 'a.', 'Of or pertaining to the breadfruit, or to the genus\n   Artocarpus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artotype', 'n.', 'A kind of autotype.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artotyrite', 'n.', 'One of a sect in the primitive church, who celebrated\n   the Lords Supper with bread and cheese, alleging that the first\n   oblations of men not only of the fruit of the earth, but of their\n   flocks. [Gen. iv. 3, 4.]');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artow', '', 'A contraction of art thou.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Artsman', 'n.', 'A man skilled in an art or in arts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Art union', '', 'An association for promoting art (esp. the arts of\n   design); and giving encouragement to artists.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arum', 'n.', 'A genus of plants found in central Europe and about the\n   Mediterranean, having flowers on a spadix inclosed in a spathe. The\n   cuckoopint of the English is an example.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arundelian', 'a.', 'Pertaining to an Earl of Arundel; as, Arundel or\n   Arundelian marbles, marbles from ancient Greece, bought by the Earl of\n   Arundel in 1624.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arundiferous', 'a.', 'Producing reeds or canes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arundinaceous', 'a.', 'Of or pertaining to a reed; resembling the reed or\n   cane.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arundineous', 'a.', 'Abounding with reeds; reedy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aruspices', 'pl. ', 'of Aruspex');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aruspex', 'n.', 'One of the class of diviners among the Etruscans and\n   Romans, who foretold events by the inspection of the entrails of\n   victims offered on the altars of the gods.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aruspice', 'n.', 'A soothsayer of ancient Rome. Same as Aruspex.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aruspicy', 'n.', 'Prognostication by inspection of the entrails of victims\n   slain sacrifice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arval', 'n.', 'A funeral feast.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arvicole', 'n.', 'A mouse of the genus Arvicola; the meadow mouse. There\n   are many species.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aryan', 'n.', 'One of a primitive people supposed to have lived in\n   prehistoric times, in Central Asia, east of the Caspian Sea, and north\n   of the Hindoo Koosh and Paropamisan Mountains, and to have been the\n   stock from which sprang the Hindoo, Persian, Greek, Latin, Celtic,\n   Teutonic, Slavonic, and other races; one of that ethnological division\n   of mankind called also Indo-European or Indo-Germanic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aryan', 'n.', 'The language of the original Aryans.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aryan', 'a.', 'Of or pertaining to the people called Aryans; Indo-European;\n   Indo-Germanic; as, the Aryan stock, the Aryan languages.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aryanize', 'v. t.', 'To make Aryan (a language, or in language).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Arytenoid', 'a.', 'Ladle-shaped; -- applied to two small cartilages of the\n   larynx, and also to the glands, muscles, etc., connected with them. The\n   cartilages are attached to the cricoid cartilage and connected with the\n   vocal cords.');
  INSERT INTO words (word, word_type, description) VALUES  (  'As', 'adv. & conj.', 'Denoting equality or likeness in kind, degree, or\n   manner; like; similar to; in the same manner with or in which; in\n   accordance with; in proportion to; to the extent or degree in which or\n   to which; equally; no less than; as, ye shall be as gods, knowing good\n   and evil; you will reap as you sow; do as you are bidden.');
  INSERT INTO words (word, word_type, description) VALUES  (  'As', 'adv. & conj.', 'In the idea, character, or condition of, -- limiting\n   the view to certain attributes or relations; as, virtue considered as\n   virtue; this actor will appear as Hamlet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'As', 'adv. & conj.', 'While; during or at the same time that; when; as, he\n   trembled as he spoke.');
  INSERT INTO words (word, word_type, description) VALUES  (  'As', 'adv. & conj.', 'Because; since; it being the case that.');
  INSERT INTO words (word, word_type, description) VALUES  (  'As', 'adv. & conj.', 'Expressing concession. (Often approaching though in\n   meaning).');
  INSERT INTO words (word, word_type, description) VALUES  (  'As', 'adv. & conj.', 'That, introducing or expressing a result or\n   consequence, after the correlatives so and such.');
  INSERT INTO words (word, word_type, description) VALUES  (  'As', 'adv. & conj.', 'As if; as though.');
  INSERT INTO words (word, word_type, description) VALUES  (  'As', 'adv. & conj.', 'For instance; by way of example; thus; -- used to\n   introduce illustrative phrases, sentences, or citations.');
  INSERT INTO words (word, word_type, description) VALUES  (  'As', 'adv. & conj.', 'Than.');
  INSERT INTO words (word, word_type, description) VALUES  (  'As', 'adv. & conj.', 'Expressing a wish.');
  INSERT INTO words (word, word_type, description) VALUES  (  'As', 'n.', 'An ace.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asses', 'pl. ', 'of As');
  INSERT INTO words (word, word_type, description) VALUES  (  'As', 'n.', 'A Roman weight, answering to the libra or pound, equal to\n   nearly eleven ounces Troy weight. It was divided into twelve ounces.');
  INSERT INTO words (word, word_type, description) VALUES  (  'As', 'n.', 'A Roman copper coin, originally of a pound weight (12 oz.); but\n   reduced, after the first Punic war, to two ounces; in the second Punic\n   war, to one ounce; and afterwards to half an ounce.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asa', 'n.', 'An ancient name of a gum.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asafetida', 'n.', 'Alt. of Asafoetida');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asafoetida', 'n.', 'The fetid gum resin or inspissated juice of a large\n   umbelliferous plant (Ferula asafoetida) of Persia and the East Indies.\n   It is used in medicine as an antispasmodic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asaphus', 'n.', 'A genus of trilobites found in the Lower Silurian\n   formation. See Illust. in Append.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asarabacca', 'n.', 'An acrid herbaceous plant (Asarum Europaeum); the\n   leaves and roots of which are emetic and cathartic. It is principally\n   used in cephalic snuffs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asarone', 'n.', 'A crystallized substance, resembling camphor, obtained\n   from the Asarum Europaeum; -- called also camphor of asarum.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asbestic', 'a.', 'Of, pertaining to, or resembling asbestus; inconsumable;\n   asbestine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asbestiform', 'a.', 'Having the form or structure of asbestus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asbestine', 'a.', 'Of or pertaining to asbestus, or partaking of its\n   nature; incombustible; asbestic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asbestous', 'a.', 'Asbestic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asbestus', 'n.', 'Alt. of Asbestos');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asbestos', 'n.', 'A variety of amphibole or of pyroxene, occurring in long\n   and delicate fibers, or in fibrous masses or seams, usually of a white,\n   gray, or green-gray color. The name is also given to a similar variety\n   of serpentine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asbolin', 'n.', 'A peculiar acrid and bitter oil, obtained from wood soot.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascarides', 'pl. ', 'of Ascarid');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascarid', 'n.', 'A parasitic nematoid worm, espec. the roundworm, Ascaris\n   lumbricoides, often occurring in the human intestine, and allied\n   species found in domestic animals; also commonly applied to the pinworm\n   (Oxyuris); often troublesome to children and aged persons.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascended', 'imp. & p. p.', 'of Ascend');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascending', 'p. pr. & vb. n.', 'of Ascend');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascend', 'v. i.', 'To move upward; to mount; to go up; to rise; -- opposed\n   to descend.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascend', 'v. i.', 'To rise, in a figurative sense; to proceed from an\n   inferior to a superior degree, from mean to noble objects, from\n   particulars to generals, from modern to ancient times, from one note to\n   another more acute, etc.; as, our inquiries ascend to the remotest\n   antiquity; to ascend to our first progenitor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascend', 'v. t.', 'To go or move upward upon or along; to climb; to mount;\n   to go up the top of; as, to ascend a hill, a ladder, a tree, a river, a\n   throne.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascendable', 'a.', 'Capable of being ascended.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascendancy', 'n.', 'Alt. of Ascendance');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascendance', 'n.', 'Same as Ascendency.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascendant', 'n.', 'Ascent; height; elevation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascendant', 'n.', 'The horoscope, or that degree of the ecliptic which\n   rises above the horizon at the moment of ones birth; supposed to have\n   a commanding influence on a persons life and fortune.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascendant', 'n.', 'Superiority, or commanding influence; ascendency; as,\n   one man has the ascendant over another.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascendant', 'n.', 'An ancestor, or one who precedes in genealogy or degrees\n   of kindred; a relative in the ascending line; a progenitor; -- opposed\n   to descendant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascendant', 'a.', 'Alt. of Ascendent');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascendent', 'a.', 'Rising toward the zenith; above the horizon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascendent', 'a.', 'Rising; ascending.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascendent', 'a.', 'Superior; surpassing; ruling.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascendency', 'n.', 'Governing or controlling influence; domination; power.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascendible', 'a.', 'Capable of being ascended; climbable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascending', 'a.', 'Rising; moving upward; as, an ascending kite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascension', 'n.', 'The act of ascending; a rising; ascent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascension', 'n.', 'Specifically: The visible ascent of our Savior on the\n   fortieth day after his resurrection. (Acts i. 9.) Also, Ascension Day.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascension', 'n.', 'An ascending or arising, as in distillation; also that\n   which arises, as from distillation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascensional', 'a.', 'Relating to ascension; connected with ascent;\n   ascensive; tending upward; as, the ascensional power of a balloon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascensive', 'a.', 'Rising; tending to rise, or causing to rise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascensive', 'a.', 'Augmentative; intensive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascent', '', 'The act of rising; motion upward; rise; a mounting upward;\n   as, he made a tedious ascent; the ascent of vapors from the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascent', '', 'The way or means by which one ascends.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascent', '', 'An eminence, hill, or high place.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascent', '', 'The degree of elevation of an object, or the angle it makes\n   with a horizontal line; inclination; rising grade; as, a road has an\n   ascent of five degrees.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascertained', 'imp. & p. p.', 'of Ascertain');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascertaining', 'p. pr. & vb. n.', 'of Ascertain');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascertain', 'v. t.', 'To render (a person) certain; to cause to feel\n   certain; to make confident; to assure; to apprise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascertain', 'v. t.', 'To make (a thing) certain to the mind; to free from\n   obscurity, doubt, or change; to make sure of; to fix; to determine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascertain', 'v. t.', 'To find out or learn for a certainty, by trial,\n   examination, or experiment; to get to know; as, to ascertain the weight\n   of a commodity, or the purity of a metal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascertainable', 'a.', 'That may be ascertained.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascertainer', 'n.', 'One who ascertains.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascertainment', 'n.', 'The act of ascertaining; a reducing to certainty; a\n   finding out by investigation; discovery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascessancy', 'a.', 'Alt. of Ascessant');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascessant', 'a.', 'See Acescency, Acescent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascetic', 'a.', 'Extremely rigid in self-denial and devotions; austere;\n   severe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascetic', 'n.', 'In the early church, one who devoted himself to a solitary\n   and contemplative life, characterized by devotion, extreme self-denial,\n   and self-mortification; a hermit; a recluse; hence, one who practices\n   extreme rigor and self-denial in religious things.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asceticism', 'n.', 'The condition, practice, or mode of life, of ascetics.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascham', 'n.', 'A sort of cupboard, or case, to contain bows and other\n   implements of archery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asci', 'n. pl.', 'See Ascus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascian', 'n.', 'One of the Ascii.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascidian', 'n.', 'One of the Ascidioidea, or in a more general sense, one\n   of the Tunicata. Also as an adj.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascidiarium', 'n.', 'The structure which unites together the ascidiozooids\n   in a compound ascidian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascidiform', 'a.', 'Shaped like an ascidian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascidioidea', 'n. pl.', 'A group of Tunicata, often shaped like a\n   two-necked bottle. The group includes, social, and compound species.\n   The gill is a netlike structure within the oral aperture. The\n   integument is usually leathery in texture. See Illustration in\n   Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascidiozooid', 'n.', 'One of the individual members of a compound ascidian.\n   See Ascidioidea.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascidia', 'pl. ', 'of Ascidium');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascidium', 'n.', 'A pitcher-shaped, or flask-shaped, organ or appendage of\n   a plant, as the leaves of the pitcher plant, or the little bladderlike\n   traps of the bladderwort (Utricularia).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascidium', 'n.', 'A genus of simple ascidians, which formerly included most\n   of the known species. It is sometimes used as a name for the\n   Ascidioidea, or for all the Tunicata.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascigerous', 'a.', 'Having asci.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascii', 'n. pl.', 'Alt. of Ascians');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascians', 'n. pl.', 'Persons who, at certain times of the year, have no\n   shadow at noon; -- applied to the inhabitants of the torrid zone, who\n   have, twice a year, a vertical sun.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascites', 'n.', 'A collection of serous fluid in the cavity of the abdomen;\n   dropsy of the peritoneum.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascitic', 'a.', 'Alt. of Ascitical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascitical', 'a.', 'Of, pertaining to, or affected by, ascites; dropsical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascititious', 'a.', 'Supplemental; not inherent or original; adscititious;\n   additional; assumed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asclepiad', 'n.', 'A choriambic verse, first used by the Greek poet\n   Asclepias, consisting of four feet, viz., a spondee, two choriambi, and\n   an iambus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asclepiadaceous', 'a.', 'Of, pertaining to, or resembling, plants of the\n   Milkweed family.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asclepias', 'n.', 'A genus of plants including the milkweed, swallowwort,\n   and some other species having medicinal properties.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascococci', 'pl. ', 'of Ascococcus');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascococcus', 'n.', 'A form of micrococcus, found in putrid meat infusions,\n   occurring in peculiar masses, each of which is inclosed in a hyaline\n   capsule and contains a large number of spherical micrococci.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascospore', 'n.', 'One of the spores contained in the asci of lichens and\n   fungi. [See Illust. of Ascus.]');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascribable', 'a.', 'Capable of being ascribed; attributable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascribed', 'imp. & p. p.', 'of Ascribe');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascribing', 'p. pr. & vb. n.', 'of Ascribe');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascribe', 'v. t.', 'To attribute, impute, or refer, as to a cause; as, his\n   death was ascribed to a poison; to ascribe an effect to the right\n   cause; to ascribe such a book to such an author.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascribe', 'v. t.', 'To attribute, as a quality, or an appurtenance; to\n   consider or allege to belong.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascript', 'a.', 'See Adscript.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascription', 'n.', 'The act of ascribing, imputing, or affirming to belong;\n   also, that which is ascribed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascriptitious', 'a.', 'Ascribed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascriptitious', 'a.', 'Added; additional.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ascus', 'n.', 'A small membranous bladder or tube in which are inclosed the\n   seedlike reproductive particles or sporules of lichens and certain\n   fungi.');
  INSERT INTO words (word, word_type, description) VALUES  (  'A-sea', 'adv.', 'On the sea; at sea; toward the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aseptic', 'a.', 'Not liable to putrefaction; nonputrescent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aseptic', 'n.', 'An aseptic substance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asexual', 'a.', 'Having no distinct sex; without sexual action; as, asexual\n   reproduction. See Fission and Gemmation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asexually', 'adv.', 'In an asexual manner; without sexual agency.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ash', 'n.', 'A genus of trees of the Olive family, having opposite pinnate\n   leaves, many of the species furnishing valuable timber, as the European\n   ash (Fraxinus excelsior) and the white ash (F. Americana).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ash', 'n.', 'The tough, elastic wood of the ash tree.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ash', 'n.', 'sing. of Ashes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ash', 'v. t.', 'To strew or sprinkle with ashes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashame', 'v. t.', 'To shame.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashamed', 'a.', 'Affected by shame; abashed or confused by guilt, or a\n   conviction or consciousness of some wrong action or impropriety.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashamedly', 'adv.', 'Bashfully.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashantees', 'pl. ', 'of Ashantee');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashantee', 'n.', 'A native or an inhabitant of Ashantee in Western Africa.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashantee', 'a.', 'Of or pertaining to Ashantee.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ash-colored', 'a.', 'Of the color of ashes; a whitish gray or brownish\n   gray.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashen', 'a.', 'Of or pertaining to the ash tree.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashen', 'a.', 'Consisting of, or resembling, ashes; of a color between\n   brown and gray, or white and gray.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashen', 'n.', 'obs. pl. for Ashes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashery', 'n.', 'A depository for ashes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashery', 'n.', 'A place where potash is made.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashes', 'n. pl.', 'The earthy or mineral particles of combustible\n   substances remaining after combustion, as of wood or coal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashes', 'n. pl.', 'Specifically: The remains of the human body when burnt,\n   or when \"returned to dust\" by natural decay.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashes', 'n. pl.', 'The color of ashes; deathlike paleness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ash-fire', 'n.', 'A low fire used in chemical operations.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ash-furnace', 'n.', 'Alt. of Ash-oven');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ash-oven', 'n.', 'A furnace or oven for fritting materials for glass\n   making.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashine', 'a.', 'Shining; radiant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashlar', 'n.', 'Alt. of Ashler');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashler', 'n.', 'Hewn or squared stone; also, masonry made of squared or\n   hewn stone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashler', 'n.', 'In the United States especially, a thin facing of squared\n   and dressed stone upon a wall of rubble or brick.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashlaring', 'n.', 'Alt. of Ashlering');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashlering', 'n.', 'The act of bedding ashlar in mortar.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashlering', 'n.', 'Ashlar when in thin slabs and made to serve merely as a\n   case to the body of the wall.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashlering', 'n.', 'The short upright pieces between the floor beams and\n   rafters in garrets. See Ashlar, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashore', 'adv.', 'On shore or on land; on the land adjacent to water; to\n   the shore; to the land; aground (when applied to a ship); -- sometimes\n   opposed to aboard or afloat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashtaroth', 'pl. ', 'of Ashtoreth');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashtoreth', 'n.', 'The principal female divinity of the Phoenicians, as\n   Baal was the principal male divinity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ash Wednesday', '', 'The first day of Lent; -- so called from a custom in\n   the Roman Catholic church of putting ashes, on that day, upon the\n   foreheads of penitents.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashweed', 'n.', 'Goutweed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashy', 'a.', 'Pertaining to, or composed of, ashes; filled, or strewed\n   with, ashes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ashy', 'a.', 'Ash-colored; whitish gray; deadly pale.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asian', 'a.', 'Of or pertaining to Asia; Asiatic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asian', 'n.', 'An Asiatic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asiarch', 'n.', 'One of the chiefs or pontiffs of the Roman province of\n   Asia, who had the superintendence of the public games and religious\n   rites.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asiatic', 'a.', 'Of or pertaining to Asia or to its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asiatic', 'n.', 'A native, or one of the people, of Asia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asiaticism', 'n.', 'Something peculiar to Asia or the Asiatics.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aside', 'adv.', 'On, or to, one side; out of a straight line, course, or\n   direction; at a little distance from the rest; out of the way; apart.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aside', 'adv.', 'Out of ones thoughts; off; away; as, to put aside gloomy\n   thoughts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aside', 'adv.', 'So as to be heard by others; privately.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aside', 'n.', 'Something spoken aside; as, a remark made by a stageplayer\n   which the other players are not supposed to hear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asilus', 'n.', 'A genus of large and voracious two-winged flies, including\n   the bee killer and robber fly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asinego', 'n.', 'Alt. of Assinego');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assinego', 'n.', 'A stupid fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asinine', 'a.', 'Of or belonging to, or having the qualities of, the ass,\n   as stupidity and obstinacy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asininity', 'n.', 'The quality of being asinine; stupidity combined with\n   obstinacy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asiphonate', 'a.', 'Destitute of a siphon or breathing tube; -- said of\n   many bivalve shells.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asiphonate', 'n.', 'An asiphonate mollusk.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asiphonea', 'n. pl.', 'Alt. of Asiphonida');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asiphonata', 'n. pl.', 'Alt. of Asiphonida');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asiphonida', 'n. pl.', 'A group of bivalve mollusks destitute of siphons,\n   as the oyster; the asiphonate mollusks.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asitia', 'n.', 'Want of appetite; loathing of food.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asked', 'imp. & p. p.', 'of Ask');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asking', 'p. pr. & vb. n.', 'of Ask');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ask', 'v. t.', 'To request; to seek to obtain by words; to petition; to\n   solicit; -- often with of, in the sense of from, before the person\n   addressed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ask', 'v. t.', 'To require, demand, claim, or expect, whether by way of\n   remuneration or return, or as a matter of necessity; as, what price do\n   you ask?');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ask', 'v. t.', 'To interrogate or inquire of or concerning; to put a\n   question to or about; to question.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ask', 'v. t.', 'To invite; as, to ask one to an entertainment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ask', 'v. t.', 'To publish in church for marriage; -- said of both the\n   banns and the persons.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ask', 'v. i.', 'To request or petition; -- usually followed by for; as, to\n   ask for bread.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ask', 'v. i.', 'To make inquiry, or seek by request; -- sometimes followed\n   by after.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ask', 'n.', 'A water newt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Askance', 'adv.', 'Alt. of Askant');
  INSERT INTO words (word, word_type, description) VALUES  (  'Askant', 'adv.', 'Sideways; obliquely; with a side glance; with disdain,\n   envy, or suspicion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Askance', 'v. t.', 'To turn aside.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asker', 'n.', 'One who asks; a petitioner; an inquirer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asker', 'n.', 'An ask; a water newt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Askew', 'adv. & a.', 'Awry; askance; asquint; oblique or obliquely; --\n   sometimes indicating scorn, or contempt, or entry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asking', 'n.', 'The act of inquiring or requesting; a petition;\n   solicitation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asking', 'n.', 'The publishing of banns.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aslake', 'v. t. & i.', 'To mitigate; to moderate; to appease; to abate; to\n   diminish.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aslant', 'adv. & a.', 'Toward one side; in a slanting direction; obliquely.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aslant', 'prep.', 'In a slanting direction over; athwart.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asleep', 'a. & adv.', 'In a state of sleep; in sleep; dormant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asleep', 'a. & adv.', 'In the sleep of the grave; dead.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asleep', 'a. & adv.', 'Numbed, and, usually, tingling.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aslope', 'adv. & a.', 'Slopingly; aslant; declining from an upright\n   direction; sloping.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aslug', 'adv.', 'Sluggishly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asmear', 'a.', 'Smeared over.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asmonean', 'a.', 'Of or pertaining to the patriotic Jewish family to which\n   the Maccabees belonged; Maccabean; as, the Asmonean dynasty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asmonean', 'n.', 'One of the Asmonean family. The Asmoneans were leaders\n   and rulers of the Jews from 168 to 35 b. c.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asoak', 'a.', 'Soaking.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asomatous', 'a.', 'Without a material body; incorporeal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asonant', 'a.', 'Not sounding or sounded.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asp', 'n.', 'Same as Aspen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asp', 'n.', 'A small, hooded, poisonous serpent of Egypt and adjacent\n   countries, whose bite is often fatal. It is the Naja haje. The name is\n   also applied to other poisonous serpents, esp. to Vipera aspis of\n   southern Europe. See Haje.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspalathus', 'n.', 'A thorny shrub yielding a fragrant oil.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspalathus', 'n.', 'A genus of plants of the natural order Leguminosae. The\n   species are chiefly natives of the Cape of Good Hope.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asparagine', 'n.', 'A white, nitrogenous, crystallizable substance,\n   C4H8N2O3+H2O, found in many plants, and first obtained from asparagus.\n   It is believed to aid in the disposition of nitrogenous matter\n   throughout the plant; -- called also altheine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asparaginous', 'a.', 'Pertaining or allied to, or resembling, asparagus;\n   having shoots which are eaten like asparagus; as, asparaginous\n   vegetables.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asparagus', 'n.', 'A genus of perennial plants belonging to the natural\n   order Liliaceae, and having erect much branched stems, and very slender\n   branchlets which are sometimes mistaken for leaves. Asparagus racemosus\n   is a shrubby climbing plant with fragrant flowers. Specifically: The\n   Asparagus officinalis, a species cultivated in gardens.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asparagus', 'n.', 'The young and tender shoots of A. officinalis, which\n   form a valuable and well-known article of food.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspartic', 'a.', 'Pertaining to, or derived, asparagine; as, aspartic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspect', 'n.', 'The act of looking; vision; gaze; glance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspect', 'n.', 'Look, or particular appearance of the face; countenance;\n   mien; air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspect', 'n.', 'Appearance to the eye or the mind; look; view.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspect', 'n.', 'Position or situation with regard to seeing; that position\n   which enables one to look in a particular direction; position in\n   relation to the points of the compass; as, a house has a southern\n   aspect, that is, a position which faces the south.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspect', 'n.', 'Prospect; outlook.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspect', 'n.', 'The situation of planets or stars with respect to one\n   another, or the angle formed by the rays of light proceeding from them\n   and meeting at the eye; the joint look of planets or stars upon each\n   other or upon the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspect', 'n.', 'The influence of the stars for good or evil; as, an ill\n   aspect.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspect', 'n.', 'To behold; to look at.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspectable', 'a.', 'Capable of being; visible.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspectant', 'a.', 'Facing each other.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspected', 'a.', 'Having an aspect.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspection', 'n.', 'The act of viewing; a look.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspen', 'n.', 'Alt. of Asp');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asp', 'n.', 'One of several species of poplar bearing this name, especially\n   the Populus tremula, so called from the trembling of its leaves, which\n   move with the slightest impulse of the air.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspen', 'a.', 'Of or pertaining to the aspen, or resembling it; made of\n   aspen wood.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asper', 'a.', 'Rough; rugged; harsh; bitter; stern; fierce.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asper', 'n.', 'The rough breathing; a mark (/) placed over an initial vowel\n   sound or over / to show that it is aspirated, that is, pronounced with\n   h before it; thus \"ws, pronounced h/s, \"rh`twr, pronounced hra\"t/r.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asper', 'n.', 'A Turkish money of account (formerly a coin); of little\n   value; the 120th part of a piaster.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asperated', 'imp. & p. p.', 'of Asperate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asperating', 'p. pr. & vb. n.', 'of Asperate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asperate', 'v. t.', 'To make rough or uneven.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asperation', 'n.', 'The act of asperating; a making or becoming rough.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asperges', 'n.', 'The service or ceremony of sprinkling with holy water.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asperges', 'n.', 'The brush or instrument used in sprinkling holy water; an\n   aspergill.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspergill', 'n.', 'Alt. of Aspergillum');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspergillum', 'n.', 'The brush used in the Roman Catholic church for\n   sprinkling holy water on the people.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspergillum', 'n.', 'See Wateringpot shell.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspergilliform', 'a.', 'Resembling the aspergillum in form; as, an\n   aspergilliform stigma.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asperifoliate', 'a.', 'Alt. of Asperifolious');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asperifolious', 'a.', 'Having rough leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asperities', 'pl. ', 'of Asperity');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asperity', 'n.', 'Roughness of surface; unevenness; -- opposed to\n   smoothness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asperity', 'n.', 'Roughness or harshness of sound; that quality which\n   grates upon the ear; raucity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asperity', 'n.', 'Roughness to the taste; sourness; tartness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asperity', 'n.', 'Moral roughness; roughness of manner; severity;\n   crabbedness; harshness; -- opposed to mildness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asperity', 'n.', 'Sharpness; disagreeableness; difficulty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspermatous', 'a.', 'Aspermous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspermous', 'a.', 'Destitute of seeds; aspermatous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asperne', 'v. t.', 'To spurn; to despise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asperous', 'a.', 'Rough; uneven.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspersed', 'imp. & p. p.', 'of Asperse');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspersing', 'p. pr. & vb. n.', 'of Asperse');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asperse', 'v. t.', 'To sprinkle, as water or dust, upon anybody or\n   anything, or to besprinkle any one with a liquid or with dust.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asperse', 'v. t.', 'To bespatter with foul reports or false and injurious\n   charges; to tarnish in point of reputation or good name; to slander or\n   calumniate; as, to asperse a poet or his writings; to asperse a mans\n   character.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspersed', 'a.', 'Having an indefinite number of small charges scattered or\n   strewed over the surface.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspersed', 'a.', 'Bespattered; slandered; calumniated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asperser', 'n.', 'One who asperses; especially, one who vilifies another.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspersion', 'n.', 'A sprinkling, as with water or dust, in a literal sense.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspersion', 'n.', 'The spreading of calumniations reports or charges which\n   tarnish reputation, like the bespattering of a body with foul water;\n   calumny.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspersive', 'a.', 'Tending to asperse; defamatory; slanderous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspersoir', 'n.', 'An aspergill.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspersoria', 'pl. ', 'of Aspersorium');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspersorium', 'n.', 'The stoup, basin, or other vessel for holy water in\n   Roman Catholic churches.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspersorium', 'n.', 'A brush for sprinkling holy water; an aspergill.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asphalt', 'n.', 'Alt. of Asphaltum');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asphaltum', 'n.', 'Mineral pitch, Jews pitch, or compact native bitumen.\n   It is brittle, of a black or brown color and high luster on a surface\n   of fracture; it melts and burns when heated, leaving no residue. It\n   occurs on the surface and shores of the Dead Sea, which is therefore\n   called Asphaltites, or the Asphaltic Lake. It is found also in many\n   parts of Asia, Europe, and America. See Bitumen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asphaltum', 'n.', 'A composition of bitumen, pitch, lime, and gravel, used\n   for forming pavements, and as a water-proof cement for bridges, roofs,\n   etc.; asphaltic cement. Artificial asphalt is prepared from coal tar,\n   lime, sand, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asphalt', 'v. t.', 'To cover with asphalt; as, to asphalt a roof; asphalted\n   streets.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asphalte', 'n.', 'Asphaltic mastic or cement. See Asphalt, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asphaltic', 'a.', 'Pertaining to, of the nature of, or containing, asphalt;\n   bituminous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asphaltite', 'a.', 'Asphaltic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asphaltite', 'a.', 'Asphaltic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asphaltus', 'n.', 'See Asphalt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asphodel', 'n.', 'A general name for a plant of the genus Asphodelus. The\n   asphodels are hardy perennial plants, several species of which are\n   cultivated for the beauty of their flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asphyctic', 'a.', 'Pertaining to asphyxia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asphyxia', 'n.', 'Alt. of Asphyxy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asphyxy', 'n.', 'Apparent death, or suspended animation; the condition\n   which results from interruption of respiration, as in suffocation or\n   drowning, or the inhalation of irrespirable gases.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asphyxial', 'a.', 'Of or relating to asphyxia; as, asphyxial phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asphyxiate', 'v. t.', 'To bring to a state of asphyxia; to suffocate. [Used\n   commonly in the past pple.]');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asphyxiated', 'p. p. ', 'Alt. of Asphyxied');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asphyxied', 'p. p. ', 'In a state of asphyxia; suffocated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asphyxiation', 'n.', 'The act of causing asphyxia; a state of asphyxia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspic', 'n.', 'The venomous asp.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspic', 'n.', 'A piece of ordnance carrying a 12 pound shot.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspic', 'n.', 'A European species of lavender (Lavandula spica); which\n   produces a volatile oil. See Spike.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspic', 'n.', 'A savory meat jelly containing portions of fowl, game, fish,\n   hard boiled eggs, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspidobranchia', 'n. pl.', 'A group of Gastropoda, with limpetlike shells,\n   including the abalone shells and keyhole limpets.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspirant', 'a.', 'Aspiring.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspirant', 'n.', 'One who aspires; one who eagerly seeks some high position\n   or object of attainment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspirated', 'imp. & p. p.', 'of Aspirate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspirating', 'p. pr. & vb. n.', 'of Aspirate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspirate', 'v. t.', 'To pronounce with a breathing, an aspirate, or an h\n   sound; as, we aspirate the words horse and house; to aspirate a vowel\n   or a liquid consonant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspirate', 'n.', 'A sound consisting of, or characterized by, a breath like\n   the sound of h; the breathing h or a character representing such a\n   sound; an aspirated sound.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspirate', 'n.', 'A mark of aspiration (/) used in Greek; the asper, or\n   rough breathing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspirate', 'n.', 'An elementary sound produced by the breath alone; a surd,\n   or nonvocal consonant; as, f, th in thin, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspirate', 'a.', 'Alt. of Aspirated');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspirated', 'a.', 'Pronounced with the h sound or with audible breath.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspiration', 'n.', 'The act of aspirating; the pronunciation of a letter\n   with a full or strong emission of breath; an aspirated sound.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspiration', 'n.', 'The act of breathing; a breath; an inspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspiration', 'n.', 'The act of aspiring of a ardently desiring; strong\n   wish; high desire.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspirator', 'n.', 'An apparatus for passing air or gases through or over\n   certain liquids or solids, or for exhausting a closed vessel, by means\n   of suction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspirator', 'n.', 'An instrument for the evacuation of the fluid contents\n   of tumors or collections of blood.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspiratory', 'a.', 'Of or pertaining to breathing; suited to the inhaling\n   of air');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspired', 'imp. & p. p.', 'of Aspire');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspiring', 'p. pr. & vb. n.', 'of Aspire');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspire', 'v. t.', 'To desire with eagerness; to seek to attain something\n   high or great; to pant; to long; -- followed by to or after, and rarely\n   by at; as, to aspire to a crown; to aspire after immorality.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspire', 'v. t.', 'To rise; to ascend; to tower; to soar.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspire', 'v. t.', 'To aspire to; to long for; to try to reach; to mount to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspire', 'n.', 'Aspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspirement', 'n.', 'Aspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspirer', 'n.', 'One who aspires.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspiring', 'a.', 'That aspires; as, an Aspiring mind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aspish', 'a.', 'Pertaining to, or like, an asp.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asportation', 'n.', 'The felonious removal of goods from the place where\n   they were deposited.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asprawl', 'adv. & a.', 'Sprawling.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asquat', 'adv. & a.', 'Squatting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asquint', 'adv.', 'With the eye directed to one side; not in the straight\n   line of vision; obliquely; awry, so as to see distortedly; as, to look\n   asquint.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ass', 'n.', 'A quadruped of the genus Equus (E. asinus); smaller than the\n   horse, and having a peculiarly harsh bray and long ears. The tame or\n   domestic ass is patient, slow, and sure-footed, and has become the type\n   of obstinacy and stupidity. There are several species of wild asses\n   which are swift-footed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ass', 'n.', 'A dull, heavy, stupid fellow; a dolt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assaf/tida', 'n.', 'Same as Asafetida.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assagai', 'n.', 'Alt. of Assegai');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assegai', 'n.', 'A spear used by tribes in South Africa as a missile and\n   for stabbing, a kind of light javelin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assai', '', 'A direction equivalent to very; as, adagio assai, very slow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assailed', 'imp. & p. p.', 'of Assail');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assailing', 'p. pr. & vb. n.', 'of Assail');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assail', 'v. t.', 'To attack with violence, or in a vehement and hostile\n   manner; to assault; to molest; as, to assail a man with blows; to\n   assail a city with artillery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assail', 'v. t.', 'To encounter or meet purposely with the view of\n   mastering, as an obstacle, difficulty, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assail', 'v. t.', 'To attack morally, or with a view to produce changes in\n   the feelings, character, conduct, existing usages, institutions; to\n   attack by words, hostile influence, etc.; as, to assail one with\n   appeals, arguments, abuse, ridicule, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assailable', 'a.', 'Capable of being assailed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assailant', 'a.', 'Assailing; attacking.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assailant', 'n.', 'One who, or that which, assails, attacks, or assaults;\n   an assailer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assailer', 'n.', 'One who assails.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assailment', 'n.', 'The act or power of assailing; attack; assault.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assamar', 'n.', 'The peculiar bitter substance, soft or liquid, and of a\n   yellow color, produced when meat, bread, gum, sugar, starch, and the\n   like, are roasted till they turn brown.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assamese', 'a.', 'Of or pertaining to Assam, a province of British India,\n   or to its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assamese', 'n. sing. & pl.', 'A native or natives of Assam.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assapan', 'n.', 'Alt. of Assapanic');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assapanic', 'n.', 'The American flying squirrel (Pteromys volucella).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assart', 'n.', 'The act or offense of grubbing up trees and bushes, and\n   thus destroying the thickets or coverts of a forest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assart', 'n.', 'A piece of land cleared of trees and bushes, and fitted for\n   cultivation; a clearing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assart', 'v. t.', 'To grub up, as trees; to commit an assart upon; as, to\n   assart land or trees.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assassin', 'n.', 'One who kills, or attempts to kill, by surprise or secret\n   assault; one who treacherously murders any one unprepared for defense.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assassin', 'v. t.', 'To assassinate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assassinated', 'imp. & p. p.', 'of Assassinate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assassinating', 'p. pr. & vb. n.', 'of Assassinate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assassinate', 'v. t.', 'To kill by surprise or secret assault; to murder by\n   treacherous violence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assassinate', 'v. t.', 'To assail with murderous intent; hence, by extended\n   meaning, to maltreat exceedingly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assassinate', 'n.', 'An assassination, murder, or murderous assault.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assassinate', 'n.', 'An assassin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assassination', 'n.', 'The act of assassinating; a killing by treacherous\n   violence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assassinator', 'n.', 'An assassin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assassinous', 'a.', 'Murderous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assastion', 'n.', 'Roasting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assault', 'n.', 'A violent onset or attack with physical means, as blows,\n   weapons, etc.; an onslaught; the rush or charge of an attacking force;\n   onset; as, to make assault upon a man, a house, or a town.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assault', 'n.', 'A violent onset or attack with moral weapons, as words,\n   arguments, appeals, and the like; as, to make an assault on the\n   prerogatives of a prince, or on the constitution of a government.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assault', 'n.', 'An apparently violent attempt, or willful offer with force\n   or violence, to do hurt to another; an attempt or offer to beat\n   another, accompanied by a degree of violence, but without touching his\n   person, as by lifting the fist, or a cane, in a threatening manner, or\n   by striking at him, and missing him. If the blow aimed takes effect, it\n   is a battery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assaulted', 'imp. & p. p.', 'of Assault');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assaulting', 'p. pr. & vb. n.', 'of Assault');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assault', 'n.', 'To make an assault upon, as by a sudden rush of armed men;\n   to attack with unlawful or insulting physical violence or menaces.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assault', 'n.', 'To attack with moral means, or with a view of producing\n   moral effects; to attack by words, arguments, or unfriendly measures;\n   to assail; as, to assault a reputation or an administration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assaultable', 'a.', 'Capable of being assaulted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assaulter', 'n.', 'One who assaults, or violently attacks; an assailant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assay', 'n.', 'Trial; attempt; essay.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assay', 'n.', 'Examination and determination; test; as, an assay of bread\n   or wine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assay', 'n.', 'Trial by danger or by affliction; adventure; risk; hardship;\n   state of being tried.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assay', 'n.', 'Tested purity or value.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assay', 'n.', 'The act or process of ascertaining the proportion of a\n   particular metal in an ore or alloy; especially, the determination of\n   the proportion of gold or silver in bullion or coin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assay', 'n.', 'The alloy or metal to be assayed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assayed', 'imp. & p. p.', 'of Assay');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assaying', 'p. pr. & vb. n.', 'of Assay');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assay', 'v.', 'To try; to attempt; to apply.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assay', 'v.', 'To affect.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assay', 'v.', 'To try tasting, as food or drink.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assay', 'v.', 'To subject, as an ore, alloy, or other metallic compound, to\n   chemical or metallurgical examination, in order to determine the amount\n   of a particular metal contained in it, or to ascertain its composition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assay', 'v. i.', 'To attempt, try, or endeavor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assayable', 'a.', 'That may be assayed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assayer', 'n.', 'One who assays. Specifically: One who examines metallic\n   ores or compounds, for the purpose of determining the amount of any\n   particular metal in the same, especially of gold or silver.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assaying', 'n.', 'The act or process of testing, esp. of analyzing or\n   examining metals and ores, to determine the proportion of pure metal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asse', 'n.', 'A small foxlike animal (Vulpes cama) of South Africa, valued\n   for its fur.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assecuration', 'n.', 'Assurance; certainty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assecure', 'v. t.', 'To make sure or safe; to assure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assecution', 'n.', 'An obtaining or acquiring.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assegai', 'n.', 'Same as Assagai.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assemblage', 'n.', 'The act of assembling, or the state of being assembled;\n   association.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assemblage', 'n.', 'A collection of individuals, or of individuals, or of\n   particular things; as, a political assemblage; an assemblage of ideas.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assemblance', 'n.', 'Resemblance; likeness; appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assemblance', 'n.', 'An assembling; assemblage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assembled', 'imp. & p. p.', 'of Assemble');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assembling', 'p. pr. & vb. n.', 'of Assemble');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assemble', 'v. t.', 'To collect into one place or body; to bring or call\n   together; to convene; to congregate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assemble', 'v. i.', 'To meet or come together, as a number of individuals;\n   to convene; to congregate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assemble', 'v. i.', 'To liken; to compare.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assembler', 'n.', 'One who assembles a number of individuals; also, one of\n   a number assembled.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assemblies', 'pl. ', 'of Assembly');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assembly', 'n.', 'A company of persons collected together in one place, and\n   usually for some common purpose, esp. for deliberation and legislation,\n   for worship, or for social entertainment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assembly', 'n.', 'A collection of inanimate objects.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assembly', 'n.', 'A beat of the drum or sound of the bugle as a signal to\n   troops to assemble.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assemblymen', 'pl. ', 'of Assemblyman');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assemblyman', 'n.', 'A member of an assembly, especially of the lower\n   branch of a state legislature.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assented', 'imp. & p. p.', 'of Assent');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assenting', 'p. pr. & vb. n.', 'of Assent');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assent', 'v. t.', 'To admit a thing as true; to express ones agreement,\n   acquiescence, concurrence, or concession.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assent', 'v.', 'The act of assenting; the act of the mind in admitting or\n   agreeing to anything; concurrence with approval; consent; agreement;\n   acquiescence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assentation', 'n.', 'Insincere, flattering, or obsequious assent;\n   hypocritical or pretended concurrence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assentator', 'n.', 'An obsequious; a flatterer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assentatory', 'a.', 'Flattering; obsequious.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assenter', 'n.', 'One who assents.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assentient', 'a.', 'Assenting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assenting', 'a.', 'Giving or implying assent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assentive', 'a.', 'Giving assent; of the nature of assent; complying.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assentment', 'n.', 'Assent; agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asserted', 'imp. & p. p.', 'of Assert');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asserting', 'p. pr. & vb. n.', 'of Assert');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assert', 'v. t.', 'To affirm; to declare with assurance, or plainly and\n   strongly; to state positively; to aver; to asseverate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assert', 'v. t.', 'To maintain; to defend.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assert', 'v. t.', 'To maintain or defend, as a cause or a claim, by words\n   or measures; to vindicate a claim or title to; as, to assert our rights\n   and liberties.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asserter', 'n.', 'One who asserts; one who avers pr maintains; an assertor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assertion', 'n.', 'The act of asserting, or that which is asserted;\n   positive declaration or averment; affirmation; statement asserted;\n   position advanced.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assertion', 'n.', 'Maintenance; vindication; as, the assertion of ones\n   rights or prerogatives.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assertive', 'a.', 'Positive; affirming confidently; affirmative;\n   peremptory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assertor', 'n.', 'One who asserts or avers; one who maintains or vindicates\n   a claim or a right; an affirmer, supporter, or vindicator; a defender;\n   an asserter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assertorial', 'a.', 'Asserting that a thing is; -- opposed to problematical\n   and apodeictical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assertory', 'a.', 'Affirming; maintaining.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assessed', 'imp. & p. p.', 'of Assess');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assessing', 'p. pr. & vb. n.', 'of Assess');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assess', 'v.', 'To value; to make a valuation or official estimate of for\n   the purpose of taxation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assess', 'v.', 'To apportion a sum to be paid by (a person, a community, or\n   an estate); in the nature of a tax, fine, etc.; to impose a tax upon (a\n   person, an estate, or an income) according to a rate or apportionment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assess', 'v.', 'To determine and impose a tax or fine upon (a person,\n   community, estate, or income); to tax; as, the club assessed each\n   member twenty-five cents.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assess', 'v.', 'To fix or determine the rate or amount of.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assessable', 'a.', 'Liable to be assessed or taxed; as, assessable\n   property.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assessee', 'n.', 'One who is assessed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assession', 'n.', 'A sitting beside or near.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assessment', 'n.', 'The act of assessing; the act of determining an amount\n   to be paid; as, an assessment of damages, or of taxes; an assessment of\n   the members of a club.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assessment', 'n.', 'A valuation of property or profits of business, for the\n   purpose of taxation; such valuation and an adjudging of the proper sum\n   to be levied on the property; as, an assessment of property or an\n   assessment on property.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assessment', 'n.', 'The specific sum levied or assessed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assessment', 'n.', 'An apportionment of a subscription for stock into\n   successive installments; also, one of these installments (in England\n   termed a \"call\").');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assessor', 'v.', 'One appointed or elected to assist a judge or magistrate\n   with his special knowledge of the subject to be decided; as legal\n   assessors, nautical assessors.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assessor', 'v.', 'One who sits by another, as next in dignity, or as an\n   assistant and adviser; an associate in office.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assessor', 'v.', 'One appointed to assess persons or property for the\n   purpose of taxation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assessorial', 'a.', 'Of or pertaining to an assessor, or to a court of\n   assessors.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assessorship', 'n.', 'The office or function of an assessor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asset', 'n.', 'Any article or separable part of ones assets.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assets', 'n. pl.', 'Property of a deceased person, subject by law to the\n   payment of his debts and legacies; -- called assets because sufficient\n   to render the executor or administrator liable to the creditors and\n   legatees, so far as such goods or estate may extend.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assets', 'n. pl.', 'Effects of an insolvent debtor or bankrupt, applicable\n   to the payment of debts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assets', 'n. pl.', 'The entire property of all sorts, belonging to a\n   person, a corporation, or an estate; as, the assets of a merchant or a\n   trading association; -- opposed to liabilities.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assever', 'v. t.', 'See Asseverate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asseverated', 'imp. & p. p.', 'of Asseverate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asseverating', 'p. pr. & vb. n.', 'of Asseverate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asseverate', 'v. t.', 'To affirm or aver positively, or with solemnity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asseveration', 'n.', 'The act of asseverating, or that which is\n   asseverated; positive affirmation or assertion; solemn declaration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asseverative', 'a.', 'Characterized by asseveration; asserting positively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asseveratory', 'a.', 'Asseverative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assibilate', 'v. t.', 'To make sibilant; to change to a sibilant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assibilation', 'n.', 'Change of a non-sibilant letter to a sibilant, as of\n   -tion to -shun, duke to ditch.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assidean', 'n.', 'One of a body of devoted Jews who opposed the Hellenistic\n   Jews, and supported the Asmoneans.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assident', 'a.', 'Usually attending a disease, but not always; as, assident\n   signs, or symptoms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assiduate', 'a.', 'Unremitting; assiduous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assiduities', 'pl. ', 'of Assiduity');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assiduity', 'n.', 'Constant or close application or attention, particularly\n   to some business or enterprise; diligence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assiduity', 'n.', 'Studied and persevering attention to a person; --\n   usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assiduous', 'a.', 'Constant in application or attention; devoted;\n   attentive; unremitting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assiduous', 'a.', 'Performed with constant diligence or attention;\n   unremitting; persistent; as, assiduous labor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assiege', 'v. t.', 'To besiege.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assiege', 'n.', 'A siege.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assientist', 'n.', 'A shareholder of the Assiento company; one of the\n   parties to the Assiento contract.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assiento', 'n.', 'A contract or convention between Spain and other powers\n   for furnishing negro slaves for the Spanish dominions in America, esp.\n   the contract made with Great Britain in 1713.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assigned', 'imp. & p. p.', 'of Assign');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assigning', 'p. pr. & vb. n.', 'of Assign');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assign', 'v. t.', 'To appoint; to allot; to apportion; to make over.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assign', 'v. t.', 'To fix, specify, select, or designate; to point out\n   authoritatively or exactly; as, to assign a limit; to assign counsel\n   for a prisoner; to assign a day for trial.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assign', 'v. t.', 'To transfer, or make over to another, esp. to transfer\n   to, and vest in, certain persons, called assignees, for the benefit of\n   creditors.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assign', 'v.', 'A thing pertaining or belonging to something else; an\n   appurtenance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assign', 'n.', 'A person to whom property or an interest is transferred;\n   as, a deed to a man and his heirs and assigns.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assignability', 'n.', 'The quality of being assignable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assignable', 'a.', 'Capable of being assigned, allotted, specified, or\n   designated; as, an assignable note or bill; an assignable reason; an\n   assignable quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assignat', 'n.', 'One of the notes, bills, or bonds, issued as currency by\n   the revolutionary government of France (1790-1796); and based on the\n   security of the lands of the church and of nobles which had been\n   appropriated by the state.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assignation', 'n.', 'The act of assigning or allotting; apportionment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assignation', 'n.', 'An appointment of time and place for meeting or\n   interview; -- used chiefly of love interviews, and now commonly in a\n   bad sense.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assignation', 'n.', 'A making over by transfer of title; assignment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assignee', 'v.', 'A person to whom an assignment is made; a person\n   appointed or deputed by another to do some act, perform some business,\n   or enjoy some right, privilege, or property; as, an assignee of a\n   bankrupt. See Assignment (c). An assignee may be by special appointment\n   or deed, or be created by jaw; as an executor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assignee', 'v.', 'In England, the persons appointed, under a commission of\n   bankruptcy, to manage the estate of a bankrupt for the benefit of his\n   creditors.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assigner', 'n.', 'One who assigns, appoints, allots, or apportions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assignment', 'n.', 'An allotting or an appointment to a particular person\n   or use; or for a particular time, as of a cause or causes in court.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assignment', 'n.', 'A transfer of title or interest by writing, as of\n   lease, bond, note, or bill of exchange; a transfer of the whole of some\n   particular estate or interest in lands.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assignment', 'n.', 'The writing by which an interest is transferred.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assignment', 'n.', 'The transfer of the property of a bankrupt to certain\n   persons called assignees, in whom it is vested for the benefit of\n   creditors.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assignor', 'n.', 'An assigner; a person who assigns or transfers an\n   interest; as, the assignor of a debt or other chose in action.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assimilability', 'n.', 'The quality of being assimilable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assimilable', 'a.', 'That may be assimilated; that may be likened, or\n   appropriated and incorporated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assimilated', 'imp. & p. p.', 'of Assimilate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assimilating', 'p. pr. & vb. n.', 'of Assimilate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assimilate', 'v. t.', 'To bring to a likeness or to conformity; to cause a\n   resemblance between.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assimilate', 'v. t.', 'To liken; to compa/e.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assimilate', 'v. t.', 'To appropriate and transform or incorporate into the\n   substance of the assimilating body; to absorb or appropriate, as\n   nourishment; as, food is assimilated and converted into organic tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assimilate', 'v. i.', 'To become similar or like something else.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assimilate', 'v. i.', 'To change and appropriate nourishment so as to make\n   it a part of the substance of the assimilating body.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assimilate', 'v. i.', 'To be converted into the substance of the\n   assimilating body; to become incorporated; as, some kinds of food\n   assimilate more readily than others.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assimilation', 'n.', 'The act or process of assimilating or bringing to a\n   resemblance, likeness, or identity; also, the state of being so\n   assimilated; as, the assimilation of one sound to another.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assimilation', 'n.', 'The conversion of nutriment into the fluid or solid\n   substance of the body, by the processes of digestion and absorption,\n   whether in plants or animals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assimilative', 'a.', 'Tending to, or characterized by, assimilation; that\n   assimilates or causes assimilation; as, an assimilative process or\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assimilatory', 'a.', 'Tending to assimilate, or produce assimilation; as,\n   assimilatory organs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assimulate', 'v. t.', 'To feign; to counterfeit; to simulate; to resemble.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assimulate', 'v. t.', 'To assimilate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assimulation', 'n.', 'Assimilation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assinego', 'n.', 'See Asinego.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assish', 'a.', 'Resembling an ass; asinine; stupid or obstinate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assisted', 'imp. & p. p.', 'of Assist');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assisting', 'p. pr. & vb. n.', 'of Assist');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assist', 'v. t.', 'To give support to in some undertaking or effort, or in\n   time of distress; to help; to aid; to succor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assist', 'v. i.', 'To lend aid; to help.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assist', 'v. i.', 'To be present as a spectator; as, to assist at a public\n   meeting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assistance', 'n.', 'The act of assisting; help; aid; furtherance; succor;\n   support.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assistance', 'n.', 'An assistant or helper; a body of helpers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assistance', 'n.', 'Persons present.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assistant', 'a.', 'Helping; lending aid or support; auxiliary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assistant', 'a.', 'Of the second grade in the staff of the army; as, an\n   assistant surgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assistant', 'n.', 'One who, or that which, assists; a helper; an auxiliary;\n   a means of help.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assistant', 'n.', 'An attendant; one who is present.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assistantly', 'adv.', 'In a manner to give aid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assister', 'n.', 'An assistant; a helper.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assistful', 'a.', 'Helpful.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assistive', 'a.', 'Lending aid, helping.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assistless', 'a.', 'Without aid or help.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assistor', 'n.', 'A assister.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assithment', 'n.', 'See Assythment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assize', 'n.', 'An assembly of knights and other substantial men, with a\n   bailiff or justice, in a certain place and at a certain time, for\n   public business.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assize', 'n.', 'A special kind of jury or inquest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assize', 'n.', 'A kind of writ or real action.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assize', 'n.', 'A verdict or finding of a jury upon such writ.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assize', 'n.', 'A statute or ordinance in general. Specifically: (1) A\n   statute regulating the weight, measure, and proportions of ingredients\n   and the price of articles sold in the market; as, the assize of bread\n   and other provisions; (2) A statute fixing the standard of weights and\n   measures.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assize', 'n.', 'Anything fixed or reduced to a certainty in point of time,\n   number, quantity, quality, weight, measure, etc.; as, rent of assize.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assize', 'n.', 'A court, the sitting or session of a court, for the trial\n   of processes, whether civil or criminal, by a judge and jury.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assize', 'n.', 'The periodical sessions of the judges of the superior\n   courts in every county of England for the purpose of administering\n   justice in the trial and determination of civil and criminal cases; --\n   usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assize', 'n.', 'The time or place of holding the court of assize; --\n   generally in the plural, assizes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assize', 'n.', 'Measure; dimension; size.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assized', 'imp. & p. p.', 'of Assize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assizing', 'p. pr. & vb. n.', 'of Assize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assize', 'v.', 'To assess; to value; to rate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assize', 'v.', 'To fix the weight, measure, or price of, by an ordinance or\n   regulation of authority.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assizer', 'n.', 'An officer who has the care or inspection of weights and\n   measures, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assizor', 'n.', 'A juror.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assober', 'v. t.', 'To make or keep sober.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associability', 'n.', 'The quality of being associable, or capable of\n   association; associableness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associable', 'a.', 'Capable of being associated or joined.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associable', 'a.', 'Sociable; companionable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associable', 'a.', 'Liable to be affected by sympathy with other parts; --\n   said of organs, nerves, muscles, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associableness', 'n.', 'Associability.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associated', 'imp. & p. p.', 'of Associate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associating', 'p. pr. & vb. n.', 'of Associate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associate', 'v. t.', 'To join with one, as a friend, companion, partner, or\n   confederate; as, to associate others with us in business, or in an\n   enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associate', 'v. t.', 'To join or connect; to combine in acting; as,\n   particles of gold associated with other substances.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associate', 'v. t.', 'To connect or place together in thought.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associate', 'v. t.', 'To accompany; to keep company with.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associate', 'v. i.', 'To unite in company; to keep company, implying\n   intimacy; as, congenial minds are disposed to associate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associate', 'v. i.', 'To unite in action, or to be affected by the action\n   of a different part of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associate', 'a.', 'Closely connected or joined with some other, as in\n   interest, purpose, employment, or office; sharing responsibility or\n   authority; as, an associate judge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associate', 'a.', 'Admitted to some, but not to all, rights and privileges;\n   as, an associate member.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associate', 'a.', 'Connected by habit or sympathy; as, associate motions,\n   such as occur sympathetically, in consequence of preceding motions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associate', 'n.', 'A companion; one frequently in company with another,\n   implying intimacy or equality; a mate; a fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associate', 'n.', 'A partner in interest, as in business; or a confederate\n   in a league.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associate', 'n.', 'One connected with an association or institution without\n   the full rights or privileges of a regular member; as, an associate of\n   the Royal Academy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associate', 'n.', 'Anything closely or usually connected with another; an\n   concomitant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associated', 'a.', 'Joined as a companion; brought into association;\n   accompanying; combined.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associateship', 'n.', 'The state of an associate, as in Academy or an\n   office.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Association', 'n.', 'The act of associating, or state of being associated;\n   union; connection, whether of persons of things.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Association', 'n.', 'Mental connection, or that which is mentally linked or\n   associated with a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Association', 'n.', 'Union of persons in a company or society for some\n   particular purpose; as, the American Association for the Advancement of\n   Science; a benevolent association. Specifically, as among the\n   Congregationalists, a society, consisting of a number of ministers,\n   generally the pastors of neighboring churches, united for promoting the\n   interests of religion and the harmony of the churches.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associational', 'a.', 'Of or pertaining to association, or to an\n   association.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associational', 'a.', 'Pertaining to the theory held by the\n   associationists.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associationism', 'n.', 'The doctrine or theory held by associationists.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associationist', 'n.', 'One who explains the higher functions and relations\n   of the soul by the association of ideas; e. g., Hartley, J. C. Mill.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associative', 'a.', 'Having the quality of associating; tending or leading\n   to association; as, the associative faculty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Associator', 'n.', 'An associate; a confederate or partner in any scheme.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assoil', 'v. t.', 'To set free; to release.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assoil', 'v. t.', 'To solve; to clear up.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assoil', 'v. t.', 'To set free from guilt; to absolve.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assoil', 'v. t.', 'To expiate; to atone for.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assoil', 'v. t.', 'To remove; to put off.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assoil', 'v. t.', 'To soil; to stain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assoilment', 'n.', 'Act of assoiling, or state of being assoiled;\n   absolution; acquittal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assoilment', 'n.', 'A soiling; defilement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assoilzie', 'v. t.', 'Alt. of Assoilyie');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assoilyie', 'v. t.', 'To absolve; to acquit by sentence of court.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assonance', 'n.', 'Resemblance of sound.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assonance', 'n.', 'A peculiar species of rhyme, in which the last acce`ted\n   vow`l and tnose whioh follow it in one word correspond in sound with\n   the vowels of another word, while the consonants of the two words are\n   unlike in sound; as, calamo and platano, baby and chary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assonance', 'n.', 'Incomplete correspondence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assonant', 'a.', 'Having a resemblance of sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assonant', 'a.', 'Pertaining to the peculiar species of rhyme called\n   assonance; not consonant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assonantal', 'a.', 'Assonant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assonate', 'v. i.', 'To correspond in sound.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assorted', 'imp. & p. p.', 'of Assort');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assorting', 'p. pr. & vb. n.', 'of Assort');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assort', 'v. t.', 'To separate and distribute into classes, as things of a\n   like kind, nature, or quality, or which are suited to a like purpose;\n   to classify; as, to assort goods. [Rarely applied to persons.]');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assort', 'v. t.', 'To furnish with, or make up of, various sorts or a\n   variety of goods; as, to assort a cargo.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assort', 'v. i.', 'To agree; to be in accordance; to be adapted; to suit;\n   to fall into a class or place.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assorted', 'a.', 'Selected; culled.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assortment', 'n.', 'Act of assorting, or distributing into sorts, kinds, or\n   classes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assortment', 'n.', 'A collection or quantity of things distributed into\n   kinds or sorts; a number of things assorted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assortment', 'n.', 'A collection containing a variety of sorts or kinds\n   adapted to various wants, demands, or purposes; as, an assortment of\n   goods.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assot', 'v. t.', 'To besot; to befool; to beguile; to infatuate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assot', 'a.', 'Dazed; foolish; infatuated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assuaged', 'imp. & p. p.', 'of Assuage');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assuaging', 'p. pr. & vb. n.', 'of Assuage');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assuage', 'v. t.', 'To soften, in a figurative sense; to allay, mitigate,\n   ease, or lessen, as heat, pain, or grief; to appease or pacify, as\n   passion or tumult; to satisfy, as appetite or desire.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assuage', 'v. i.', 'To abate or subside.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assuagement', 'n.', 'Mitigation; abatement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assuager', 'n.', 'One who, or that which, assuages.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assuasive', 'a.', 'Mitigating; tranquilizing; soothing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assubjugate', 'v. t.', 'To bring into subjection.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assuefaction', 'n.', 'The act of accustoming, or the state of being\n   accustomed; habituation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assuetude', 'n.', 'Accustomedness; habit; habitual use.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assumable', 'a.', 'That may be assumed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assumably', 'adv.', 'By way of assumption.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assumed', 'imp. & p. p.', 'of Assume');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assuming', 'p. pr. & vb. n.', 'of Assume');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assume', 'v. t.', 'To take to or upon ones self; to take formally and\n   demonstratively; sometimes, to appropriate or take unjustly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assume', 'v. t.', 'To take for granted, or without proof; to suppose as a\n   fact; to suppose or take arbitrarily or tentatively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assume', 'v. t.', 'To pretend to possess; to take in appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assume', 'v. t.', 'To receive or adopt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assume', 'v. i.', 'To be arrogant or pretentious; to claim more than is\n   due.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assume', 'v. i.', 'To undertake, as by a promise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assumed', 'a.', 'Supposed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assumed', 'a.', 'Pretended; hypocritical; make-believe; as, an assumed\n   character.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assumedly', 'adv.', 'By assumption.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assument', 'n.', 'A patch; an addition; a piece put on.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assumer', 'n.', 'One who assumes, arrogates, pretends, or supposes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assuming', 'a.', 'Pretentious; taking much upon ones self; presumptuous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assumpsit', 'n.', 'A promise or undertaking, founded on a consideration.\n   This promise may be oral or in writing not under seal. It may be\n   express or implied.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assumpsit', 'n.', 'An action to recover damages for a breach or\n   nonperformance of a contract or promise, express or implied, oral or in\n   writing not under seal. Common or indebitatus assumpsit is brought for\n   the most part on an implied promise. Special assumpsit is founded on an\n   express promise or undertaking.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assumpt', 'v. t.', 'To take up; to elevate; to assume.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assumpt', 'n.', 'That which is assumed; an assumption.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assumption', 'n.', 'The act of assuming, or taking to or upon ones self;\n   the act of taking up or adopting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assumption', 'n.', 'The act of taking for granted, or supposing a thing\n   without proof; supposition; unwarrantable claim.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assumption', 'n.', 'The thing supposed; a postulate, or proposition\n   assumed; a supposition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assumption', 'n.', 'The minor or second proposition in a categorical\n   syllogism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assumption', 'n.', 'The taking of a person up into heaven.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assumption', 'n.', 'A festival in honor of the ascent of the Virgin Mary\n   into heaven.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assumptive', 'a.', 'Assumed, or capable of being assumed; characterized by\n   assumption; making unwarranted claims.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assurance', 'n.', 'The act of assuring; a declaration tending to inspire\n   full confidence; that which is designed to give confidence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assurance', 'n.', 'The state of being assured; firm persuasion; full\n   confidence or trust; freedom from doubt; certainty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assurance', 'n.', 'Firmness of mind; undoubting, steadiness; intrepidity;\n   courage; confidence; self-reliance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assurance', 'n.', 'Excess of boldness; impudence; audacity; as, his\n   assurance is intolerable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assurance', 'n.', 'Betrothal; affiance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assurance', 'n.', 'Insurance; a contract for the payment of a sum on\n   occasion of a certain event, as loss or death.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assurance', 'n.', 'Any written or other legal evidence of the conveyance of\n   property; a conveyance; a deed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assured', 'imp. & p. p.', 'of Assure');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assuring', 'p. pr. & vb. n.', 'of Assure');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assure', 'v. t.', 'To make sure or certain; to render confident by a\n   promise, declaration, or other evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assure', 'v. t.', 'To declare to, solemnly; to assert to (any one) with the\n   design of inspiring belief or confidence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assure', 'v. t.', 'To confirm; to make certain or secure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assure', 'v. t.', 'To affiance; to betroth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assure', 'v. t.', 'To insure; to covenant to indemnify for loss, or to pay\n   a specified sum at death. See Insure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assured', 'a.', 'Made sure; safe; insured; certain; indubitable; not\n   doubting; bold to excess.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assured', 'n.', 'One whose life or property is insured.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assuredly', 'adv.', 'Certainly; indubitably.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assuredness', 'n.', 'The state of being assured; certainty; full\n   confidence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assurer', 'n.', 'One who assures. Specifically: One who insures against\n   loss; an insurer or underwriter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assurer', 'n.', 'One who takes out a life assurance policy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assurgency', 'n.', 'Act of rising.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assurgent', 'a.', 'Ascending');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assurgent', 'a.', 'rising obliquely; curving upward.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assuring', 'a.', 'That assures; tending to assure; giving confidence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asswage', 'v.', 'See Assuage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assyrian', 'a.', 'Of or pertaining to Assyria, or to its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assyrian', 'n.', 'A native or an inhabitant of Assyria; the language of\n   Assyria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assyriological', 'a.', 'Of or pertaining to Assyriology; as, Assyriological\n   studies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assyriologist', 'n.', 'One versed in Assyriology; a student of Assyrian\n   archaeology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assyriology', 'n.', 'The science or study of the antiquities, language,\n   etc., of ancient Assyria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Assythment', 'n.', 'Indemnification for injury; satisfaction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astacus', 'n.', 'A genus of crustaceans, containing the crawfish of\n   fresh-water lobster of Europe, and allied species of western North\n   America. See Crawfish.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astarboard', 'adv.', 'Over to the starboard side; -- said of the tiller.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astart', 'v. t. & i.', 'Same as Astert.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astarte', 'n.', 'A genus of bivalve mollusks, common on the coasts of\n   America and Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astate', 'n.', 'Estate; state.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astatic', 'a.', 'Having little or no tendency to take a fixed or definite\n   position or direction: thus, a suspended magnetic needle, when rendered\n   astatic, loses its polarity, or tendency to point in a given direction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astatically', 'adv.', 'In an astatic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astaticism', 'n.', 'The state of being astatic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astay', 'adv.', 'An anchor is said to be astay, when, in heaving it, an\n   acute angle is formed between the cable and the surface of the water.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asteism', 'n.', 'Genteel irony; a polite and ingenious manner of deriding\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astel', 'n.', 'An arch, or ceiling, of boards, placed over the mens heads\n   in a mine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aster', 'n.', 'A genus of herbs with compound white or bluish flowers;\n   starwort; Michaelmas daisy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aster', 'n.', 'A plant of the genus Callistephus. Many varieties (called\n   China asters, German asters, etc.) are cultivated for their handsome\n   compound flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asterias', 'n.', 'A genus of echinoderms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asteriated', 'a.', 'Radiated, with diverging rays; as, asteriated sapphire.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asteridian', 'a.', 'Of or pertaining to the Asterioidea.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asteridian', 'n.', 'A starfish; one of the Asterioidea.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asterioidea', 'n. pl.', 'Alt. of Asteridea');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asteridea', 'n. pl.', 'A class of Echinodermata including the true\n   starfishes. The rays vary in number and always have ambulacral grooves\n   below. The body is star-shaped or pentagonal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asterion', 'n.', 'The point on the side of the skull where the lambdoid,\n   parieto-mastoid and occipito-mastoid sutures.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asteriscus', 'n.', 'The smaller of the two otoliths found in the inner ear\n   of many fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asterisk', 'n.', 'The figure of a star, thus, /, used in printing and\n   writing as a reference to a passage or note in the margin, to supply\n   the omission of letters or words, or to mark a word or phrase as having\n   a special character.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asterism', 'n.', 'A constellation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asterism', 'n.', 'A small cluster of stars.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asterism', 'n.', 'An asterisk, or mark of reference.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asterism', 'n.', 'Three asterisks placed in this manner, /, to direct\n   attention to a particular passage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asterism', 'n.', 'An optical property of some crystals which exhibit a\n   star-shaped by reflected light, as star sapphire, or by transmitted\n   light, as some mica.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astern', 'adv.', 'In or at the hinder part of a ship; toward the hinder\n   part, or stern; backward; as, to go astern.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astern', 'adv.', 'Behind a ship; in the rear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asternal', 'a.', 'Not sternal; -- said of ribs which do not join the\n   sternum.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asteroid', 'n.', 'A starlike body; esp. one of the numerous small planets\n   whose orbits lie between those of Mars and Jupiter; -- called also\n   planetoids and minor planets.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asteroidal', 'a.', 'Of or pertaining to an asteroid, or to the asteroids.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asterolepis', 'n.', 'A genus of fishes, some of which were eighteen or\n   twenty feet long, found in a fossil state in the Old Red Sandstone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asterophyllite', 'n.', 'A fossil plant from the coal formations of Europe\n   and America, now regarded as the branchlets and foliage of calamites.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astert', 'v. t.', 'To start up; to befall; to escape; to shun.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astert', 'v. i.', 'To escape.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asthenia', 'n.', 'Alt. of Astheny');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astheny', 'n.', 'Want or loss of strength; debility; diminution of the\n   vital forces.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asthenic', 'a.', 'Characterized by, or pertaining to, debility; weak;\n   debilitating.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asthenopia', 'n.', 'Weakness of sight.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asthma', 'n.', 'A disease, characterized by difficulty of breathing (due to\n   a spasmodic contraction of the bronchi); recurring at intervals,\n   accompanied with a wheezing sound, a sense of constriction in the\n   chest, a cough, and expectoration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asthmatic', 'a.', 'Alt. of Asthmatical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asthmatical', 'a.', 'Of or pertaining to asthma; as, an asthmatic cough;\n   liable to, or suffering from, asthma; as, an asthmatic patient.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asthmatic', 'n.', 'A person affected with asthma.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astigmatic', 'a.', 'Affected with, or pertaining to, astigmatism; as,\n   astigmatic eyes; also, remedying astigmatism; as, astigmatic lenses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astigmatism', 'n.', 'A defect of the eye or of a lens, in consequence of\n   which the rays derived from one point are not brought to a single focal\n   point, thus causing imperfect images or indistinctness of vision.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astipulate', 'v. i.', 'To assent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astipulation', 'n.', 'Stipulation; agreement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astir', 'adv. & a.', 'Stirring; in a state of activity or motion; out of\n   bed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astomatous', 'a.', 'Alt. of Astomous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astomous', 'a.', 'Not possessing a mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astoned', 'imp. & p. p.', 'of Astone');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astond', '', 'of Astone');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astound', '', 'of Astone');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aston', 'v. t.', 'Alt. of Astone');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astone', 'v. t.', 'To stun; to astonish; to stupefy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astonied', 'p. p.', 'Stunned; astonished. See Astony.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astonished', 'imp. & p. p.', 'of Astonish');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astonishing', 'p. pr. & vb. n.', 'of Astonish');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astonish', 'v. t.', 'To stun; to render senseless, as by a blow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astonish', 'v. t.', 'To strike with sudden fear, terror, or wonder; to\n   amaze; to surprise greatly, as with something unaccountable; to\n   confound with some sudden emotion or passion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astonishedly', 'adv.', 'In an astonished manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astonishing', 'a.', 'Very wonderful; of a nature to excite astonishment;\n   as, an astonishing event.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astonishment', 'n.', 'The condition of one who is stunned. Hence: Numbness;\n   loss of sensation; stupor; loss of sense.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astonishment', 'n.', 'Dismay; consternation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astonishment', 'n.', 'The overpowering emotion excited when something\n   unaccountable, wonderful, or dreadful is presented to the mind; an\n   intense degree of surprise; amazement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astonishment', 'n.', 'The object causing such an emotion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astonied', 'imp. & p. p.', 'of Astony');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astonying', 'p. pr. & vb. n.', 'of Astony');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astony', 'v. t.', 'To stun; to bewilder; to astonish; to dismay.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astoop', 'adv.', 'In a stooping or inclined position.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astound', 'a.', 'Stunned; astounded; astonished.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astounded', 'imp. & p. p.', 'of Astound');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astound', '', 'of Astound');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astounding', 'p. pr. & vb. n.', 'of Astound');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astound', 'a.', 'To stun; to stupefy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astound', 'a.', 'To astonish; to strike with amazement; to confound with\n   wonder, surprise, or fear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astounding', 'a.', 'Of a nature to astound; astonishing; amazing; as, an\n   astounding force, statement, or fact.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astoundment', 'n.', 'Amazement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrachan', 'a. & n.', 'See Astrakhan.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astraddle', 'adv.', 'In a straddling position; astride; bestriding; as, to\n   sit astraddle a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astraean', 'a.', 'Pertaining to the genus Astraea or the family Astraeidae.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astraean', 'n.', 'A coral of the family Astraeidae; a star coral.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astragal', 'n.', 'A convex molding of rounded surface, generally from half\n   to three quarters of a circle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astragal', 'n.', 'A round molding encircling a cannon near the mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astragalar', 'a.', 'Of or pertaining to the astragalus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astragaloid', 'a.', 'Resembling the astragalus in form.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astragalomancy', 'n.', 'Divination by means of small bones or dice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astragalus', 'n.', 'The ankle bone, or hock bone; the bone of the tarsus\n   which articulates with the tibia at the ankle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astragalus', 'n.', 'A genus of papilionaceous plants, of the tribe\n   Galegeae, containing numerous species, two of which are called, in\n   English, milk vetch and licorice vetch. Gum tragacanth is obtained from\n   different oriental species, particularly the A. gummifer and A. verus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astragalus', 'n.', 'See Astragal, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrakhan', 'a.', 'Of or pertaining to Astrakhan in Russia or its products;\n   made of an Astrakhan skin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrakhan', 'n.', 'The skin of stillborn or young lambs of that region, the\n   curled wool of which resembles fur.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astral', 'a.', 'Pertaining to, coming from, or resembling, the stars;\n   starry; starlike.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrand', 'adv. & a.', 'Stranded.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astray', 'adv. & a.', 'Out of the right, either in a literal or in a\n   figurative sense; wandering; as, to lead one astray.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astricted', 'imp. & p. p.', 'of Astrict');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astricting', 'p. pr. & vb. n.', 'of Astrict');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrict', 'v. t.', 'To bind up; to confine; to constrict; to contract.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrict', 'v. t.', 'To bind; to constrain; to restrict; to limit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrict', 'v. t.', 'To restrict the tenure of; as, to astrict lands. See\n   Astriction, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrict', 'a.', 'Concise; contracted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astriction', 'n.', 'The act of binding; restriction; also, obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astriction', 'n.', 'A contraction of parts by applications; the action of\n   an astringent substance on the animal economy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astriction', 'n.', 'Constipation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astriction', 'n.', 'Astringency.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astriction', 'n.', 'An obligation to have the grain growing on certain\n   lands ground at a certain mill, the owner paying a toll.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrictive', 'a.', 'Binding; astringent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrictive', 'n.', 'An astringent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrictory', 'a.', 'Astrictive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astride', 'adv.', 'With one leg on each side, as a man when on horseback;\n   with the legs stretched wide apart; astraddle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astriferous', 'a.', 'Bearing stars.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astringed', 'imp. & p. p.', 'of Astringe');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astringing', 'p. pr. & vb. n.', 'of Astringe');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astringe', 'v. t.', 'To bind fast; to constrict; to contract; to cause\n   parts to draw together; to compress.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astringe', 'v. t.', 'To bind by moral or legal obligation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astringency', 'n.', 'The quality of being astringent; the power of\n   contracting the parts of the body; that quality in medicines or other\n   substances which causes contraction of the organic textures; as, the\n   astringency of tannin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astringent', 'a.', 'Drawing together the tissues; binding; contracting; --\n   opposed to laxative; as, astringent medicines; a butter and astringent\n   taste; astringent fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astringent', 'a.', 'Stern; austere; as, an astringent type of virtue.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astringent', 'n.', 'A medicine or other substance that produces contraction\n   in the soft organic textures, and checks discharges of blood, mucus,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astringently', 'adv.', 'In an astringent manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astringer', 'n.', 'A falconer who keeps a goshawk.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astro-', '', 'The combining form of the Greek word a`stron, meaning star.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrofel', 'n.', 'Alt. of Astrofell');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrofell', 'n.', 'A bitter herb, probably the same as aster, or starwort.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrogeny', 'n.', 'The creation or evolution of the stars or the heavens.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrognosy', 'n.', 'The science or knowledge of the stars, esp. the fixed\n   stars.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrogony', 'n.', 'Same as Astrogeny.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrography', 'n.', 'The art of describing or delineating the stars; a\n   description or mapping of the heavens.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astroite', 'n.', 'A radiated stone or fossil; star-stone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrolabe', 'n.', 'An instrument for observing or showing the positions of\n   the stars. It is now disused.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrolabe', 'n.', 'A stereographic projection of the sphere on the plane of\n   a great circle, as the equator, or a meridian; a planisphere.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrolater', 'n.', 'A worshiper of the stars.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrolatry', 'n.', 'The worship of the stars.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrolithology', 'n.', 'The science of aerolites.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrologer', 'n.', 'One who studies the stars; an astronomer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrologer', 'n.', 'One who practices astrology; one who professes to\n   foretell events by the aspects and situation of the stars.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrologian', 'n.', 'An astrologer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrologic', 'a.', 'Alt. of Astrological');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrological', 'a.', 'Of or pertaining to astrology; professing or\n   practicing astrology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrologize', 'v. t. & i.', 'To apply astrology to; to study or practice\n   astrology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrology', 'n.', 'In its etymological signification, the science of the\n   stars; among the ancients, synonymous with astronomy; subsequently, the\n   art of judging of the influences of the stars upon human affairs, and\n   of foretelling events by their position and aspects.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astromantic', 'a.', 'Of or pertaining to divination by means of the stars;\n   astrologic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrometeorology', 'n.', 'The investigation of the relation between the\n   sun, moon, and stars, and the weather.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrometer', 'n.', 'An instrument for comparing the relative amount of the\n   light of stars.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrometry', 'n.', 'The art of making measurements among the stars, or of\n   determining their relative magnitudes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astronomer', 'n.', 'An astrologer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astronomer', 'n.', 'One who is versed in astronomy; one who has a knowledge\n   of the laws of the heavenly orbs, or the principles by which their\n   motions are regulated, with their various phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astronomian', 'n.', 'An astrologer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astronomic', 'a.', 'Astronomical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astronomical', 'a.', 'Of or pertaining to astronomy; in accordance with the\n   methods or principles of astronomy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astronomize', 'v. i.', 'To study or to talk astronomy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astronomy', 'n.', 'Astrology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astronomy', 'n.', 'The science which treats of the celestial bodies, of\n   their magnitudes, motions, distances, periods of revolution, eclipses,\n   constitution, physical condition, and of the causes of their various\n   phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astronomy', 'n.', 'A treatise on, or text-book of, the science.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrophel', 'n.', 'See Astrofel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrophotography', 'n.', 'The application of photography to the delineation\n   of the sun, moon, and stars.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrophysical', 'a.', 'Pertaining to the physics of astronomical science.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrophyton', 'n.', 'A genus of ophiurans having the arms much branched.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astroscope', 'n.', 'An old astronomical instrument, formed of two cones, on\n   whose surface the constellations were delineated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astroscopy', 'n.', 'Observation of the stars.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrotheology', 'n.', 'Theology founded on observation or knowledge of the\n   celestial bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astructive', 'a.', 'Building up; constructive; -- opposed to destructive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrut', 'a. & adv.', 'Sticking out, or puffed out; swelling; in a swelling\n   manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astrut', 'a. & adv.', 'In a strutting manner; with a strutting gait.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astucious', 'a.', 'Subtle; cunning; astute.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astucity', 'n.', 'Craftiness; astuteness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astun', 'v. t.', 'To stun.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asturian', 'a.', 'Of or pertaining to Asturias in Spain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asturian', 'n.', 'A native of Asturias.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astute', 'a.', 'Critically discerning; sagacious; shrewd; subtle; crafty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astylar', 'a.', 'Without columns or pilasters.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Astyllen', 'n.', 'A small dam to prevent free passage of water in an adit\n   or level.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asunder', 'adv.', 'Apart; separate from each other; into parts; in two;\n   separately; into or in different pieces or places.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asura', 'n.', 'An enemy of the gods, esp. one of a race of demons and\n   giants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aswail', 'n.', 'The sloth bear (Melursus labiatus) of India.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asweve', 'v. t.', 'To stupefy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aswing', 'adv.', 'In a state of swinging.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aswoon', 'adv.', 'In a swoon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aswooned', 'adv.', 'In a swoon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asylums', 'pl. ', 'of Asylum');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asyla', 'pl. ', 'of Asylum');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asylum', 'n.', 'A sanctuary or place of refuge and protection, where\n   criminals and debtors found shelter, and from which they could not be\n   forcibly taken without sacrilege.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asylum', 'n.', 'Any place of retreat and security.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asylum', 'n.', 'An institution for the protection or relief of some class\n   of destitute, unfortunate, or afflicted persons; as, an asylum for the\n   aged, for the blind, or for the insane; a lunatic asylum; an orphan\n   asylum.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asymmetral', 'a.', 'Incommensurable; also, unsymmetrical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asymmetric', 'a.', 'Alt. of Asymmetrical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asymmetrical', 'a.', 'Incommensurable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asymmetrical', 'a.', 'Not symmetrical; wanting proportion; esp., not\n   bilaterally symmetrical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asymmetrous', 'a.', 'Asymmetrical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asymmetry', 'n.', 'Want of symmetry, or proportion between the parts of a\n   thing, esp. want of bilateral symmetry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asymmetry', 'n.', 'Incommensurability.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asymptote', 'n.', 'A line which approaches nearer to some curve than\n   assignable distance, but, though infinitely extended, would never meet\n   it. Asymptotes may be straight lines or curves. A rectilinear asymptote\n   may be conceived as a tangent to the curve at an infinite distance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asynartete', 'a.', 'Disconnected; not fitted or adjusted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asyndetic', 'a.', 'Characterized by the use of asyndeton; not connected by\n   conjunctions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asyndeton', 'n.', 'A figure which omits the connective; as, I came, I saw,\n   I conquered. It stands opposed to polysyndeton.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asystole', 'n.', 'A weakening or cessation of the contractile power of the\n   heart.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Asystolism', 'n.', 'The state or symptoms characteristic of asystole.');
  INSERT INTO words (word, word_type, description) VALUES  (  'At', 'prep.', 'Primarily, this word expresses the relations of presence,\n   nearness in place or time, or direction toward; as, at the ninth hour;\n   at the house; to aim at a mark. It is less definite than in or on; at\n   the house may be in or near the house. From this original import are\n   derived all the various uses of at.');
  INSERT INTO words (word, word_type, description) VALUES  (  'At', 'prep.', 'A relation of proximity to, or of presence in or on,\n   something; as, at the door; at your shop; at home; at school; at hand;\n   at sea and on land.');
  INSERT INTO words (word, word_type, description) VALUES  (  'At', 'prep.', 'The relation of some state or condition; as, at war; at\n   peace; at ease; at your service; at fault; at liberty; at risk; at\n   disadvantage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'At', 'prep.', 'The relation of some employment or action; occupied with;\n   as, at engraving; at husbandry; at play; at work; at meat (eating);\n   except at puns.');
  INSERT INTO words (word, word_type, description) VALUES  (  'At', 'prep.', 'The relation of a point or position in a series, or of\n   degree, rate, or value; as, with the thermometer at 80¡; goods sold at\n   a cheap price; a country estimated at 10,000 square miles; life is\n   short at the longest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'At', 'prep.', 'The relations of time, age, or order; as, at ten oclock; at\n   twenty-one; at once; at first.');
  INSERT INTO words (word, word_type, description) VALUES  (  'At', 'prep.', 'The relations of source, occasion, reason, consequence, or\n   effect; as, at the sight; at this news; merry at anything; at this\n   declaration; at his command; to demand, require, receive, deserve,\n   endure at your hands.');
  INSERT INTO words (word, word_type, description) VALUES  (  'At', 'prep.', 'Relation of direction toward an object or end; as, look at\n   it; to point at one; to aim at a mark; to throw, strike, shoot, wink,\n   mock, laugh at any one.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atabal', 'n.', 'A kettledrum; a kind of tabor, used by the Moors.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atacamite', 'n.', 'An oxychloride of copper, usually in emerald-green\n   prismatic crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atafter', 'prep.', 'After.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ataghan', 'n.', 'See Yataghan.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atake', 'v. t.', 'To overtake.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ataman', 'n.', 'A hetman, or chief of the Cossacks.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ataraxia', 'n.', 'Alt. of Ataraxy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ataraxy', 'n.', 'Perfect peace of mind, or calmness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ataunt', 'adv.', 'Alt. of Ataunto');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ataunto', 'adv.', 'Fully rigged, as a vessel; with all sails set; set on\n   end or set right.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atavic', 'a.', 'Pertaining to a remote ancestor, or to atavism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atavism', 'n.', 'The recurrence, or a tendency to a recurrence, of the\n   original type of a species in the progeny of its varieties; resemblance\n   to remote rather than to near ancestors; reversion to the original\n   form.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atavism', 'n.', 'The recurrence of any peculiarity or disease of an\n   ancestor in a subsequent generation, after an intermission for a\n   generation or two.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ataxia', 'n.', 'Alt. of Ataxy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ataxy', 'n.', 'Disorder; irregularity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ataxy', 'n.', 'Irregularity in disease, or in the functions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ataxy', 'n.', 'The state of disorder that characterizes nervous fevers and\n   the nervous condition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ataxic', 'a.', 'Characterized by ataxy, that is, (a) by great irregularity\n   of functions or symptoms, or (b) by a want of coordinating power in\n   movements.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atazir', 'n.', 'The influence of a star upon other stars or upon men.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ate', '', 'the preterit of Eat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ate', 'n.', 'The goddess of mischievous folly; also, in later poets, the\n   goddess of vengeance.');
  INSERT INTO words (word, word_type, description) VALUES  (  '-ate', '', 'As an ending of participles or participial adjectives it is\n   equivalent to -ed; as, situate or situated; animate or animated.');
  INSERT INTO words (word, word_type, description) VALUES  (  '-ate', '', 'As the ending of a verb, it means to make, to cause, to act,\n   etc.; as, to propitiate (to make propitious); to animate (to give life\n   to).');
  INSERT INTO words (word, word_type, description) VALUES  (  '-ate', '', 'As a noun suffix, it marks the agent; as, curate, delegate. It\n   also sometimes marks the office or dignity; as, tribunate.');
  INSERT INTO words (word, word_type, description) VALUES  (  '-ate', '', 'In chemistry it is used to denote the salts formed from those\n   acids whose names end -ic (excepting binary or halogen acids); as,\n   sulphate from sulphuric acid, nitrate from nitric acid, etc. It is also\n   used in the case of certain basic salts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atechnic', 'a.', 'Without technical or artistic knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ateles', 'n.', 'A genus of American monkeys with prehensile tails, and\n   having the thumb wanting or rudimentary. See Spider monkey, and Coaita.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atelier', 'n.', 'A workshop; a studio.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atellan', 'a.', 'Of or pertaining to Atella, in ancient Italy; as, Atellan\n   plays; farcical; ribald.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atellan', 'n.', 'A farcical drama performed at Atella.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athalamous', 'a.', 'Not furnished with shields or beds for the spores, as\n   the thallus of certain lichens.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athamaunt', 'n.', 'Adamant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athanasian', 'a.', 'Of or pertaining to Athanasius, bishop of Alexandria in\n   the 4th century.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athanor', 'n.', 'A digesting furnace, formerly used by alchemists. It was\n   so constructed as to maintain uniform and durable heat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athecata', 'n. pl.', 'A division of Hydroidea in which the zooids are\n   naked, or not inclosed in a capsule. See Tubularian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atheism', 'n.', 'The disbelief or denial of the existence of a God, or\n   supreme intelligent Being.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atheism', 'n.', 'Godlessness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atheist', 'n.', 'One who disbelieves or denies the existence of a God, or\n   supreme intelligent Being.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atheist', 'n.', 'A godless person.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atheistic', 'a.', 'Alt. of Atheistical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atheistical', 'a.', 'Pertaining to, implying, or containing, atheism; --\n   applied to things; as, atheistic doctrines, opinions, or books.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atheistical', 'a.', 'Disbelieving the existence of a God; impious; godless;\n   -- applied to persons; as, an atheistic writer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atheize', 'v. t.', 'To render atheistic or godless.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atheize', 'v. i.', 'To discourse, argue, or act as an atheist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atheling', 'n.', 'An Anglo-Saxon prince or nobleman; esp., the heir\n   apparent or a prince of the royal family.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atheneums', 'pl. ', 'of Athenaeum');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athenaea', 'pl. ', 'of Athenaeum');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atheneum', 'n.', 'Alt. of Athenaeum');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athenaeum', 'n.', 'A temple of Athene, at Athens, in which scholars and\n   poets were accustomed to read their works and instruct students.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athenaeum', 'n.', 'A school founded at Rome by Hadrian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athenaeum', 'n.', 'A literary or scientific association or club.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athenaeum', 'n.', 'A building or an apartment where a library, periodicals,\n   and newspapers are kept for use.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athenian', 'a.', 'Of or pertaining to Athens, the metropolis of Greece.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athenian', 'n.', 'A native or citizen of Athens.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atheological', 'a.', 'Opposed to theology; atheistic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atheology', 'n.', 'Antagonism to theology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atheous', 'a.', 'Atheistic; impious.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atheous', 'a.', 'Without God, neither accepting nor denying him.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atherine', 'n.', 'A small marine fish of the family Atherinidae, having a\n   silvery stripe along the sides. The European species (Atherina\n   presbyter) is used as food. The American species (Menidia notata) is\n   called silversides and sand smelt. See Silversides.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athermancy', 'n.', 'Inability to transmit radiant heat; impermeability to\n   heat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athermanous', 'a.', 'Not transmitting heat; -- opposed to diathermanous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athermous', 'a.', 'Athermanous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atheroid', 'a.', 'Shaped like an ear of grain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atheroma', 'n.', 'An encysted tumor containing curdy matter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atheroma', 'n.', 'A disease characterized by thickening and fatty\n   degeneration of the inner coat of the arteries.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atheromatous', 'a.', 'Of, pertaining to, or having the nature of, atheroma.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athetosis', 'n.', 'A variety of chorea, marked by peculiar tremors of the\n   fingers and toes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athink', 'v. t.', 'To repent; to displease; to disgust.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athirst', 'a.', 'Wanting drink; thirsty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athirst', 'a.', 'Having a keen appetite or desire; eager; longing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athlete', 'n.', 'One who contended for a prize in the public games of\n   ancient Greece or Rome.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athlete', 'n.', 'Any one trained to contend in exercises requiring great\n   physical agility and strength; one who has great activity and strength;\n   a champion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athlete', 'n.', 'One fitted for, or skilled in, intellectual contests; as,\n   athletes of debate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athletic', 'a.', 'Of or pertaining to athletes or to the exercises\n   practiced by them; as, athletic games or sports.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athletic', 'a.', 'Befitting an athlete; strong; muscular; robust; vigorous;\n   as, athletic Celts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athleticism', 'n.', 'The practice of engaging in athletic games; athletism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athletics', 'n.', 'The art of training by athletic exercises; the games and\n   sports of athletes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athletism', 'n.', 'The state or practice of an athlete; the characteristics\n   of an athlete.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athwart', 'prep.', 'Across; from side to side of.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athwart', 'prep.', 'Across the direction or course of; as, a fleet standing\n   athwart our course.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athwart', 'adv.', 'Across, especially in an oblique direction; sidewise;\n   obliquely.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Athwart', 'adv.', 'Across the course; so as to thwart; perversely.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atilt', 'adv.', 'In the manner of a tilter; in the position, or with the\n   action, of one making a thrust.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atilt', 'adv.', 'In the position of a cask tilted, or with one end raised.\n   [In this sense sometimes used as an adjective.]');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atimy', 'n.', 'Public disgrace or stigma; infamy; loss of civil rights.');
  INSERT INTO words (word, word_type, description) VALUES  (  '-ation', '', 'A suffix forming nouns of action, and often equivalent to the\n   verbal substantive in -ing. It sometimes has the further meanings of\n   state, and that which results from the action. Many of these nouns have\n   verbs in -ate; as, alliterate -ation, narrate -ation; many are derived\n   through the French; as, alteration, visitation; and many are formed on\n   verbs ending in the Greek formative -ize (Fr. -ise); as, civilization,\n   demoralization.');
  INSERT INTO words (word, word_type, description) VALUES  (  'A-tiptoe', 'adv.', 'On tiptoe; eagerly expecting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atlanta', 'n.', 'A genus of small glassy heteropod mollusks found swimming\n   at the surface in mid ocean. See Heteropod.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atlantal', 'a.', 'Relating to the atlas.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atlantal', 'a.', 'Anterior; cephalic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atlantean', 'a.', 'Of or pertaining to the isle Atlantis, which the\n   ancients allege was sunk, and overwhelmed by the ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atlantean', 'a.', 'Pertaining to, or resembling, Atlas; strong.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atlantes', 'n. pl.', 'Figures or half figures of men, used as columns to\n   support an entablature; -- called also telamones. See Caryatides.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atlantic', 'a.', 'Of or pertaining to Mt. Atlas in Libya, and hence applied\n   to the ocean which lies between Europe and Africa on the east and\n   America on the west; as, the Atlantic Ocean (called also the Atlantic);\n   the Atlantic basin; the Atlantic telegraph.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atlantic', 'a.', 'Of or pertaining to the isle of Atlantis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atlantic', 'a.', 'Descended from Atlas.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atlantides', 'n. pl.', 'The Pleiades or seven stars, fabled to have been\n   the daughters of Atlas.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atlases', 'pl. ', 'of Atlas');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atlas', 'n.', 'One who sustains a great burden.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atlas', 'n.', 'The first vertebra of the neck, articulating immediately\n   with the skull, thus sustaining the globe of the head, whence the name.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atlas', 'n.', 'A collection of maps in a volume');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atlas', 'n.', 'A volume of plates illustrating any subject.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atlas', 'n.', 'A work in which subjects are exhibited in a tabular from or\n   arrangement; as, an historical atlas.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atlas', 'n.', 'A large, square folio, resembling a volume of maps; --\n   called also atlas folio.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atlas', 'n.', 'A drawing paper of large size. See under Paper, n.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atlas', 'n.', 'A rich kind of satin manufactured in India.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmidometer', 'n.', 'An instrument for measuring the evaporation from\n   water, ice, or snow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmo', 'n.', 'The standard atmospheric pressure used in certain physical\n   measurements calculations; conventionally, that pressure under which\n   the barometer stands at 760 millimeters, at a temperature of 0¡\n   Centigrade, at the level of the sea, and in the latitude of Paris.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmologic', 'a.', 'Alt. of Atmological');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmological', 'a.', 'Of or pertaining to atmology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmologist', 'n.', 'One who is versed in atmology.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmology', 'n.', 'That branch of science which treats of the laws and\n   phenomena of aqueous vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmolysis', 'n.', 'The act or process of separating mingled gases of\n   unequal diffusibility by transmission through porous substances.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmolyzation', 'n.', 'Separation by atmolysis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmolyze', 'v. t.', 'To subject to atmolysis; to separate by atmolysis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmolyzer', 'n.', 'An apparatus for effecting atmolysis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmometer', 'n.', 'An instrument for measuring the rate of evaporation from\n   a moist surface; an evaporometer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmosphere', 'n.', 'The whole mass of aeriform fluid surrounding the earth;\n   -- applied also to the gaseous envelope of any celestial orb, or other\n   body; as, the atmosphere of Mars.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmosphere', 'n.', 'Any gaseous envelope or medium.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmosphere', 'n.', 'A supposed medium around various bodies; as, electrical\n   atmosphere, a medium formerly supposed to surround electrical bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmosphere', 'n.', 'The pressure or weight of the air at the sea level, on\n   a unit of surface, or about 14.7 Ibs. to the sq. inch.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmosphere', 'n.', 'Any surrounding or pervading influence or condition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmosphere', 'n.', 'The portion of air in any locality, or affected by a\n   special physical or sanitary condition; as, the atmosphere of the room;\n   a moist or noxious atmosphere.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmospheric', 'a.', 'Alt. of Atmospherical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmospherical', 'a.', 'Of or pertaining to the atmosphere; of the nature\n   of, or resembling, the atmosphere; as, atmospheric air; the atmospheric\n   envelope of the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmospherical', 'a.', 'Existing in the atmosphere.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmospherical', 'a.', 'Caused, or operated on, by the atmosphere; as, an\n   atmospheric effect; an atmospheric engine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmospherical', 'a.', 'Dependent on the atmosphere.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmospherically', 'adv.', 'In relation to the atmosphere.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atmospherology', 'n.', 'The science or a treatise on the atmosphere.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atokous', 'a.', 'Producing only asexual individuals, as the eggs of certain\n   annelids.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atoll', 'n.', 'A coral island or islands, consisting of a belt of coral\n   reef, partly submerged, surrounding a central lagoon or depression; a\n   lagoon island.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atom', 'n.', 'An ultimate indivisible particle of matter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atom', 'n.', 'An ultimate particle of matter not necessarily indivisible; a\n   molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atom', 'n.', 'A constituent particle of matter, or a molecule supposed to\n   be made up of subordinate particles.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atom', 'n.', 'The smallest particle of matter that can enter into\n   combination; one of the elementary constituents of a molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atom', 'n.', 'Anything extremely small; a particle; a whit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atom', 'v. t.', 'To reduce to atoms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atomic', 'a.', 'Alt. of Atomical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atomical', 'a.', 'Of or pertaining to atoms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atomical', 'a.', 'Extremely minute; tiny.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atomically', 'adv.', 'In an atomic manner; in accordance with the atomic\n   philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atomician', 'n.', 'An atomist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atomicism', 'n.', 'Atomism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atomicity', 'n.', 'Degree of atomic attraction; equivalence; valence; also\n   (a later use) the number of atoms in an elementary molecule. See\n   Valence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atomism', 'n.', 'The doctrine of atoms. See Atomic philosophy, under\n   Atomic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atomist', 'n.', 'One who holds to the atomic philosophy or theory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atomistic', 'a.', 'Of or pertaining to atoms; relating to atomism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atomization', 'n.', 'The act of reducing to atoms, or very minute\n   particles; or the state of being so reduced.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atomization', 'n.', 'The reduction of fluids into fine spray.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atomize', 'v. t.', 'To reduce to atoms, or to fine spray.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atomizer', 'n.', 'One who, or that which, atomizes; esp., an instrument for\n   reducing a liquid to spray for disinfecting, cooling, or perfuming.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atomology', 'n.', 'The doctrine of atoms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atomy', 'n.', 'An atom; a mite; a pigmy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atomy', 'n.', 'A skeleton.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atonable', 'a.', 'Admitting an atonement; capable of being atoned for;\n   expiable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'At one', '', 'In concord or friendship; in agreement (with each other); as,\n   to be, bring, make, or set, at one, i. e., to be or bring in or to a\n   state of agreement or reconciliation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'At one', '', 'Of the same opinion; agreed; as, on these points we are at\n   one.');
  INSERT INTO words (word, word_type, description) VALUES  (  'At one', '', 'Together.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atoned', 'imp. & p. p.', 'of Atone');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atoning', 'p. pr. & vb. n.', 'of Atone');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atone', 'v. i.', 'To agree; to be in accordance; to accord.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atone', 'v. i.', 'To stand as an equivalent; to make reparation,\n   compensation, or amends, for an offense or a crime.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atone', 'v. t.', 'To set at one; to reduce to concord; to reconcile, as\n   parties at variance; to appease.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atone', 'v. t.', 'To unite in making.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atone', 'v. t.', 'To make satisfaction for; to expiate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atonement', 'n.', 'Reconciliation; restoration of friendly relations;\n   agreement; concord.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atonement', 'n.', 'Satisfaction or reparation made by giving an equivalent\n   for an injury, or by doing of suffering that which will be received in\n   satisfaction for an offense or injury; expiation; amends; -- with for.\n   Specifically, in theology: The expiation of sin made by the obedience,\n   personal suffering, and death of Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atoner', 'n.', 'One who makes atonement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atonic', 'a.', 'Characterized by atony, or want of vital energy; as, an\n   atonic disease.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atonic', 'a.', 'Unaccented; as, an atonic syllable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atonic', 'a.', 'Destitute of tone vocality; surd.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atonic', 'n.', 'A word that has no accent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atonic', 'n.', 'An element of speech entirely destitute of vocality, or\n   produced by the breath alone; a nonvocal or surd consonant; a\n   breathing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atonic', 'n.', 'A remedy capable of allaying organic excitement or\n   irritation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atony', 'n.', 'Want of tone; weakness of the system, or of any organ,\n   especially of such as are contractile.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atop', 'adv.', 'On or at the top.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrabilarian', 'a.', 'Alt. of Atrabilarious');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrabilarious', 'a.', 'Affected with melancholy; atrabilious.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrabilarian', 'n.', 'A person much given to melancholy; a hypochondriac.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrabiliar', 'a.', 'Melancholy; atrabilious.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrabiliary', 'a.', 'Of or pertaining to atra bilis or black bile, a fluid\n   formerly supposed to be produced by the kidneys.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrabiliary', 'a.', 'Melancholic or hypohondriac; atrabilious; -- from the\n   supposed predominance of black bile, to the influence of which the\n   ancients attributed hypochondria, melancholy, and mania.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrabilious', 'a.', 'Melancholic or hypochondriac; atrabiliary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atramentaceous', 'a.', 'Black, like ink; inky; atramental.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atramental', 'a.', 'Alt. of Atramentous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atramentous', 'a.', 'Of or pertaining to ink; inky; black, like ink; as,\n   atramental galls; atramentous spots.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atramentarious', 'a.', 'Like ink; suitable for making ink. Sulphate of iron\n   (copperas, green vitriol) is called atramentarious, as being used in\n   making ink.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrede', 'v. t.', 'To surpass in council.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrenne', 'v. t.', 'To outrun.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atresia', 'n.', 'Absence or closure of a natural passage or channel of the\n   body; imperforation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrial', 'a.', 'Of or pertaining to an atrium.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrip', 'adv.', 'Just hove clear of the ground; -- said of the anchor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrip', 'adv.', 'Sheeted home, hoisted taut up and ready for trimming; --\n   said of sails.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrip', 'adv.', 'Hoisted up and ready to be swayed across; -- said of\n   yards.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atria', 'pl. ', 'of Atrium');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrium', 'n.', 'A square hall lighted from above, into which rooms open at\n   one or more levels.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrium', 'n.', 'An open court with a porch or gallery around three or more\n   sides; especially at the entrance of a basilica or other church. The\n   name was extended in the Middle Ages to the open churchyard or\n   cemetery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrium', 'n.', 'The main part of either auricle of the heart as distinct\n   from the auricular appendix. Also, the whole articular portion of the\n   heart.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrium', 'n.', 'A cavity in ascidians into which the intestine and\n   generative ducts open, and which also receives the water from the\n   gills. See Ascidioidea.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrocha', 'n.', 'A kind of chaetopod larva in which no circles of cilia are\n   developed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrocious', 'a.', 'Extremely heinous; full of enormous wickedness; as,\n   atrocious quilt or deeds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrocious', 'a.', 'Characterized by, or expressing, great atrocity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrocious', 'a.', 'Very grievous or violent; terrible; as, atrocious\n   distempers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrocities', 'pl. ', 'of Atrocity');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrocity', 'n.', 'Enormous wickedness; extreme heinousness or cruelty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrocity', 'n.', 'An atrocious or extremely cruel deed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrophic', 'a.', 'Relating to atrophy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrophied', 'p. a.', 'Affected with atrophy, as a tissue or organ; arrested\n   in development at a very early stage; rudimentary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrophy', 'n.', 'A wasting away from want of nourishment; diminution in\n   bulk or slow emaciation of the body or of any part.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrophied', 'p. p.', 'of Atrophy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrophy', 'v. t.', 'To cause to waste away or become abortive; to starve or\n   weaken.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrophy', 'v. i.', 'To waste away; to dwindle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atropia', 'n.', 'Same as Atropine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atropine', 'n.', 'A poisonous, white, crystallizable alkaloid, extracted\n   from the Atropa belladonna, or deadly nightshade, and the Datura\n   Stramonium, or thorn apple. It is remarkable for its power in dilating\n   the pupil of the eye. Called also daturine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atropism', 'n.', 'A condition of the system produced by long use of\n   belladonna.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atropous', 'a.', 'Not inverted; orthotropous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrous', 'a.', 'Coal-black; very black.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atrypa', 'n.', 'A extinct genus of Branchiopoda, very common in Silurian\n   limestones.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attabal', 'n.', 'See Atabal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attacca', '', 'Attack at once; -- a direction at the end of a movement to\n   show that the next is to follow immediately, without any pause.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attached', 'imp. & p. p.', 'of Attach');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attaching', 'p. pr. & vb. n.', 'of Attach');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attach', 'v. t.', 'To bind, fasten, tie, or connect; to make fast or join;\n   as, to attach one thing to another by a string, by glue, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attach', 'v. t.', 'To connect; to place so as to belong; to assign by\n   authority; to appoint; as, an officer is attached to a certain\n   regiment, company, or ship.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attach', 'v. t.', 'To win the heart of; to connect by ties of love or\n   self-interest; to attract; to fasten or bind by moral influence; --\n   with to; as, attached to a friend; attaching others to us by wealth or\n   flattery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attach', 'v. t.', 'To connect, in a figurative sense; to ascribe or\n   attribute; to affix; -- with to; as, to attach great importance to a\n   particular circumstance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attach', 'v. t.', 'To take, seize, or lay hold of.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attach', 'v. t.', 'To take by legal authority: (a) To arrest by writ, and\n   bring before a court, as to answer for a debt, or a contempt; --\n   applied to a taking of the person by a civil process; being now rarely\n   used for the arrest of a criminal. (b) To seize or take (goods or real\n   estate) by virtue of a writ or precept to hold the same to satisfy a\n   judgment which may be rendered in the suit. See Attachment, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attach', 'v. i.', 'To adhere; to be attached.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attach', 'v. i.', 'To come into legal operation in connection with\n   anything; to vest; as, dower will attach.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attach', 'n.', 'An attachment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attachable', 'a.', 'Capable of being attached; esp., liable to be taken by\n   writ or precept.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attache', 'v. t.', 'One attached to another person or thing, as a part of a\n   suite or staff. Specifically: One attached to an embassy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attachment', 'n.', 'The act attaching, or state of being attached; close\n   adherence or affection; fidelity; regard; an/ passion of affection that\n   binds a person; as, an attachment to a friend, or to a party.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attachment', 'n.', 'That by which one thing is attached to another;\n   connection; as, to cut the attachments of a muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attachment', 'n.', 'Something attached; some adjunct attached to an\n   instrument, machine, or other object; as, a sewing machine attachment\n   (i. e., a device attached to a sewing machine to enable it to do\n   special work, as tucking, etc.).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attachment', 'n.', 'A seizure or taking into custody by virtue of a legal\n   process.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attachment', 'n.', 'The writ or percept commanding such seizure or taking.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attacked', 'imp. & p. p.', 'of Attack');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attacking', 'p. pr. & vb. n.', 'of Attack');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attack', 'v. t.', 'To fall upon with force; to assail, as with force and\n   arms; to assault.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attack', 'v. t.', 'To assail with unfriendly speech or writing; to begin a\n   controversy with; to attempt to overthrow or bring into disrepute, by\n   criticism or satire; to censure; as, to attack a man, or his opinions,\n   in a pamphlet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attack', 'v. t.', 'To set to work upon, as upon a task or problem, or some\n   object of labor or investigation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attack', 'v. t.', 'To begin to affect; to begin to act upon, injuriously or\n   destructively; to begin to decompose or waste.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attack', 'v. i.', 'To make an onset or attack.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attack', 'n.', 'The act of attacking, or falling on with force or violence;\n   an onset; an assault; -- opposed to defense.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attack', 'n.', 'An assault upon ones feelings or reputation with\n   unfriendly or bitter words.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attack', 'n.', 'A setting to work upon some task, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attack', 'n.', 'An access of disease; a fit of sickness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attack', 'n.', 'The beginning of corrosive, decomposing, or destructive\n   action, by a chemical agent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attackable', 'a.', 'Capable of being attacked.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attacker', 'n.', 'One who attacks.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attagas', 'n.', 'Alt. of Attagen');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attagen', 'n.', 'A species of sand grouse (Syrrghaptes Pallasii) found in\n   Asia and rarely in southern Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attaghan', 'n.', 'See Yataghan.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attained', 'imp. & p. p.', 'of Attain');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attaining', 'p. pr. & vb. n.', 'of Attain');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attain', 'v. t.', 'To achieve or accomplish, that is, to reach by efforts;\n   to gain; to compass; as, to attain rest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attain', 'v. t.', 'To gain or obtain possession of; to acquire.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attain', 'v. t.', 'To get at the knowledge of; to ascertain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attain', 'v. t.', 'To reach or come to, by progression or motion; to arrive\n   at.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attain', 'v. t.', 'To overtake.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attain', 'v. t.', 'To reach in excellence or degree; to equal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attain', 'v. i.', 'To come or arrive, by motion, growth, bodily exertion,\n   or efforts toward a place, object, state, etc.; to reach.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attain', 'v. i.', 'To come or arrive, by an effort of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attain', 'n.', 'Attainment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attainability', 'n.', 'The quality of being attainable; attainableness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attainable', 'a.', 'Capable of being attained or reached by efforts of the\n   mind or body; capable of being compassed or accomplished by efforts\n   directed to the object.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attainable', 'a.', 'Obtainable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attainableness', 'n.', 'The quality of being attainable; attainability.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attainder', 'n.', 'The act of attainting, or the state of being attainted;\n   the extinction of the civil rights and capacities of a person,\n   consequent upon sentence of death or outlawry; as, an act of attainder.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attainder', 'n.', 'A stain or staining; state of being in dishonor or\n   condemnation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attainment', 'n.', 'The act of attaining; the act of arriving at or\n   reaching; hence, the act of obtaining by efforts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attainment', 'n.', 'That which is attained to, or obtained by exertion;\n   acquirement; acquisition; (pl.); mental acquirements; knowledge; as,\n   literary and scientific attainments.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attainted', 'imp. & p. p.', 'of Attaint');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attainting', 'p. pr. & vb. n.', 'of Attaint');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attaint', 'v. t.', 'To attain; to get act; to hit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attaint', 'v. t.', 'To find guilty; to convict; -- said esp. of a jury on\n   trial for giving a false verdict.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attaint', 'v. t.', 'To subject (a person) to the legal condition formerly\n   resulting from a sentence of death or outlawry, pronounced in respect\n   of treason or felony; to affect by attainder.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attaint', 'v. t.', 'To accuse; to charge with a crime or a dishonorable\n   act.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attaint', 'v. t.', 'To affect or infect, as with physical or mental disease\n   or with moral contagion; to taint or corrupt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attaint', 'v. t.', 'To stain; to obscure; to sully; to disgrace; to cloud\n   with infamy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attaint', 'p. p.', 'Attainted; corrupted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attaint', 'v.', 'A touch or hit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attaint', 'v.', 'A blow or wound on the leg of a horse, made by\n   overreaching.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attaint', 'v.', 'A writ which lies after judgment, to inquire whether a\n   jury has given a false verdict in any court of record; also, the\n   convicting of the jury so tried.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attaint', 'v.', 'A stain or taint; disgrace. See Taint.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attaint', 'v.', 'An infecting influence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attaintment', 'n.', 'Attainder; attainture; conviction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attainture', 'n.', 'Attainder; disgrace.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attal', 'n.', 'Same as Attle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attame', 'v. t.', 'To pierce; to attack.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attame', 'v. t.', 'To broach; to begin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attaminate', 'v. t.', 'To corrupt; to defile; to contaminate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attar', 'n.', 'A fragrant essential oil; esp., a volatile and highly\n   fragrant essential oil obtained from the petals of roses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attask', 'v. t.', 'To take to task; to blame.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attaste', 'v. t.', 'To taste or cause to taste.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atte', '', 'At the.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attempered', 'imp. & p. p.', 'of Attemper');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attempering', 'p. pr. & vb. n.', 'of Attemper');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attemper', 'v. t.', 'To reduce, modify, or moderate, by mixture; to temper;\n   to regulate, as temperature.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attemper', 'v. t.', 'To soften, mollify, or moderate; to soothe; to temper;\n   as, to attemper rigid justice with clemency.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attemper', 'v. t.', 'To mix in just proportion; to regulate; as, a mind\n   well attempered with kindness and justice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attemper', 'v. t.', 'To accommodate; to make suitable; to adapt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attemperament', 'n.', 'A tempering, or mixing in due proportion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attemperance', 'n.', 'Temperance; attemperament.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attemperate', 'a.', 'Tempered; proportioned; properly adapted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attemperate', 'v. t.', 'To attemper.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attemperation', 'n.', 'The act of attempering or regulating.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attemperly', 'adv.', 'Temperately.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attemperment', 'n.', 'Attemperament.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attempted', 'imp. & p. p.', 'of Attempt');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attempting', 'p. pr. & vb. n.', 'of Attempt');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attempt', 'v. t.', 'To make trial or experiment of; to try; to endeavor to\n   do or perform (some action); to assay; as, to attempt to sing; to\n   attempt a bold flight.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attempt', 'v. t.', 'To try to move, by entreaty, by afflictions, or by\n   temptations; to tempt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attempt', 'v. t.', 'To try to win, subdue, or overcome; as, one who\n   attempts the virtue of a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attempt', 'v. t.', 'To attack; to make an effort or attack upon; to try to\n   take by force; as, to attempt the enemys camp.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attempt', 'v. i.', 'To make an attempt; -- with upon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attempt', 'n.', 'A essay, trial, or endeavor; an undertaking; an attack, or\n   an effort to gain a point; esp. an unsuccessful, as contrasted with a\n   successful, effort.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attemptable', 'a.', 'Capable of being attempted, tried, or attacked.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attempter', 'n.', 'One who attempts; one who essays anything.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attempter', 'n.', 'An assailant; also, a temper.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attemptive', 'a.', 'Disposed to attempt; adventurous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attended', 'imp. & p. p.', 'of Attend');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attending', 'p. pr. & vb. n.', 'of Attend');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attend', 'v. t.', 'To direct the attention to; to fix the mind upon; to\n   give heed to; to regard.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attend', 'v. t.', 'To care for; to look after; to take charge of; to watch\n   over.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attend', 'v. t.', 'To go or stay with, as a companion, nurse, or servant;\n   to visit professionally, as a physician; to accompany or follow in\n   order to do service; to escort; to wait on; to serve.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attend', 'v. t.', 'To be present with; to accompany; to be united or\n   consequent to; as, a measure attended with ill effects.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attend', 'v. t.', 'To be present at; as, to attend church, school, a\n   concert, a business meeting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attend', 'v. t.', 'To wait for; to await; to remain, abide, or be in store\n   for.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attend', 'v. i.', 'To apply the mind, or pay attention, with a view to\n   perceive, understand, or comply; to pay regard; to heed; to listen; --\n   usually followed by to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attend', 'v. i.', 'To accompany or be present or near at hand, in pursuance\n   of duty; to be ready for service; to wait or be in waiting; -- often\n   followed by on or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attend', 'v. i.', '(with to) To take charge of; to look after; as, to\n   attend to a matter of business.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attend', 'v. i.', 'To wait; to stay; to delay.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attendance', 'v. t.', 'Attention; regard; careful application.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attendance', 'v. t.', 'The act of attending; state of being in waiting;\n   service; ministry; the fact of being present; presence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attendance', 'v. t.', 'Waiting for; expectation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attendance', 'v. t.', 'The persons attending; a retinue; attendants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attendancy', 'n.', 'The quality of attending or accompanying; attendance;\n   an attendant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attendant', 'v. t.', 'Being present, or in the train; accompanying; in\n   waiting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attendant', 'v. t.', 'Accompanying, connected with, or immediately\n   following, as consequential; consequent; as, intemperance with all its\n   attendant evils.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attendant', 'v. t.', 'Depending on, or owing duty or service to; as, the\n   widow attendant to the heir.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attendant', 'n.', 'One who attends or accompanies in any character\n   whatever, as a friend, companion, servant, agent, or suitor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attendant', 'n.', 'One who is present and takes part in the proceedings;\n   as, an attendant at a meeting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attendant', 'n.', 'That which accompanies; a concomitant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attendant', 'n.', 'One who owes duty or service to, or depends on, another.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attendement', 'n.', 'Intent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attender', 'n.', 'One who, or that which, attends.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attendment', 'n.', 'An attendant circumstance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attent', 'v. t.', 'Attentive; heedful.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attent', 'n.', 'Attention; heed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attentate', 'n.', 'Alt. of Attentat');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attentat', 'n.', 'An attempt; an assault.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attentat', 'n.', 'A proceeding in a court of judicature, after an\n   inhibition is decreed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attentat', 'n.', 'Any step wrongly innovated or attempted in a suit by an\n   inferior judge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attention', 'n.', 'The act or state of attending or heeding; the\n   application of the mind to any object of sense, representation, or\n   thought; notice; exclusive or special consideration; earnest\n   consideration, thought, or regard; obedient or affectionate heed; the\n   supposed power or faculty of attending.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attention', 'n.', 'An act of civility or courtesy; care for the comfort and\n   pleasure of others; as, attentions paid to a stranger.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attentive', 'a.', 'Heedful; intent; observant; regarding with care or\n   attention.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attentive', 'a.', 'Heedful of the comfort of others; courteous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attently', 'adv.', 'Attentively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attenuant', 'a.', 'Making thin, as fluids; diluting; rendering less dense\n   and viscid; diluent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attenuant', 'n.', 'A medicine that thins or dilutes the fluids; a diluent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attenuated', 'imp. & p. p.', 'of Attenuate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attenuating', 'p. pr. & vb. n.', 'of Attenuate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attenuate', 'v. t.', 'To make thin or slender, as by mechanical or chemical\n   action upon inanimate objects, or by the effects of starvation,\n   disease, etc., upon living bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attenuate', 'v. t.', 'To make thin or less consistent; to render less\n   viscid or dense; to rarefy. Specifically: To subtilize, as the humors\n   of the body, or to break them into finer parts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attenuate', 'v. t.', 'To lessen the amount, force, or value of; to make\n   less complex; to weaken.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attenuate', 'v. i.', 'To become thin, slender, or fine; to grow less; to\n   lessen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attenuate', 'a.', 'Alt. of Attenuated');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attenuated', 'a.', 'Made thin or slender.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attenuated', 'a.', 'Made thin or less viscid; rarefied.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attenuation', 'n.', 'The act or process of making slender, or the state of\n   being slender; emaciation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attenuation', 'n.', 'The act of attenuating; the act of making thin or less\n   dense, or of rarefying, as fluids or gases.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attenuation', 'n.', 'The process of weakening in intensity; diminution of\n   virulence; as, the attenuation of virus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atter', 'n.', 'Poison; venom; corrupt matter from a sore.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attercop', 'n.', 'A spider.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attercop', 'n.', 'A peevish, ill-natured person.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atterrate', 'v. t.', 'To fill up with alluvial earth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atterration', 'n.', 'The act of filling up with earth, or of forming land\n   with alluvial earth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attested', 'imp. & p. p.', 'of Attest');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attesting', 'p. pr. & vb. n.', 'of Attest');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attest', 'v. t.', 'To bear witness to; to certify; to affirm to be true or\n   genuine; as, to attest the truth of a writing, a copy of record.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attest', 'v. t.', 'To give proof of; to manifest; as, the ruins of Palmyra\n   attest its ancient magnificence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attest', 'v. t.', 'To call to witness; to invoke.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attest', 'n.', 'Witness; testimony; attestation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attestation', 'n.', 'The act of attesting; testimony; witness; a solemn or\n   official declaration, verbal or written, in support of a fact;\n   evidence. The truth appears from the attestation of witnesses, or of\n   the proper officer. The subscription of a name to a writing as a\n   witness, is an attestation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attestative', 'a.', 'Of the nature of attestation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attester', 'n.', 'Alt. of Attestor');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attestor', 'n.', 'One who attests.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attestive', 'a.', 'Attesting; furnishing evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attic', 'a.', 'Of or pertaining to Attica, in Greece, or to Athens, its\n   principal city; marked by such qualities as were characteristic of the\n   Athenians; classical; refined.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attic', 'a.', 'A low story above the main order or orders of a facade, in\n   the classical styles; -- a term introduced in the 17th century. Hence:');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attic', 'a.', 'A room or rooms behind that part of the exterior; all the\n   rooms immediately below the roof.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attic', 'a.', 'An Athenian; an Athenian author.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attical', 'a.', 'Attic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atticism', 'n.', 'A favoring of, or attachment to, the Athenians.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atticism', 'n.', 'The style and idiom of the Greek language, used by the\n   Athenians; a concise and elegant expression.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atticize', 'v. t.', 'To conform or make conformable to the language,\n   customs, etc., of Attica.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atticize', 'v. i.', 'To side with the Athenians.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atticize', 'v. i.', 'To use the Attic idiom or style; to conform to the\n   customs or modes of thought of the Athenians.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attiguous', 'a.', 'Touching; bordering; contiguous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attinge', 'v. t.', 'To touch lightly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attired', 'imp. & p. p.', 'of Attire');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attiring', 'p. pr. & vb. n.', 'of Attire');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attire', 'v. t.', 'To dress; to array; to adorn; esp., to clothe with\n   elegant or splendid garments.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attire', 'n.', 'Dress; clothes; headdress; anything which dresses or\n   adorns; esp., ornamental clothing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attire', 'n.', 'The antlers, or antlers and scalp, of a stag or buck.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attire', 'n.', 'The internal parts of a flower, included within the calyx\n   and the corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attired', 'p. p.', 'Provided with antlers, as a stag.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attirement', 'n.', 'Attire; adornment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attirer', 'n.', 'One who attires.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attitude', 'n.', 'The posture, action, or disposition of a figure or a\n   statue.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attitude', 'n.', 'The posture or position of a person or an animal, or the\n   manner in which the parts of his body are disposed; position assumed or\n   studied to serve a purpose; as, a threatening attitude; an attitude of\n   entreaty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attitude', 'n.', 'Fig.: Position as indicating action, feeling, or mood;\n   as, in times of trouble let a nation preserve a firm attitude; ones\n   mental attitude in respect to religion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attitudinal', 'a.', 'Relating to attitude.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attitudinarian', 'n.', 'One who attitudinizes; a posture maker.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attitudinarianism', 'n.', 'A practicing of attitudes; posture making.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attitudinize', 'v. i.', 'To assume affected attitudes; to strike an\n   attitude; to pose.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attitudinizer', 'n.', 'One who practices attitudes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attle', 'n.', 'Rubbish or refuse consisting of broken rock containing\n   little or no ore.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attollent', 'a.', 'Lifting up; raising; as, an attollent muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attonce', 'adv.', 'At once; together.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attone', 'adv.', 'See At one.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attorn', 'v. t.', 'To turn, or transfer homage and service, from one lord\n   to another. This is the act of feudatories, vassals, or tenants, upon\n   the alienation of the estate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attorn', 'v. t.', 'To agree to become tenant to one to whom reversion has\n   been granted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attorneys', 'pl. ', 'of Attorney');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attorney', 'n.', 'A substitute; a proxy; an agent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attorney', 'n.', 'One who is legally appointed by another to transact any\n   business for him; an attorney in fact.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attorney', 'n.', 'A legal agent qualified to act for suitors and defendants\n   in legal proceedings; an attorney at law.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attorney', 'v. t.', 'To perform by proxy; to employ as a proxy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attorney-general', 'n.', 'The chief law officer of the state, empowered to\n   act in all litigation in which the law-executing power is a party, and\n   to advise this supreme executive whenever required.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attorneyism', 'n.', 'The practice or peculiar cleverness of attorneys.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attorneyship', 'n.', 'The office or profession of an attorney; agency for\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attornment', 'n.', 'The act of a feudatory, vassal, or tenant, by which he\n   consents, upon the alienation of an estate, to receive a new lord or\n   superior, and transfers to him his homage and service; the agreement of\n   a tenant to acknowledge the purchaser of the estate as his landlord.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attracted', 'imp. & p. p.', 'of Attract');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attracting', 'p. pr. & vb. n.', 'of Attract');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attract', 'v. t.', 'To draw to, or cause to tend to; esp. to cause to\n   approach, adhere, or combine; or to cause to resist divulsion,\n   separation, or decomposition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attract', 'v. t.', 'To draw by influence of a moral or emotional kind; to\n   engage or fix, as the mind, attention, etc.; to invite or allure; as,\n   to attract admirers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attract', 'n.', 'Attraction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attractability', 'n.', 'The quality or fact of being attractable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attractable', 'a.', 'Capable of being attracted; subject to attraction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attracter', 'n.', 'One who, or that which, attracts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attractile', 'a.', 'Having power to attract.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attracting', 'a.', 'That attracts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attraction', 'n.', 'An invisible power in a body by which it draws anything\n   to itself; the power in nature acting mutually between bodies or\n   ultimate particles, tending to draw them together, or to produce their\n   cohesion or combination, and conversely resisting separation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attraction', 'n.', 'The act or property of attracting; the effect of the\n   power or operation of attraction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attraction', 'n.', 'The power or act of alluring, drawing to, inviting, or\n   engaging; an attractive quality; as, the attraction of beauty or\n   eloquence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attraction', 'n.', 'That which attracts; an attractive object or feature.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attractive', 'a.', 'Having the power or quality of attracting or drawing;\n   as, the attractive force of bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attractive', 'a.', 'Attracting or drawing by moral influence or pleasurable\n   emotion; alluring; inviting; pleasing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attractive', 'n.', 'That which attracts or draws; an attraction; an\n   allurement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attractivity', 'n.', 'The quality or degree of attractive power.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attractor', 'n.', 'One who, or that which, attracts.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attrahent', 'v. t.', 'Attracting; drawing; attractive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attrahent', 'n.', 'That which attracts, as a magnet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attrahent', 'n.', 'A substance which, by irritating the surface, excites\n   action in the part to which it is applied, as a blister, an epispastic,\n   a sinapism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attrap', 'v. t.', 'To entrap; to insnare.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attrap', 'v. t.', 'To adorn with trapping; to array.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attrectation', 'n.', 'Frequent handling or touching.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attributable', 'a.', 'Capable of being attributed; ascribable; imputable.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attributed', 'imp. & p. p.', 'of Attribute');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attributing', 'p. pr. & vb. n.', 'of Attribute');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attribute', 'v. t.', 'To ascribe; to consider (something) as due or\n   appropriate (to); to refer, as an effect to a cause; to impute; to\n   assign; to consider as belonging (to).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attribute', 'n.', 'That which is attributed; a quality which is considered\n   as belonging to, or inherent in, a person or thing; an essential or\n   necessary property or characteristic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attribute', 'n.', 'Reputation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attribute', 'n.', 'A conventional symbol of office, character, or identity,\n   added to any particular figure; as, a club is the attribute of\n   Hercules.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attribute', 'n.', 'Quality, etc., denoted by an attributive; an attributive\n   adjunct or adjective.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attribution', 'n.', 'The act of attributing or ascribing, as a quality,\n   character, or function, to a thing or person, an effect to a cause.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attribution', 'n.', 'That which is ascribed or attributed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attributive', 'a.', 'Attributing; pertaining to, expressing, or assigning\n   an attribute; of the nature of an attribute.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attributive', 'n.', 'A word that denotes an attribute; esp. a modifying\n   word joined to a noun; an adjective or adjective phrase.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attributively', 'adv.', 'In an attributive manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attrite', 'a.', 'Rubbed; worn by friction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attrite', 'a.', 'Repentant from fear of punishment; having attrition of\n   grief for sin; -- opposed to contrite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attrition', 'n.', 'The act of rubbing together; friction; the act of\n   wearing by friction, or by rubbing substances together; abrasion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attrition', 'n.', 'The state of being worn.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attrition', 'n.', 'Grief for sin arising only from fear of punishment or\n   feelings of shame. See Contrition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attry', 'a.', 'Poisonous; malignant; malicious.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attuned', 'imp. & p. p.', 'of Attune');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attuning', 'p. pr. & vb. n.', 'of Attune');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attune', 'v. t.', 'To tune or put in tune; to make melodious; to adjust, as\n   one sound or musical instrument to another; as, to attune the voice to\n   a harp.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Attune', 'v. t.', 'To arrange fitly; to make accordant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atwain', 'adv.', 'In twain; asunder.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atween', 'adv. or prep.', 'Between.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atwirl', 'a. & adv.', 'Twisted; distorted; awry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atwite', 'v. t.', 'To speak reproachfully of; to twit; to upbraid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atwixt', 'adv.', 'Betwixt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atwo', 'adv.', 'In two; in twain; asunder.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atypic', 'a.', 'Alt. of Atypical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Atypical', 'a.', 'That has no type; devoid of typical character; irregular;\n   unlike the type.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aubade', 'n.', 'An open air concert in the morning, as distinguished from\n   an evening serenade; also, a pianoforte composition suggestive of\n   morning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aubaine', 'n.', 'Succession to the goods of a stranger not naturalized.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aube', 'n.', 'An alb.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auberge', 'n.', 'An inn.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aubin', 'n.', 'A broken gait of a horse, between an amble and a gallop; --\n   commonly called a Canterbury gallop.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auburn', 'a.', 'Flaxen-colored.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auburn', 'a.', 'Reddish brown.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auchenium', 'n.', 'The part of the neck nearest the back.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auctary', 'n.', 'That which is superadded; augmentation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auction', 'n.', 'A public sale of property to the highest bidder, esp. by a\n   person licensed and authorized for the purpose; a vendue.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auction', 'n.', 'The things sold by auction or put up to auction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auction', 'v. t.', 'To sell by auction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auctionary', 'a.', 'Of or pertaining to an auction or an auctioneer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auctioneer', 'n.', 'A person who sells by auction; a person whose business\n   it is to dispose of goods or lands by public sale to the highest or\n   best bidder.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auctioneer', 'v. t.', 'To sell by auction; to auction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aucupation', 'n.', 'Birdcatching; fowling.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audacious', 'a.', 'Daring; spirited; adventurous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audacious', 'a.', 'Contemning the restraints of law, religion, or decorum;\n   bold in wickedness; presumptuous; impudent; insolent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audacious', 'a.', 'Committed with, or proceedings from, daring effrontery\n   or contempt of law, morality, or decorum.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audaciously', 'adv.', 'In an audacious manner; with excess of boldness;\n   impudently.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audaciousness', 'n.', 'The quality of being audacious; impudence; audacity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audacity', 'n.', 'Daring spirit, resolution, or confidence;\n   venturesomeness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audacity', 'n.', 'Reckless daring; presumptuous impudence; -- implying a\n   contempt of law or moral restraints.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audibility', 'n.', 'The quality of being audible; power of being heard;\n   audible capacity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audible', 'a.', 'Capable of being heard; loud enough to be heard; actually\n   heard; as, an audible voice or whisper.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audible', 'n.', 'That which may be heard.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audibleness', 'n.', 'The quality of being audible.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audibly', 'adv.', 'So as to be heard.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audience', 'a.', 'The act of hearing; attention to sounds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audience', 'a.', 'Admittance to a hearing; a formal interview, esp. with a\n   sovereign or the head of a government, for conference or the\n   transaction of business.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audience', 'a.', 'An auditory; an assembly of hearers. Also applied by\n   authors to their readers.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audient', 'a.', 'Listening; paying attention; as, audient souls.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audient', 'n.', 'A hearer; especially a catechumen in the early church.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audiometer', 'n.', 'An instrument by which the power of hearing can be\n   gauged and recorded on a scale.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audiphone', 'n.', 'An instrument which, placed against the teeth, conveys\n   sound to the auditory nerve and enables the deaf to hear more or less\n   distinctly; a dentiphone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audit', 'a.', 'An audience; a hearing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audit', 'a.', 'An examination in general; a judicial examination.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audit', 'a.', 'The result of such an examination, or an account as adjusted\n   by auditors; final account.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audit', 'a.', 'A general receptacle or receiver.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audited', 'imp. & p. p.', 'of Audit');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auditing', 'p. pr. & vb. n.', 'of Audit');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audit', 'v. t.', 'To examine and adjust, as an account or accounts; as, to\n   audit the accounts of a treasure, or of parties who have a suit\n   depending in court.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audit', 'v. i.', 'To settle or adjust an account.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audita querela', '', 'A writ which lies for a party against whom judgment\n   is recovered, but to whom good matter of discharge has subsequently\n   accrued which could not have been availed of to prevent such judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Audition', 'n.', 'The act of hearing or listening; hearing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auditive', 'a.', 'Of or pertaining to hearing; auditory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auditor', 'a.', 'A hearer or listener.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auditor', 'a.', 'A person appointed and authorized to audit or examine an\n   account or accounts, compare the charges with the vouchers, examine the\n   parties and witnesses, allow or reject charges, and state the balance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auditor', 'a.', 'One who hears judicially, as in an audience court.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auditorial', 'a.', 'Auditory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auditorium', 'n.', 'The part of a church, theater, or other public\n   building, assigned to the audience.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auditorship', 'n.', 'The office or function of auditor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auditory', 'a.', 'Of or pertaining to hearing, or to the sense or organs of\n   hearing; as, the auditory nerve. See Ear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auditory', 'n.', 'An assembly of hearers; an audience.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auditory', 'n.', 'An auditorium.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auditress', 'n.', 'A female hearer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auditual', 'a.', 'Auditory.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auf', 'n.', 'A changeling or elf child, -- that is, one left by fairies; a\n   deformed or foolish child; a simpleton; an oaf.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Au fait', '', 'Expert; skillful; well instructed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augean', 'a.', 'Of or pertaining to Augeus, king of Elis, whose stable\n   contained 3000 oxen, and had not been cleaned for 30 years. Hercules\n   cleansed it in a single day.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augean', 'a.', 'Hence: Exceedingly filthy or corrupt.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auger', 'n.', 'A carpenters tool for boring holes larger than those bored\n   by a gimlet. It has a handle placed crosswise by which it is turned\n   with both hands. A pod auger is one with a straight channel or groove,\n   like the half of a bean pod. A screw auger has a twisted blade, by the\n   spiral groove of which the chips are discharge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auger', 'n.', 'An instrument for boring or perforating soils or rocks, for\n   determining the quality of soils, or the nature of the rocks or strata\n   upon which they lie, and for obtaining water.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auget', 'n.', 'A priming tube connecting the charge chamber with the\n   gallery, or place where the slow match is applied.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aught', 'n.', 'Alt. of Aucht');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aucht', 'n.', 'Property; possession.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aught', 'n.', 'Anything; any part.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aught', 'adv.', 'At all; in any degree.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augite', 'n.', 'A variety of pyroxene, usually of a black or dark green\n   color, occurring in igneous rocks, such as basalt; -- also used instead\n   of the general term pyroxene.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augitic', 'a.', 'Pertaining to, or like, augite; containing augite as a\n   principal constituent; as, augitic rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augmented', 'imp. & p. p.', 'of Augment');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augmenting', 'p. pr. & vb. n.', 'of Augment');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augment', 'v. t.', 'To enlarge or increase in size, amount, or degree; to\n   swell; to make bigger; as, to augment an army by reeforcements; rain\n   augments a stream; impatience augments an evil.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augment', 'v. t.', 'To add an augment to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augment', 'v. i.', 'To increase; to grow larger, stronger, or more intense;\n   as, a stream augments by rain.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augment', 'n.', 'Enlargement by addition; increase.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augment', 'n.', 'A vowel prefixed, or a lengthening of the initial vowel,\n   to mark past time, as in Greek and Sanskrit verbs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augmentable', 'a.', 'Capable of augmentation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augmentation', 'n.', 'The act or process of augmenting, or making larger,\n   by addition, expansion, or dilation; increase.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augmentation', 'n.', 'The state of being augmented; enlargement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augmentation', 'n.', 'The thing added by way of enlargement.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augmentation', 'n.', 'A additional charge to a coat of arms, given as a\n   mark of honor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augmentation', 'n.', 'The stage of a disease in which the symptoms go on\n   increasing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augmentation', 'n.', 'In counterpoint and fugue, a repetition of the\n   subject in tones of twice the original length.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augmentative', 'a.', 'Having the quality or power of augmenting; expressing\n   augmentation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augmentative', 'n.', 'A word which expresses with augmented force the idea\n   or the properties of the term from which it is derived; as, dullard,\n   one very dull. Opposed to diminutive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augmenter', 'n.', 'One who, or that which, augments or increases anything.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augrim', 'n.', 'See Algorism.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augur', 'n.', 'An official diviner who foretold events by the singing,\n   chattering, flight, and feeding of birds, or by signs or omens derived\n   from celestial phenomena, certain appearances of quadrupeds, or unusual\n   occurrences.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augur', 'n.', 'One who foretells events by omens; a soothsayer; a diviner;\n   a prophet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augured', 'imp. & p. p.', 'of Augur');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auguring', 'p. pr. & vb. n.', 'of Augur');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augur', 'v. i.', 'To conjecture from signs or omens; to prognosticate; to\n   foreshow.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augur', 'v. i.', 'To anticipate, to foretell, or to indicate a favorable or\n   an unfavorable issue; as, to augur well or ill.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augur', 'v. t.', 'To predict or foretell, as from signs or omens; to\n   betoken; to presage; to infer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augural', 'a.', 'Of or pertaining to augurs or to augury; betokening;\n   ominous; significant; as, an augural staff; augural books.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augurate', 'v. t. & i.', 'To make or take auguries; to augur; to predict.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augurate', 'n.', 'The office of an augur.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auguration', 'n.', 'The practice of augury.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augurer', 'n.', 'An augur.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augurial', 'a.', 'Relating to augurs or to augury.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augurist', 'n.', 'An augur.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Angurize', 'v. t.', 'To augur.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augurous', 'a.', 'Full of augury; foreboding.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augurship', 'n.', 'The office, or period of office, of an augur.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auguries', 'pl. ', 'of Augury');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augury', 'n.', 'The art or practice of foretelling events by observing the\n   actions of birds, etc.; divination.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augury', 'n.', 'An omen; prediction; prognostication; indication of the\n   future; presage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augury', 'n.', 'A rite, ceremony, or observation of an augur.');
  INSERT INTO words (word, word_type, description) VALUES  (  'August', 'a.', 'Of a quality inspiring mingled admiration and reverence;\n   having an aspect of solemn dignity or grandeur; sublime; majestic;\n   having exalted birth, character, state, or authority.');
  INSERT INTO words (word, word_type, description) VALUES  (  'August', 'a.', 'The eighth month of the year, containing thirty-one days.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augustan', 'n.', 'Of or pertaining to Augustus Caesar or to his times.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augustan', 'n.', 'Of or pertaining to the town of Augsburg.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augustine', 'n.', 'Alt. of Augustinian');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augustinian', 'n.', 'A member of one of the religious orders called after\n   St. Augustine; an Austin friar.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augustinian', 'a.', 'Of or pertaining to St. Augustine, bishop of Hippo in\n   Northern Africa (b. 354 -- d. 430); or to his doctrines.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augustinian', 'n.', 'One of a class of divines, who, following St.\n   Augustine, maintain that grace by its nature is effectual absolutely\n   and creatively, not relatively and conditionally.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augustinianism', 'n.', 'Alt. of Augustinism');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augustinism', 'n.', 'The doctrines held by Augustine or by the\n   Augustinians.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augustly', 'adv.', 'In an august manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Augustness', 'n.', 'The quality of being august; dignity of mien; grandeur;\n   magnificence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auk', 'n.', 'A name given to various species of arctic sea birds of the\n   family Alcidae. The great auk, now extinct, is Alca (/ Plautus)\n   impennis. The razor-billed auk is A. torda. See Puffin, Guillemot, and\n   Murre.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aukward', 'a.', 'See Awkward.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aularian', 'a.', 'Relating to a hall.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aularian', 'n.', 'At Oxford, England, a member of a hall, distinguished\n   from a collegian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auld', 'a.', 'Old; as, Auld Reekie (old smoky); i. e., Edinburgh.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auld lang syne', '', 'A Scottish phrase used in recalling recollections of\n   times long since past.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auletic', 'a.', 'Of or pertaining to a pipe (flute) or piper.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aulic', 'a.', 'Pertaining to a royal court.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aulic', 'n.', 'The ceremony observed in conferring the degree of doctor of\n   divinity in some European universities. It begins by a harangue of the\n   chancellor addressed to the young doctor, who then receives the cap,\n   and presides at the disputation (also called the aulic).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auln', 'n.', 'An ell. [Obs.] See Aune.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aulnage', 'n.', 'Alt. of Aulnager');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aulnager', 'n.', 'See Alnage and Alnager.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aum', 'n.', 'Same as Aam.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aumail', 'v. t.', 'To figure or variegate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aumbry', 'n.', 'Same as Ambry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aumery', 'n.', 'A form of Ambry, a closet; but confused with Almonry, as if\n   a place for alms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auncel', 'n.', 'A rude balance for weighing, and a kind of weight, formerly\n   used in England.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auncetry', 'n.', 'Ancestry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aune', 'n.', 'A French cloth measure, of different parts of the country (at\n   Paris, 0.95 of an English ell); -- now superseded by the meter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aunt', 'n.', 'The sister of ones father or mother; -- correlative to\n   nephew or niece. Also applied to an uncles wife.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aunt', 'n.', 'An old woman; and old gossip.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aunt', 'n.', 'A bawd, or a prostitute.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auntter', 'n.', 'Adventure; hap.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aunter', 'v. t.', 'Alt. of Auntre');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auntre', 'v. t.', 'To venture; to dare.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auntie', 'n.', 'Alt. of Aunty');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aunty', 'n.', 'A familiar name for an aunt. In the southern United States a\n   familiar term applied to aged negro women.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auntrous', 'a.', 'Adventurous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurae', 'pl. ', 'of Aura');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aura', 'n.', 'Any subtile, invisible emanation, effluvium, or exhalation\n   from a substance, as the aroma of flowers, the odor of the blood, a\n   supposed fertilizing emanation from the pollen of flowers, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aura', 'n.', 'The peculiar sensation, as of a light vapor, or cold air,\n   rising from the trunk or limbs towards the head, a premonitory symptom\n   of epilepsy or hysterics.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aural', 'a.', 'Of or pertaining to the air, or to an aura.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aural', 'a.', 'Of or pertaining to the ear; as, aural medicine and surgery.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurantiaceous', 'a.', 'Pertaining to, or resembling, the Aurantiaceae, an\n   order of plants (formerly considered natural); of which the orange is\n   the type.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurate', 'n.', 'A combination of auric acid with a base; as, aurate or\n   potassium.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurated', 'a.', 'Resembling or containing gold; gold-colored; gilded.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurated', 'a.', 'Combined with auric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurated', 'a.', 'Having ears. See Aurited.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aureate', 'a.', 'Golden; gilded.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurelia', 'n.', 'The chrysalis, or pupa of an insect, esp. when reflecting\n   a brilliant golden color, as that of some of the butterflies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurelia', 'n.', 'A genus of jellyfishes. See Discophora.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurelian', 'a.', 'Of or pertaining to the aurelia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurelian', 'n.', 'An amateur collector and breeder of insects, esp. of\n   butterflies and moths; a lepidopterist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aureola', 'n.', 'Alt. of Aureole');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aureole', 'n.', 'A celestial crown or accidental glory added to the bliss\n   of heaven, as a reward to those (as virgins, martyrs, preachers, etc.)\n   who have overcome the world, the flesh, and the devil.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aureole', 'n.', 'The circle of rays, or halo of light, with which painters\n   surround the figure and represent the glory of Christ, saints, and\n   others held in special reverence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aureole', 'n.', 'A halo, actual or figurative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aureole', 'n.', 'See Areola, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auric', 'a.', 'Of or pertaining to gold.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auric', 'a.', 'Pertaining to, or derived from, gold; -- said of those\n   compounds of gold in which this element has its higher valence; as,\n   auric oxide; auric chloride.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurichalceous', 'a.', 'Brass-colored.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurichalcite', 'n.', 'A hydrous carbonate of copper and zinc, found in pale\n   green or blue crystalline aggregations. It yields a kind of brass on\n   reduction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auricle', 'n.', 'The external ear, or that part of the ear which is\n   prominent from the head.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auricle', 'n.', 'The chamber, or one of the two chambers, of the heart, by\n   which the blood is received and transmitted to the ventricle or\n   ventricles; -- so called from its resemblance to the auricle or\n   external ear of some quadrupeds. See Heart.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auricle', 'n.', 'An angular or ear-shaped lobe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auricle', 'n.', 'An instrument applied to the ears to give aid in hearing;\n   a kind of ear trumpet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auricled', 'a.', 'Having ear-shaped appendages or lobes; auriculate; as,\n   auricled leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auriculae', 'pl. ', 'of Auricula');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auriculas', 'pl. ', 'of Auricula');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auricula', 'n.', 'A species of Primula, or primrose, called also, from the\n   shape of its leaves, bears-ear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auricula', 'n.', 'A species of Hirneola (H. auricula); a membranaceous\n   fungus, called also auricula Judae, or Jews-ear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auricula', 'n.', 'A genus of air-breathing mollusks mostly found near the\n   sea, where the water is brackish');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auricula', 'n.', 'One of the five arched processes of the shell around the\n   jaws of a sea urchin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auricular', 'a.', 'Of or pertaining to the ear, or to the sense of hearing;\n   as, auricular nerves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auricular', 'a.', 'Told in the ear, i. e., told privately; as, auricular\n   confession to the priest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auricular', 'a.', 'Recognized by the ear; known by the sense of hearing;\n   as, auricular evidence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auricular', 'a.', 'Received by the ear; known by report.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auricular', 'a.', 'Pertaining to the auricles of the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auricularia', 'n. pl.', 'A kind of holothurian larva, with soft, blunt\n   appendages. See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auricularly', 'adv.', 'In an auricular manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auriculars', 'n. pl.', 'A circle of feathers surrounding the opening of the\n   ear of birds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auriculate', 'a.', 'Alt. of Auriculated');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auriculated', 'a.', 'Having ears or appendages like ears; eared. Esp.: (a)\n   (Bot.) Having lobes or appendages like the ear; shaped like the ear;\n   auricled. (b) (Zool.) Having an angular projection on one or both\n   sides, as in certain bivalve shells, the foot of some gastropods, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auriferous', 'a.', 'Gold-bearing; containing or producing gold.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auriflamme', 'n.', 'See Oriflamme.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auriform', 'a.', 'Having the form of the human ear; ear-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auriga', 'n.', 'The Charioteer, or Wagoner, a constellation in the northern\n   hemisphere, situated between Perseus and Gemini. It contains the bright\n   star Capella.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurigal', 'a.', 'Of or pertaining to a chariot.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurigation', 'n.', 'The act of driving a chariot or a carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurigraphy', 'n.', 'The art of writing with or in gold.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurin', 'n.', 'A red coloring matter derived from phenol; -- called also,\n   in commerce, yellow corallin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auriphrygiate', 'a.', 'Embroidered or decorated with gold.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auripigment', 'n.', 'See Orpiment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auriscalp', 'n.', 'An earpick.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auriscope', 'n.', 'An instrument for examining the condition of the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auriscopy', 'n.', 'Examination of the ear by the aid of the auriscope.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurist', 'n.', 'One skilled in treating and curing disorders of the ear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurited', 'a.', 'Having lobes like the ear; auriculate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurivorous', 'a.', 'Gold-devouring.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurocephalous', 'a.', 'Having a gold-colored head.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurochloride', 'n.', 'The trichloride of gold combination with the chloride\n   of another metal, forming a double chloride; -- called also\n   chloraurate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurochs', 'n.', 'The European bison (Bison bonasus, / Europaeus); once\n   widely distributed, but now nearly extinct, except where protected in\n   the Lithuanian forests, and perhaps in the Caucasus. It is distinct\n   from the Urus of Caesar, with which it has often been confused.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurocyanide', 'n.', 'A double cyanide of gold and some other metal or\n   radical; -- called also cyanaurate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auroras', 'pl. ', 'of Aurora');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurorae', 'pl. ', 'of Aurora');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurora', 'n.', 'The rising light of the morning; the dawn of day; the\n   redness of the sky just before the sun rises.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurora', 'n.', 'The rise, dawn, or beginning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurora', 'n.', 'The Roman personification of the dawn of day; the goddess\n   of the morning. The poets represented her a rising out of the ocean, in\n   a chariot, with rosy fingers dropping gentle dew.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurora', 'n.', 'A species of crowfoot.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurora', 'n.', 'The aurora borealis or aurora australis (northern or\n   southern lights).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auroral', 'a.', 'Belonging to, or resembling, the aurora (the dawn or the\n   northern lights); rosy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurous', 'a.', 'Containing gold.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurous', 'a.', 'Pertaining to, or derived from, gold; -- said of those\n   compounds of gold in which this element has its lower valence; as,\n   aurous oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aurum', 'n.', 'Gold.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auscult', 'v. i. & t.', 'To auscultate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auscultate', 'v. i. & t.', 'To practice auscultation; to examine by\n   auscultation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auscultation', 'n.', 'The act of listening or hearkening to.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auscultation', 'n.', 'An examination by listening either directly with the\n   ear (immediate auscultation) applied to parts of the body, as the\n   abdomen; or with the stethoscope (mediate auscultation); in order to\n   distinguish sounds recognized as a sign of health or of disease.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auscultator', 'n.', 'One who practices auscultation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auscultatory', 'a.', 'Of or pertaining to auscultation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ausonian', 'a.', 'Italian.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auspicate', 'a.', 'Auspicious.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auspicate', 'v. t.', 'To foreshow; to foretoken.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auspicate', 'v. t.', 'To give a favorable turn to in commencing; to\n   inaugurate; -- a sense derived from the Roman practice of taking the\n   auspicium, or inspection of birds, before undertaking any important\n   business.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auspices', 'pl. ', 'of Auspice');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auspice', 'a.', 'A divining or taking of omens by observing birds; an omen\n   as to an undertaking, drawn from birds; an augury; an omen or sign in\n   general; an indication as to the future.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auspice', 'a.', 'Protection; patronage and care; guidance.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auspicial', 'a.', 'Of or pertaining to auspices; auspicious.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auspicious', 'a.', 'Having omens or tokens of a favorable issue; giving\n   promise of success, prosperity, or happiness; predicting good; as, an\n   auspicious beginning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auspicious', 'a.', 'Prosperous; fortunate; as, auspicious years.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auspicious', 'a.', 'Favoring; favorable; propitious; -- applied to persons\n   or things.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auster', 'n.', 'The south wind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Austere', '', 'Sour and astringent; rough to the state; having acerbity;\n   as, an austere crab apple; austere wine.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Austere', '', 'Severe in modes of judging, or living, or acting; rigid;\n   rigorous; stern; as, an austere man, look, life.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Austere', '', 'Unadorned; unembellished; severely simple.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Austerely', 'adv.', 'Severely; rigidly; sternly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Austereness', 'n.', 'Harshness or astringent sourness to the taste;\n   acerbity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Austereness', 'n.', 'Severity; strictness; austerity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Austerities', 'pl. ', 'of Austerity');
  INSERT INTO words (word, word_type, description) VALUES  (  'Austerity', 'n.', 'Sourness and harshness to the taste.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Austerity', 'n.', 'Severity of manners or life; extreme rigor or\n   strictness; harsh discipline.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Austerity', 'n.', 'Plainness; freedom from adornment; severe simplicity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Austin', 'a.', 'Augustinian; as, Austin friars.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Austral', 'a.', 'Southern; lying or being in the south; as, austral land;\n   austral ocean.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Australasian', 'a.', 'Of or pertaining to Australasia; as, Australasian\n   regions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Australasian', 'n.', 'A native or an inhabitant of Australasia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Australian', 'a.', 'Of or pertaining to Australia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Australian', 'n.', 'A native or an inhabitant of Australia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Australize', 'v. i.', 'To tend toward the south pole, as a magnet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Austrian', 'a.', 'Of or pertaining to Austria, or to its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Austrian', 'n.', 'A native or an inhabitant of Austria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Austrine', 'n.', 'Southern; southerly; austral.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Austro-Hungarian', 'a.', 'Of or pertaining to the monarchy composed of\n   Austria and Hungary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Austromancy', 'n.', 'Soothsaying, or prediction of events, from observation\n   of the winds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autarchy', 'n.', 'Self-sufficiency.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authentic', 'n.', 'Having a genuine original or authority, in opposition to\n   that which is false, fictitious, counterfeit, or apocryphal; being what\n   it purports to be; genuine; not of doubtful origin; real; as, an\n   authentic paper or register.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authentic', 'n.', 'Authoritative.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authentic', 'n.', 'Of approved authority; true; trustworthy; credible; as,\n   an authentic writer; an authentic portrait; authentic information.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authentic', 'n.', 'Vested with all due formalities, and legally attested.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authentic', 'n.', 'Having as immediate relation to the tonic, in\n   distinction from plagal, which has a correspondent relation to the\n   dominant in the octave below the tonic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authentic', 'n.', 'An original (book or document).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authentical', 'a.', 'Authentic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authentically', 'adv.', 'In an authentic manner; with the requisite or\n   genuine authority.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authenticalness', 'n.', 'The quality of being authentic; authenticity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authenticated', 'imp. & p. p.', 'of Authenticate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authenticating', 'p. pr. & vb. n.', 'of Authenticate');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authenticate', 'v. t.', 'To render authentic; to give authority to, by the\n   proof, attestation, or formalities required by law, or sufficient to\n   entitle to credit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authenticate', 'v. t.', 'To prove authentic; to determine as real and true;\n   as, to authenticate a portrait.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authenticity', 'n.', 'The quality of being authentic or of established\n   authority for truth and correctness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authenticity', 'n.', 'Genuineness; the quality of being genuine or not\n   corrupted from the original.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authenticly', 'adv.', 'Authentically.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authenticness', 'n.', 'The quality of being authentic; authenticity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authentics', 'n.', 'A collection of the Novels or New Constitutions of\n   Justinian, by an anonymous author; -- so called on account of its\n   authenticity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Author', 'n.', 'The beginner, former, or first mover of anything; hence,\n   the efficient cause of a thing; a creator; an originator.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Author', 'n.', 'One who composes or writes a book; a composer, as\n   distinguished from an editor, translator, or compiler.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Author', 'n.', 'The editor of a periodical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Author', 'n.', 'An informant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Author', 'v. t.', 'To occasion; to originate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Author', 'v. t.', 'To tell; to say; to declare.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authoress', 'n.', 'A female author.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authorial', 'a.', 'Of or pertaining to an author.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authorism', 'n.', 'Authorship.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authoritative', 'a.', 'Having, or proceeding from, due authority; entitled\n   to obedience, credit, or acceptance; determinate; commanding.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authoritative', 'a.', 'Having an air of authority; positive; dictatorial;\n   peremptory; as, an authoritative tone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authorities', 'pl. ', 'of Authority');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authority', 'n.', 'Legal or rightful power; a right to command or to act;\n   power exercised buy a person in virtue of his office or trust;\n   dominion; jurisdiction; authorization; as, the authority of a prince\n   over subjects, and of parents over children; the authority of a court.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authority', 'n.', 'Government; the persons or the body exercising power or\n   command; as, the local authorities of the States; the military\n   authorities.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authority', 'n.', 'The power derived from opinion, respect, or esteem;\n   influence of character, office, or station, or mental or moral\n   superiority, and the like; claim to be believed or obeyed; as, an\n   historian of no authority; a magistrate of great authority.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authority', 'n.', 'That which, or one who, is claimed or appealed to in\n   support of opinions, actions, measures, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authority', 'n.', 'Testimony; witness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authority', 'n.', 'A precedent; a decision of a court, an official\n   declaration, or an opinion, saying, or statement worthy to be taken as\n   a precedent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authority', 'n.', 'A book containing such a statement or opinion, or the\n   author of the book.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authority', 'n.', 'Justification; warrant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authorizable', 'a.', 'Capable of being authorized.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authorization', 'n.', 'The act of giving authority or legal power;\n   establishment by authority; sanction or warrant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authorized', 'imp. & p. p.', 'of Authorize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authorizing', 'p. pr. & vb. n.', 'of Authorize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authorize', 'v. t.', 'To clothe with authority, warrant, or legal power; to\n   give a right to act; to empower; as, to authorize commissioners to\n   settle a boundary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authorize', 'v. t.', 'To make legal; to give legal sanction to; to\n   legalize; as, to authorize a marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authorize', 'v. t.', 'To establish by authority, as by usage or public\n   opinion; to sanction; as, idioms authorized by usage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authorize', 'v. t.', 'To sanction or confirm by the authority of some one;\n   to warrant; as, to authorize a report.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authorize', 'v. t.', 'To justify; to furnish a ground for.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authorized', 'a.', 'Possessed of or endowed with authority; as, an\n   authorized agent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authorized', 'a.', 'Sanctioned by authority.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authorizer', 'n.', 'One who authorizes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authorless', 'a.', 'Without an author; without authority; anonymous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authorly', 'a.', 'Authorial.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authorship', 'n.', 'The quality or state of being an author; function or\n   dignity of an author.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authorship', 'n.', 'Source; origin; origination; as, the authorship of a\n   book or review, or of an act, or state of affairs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authotype', 'n.', 'A type or block containing a facsimile of an autograph.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auto-', '', 'A combining form, with the meaning of self, ones self, ones\n   own, itself, its own.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autobiographer', 'n.', 'One who writers his own life or biography.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autobiographic', 'a.', 'Alt. of Autobiographical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autobiographical', 'a.', 'Pertaining to, or containing, autobiography; as,\n   an autobiographical sketch.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autobiographist', 'n.', 'One who writes his own life; an autobiographer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autobiographies', 'pl. ', 'of Autobiography');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autobiography', 'n.', 'A biography written by the subject of it; memoirs of\n   ones life written by ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autocarpous', 'a.', 'Alt. of Autocarpian');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autocarpian', 'a.', 'Consisting of the ripened pericarp with no other parts\n   adnate to it, as a peach, a poppy capsule, or a grape.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autocephalous', 'a.', 'Having its own head; independent of episcopal or\n   patriarchal jurisdiction, as certain Greek churches.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autochronograph', 'n.', 'An instrument for the instantaneous self-recording\n   or printing of time.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authochthons', 'pl. ', 'of Autochthon');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autochthones', 'pl. ', 'of Autochthon');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autochthon', 'n.', 'One who is supposed to rise or spring from the ground\n   or the soil he inhabits; one of the original inhabitants or aborigines;\n   a native; -- commonly in the plural. This title was assumed by the\n   ancient Greeks, particularly the Athenians.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autochthon', 'n.', 'That which is original to a particular country, or\n   which had there its origin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autochthonal', 'a.', 'Alt. of Autochthonous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Authochthonic', 'a.', 'Alt. of Autochthonous');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autochthonous', 'a.', 'Aboriginal; indigenous; native.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autochthonism', 'n.', 'The state of being autochthonal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autochthony', 'n.', 'An aboriginal or autochthonous condition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autoclave', 'n.', 'A kind of French stewpan with a steam-tight lid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autocracies', 'pl. ', 'of Autocracy');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autocracy', 'n.', 'Independent or self-derived power; absolute or\n   controlling authority; supremacy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autocracy', 'n.', 'Supreme, uncontrolled, unlimited authority, or right of\n   governing in a single person, as of an autocrat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autocracy', 'n.', 'Political independence or absolute sovereignty (of a\n   state); autonomy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autocracy', 'n.', 'The action of the vital principle, or of the instinctive\n   powers, toward the preservation of the individual; also, the vital\n   principle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autocrat', 'a.', 'An absolute sovereign; a monarch who holds and exercises\n   the powers of government by claim of absolute right, not subject to\n   restriction; as, Autocrat of all the Russias (a title of the Czar).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autocrat', 'a.', 'One who rules with undisputed sway in any company or\n   relation; a despot.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autocratic', 'a.', 'Alt. of Autocratical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autocratical', 'a.', 'Of or pertaining to autocracy or to an autocrat;\n   absolute; holding independent and arbitrary powers of government.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autocrator', 'n.', 'An autocrat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autocratorical', 'a.', 'Pertaining to an autocrator; absolute.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autocratrix', 'n.', 'A female sovereign who is independent and absolute; --\n   a title given to the empresses of Russia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autocratship', 'n.', 'The office or dignity of an autocrat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autos-da-fe', 'pl. ', 'of Auto-da-fe');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auto-da-fe', 'n.', 'A judgment of the Inquisition in Spain and Portugal\n   condemning or acquitting persons accused of religious offenses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auto-da-fe', 'n.', 'An execution of such sentence, by the civil power, esp.\n   the burning of a heretic. It was usually held on Sunday, and was made a\n   great public solemnity by impressive forms and ceremonies.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auto-da-fe', 'n.', 'A session of the court of Inquisition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autos-de-fe', 'pl. ', 'of Auto-de-fe');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auto-de-fe', 'n.', 'Same as Auto-da-fe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autodidact', 'n.', 'One who is self-taught; an automath.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autodynamic', 'a.', 'Supplying its own power; -- applied to an instrument\n   of the nature of a water-ram.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autofecundation', 'n.', 'Self-impregnation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autogamous', 'a.', 'Characterized by autogamy; self-fertilized.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autogamy', 'n.', 'Self-fertilization, the fertilizing pollen being derived\n   from the same blossom as the pistil acted upon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autogeneal', 'a.', 'Self-produced; autogenous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autogenesis', 'n.', 'Spontaneous generation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autogenetic', 'a.', 'Relating to autogenesis; self-generated.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autogenous', 'a.', 'Self-generated; produced independently.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autogenous', 'a.', 'Developed from an independent center of ossification.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autogenously', 'adv.', 'In an autogenous manner; spontaneously.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autograph', 'n.', 'That which is written with ones own hand; an original\n   manuscript; a persons own signature or handwriting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autograph', 'a.', 'In ones own handwriting; as, an autograph letter; an\n   autograph will.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autographal', 'a.', 'Autographic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autographic', 'a.', 'Alt. of Autographical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autographical', 'a.', 'Pertaining to an autograph, or ones own\n   handwriting; of the nature of an autograph.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autographical', 'a.', 'Pertaining to, or used in, the process of\n   autography; as, autographic ink, paper, or press.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autography', 'n.', 'The science of autographs; a persons own handwriting;\n   an autograph.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autography', 'n.', 'A process in lithography by which a writing or drawing\n   is transferred from paper to stone.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autolatry', 'n.', 'Self-worship.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Automath', 'n.', 'One who is self-taught.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Automatic', 'a.', 'Alt. of Automatical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Automatical', 'a.', 'Having an inherent power of action or motion.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Automatical', 'a.', 'Pertaining to, or produced by, an automaton; of the\n   nature of an automaton; self-acting or self-regulating under fixed\n   conditions; -- esp. applied to machinery or devices in which certain\n   things formerly or usually done by hand are done by the machine or\n   device itself; as, the automatic feed of a lathe; automatic gas\n   lighting; an automatic engine or switch; an automatic mouse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Automatical', 'a.', 'Not voluntary; not depending on the will; mechanical;\n   as, automatic movements or functions.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Automatically', 'adv.', 'In an automatic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Automatism', 'n.', 'The state or quality of being automatic; the power of\n   self-moving; automatic, mechanical, or involuntary action. (Metaph.) A\n   theory as to the activity of matter.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Automata', 'pl. ', 'of Automaton');
  INSERT INTO words (word, word_type, description) VALUES  (  'Automatons', 'pl. ', 'of Automaton');
  INSERT INTO words (word, word_type, description) VALUES  (  'Automaton', 'v. i.', 'Any thing or being regarded as having the power of\n   spontaneous motion or action.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Automaton', 'v. i.', 'A self-moving machine, or one which has its motive\n   power within itself; -- applied chiefly to machines which appear to\n   imitate spontaneously the motions of living beings, such as men, birds,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Automatous', 'a.', 'Automatic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Automorphic', 'a.', 'Patterned after ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Automorphism', 'n.', 'Automorphic characterization.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autonomasy', 'n.', 'The use of a word of common or general signification\n   for the name of a particular thing; as, \"He has gone to town,\" for, \"He\n   has gone to London.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autonomic', 'a.', 'Having the power of self-government; autonomous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autoomist', 'n.', 'One who advocates autonomy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autonomous', 'a.', 'Independent in government; having the right or power of\n   self-government.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autonomous', 'a.', 'Having independent existence or laws.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autonomy', 'n.', 'The power or right of self-government; self-government,\n   or political independence, of a city or a state.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autonomy', 'n.', 'The sovereignty of reason in the sphere of morals; or\n   mans power, as possessed of reason, to give law to himself. In this,\n   according to Kant, consist the true nature and only possible proof of\n   liberty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autophagi', 'n. pl.', 'Birds which are able to run about and obtain their\n   own food as soon as hatched.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autophoby', 'n.', 'Fear of ones self; fear of being egotistical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autophony', 'n.', 'An auscultatory process, which consists in noting the\n   tone of the observers own voice, while he speaks, holding his head\n   close to the patients chest.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autoplastic', 'a.', 'Of or pertaining to autoplasty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autoplasty', 'n.', 'The process of artificially repairing lesions by taking\n   a piece of healthy tissue, as from a neighboring part, to supply the\n   deficiency caused by disease or wounds.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autopsic', 'a.', 'Alt. of Autopsical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autopsical', 'a.', 'Pertaining to autopsy; autoptical.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autopsorin', 'n.', 'That which is given under the doctrine of administering\n   a patients own virus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autopsy', 'a.', 'Personal observation or examination; seeing with ones own\n   eyes; ocular view.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autopsy', 'a.', 'Dissection of a dead body, for the purpose of ascertaining\n   the cause, seat, or nature of a disease; a post-mortem examination.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autoptic', 'a.', 'Alt. of Autoptical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autoptical', 'a.', 'Seen with ones own eyes; belonging to, or connected\n   with, personal observation; as, autoptic testimony or experience.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autoptically', 'adv.', 'By means of ocular view, or ones own observation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autoschediastic', 'a.', 'Alt. of Autoschediastical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autoschediastical', 'a.', 'Extemporary; offhand.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autostylic', 'a.', 'Having the mandibular arch articulated directly to the\n   cranium, as in the skulls of the Amphibia.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autotheism', 'n.', 'The doctrine of Gods self-existence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autotheism', 'n.', 'Deification of ones self; self-worship.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autotheist', 'n.', 'One given to self-worship.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autotype', 'n.', 'A facsimile.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autotype', 'n.', 'A photographic picture produced in sensitized pigmented\n   gelatin by exposure to light under a negative; and subsequent washing\n   out of the soluble parts; a kind of picture in ink from a gelatin\n   plate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autotypography', 'n.', 'A process resembling \"nature printing,\" by which\n   drawings executed on gelatin are impressed into a soft metal plate,\n   from which the printing is done as from copperplate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autotypy', 'n.', 'The art or process of making autotypes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autumn', 'n.', 'The third season of the year, or the season between summer\n   and winter, often called \"the fall.\" Astronomically, it begins in the\n   northern temperate zone at the autumnal equinox, about September 23,\n   and ends at the winter solstice, about December 23; but in popular\n   language, autumn, in America, comprises September, October, and\n   November.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autumn', 'n.', 'The harvest or fruits of autumn.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autumn', 'n.', 'The time of maturity or decline; latter portion; third\n   stage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autumnal', 'a.', 'Of, belonging to, or peculiar to, autumn; as, an autumnal\n   tint; produced or gathered in autumn; as, autumnal fruits; flowering in\n   autumn; as, an autumnal plant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Autumnal', 'a.', 'Past the middle of life; in the third stage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auxanometer', 'n.', 'An instrument to measure the growth of plants.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auxesis', 'n.', 'A figure by which a grave and magnificent word is put for\n   the proper word; amplification; hyperbole.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auxetic', 'a.', 'Pertaining to, or containing, auxesis; amplifying.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auxiliar', 'a.', 'Auxiliary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auxiliar', 'n.', 'An auxiliary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auxiliarly', 'adv.', 'By way of help.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auxiliary', 'a.', 'Conferring aid or help; helping; aiding; assisting;\n   subsidiary; as auxiliary troops.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auxiliaries', 'pl. ', 'of Auxiliary');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auxiliary', 'n.', 'A helper; an assistant; a confederate in some action or\n   enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auxiliary', 'n.', 'Foreign troops in the service of a nation at war;\n   (rarely in sing.); a member of the allied or subsidiary force.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auxiliary', 'sing.', 'A verb which helps to form the voices, modes, and\n   tenses of other verbs; -- called, also, an auxiliary verb; as, have,\n   be, may, can, do, must, shall, and will, in English; etre and avoir, in\n   French; avere and essere, in Italian; estar and haber, in Spanish.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auxiliary', 'sing.', 'A quantity introduced for the purpose of simplifying\n   or facilitating some operation, as in equations or trigonometrical\n   formulae.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Auxiliatory', 'a.', 'Auxiliary; helping.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ava', 'n.', 'Same as Kava.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avadavat', 'n.', 'Same as Amadavat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Availed', 'imp. & p. p.', 'of Avail');
  INSERT INTO words (word, word_type, description) VALUES  (  'Availing', 'p. pr. & vb. n.', 'of Avail');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avail', 'v. t.', 'To turn to the advantage of; to be of service to; to\n   profit; to benefit; to help; as, artifices will not avail the sinner in\n   the day of judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avail', 'v. t.', 'To promote; to assist.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avail', 'v. i.', 'To be of use or advantage; to answer the purpose; to have\n   strength, force, or efficacy sufficient to accomplish the object; as,\n   the plea in bar must avail, that is, be sufficient to defeat the suit;\n   this scheme will not avail; medicines will not avail to check the\n   disease.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avail', 'n.', 'Profit; advantage toward success; benefit; value; as, labor,\n   without economy, is of little avail.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avail', 'n.', 'Proceeds; as, the avails of a sale by auction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avail', 'v. t. & i.', 'See Avale, v.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Availabilities', 'pl. ', 'of Availability');
  INSERT INTO words (word, word_type, description) VALUES  (  'Availability', 'n.', 'The quality of being available; availableness.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Availability', 'n.', 'That which is available.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Available', 'a.', 'Having sufficient power, force, or efficacy, for the\n   object; effectual; valid; as, an available plea.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Available', 'a.', 'Such as one may avail ones self of; capable of being\n   used for the accomplishment of a purpose; usable; profitable;\n   advantageous; convertible into a resource; as, an available measure; an\n   available candidate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Availableness', 'n.', 'Competent power; validity; efficacy; as, the\n   availableness of a title.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Availableness', 'n.', 'Quality of being available; capability of being used\n   for the purpose intended.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avaiably', 'adv.', 'In an available manner; profitably; advantageously;\n   efficaciously.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Availment', 'n.', 'Profit; advantage.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avalanche', 'n.', 'A large mass or body of snow and ice sliding swiftly\n   down a mountain side, or falling down a precipice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avalanche', 'n.', 'A fall of earth, rocks, etc., similar to that of an\n   avalanche of snow or ice.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avalanche', 'n.', 'A sudden, great, or irresistible descent or influx of\n   anything.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avale', 'v. t. & i.', 'To cause to descend; to lower; to let fall; to doff.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avale', 'v. t. & i.', 'To bring low; to abase.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avale', 'v. t. & i.', 'To descend; to fall; to dismount.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avant', 'n.', 'The front of an army. [Obs.] See Van.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avant-courier', 'n.', 'A person dispatched before another person or\n   company, to give notice of his or their approach.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avant-guard', 'n.', 'The van or advanced body of an army. See Vanguard.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avarice', 'n.', 'An excessive or inordinate desire of gain; greediness\n   after wealth; covetousness; cupidity.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avarice', 'n.', 'An inordinate desire for some supposed good.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avaricious', 'a.', 'Actuated by avarice; greedy of gain; immoderately\n   desirous of accumulating property.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avarous', 'a.', 'Avaricious.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avast', 'a.', 'Cease; stop; stay.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avatar', 'n.', 'The descent of a deity to earth, and his incarnation as a\n   man or an animal; -- chiefly associated with the incarnations of\n   Vishnu.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avatar', 'n.', 'Incarnation; manifestation as an object of worship or\n   admiration.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avaunce', 'v. t. & i.', 'To advance; to profit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avaunt', 'interj.', 'Begone; depart; -- a word of contempt or abhorrence,\n   equivalent to the phrase \"Get thee gone.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avaunt', 'v. t. & i.', 'To advance; to move forward; to elevate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avaunt', 'v. t. & i.', 'To depart; to move away.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avaunt', 'v. t. & i.', 'To vaunt; to boast.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avaunt', 'n.', 'A vaunt; to boast.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avauntour', 'n.', 'A boaster.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ave', 'n.', 'An ave Maria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ave', 'n.', 'A reverential salutation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avel', 'v. t.', 'To pull away.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avellane', 'a.', 'In the form of four unhusked filberts; as, an avellane\n   cross.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ave Maria', '', 'Alt. of Ave Mary');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ave Mary', '', 'A salutation and prayer to the Virgin Mary, as mother of\n   God; -- used in the Roman Catholic church.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ave Mary', '', 'A particular time (as in Italy, at the ringing of the bells\n   about half an hour after sunset, and also at early dawn); when the\n   people repeat the Ave Maria.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avena', 'n.', 'A genus of grasses, including the common oat (Avena sativa);\n   the oat grasses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avenaceous', 'a.', 'Belonging to, or resembling, oats or the oat grasses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avenage', 'n.', 'A quantity of oats paid by a tenant to a landlord in lieu\n   of rent.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avener', 'n.', 'An officer of the kings stables whose duty it was to\n   provide oats for the horses.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avenged', 'imp. & p. p.', 'of Avenge');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avenging', 'p. pr. & vb. n.', 'of Avenge');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avenge', 'v. t.', 'To take vengeance for; to exact satisfaction for by\n   punishing the injuring party; to vindicate by inflicting pain or evil\n   on a wrongdoer.');
