  INSERT INTO words (word, word_type, description) VALUES  (  'Avulsion', 'n.', 'A tearing asunder; a forcible separation.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avulsion', 'n.', 'A fragment torn off.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avulsion', 'n.', 'The sudden removal of lands or soil from the estate of\n   one man to that of another by an inundation or a current, or by a\n   sudden change in the course of a river by which a part of the estate of\n   one man is cut off and joined to the estate of another. The property in\n   the part thus separated, or cut off, continues in the original owner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Avuncular', 'a.', 'Of or pertaining to an uncle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awaited', 'imp. & p. p.', 'of Await');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awaiting', 'p. pr. & vb. n.', 'of Await');
  INSERT INTO words (word, word_type, description) VALUES  (  'Await', 'v. t.', 'To watch for; to look out for.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Await', 'v. t.', 'To wait on, serve, or attend.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Await', 'v. t.', 'To wait for; to stay for; to expect. See Expect.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Await', 'v. t.', 'To be in store for; to be ready or in waiting for; as, a\n   glorious reward awaits the good.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Await', 'v. i.', 'To watch.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Await', 'v. i.', 'To wait (on or upon).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Await', 'v. i.', 'To wait; to stay in waiting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Await', 'n.', 'A waiting for; ambush; watch; watching; heed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awoke', 'imp.', 'of Awake');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awaked', '', 'of Awake');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awaked', 'p. p.', 'of Awake');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awaken', '', 'of Awake');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awoken', '', 'of Awake');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awaking', 'p. pr. & vb. n.', 'of Awake');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awoke', '', 'of Awake');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awake', 'v. t.', 'To rouse from sleep; to wake; to awaken.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awake', 'v. t.', 'To rouse from a state resembling sleep, as from death,\n   stupidity., or inaction; to put into action; to give new life to; to\n   stir up; as, to awake the dead; to awake the dormant faculties.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awake', 'v. i.', 'To cease to sleep; to come out of a state of natural\n   sleep; and, figuratively, out of a state resembling sleep, as inaction\n   or death.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awake', 'a.', 'Not sleeping or lethargic; roused from sleep; in a state of\n   vigilance or action.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awakened', 'imp. & p. p.', 'of Awaken');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awakening', 'p. pr. & vb. n.', 'of Awaken');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awaken', 'v. t.', 'To rouse from sleep or torpor; to awake; to wake.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awakener', 'n.', 'One who, or that which, awakens.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awakening', 'a.', 'Rousing from sleep, in a natural or a figurative sense;\n   rousing into activity; exciting; as, the awakening city; an awakening\n   discourse; the awakening dawn.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awakening', 'n.', 'The act of awaking, or ceasing to sleep. Specifically: A\n   revival of religion, or more general attention to religious matters\n   than usual.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awakenment', 'n.', 'An awakening.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awanting', 'a.', 'Missing; wanting.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awarded', 'imp. & p. p.', 'of Award');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awarding', 'p. pr. & vb. n.', 'of Award');
  INSERT INTO words (word, word_type, description) VALUES  (  'Award', 'v. t.', 'To give by sentence or judicial determination; to assign\n   or apportion, after careful regard to the nature of the case; to\n   adjudge; as, the arbitrators awarded damages to the complainant.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Award', 'v. i.', 'To determine; to make an award.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Award', 'v. t.', 'A judgment, sentence, or final decision. Specifically:\n   The decision of arbitrators in a case submitted.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Award', 'v. t.', 'The paper containing the decision of arbitrators; that\n   which is warded.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awarder', 'n.', 'One who awards, or assigns by sentence or judicial\n   determination; a judge.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aware', 'a.', 'Watchful; vigilant or on ones guard against danger or\n   difficulty.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aware', 'a.', 'Apprised; informed; cognizant; conscious; as, he was aware\n   of the enemys designs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awarn', 'v. t.', 'To warn.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awash', 'a.', 'Washed by the waves or tide; -- said of a rock or strip of\n   shore, or (Naut.) of an anchor, etc., when flush with the surface of\n   the water, so that the waves break over it.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Away', 'adv.', 'From a place; hence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Away', 'adv.', 'Absent; gone; at a distance; as, the master is away from\n   home.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Away', 'adv.', 'Aside; off; in another direction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Away', 'adv.', 'From a state or condition of being; out of existence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Away', 'adv.', 'By ellipsis of the verb, equivalent to an imperative: Go or\n   come away; begone; take away.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Away', 'adv.', 'On; in continuance; without intermission or delay; as, sing\n   away.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Away-going', 'a.', 'Sown during the last years of a tenancy, but not ripe\n   until after its expiration; -- said of crops.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awayward', 'adv.', 'Turned away; away.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awe', 'n.', 'Dread; great fear mingled with respect.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awe', 'n.', 'The emotion inspired by something dreadful and sublime; an\n   undefined sense of the dreadful and the sublime; reverential fear, or\n   solemn wonder; profound reverence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awed', 'imp. & p. p.', 'of Awe');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awing', 'p. pr. & vb. n.', 'of Awe');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awe', 'v. t.', 'To strike with fear and reverence; to inspire with awe; to\n   control by inspiring dread.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awearied', 'p. p.', 'Wearied.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aweary', 'a.', 'Weary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aweather', 'adv.', 'On the weather side, or toward the wind; in the\n   direction from which the wind blows; -- opposed to alee; as, helm\n   aweather!');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aweigh', 'adv.', 'Just drawn out of the ground, and hanging\n   perpendicularly; atrip; -- said of the anchor.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aweless', 'a.', 'See Awless.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awesome', 'a.', 'Causing awe; appalling; awful; as, an awesome sight.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awesome', 'a.', 'Expressive of awe or terror.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awesomeness', 'n.', 'The quality of being awesome.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awe-stricken', 'a.', 'Awe-struck.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awe-struck', 'a.', 'Struck with awe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awful', 'a.', 'Oppressing with fear or horror; appalling; terrible; as, an\n   awful scene.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awful', 'a.', 'Inspiring awe; filling with profound reverence, or with fear\n   and admiration; fitted to inspire reverential fear; profoundly\n   impressive.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awful', 'a.', 'Struck or filled with awe; terror-stricken.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awful', 'a.', 'Worshipful; reverential; law-abiding.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awful', 'a.', 'Frightful; exceedingly bad; great; -- applied intensively;\n   as, an awful bonnet; an awful boaster.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awfully', 'adv.', 'In an awful manner; in a manner to fill with terror or\n   awe; fearfully; reverently.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awfully', 'adv.', 'Very; excessively.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awfulness', 'n.', 'The quality of striking with awe, or with reverence;\n   dreadfulness; solemnity; as, the awfulness of this sacred place.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awfulness', 'n.', 'The state of being struck with awe; a spirit of\n   solemnity; profound reverence.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awhape', 'v. t.', 'To confound; to terrify; to amaze.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awhile', 'adv.', 'For a while; for some time; for a short time.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awing', 'adv.', 'On the wing; flying; fluttering.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awk', 'a.', 'Odd; out of order; perverse.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awk', 'a.', 'Wrong, or not commonly used; clumsy; sinister; as, the awk end\n   of a rod (the but end).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awk', 'a.', 'Clumsy in performance or manners; unhandy; not dexterous;\n   awkward.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awk', 'adv.', 'Perversely; in the wrong way.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awkly', 'adv.', 'In an unlucky (left-handed) or perverse manner.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awkly', 'adv.', 'Awkwardly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awkward', 'a.', 'Wanting dexterity in the use of the hands, or of\n   instruments; not dexterous; without skill; clumsy; wanting ease, grace,\n   or effectiveness in movement; ungraceful; as, he was awkward at a\n   trick; an awkward boy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awkward', 'a.', 'Not easily managed or effected; embarrassing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awkward', 'a.', 'Perverse; adverse; untoward.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awl', 'n.', 'A pointed instrument for piercing small holes, as in leather\n   or wood; used by shoemakers, saddlers, cabinetmakers, etc. The blade is\n   differently shaped and pointed for different uses, as in the brad awl,\n   saddlers awl, shoemakers awl, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awless', 'a.', 'Wanting reverence; void of respectful fear.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awless', 'a.', 'Inspiring no awe.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awlessness', 'n.', 'The quality of being awless.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awl-shaped', 'a.', 'Shaped like an awl.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awl-shaped', 'a.', 'Subulate. See Subulate.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awlwort', 'n.', 'A plant (Subularia aquatica); with awl-shaped leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awm', 'n.', 'See Aam.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awn', 'n.', 'The bristle or beard of barley, oats, grasses, etc., or any\n   similar bristlelike appendage; arista.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awned', 'a.', 'Furnished with an awn, or long bristle-shaped tip; bearded.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awning', 'n.', 'A rooflike cover, usually of canvas, extended over or\n   before any place as a shelter from the sun, rain, or wind.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awning', 'n.', 'That part of the poop deck which is continued forward\n   beyond the bulkhead of the cabin.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awninged', 'a.', 'Furnished with an awning.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awnless', 'a.', 'Without awns or beard.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awny', 'a.', 'Having awns; bearded.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awork', 'adv.', 'At work; in action.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aworking', 'adv.', 'At work; in action.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awreak', 'v. t. & i.', 'Alt. of Awreke');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awreke', 'v. t. & i.', 'To avenge. [Obs.] See Wreak.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awrong', 'adv.', 'Wrongly.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awry', 'adv. & a.', 'Turned or twisted toward one side; not in a straight\n   or true direction, or position; out of the right course; distorted;\n   obliquely; asquint; with oblique vision; as, to glance awry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awry', 'adv. & a.', 'Aside from the line of truth, or right reason;\n   unreasonable or unreasonably; perverse or perversely.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Awsome', 'a.', 'Same as Awesome.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ax', 'n.', 'Alt. of Axe');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axe', 'n.', 'A tool or instrument of steel, or of iron with a steel edge or\n   blade, for felling trees, chopping and splitting wood, hewing timber,\n   etc. It is wielded by a wooden helve or handle, so fixed in a socket or\n   eye as to be in the same plane with the blade. The broadax, or\n   carpenters ax, is an ax for hewing timber, made heavier than the\n   chopping ax, and with a broader and thinner blade and a shorter handle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ax', 'v. t. & i.', 'To ask; to inquire or inquire of.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axal', 'a.', '[See Axial.]');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axe', '', 'Alt. of Axeman');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axeman', '', 'See Ax, Axman.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axial', 'a.', 'Of or pertaining to an axis; of the nature of, or\n   resembling, an axis; around an axis.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axial', 'a.', 'Belonging to the axis of the body; as, the axial skeleton;\n   or to the axis of any appendage or organ; as, the axial bones.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axially', 'adv.', 'In relation to, or in a line with, an axis; in the axial\n   (magnetic) line.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axil', 'n.', 'The angle or point of divergence between the upper side of a\n   branch, leaf, or petiole, and the stem or branch from which it springs.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axile', 'a.', 'Situated in the axis of anything; as an embryo which lies in\n   the axis of a seed.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axillae', 'pl. ', 'of Axilla');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axilla', 'n.', 'The armpit, or the cavity beneath the junction of the arm\n   and shoulder.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axilla', 'n.', 'An axil.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axillar', 'a.', 'Axillary.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axillaries', 'n. pl.', 'Alt. of Axillars');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axillars', 'n. pl.', 'Feathers connecting the under surface of the wing and\n   the body, and concealed by the closed wing.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axillary', 'a.', 'Of or pertaining to the axilla or armpit; as, axillary\n   gland, artery, nerve.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axillary', 'a.', 'Situated in, or rising from, an axil; of or pertaining to\n   an axil.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axinite', 'n.', 'A borosilicate of alumina, iron, and lime, commonly found\n   in glassy, brown crystals with acute edges.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axinomancy', 'n.', 'A species of divination, by means of an ax or hatchet.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axiom', 'a.', 'A self-evident and necessary truth, or a proposition whose\n   truth is so evident as first sight that no reasoning or demonstration\n   can make it plainer; a proposition which it is necessary to take for\n   granted; as, \"The whole is greater than a part;\" \"A thing can not, at\n   the same time, be and not be.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axiom', 'a.', 'An established principle in some art or science, which,\n   though not a necessary truth, is universally received; as, the axioms\n   of political economy.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axiomatic', 'a.', 'Alt. of Axiomatical');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axiomatical', 'a.', 'Of or pertaining to an axiom; having the nature of an\n   axiom; self-evident; characterized by axioms.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axiomatically', 'adv.', 'By the use of axioms; in the form of an axiom.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axis', 'n.', 'The spotted deer (Cervus axis or Axis maculata) of India,\n   where it is called hog deer and parrah (Moorish name).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axes', 'pl. ', 'of Axis');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axis', 'n.', 'A straight line, real or imaginary, passing through a body,\n   on which it revolves, or may be supposed to revolve; a line passing\n   through a body or system around which the parts are symmetrically\n   arranged.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axis', 'n.', 'A straight line with respect to which the different parts of\n   a magnitude are symmetrically arranged; as, the axis of a cylinder, i.\n   e., the axis of a cone, that is, the straight line joining the vertex\n   and the center of the base; the axis of a circle, any straight line\n   passing through the center.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axis', 'n.', 'The stem; the central part, or longitudinal support, on which\n   organs or parts are arranged; the central line of any body.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axis', 'n.', 'The second vertebra of the neck, or vertebra dentata.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axis', 'n.', 'Also used of the body only of the vertebra, which is\n   prolonged anteriorly within the foramen of the first vertebra or atlas,\n   so as to form the odontoid process or peg which serves as a pivot for\n   the atlas and head to turn upon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axis', 'n.', 'One of several imaginary lines, assumed in describing the\n   position of the planes by which a crystal is bounded.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axis', 'n.', 'The primary or secondary central line of any design.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axle', 'n.', 'The pin or spindle on which a wheel revolves, or which\n   revolves with a wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axle', 'n.', 'A transverse bar or shaft connecting the opposite wheels of a\n   car or carriage; an axletree.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axle', 'n.', 'An axis; as, the suns axle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axle box', '', 'A bushing in the hub of a wheel, through which the axle\n   passes.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axle box', '', 'The journal box of a rotating axle, especially a railway\n   axle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axled', 'a.', 'Having an axle; -- used in composition.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axle guard', '', 'The part of the framing of a railway car or truck, by\n   which an axle box is held laterally, and in which it may move\n   vertically; -- also called a jaw in the United States, and a housing in\n   England.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axletree', 'n.', 'A bar or beam of wood or iron, connecting the opposite\n   wheels of a carriage, on the ends of which the wheels revolve.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axletree', 'n.', 'A spindle or axle of a wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axmen', 'pl. ', 'of Axman');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axman', 'n.', 'One who wields an ax.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axminster', 'n.', 'An Axminster carpet, an imitation Turkey carpet, noted\n   for its thick and soft pile; -- so called from Axminster, Eng.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axolotl', 'n.', 'An amphibian of the salamander tribe found in the elevated\n   lakes of Mexico; the siredon.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axstone', 'n.', 'A variety of jade. It is used by some savages,\n   particularly the natives of the South Sea Islands, for making axes or\n   hatchets.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axtree', 'n.', 'Axle or axletree.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Axunge', 'n.', 'Fat; grease; esp. the fat of pigs or geese; usually\n   (Pharm.); lard prepared for medical use.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ay', 'interj.', 'Ah! alas!');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ay', 'adv.', 'Same as Aye.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ayah', 'n.', 'A native nurse for children; also, a ladys maid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aye', 'adv.', 'Alt. of Ay');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ay', 'adv.', 'Yes; yea; -- a word expressing assent, or an affirmative\n   answer to a question. It is much used in viva voce voting in\n   legislative bodies, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aye', 'n.', 'An affirmative vote; one who votes in the affirmative; as, \"To\n   call for the ayes and noes;\" \"The ayes have it.\"');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aye', 'a.', 'Alt. of Ay');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ay', 'a.', 'Always; ever; continually; for an indefinite time.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aye-aye', 'n.', 'A singular nocturnal quadruped, allied to the lemurs,\n   found in Madagascar (Cheiromys Madagascariensis); remarkable for its\n   long fingers, sharp nails, and rodent-like incisor teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ayegreen', 'n.', 'The houseleek (Sempervivum tectorum).');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ayen', 'adv. & prep.', 'Alt. of Ayeins');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ayein', 'adv. & prep.', 'Alt. of Ayeins');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ayeins', 'adv. & prep.', 'Again; back against.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ayenward', 'adv.', 'Backward.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ayle', 'n.', 'A grandfather.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ayme', 'n.', 'The utterance of the ejaculation \"Ay me !\" [Obs.] See Ay,\n   interj.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ayond', 'prep. & adv.', 'Beyond.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ayont', 'prep. & adv.', 'Beyond.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ayrie', 'n.', 'Alt. of Ayry');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ayry', 'n.', 'See Aerie.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ayrshire', 'n.', 'One of a superior breed of cattle from Ayrshire,\n   Scotland. Ayrshires are notable for the quantity and quality of their\n   milk.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ayuntamiento', 'n.', 'In Spain and Spanish America, a corporation or body\n   of magistrates in cities and towns, corresponding to mayor and\n   aldermen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azaleas', 'pl. ', 'of Azalea');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azalea', 'n.', 'A genus of showy flowering shrubs, mostly natives of China\n   or of North America; false honeysuckle. The genus is scarcely distinct\n   from Rhododendron.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azarole', 'n.', 'The Neapolitan medlar (Crataegus azarolus); a shrub of\n   southern Europe; also, its fruit.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azedarach', 'n.', 'A handsome Asiatic tree (Melia azedarach); common in the\n   southern United States; -- called also, Pride of India, Pride of China,\n   and Bead tree.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azedarach', 'n.', 'The bark of the roots of the azedarach, used as a\n   cathartic and emetic.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azimuth', 'n.', 'The quadrant of an azimuth circle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azimuth', 'n.', 'An arc of the horizon intercepted between the meridian of\n   the place and a vertical circle passing through the center of any\n   object; as, the azimuth of a star; the azimuth or bearing of a line\n   surveying.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azimuthal', 'a.', 'Of or pertaining to the azimuth; in a horizontal circle.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azo-', '', 'A combining form of azote');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azo-', '', 'Applied loosely to compounds having nitrogen variously\n   combined, as in cyanides, nitrates, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azo-', '', 'Now especially applied to compounds containing a two atom\n   nitrogen group uniting two hydrocarbon radicals, as in azobenzene,\n   azobenzoic, etc. These compounds furnish many artificial dyes. See\n   Diazo-.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azobenzene', 'n.', 'A substance (C6H5.N2.C6H5) derived from nitrobenzene,\n   forming orange red crystals which are easily fusible.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azoic', 'a.', 'Destitute of any vestige of organic life, or at least of\n   animal life; anterior to the existence of animal life; formed when\n   there was no animal life on the globe; as, the azoic. rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azoleic', 'a.', 'Pertaining to an acid produced by treating oleic with\n   nitric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azonic', 'a.', 'Confined to no zone or region; not local.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azorian', 'a.', 'Of or pertaining to the Azores.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azorian', 'n.', 'A native of the Azores.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azote', 'n.', 'Same as Nitrogen.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azoth', 'n.', 'The first principle of metals, i. e., mercury, which was\n   formerly supposed to exist in all metals, and to be extractable from\n   them.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azoth', 'n.', 'The universal remedy of Paracelsus.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azotic', 'a.', 'Pertaining to azote, or nitrogen; formed or consisting of\n   azote; nitric; as, azotic gas; azotic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azotite', 'n.', 'A salt formed by the combination of azotous, or nitrous,\n   acid with a base; a nitrite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azotized', 'imp. & p. p.', 'of Azotize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azotizing', 'p. pr. & vb. n.', 'of Azotize');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azotize', 'v. t.', 'To impregnate with azote, or nitrogen; to nitrogenize.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azotometer', 'n.', 'An apparatus for measuring or determining the\n   proportion of nitrogen; a nitrometer.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azotous', 'a.', 'Nitrous; as, azotous acid.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aztec', 'a.', 'Of or relating to one of the early races in Mexico that\n   inhabited the great plateau of that country at the time of the Spanish\n   conquest in 1519.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Aztec', 'n.', 'One of the Aztec race or people.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azure', 'a.', 'Sky-blue; resembling the clear blue color of the unclouded\n   sky; cerulean; also, cloudless.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azure', 'n.', 'The lapis lazuli.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azure', 'n.', 'The clear blue color of the sky; also, a pigment or dye of\n   this color.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azure', 'n.', 'The blue vault above; the unclouded sky.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azure', 'n.', 'A blue color, represented in engraving by horizontal\n   parallel lines.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azure', 'v. t.', 'To color blue.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azured', 'a.', 'Of an azure color; sky-blue.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azureous', 'a.', 'Of a fine blue color; azure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azurine', 'a.', 'Azure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azurine', 'n.', 'The blue roach of Europe (Leuciscus caeruleus); -- so\n   called from its color.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azurite', 'n.', 'Blue carbonate of copper; blue malachite.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azurn', 'a.', 'Azure.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azygous', 'a.', 'Odd; having no fellow; not one of a pair; single; as, the\n   azygous muscle of the uvula.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azym', 'n.', 'Alt. of Azyme');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azyme', 'n.', 'Unleavened bread.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azymic', 'a.', 'Azymous.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azymite', 'n.', 'One who administered the Eucharist with unleavened bread;\n   -- a name of reproach given by those of the Greek church to the Latins.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Azymous', 'a.', 'Unleavened; unfermented.\n   B () is the second letter of the English alphabet. (See Guide to\n   Pronunciation, // 196, 220.) It is etymologically related to p, v, f, w\n   and m , letters representing sounds having a close organic affinity to\n   its own sound; as in Eng. bursar and purser; Eng. bear and Lat. ferre;\n   Eng. silver and Ger. silber; Lat. cubitum and It. gomito; Eng. seven,\n   Anglo-Saxon seofon, Ger. sieben, Lat. septem, Gr.\"epta`, Sanskrit\n   saptan. The form of letter B is Roman, from Greek B (Beta); of Semitic\n   origin. The small b was formed by gradual change from the capital B.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Ba', 'v. i.', 'To kiss.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Baa', 'v. i.', 'To cry baa, or bleat as a sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Baas', 'pl. ', 'of Baa');
  INSERT INTO words (word, word_type, description) VALUES  (  'Baa', 'n.', 'The cry or bleating of a sheep; a bleat.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Baaing', 'n.', 'The bleating of a sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Baalim', 'pl. ', 'of Baal');
  INSERT INTO words (word, word_type, description) VALUES  (  'Baal', 'n.', 'The supreme male divinity of the Phoenician and Canaanitish\n   nations.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Baal', 'n.', 'The whole class of divinities to whom the name Baal was\n   applied.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Baalism', 'n.', 'Worship of Baal; idolatry.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Baalist', 'n.', 'Alt. of Baalite');
  INSERT INTO words (word, word_type, description) VALUES  (  'Baalite', 'n.', 'A worshiper of Baal; a devotee of any false religion; an\n   idolater.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Baba', 'n.', 'A kind of plum cake.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Babbitt', 'v. t.', 'To line with Babbitt metal.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Babbitt metal', '', 'A soft white alloy of variable composition (as a nine\n   parts of tin to one of copper, or of fifty parts of tin to five of\n   antimony and one of copper) used in bearings to diminish friction.');
  INSERT INTO words (word, word_type, description) VALUES  (  'Babbled', 'imp. & p. p.', 'of Babble');
INSERT INTO words (word, word_type, description) VALUES  ('Babbling',    'p. pr. & vb. n.',    'of Babble');
INSERT INTO words (word, word_type, description) VALUES  ('Babble',    'v. i.',    'To utter words indistinctly or unintelligibly; to utter\n   inarticulate sounds; as a child babbles.');
INSERT INTO words (word, word_type, description) VALUES  ('Babble',    'v. i.',    'To talk incoherently; to utter unmeaning words.');
INSERT INTO words (word, word_type, description) VALUES  (    'Babble',    'v. i.',    'To talk much; to chatter; to prate.');
