INSERT INTO words (word, word_type, description)VALUES  (    'Babble',    'v. i.',    'To make a continuous murmuring noise, as shallow water\n   running over stones.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babble', 'v. i.', 'To utter in an indistinct or incoherent way; to repeat,\n   as words, in a childish way without understanding.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babble', 'v. i.', 'To disclose by too free talk, as a secret.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babble', 'n.', 'Idle talk; senseless prattle; gabble; twaddle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babble', 'n.', 'Inarticulate speech; constant or confused murmur.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babblement', 'n.', 'Babble.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babbler', 'n.', 'An idle talker; an irrational prater; a teller of secrets.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babbler', 'n.', 'A hound too noisy on finding a good scent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babbler', 'n.', 'A name given to any one of family (Timalinae) of\n   thrushlike birds, having a chattering note.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babblery', 'n.', 'Babble.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babe', 'n.', 'An infant; a young child of either sex; a baby.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babe', 'n.', 'A doll for children.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babehood', 'n.', 'Babyhood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babel', 'n.', 'The city and tower in the land of Shinar, where the\n   confusion of languages took place.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babel', 'n.', 'Hence: A place or scene of noise and confusion; a confused\n   mixture of sounds, as of voices or languages.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babery', 'n.', 'Finery of a kind to please a child.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babian', 'n.', 'Alt. of Babion');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babion', 'n.', 'A baboon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babillard', 'n.', 'The lesser whitethroat of Europe; -- called also\n   babbling warbler.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babingtonite', 'n.', 'A mineral occurring in triclinic crystals approaching\n   pyroxene in angle, and of a greenish black color. It is a silicate of\n   iron, manganese, and lime.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babiroussa', 'n.', 'Alt. of Babirussa');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babirussa', 'n.', 'A large hoglike quadruped (Sus, / Porcus, babirussa) of\n   the East Indies, sometimes domesticated; the Indian hog. Its upper\n   canine teeth or tusks are large and recurved.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babish', 'a.', 'Like a babe; a childish; babyish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babism', 'n.', 'The doctrine of a modern religious sect, which originated\n   in Persia in 1843, being a mixture of Mohammedan, Christian, Jewish and\n   Parsee elements.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babist', 'n.', 'A believer in Babism.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bablah', 'n.', 'The ring of the fruit of several East Indian species of\n   acacia; neb-neb. It contains gallic acid and tannin, and is used for\n   dyeing drab.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baboo', 'n.', 'Alt. of Babu');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babu', 'n.', 'A Hindoo gentleman; a native clerk who writes English; also,\n   a Hindoo title answering to Mr. or Esquire.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baboon', 'n.', 'One of the Old World Quadrumana, of the genera Cynocephalus\n   and Papio; the dog-faced ape. Baboons have dog-like muzzles and large\n   canine teeth, cheek pouches, a short tail, and naked callosities on the\n   buttocks. They are mostly African. See Mandrill, and Chacma, and Drill\n   an ape.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baboonery', 'n.', 'Baboonish behavior.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baboonish', 'a.', 'Like a baboon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babies', 'pl. ', 'of Baby');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baby', 'n.', 'An infant or young child of either sex; a babe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baby', 'n.', 'A small image of an infant; a doll.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baby', 'a.', 'Pertaining to, or resembling, an infant; young or little; as,\n   baby swans.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babied', 'imp. & p. p.', 'of Baby');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babying', 'p. pr. & vb. n.', 'of Baby');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baby', 'v. i.', 'To treat like a young child; to keep dependent; to humor;\n   to fondle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baby farm', '', 'A place where the nourishment and care of babies are\n   offered for hire.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baby farmer', '', 'One who keeps a baby farm.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baby farming', '', 'The business of keeping a baby farm.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babyhood', 'n.', 'The state or period of infancy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babyhouse', 'a.', 'A place for childrens dolls and dolls furniture.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babyish', 'a.', 'Like a baby; childish; puerile; simple.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babyism', 'n.', 'The state of being a baby.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babyism', 'n.', 'A babyish manner of acting or speaking.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baby jumper', '', 'A hoop suspended by an elastic strap, in which a young\n   child may be held secure while amusing itself by jumping on the floor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babylonian', 'a.', 'Of or pertaining to the real or to the mystical\n   Babylon, or to the ancient kingdom of Babylonia; Chaldean.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babylonian', 'n.', 'An inhabitant of Babylonia (which included Chaldea); a\n   Chaldean.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babylonian', 'n.', 'An astrologer; -- so called because the Chaldeans were\n   remarkable for the study of astrology.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babylonic', 'a.', 'Alt. of Babylonical');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babylonical', 'a.', 'Pertaining to Babylon, or made there; as, Babylonic\n   garments, carpets, or hangings.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babylonical', 'a.', 'Tumultuous; disorderly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babylonish', 'n.', 'Of or pertaining to, or made in, Babylon or Babylonia.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babylonish', 'n.', 'Pertaining to the Babylon of Revelation xiv. 8.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babylonish', 'n.', 'Pertaining to Rome and papal power.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babylonish', 'n.', 'Confused; Babel-like.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babyroussa', 'n.', 'Alt. of Babyrussa');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babyrussa', 'n.', 'See Babyroussa.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Babyship', 'n.', 'The quality of being a baby; the personality of an\n   infant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bac', 'n.', 'A broad, flatbottomed ferryboat, usually worked by a rope.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bac', 'n.', 'A vat or cistern. See 1st Back.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baccalaureate', 'n.', 'The degree of bachelor of arts. (B.A. or A.B.); the\n   first or lowest academical degree conferred by universities and\n   colleges.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baccalaureate', 'n.', 'A baccalaureate sermon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baccalaureate', 'a.', 'Pertaining to a bachelor of arts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baccara', 'n.', 'Alt. of Baccarat');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baccarat', 'n.', 'A French game of cards, played by a banker and punters.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baccare', 'interj.', 'Alt. of Backare');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backare', 'interj.', 'Stand back! give place! -- a cant word of the\n   Elizabethan writers, probably in ridicule of some person who pretended\n   to a knowledge of Latin which he did not possess.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baccate', 'a.', 'Pulpy throughout, like a berry; -- said of fruits.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baccated', 'a.', 'Having many berries.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baccated', 'a.', 'Set or adorned with pearls.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchanal', 'a.', 'Relating to Bacchus or his festival.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchanal', 'a.', 'Engaged in drunken revels; drunken and riotous or noisy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchanal', 'n.', 'A devotee of Bacchus; one who indulges in drunken\n   revels; one who is noisy and riotous when intoxicated; a carouser.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchanal', 'n.', 'The festival of Bacchus; the bacchanalia.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchanal', 'n.', 'Drunken revelry; an orgy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchanal', 'n.', 'A song or dance in honor of Bacchus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchanalia', 'n. pl.', 'A feast or an orgy in honor of Bacchus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchanalia', 'n. pl.', 'Hence: A drunken feast; drunken reveler.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchanalian', 'a.', 'Of or pertaining to the festival of Bacchus; relating\n   to or given to reveling and drunkenness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchanalian', 'n.', 'A bacchanal; a drunken reveler.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchanalianism', 'n.', 'The practice of bacchanalians; bacchanals; drunken\n   revelry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchants', 'pl. ', 'of Bacchant');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchantes', 'pl. ', 'of Bacchant');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchant', 'n.', 'A priest of Bacchus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchant', 'n.', 'A bacchanal; a reveler.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchant', 'a.', 'Bacchanalian; fond of drunken revelry; wine-loving;\n   reveling; carousing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchantes', 'pl. ', 'of Bacchante');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchante', 'n.', 'A priestess of Bacchus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchante', 'n.', 'A female bacchanal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchantic', 'a.', 'Bacchanalian.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchic', 'a.', 'Alt. of Bacchical');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchical', 'a.', 'Of or relating to Bacchus; hence, jovial, or\n   riotous,with intoxication.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchii', 'pl. ', 'of Bacchius');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchius', 'n.', 'A metrical foot composed of a short syllable and two long\n   ones; according to some, two long and a short.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacchus', 'n.', 'The god of wine, son of Jupiter and Semele.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacciferous', 'a.', 'Producing berries.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacciform', 'a.', 'Having the form of a berry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baccivorous', 'a.', 'Eating, or subsisting on, berries; as, baccivorous\n   birds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bace', 'n., a., & v.', 'See Base.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacharach', 'n.', 'Alt. of Backarack');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backarack', 'n.', 'A kind of wine made at Bacharach on the Rhine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bachelor', 'n.', 'A man of any age who has not been married.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bachelor', 'n.', 'An unmarried woman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bachelor', 'n.', 'A person who has taken the first or lowest degree in the\n   liberal arts, or in some branch of science, at a college or university;\n   as, a bachelor of arts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bachelor', 'n.', 'A knight who had no standard of his own, but fought under\n   the standard of another in the field; often, a young knight.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bachelor', 'n.', 'In the companies of London tradesmen, one not yet\n   admitted to wear the livery; a junior member.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bachelor', 'n.', 'A kind of bass, an edible fresh-water fish (Pomoxys\n   annularis) of the southern United States.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bachelordom', 'n.', 'The state of bachelorhood; the whole body of\n   bachelors.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bachelorhood', 'n.', 'The state or condition of being a bachelor;\n   bachelorship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bachelorism', 'n.', 'Bachelorhood; also, a manner or peculiarity belonging\n   to bachelors.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bachelors button', '', 'A plant with flowers shaped like buttons;\n   especially, several species of Ranunculus, and the cornflower\n   (Centaures cyanus) and globe amaranth (Gomphrena).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bachelorship', 'n.', 'The state of being a bachelor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bachelry', 'n.', 'The body of young aspirants for knighthood.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacillar', 'a.', 'Shaped like a rod or staff.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacillariae', 'n. pl.', 'See Diatom.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacillary', 'a.', 'Of or pertaining to little rods; rod-shaped.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacilliform', 'a.', 'Rod-shaped.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacilli', 'pl. ', 'of Bacillus');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacillus', 'n.', 'A variety of bacterium; a microscopic, rod-shaped\n   vegetable organism.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'n.', 'A large shallow vat; a cistern, tub, or trough, used by\n   brewers, distillers, dyers, picklers, gluemakers, and others, for\n   mixing or cooling wort, holding water, hot glue, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'n.', 'A ferryboat. See Bac, 1.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'n.', 'In human beings, the hinder part of the body, extending from\n   the neck to the end of the spine; in other animals, that part of the\n   body which corresponds most nearly to such part of a human being; as,\n   the back of a horse, fish, or lobster.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'n.', 'An extended upper part, as of a mountain or ridge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'n.', 'The outward or upper part of a thing, as opposed to the inner\n   or lower part; as, the back of the hand, the back of the foot, the back\n   of a hand rail.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'n.', 'The part opposed to the front; the hinder or rear part of a\n   thing; as, the back of a book; the back of an army; the back of a\n   chimney.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'n.', 'The part opposite to, or most remote from, that which fronts\n   the speaker or actor; or the part out of sight, or not generally seen;\n   as, the back of an island, of a hill, or of a village.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'n.', 'The part of a cutting tool on the opposite side from its\n   edge; as, the back of a knife, or of a saw.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'n.', 'A support or resource in reserve.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'n.', 'The keel and keelson of a ship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'n.', 'The upper part of a lode, or the roof of a horizontal\n   underground passage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'n.', 'A garment for the back; hence, clothing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'a.', 'Being at the back or in the rear; distant; remote; as, the\n   back door; back settlements.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'a.', 'Being in arrear; overdue; as, back rent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'a.', 'Moving or operating backward; as, back action.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backed', 'imp. & p. p.', 'of Back');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backing', 'p. pr. & vb. n.', 'of Back');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'v. i.', 'To get upon the back of; to mount.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'v. i.', 'To place or seat upon the back.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'v. i.', 'To drive or force backward; to cause to retreat or recede;\n   as, to back oxen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'v. i.', 'To make a back for; to furnish with a back; as, to back\n   books.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'v. i.', 'To adjoin behind; to be at the back of.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'v. i.', 'To write upon the back of; as, to back a letter; to\n   indorse; as, to back a note or legal document.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'v. i.', 'To support; to maintain; to second or strengthen by aid or\n   influence; as, to back a friend.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'v. i.', 'To bet on the success of; -- as, to back a race horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'v. i.', 'To move or go backward; as, the horse refuses to back.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'v. i.', 'To change from one quarter to another by a course opposite\n   to that of the sun; -- used of the wind.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'v. i.', 'To stand still behind another dog which has pointed; --\n   said of a dog.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'adv.', 'In, to, or toward, the rear; as, to stand back; to step\n   back.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'adv.', 'To the place from which one came; to the place or person\n   from which something is taken or derived; as, to go back for something\n   left behind; to go back to ones native place; to put a book back after\n   reading it.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'adv.', 'To a former state, condition, or station; as, to go back to\n   private life; to go back to barbarism.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'adv.', '(Of time) In times past; ago.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'adv.', 'Away from contact; by reverse movement.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'adv.', 'In concealment or reserve; in ones own possession; as, to\n   keep back the truth; to keep back part of the money due to another.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'adv.', 'In a state of restraint or hindrance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'adv.', 'In return, repayment, or requital.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'adv.', 'In withdrawal from a statement, promise, or undertaking;\n   as, he took back0 the offensive words.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back', 'adv.', 'In arrear; as, to be back in ones rent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backarack', 'n.', 'See Bacharach.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backare', 'interj.', 'Same as Baccare.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backband', 'n.', 'The band which passes over the back of a horse and holds\n   up the shafts of a carriage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backbite', 'v. i.', 'To wound by clandestine detraction; to censure meanly\n   or spitefully (an absent person); to slander or speak evil of (one\n   absent).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backbite', 'v. i.', 'To censure or revile the absent.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backbiter', 'n.', 'One who backbites; a secret calumniator or detractor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backbiting', 'n.', 'Secret slander; detraction.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backboard', 'n.', 'A board which supports the back wen one is sitting;');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backboard', 'n.', 'A board serving as the back part of anything, as of a\n   wagon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backboard', 'n.', 'A thin stuff used for the backs of framed pictures,\n   mirrors, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backboard', 'n.', 'A board attached to the rim of a water wheel to prevent\n   the water from running off the floats or paddies into the interior of\n   the wheel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backboard', 'n.', 'A board worn across the back to give erectness to the\n   figure.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backbond', 'n.', 'An instrument which, in conjunction with another making\n   an absolute disposition, constitutes a trust.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backbone', 'n.', 'The column of bones in the back which sustains and gives\n   firmness to the frame; the spine; the vertebral or spinal column.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backbone', 'n.', 'Anything like , or serving the purpose of, a backbone.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backbone', 'n.', 'Firmness; moral principle; steadfastness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backboned', 'a.', 'Vertebrate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backcast', 'n.', 'Anything which brings misfortune upon one, or causes\n   failure in an effort or enterprise; a reverse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back door', '', 'A door in the back part of a building; hence, an indirect\n   way.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backdoor', 'a.', 'Acting from behind and in concealment; as, backdoor\n   intrigues.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backdown', 'n.', 'A receding or giving up; a complete surrender.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backed', 'a.', 'Having a back; fitted with a back; as, a backed electrotype\n   or stereotype plate. Used in composition; as, broad-backed;\n   hump-backed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backer', 'n.', 'One who, or that which, backs; especially one who backs a\n   person or thing in a contest.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backfall', 'n.', 'A fall or throw on the back in wrestling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backfriend', 'n.', 'A secret enemy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backgammon', 'n.', 'A game of chance and skill, played by two persons on a\n   \"board\" marked off into twenty-four spaces called \"points\". Each player\n   has fifteen pieces, or \"men\", the movements of which from point to\n   point are determined by throwing dice. Formerly called tables.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backgammon', 'v. i.', 'In the game of backgammon, to beat by ending the\n   game before the loser is clear of his first \"table\".');
  INSERT INTO words (word, word_type, description)VALUES  (    'Background', 'n.', 'Ground in the rear or behind, or in the distance, as\n   opposed to the foreground, or the ground in front.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Background', 'n.', 'The space which is behind and subordinate to a portrait\n   or group of figures.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Background', 'n.', 'Anything behind, serving as a foil; as, the statue had\n   a background of red hangings.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Background', 'n.', 'A place in obscurity or retirement, or out of sight.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backhand', 'n.', 'A kind of handwriting in which the downward slope of the\n   letters is from left to right.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backhand', 'a.', 'Sloping from left to right; -- said of handwriting.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backhand', 'a.', 'Backhanded; indirect; oblique.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backhanded', 'a.', 'With the hand turned backward; as, a backhanded blow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backhanded', 'a.', 'Indirect; awkward; insincere; sarcastic; as, a\n   backhanded compliment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backhanded', 'a.', 'Turned back, or inclining to the left; as, a backhanded\n   letters.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backhandedness', 'n.', 'State of being backhanded; the using of backhanded\n   or indirect methods.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backhander', 'n.', 'A backhanded blow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backhouse', 'n.', 'A building behind the main building. Specifically: A\n   privy; a necessary.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backing', 'n.', 'The act of moving backward, or of putting or moving\n   anything backward.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backing', 'n.', 'That which is behind, and forms the back of, anything,\n   usually giving strength or stability.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backing', 'n.', 'Support or aid given to a person or cause.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backing', 'n.', 'The preparation of the back of a book with glue, etc.,\n   before putting on the cover.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backjoint', 'n.', 'A rebate or chase in masonry left to receive a permanent\n   slab or other filling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backlash', 'n.', 'The distance through which one part of connected\n   machinery, as a wheel, piston, or screw, can be moved without moving\n   the connected parts, resulting from looseness in fitting or from wear;\n   also, the jarring or reflex motion caused in badly fitting machinery by\n   irregularities in velocity or a reverse of motion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backless', 'a.', 'Without a back.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backlog', 'n.', 'A large stick of wood, forming the back of a fire on the\n   hearth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backpiece', 'n.', 'Alt. of Backplate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backplate', 'n.', 'A piece, or plate which forms the back of anything, or\n   which covers the back; armor for the back.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backrack', 'n.', 'Alt. of Backrag');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backrag', 'n.', 'See Bacharach.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backs', 'n. pl.', 'Among leather dealers, the thickest and stoutest tanned\n   hides.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backsaw', 'n.', 'A saw (as a tenon saw) whose blade is stiffened by an\n   added metallic back.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backset', 'n.', 'A check; a relapse; a discouragement; a setback.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backset', 'n.', 'Whatever is thrown back in its course, as water.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backset', 'v. i.', 'To plow again, in the fall; -- said of prairie land\n   broken up in the spring.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backsettler', 'n.', 'One living in the back or outlying districts of a\n   community.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backsheesh', 'n.', 'Alt. of Backshish');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backshish', 'n.', 'In Egypt and the Turkish empire, a gratuity; a \"tip\".');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backside', 'n.', 'The hinder part, posteriors, or rump of a person or\n   animal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backsight', 'n.', 'The reading of the leveling staff in its unchanged\n   position when the leveling instrument has been taken to a new position;\n   a sight directed backwards to a station previously occupied. Cf.\n   Foresight, n., 3.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backslid', 'imp.', 'of Backslide');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backslidden', 'p. p.', 'of Backslide');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backslid', '', 'of Backslide');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backsliding', 'p. pr. & vb. n.', 'of Backslide');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backslide', 'v. i.', 'To slide back; to fall away; esp. to abandon\n   gradually the faith and practice of a religion that has been professed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backslider', 'n.', 'One who backslides.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backsliding', 'a.', 'Slipping back; falling back into sin or error;\n   sinning.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backsliding', 'n.', 'The act of one who backslides; abandonment of faith or\n   duty.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backstaff', 'n.', 'An instrument formerly used for taking the altitude of\n   the heavenly bodies, but now superseded by the quadrant and sextant; --\n   so called because the observer turned his back to the body observed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Back stairs', '', 'Stairs in the back part of a house, as distinguished\n   from the front stairs; hence, a private or indirect way.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backstairs', 'a.', 'Alt. of Backstair');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backstair', 'a.', 'Private; indirect; secret; intriguing; -- as if finding\n   access by the back stairs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backstay', 'n.', 'A rope or stay extending from the masthead to the side of\n   a ship, slanting a little aft, to assist the shrouds in supporting the\n   mast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backstay', 'n.', 'A rope or strap used to prevent excessive forward motion.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backster', 'n.', 'A backer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backstitch', 'n.', 'A stitch made by setting the needle back of the end of\n   the last stitch, and bringing it out in front of the end.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backstitch', 'v. i.', 'To sew with backstitches; as, to backstitch a seam.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backstress', 'n.', 'A female baker.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backsword', 'n.', 'A sword with one sharp edge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backsword', 'n.', 'In England, a stick with a basket handle, used in rustic\n   amusements; also, the game in which the stick is used. Also called\n   singlestick.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backward', 'adv.', 'Alt. of Backwards');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backwards', 'adv.', 'With the back in advance or foremost; as, to ride\n   backward.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backwards', 'adv.', 'Toward the back; toward the rear; as, to throw the\n   arms backward.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backwards', 'adv.', 'On the back, or with the back downward.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backwards', 'adv.', 'Toward, or in, past time or events; ago.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backwards', 'adv.', 'By way of reflection; reflexively.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backwards', 'adv.', 'From a better to a worse state, as from honor to\n   shame, from religion to sin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backwards', 'adv.', 'In a contrary or reverse manner, way, or direction;\n   contrarily; as, to read backwards.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backward', 'a.', 'Directed to the back or rear; as, backward glances.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backward', 'a.', 'Unwilling; averse; reluctant; hesitating; loath.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backward', 'a.', 'Not well advanced in learning; not quick of apprehension;\n   dull; inapt; as, a backward child.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backward', 'a.', 'Late or behindhand; as, a backward season.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backward', 'a.', 'Not advanced in civilization; undeveloped; as, the\n   country or region is in a backward state.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backward', 'a.', 'Already past or gone; bygone.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backward', 'n.', 'The state behind or past.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backward', 'v. i.', 'To keep back; to hinder.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backwardation', 'n.', 'The sellers postponement of delivery of stock or\n   shares, with the consent of the buyer, upon payment of a premium to the\n   latter; -- also, the premium so paid. See Contango.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backwardly', 'adv.', 'Reluctantly; slowly; aversely.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backwardly', 'adv.', 'Perversely; ill.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backwardness', 'n.', 'The state of being backward.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backwash', 'v. i.', 'To clean the oil from (wood) after combing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backwater', 'n.', 'Water turned back in its course by an obstruction, an\n   opposing current , or the flow of the tide, as in a sewer or river\n   channel, or across a river bar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backwater', 'n.', 'An accumulation of water overflowing the low lands,\n   caused by an obstruction.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backwater', 'n.', 'Water thrown back by the turning of a waterwheel, or by\n   the paddle wheels of a steamer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backwoods', 'n. pl.', 'The forests or partly cleared grounds on the\n   frontiers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backwoodsmen', 'pl. ', 'of Backwoodsman');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backwoodsman', 'n.', 'A man living in the forest in or beyond the new\n   settlements, especially on the western frontiers of the older portions\n   of the United States.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Backworm', 'n.', 'A disease of hawks. See Filanders.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacon', 'n.', 'The back and sides of a pig salted and smoked; formerly, the\n   flesh of a pig salted or fresh.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baconian', 'a.', 'Of or pertaining to Lord Bacon, or to his system of\n   philosophy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacteria', 'n.p.', 'See Bacterium.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacterial', 'a.', 'Of or pertaining to bacteria.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bactericidal', 'a.', 'Destructive of bacteria.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bactericide', 'n.', 'Same as Germicide.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacteriological', 'a.', 'Of or pertaining to bacteriology; as,\n   bacteriological studies.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacteriologist', 'n.', 'One skilled in bacteriology.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacteriology', 'n.', 'The science relating to bacteria.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacterioscopic', 'a.', 'Relating to bacterioscopy; as, a bacterioscopic\n   examination.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacterioscopist', 'n.', 'One skilled in bacterioscopic examinations.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacterioscopy', 'n.', 'The application of a knowledge of bacteria for their\n   detection and identification, as in the examination of polluted water.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacteria', 'pl. ', 'of Bacterium');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacterium', 'n.', 'A microscopic vegetable organism, belonging to the class\n   Algae, usually in the form of a jointed rodlike filament, and found in\n   putrefying organic infusions. Bacteria are destitute of chlorophyll,\n   and are the smallest of microscopic organisms. They are very widely\n   diffused in nature, and multiply with marvelous rapidity, both by\n   fission and by spores. Certain species are active agents in\n   fermentation, while others appear to be the cause of certain infectious\n   diseases. See Bacillus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacteroid', 'a.', 'Alt. of Bacteroidal');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacteroidal', 'a.', 'Resembling bacteria; as, bacteroid particles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bactrian', 'a.', 'Of or pertaining to Bactria in Asia.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bactrian', 'n.', 'A native of Bactria.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bacule', 'n.', 'See Bascule.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baculine', 'a.', 'Of or pertaining to the rod or punishment with the rod.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baculite', 'n.', 'A cephalopod of the extinct genus Baculites, found fossil\n   in the Cretaceous rocks. It is like an uncoiled ammonite.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baculometry', 'n.', 'Measurement of distance or altitude by a staff or\n   staffs.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bad', 'imp.', 'Bade.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bad', 'superl.', 'Wanting good qualities, whether physical or moral;\n   injurious, hurtful, inconvenient, offensive, painful, unfavorable, or\n   defective, either physically or morally; evil; vicious; wicked; -- the\n   opposite of good; as, a bad man; bad conduct; bad habits; bad soil; bad\n   health; bad crop; bad news.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badder', '', 'compar. of Bad, a.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badderlocks', 'n.', 'A large black seaweed (Alaria esculenta) sometimes\n   eaten in Europe; -- also called murlins, honeyware, and henware.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baddish', 'a.', 'Somewhat bad; inferior.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bade', '', 'A form of the pat tense of Bid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badge', 'n.', 'A distinctive mark, token, sign, or cognizance, worn on the\n   person; as, the badge of a society; the badge of a policeman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badge', 'n.', 'Something characteristic; a mark; a token.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badge', 'n.', 'A carved ornament on the stern of a vessel, containing a\n   window or the representation of one.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badge', 'v. t.', 'To mark or distinguish with a badge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badgeless', 'a.', 'Having no badge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badger', 'n.', 'An itinerant licensed dealer in commodities used for food;\n   a hawker; a huckster; -- formerly applied especially to one who bought\n   grain in one place and sold it in another.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badger', 'n.', 'A carnivorous quadruped of the genus Meles or of an allied\n   genus. It is a burrowing animal, with short, thick legs, and long claws\n   on the fore feet. One species (M. vulgaris); called also brock,\n   inhabits the north of Europe and Asia; another species (Taxidea\n   Americana / Labradorica) inhabits the northern parts of North America.\n   See Teledu.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badger', 'n.', 'A brush made of badgers hair, used by artists.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badgered', 'imp. & p. p.', 'of Badger');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badgering', 'p. pr. & vb. n.', 'of Badger');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badger', 'v. t.', 'To tease or annoy, as a badger when baited; to worry or\n   irritate persistently.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badger', 'v. t.', 'To beat down; to cheapen; to barter; to bargain.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badgerer', 'n.', 'One who badgers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badgerer', 'n.', 'A kind of dog used in badger baiting.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badgering', 'n.', 'The act of one who badgers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badgering', 'n.', 'The practice of buying wheat and other kinds of food in\n   one place and selling them in another for a profit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badger-legged', 'a.', 'Having legs of unequal length, as the badger was\n   thought to have.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badiaga', 'n.', 'A fresh-water sponge (Spongilla); common in the north of\n   Europe, the powder of which is used to take away the livid marks of\n   bruises.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badian', 'n.', 'An evergreen Chinese shrub of the Magnolia family (Illicium\n   anisatum); and its aromatic seeds; Chinese anise; star anise.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badigeon', 'n.', 'A cement or paste (as of plaster and freestone, or of\n   sawdust and glue or lime) used by sculptors, builders, and workers in\n   wood or stone, to fill holes, cover defects, or finish a surface.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badinage', 'n.', 'Playful raillery; banter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bad lands', '', 'Barren regions, especially in the western United States,\n   where horizontal strata (Tertiary deposits) have been often eroded into\n   fantastic forms, and much intersected by caons, and where lack of wood,\n   water, and forage increases the difficulty of traversing the country,\n   whence the name, first given by the Canadian French, Mauvaises Terres\n   (bad lands).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badly', 'adv.', 'In a bad manner; poorly; not well; unskillfully;\n   imperfectly; unfortunately; grievously; so as to cause harm;\n   disagreeably; seriously.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badminton', 'n.', 'A game, similar to lawn tennis, played with\n   shuttlecocks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badminton', 'n.', 'A preparation of claret, spiced and sweetened.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Badness', 'n.', 'The state of being bad.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baenomere', 'n.', 'One of the somites (arthromeres) that make up the thorax\n   of Arthropods.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baenopod', 'n.', 'One of the thoracic legs of Arthropods.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baenosome', 'n.', 'The thorax of Arthropods.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baff', 'n.', 'A blow; a stroke.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baffled', 'imp. & p. p.', 'of Baffle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baffling', 'p. pr. & vb. n.', 'of Baffle');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baffle', 'v. t.', 'To cause to undergo a disgraceful punishment, as a\n   recreant knight.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baffle', 'v. t.', 'To check by shifts and turns; to elude; to foil.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baffle', 'v. t.', 'To check by perplexing; to disconcert, frustrate, or\n   defeat; to thwart.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baffle', 'v. i.', 'To practice deceit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baffle', 'v. i.', 'To struggle against in vain; as, a ship baffles with the\n   winds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baffle', 'n.', 'A defeat by artifice, shifts, and turns; discomfiture.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bafflement', 'n.', 'The process or act of baffling, or of being baffled;\n   frustration; check.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baffler', 'n.', 'One who, or that which, baffles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baffling', 'a.', 'Frustrating; discomfiting; disconcerting; as, baffling\n   currents, winds, tasks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baft', 'n.', 'Same as Bafta.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bafta', 'n.', 'A coarse stuff, usually of cotton, originally made in India.\n   Also, an imitation of this fabric made for export.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bag', 'n.', 'A sack or pouch, used for holding anything; as, a bag of meal\n   or of money.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bag', 'n.', 'A sac, or dependent gland, in animal bodies, containing some\n   fluid or other substance; as, the bag of poison in the mouth of some\n   serpents; the bag of a cow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bag', 'n.', 'A sort of silken purse formerly tied about mens hair behind,\n   by way of ornament.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bag', 'n.', 'The quantity of game bagged.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bag', 'n.', 'A certain quantity of a commodity, such as it is customary to\n   carry to market in a sack; as, a bag of pepper or hops; a bag of\n   coffee.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bagged', 'imp. & p. p.', 'of Bag');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bagging', 'p. pr. & vb. n.', 'of Bag');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bag', 'v. t.', 'To put into a bag; as, to bag hops.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bag', 'v. t.', 'To seize, capture, or entrap; as, to bag an army; to bag\n   game.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bag', 'v. t.', 'To furnish or load with a bag or with a well filled bag.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bag', 'v. i.', 'To swell or hang down like a full bag; as, the skin bags\n   from containing morbid matter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bag', 'v. i.', 'To swell with arrogance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bag', 'v. i.', 'To become pregnant.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bagasse', 'n.', 'Sugar cane, as it comes crushed from the mill. It is then\n   dried and used as fuel. Also extended to the refuse of beetroot sugar.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bagatelle', 'n.', 'A trifle; a thing of no importance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bagatelle', 'n.', 'A game played on an oblong board, having, at one end,\n   cups or arches into or through which balls are to be driven by a rod\n   held in the hand of the player.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baggage', 'n.', 'The clothes, tents, utensils, and provisions of an army.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baggage', 'n.', 'The trunks, valises, satchels, etc., which a traveler\n   carries with him on a journey; luggage.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baggage', 'n.', 'Purulent matter.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baggage', 'n.', 'Trashy talk.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baggage', 'n.', 'A man of bad character.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baggage', 'n.', 'A woman of loose morals; a prostitute.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baggage', 'n.', 'A romping, saucy girl.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baggage master', '', 'One who has charge of the baggage at a railway\n   station or upon a line of public travel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baggager', 'n.', 'One who takes care of baggage; a camp follower.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baggala', 'n.', 'A two-masted Arab or Indian trading vessel, used in Indian\n   Ocean.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baggily', 'adv.', 'In a loose, baggy way.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bagging', 'n.', 'Cloth or other material for bags.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bagging', 'n.', 'The act of putting anything into, or as into, a bag.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bagging', 'n.', 'The act of swelling; swelling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bagging', 'n.', 'Reaping peas, beans, wheat, etc., with a chopping stroke.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baggy', 'a.', 'Resembling a bag; loose or puffed out, or pendent, like a\n   bag; flabby; as, baggy trousers; baggy cheeks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bagmen', 'pl. ', 'of Bagman');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bagman', 'n.', 'A commercial traveler; one employed to solicit orders for\n   manufacturers and tradesmen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bag net', '', 'A bag-shaped net for catching fish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bagnio', 'n.', 'A house for bathing, sweating, etc.; -- also, in Turkey, a\n   prison for slaves.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bagnio', 'n.', 'A brothel; a stew; a house of prostitution.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bagpipe', 'n.', 'A musical wind instrument, now used chiefly in the\n   Highlands of Scotland.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bagpipe', 'v. t.', 'To make to look like a bagpipe.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bagpiper', 'n.', 'One who plays on a bagpipe; a piper.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bagreef', 'n.', 'The lower reef of fore and aft sails; also, the upper reef\n   of topsails.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bague', 'n.', 'The annular molding or group of moldings dividing a long\n   shaft or clustered column into two or more parts.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baguet', 'n.', 'Alt. of Baguette');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baguette', 'n.', 'A small molding, like the astragal, but smaller; a bead.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baguette', 'n.', 'One of the minute bodies seen in the divided nucleoli of\n   some Infusoria after conjugation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bagwig', 'n.', 'A wig, in use in the 18th century, with the hair at the\n   back of the head in a bag.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bagworm', 'n.', 'One of several lepidopterous insects which construct, in\n   the larval state, a baglike case which they carry about for protection.\n   One species (Platoeceticus Gloveri) feeds on the orange tree. See\n   Basket worm.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bah', 'interj.', 'An exclamation expressive of extreme contempt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bahar', 'n.', 'A weight used in certain parts of the East Indies, varying\n   considerably in different localities, the range being from 223 to 625\n   pounds.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baigne', 'v. i.', 'To soak or drench.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bail', 'n.', 'A bucket or scoop used in bailing water out of a boat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bailed', 'imp. & p. p.', 'of Bail');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bailing', 'p. pr. & vb. n.', 'of Bail');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bail', 'v. t.', 'To lade; to dip and throw; -- usually with out; as, to\n   bail water out of a boat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bail', 'v. t.', 'To dip or lade water from; -- often with out to express\n   completeness; as, to bail a boat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bail', 'v./t.', 'To deliver; to release.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bail', 'v./t.', 'To set free, or deliver from arrest, or out of custody, on\n   the undertaking of some other person or persons that he or they will be\n   responsible for the appearance, at a certain day and place, of the\n   person bailed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bail', 'v./t.', 'To deliver, as goods in trust, for some special object or\n   purpose, upon a contract, expressed or implied, that the trust shall be\n   faithfully executed on the part of the bailee, or person intrusted; as,\n   to bail cloth to a tailor to be made into a garment; to bail goods to a\n   carrier.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bail', 'n.', 'Custody; keeping.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bail', 'n.', 'The person or persons who procure the release of a prisoner\n   from the custody of the officer, or from imprisonment, by becoming\n   surely for his appearance in court.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bail', 'n.', 'The security given for the appearance of a prisoner in order\n   to obtain his release from custody of the officer; as, the man is out\n   on bail; to go bail for any one.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bail', 'n.', 'The arched handle of a kettle, pail, or similar vessel,\n   usually movable.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bail', 'n.', 'A half hoop for supporting the cover of a carriers wagon,\n   awning of a boat, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bail', 'n.', 'A line of palisades serving as an exterior defense.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bail', 'n.', 'The outer wall of a feudal castle. Hence: The space inclosed\n   by it; the outer court.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bail', 'n.', 'A certain limit within a forest.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bail', 'n.', 'A division for the stalls of an open stable.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bail', 'n.', 'The top or cross piece ( or either of the two cross pieces)\n   of the wicket.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bailable', 'a.', 'Having the right or privilege of being admitted to bail,\n   upon bond with sureties; -- used of persons.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bailable', 'a.', 'Admitting of bail; as, a bailable offense.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bailable', 'a.', 'That can be delivered in trust; as, bailable goods.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bail bond', '', 'A bond or obligation given by a prisoner and his surety,\n   to insure the prisoners appearance in court, at the return of the\n   writ.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bail bond', '', 'Special bail in court to abide the judgment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bailee', 'n.', 'The person to whom goods are committed in trust, and who\n   has a temporary possession and a qualified property in them, for the\n   purposes of the trust.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bailer', 'n.', 'See Bailor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bailer', 'n.', 'One who bails or lades.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bailer', 'n.', 'A utensil, as a bucket or cup, used in bailing; a machine\n   for bailing water out of a pit.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bailey', 'n.', 'The outer wall of a feudal castle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bailey', 'n.', 'The space immediately within the outer wall of a castle or\n   fortress.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bailey', 'n.', 'A prison or court of justice; -- used in certain proper\n   names; as, the Old Bailey in London; the New Bailey in Manchester.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bailie', 'n.', 'An officer in Scotland, whose office formerly corresponded\n   to that of sheriff, but now corresponds to that of an English alderman.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bailiff', 'n.', 'Originally, a person put in charge of something\n   especially, a chief officer, magistrate, or keeper, as of a county,\n   town, hundred, or castle; one to whom power/ of custody or care are\n   intrusted.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bailiff', 'n.', 'A sheriffs deputy, appointed to make arrests, collect\n   fines, summon juries, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bailiff', 'n.', 'An overseer or under steward of an estate, who directs\n   husbandry operations, collects rents, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bailiffwick', 'n.', 'See Bailiwick.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bailiwick', 'n.', 'The precincts within which a bailiff has jurisdiction;\n   the limits of a bailiffs authority.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baillie', 'n.', 'Bailiff.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baillie', 'n.', 'Same as Bailie.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bailment', 'n.', 'The action of bailing a person accused.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bailment', 'n.', 'A delivery of goods or money by one person to another in\n   trust, for some special purpose, upon a contract, expressed or implied,\n   that the trust shall be faithfully executed.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bailor', 'n.', 'One who delivers goods or money to another in trust.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bailpiece', 'n.', 'A piece of parchment, or paper, containing a\n   recognizance or bail bond.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bain', 'n.', 'A bath; a bagnio.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bain-marie', 'n.', 'A vessel for holding hot water in which another vessel\n   may be heated without scorching its contents; -- used for warming or\n   preparing food or pharmaceutical preparations.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bairam', 'n.', 'The name of two Mohammedan festivals, of which one is held\n   at the close of the fast called Ramadan, and the other seventy days\n   after the fast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bairn', 'n.', 'A child.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baisemains', 'n. pl.', 'Respects; compliments.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bait', 'v. i.', 'Any substance, esp. food, used in catching fish, or other\n   animals, by alluring them to a hook, snare, inclosure, or net.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bait', 'v. i.', 'Anything which allures; a lure; enticement; temptation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bait', 'v. i.', 'A portion of food or drink, as a refreshment taken on a\n   journey; also, a stop for rest and refreshment.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bait', 'v. i.', 'A light or hasty luncheon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baited', 'imp. & p. p.', 'of Bait');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baiting', 'p. pr. & vb. n.', 'of Bait');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bait', 'v. t.', 'To provoke and harass; esp., to harass or torment for\n   sport; as, to bait a bear with dogs; to bait a bull.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bait', 'v. t.', 'To give a portion of food and drink to, upon the road; as,\n   to bait horses.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bait', 'v. t.', 'To furnish or cover with bait, as a trap or hook.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bait', 'v. i.', 'To stop to take a portion of food and drink for\n   refreshment of ones self or ones beasts, on a journey.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bait', 'v. i.', 'To flap the wings; to flutter as if to fly; or to hover,\n   as a hawk when she stoops to her prey.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baiter', 'n.', 'One who baits; a tormentor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baize', 'n.', 'A coarse woolen stuff with a long nap; -- usually dyed in\n   plain colors.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bajocco', 'n.', 'A small copper coin formerly current in the Roman States,\n   worth about a cent and a half.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baked', 'imp. & p. p.', 'of Bake');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baking', 'p. pr. & vb. n.', 'of Bake');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bake', 'v. t.', 'To prepare, as food, by cooking in a dry heat, either in\n   an oven or under coals, or on heated stone or metal; as, to bake bread,\n   meat, apples.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bake', 'v. t.', 'To dry or harden (anything) by subjecting to heat, as, to\n   bake bricks; the sun bakes the ground.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bake', 'v. t.', 'To harden by cold.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bake', 'v. i.', 'To do the work of baking something; as, she brews, washes,\n   and bakes.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bake', 'v. i.', 'To be baked; to become dry and hard in heat; as, the bread\n   bakes; the ground bakes in the hot sun.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bake', 'n.', 'The process, or result, of baking.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bakehouse', 'v. t.', 'A house for baking; a bakery.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bakemeat', 'n.', 'Alt. of Baked-meat');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baked-meat', 'n.', 'A pie; baked food.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baken', '', 'p. p. of Bake.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baker', 'v. i.', 'One whose business it is to bake bread, biscuit, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baker', 'v. i.', 'A portable oven in which baking is done.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baker-legged', 'a.', 'Having legs that bend inward at the knees.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bakery', 'n.', 'The trade of a baker.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bakery', 'n.', 'The place for baking bread; a bakehouse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baking', 'n.', 'The act or process of cooking in an oven, or of drying and\n   hardening by heat or cold.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baking', 'n.', 'The quantity baked at once; a batch; as, a baking of bread.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bakingly', 'adv.', 'In a hot or baking manner.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bakistre', 'n.', 'A baker.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baksheesh', 'n.', 'Alt. of Bakshish');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bakshish', 'n.', 'Same as Backsheesh.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balaam', 'n.', 'A paragraph describing something wonderful, used to fill\n   out a newspaper column; -- an allusion to the miracle of Balaams ass\n   speaking.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balachong', 'n.', 'A condiment formed of small fishes or shrimps, pounded\n   up with salt and spices, and then dried. It is much esteemed in China.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balaenoidea', 'n.', 'A division of the Cetacea, including the right whale\n   and all other whales having the mouth fringed with baleen. See Baleen.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance', 'n.', 'An apparatus for weighing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance', 'n.', 'Act of weighing mentally; comparison; estimate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance', 'n.', 'Equipoise between the weights in opposite scales.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance', 'n.', 'The state of being in equipoise; equilibrium; even\n   adjustment; steadiness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance', 'n.', 'An equality between the sums total of the two sides of an\n   account; as, to bring ones accounts to a balance; -- also, the excess\n   on either side; as, the balance of an account.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance', 'n.', 'A balance wheel, as of a watch, or clock. See Balance\n   wheel (in the Vocabulary).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance', 'n.', 'The constellation Libra.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance', 'n.', 'The seventh sign in the Zodiac, called Libra, which the\n   sun enters at the equinox in September.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance', 'n.', 'A movement in dancing. See Balance, v. i., S.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balanced', 'imp. & p. p.', 'of Balance');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balancing', 'p. pr. & vb. n.', 'of Balance');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance', 'n.', 'To bring to an equipoise, as the scales of a balance by\n   adjusting the weights; to weigh in a balance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance', 'n.', 'To support on a narrow base, so as to keep from falling;\n   as, to balance a plate on the end of a cane; to balance ones self on a\n   tight rope.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance', 'n.', 'To equal in number, weight, force, or proportion; to\n   counterpoise, counterbalance, counteract, or neutralize.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance', 'n.', 'To compare in relative force, importance, value, etc.; to\n   estimate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance', 'n.', 'To settle and adjust, as an account; to make two accounts\n   equal by paying the difference between them.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance', 'n.', 'To make the sums of the debits and credits of an account\n   equal; -- said of an item; as, this payment, or credit, balances the\n   account.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance', 'n.', 'To arrange accounts in such a way that the sum total of\n   the debits is equal to the sum total of the credits; as, to balance a\n   set of books.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance', 'n.', 'To move toward, and then back from, reciprocally; as, to\n   balance partners.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance', 'n.', 'To contract, as a sail, into a narrower compass; as, to\n   balance the boom mainsail.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance', 'v. i.', 'To have equal weight on each side; to be in equipoise;\n   as, the scales balance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance', 'v. i.', 'To fluctuate between motives which appear of equal\n   force; to waver; to hesitate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance', 'v. i.', 'To move toward a person or couple, and then back.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balanceable', 'a.', 'Such as can be balanced.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balancement', 'n.', 'The act or result of balancing or adjusting;\n   equipoise; even adjustment of forces.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balancer', 'n.', 'One who balances, or uses a balance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balancer', 'n.', 'In Diptera, the rudimentary posterior wing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balancereef', 'n.', 'The last reef in a fore-and-aft sail, taken to steady\n   the ship.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance wheel', '', 'A wheel which regulates the beats or pulses of a watch\n   or chronometer, answering to the pendulum of a clock; -- often called\n   simply a balance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance wheel', '', 'A ratchet-shaped scape wheel, which in some watches is\n   acted upon by the axis of the balance wheel proper (in those watches\n   called a balance).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balance wheel', '', 'A wheel which imparts regularity to the movements of\n   any engine or machine; a fly wheel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balaniferous', 'a.', 'Bearing or producing acorns.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balanite', 'n.', 'A fossil balanoid shell.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balanoglossus', 'n.', 'A peculiar marine worm. See Enteropneusta, and\n   Tornaria.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balanoid', 'a.', 'Resembling an acorn; -- applied to a group of barnacles\n   having shells shaped like acorns. See Acornshell, and Barnacle.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balas ruby', '', 'A variety of spinel ruby, of a pale rose red, or\n   inclining to orange. See Spinel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balaustine', 'n.', 'The pomegranate tree (Punica granatum). The bark of the\n   root, the rind of the fruit, and the flowers are used medicinally.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balbutiate', 'v. i.', 'Alt. of Balbucinate');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balbucinate', 'v. i.', 'To stammer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balbuties', 'n.', 'The defect of stammering; also, a kind of incomplete\n   pronunciation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balcon', 'n.', 'A balcony.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balconied', 'a.', 'Having balconies.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balconies', 'pl. ', 'of Balcony');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balcony', 'n.', 'A platform projecting from the wall of a building, usually\n   resting on brackets or consoles, and inclosed by a parapet; as, a\n   balcony in front of a window. Also, a projecting gallery in places of\n   amusement; as, the balcony in a theater.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balcony', 'n.', 'A projecting gallery once common at the stern of large\n   ships.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bald', 'a.', 'Destitute of the natural or common covering on the head or\n   top, as of hair, feathers, foliage, trees, etc.; as, a bald head; a\n   bald oak.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bald', 'a.', 'Destitute of ornament; unadorned; bare; literal.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bald', 'a.', 'Undisguised.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bald', 'a.', 'Destitute of dignity or value; paltry; mean.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bald', 'a.', 'Destitute of a beard or awn; as, bald wheat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bald', 'a.', 'Destitute of the natural covering.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bald', 'a.', 'Marked with a white spot on the head; bald-faced.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baldachin', 'n.', 'A rich brocade; baudekin.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baldachin', 'n.', 'A structure in form of a canopy, sometimes supported by\n   columns, and sometimes suspended from the roof or projecting from the\n   wall; generally placed over an altar; as, the baldachin in St. Peters.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baldachin', 'n.', 'A portable canopy borne over shrines, etc., in\n   procession.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bald eagle', '', 'The white-headed eagle (Haliaeetus leucocephalus) of\n   America. The young, until several years old, lack the white feathers on\n   the head.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balder', 'n.', 'The most beautiful and beloved of the gods; the god of\n   peace; the son of Odin and Freya.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balderdash', 'n.', 'A worthless mixture, especially of liquors.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balderdash', 'n.', 'Senseless jargon; ribaldry; nonsense; trash.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balderdash', 'v. t.', 'To mix or adulterate, as liquors.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bald-faced', 'a.', 'Having a white face or a white mark on the face, as a\n   stag.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baldhead', 'n.', 'A person whose head is bald.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baldhead', 'n.', 'A white-headed variety of pigeon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baldheaded', 'a.', 'Having a bald head.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baldly', 'adv.', 'Nakedly; without reserve; inelegantly.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baldness', 'n.', 'The state or condition of being bald; as, baldness of the\n   head; baldness of style.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baldpate', 'n.', 'A baldheaded person.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baldpate', 'n.', 'The American widgeon (Anas Americana).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baldpate', 'a.', 'Alt. of Baldpated');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baldpated', 'a.', 'Destitute of hair on the head; baldheaded.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baldrib', 'n.', 'A piece of pork cut lower down than the sparerib, and\n   destitute of fat.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baldric', 'n.', 'A broad belt, sometimes richly ornamented, worn over one\n   shoulder, across the breast, and under the opposite arm; less properly,\n   any belt.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baldwin', 'n.', 'A kind of reddish, moderately acid, winter apple.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bale', 'n.', 'A bundle or package of goods in a cloth cover, and corded for\n   storage or transportation; also, a bundle of straw / hay, etc., put up\n   compactly for transportation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baled', 'imp. & p. p.', 'of Bale');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baling', 'p. pr. & vb. n.', 'of Bale');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bale', 'v. t.', 'To make up in a bale.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bale', 'v. t.', 'See Bail, v. t., to lade.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bale', 'n.', 'Misery; calamity; misfortune; sorrow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Bale', 'n.', 'Evil; an evil, pernicious influence; something causing great\n   injury.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balearic', 'a.', 'Of or pertaining to the isles of Majorca, Minorca, Ivica,\n   etc., in the Mediterranean Sea, off the coast of Valencia.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baleen', 'n.', 'Plates or blades of \"whalebone,\" from two to twelve feet\n   long, and sometimes a foot wide, which in certain whales (Balaenoidea)\n   are attached side by side along the upper jaw, and form a fringelike\n   sieve by which the food is retained in the mouth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balefire', 'n.', 'A signal fire; an alarm fire.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baleful', 'a.', 'Full of deadly or pernicious influence; destructive.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Baleful', 'a.', 'Full of grief or sorrow; woeful; sad.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balefully', 'adv.', 'In a baleful manner; perniciously.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balefulness', 'n.', 'The quality or state of being baleful.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balisaur', 'n.', 'A badgerlike animal of India (Arcionyx collaris).');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balister', 'n.', 'A crossbow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balistoid', 'a.', 'Like a fish of the genus Balistes; of the family\n   Balistidae. See Filefish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balistraria', 'n.', 'A narrow opening, often cruciform, through which\n   arrows might be discharged.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balize', 'n.', 'A pole or a frame raised as a sea beacon or a landmark.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balk', 'v. i.', 'A ridge of land left unplowed between furrows, or at the\n   end of a field; a piece missed by the plow slipping aside.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balk', 'v. i.', 'A great beam, rafter, or timber; esp., the tie-beam of a\n   house. The loft above was called \"the balks.\"');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balk', 'v. i.', 'One of the beams connecting the successive supports of a\n   trestle bridge or bateau bridge.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balk', 'v. i.', 'A hindrance or disappointment; a check.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balk', 'v. i.', 'A sudden and obstinate stop; a failure.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balk', 'v. i.', 'A deceptive gesture of the pitcher, as if to deliver the\n   ball.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balked', 'imp. & p. p.', 'of Balk');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balking', 'p. pr. & vb. n.', 'of Balk');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balk', 'v. t.', 'To leave or make balks in.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balk', 'v. t.', 'To leave heaped up; to heap up in piles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balk', 'v. t.', 'To omit, miss, or overlook by chance.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balk', 'v. t.', 'To miss intentionally; to avoid; to shun; to refuse; to\n   let go by; to shirk.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balk', 'v. t.', 'To disappoint; to frustrate; to foil; to baffle; to\n   /hwart; as, to balk expectation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balk', 'v. i.', 'To engage in contradiction; to be in opposition.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balk', 'v. i.', 'To stop abruptly and stand still obstinately; to jib; to\n   stop short; to swerve; as, the horse balks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balk', 'v. i.', 'To indicate to fishermen, by shouts or signals from shore,\n   the direction taken by the shoals of herring.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balker', 'n.', 'One who, or that which balks.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balker', 'n.', 'A person who stands on a rock or eminence to espy the\n   shoals of herring, etc., and to give notice to the men in boats which\n   way they pass; a conder; a huer.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balkingly', 'adv.', 'In a manner to balk or frustrate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balkish', 'a.', 'Uneven; ridgy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balky', 'a.', 'Apt to balk; as, a balky horse.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ball', 'n.', 'Any round or roundish body or mass; a sphere or globe; as, a\n   ball of twine; a ball of snow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ball', 'n.', 'A spherical body of any substance or size used to play with,\n   as by throwing, knocking, kicking, etc.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ball', 'n.', 'A general name for games in which a ball is thrown, kicked,\n   or knocked. See Baseball, and Football.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ball', 'n.', 'Any solid spherical, cylindrical, or conical projectile of\n   lead or iron, to be discharged from a firearm; as, a cannon ball; a\n   rifle ball; -- often used collectively; as, powder and ball. Spherical\n   balls for the smaller firearms are commonly called bullets.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ball', 'n.', 'A flaming, roundish body shot into the air; a case filled\n   with combustibles intended to burst and give light or set fire, or to\n   produce smoke or stench; as, a fire ball; a stink ball.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ball', 'n.', 'A leather-covered cushion, fastened to a handle called a\n   ballstock; -- formerly used by printers for inking the form, but now\n   superseded by the roller.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ball', 'n.', 'A roundish protuberant portion of some part of the body; as,\n   the ball of the thumb; the ball of the foot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ball', 'n.', 'A large pill, a form in which medicine is commonly given to\n   horses; a bolus.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ball', 'n.', 'The globe or earth.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balled', 'imp. & p. p.', 'of Ball');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balling', 'p. pr. & vb. n.', 'of Ball');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ball', 'v. i.', 'To gather balls which cling to the feet, as of damp snow\n   or clay; to gather into balls; as, the horse balls; the snow balls.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ball', 'v. t.', 'To heat in a furnace and form into balls for rolling.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ball', 'v. t.', 'To form or wind into a ball; as, to ball cotton.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ball', 'n.', 'A social assembly for the purpose of dancing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballad', 'n.', 'A popular kind of narrative poem, adapted for recitation or\n   singing; as, the ballad of Chevy Chase; esp., a sentimental or romantic\n   poem in short stanzas.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballad', 'v. i.', 'To make or sing ballads.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballad', 'v. t.', 'To make mention of in ballads.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballade', 'n.', 'A form of French versification, sometimes imitated in\n   English, in which three or four rhymes recur through three stanzas of\n   eight or ten lines each, the stanzas concluding with a refrain, and the\n   whole poem with an envoy.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballader', 'n.', 'A writer of ballads.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballad monger', '', 'A seller or maker of ballads; a poetaster.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balladry', 'n.', 'Ballad poems; the subject or style of ballads.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballahoo', 'n.', 'Alt. of Ballahou');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballahou', 'n.', 'A fast-sailing schooner, used in the Bermudas and West\n   Indies.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballarag', 'v. i.', 'To bully; to threaten.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballast', 'a.', 'Any heavy substance, as stone, iron, etc., put into the\n   hold to sink a vessel in the water to such a depth as to prevent\n   capsizing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballast', 'a.', 'Any heavy matter put into the car of a balloon to give it\n   steadiness.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballast', 'a.', 'Gravel, broken stone, etc., laid in the bed of a railroad\n   to make it firm and solid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballast', 'a.', 'The larger solids, as broken stone or gravel, used in\n   making concrete.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballast', 'a.', 'Fig.: That which gives, or helps to maintain, uprightness,\n   steadiness, and security.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballasted', 'imp. & p. p.', 'of Ballast');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballasting', 'p. pr. & vb. n.', 'of Ballast');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballast', 'v. t.', 'To steady, as a vessel, by putting heavy substances in\n   the hold.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballast', 'v. t.', 'To fill in, as the bed of a railroad, with gravel,\n   stone, etc., in order to make it firm and solid.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballast', 'v. t.', 'To keep steady; to steady, morally.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballastage', 'n.', 'A toll paid for the privilege of taking up ballast in a\n   port or harbor.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballasting', 'n.', 'That which is used for steadying anything; ballast.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballatry', 'n.', 'See Balladry.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballet', 'n.', 'An artistic dance performed as a theatrical entertainment,\n   or an interlude, by a number of persons, usually women. Sometimes, a\n   scene accompanied by pantomime and dancing.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballet', 'n.', 'The company of persons who perform the ballet.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballet', 'n.', 'A light part song, or madrigal, with a fa la burden or\n   chorus, -- most common with the Elizabethan madrigal composers.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballet', 'n.', 'A bearing in coats of arms, representing one or more balls,\n   which are denominated bezants, plates, etc., according to color.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ball-flower', 'n.', 'An ornament resembling a ball placed in a circular\n   flower, the petals of which form a cup round it, -- usually inserted in\n   a hollow molding.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballist/', 'pl. ', 'of Ballista');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballista', 'n.', 'An ancient military engine, in the form of a crossbow,\n   used for hurling large missiles.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballister', 'n.', 'A crossbow.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballistic', 'a.', 'Of or pertaining to the ballista, or to the art of\n   hurling stones or missile weapons by means of an engine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballistic', 'a.', 'Pertaining to projection, or to a projectile.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballistics', 'n.', 'The science or art of hurling missile weapons by the\n   use of an engine.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballium', 'n.', 'See Bailey.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balloon', 'n.', 'A bag made of silk or other light material, and filled\n   with hydrogen gas or heated air, so as to rise and float in the\n   atmosphere; especially, one with a car attached for aerial navigation.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balloon', 'n.', 'A ball or globe on the top of a pillar, church, etc., as\n   at St. Pauls, in London.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balloon', 'n.', 'A round vessel, usually with a short neck, to hold or\n   receive whatever is distilled; a glass vessel of a spherical form.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balloon', 'n.', 'A bomb or shell.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balloon', 'n.', 'A game played with a large inflated ball.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balloon', 'n.', 'The outline inclosing words represented as coming from the\n   mouth of a pictured figure.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balloon', 'v. t.', 'To take up in, or as if in, a balloon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balloon', 'v. i.', 'To go up or voyage in a balloon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balloon', 'v. i.', 'To expand, or puff out, like a balloon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballooned', 'a.', 'Swelled out like a balloon.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballooner', 'n.', 'One who goes up in a balloon; an aeronaut.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balloon fish', '', 'A fish of the genus Diodon or the genus Tetraodon,\n   having the power of distending its body by taking air or water into its\n   dilatable esophagus. See Globefish, and Bur fish.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballooning', 'n.', 'The art or practice of managing balloons or voyaging in\n   them.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballooning', 'n.', 'The process of temporarily raising the value of a\n   stock, as by fictitious sales.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballooning spider', '', 'A spider which has the habit of rising into the\n   air. Many kinds ( esp. species of Lycosa) do this while young by\n   ejecting threads of silk until the force of the wind upon them carries\n   the spider aloft.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balloonist', 'n.', 'An aeronaut.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balloonry', 'n.', 'The art or practice of ascending in a balloon;\n   aeronautics.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballot', 'n.', 'Originally, a ball used for secret voting. Hence: Any\n   printed or written ticket used in voting.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballot', 'n.', 'The act of voting by balls or written or printed ballots or\n   tickets; the system of voting secretly by balls or by tickets.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballot', 'n.', 'The whole number of votes cast at an election, or in a\n   given territory or electoral district.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balloted', 'imp. & p. p.', 'of Ballot');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balloting', 'p. pr. & vb. n.', 'of Ballot');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballot', 'n.', 'To vote or decide by ballot; as, to ballot for a candidate.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballot', 'v. t.', 'To vote for or in opposition to.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballotade', 'v. i.', 'A leap of a horse, as between two pillars, or upon a\n   straight line, so that when his four feet are in the air, he shows only\n   the shoes of his hind feet, without jerking out.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballotation', 'n.', 'Voting by ballot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Balloter', 'n.', 'One who votes by ballot.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballotin', 'n.', 'An officer who has charge of a ballot box.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballow', 'n.', 'A cudgel.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballproof', 'a.', 'Incapable of being penetrated by balls from firearms.');
  INSERT INTO words (word, word_type, description)VALUES  (    'Ballroom', 'n.', 'A room for balls or dancing.');
